import{r as p,o as c,c as a,a as e,b as n,w as s,F as _,e as t,d}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const f={},l=e("h3",{id:"\u53CD\u590D\u53E3\u8154\u6E83\u75A1-\u8981\u4E0D\u8981\u62C5\u5FC3\u764C\u53D8",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#\u53CD\u590D\u53E3\u8154\u6E83\u75A1-\u8981\u4E0D\u8981\u62C5\u5FC3\u764C\u53D8","aria-hidden":"true"},"#"),t(" \u53CD\u590D\u53E3\u8154\u6E83\u75A1\uFF0C\u8981\u4E0D\u8981\u62C5\u5FC3\u764C\u53D8\uFF1F")],-1),h=t("\u524D\u51E0\u5929\u5206\u4EAB\u4E86\u4E00\u4F4D\u5988\u5988"),b=t("\u5C31\u533B\u7ECF\u5386"),g=t("\uFF0C\u5BFC\u81F4\u6700\u8FD1\u51E0\u5929\u6021\u79BE\u53E3\u8154\u79D1\u533B\u751F\u4EEC\u6536\u5230\u5F88\u591A\u53E3\u8154\u6E83\u75A1\u7684\u54A8\u8BE2\uFF0C\u800C\u4E14\u5F88\u591A\u95EE\u9898\u90FD\u7C7B\u4F3C\u3002\u6B63\u597D\u6587\u4E2D\u63D0\u5230\u7684\u97E9\u83B9\u533B\u751F\u6B64\u524D\u5199\u8FC7\u4E00\u7BC7\u5173\u4E8E\u53E3\u8154\u6E83\u75A1\u7684\u79D1\u666E\uFF0C\u5206\u4EAB\u7ED9\u5927\u5BB6\uFF0C\u56FE\u7247\u4E0B\u9762\u662F\u6B63\u6587\u3002"),m=t("\u70B9\u51FB\u9605\u8BFB| \u6211\u548C\u80BF\u7624\u541B\u7684\u6545\u4E8B"),u=d("<p>\u65E0\u8BBA\u5927\u4EBA\u5B69\u5B50\uFF0C\u90FD\u53EF\u80FD\u9047\u5230\u4E00\u79CD\u60C5\u51B5\uFF1A\u5634\u91CC\u83AB\u540D\u5176\u5999\u51FA\u73B0\u5706\u5706\u7684\u6E83\u70C2\uFF0C\u4E00\u5403\u4E1C\u897F\u5C31\u75DB\uFF0C\u5C24\u5176\u662F\u5403\u54B8\u7684\u8FA3\u7684\uFF0C\u8FC7\u6BB5\u65F6\u95F4\u53C8\u81EA\u5DF1\u957F\u597D\u4E86\uFF0C\u8FD9\u5C31\u662F\u6BD4\u8F83\u5178\u578B\u7684\u53E3\u8154\u6E83\u75A1\u3002</p><p>\u53E3\u8154\u6E83\u75A1\u867D\u7136\u662F\u4E00\u4E2A\u5C0F\u95EE\u9898\uFF0C\u4F46\u4E5F\u5F88\u607C\u4EBA\uFF0C\u6709\u65F6\u8FD8\u62C5\u5FC3\u662F\u4E0D\u662F\u6709\u5176\u5B83\u7684\u4E25\u91CD\u95EE\u9898\uFF0C\u5173\u4E8E\u5B83\u8FD8\u6709\u5F88\u591A\u6D41\u4F20\u7684\u8BF4\u6CD5\uFF0C\u5B83\u5230\u5E95\u662F\u600E\u4E48\u56DE\u4E8B\u5462\uFF1F</p><p>\u6240\u8C13\u6E83\u75A1\uFF0C\u662F\u6307\u76AE\u80A4\u6216\u9ECF\u819C\u4E0A\u53D1\u751F\u4E86\u7F3A\u635F\u548C\u6E83\u70C2\uFF0C\u5F15\u8D77\u5C40\u90E8\u8868\u5C42\u574F\u6B7B\u8131\u843D\u800C\u5F62\u6210\u7684\u51F9\u9677\uFF0C\u53D1\u751F\u5728\u53E3\u8154\u5185\uFF0C\u6BD4\u5982\u988A\u9ECF\u819C\u3001\u5634\u5507\u3001\u820C\u5934\u4E0A\u7684\u6E83\u75A1\u5C31\u53EB\u53E3\u8154\u6E83\u75A1\u3002</p><p>\u80FD\u5BFC\u81F4\u53E3\u8154\u51FA\u73B0\u6E83\u75A1\u7684\u60C5\u51B5\u5F88\u591A\uFF0C\u6BD4\u5982\u54AC\u7834\u4E86\u9020\u6210\u635F\u4F24\uFF0C\u53C8\u6BD4\u5982\u513F\u7AE5\u5E38\u89C1\u7684\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\uFF0C\u90FD\u53EF\u4EE5\u5BFC\u81F4\u53E3\u8154\u5185\u6E83\u75A1\u3002\u6211\u4EEC\u5E73\u65F6\u6240\u8BF4\u7684\u53E3\u8154\u6E83\u75A1\uFF0C\u4E00\u822C\u6307\u7684\u662F\u75C5\u56E0\u4E0D\u660E\u4E14\u53CD\u590D\u53D1\u751F\u7684\u90A3\u79CD\uFF0C\u4E5F\u5C31\u662F\u590D\u53D1\u6027\u53E3\u8154\u6E83\u75A1\u3002</p><p><strong>\u590D\u53D1\u6027\u53E3\u8154\u6E83\u75A1\u662F\u4EC0\u4E48\u6837\u5B50\uFF1F</strong></p><p>\u590D\u53D1\u6027\u53E3\u8154\u6E83\u75A1\u662F\u6700\u5E38\u89C1\u7684\u53E3\u8154\u9ECF\u819C\u75BE\u75C5\uFF0C\u60A3\u75C5\u7387\u7EA6\u4E3A30%\u3002\u901A\u5E38\u662F\u4E00\u6B21\u51FA\u73B0\u4E00\u4E2A\u6216\u51E0\u4E2A\u5706\u5F62\u6216\u692D\u5706\u5F62\u5F62\u72B6\u89C4\u5219\u7684\u521B\u9762\uFF0C\u4F34\u6709\u201C\u7EA2\u3001\u9EC4\u3001\u51F9\u3001\u75DB\u201D\u7684\u5178\u578B\u8868\u73B0\u3002\u5373\u4E2D\u592E\u51F9\u9677\uFF0C\u4E0A\u8986\u7070\u767D\u8272\u6216\u9EC4\u8272\u4F2A\u819C\uFF0C\u5468\u56F4\u5145\u8840\u7EA2\u6655\uFF0C\u89E6\u75DB\u660E\u663E\u3002\u540C\u65F6\u6CA1\u6709\u53D1\u70ED\u3001\u8179\u6CFB\u3001\u76AE\u75B9\u3001\u773C\u90E8\u708E\u75C7\u3001\u751F\u6B96\u5668\u6E83\u75A1\u7B49\u4F34\u968F\u75C7\u72B6\uFF0C\u901A\u5E381-2\u5468\u81EA\u5DF1\u4F1A\u597D\uFF0C\u4F46\u8FC7\u6BB5\u65F6\u95F4\u53C8\u4F1A\u51FA\u73B0\u3002</p><p><strong>\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0\u53E3\u8154\u6E83\u75A1\uFF1F</strong></p><p>\u590D\u53D1\u6027\u53E3\u8154\u6E83\u75A1\u867D\u7136\u5F88\u5E38\u89C1\uFF0C\u4F46\u53D1\u751F\u539F\u56E0\u5374\u4E0D\u6E05\u695A\uFF0C\u4E00\u822C\u8BA4\u4E3A35\u5C81\u524D\u5F00\u59CB\u7684\u53CD\u590D\u53E3\u8154\u6E83\u75A1\u4E3B\u8981\u4E0E\u5BB6\u65CF\u56E0\u7D20\u6709\u5173\uFF0C\u6B64\u5916\u5973\u6027\u7684\u6FC0\u7D20\u53D8\u5316\uFF0C\u7126\u8651\u3001\u71AC\u591C\u3001\u5931\u7720\u3001\u751F\u6D3B\u6B20\u89C4\u5F8B\u4E4B\u7C7B\u7684\u60C5\u7EEA\u5E94\u6FC0\u7B49\uFF0C\u4E5F\u88AB\u8BA4\u4E3A\u53EF\u80FD\u662F\u8BF1\u56E0\u3002</p><p><strong>\u4F1A\u4E0D\u4F1A\u53D1\u5C55\u4E3A\u53E3\u8154\u764C\uFF1F</strong></p><p>\u56E0\u4E3A\u5BB9\u6613\u53CD\u590D\uFF0C\u6240\u4EE5\u5F88\u591A\u6709\u590D\u53D1\u6027\u53E3\u8154\u6E83\u75A1\u7684\u4EBA\u90FD\u62C5\u5FC3\u4F1A\u4E0D\u4F1A\u764C\u53D8\uFF0C\u5C24\u5176\u662F\u4E00\u4E24\u5468\u6CA1\u957F\u597D\u7684\u3002\u5C3D\u7BA1\u53E3\u8154\u6E83\u75A1\u901A\u5E387-10\u5929\u81EA\u6108\uFF0C\u4F46\u6839\u636E\u6E83\u75A1\u7684\u5927\u5C0F\u3001\u90E8\u4F4D\u3001\u5C40\u90E8\u523A\u6FC0\u56E0\u7D20\u7B49\u60C5\u51B5\uFF0C\u5176\u6108\u5408\u7684\u65F6\u5019\u4E5F\u6709\u6240\u4E0D\u540C\uFF0C\u4F46\u603B\u7684\u6765\u8BB2\uFF0C\u53E3\u8154\u6E83\u75A1\u90FD\u662F\u4F1A\u9010\u6E10\u597D\u8F6C\u7684\u3002</p><p>\u597D\u6D88\u606F\u662F\u76EE\u524D\u4E3A\u6B62\u8FD8\u6CA1\u6709\u590D\u53D1\u6027\u53E3\u8154\u6E83\u75A1\u764C\u53D8\u7684\u76F8\u5173\u62A5\u9053\u3002\u4F46\u521B\u4F24\u6027\u6E83\u75A1\uFF0C\u5982\u679C\u521B\u4F24\u56E0\u7D20\u957F\u671F\u5B58\u5728\uFF0C\u662F\u6709\u53EF\u80FD\u53D1\u751F\u6076\u53D8\u7684\uFF0C\u800C\u764C\u6027\u6E83\u75A1\u81EA\u53D1\u751F\u4E4B\u65E5\u8D77\u5C31\u662F\u6076\u6027\u7684\uFF0C\u8868\u73B0\u4E0E\u590D\u6E83\u5B8C\u5168\u4E0D\u540C\uFF0C\u5982\u679C\u81EA\u5DF1\u62C5\u5FC3\u53C8\u5224\u65AD\u4E0D\u51C6\uFF0C\u5C31\u53EA\u6709\u627E\u533B\u751F\u770B\u770B\u4E86\u3002</p><p><strong>\u8981\u4E0D\u8981\u505A\u5316\u9A8C\uFF1F</strong></p><p>\u5F88\u591A\u590D\u53D1\u6027\u53E3\u8154\u6E83\u75A1\u7684\u75C5\u4EBA\u90FD\u4F1A\u95EE\u8FD9\u4E2A\u95EE\u9898\u3002\u5982\u679C\u75C7\u72B6\u5178\u578B\uFF0C\u6709\u7ECF\u9A8C\u7684\u533B\u751F\u4E00\u822C\u53EF\u4EE5\u6839\u636E\u75C5\u53F2\u548C\u8868\u73B0\u786E\u8BCA\u7684\uFF0C\u800C\u4E14\u590D\u53D1\u6027\u6E83\u75A1\u53D1\u75C5\u7387\u5F88\u9AD8\uFF0C\u6240\u4EE5\u4E0D\u9700\u8981\u5E38\u89C4\u505A\u5316\u9A8C\u3002\u53EA\u6709\u533B\u751F\u6000\u7591\u6709\u53EF\u7591\u60C5\u51B5\u65F6\uFF0C\u6BD4\u5982\u540C\u4E00\u4E2A\u6E83\u75A1\u6301\u7EED2\u5468\u4EE5\u4E0A\uFF0C\u624D\u53EF\u80FD\u4F1A\u5EFA\u8BAE\u8FDB\u4E00\u6B65\u505A\u5176\u5B83\u68C0\u67E5\u3002</p><p><strong>\u9700\u8981\u8865\u5145\u7EF4\u751F\u7D20\u5417\uFF1F</strong></p><p>\u5F88\u591A\u4EBA\u90FD\u8BA4\u4E3A\u590D\u53D1\u6027\u53E3\u8154\u6E83\u75A1\u662F\u56E0\u4E3A\u7EF4\u751F\u7D20\u7F3A\u4E4F\u5F15\u8D77\uFF0C\u6240\u4EE5\u53BB\u5403\u7EF4\u751F\u7D20\uFF0C\u7136\u800C\u4E8B\u5B9E\u5E76\u975E\u5982\u6B64\u3002\u5927\u90E8\u5206\u7684\u590D\u53D1\u6027\u53E3\u8154\u6E83\u75A1\u90FD\u4E0E\u7EF4\u751F\u7D20\u7F3A\u4E4F\u65E0\u5173\uFF0C\u4E0D\u9700\u8981\u5E38\u89C4\u8865\u5145\u7EF4\u751F\u7D20\u3002</p><p>\u4F46\u5BF9\u4E8E\u6709\u7EF4\u751F\u7D20B12\u6216\u94C1\u5143\u7D20\u7F3A\u4E4F\u76F8\u5173\u56E0\u7D20\u7684\u75C5\u4EBA\uFF0C\u6BD4\u5982\u957F\u671F\u7D20\u98DF\u3001\u6709\u6162\u6027\u8D2B\u8840\u3001\u6709\u80C3\u75C5\u6216\u505A\u8FC7\u80C3\u90E8\u5206\u5207\u9664\u624B\u672F\u7B49\uFF0C\u53EF\u4EE5\u8865\u5145\u76F8\u5E94\u7684\u7EF4\u751F\u7D20\u3002</p><p><strong>\u590D\u53D1\u6027\u53E3\u8154\u6E83\u75A1\u600E\u4E48\u6CBB\u7597\uFF1F</strong></p><p>\u590D\u53D1\u6027\u6E83\u75A1\u662F\u53EF\u4EE5\u81EA\u6108\uFF0C\u5BF9\u4E8E\u75C7\u72B6\u5F88\u8F7B\u7684\uFF0C\u9762\u79EF\u8F83\u5C0F\u7684\u6E83\u75A1\uFF0C\u53EF\u4EE5\u4FDD\u6301\u53E3\u8154\u536B\u751F\uFF0C\u5F85\u5176\u81EA\u6108\u3002\u56E0\u4E3A\u4E0A\u76AE\u7684\u722C\u884C\u751F\u957F\u4FEE\u590D\u901F\u5EA6\u662F7\u5929\u5DE6\u53F3\uFF0C\u56E0\u6B64\uFF0C\u5BF9\u4E8E\u5C0F\u5C0F\u7684\u6E83\u75A1\u800C\u8A00\uFF0C\u7528\u836F\u5E76\u4E0D\u80FD\u663E\u8457\u63D0\u9AD8\u6108\u5408\u901F\u5EA6\uFF0C\u6240\u8C13\u7684\u201C\u7528\u4E0D\u7528\u836F\u90FD\u4E00\u5468\u5C31\u597D\u4E86\u201D\u3002</p><p>\u4F46\u5BF9\u4E8E\u9762\u79EF\u5927\uFF0C\u6570\u91CF\u591A\u6216\u75BC\u75DB\u660E\u663E\u7684\u6E83\u75A1\uFF0C\u4E5F\u53EF\u4EE5\u7528\u4E00\u4E9B\u5916\u7528\u836F\u4FC3\u8FDB\u6108\u5408\uFF0C\u7F13\u89E3\u75C7\u72B6\uFF0C\u4E00\u7EBF\u7528\u836F\u662F\u7CD6\u76AE\u8D28\u6FC0\u7D20\u818F\u5242\u6216\u8D34\u7247\uFF0C\u6C28\u6765\u5360\u8BFA\uFF0C\u6F31\u53E3\u6C34\u7B49\u3002</p><p>\u53E3\u670D\u836F\u91CC\uFF0C\u4F7F\u7528\u6700\u4E3A\u5E7F\u6CDB\u7684\uFF0C\u540C\u65F6\u4E5F\u5B58\u5728\u6700\u591A\u4E34\u5E8A\u968F\u673A\u5BF9\u7167\u7814\u7A76\u7684\u5C31\u662F\u6C99\u5229\u5EA6\u80FA\uFF08\u598A\u5A20\u671F\u7981\u7528\uFF09\uFF0C\u5176\u6B21\u4E3A\u5DE6\u65CB\u54AA\u5511\u7B49\uFF0C\u4E25\u91CD\u7684\u4E5F\u53EF\u4EE5\u4F7F\u7528\u5C0F\u5242\u91CF\u6FC0\u7D20\u6CBB\u7597\uFF0C\u4F46\u53E3\u670D\u836F\u90FD\u9700\u8981\u5728\u533B\u751F\u6307\u5BFC\u4E0B\u670D\u7528\u3002</p><p>\u76EE\u524D\u8FD8\u6CA1\u6709\u9488\u5BF9\u513F\u7AE5\u8BBE\u8BA1\u7684\u6709\u660E\u786E\u968F\u673A\u5BF9\u7167\u7814\u7A76\u8BC1\u636E\u7684\u5B89\u5168\u4E14\u6709\u6548\u7684\u5916\u7528\u836F\u3002\u56E0\u6B64\uFF0C\u5BF9\u4E8E\u5076\u5C14\u53D1\u4F5C\uFF0C\u75C7\u72B6\u8F7B\u5FAE\u7684\u513F\u7AE5\u60A3\u8005\uFF0C\u5EFA\u8BAE\u4FDD\u6301\u53E3\u8154\u536B\u751F\uFF0C\u907F\u514D\u523A\u6FC0\u6027\u996E\u98DF\uFF0C\u5F85\u5176\u81EA\u6108\uFF1B\u540C\u65F6\u53EF\u4EE5\u5C40\u90E8\u4F7F\u7528\u6DA6\u6ED1\u5242\uFF0C\u51CF\u5C11\u6469\u64E6\uFF0C\u51CF\u5C11\u75BC\u75DB\u3002</p><p><strong>\u590D\u53D1\u6027\u53E3\u8154\u6E83\u75A1\u600E\u4E48\u9884\u9632\uFF1F</strong></p><p>\u56E0\u4E3A\u53D1\u75C5\u539F\u56E0\u4E0D\u662F\u5F88\u6E05\u695A\uFF0C\u800C\u4E14\u53EF\u80FD\u548C\u5148\u5929\u6027\u56E0\u7D20\u6709\u5173\uFF0C\u56E0\u6B64\u4E0D\u80FD\u6839\u6CBB\u3002\u5C3D\u91CF\u907F\u514D\u8BF1\u56E0\uFF0C\u5982\u7CBE\u795E\u7D27\u5F20\u3001\u751F\u6D3B\u4E0D\u89C4\u5F8B\u7B49\u8FD9\u4E9B\u8BF1\u53D1\u56E0\u7D20\uFF0C\u53EF\u4EE5\u51CF\u5C11\u6E83\u75A1\u53D1\u751F\u7684\u9891\u7387\u3002\u5982\u679C\u603B\u662F\u5728\u5403\u5DE7\u514B\u529B\u3001\u5496\u5561\u3001\u8FA3\u6912\u7B49\u7279\u5B9A\u98DF\u7269\u540E\u51FA\u73B0\u6E83\u75A1\uFF0C\u90A3\u5C31\u53EF\u4EE5\u56DE\u907F\u4E00\u4E0B\u8FD9\u4E9B\u98DF\u7269\u3002</p><p>\u6700\u540E\uFF0C\u5982\u679C\u9664\u4E86\u53CD\u590D\u53E3\u8154\u6E83\u75A1\u4E4B\u5916\uFF0C\u8FD8\u540C\u65F6\u4F34\u5168\u8EAB\u75C7\u72B6\uFF0C\u5982\u6709\u8179\u6CFB\u3001\u76AE\u75B9\u3001\u751F\u6B96\u5668\u6E83\u75A1\u3001\u5468\u671F\u6027\u53D1\u70ED\u3001\u6DCB\u5DF4\u7ED3\u8282\u708E\u7B49\uFF0C\u5EFA\u8BAE\u8FDB\u4E00\u6B65\u5C31\u8BCA\uFF0C\u6392\u9664\u7CFB\u7EDF\u6027\u75BE\u75C5\u3002</p><hr><p><strong>\u5173\u6CE8\u6021\u79BE\u5065\u5EB7\uFF0C\u53EF\u54A8\u8BE2\u533B\u751F</strong></p>",26),x={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627789&idx=1&sn=35667fb69958c2f78e75c6255cf1359f&chksm=8b5c88f7bc2b01e1cb5d7b5bbdf792f8ff714834196d95286b25e7e58bb9583441507c155e0e&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},z=t("\u67E5\u770B\u539F\u6587");function k(N,O){const o=p("RouterLink"),r=p("ExternalLinkIcon");return c(),a(_,null,[l,e("p",null,[h,n(o,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651627772&idx=1&sn=7403a04b694a39238f7fcd18822e7254/"},{default:s(()=>[b]),_:1}),g]),e("p",null,[n(o,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651627772&idx=1&sn=7403a04b694a39238f7fcd18822e7254/"},{default:s(()=>[m]),_:1})]),e("p",null,[n(o,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651627772&idx=1&sn=7403a04b694a39238f7fcd18822e7254/"})]),u,e("p",null,[e("a",x,[z,n(r)])])],64)}var w=i(f,[["render",k]]);export{w as default};
