import{r as t,o as n,c as o,a as e,b as a,F as s,d as r,e as c}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const d={},h=r('<h3 id="\u624B\u8DB3\u53E3\u75C5\u9700\u8981\u7528\u5229\u5DF4\u97E6\u6797\u8FD9\u4E9B\u6297\u75C5\u6BD2\u836F\u5417" tabindex="-1"><a class="header-anchor" href="#\u624B\u8DB3\u53E3\u75C5\u9700\u8981\u7528\u5229\u5DF4\u97E6\u6797\u8FD9\u4E9B\u6297\u75C5\u6BD2\u836F\u5417" aria-hidden="true">#</a> \u624B\u8DB3\u53E3\u75C5\u9700\u8981\u7528\u5229\u5DF4\u97E6\u6797\u8FD9\u4E9B\u6297\u75C5\u6BD2\u836F\u5417\uFF1F</h3><p>\u8F6C\u773C\u5DF2\u662F\u4E94\u6708\uFF0C\u53C8\u5230\u4E86\u624B\u8DB3\u53E3\u75C5\u6D41\u884C\u7684\u5B63\u8282\uFF0C\u6240\u4EE5\u6700\u8FD1\u5728\u6021\u79BE\u533B\u751F\u7FA4\u91CC\uFF0C\u6709\u4EBA\u95EE\u624B\u8DB3\u53E3\u75C5\u8981\u4E0D\u8981\u6297\u75C5\u6BD2\u6CBB\u7597\u3002</p><p>\u533B\u751F\u6709\u8FD9\u6837\u7684\u56F0\u60D1\uFF0C\u4E5F\u4E0D\u662F\u5B8C\u5168\u4E0D\u80FD\u7406\u89E3\u3002\u53BB\u5E74\u56FD\u5BB6\u536B\u5065\u59D4\u53D1\u5E03\u4E86\u4E00\u4EFD\u65B0\u7684\u300A\u624B\u8DB3\u53E3\u75C5\u8BCA\u7597\u6307\u5357\u300B\u3002\u76F8\u6BD4\u4E8E2010\u5E74\u7684\u7248\u672C\uFF0C\u867D\u7136\u5220\u9664\u4E86\u5BF9\u70ED\u6BD2\u5B81\u6CE8\u5C04\u6DB2\u3001\u559C\u708E\u5E73\u6CE8\u5C04\u6DB2\u3001\u4E39\u53C2\u6CE8\u5C04\u6DB2\u8FD9\u4E9B\u4E2D\u836F\u6CE8\u5C04\u6DB2\u7684\u63A8\u8350\uFF0C\u4F46\u5BF9\u6297\u75C5\u6BD2\u6CBB\u7597\u7684\u8868\u8FF0\u5374\u6A21\u51CC\u4E24\u53EF\u3002</p><p>\u8FD9\u4EFD\u6307\u5357\u91CC\uFF0C\u4E00\u9762\u8BF4\u76EE\u524D\u5C1A\u65E0\u7279\u6548\u6297\u75C5\u6BD2\u836F\u7269\uFF0C\u540E\u9762\u53C8\u63A5\u7740\u8BF4\uFF1A\u7814\u7A76\u663E\u793A\uFF0C\u5E72\u6270\u7D20\u03B1\u55B7\u96FE\u6216\u96FE\u5316\u3001\u5229\u5DF4\u97E6\u6797\u9759\u8109\u6EF4\u6CE8\u65E9\u671F\u53EF\u6709\u4E00\u5B9A\u7597\u6548\u3002</p><p>\u53EF\u60F3\u800C\u4E4B\uFF0C\u533B\u751F\u770B\u4E86\u8FD9\u6837\u7684\u8868\u8FF0\uFF0C\u96BE\u514D\u4F1A\u60F3\uFF1A\u65E2\u7136\u6709\u4E00\u5B9A\u7597\u6548\uFF0C\u90A3\u8BF4\u660E\u8FD8\u662F\u53EF\u4EE5\u7528\u7684\u561B\u3002\u6240\u4EE5\u6709\u4EBA\u4F1A\u56F0\u60D1\uFF0C\u6709\u4EBA\u4F1A\u5FC3\u5B89\u7406\u5F97\u5730\u7ED9\u624B\u8DB3\u53E3\u75C5\u548C\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u7684\u5B69\u5B50\u5F00\u5229\u5DF4\u97E6\u6797\u548C\u5E72\u6270\u7D20\u3002</p><p>\u6211\u5F88\u60F3\u77E5\u9053\u8FD9\u4EFD\u6307\u5357\u91CC\u5199\u8FD9\u6837\u610F\u89C1\u7684\u4F9D\u636E\u662F\u4EC0\u4E48\uFF0C\u4F46\u5B83\u53EA\u8BF4\u201C\u7814\u7A76\u663E\u793A\u201D\u5374\u6CA1\u6709\u5217\u51FA\u76F8\u5E94\u7684\u53C2\u8003\u6587\u732E\uFF0C\u8FD9\u662F\u56FD\u5185\u5F88\u591A\u6307\u5357\u7684\u901A\u75C5\uFF1A\u53EA\u7ED9\u7ED3\u8BBA\uFF0C\u4E0D\u7ED9\u4F9D\u636E\u3002</p><p>\u6CA1\u7ED9\u4F9D\u636E\u4E0D\u7B49\u4E8E\u4E0D\u53EF\u4EE5\u53BB\u67E5\uFF0C\u6BD5\u7ADF\u4F5C\u8005\u8BA4\u4E3A\u6709\u4EF7\u503C\u7684\u533B\u5B66\u7814\u7A76\u90FD\u4F1A\u53D1\u8868\uFF0C\u53D1\u8868\u540E\u5927\u5BB6\u90FD\u662F\u53EF\u4EE5\u67E5\uFF0C\u4E0APubmed\u4E0A\u4EE5\u624B\u8DB3\u53E3\u75C5+\u5229\u5DF4\u97E6\u6797\u4E3A\u5173\u952E\u8BCD\uFF0C\u603B\u5171\u641C\u523019\u7BC7\u6587\u7AE0\uFF0C\u5176\u4E2D15\u7BC7\u662F\u6211\u4EEC\u4E2D\u56FD\u4EBA\u5199\u7684\uFF0C\u5176\u4E2D6\u7BC7\u662F\u53D1\u8868\u5728\u4E2D\u6587\u671F\u520A\u4E0A\u7684\uFF0C\u770B\u6765\u6211\u4EEC\u4E2D\u56FD\u4EBA\u6700\u70ED\u8877\u4E8E\u5229\u5DF4\u97E6\u6797\u6CBB\u7597\u624B\u8DB3\u53E3\u75C5\u7684\u7814\u7A76\u3002</p><p>\u5728\u8FD919\u7BC7\u6587\u7AE0\u91CC\uFF0C\u4E34\u5E8A\u7814\u7A76\u53EA\u67092\u7BC7[1,2]\uFF0C\u90FD\u662F\u7528\u9A8C\u8BC1\u5229\u5DF4\u97E6\u6797\u6C14\u96FE\u5242\u5BF9\u8F7B\u578B\u624B\u8DB3\u53E3\u75C5\u7684\u6548\u679C\uFF0C\u7ED3\u8BBA\u90FD\u662F\u5229\u5DF4\u97E6\u6797\u6C14\u96FE\u5242\u4E34\u5E8A\u7597\u6548\u597D\u800C\u4E14\u4E0D\u826F\u53CD\u5E94\u5C11\uFF0C\u800C\u4E14\u8FD9\u4E24\u4E2A\u7814\u7A76\u7684\u4E3B\u8981\u4F5C\u8005\u90FD\u6765\u540C\u4E00\u5BB6\u533B\u9662\uFF0C\u4E0A\u6D77\u513F\u7AE5\u533B\u5B66\u4E2D\u5FC3\uFF0C\u800C\u4E14\u75C5\u4EBA\u6765\u6E90\u7684\u65F6\u95F4\u4E5F\u6709\u91CD\u53E0\uFF0C\u4E00\u4E2A\u662F2011\u5E748\u6708\u52302012\u5E748\u6708\uFF0C\u4E00\u4E2A\u662F2011\u5E747\u6708-2013\u5E746\u6708\u3002</p><p>\u624B\u8DB3\u53E3\u75C5\u662F\u80A0\u9053\u75C5\u6BD2\u611F\u67D3\u5F15\u8D77\u7684\u5168\u8EAB\u611F\u67D3\uFF0C\u5982\u679C\u67D0\u79CD\u836F\u7269\u6709\u6297\u75C5\u6BD2\u4F5C\u7528\uFF0C\u4E5F\u662F\u9700\u8981\u8FDB\u5165\u8840\u6DB2\uFF0C\u5E76\u8FBE\u5230\u4E00\u5B9A\u8840\u836F\u6D53\u5EA6\u624D\u4F1A\u6709\u7528\uFF0C\u4ECE\u63A7\u5236\u8840\u836F\u6D53\u5EA6\u7684\u89D2\u5EA6\u53BB\u8003\u8651\uFF0C\u80AF\u5B9A\u662F\u9759\u8109\u7ED9\u836F\u6216\u53E3\u670D\u7ED9\u836F\u4F1A\u66F4\u51C6\u786E\uFF0C\u4F46\u5947\u602A\u7684\u662F\uFF0C\u8FD9\u4E24\u4E2A\u7814\u7A76\u7684\u7528\u836F\u90FD\u6765\u81EA\u540C\u4E00\u5BB6\u836F\u5382\uFF0C\u4E0A\u6D77\u4FE1\u8C0A\u5236\u836F\u5382\uFF0C\u800C\u4E14\u90FD\u662F\u6C14\u96FE\u5242\u578B\u7684\u5229\u5DF4\u97E6\u6797\u3002</p><p>\u5728\u300A\u5229\u5DF4\u97E6\u6797\u6C14\u96FE\u5242\u5BF9\u624B\u8DB3\u53E3\u75C5\u60A3\u513F\u6392\u6BD2\u7684\u5F71\u54CD\u300B\u8FD9\u7BC7\u6587\u7AE0\u4E2D\uFF0C\u4F5C\u8005\u5728\u8BA8\u8BBA\u5206\u6790\u7684\u65F6\u5019\u8BF4\uFF1A\u201C\u55B7\u96FE\u76F4\u63A5\u4F5C\u7528\u4E8E\u76AE\u80A4\u3001\u9ECF\u819C\uFF0C\u6E17\u900F\u81F3\u75B1\u75B9\u5185\u90E8\u6740\u706D\u75C5\u6BD2\uFF0C\u4FC3\u4F7F\u75B1\u75B9\u6108\u5408\u201D\uFF0C\u4F3C\u4E4E\u662F\u5728\u8BF4\u7528\u5229\u5DF4\u97E6\u6797\u6C14\u96FE\u5242\u6740\u706D\u76AE\u80A4\u9ECF\u819C\u7684\u75C5\u6BD2\uFF0C\u5C31\u4EA7\u751F\u4E86\u6548\u679C\u3002\u8FD9\u5C31\u50CF\u544A\u8BC9\u6211\u4EEC\u4E00\u4E2A\u9E21\u86CB\u574F\u4E86\uFF0C\u5728\u86CB\u58F3\u4E0A\u62B9\u70B9\u6D88\u6BD2\u6C34\uFF0C\u5C31\u628A\u9E21\u86CB\u53D8\u597D\u4E86\u4E00\u6837\uFF0C\u8FD9\u4E5F\u8FC7\u4E8E\u795E\u5947\u4E86\u3002</p><p>\u5728\u300A\u5229\u5DF4\u97E6\u6797\u6C14\u96FE\u5242\u6CBB\u7597\u5C0F\u513F\u624B\u8DB3\u53E3\u75C5\u7684\u6709\u6548\u6027\u548C\u5B89\u5168\u6027\u7814\u7A76\u300B\u8FD9\u7BC7\u6587\u7AE0\u4E2D\uFF0C\u4F5C\u8005\u5728\u89E3\u91CA\u9009\u7528\u6C14\u96FE\u5242\u7684\u89E3\u91CA\u4E2D\uFF0C\u6709\u4E0B\u9762\u4E00\u6BB5\u8BDD\u3002</p><p>\u5BF9\u7167\u539F\u6587\u53C2\u8003\u6587\u732E\u7684 [12] \u53BB\u770B\u4E86\u4E00\u4E0B\uFF0C\u8BB2\u7684\u5374\u662F\u6D41\u611F\u7684\u6C14\u96FE\u548C\u7ECF\u9F3B\u7ED9\u836F\u6CBB\u7597\uFF0C\u6839\u672C\u5C31\u6CA1\u8BF4\u5229\u5DF4\u97E6\u6797\u6C14\u96FE\u5242\u6CBB\u7597\u624B\u8DB3\u53E3\u75C5\u7684\u4E8B\uFF0C\u4E0D\u77E5\u9053\u4E3A\u4EC0\u4E48\u8981\u8FD9\u6837\u6B6A\u66F2\u539F\u6587\u610F\u601D\u6765\u5F3A\u884C\u8BBA\u8BC1\u5229\u5DF4\u97E6\u6797\u6C14\u96FE\u5242\u6CBB\u7597\u624B\u8DB3\u53E3\u75C5\u7684\u5408\u7406\u6027\u3002</p><p>\u6240\u4EE5\uFF0C\u6307\u5357\u91CC\u8BF4\u6709\u7814\u7A76\u663E\u793A\uFF0C\u786E\u5B9E\u662F\u6709\u7814\u7A76\uFF0C\u4F46\u4E0D\u662F\u6307\u5357\u91CC\u8BF4\u7684\u9759\u8109\u6EF4\u6CE8\u5229\u5DF4\u97E6\u6797\uFF0C\u800C\u662F\u6C14\u96FE\u55B7\u5242\uFF0C\u4F46\u8FD9\u6765\u81EA\u56FD\u5185\u540C\u4E00\u5BB6\u533B\u9662\u7684\u8FD9\u4E24\u4E2A\u7814\u7A76\uFF0C\u6211\u662F\u4E0D\u6562\u76F8\u4FE1\u7684\u3002</p><p>\u5176\u5B9E\u5C31\u56FD\u5185\u51FA\u6765\u7684\u7814\u7A76\u8BC1\u636E\u800C\u8A00\uFF0C\u53EA\u8981\u4F60\u60F3\u8981\uFF0C\u51E0\u4E4E\u4EC0\u4E48\u8BC1\u636E\u90FD\u80FD\u627E\u5230\uFF0C\u751A\u81F3\u8FDE\u836F\u5382\u90FD\u60F3\u4E0D\u5230\u7684\u529F\u6548\uFF0C\u6BD4\u5982\u5965\u53F8\u4ED6\u97E6\u6CBB\u7597\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\uFF0C\u6211\u4EEC\u56FD\u4EBA\u4E5F\u80FD\u505A\u51FA\u8BC1\u636E\u6765\u3002</p><p>\u5C31\u7B97\u4E0A\u9762\u90A3\u4E24\u4E2A\u5229\u5DF4\u97E6\u6797\u7684\u7814\u7A76\u6570\u636E\u662F\u771F\u7684\uFF0C\u5BF9\u4E8E\u624B\u8DB3\u53E3\u75C5\u8FD9\u6837\u672C\u6765\u5C31\u6301\u7EED\u65F6\u95F4\u4E0D\u957F\u7684\u81EA\u6108\u6027\u75BE\u75C5\uFF0C\u5982\u679C\u53EA\u662F\u7F29\u77ED\u4E00\u70B9\u8F7B\u578B\u624B\u8DB3\u53E3\u75C5\u7684\u75C7\u72B6\u6301\u7EED\u65F6\u95F4\uFF0C\u800C\u4F7F\u7528\u6EB6\u8840\u6027\u8D2B\u8840\u3001\u767D\u7EC6\u80DE\u51CF\u5C11\u7B49\u4E0D\u826F\u53CD\u5E94\u660E\u663E\u7684\u5229\u5DF4\u97E6\u6797\uFF0C\u663E\u7136\u662F\u4E0D\u503C\u5F97\u7684\uFF0C\u8FD9\u6837\u7684\u4E34\u5E8A\u8BD5\u9A8C\u5728\u56FD\u5916\u5F88\u53EF\u80FD\u65E0\u6CD5\u901A\u8FC7\u4F26\u7406\u5BA1\u6838\u3002\u8FD9\u4E5F\u662F\u4E3A\u4EC0\u4E48Pubmed\u91CC\u641C\u5230\u7684\uFF0C\u4E0D\u8BBA\u662F\u5229\u5DF4\u97E6\u6797\u8FD8\u662F\u5E72\u6270\u7D20\uFF0C\u6CBB\u7597\u624B\u8DB3\u53E3\u75C5\u7684\u4E34\u5E8A\u7814\u7A76\uFF0C\u4EC5\u6709\u7684\u51E0\u7BC7\u6587\u7AE0\u5168\u90FD\u662F\u6211\u4EEC\u4E2D\u56FD\u4EBA\u5199\u7684\u3002</p><p>\u800C\u5BF9\u4E8E\u624B\u8DB3\u53E3\u75C5\u7684\u6297\u75C5\u6BD2\u7528\u836F\u95EE\u9898\uFF0C\u56FD\u5916\u7684\u6743\u5A01\u673A\u6784\u7684\u76F8\u5173\u89C2\u70B9\u662F\u8FD9\u6837\u7684\u3002</p><p><strong>\u4E16\u754C\u536B\u751F\u7EC4\u7EC7</strong>[3]\uFF1A\u624B\u8DB3\u53E3\u75C5\u4E34\u5E8A\u6CBB\u7597\u4EE5\u5BF9\u75C7\u652F\u6301\u4E3A\u4E3B\uFF0C\u6CA1\u6709\u9488\u5BF9\u6027\u7684\u6297\u75C5\u6BD2\u836F\u7269\u3002</p><p><strong>\u7F8E\u56FDCDC</strong>[4]\uFF1A\u624B\u8DB3\u53E3\u75C5\u6CA1\u6709\u7279\u6548\u6CBB\u7597\u3002</p><p><strong>\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A</strong>[5]\uFF1A\u6CA1\u6709\u836F\u80FD\u6CBB\u7597\u6216\u6CBB\u6108\u624B\u8DB3\u53E3\u75C5\u3002</p><p><strong>\u82F1\u56FDNHS</strong>[6]\uFF1A\u4E0D\u80FD\u9760\u5403\u6297\u751F\u7D20\u6216\u836F\u6765\u6CBB\u7597\u624B\u8DB3\u53E3\u75C5\u3002</p><p>\u56FD\u5BB6\u536B\u5065\u59D4\u53D1\u5E03\u7684\u6307\u5357\uFF0C\u901A\u5E38\u4F1A\u7EC4\u7EC7\u4E34\u5E8A\u533B\u751F\u8FDB\u884C\u5B66\u4E60\uFF0C\u53EF\u4EE5\u60F3\u8C61\u8FD9\u4EFD\u6307\u5357\u53EF\u80FD\u7ED9\u533B\u751F\u7528\u836F\u5E26\u6765\u7684\u5F71\u54CD\uFF0C\u6240\u4EE5\u5982\u679C\u5B69\u5B50\u5F97\u4E86\u624B\u8DB3\u53E3\u75C5\u6216\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\uFF0C\u5230\u533B\u9662\u88AB\u5F00\u4E86\u5229\u5DF4\u97E6\u6797\u6216\u5E72\u6270\u7D20\uFF0C\u4E00\u70B9\u4E5F\u4E0D\u7528\u5947\u602A\u3002</p><p>\u533B\u751F\u4EEC\u9700\u8981\u77E5\u9053\uFF0C\u53EA\u6709\u610F\u89C1\u800C\u6CA1\u6709\u53EF\u4FE1\u8BC1\u636E\u7684\u6307\u5357\uFF0C\u662F\u4E0D\u53EF\u4FE1\u7684\uFF0C\u5236\u5B9A\u6307\u5357\u7684\u8FD9\u4E9B\u4EBA\uFF0C\u5982\u679C\u6709\u57FA\u672C\u7684\u8BC1\u636E\u610F\u8BC6\uFF0C\u5C31\u4E0D\u4F1A\u5728\u4EE5\u524D\u7684\u7248\u672C\u91CC\u63A8\u8350\u4E2D\u836F\u6CE8\u5C04\u6DB2\u4E86\u3002</p><p>\u5BB6\u957F\u4EEC\u5E94\u8BE5\u77E5\u9053\uFF0C\u65E0\u8BBA\u662F\u624B\u8DB3\u53E3\u75C5\u8FD8\u662F\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\uFF0C\u5229\u5DF4\u97E6\u6797\u548C\u5E72\u6270\u7D20\u8FD9\u6837\u7684\u6297\u75C5\u6BD2\u836F\u7269\uFF0C\u90FD\u662F\u6CA1\u5FC5\u8981\u7528\u7684\u3002</p><p><strong>\u53C2\u8003\u8D44\u6599</strong>\uFF1A</p><p>[1]https://www.ncbi.nlm.nih.gov/pubmed/25146745</p><p>[2]https://www.ncbi.nlm.nih.gov/pubmed/24661520</p><p>[3]http://www.wpro.who.int/publications/docs/GuidancefortheclinicalmanagementofHFMD.pdf</p><p>[4]https://www.cdc.gov/hand-foot-mouth/about/prevention-treatment.html</p><p>[5]https://www.healthychildren.org/English/health-issues/conditions/infections/Pages/Hand-Foot-and-Mouth-Disease.aspx</p><p>[6] https://www.nhs.uk/conditions/hand-foot-mouth-disease/</p><hr>',31),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651628180&idx=1&sn=a65f30ab10245995a5806b8894327d9c&chksm=8b5c8b6ebc2b02783b1984f19231379a1cdfa93144ff2a6ebcc21f67ea24876a1a3a423a3e12&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},m=c("\u67E5\u770B\u539F\u6587");function _(f,w){const p=t("ExternalLinkIcon");return n(),o(s,null,[h,e("p",null,[e("a",l,[m,a(p)])])],64)}var u=i(d,[["render",_]]);export{u as default};
