import{r as a,o as r,c,a as e,b as t,F as n,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=o('<h3 id="\u5B69\u5B50\u4E0D\u597D\u597D\u5403\u996D-\u5403\u8D56\u6C28\u9178b12\u6709\u7528\u5417" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u4E0D\u597D\u597D\u5403\u996D-\u5403\u8D56\u6C28\u9178b12\u6709\u7528\u5417" aria-hidden="true">#</a> \u5B69\u5B50\u4E0D\u597D\u597D\u5403\u996D\uFF0C\u5403\u8D56\u6C28\u9178B12\u6709\u7528\u5417\uFF1F</h3><p>\u5B69\u5B50\u4E0D\u5403\u996D\uFF0C\u6311\u98DF\uFF0C\u4E0D\u957F\u8089\uFF0C\u8FD9\u4E9B\u95EE\u9898\u56F0\u6270\u7740\u5F88\u591A\u5BB6\u957F\uFF0C\u5404\u79CD\u529E\u6CD5\u90FD\u8BD5\u8FC7\u540E\uFF0C\u6709\u7684\u5BB6\u957F\u4F1A\u5E26\u5B69\u5B50\u53BB\u770B\u533B\u751F\u3002</p><p>\u5230\u4E86\u533B\u9662\uFF0C\u533B\u751F\u53EF\u80FD\u4F1A\u8BA9\u67E5\u5FAE\u91CF\u5143\u7D20\uFF0C\u6216\u5F00\u9C7C\u809D\u6CB9\uFF0C\u6216\u5EFA\u8BAE\u8865\u9499\u3001\u8865\u950C\uFF0C\u6709\u65F6\u8FD8\u4F1A\u5F00\u4E0A\u4E00\u79CD\u53EB\u8D56\u6C28\u9178B12\u7684\u836F\uFF0C\u8BF4\u80FD\u6539\u5584\u80C3\u53E3\u4FC3\u8FDB\u53D1\u80B2\u3002</p><p>\u62FF\u5230\u8FD9\u4E2A\u836F\uFF0C\u770B\u770B\u8BF4\u660E\u4E66\uFF0C\u4E0A\u9762\u9002\u5E94\u75C7\u5199\u7740\uFF1A\u52A0\u901F\u513F\u7AE5\u751F\u957F\u53D1\u80B2\u3001\u589E\u8FDB\u98DF\u6B32\uFF1B\u6D88\u9664\u5F02\u98DF\u7656\uFF1B\u6709\u52A9\u4E8E\u5927\u8111\u53D1\u80B2\u548C\u63D0\u9AD8\u667A\u529B\uFF1B\u6709\u6297\u8D2B\u8840\u3001\u589E\u5F3A\u4F53\u8D28\u548C\u6297\u75C5\u7684\u529F\u6548\u3002</p><p>\u4E0D\u4F46\u770B\u7740\u5BF9\u8DEF\uFF0C\u4F3C\u4E4E\u8FD8\u6709\u4E9B\u60CA\u559C\uFF0C\u65E2\u80FD\u6539\u5584\u5B69\u5B50\u98DF\u6B32\uFF0C\u8FD8\u80FD\u63D0\u9AD8\u5B69\u5B50\u667A\u529B\uFF0C\u54EA\u4E2A\u5BB6\u957F\u4E0D\u60F3\u8981\u5462\uFF0C\u4F46\u8FD9\u771F\u7684\u6709\u7528\u5417\uFF1F</p><p>\u8D56\u6C28\u9178\u662F\u4E00\u79CD\u4EBA\u4F53\u5FC5\u987B\u6C28\u57FA\u9178\uFF0C\u5982\u679C\u7F3A\u4E4F\u53EF\u80FD\u5BFC\u81F4\u8D2B\u8840\u3001\u75B2\u5026\u3001\u98DF\u6B32\u4E0D\u632F\u3001\u5934\u6655\u3001\u773C\u775B\u5145\u8840\u3001\u8131\u53D1\u7B49\u95EE\u9898\uFF0C\u5E76\u5F71\u54CD\u751F\u957F\u53D1\u80B2\u3002</p><p>\u6839\u636E\u7F8E\u56FD\u98DF\u54C1\u8425\u517B\u59D4\u5458\u4F1A\u7684\u5EFA\u8BAE\uFF0C\u4E0D\u540C\u5E74\u9F84\u6BB5\u513F\u7AE5\u8D56\u6C28\u9178\u7684\u9700\u8981\u91CF\u5982\u4E0B\u8868\u3002</p><p>\u6839\u636E\u4E0A\u56FE\uFF0C\u4E00\u4E2A10\u516C\u65A4\u91CD\u76841\u5C81\u5B69\u5B50\uFF0C\u6216\u8005\u4E00\u4E2A15\u516C\u65A4\u91CD\u76843\u5C81\u5B69\u5B50\uFF0C\u65E5\u63A8\u8350\u4F9B\u7ED9\u91CF\u5206\u522B\u4E3A890mg\u548C870mg\u3002</p><p>\u4EBA\u4F53\u81EA\u5DF1\u4E5F\u65E0\u6CD5\u5408\u6210\u8D56\u6C28\u9178\uFF0C\u6240\u4EE5\u9700\u8981\u4ECE\u5916\u90E8\u8865\u5145\u3002\u5BCC\u542B\u52A8\u3001\u690D\u7269\u86CB\u767D\u7684\u98DF\u7269\uFF0C\u5927\u591A\u542B\u6709\u8F83\u591A\u8D56\u6C28\u9178\uFF0C\u6BD4\u5982\u5976\u5236\u54C1\u3001\u8089\u7C7B\u3001\u9E21\u86CB\u3001\u8C46\u7C7B\u3001\u575A\u679C\u7B49\uFF0C\u6BD4\u5982\uFF1A</p><p>1\u4E2A45\u514B\u7684\u9E21\u86CB\u5927\u7EA6\u542B\u6709369mg\u8D56\u6C28\u9178\uFF1B</p><p>50\u514B\u7684\u732A\u817F\u8089\u5927\u7EA6\u542B\u6709831mg\u7684\u8D56\u6C28\u9178\uFF1B</p><p>250\u514B\u7684\u725B\u5976\uFF0C\u5927\u7EA6\u6709652mg\u7684\u8D56\u6C28\u9178</p><p>\u7B97\u4E0B\u6765\uFF0C\u5927\u7EA62\u4E2A\u591A\u4E00\u70B9\u9E21\u86CB\uFF0C\u6216\u800560\u514B\u7684\u732A\u817F\u8089\uFF0C\u6216\u8005350ml\u7684\u725B\u5976\uFF0C\u5C31\u53EF\u4EE5\u6EE1\u8DB3\u90A3\u4E24\u4E2A\u5B69\u5B50\u5BF9\u8D56\u6C28\u9178\u7684\u9700\u6C42\u3002</p><p>\u5B69\u5B50\u7684\u996E\u98DF\u5E76\u4E0D\u662F\u5355\u4E00\u7684\uFF0C\u4ED6\u4EEC\u4E00\u5929\u5185\u65E2\u53EF\u80FD\u559D\u5976\uFF0C\u8FD8\u53EF\u80FD\u5403\u9E21\u86CB\u3001\u5403\u8089\uFF0C\u5403\u8C46\u7C7B\u7B49\u5BCC\u542B\u8D56\u6C28\u9178\u7684\u98DF\u7269\uFF0C\u6240\u4EE5\u53EA\u8981\u4E0D\u662F\u5728\u7ECF\u6D4E\u843D\u540E\u5230\u5403\u4E0D\u5230\u4E1C\u897F\uFF0C\u6216\u8005\u7EAF\u5403\u7D20\u7684\u5B69\u5B50\uFF0C\u662F\u4E0D\u592A\u53EF\u80FD\u51FA\u73B0\u8D56\u6C28\u9178\u7F3A\u4E4F\u3002</p><p>\u7F3A\u4E4F\u8D56\u6C28\u9178\u7684\u5B69\u5B50\uFF0C\u8865\u5145\u8D56\u6C28\u9178\u53EF\u4EE5\u6539\u5584\u56E0\u4E3A\u7F3A\u4E4F\u8D56\u6C28\u9178\u5BFC\u81F4\u7684\u75C7\u72B6\uFF0C\u4F46\u5BF9\u4E8E\u4E0D\u5B58\u5728\u8D56\u6C28\u9178\u7F3A\u4E4F\u7684\u5B69\u5B50\uFF0C\u4E0D\u8BBA\u4F60\u8865\u591A\u5C11\u8D56\u6C28\u9178\uFF0C\u4E5F\u4E0D\u4F1A\u5BF9\u98DF\u6B32\u548C\u751F\u957F\u53D1\u80B2\u6709\u4EFB\u4F55\u6539\u5584\uFF0C\u66F4\u522B\u8BF4\u4EC0\u4E48\u63D0\u9AD8\u667A\u529B\u4E86\u3002</p><p>\u7EF4\u751F\u7D20B12\u4E5F\u662F\u4E00\u6837\u7684\uFF0C\u5728\u52A8\u7269\u6027\u98DF\u7269\u91CC\u5E7F\u6CDB\u5B58\u5728\uFF0C\u4E0D\u662F\u7D20\u98DF\u6216\u505A\u4E86\u80C3\u5927\u90E8\u5207\u9664\u624B\u672F\u6216\u6709\u80C3\u708E\u7684\u75C5\u4EBA\uFF0C\u662F\u4E0D\u7528\u62C5\u5FC3\u7F3A\u4E4F\u95EE\u9898\uFF0C\u5BF9\u4E0D\u7F3A\u4E4F\u7684\u4EBA\u7FA4\uFF0C\u4E0D\u8BBA\u600E\u4E48\u8865\u5145\uFF0C\u4E5F\u4E0D\u4F1A\u6709\u4EFB\u4F55\u529F\u6548\u3002</p><p>\u800C\u4E14\uFF0C\u5168\u4E16\u754C\u4E5F\u627E\u4E0D\u5230\u4E00\u79CD\u80FD\u52A0\u901F\u5B69\u5B50\u53D1\u80B2\uFF0C\u80FD\u63D0\u9AD8\u667A\u529B\u7684\u836F\uFF0C\u66F4\u4F55\u51B5\u52A0\u901F\u53D1\u80B2\u4E5F\u5E76\u4E0D\u662F\u4EC0\u4E48\u597D\u4E8B\uFF0C\u4E0D\u77E5\u9053\u4E2D\u56FD\u7684\u836F\u76D1\u90E8\u95E8\u662F\u600E\u4E48\u628A\u8FD9\u4E9B\u529F\u6548\u6279\u51C6\u4E86\u4E0B\u6765\u3002</p><p>\u5B69\u5B50\u4E0D\u597D\u597D\u5403\u996D\uFF0C\u539F\u56E0\u4F1A\u6709\u5F88\u591A\uFF0C\u6709\u7684\u53EF\u80FD\u662F\u6D88\u5316\u9053\u6216\u5176\u5B83\u95EE\u9898\uFF0C\u6BD4\u5982\u80C3\u98DF\u7BA1\u8FD4\u6D41\u75C5\u3001\u4EE3\u8C22\u6027\u75BE\u75C5\u3001\u7CBE\u795E\u5FC3\u7406\u95EE\u9898\u7B49\u3002\u6392\u9664\u4E86\u75C5\u7406\u56E0\u7D20\uFF0C\u8FD8\u9700\u8981\u4E86\u89E3\u5B69\u5B50\u996E\u98DF\u4E60\u60EF\uFF0C\u8FD8\u9700\u8981\u4E86\u89E3\u884C\u4E3A\u7BA1\u6559\u65B9\u5F0F\uFF0C\u8FD8\u6709\u5F88\u591A\u6240\u8C13\u7684\u5B69\u5B50\u4E0D\u597D\u597D\u5403\u996D\uFF0C\u5176\u5B9E\u662F\u5BB6\u957F\u81EA\u5DF1\u5BF9\u5B69\u5B50\u7684\u98DF\u91CF\u53CA\u751F\u957F\u53D1\u80B2\u89C4\u5F8B\u4E0D\u4E86\u89E3\uFF0C\u6CA1\u8FBE\u5230\u81EA\u5DF1\u7684\u671F\u671B\uFF0C\u6216\u8005\u6CA1\u6709\u6309\u81EA\u5DF1\u671F\u671B\u7684\u65B9\u5F0F\u5403\u996D\u5C31\u5224\u65AD\u4E3A\u4E0D\u597D\u597D\u5403\u996D\u3002</p><p>\u5728\u516C\u7ACB\u533B\u9662\u73B0\u5728\u7684\u5C31\u8BCA\u6A21\u5F0F\u4E0B\uFF0C\u533B\u751F\u6BCF\u5929\u770B\u51E0\u5341\u4E0A\u767E\u4E2A\u75C5\u4EBA\uFF0C\u6BCF\u4E2A\u75C5\u4EBA\u6C9F\u901A\u65F6\u95F4\u5C31\u51E0\u5206\u949F\uFF0C\u533B\u751F\u4E5F\u5F88\u96BE\u53BB\u4E86\u89E3\u90A3\u4E48\u591A\u60C5\u51B5\u518D\u505A\u4ED4\u7EC6\u7684\u5206\u6790\uFF0C\u5982\u679C\u89C9\u5F97\u6CA1\u5565\u5927\u95EE\u9898\uFF0C\u89C9\u5F97\u5BB6\u957F\u7279\u522B\u7126\u8651\uFF0C\u5C31\u968F\u4FBF\u5F00\u70B9\u6C28\u57FA\u9178\uFF0C\u7EF4\u751F\u7D20\u6216\u8005\u9499\u950C\uFF0C\u65E2\u5B89\u629A\u4E86\u5BB6\u957F\uFF0C\u81EA\u5DF1\u53EF\u80FD\u8FD8\u80FD\u62FF\u70B9\u56DE\u6263\uFF0C\u4E24\u5168\u5176\u7F8E\u3002</p><p>\u8FD9\u4E9B\u81B3\u98DF\u8865\u5145\u5242\uFF0C\u5403\u4E86\u901A\u5E38\u6CA1\u5565\u5927\u7684\u5371\u5BB3\uFF0C\u5BB6\u957F\u7ED9\u5B69\u5B50\u5403\u4E86\uFF0C\u4F1A\u89C9\u5F97\u81EA\u5DF1\u505A\u4E86\u70B9\u4EC0\u4E48\uFF0C\u518D\u52A0\u4E0A\u8BF4\u660E\u4E66\u4E0A\u7684\u529F\u6548\u5199\u5F97\u90A3\u4E48\u795E\u5947\uFF0C\u8FD8\u4F1A\u7ED9\u5BB6\u957F\u4EA7\u751F\u4E00\u4E9B\u5FC3\u7406\u6697\u793A\u4F5C\u7528\uFF0C\u591A\u5C11\u80FD\u7F13\u89E3\u4E00\u70B9\u7126\u8651\uFF0C\u6240\u4EE5\u5C31\u6210\u5C31\u4E86\u8D56\u6C28\u9178B12\u8FD9\u7C7B\u795E\u836F\u3002</p><p>\u5B9E\u9645\u4E0A\uFF0C\u82B1\u94B1\u7ED9\u5B69\u5B50\u5403\u8FD9\u4E2A\uFF0C\u8FD8\u4E0D\u5982\u7ED9\u5B69\u5B50\u5403\u4E2A\u9E21\u86CB\u3002</p><hr><p>\u5982\u4F55\u8BA9\u5B69\u5B50\u597D\u597D\u5403\u996D\uFF1F\u4EE5\u524D\u4E5F\u5199\u8FC7\u4E00\u7BC7\u6587\u7AE0\uFF0C\u5728\u8FD9\u4E2A\u516C\u4F17\u53F7\u56DE\u590D\u300C<strong>\u5403\u996D</strong>\u300D\u53EF\u67E5\u770B\u3002</p>',23),m={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651628909&idx=1&sn=b76328abf6d65270ed1c6040d544b4e7&chksm=8b5c8c97bc2b0581f7116531a5aa1c318f2cd57b6cdd6c6fa4e959e04a57e76ca63fa7d80652&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=s("\u67E5\u770B\u539F\u6587");function h(f,b){const p=a("ExternalLinkIcon");return r(),c(n,null,[i,e("p",null,[e("a",m,[l,t(p)])])],64)}var u=d(_,[["render",h]]);export{u as default};
