import{r as t,o as n,c as p,a as e,b as o,F as s,d as a,e as c}from"./app.9aaa2256.js";import{_ as g}from"./plugin-vue_export-helper.21dcd24c.js";const d={},_=a('<h3 id="\u5B69\u5B50\u80BA\u708E-\u4F60\u53EF\u80FD\u60F3\u77E5\u9053\u8FD93\u4E2A\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u80BA\u708E-\u4F60\u53EF\u80FD\u60F3\u77E5\u9053\u8FD93\u4E2A\u95EE\u9898" aria-hidden="true">#</a> \u5B69\u5B50\u80BA\u708E\uFF0C\u4F60\u53EF\u80FD\u60F3\u77E5\u9053\u8FD93\u4E2A\u95EE\u9898</h3><p>\u80BA\u708E\u662F\u513F\u7AE5\u3001\u5C24\u5176\u662F\u5A74\u513F\u6BD4\u8F83\u5E38\u89C1\u7684\u4E00\u79CD\u547C\u5438\u7CFB\u7EDF\u611F\u67D3\u6027\u75BE\u75C5\uFF0C\u5168\u7403\u6BCF\u5E74\u6709\u4E0A\u767E\u4E07\u7684\u5B69\u5B50\u6B7B\u4E8E\u80BA\u708E\uFF0C\u5C24\u5176\u662F\u5728\u53D1\u5C55\u4E2D\u56FD\u5BB6\u3002</p><p>\u5728\u56FD\u5185\uFF0C\u4E00\u65B9\u9762\u662F\u7ECF\u6D4E\u843D\u540E\u5730\u533A\u7684\u5B69\u5B50\uFF0C\u5F97\u4E86\u80BA\u708E\u5F97\u4E0D\u5230\u6709\u6548\u7684\u6CBB\u7597\uFF0C\u53E6\u4E00\u65B9\u9762\uFF0C\u5728\u7ECF\u6D4E\u53D1\u8FBE\u5730\u533A\uFF0C\u5927\u5BB6\u5BF9\u80BA\u708E\u53C8\u8FC7\u4E8E\u6050\u60E7\uFF0C\u5B69\u5B50\u4E00\u65E6\u88AB\u8BCA\u65AD\u4E3A\u80BA\u708E\uFF0C\u5C31\u53EF\u80FD\u88AB\u8981\u6C42\u4F4F\u9662\u8F93\u6DB2\u3002\u4E45\u800C\u4E45\u4E4B\uFF0C\u5F88\u591A\u5BB6\u957F\u4E5F\u628A\u80BA\u708E\u548C\u4F4F\u9662\u6253\u6297\u751F\u7D20\u7B49\u540C\u8D77\u6765\u3002</p><p>\u968F\u7740\u79D1\u666E\u7684\u666E\u53CA\uFF0C\u8FD1\u4E9B\u5E74\u5F88\u591A\u5BB6\u957F\u5BF9\u8FD9\u4E9B\u957F\u4E45\u7684\u8BA4\u77E5\u4EA7\u751F\u4E86\u7591\u8651\uFF0C\u6BD4\u5982\u5728\u6021\u79BE\uFF0C\u6211\u4EEC\u5C31\u7ECF\u5E38\u4F1A\u6536\u5230\u8FD9\u6837\u7684\u54A8\u8BE2\uFF1A\u5B69\u5B50\u5F97\u4E86\u80BA\u708E\uFF0C\u80FD\u4E0D\u80FD\u4E0D\u7528\u6297\u751F\u7D20\uFF1F\u80FD\u4E0D\u80FD\u4E0D\u8F93\u6DB2\uFF1F\u80FD\u4E0D\u80FD\u4E0D\u4F4F\u9662\uFF1F\u4ECA\u5929\u5C31\u6765\u56DE\u7B54\u8FD93\u4E2A\u95EE\u9898\u3002</p><p><strong>\u80BA\u708E\u4E00\u5B9A\u8981\u7528\u6297\u751F\u7D20\uFF1F</strong></p><p>**\u4E0D\u4E00\u5B9A\u3002**\u6211\u4EEC\u90FD\u77E5\u9053\uFF0C\u6297\u751F\u7D20\u662F\u9488\u5BF9\u7EC6\u83CC\u3001\u652F\u539F\u4F53\u3001\u8863\u539F\u4F53\u7B49\u5FAE\u751F\u7269\uFF0C\u5BF9\u75C5\u6BD2\u662F\u65E0\u6548\u7684\uFF0C\u4F46\u80BA\u708E\u5374\u5E76\u4E0D\u90FD\u662F\u7EC6\u83CC\u8FD9\u7C7B\u75C5\u539F\u4F53\u5F15\u8D77\u7684\uFF0C\u75C5\u6BD2\u611F\u67D3\u5360\u4E86\u513F\u7AE5\u80BA\u708E\u7684\u5F88\u5927\u6BD4\u91CD\u3002\u636E\u7EDF\u8BA1\uFF0C30%-67%\u513F\u7AE5\u80BA\u708E\u662F\u75C5\u6BD2\u6027\u7684\uFF1B\u57282\u5C81\u4EE5\u4E0B\u513F\u7AE5\u4E2D\uFF0C\u7EA650\uFF05\u7684\u80BA\u708E\u7531\u75C5\u6BD2\u5F15\u8D77\u3002</p><p>\u5355\u7EAF\u75C5\u6BD2\u6027\u80BA\u708E\u4F7F\u7528\u6297\u751F\u7D20\u4E0D\u4F46\u6CA1\u6548\uFF0C\u8FD8\u53EF\u80FD\u5F15\u53D1\u8FC7\u654F\u3001\u8179\u6CFB\u3001\u76AE\u75B9\u7B49\u4E0D\u826F\u53CD\u5E94\uFF0C\u8FD8\u53EF\u80FD\u5BFC\u81F4\u8010\u836F\u83CC\u7684\u4EA7\u751F\u3002\u7F8E\u56FD\u80F8\u79D1\u5B66\u4F1A/\u7F8E\u56FD\u611F\u67D3\u6027\u75BE\u75C5\u5B66\u4F1A\u751A\u81F3\u5EFA\u8BAE\uFF1A<strong>\u5BF9\u4E8E\u4E0D\u9700\u8981\u4F4F\u9662\u7684\u5B66\u9F84\u524D\u513F\u7AE5\u80BA\u708E\u6765\u8BF4\uFF0C\u5E38\u89C4\u90FD\u4E0D\u9700\u8981\u4F7F\u7528\u6297\u751F\u7D20\u3002</strong></p><p>\u5982\u679C\u8003\u8651\u7EC6\u83CC\u6027\u80BA\u708E\uFF0C\u5219\u662F\u8981\u4F7F\u7528\u6297\u751F\u7D20\uFF0C\u4F46\u54EA\u4E9B\u80BA\u708E\u662F\u7EC6\u83CC\u6027\uFF0C\u54EA\u4E9B\u662F\u75C5\u6BD2\u6027\uFF0C\u4E0D\u662F\u5BB6\u957F\u81EA\u5DF1\u80FD\u5224\u65AD\u7684\uFF0C\u4E5F\u4E0D\u80FD\u9760\u8BFB\u79D1\u666E\u5C31\u5224\u65AD\uFF0C\u800C\u662F\u8981\u9760\u7ED9\u5B69\u5B50\u770B\u75C5\u7684\u533B\u751F\u6765\u5224\u65AD\u3002</p><p><strong>\u80BA\u708E\u4E00\u5B9A\u8981\u8F93\u6DB2\u5417\uFF1F</strong></p><p>\u540C\u6837\u662F\u4E0D\u4E00\u5B9A\u7684\u3002\u4E0D\u7BA1\u662F\u56FD\u5185\u7684\u6307\u5357\uFF0C\u8FD8\u662F\u82F1\u56FD\u3001\u7F8E\u56FD\u7684\u6307\u5357\uFF0C\u90FD\u660E\u786E\u6307\u51FA\uFF1A\u8F7B\u5EA6\u80BA\u708E\u7684\u5B69\u5B50\u5982\u679C\u996E\u6C34\u548C\u6444\u98DF\u53EF\u4EE5\u4FDD\u8BC1\u6DB2\u4F53\u6444\u5165\u91CF\uFF0C\u90A3\u4E48\u5C31\u4E0D\u9700\u8981\u5E38\u89C4\u9759\u8109\u8865\u6DB2\u3002\u5373\u4F7F\u662F\u56E0\u547C\u5438\u56F0\u96BE\u6216\u5168\u8EAB\u8870\u5F31\u5BFC\u81F4\u96BE\u4EE5\u8FDB\u98DF\uFF0C\u6216\u9891\u7E41\u5455\u5410\u7684\u5B69\u5B50\uFF0C\u4E5F\u662F\u5148\u8003\u8651\u7ECF\u9F3B\u80C3\u7BA1\u5582\u517B\u3002\u800C\u5373\u4F7F\u9700\u8981\u4F7F\u7528\u6297\u751F\u7D20\uFF0C\u4E00\u822C\u4E5F\u8FD8\u662F\u9996\u9009\u53E3\u670D\u3002</p><p>\u53EA\u6709\u5728\u91CD\u75C7\u80BA\u708E\u3001\u6216\u51FA\u73B0\u8D25\u8840\u75C7\u4F53\u5F81\u3001\u6216\u6709\u80BA\u708E\u5E76\u53D1\u75C7\u3001\u6216\u56E0\u5455\u5410\u7B49\u60C5\u51B5\u5BFC\u81F4\u53E3\u670D\u96BE\u4EE5\u5438\u6536\u7684\u60C5\u51B5\u4E0B\uFF0C\u624D\u8003\u8651\u9759\u8109\u4F7F\u7528\u6297\u751F\u7D20\u3002\u6B64\u5916\uFF0C\u5373\u4F7F\u5DF2\u7ECF\u5F00\u59CB\u9759\u8109\u4F7F\u7528\u6297\u751F\u7D20\uFF0C\u4F46\u5F53\u75C5\u60C5\u660E\u663E\u6539\u5584\u4E14\u57FA\u672C\u7A33\u5B9A\u540E\uFF0C\u4E5F\u5E94\u53CA\u65F6\u6539\u4E3A\u53E3\u670D\u6297\u751F\u7D20\u6CBB\u7597\uFF0C\u8FD9\u4E5F\u79F0\u4E3A\u201C\u5E8F\u8D2F\u7597\u6CD5\u201D\u3002</p><p><strong>\u80BA\u708E\u4E00\u5B9A\u8981\u4F4F\u9662\u5417\uFF1F</strong></p><p>\u7B54\u6848\u4ECD\u7136\u662F\u4E0D\u4E00\u5B9A\uFF0C\u8F7B\u5EA6\u80BA\u708E\u53EF\u4EE5\u5728\u5BB6\u91CC\u6216\u95E8\u8BCA\u6CBB\u7597**\u3002**\u5F53\u7136\uFF0C\u8981\u6CE8\u610F\u5B69\u5B50\u7CBE\u795E\u72B6\u6001\u3001\u547C\u5438\u3001\u8131\u6C34\u7B49\u60C5\u51B5\uFF0C\u540C\u65F6\u4E5F\u8981\u5B9A\u671F\u968F\u8BBF\uFF0C\u5982\u679C\u95E8\u8BCA\u6CBB\u759748\u5C0F\u65F6\u65E0\u6548\u3001\u9AD8\u70ED\u4E0D\u9000\uFF0C\u6216\u75C5\u60C5\u6076\u5316\u51FA\u73B0\u547C\u5438\u6025\u4FC3\u3001\u8D39\u529B\uFF0C\u53E3\u5507\u53D1\u9752\u53D1\u7D2B\u7B49\u60C5\u51B5\uFF0C\u5219\u5FC5\u987B\u53CA\u65F6\u5230\u533B\u9662\u590D\u8BCA\u3002</p><p>\u81F3\u4E8E\u80BA\u708E\u7684\u4F4F\u9662\u6307\u5F81\uFF0C\u5404\u56FD\u6307\u5357\u6709\u4E9B\u8BB8\u4E0D\u540C\uFF0C\u4F46\u4E5F\u90FD\u5927\u540C\u5C0F\u5F02\uFF0C\u4E00\u822C\u6765\u8BF4\u5E74\u9F84\u5C0F\u7684\u5B69\u5B50\uFF0C\u6BD4\u59826\u4E2A\u6708\u5185\u7684\u8981\u8B66\u60D5\uFF0C\u5927\u5B69\u5B50\u91CD\u70B9\u662F\u770B\u4E00\u822C\u60C5\u51B5\u3001\u547C\u5438\u3001\u4F53\u6E29\u3001\u8131\u6C34\u7B49\u60C5\u51B5\u3002</p><p>\u5176\u5B9E\uFF0C\u8FD9\u4E09\u4E2A\u95EE\u9898\u90FD\u662F\u5E94\u8BE5\u7531\u533B\u751F\u6765\u628A\u63E1\u5224\u65AD\u7684\uFF0C\u4F5C\u4E3A\u7236\u6BCD\uFF0C\u5B69\u5B50\u53D1\u70E7\u54B3\u55FD\u65F6\uFF0C\u5982\u679C\u5FC3\u91CC\u6CA1\u5E95\u5E94\u8BE5\u53BB\u770B\u533B\u751F\uFF0C\u7136\u540E\u6309\u533B\u751F\u610F\u89C1\u8FDB\u884C\u6CBB\u7597\u3002\u4F46\u56E0\u4E3A\u533B\u7597\u73AF\u5883\u7684\u539F\u56E0\uFF0C\u513F\u7AE5\u80BA\u708E\u6CBB\u7597\u4E0A\u786E\u5B9E\u5B58\u5728\u4E00\u4E9B\u4E0D\u89C4\u8303\uFF0C\u8BA9\u5927\u5BB6\u4EA7\u751F\u4E86\u5F88\u591A\u56F0\u60D1\uFF0C\u5199\u8FD9\u7BC7\u7684\u76EE\u7684\u4E5F\u4E0D\u662F\u4E3A\u4E86\u6307\u5BFC\u5927\u5BB6\u81EA\u5DF1\u505A\u51B3\u5B9A\uFF0C\u800C\u662F\u4E3A\u4E86\u8BA9\u5927\u5BB6\u4E86\u89E3\u8FD9\u4E9B\u4E8B\u5B9E\uFF08\u6CE8\uFF1A\u4E0A\u6587\u4E2D\u63D0\u5230\u7684\u201C\u80BA\u708E\u201D\u5747\u4EC5\u6307\u793E\u533A\u83B7\u5F97\u6027\u80BA\u708E\uFF09</p><hr><p>\u5B69\u5B50\u547C\u5438\u9053\u95EE\u9898\u5F88\u5E38\u89C1\uFF0C\u6240\u4EE5\u5E38\u6709\u54B3\u55FD\uFF0C\u88AB\u5B69\u5B50\u54B3\u55FD\u95EE\u9898\u56F0\u6270\u7684\u5BB6\u957F\u4E5F\u5F88\u591A\uFF0C\u6240\u4EE5\u6021\u79BE\u4E0B\u4E00\u6B21\u79D1\u666E\u8BFE\u7A0B\u5C31\u9080\u8BF7\u4E86\u672C\u6587\u7684\u4F5C\u8005\uFF0C\u4E5F\u662F\u6DF1\u53D7\u6021\u79BE\u5BB6\u957F\u4EEC\u559C\u6B22\u7684\u6797\u6C49\u70BC\u533B\u751F\u6765\u8DDF\u5927\u5BB6\u8BB2\u5B69\u5B50\u7684\u54B3\u55FD\u95EE\u9898\u3002</p><p>\u4E3B\u8981\u89E3\u7B54\u4E0B\u9762\u95EE\u9898\uFF1A</p><p><strong>\u5B69\u5B50\u54B3\u55FD\u7684\u539F\u56E0</strong></p><p><strong>\u54B3\u55FD\u6709\u4EC0\u4E48\u5371\u5BB3\uFF1F</strong></p><p><strong>\u54B3\u55FD\u600E\u4E48\u62A4\u7406\uFF1F</strong></p><p><strong>\u9700\u8981\u96FE\u5316\u5417\uFF1F</strong></p><p><strong>\u6B62\u54B3\u795B\u75F0\u836F\u80FD\u7528\u5417\uFF1F</strong></p><p><strong>\u54B3\u55FD\u603B\u4E0D\u597D\u600E\u4E48\u529E\uFF1F</strong></p><p>\u8BE6\u60C5\u53C2\u89C1\u6D77\u62A5\uFF0C\u672C\u6B21\u8BFE\u7A0B\u540C\u6837\u5F00\u542F\u4E86\u514D\u8D39\u542C\u8BFE\u901A\u9053\uFF0C\u957F\u6309\u6D77\u62A5\u5173\u6CE8<strong>\u6021\u79BE\u5065\u5EB7</strong>\u540E\uFF0C\u56DE\u590D<strong>0726</strong>\uFF0C\u5B8C\u6210\u4EFB\u52A1\u53EF\u4EE5\u514D\u8D39\u542C\u8BFE\u3002</p>',25),i={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626818&idx=1&sn=39fba9abd379bbffba06530768112aee&chksm=8b5cb4b8bc2b3daec490e3463927493c0bd1f4c539953601ec8e542430480816b59aa370f778&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=c("\u67E5\u770B\u539F\u6587");function l(b,f){const r=t("ExternalLinkIcon");return n(),p(s,null,[_,e("p",null,[e("a",i,[h,o(r)])])],64)}var u=g(d,[["render",l]]);export{u as default};
