import{r,o as t,c as n,a as p,b as o,F as s,d as a,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},g=a('<h3 id="\u6211\u4EEC\u8981\u62DB\u4EBA\u4E86" tabindex="-1"><a class="header-anchor" href="#\u6211\u4EEC\u8981\u62DB\u4EBA\u4E86" aria-hidden="true">#</a> \u6211\u4EEC\u8981\u62DB\u4EBA\u4E86</h3><p>\u4E0D\u77E5\u4E0D\u89C9\uFF0C\u6021\u79BE\u5065\u5EB7\u7684\u5728\u7EBF\u54A8\u8BE2\u4E0A\u7EBF\u4E00\u4E2A\u591A\u6708\u4E86\uFF0C\u8FD9\u4E2A\u54A8\u8BE2\u5E73\u53F0\u7B97\u662F\u81EA\u5DF1\u8F9E\u804C\u540E\u505A\u7684\u7B2C\u4E00\u4E2A\u9879\u76EE\uFF0C\u4ECE\u6700\u5F00\u59CB\u7684\u6BCF\u5929\u5341\u51E0\u6B21\u54A8\u8BE2\uFF0C\u5230\u4ECA\u5929\u7684\u6BCF\u5929\u8FD1\u767E\u6B21\u54A8\u8BE2\uFF0C\u771F\u662F\u611F\u89C9\u521B\u4E1A\u7EF4\u8270\u3002</p><p>\u4ECE\u533B\u9662\u88F8\u8F9E\u51FA\u6765\uFF0C\u9996\u5148\u8981\u89E3\u51B3\u7684\u662F\u751F\u5B58\u95EE\u9898\u3002\u4F5C\u4E3A\u4E00\u4E2A\u8BA2\u9605\u91CF\u5FEB 30\u4E07\uFF0C\u6253\u5F00\u7387\u4E00\u76F4\u5F88\u9AD8\u7684\u516C\u4F17\u53F7\u8FD0\u8425\u8005\u6765\u8BF4\uFF0C\u6211\u4E5F\u53EF\u4EE5\u548C\u5176\u5B83\u6BCD\u5A74\u53F7\u4E00\u6837\uFF0C\u9009\u62E9\u6A21\u5F0F\u6210\u719F\u4E00\u70B9\u7684\u4E1A\u52A1\uFF0C\u6BD4\u5982\u505A\u505A\u5E7F\u544A\uFF0C\u5199\u70B9\u8F6F\u6587\uFF0C\u5356\u5356\u4E1C\u897F\u5E94\u8BE5\u4F1A\u66F4\u8F7B\u677E\u3002\u533B\u751F\u7684\u60C5\u7ED3\uFF0C\u8FD8\u662F\u4FC3\u4F7F\u81EA\u5DF1\u60F3\u4E3A\u533B\u7597\u884C\u4E1A\u505A\u70B9\u6539\u53D8\uFF0C\u6240\u4EE5\u9009\u62E9\u4E86\u7EE7\u7EED\u5728\u533B\u7597\u7684\u8DEF\u4E0A\u8D70\u4E0B\u53BB\u3002</p><p>\u4F46\u505A\u4E00\u4E2A\u5065\u5EB7\u54A8\u8BE2\u5E73\u53F0\uFF0C\u548C\u770B\u597D\u4E00\u6B21\u75C5\u771F\u4E0D\u662F\u4E00\u56DE\u4E8B\u3002\u867D\u7136\u5E73\u53F0\u662F\u817E\u8BAF\u817E\u7231\u533B\u751F\u56E2\u961F\u5F00\u53D1\u7684\uFF0C\u4F46\u4F5C\u4E3A\u5408\u4F5C\u65B9\u548C\u9996\u4E2A\u4F7F\u7528\u65B9\uFF0C\u4ECE6\u6708\u4EFD\u7684\u7C97\u7CD9\u5185\u6D4B\u5230\u73B0\u5728\u6BD4\u8F83\u5E73\u987A\u7684\u8FD0\u884C\uFF0C\u5F88\u591A\u9700\u6C42\u548C\u6D41\u7A0B\u7684\u8BBE\u8BA1\uFF0C\u6211\u548C\u6211\u7684\u5408\u4F19\u4EBA@\u6FB3\u6D32\u5987\u5E7CDr\u97E9 \u90FD\u6709\u53C2\u4E0E\u3002</p><p>\u8FD9\u4E2A\u8FC7\u7A0B\u8BA9\u6211\u5BF9\u624B\u673A\u91CC\u7684\u6BCF\u4E2AApp\u6709\u4E86\u4E0D\u4E00\u6837\u7684\u770B\u6CD5\uFF0C\u4EE5\u524D\u4E0B\u8F7D\u4E00\u4E2AApp\uFF0C\u4F7F\u7528\u4EFB\u4F55\u529F\u80FD\u90FD\u89C9\u5F97\u7406\u6240\u5F53\u7136\uFF0C\u4F46\u8FD9\u6B21\u7684\u53C2\u4E0E\uFF0C\u624D\u77E5\u9053\u4E00\u4E2AApp\u7684\u80CC\u540E\u9700\u8981\u90A3\u4E48\u5927\u7684\u56E2\u961F\uFF0C\u9700\u8981\u8003\u8651\u90A3\u4E48\u591A\u7684\u4E1C\u897F\u3002\u60A3\u8005\u7684\u9700\u6C42\uFF0C\u533B\u751F\u7684\u9700\u6C42\uFF0C\u533B\u60A3\u4E4B\u95F4\u7684\u5E73\u8861\uFF0C\u529F\u80FD\u548C\u4EA4\u6362\u6BCF\u4E00\u4E2A\u6D41\u7A0B\u90FD\u9762\u4E34\u592A\u591A\u7684\u53D6\u820D\u548C\u9009\u62E9\u3002</p><p>\u8FD9\u4E2A\u8FC7\u7A0B\u4E5F\u8BA9\u6211\u5BF9\u4E92\u8054\u7F51\u884C\u4E1A\u6709\u4E86\u66F4\u6DF1\u5165\u7684\u8BA4\u8BC6\uFF0C\u5373\u4FBF\u662F\u817E\u8BAF\u8FD9\u6837\u7684\u4E92\u8054\u7F51\u516C\u53F8\uFF0C\u4E5F\u65E0\u6CD5\u4E00\u6B65\u505A\u51FA\u80FD\u6D41\u7545\u4F7F\u7528\u7684\u4EA7\u54C1\u3002\u4EE5\u524D\u505A\u533B\u751F\u89C9\u5F97\u5F88\u8F9B\u82E6\uFF0C\u4F46\u665A\u4E0A\u53BB\u51E0\u6B21\u817E\u8BAF\u53D1\u73B0\uFF0C\u8FD9\u91CC\u534A\u591C\u90FD\u662F\u706F\u706B\u901A\u660E\uFF0C\u52A0\u73ED\u4E00\u70B9\u4E5F\u4E0D\u6BD4\u533B\u751F\u62A4\u58EB\u5C11\u3002\u5F88\u591A\u6BD4\u4F60\u806A\u660E\u7684\u4EBA\u5176\u5B9E\u6BD4\u4F60\u8FD8\u52E4\u594B\uFF0C\u817E\u8BAF\u7684\u6210\u529F\u4E0D\u662F\u65E0\u7F18\u65E0\u6545\u7684\u3002</p><p>\u521B\u4E1A\u7684\u7B2C\u4E00\u4E2A\u4E1A\u52A1\u9009\u62E9\u4E86\u5728\u7EBF\u54A8\u8BE2\uFF0C\u6709\u73B0\u5B9E\u7684\u8003\u8651\uFF0C\u4E5F\u6709\u89C4\u5212\u7684\u8003\u8651\u3002\u6211\u4E5F\u77E5\u9053\u5E02\u573A\u4E0A\u5DF2\u7ECF\u6709\u5F88\u591A\u4F53\u91CF\u6BD4\u6211\u4EEC\u5927\u65E0\u6570\u500D\u7684\u516C\u53F8\u5728\u505A\u8FD9\u7C7B\u4E1A\u52A1\uFF0C\u4F46\u6211\u8FD8\u662F\u60F3\u505A\u51FA\u4E00\u70B9\u4E0D\u4E00\u6837\u3002\u6211\u548C\u97E9\u533B\u751F\u5728\u5F88\u591A\u5E73\u53F0\u4E0A\u63D0\u4F9B\u8FC7\u54A8\u8BE2\u670D\u52A1\uFF0C\u4F46\u4ECE\u6CA1\u9047\u5230\u8FC7\u54EA\u5BB6\u5E73\u53F0\u7528\u5FC3\u7684\u5BF9\u533B\u751F\u7684\u56DE\u590D\u4ECE\u4E13\u4E1A\u89D2\u5EA6\u8FDB\u884C\u6807\u51C6\u5316\u7BA1\u7406\uFF0C\u4F5C\u4E3A\u60A3\u8005\uFF0C\u6211\u5E76\u4E0D\u76F8\u4FE1\u9009\u62E9\u8FD9\u4E9B\u5E73\u53F0\u5F97\u5230\u7684\u56DE\u590D\u4F1A\u6709\u4EC0\u4E48\u4E0D\u4E00\u6837\u3002</p><p>\u6240\u4EE5\u6021\u79BE\u5065\u5EB7\u4E0A\u7684\u533B\u751F\uFF0C\u6211\u5E0C\u671B\u90FD\u662F<strong>\u4E34\u5E8A\u7ECF\u9A8C\u4E30\u5BCC</strong>\u7684\u533B\u751F\uFF0C\u540C\u65F6\u4E5F\u662F\u6709<strong>\u670D\u52A1\u610F\u8BC6</strong>\u7684\u533B\u751F\uFF0C\u66F4\u91CD\u8981\u7684\u662F<strong>\u8BA4\u53EF\u5FAA\u8BC1\u533B\u5B66\u7406\u5FF5</strong>\u7684\u533B\u751F\u3002\u6240\u4EE5\u7B2C\u4E00\u6279\u533B\u751F\u6211\u6311\u9009\u7684\u662F\u90A3\u4E9B\u81EA\u5DF1\u6709\u6240\u4E86\u89E3\uFF0C\u800C\u4E14\u5728\u4E09\u7EA7\u533B\u9662\u5DE5\u4F5C\u6216\u8005\u5DE5\u4F5C\u8FC7\u7684\u533B\u751F\u3002</p><p>\u540C\u65F6\u6211\u4EEC\u5236\u5B9A\u4E86\u64CD\u4F5C\u89C4\u8303\uFF0C\u5BF9\u533B\u751F\u7684\u56DE\u590D\u65F6\u9650\u3001\u7528\u8BED\u3001\u5185\u5BB9\u3001\u9700\u8981\u89C4\u907F\u7684\u836F\u7269\u8FDB\u884C\u8981\u6C42\u3002\u6BD4\u5982\u4E0B\u56FE\u5C31\u662F\u6211\u4EEC\u64CD\u4F5C\u89C4\u8303\u91CC\u7684\u4E00\u90E8\u5206\u3002</p><p>\u6709\u64CD\u4F5C\u89C4\u8303\uFF0C\u6709\u4F18\u79C0\u533B\u751F\u662F\u4E00\u56DE\u4E8B\uFF0C\u4F46\u771F\u6B63\u5B9E\u65BD\u8D77\u6765\u662F\u53E6\u4E00\u56DE\u4E8B\uFF0C\u4E0A\u7EBF\u540E\u4F9D\u7136\u96F6\u661F\u7684\u6536\u5230\u7F51\u53CB\u53CD\u9988\u533B\u751F\u5BF9\u4E2D\u6210\u836F\u7684\u63A8\u8350\u3002\u6BCF\u6B21\u6536\u5230\u8FD9\u6837\u7684\u53CD\u9988\u6211\u90FD\u5F88\u96BE\u8FC7\uFF0C\u6211\u4E5F\u660E\u767D\u6539\u53D8\u8FD9\u4E00\u70B9\u5F88\u96BE\uFF0C\u8FD9\u53EF\u80FD\u4E5F\u662F\u5176\u5B83\u5E73\u53F0\u653E\u5F03\u8FD9\u6837\u505A\u7684\u539F\u56E0\u5427\u3002\u4F46\u65E2\u7136\u662F\u60F3\u505A\u6539\u53D8\uFF0C\u60F3\u505A\u51FA\u4E00\u70B9\u4E0D\u4E00\u6837\uFF0C\u96BE\u505A\u7684\u4E8B\u6211\u4E5F\u4F1A\u53BB\u505A\u3002</p><p>\u6BCF\u6B21\u51FA\u73B0\u8FD9\u6837\u7684\u53CD\u9988\uFF0C\u6211\u4EEC\u90FD\u4F1A\u5728\u533B\u751F\u5FAE\u4FE1\u7FA4\u91CC\u8FDB\u884C\u63D0\u9192\u3001\u8BA8\u8BBA\u548C\u6539\u8FDB\uFF0C\u6211\u4EEC\u5E0C\u671B\u5E73\u53F0\u4E0A\u7684\u533B\u751F\u90FD\u505A\u5230\u6709\u591A\u5C11\u8BC1\u636E\u8BF4\u591A\u5C11\u8BDD\uFF0C\u5FAA\u8BC1\u533B\u5B66\u4E0D\u53EA\u662F\u505C\u7559\u5728\u53E3\u53F7\u4E0A\u3002</p><p>\u53E6\u4E00\u65B9\u9762\uFF0C\u56E0\u4E3A\u6211\u5E73\u65F6\u4E00\u8D2F\u575A\u6301\u5FAA\u8BC1\u533B\u5B66\u7406\u5FF5\uFF0C\u5DF2\u7ECF\u805A\u5408\u4E86\u4E00\u4E9B\u8BA4\u53EF\u8FD9\u4E2A\u7406\u5FF5\u7684\u533B\u751F\uFF0C\u5305\u62EC\u5728\u56FD\u5916\u7684\u533B\u751F\uFF0C\u4ED6\u4EEC\u5F88\u4E50\u610F\u5230\u8FD9\u4E2A\u548C\u81EA\u5DF1\u7406\u5FF5\u4E00\u81F4\u7684\u5E73\u53F0\u4E0A\u63D0\u4F9B\u670D\u52A1\u3002\u540C\u65F6\uFF0C\u6211\u4E5F\u53BB\u9080\u8BF7\u90A3\u4E9B\u575A\u5B9A\u8BA4\u53EF\u5FAA\u8BC1\u533B\u5B66\u7406\u5FF5\uFF0C\u6BD4\u5982\u4E25\u864E\u3001\u674E\u6E05\u6668\u3001\u5218\u6B23\u8FD9\u4E9B\u533B\u751F\u5230\u5E73\u53F0\u4E0A\u6765\u3002</p><p>\u805A\u5408\u4E86\u8D8A\u6765\u8D8A\u591A\u7684\u9760\u8C31\u533B\u751F\uFF0C\u540C\u65F6\u6211\u4EEC\u5728\u533B\u751F\u7FA4\u91CC\u53CD\u590D\u5F3A\u8C03\u6211\u4EEC\u64CD\u4F5C\u89C4\u8303\uFF0C\u5206\u4EAB\u53EF\u9760\u7684\u4E34\u5E8A\u533B\u5B66\u6307\u5357\uFF0C\u5E76\u704C\u8F93\u5FAA\u8BC1\u533B\u5B66\u7406\u5FF5\uFF0C\u8BA9\u5E73\u53F0\u4E0A\u533B\u751F\u7684\u56DE\u590D\u8D8A\u6765\u8D8A\u89C4\u8303\uFF0C\u8FD9\u4E9B\u52AA\u529B\uFF0C\u90FD\u662F\u5E0C\u671B\u6BCF\u4E2A\u7528\u6237\u90FD\u80FD\u6536\u5230\u4E13\u4E1A\u53EF\u9760\u7684\u54A8\u8BE2\u3002</p><p>\u5230\u4ECA\u5929\uFF0C\u5BF9\u5E73\u53F0\u4E0A\u533B\u751F\u56DE\u590D\u7684\u8D28\u91CF\u6211\u5DF2\u7ECF\u6BD4\u8F83\u653E\u5FC3\uFF0C\u6211\u4E5F\u6536\u5230\u4E86\u8D8A\u6765\u8D8A\u591A\u7684\u6B63\u9762\u53CD\u9988\uFF0C\u6709\u65F6\u5019\u770B\u5230\u6211\u4EEC\u7684\u533B\u751F\u7ED9\u60A3\u8005\u8BE6\u5C3D\u800C\u4ED4\u7EC6\u7684\uFF0C\u81EA\u5DF1\u90FD\u89C9\u5F97\u611F\u52A8\u3002</p><p>\u867D\u7136\u5E73\u53F0\u8FD8\u6709\u5F88\u591A\u7EC6\u8282\u9700\u8981\u5F00\u53D1\u5B8C\u5584\uFF0C\u867D\u7136\u80AF\u5B9A\u8FD8\u4E0D\u80FD\u8BF4\u5728\u8FD9\u4E2A\u5E73\u53F0\u4E0A\u5F97\u5230\u7684\u54A8\u8BE2\u670D\u52A1\u5C31\u662F\u7EDD\u5BF9\u53EF\u9760\u7684\uFF0C\u4F46\u6211\u53EF\u4EE5\u626A\u5FC3\u65E0\u6127\u7684\u8BF4\uFF0C\u5982\u679C\u81EA\u5DF1\u5B69\u5B50\u6709\u5065\u5EB7\u95EE\u9898\u9700\u8981\u54A8\u8BE2\uFF0C\u6211\u4E5F\u4F1A\u9996\u9009\u6021\u79BE\u8FD9\u4E2A\u5E73\u53F0\uFF0C\u5982\u679C\u6709\u9700\u8981\u53EF\u4EE5\u957F\u6309\u8BC6\u522B\u4E0B\u9762\u4E8C\u7EF4\u7801\u5173\u6CE8\u540E\u5C1D\u8BD5\u3002</p><p>\u6211\u4E5F\u77E5\u9053\u957F\u671F\u8BA2\u9605\u6211\u516C\u4F17\u53F7\u7684\u8BFB\u8005\uFF0C\u5386\u7ECF\u6211\u7684\u53CD\u590D\u201C\u6D17\u7C89\u201D\uFF0C\u5176\u5B9E\u79D1\u5B66\u7D20\u517B\u662F\u8FDC\u9AD8\u4E8E\u666E\u901A\u7684\u4EBA\u7FA4\u7684\uFF0C\u8981\u670D\u52A1\u597D\u8FD9\u4E2A\u7FA4\u4F53\uFF0C\u5176\u5B9E\u662F\u9700\u8981\u66F4\u9AD8\u7684\u4E13\u4E1A\u6C34\u5E73\u548C\u670D\u52A1\u80FD\u529B\u3002\u7CFB\u7EDF\u4E5F\u6B63\u5728\u5F00\u53D1\u8BC4\u4EF7\u53CD\u9988\u529F\u80FD\uFF0C\u4E00\u4E2A\u533B\u751F\u5982\u679C\u80FD\u7ECF\u53D7\u4F4F\u8FD9\u4E2A\u8BFB\u8005\u7FA4\u4F53\u7684\u8003\u9A8C\uFF0C\u5728\u6021\u79BE\u8FD9\u4E2A\u5E73\u53F0\u4E0A\u83B7\u5F97\u666E\u904D\u7684\u597D\u8BC4\uFF0C\u5176\u5B9E\u5C31\u662F\u4E00\u4E2A\u53EF\u4FE1\u4EFB\u7684\u54C1\u724C\u6807\u5FD7\uFF0C\u6240\u4EE5\u6211\u4E5F\u6B22\u8FCE\u6709\u66F4\u591A\u7684\u5987\u513F\u533B\u751F\u6765\u8FD9\u91CC\u63D0\u4F9B\u670D\u52A1\uFF0C\u770B\u770B\u81EA\u5DF1\u80FD\u4E0D\u80FD\u901A\u8FC7\u6211\u8BFB\u8005\u7684\u8BC4\u6D4B\u3002\u6709\u610F\u8005\u53EF\u52A0\u6211\u7684\u4E2A\u4EBA\u5FAE\u4FE1dr_peihg\uFF0C\u9A8C\u8BC1\u65F6\u6CE8\u660E<strong>\u533B\u751F\u6CE8\u518C</strong>\u3002</p><p>\u8FD9\u4E2A\u5E73\u53F0\u4E0A\u7EBF\u81F3\u4ECA\uFF0C\u4E3B\u8981\u662F\u6211\u548C\u97E9\u533B\u751F\u4E24\u4E2A\u4EBA\u5728\u7EF4\u62A4\u548C\u8FD0\u8425\uFF0C\u968F\u7740\u4E1A\u52A1\u91CF\u7684\u9010\u6E10\u4E0A\u5347\uFF0C\u4EE5\u53CA\u540E\u7EED\u7EBF\u4E0B\u8BCA\u6240\u7684\u7B79\u5EFA\uFF0C\u4E8B\u60C5\u8D8A\u6765\u8D8A\u591A\uFF0C\u4E5F\u8D8A\u6765\u8D8A\u89C9\u5F97\u529B\u4E0D\u4ECE\u5FC3\uFF0C\u6240\u4EE5\u6211\u4EEC\u51B3\u5B9A\u62DB\u4EBA\u4E86\uFF0C\u4E0B\u9762\u662F\u6211\u4EEC\u60F3\u627E\u7684\u4EBA\u3002</p><hr><p><strong>\u804C\u4F4D\u4E00\uFF1A\u8FD0\u8425\u7ECF\u7406</strong></p><p>**\u5C97\u4F4D\u804C\u8D23\uFF1A<br> **<br> \u7B56\u5212\u5E76\u6267\u884C\u7EBF\u4E0A-\u7EBF\u4E0B\u8FD0\u8425\u89C4\u5212\uFF1B</p><p>\u68B3\u7406\u6574\u5408\u5DF2\u6709\u8D44\u6E90\u5BF9\u7EBF\u4E0A\u548C\u7EBF\u4E0B\u4EA7\u54C1\u8FDB\u884C\u6709\u6548\u7684\u6574\u4F53\u8FD0\u8425\u89C4\u5212\uFF1B</p><p>\u6D1E\u5BDF\u7528\u6237\u9700\u6C42\uFF0C\u8FD0\u7528\u6709\u6548\u8FD0\u8425\u65B9\u5F0F\uFF0C\u5B9E\u73B0\u516C\u53F8\u7EBF\u4E0A\u548C\u7EBF\u4E0B\u7528\u6237\u589E\u957F\uFF1B</p><p>\u914D\u5408\u7EBF\u4E0B\u8BCA\u6240\u62D3\u5C55\u5DE5\u4F5C\uFF0C \u7B56\u5212\u5E76\u843D\u5B9E\u533B\u751F\u548C\u60A3\u8005\u53CC\u8FB9\u8FD0\u8425\u65B9\u6848\uFF1B</p><p>\u5B8C\u6210\u4E0A\u7EA7\u5206\u914D\u7684\u5176\u4ED6\u76F8\u5173\u5DE5\u4F5C\u3002</p><p><strong>\u4EFB\u804C\u8981\u6C42\uFF1A</strong></p><p>\u672C\u79D1\u53CA\u4EE5\u4E0A\u5B66\u5386\uFF0C3\u5E74\u4EE5\u4E0A\u8FD0\u8425\u5DE5\u4F5C\u7ECF\u9A8C\uFF0C\u6709\u8FD0\u8425\u6210\u529F\u6848\u4F8B\u8005\u4F18\u5148\uFF1B</p><p>\u5177\u5907\u533B\u7597\u5065\u5EB7\u7BA1\u7406\u548C\u4E92\u8054\u7F51\u65B0\u5A92\u4F53\u8FD0\u8425\u7ECF\u9A8C\u8005\u4F18\u5148\uFF1B</p><p>\u5177\u5907\u4E00\u5B9A\u7684\u8FD0\u8425\u6570\u636E\u5206\u6790\u548C\u6587\u6848\u64B0\u5199\u80FD\u529B\uFF1B</p><p>\u6709\u8D23\u4EFB\u611F\u548C\u56E2\u961F\u5408\u4F5C\u7CBE\u795E\uFF0C \u51FA\u8272\u7684\u8BED\u8A00\u8868\u8FBE\u80FD\u529B\uFF0C\u5DE5\u4F5C\u79EF\u6781\u4E3B\u52A8\uFF0C\u5584\u4E8E\u6C9F\u901A\u534F\u4F5C\u3002</p><p><strong>\u804C\u4F4D\u4E8C\uFF1A\u79D1\u666E\u7F16\u8F91</strong></p><p><strong>\u5C97\u4F4D\u804C\u8D23\uFF1A</strong></p><p>\u5065\u5EB7\u79D1\u666E\u6587\u7AE0\u7684\u9009\u9898\uFF0C\u64B0\u5199\uFF0C\u7FFB\u8BD1\uFF0C\u7F16\u8F91\u7B49\u5DE5\u4F5C\u3002</p><p><strong>\u57FA\u672C\u8981\u6C42\uFF1A</strong></p><p>\u5B66\u5386\uFF1A\u533B\u5B66\u6216\u4F20\u5A92\u76F8\u5173\u4E13\u4E1A\uFF0C\u672C\u79D1\u53CA\u4EE5\u4E0A\uFF1B</p><p>\u5DE5\u4F5C\u7ECF\u5386\uFF1A\u7F16\u8F91\u6216\u81EA\u5A92\u4F53\u8FD0\u8425\u4E00\u5E74\u6216\u4EE5\u4E0A\u5DE5\u4F5C\u7ECF\u9A8C\u3002</p><p><strong>\u4F18\u5148\u8003\u8651\uFF1A</strong></p><p>\u533B\u5B66\u4E13\u4E1A\u80CC\u666F\uFF0C\u719F\u6089\u5E76\u8BA4\u540C\u5FAA\u8BC1\u533B\u5B66\u7406\u5FF5\uFF1B</p><p>\u5177\u5907\u533B\u5B66\u7F16\u8F91\u5DE5\u4F5C\u7ECF\u9A8C\uFF1B</p><p>\u5177\u5907\u7FFB\u8BD1\u4E13\u4E1A\u8D44\u683C\u8BC1\u4E66\uFF1B</p><p>\u5177\u5907\u5065\u5EB7\u76F8\u5173\u81EA\u5A92\u4F53\u8FD0\u8425\u5DE5\u4F5C\u7ECF\u9A8C\u3002</p><hr><p>\u4EE5\u4E0A\u5DE5\u4F5C\u90FD\u9700\u5168\u804C\uFF0C\u5DE5\u4F5C\u5730\u70B9\u5728\u6DF1\u5733\u798F\u7530\uFF0C\u5F85\u9047\u9762\u8BAE\uFF0C\u5BF9\u4E8E\u79D1\u666E\u7F16\u8F91\u8FD9\u4E2A\u804C\u4F4D\uFF0C\u6709\u4E2A\u989D\u5916\u7684\u597D\u5904\u662F\u4F60\u7684\u6BCF\u4E00\u7BC7\u7A3F\u5B50\u6211\u90FD\u4F1A\u53C2\u4E0E\u4FEE\u6539\u8BA8\u8BBA\uFF0C\u76F8\u4FE1\u80FD\u7ED9\u4F60\u5199\u4F5C\u4E00\u4E9B\u63D0\u5347\u3002</p><p>\u5982\u679C\u4F60\u4E5F\u5E0C\u671B\u548C\u6211\u4EEC\u4E00\u8D77\u4E3A\u533B\u7597\u884C\u4E1A\u7684\u6539\u53D8\u505A\u70B9\u4E8B\uFF0C\u5E0C\u671B\u5728\u6539\u53D8\u884C\u4E1A\u7684\u540C\u65F6\u5B9E\u73B0\u81EA\u5DF1\u7684\u4EF7\u503C\uFF0C\u5982\u679C\u4F60\u662F\u6211\u4EEC\u7B26\u5408\u6211\u4EEC\u804C\u52A1\u63CF\u8FF0\u7684\u671F\u671B\uFF0C\u6B22\u8FCE\u5C06\u7B80\u5386\u6295\u9012\u5230\u6211\u7684\u5DE5\u4F5C\u90AE\u7BB1\uFF1Adrpei@hotmail.com\u3002\u4E5F\u6B22\u8FCE\u5E2E\u6211\u8F6C\u53D1\u8FD9\u7BC7\u8BA9\u66F4\u591A\u4EBA\u770B\u5230\uFF0C\u8BA9\u6211\u4EEC\u65E9\u70B9\u627E\u5230\u60F3\u8981\u7684\u4EBA\u3002</p>',43),_={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626396&idx=1&sn=af7c8d8e9baebb44dcb632eda328c0e0&chksm=8b5cb266bc2b3b70292832f297cebc76f44e8bf3483f4a3dddd5e33b6f32fe66fb0c617837a1&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=c("\u67E5\u770B\u539F\u6587");function b(f,l){const e=r("ExternalLinkIcon");return t(),n(s,null,[g,p("p",null,[p("a",_,[h,o(e)])])],64)}var u=d(i,[["render",b]]);export{u as default};
