import{r as t,o as a,c as r,a as e,b as c,F as n,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=o('<h3 id="\u5EB7\u590D\u6309\u6469\u80FD\u6CBB\u597D\u8111\u762B\u5417" tabindex="-1"><a class="header-anchor" href="#\u5EB7\u590D\u6309\u6469\u80FD\u6CBB\u597D\u8111\u762B\u5417" aria-hidden="true">#</a> \u5EB7\u590D\u6309\u6469\u80FD\u6CBB\u597D\u8111\u762B\u5417\uFF1F</h3><p>\u626D\u6643\u5B69\u5B50\u8111\u888B\u6CBB\u75C5\u7684\u5F53\u4E8B\u533B\u9662\uFF0C\u6E56\u5317\u7701\u5987\u5E7C\u4FDD\u5065\u9662\u51FA\u6765\u6F84\u6E05\u4E86\uFF0C\u89C6\u9891\u91CC\u88AB\u6CBB\u7597\u7684\u90A3\u4E2A\u5B69\u5B50\uFF0C\u65E2\u4E0D\u662F\u505A\u666E\u901A\u7684\u5C0F\u513F\u63A8\u62FF\uFF0C\u4E5F\u4E0D\u662F\u5728\u6CBB\u7597\u659C\u9888\uFF0C\u800C\u662F\u5728\u6CBB\u7597\u8111\u762B\u3002</p><p>\u636E\u533B\u9662\u4ECB\u7ECD\uFF0C\u201C\u8FD9\u79CD\u624B\u6CD5\u770B\u4F3C\u5267\u70C8\uFF0C\u4F46\u975E\u5E38\u5B89\u5168\uFF0C\u5B69\u5B50\u5934\u90E8\u6D3B\u52A8\u5E45\u5EA6\u5168\u90E8\u4F4D\u4E8E\u6B63\u5E38\u7684\u6781\u9650\u8303\u56F4\u5185\uFF0C\u4E0D\u4F1A\u5BF9\u5B69\u5B50\u9AA8\u9ABC\u9020\u6210\u5F71\u54CD\u3002\u800C\u6309\u6469\u7684\u9891\u7387\u4E5F\u662F\u6839\u636E\u5B69\u5B50\u7684\u75C5\u60C5\u6765\u51B3\u5B9A\u7684\u3002\u201D</p><p>\u7136\u540E\u6709\u4E0D\u5C11\u4EBA\u89C9\u5F97\u5267\u60C5\u53CD\u8F6C\u4E86\uFF0C\u4E5F\u6709\u4EBA\u5728\u5FAE\u535A\u4E0A\u8BF4\u6211\u501F\u8FD9\u4E2A\u89C6\u9891\u9020\u8C23\u8BFD\u8C24\u4E2D\u533B\u5C0F\u513F\u63A8\u62FF\uFF0C\u4E5F\u6709\u4EBA\u8BF4\u6211\u88AB\u6253\u8138\u4E86\u3002</p><p>\u867D\u7136\u6211\u4E00\u76F4\u8BA4\u4E3A\u4E2D\u533B\u5C0F\u513F\u63A8\u62FF\u662F\u88C5\u795E\u5F04\u9B3C\uFF0C\u4E0D\u5EFA\u8BAE\u5BB6\u957F\u53BB\u505A\u3002\u4F46\u5BF9\u4E8E\u8FD9\u4E2A\u89C6\u9891\uFF0C\u65E0\u8BBA\u662F\u5728\u5FAE\u535A\u8FD8\u662F\u5FAE\u4FE1\u4E0A\uFF0C\u6211\u90FD\u6CA1\u6709\u65AD\u8A00\u8FD9\u662F\u5728\u4E2D\u533B\u5C0F\u513F\u63A8\u62FF\uFF0C\u76F8\u53CD\u6211\u8FD8\u8BF4\u4E2D\u533B\u5C0F\u513F\u63A8\u62FF\u5F88\u5C11\u6709\u8FD9\u4E48\u5267\u70C8\u7684\u64CD\u4F5C\uFF0C\u6211\u4E0D\u77E5\u9053\u6253\u7684\u4EC0\u4E48\u8138\u3002</p><p>\u8FD9\u4E2A\u533B\u751F\u662F\u5728\u6CBB\u7597\u8111\u762B\uFF0C\u8FD9\u5F20\u505A\u6CD5\u5C31\u662F\u5BF9\u7684\u5417\uFF1F</p><p>\u8111\u762B\u662F\u6307\u5B69\u5B50\u56E0\u4E3A\u79CD\u79CD\u539F\u56E0\u5927\u8111\u53D7\u635F\u5BB3\uFF0C\u5BFC\u81F4\u8EAB\u4F53\u4E00\u4E9B\u6B63\u5E38\u529F\u80FD\u51FA\u73B0\u4E86\u95EE\u9898\uFF0C\u6BD4\u5982\u8FD0\u52A8\u3001\u808C\u8089\u534F\u8C03\u3001\u5E73\u8861\u80FD\u529B\uFF0C\u8BED\u8A00\u3001\u667A\u529B\u7B49\u3002\u5BF9\u4E8E\u4E3B\u8981\u4E3A\u808C\u8089\u548C\u80A2\u4F53\u529F\u80FD\u969C\u788D\u7684\u5B69\u5B50\uFF0C\u901A\u8FC7\u5EB7\u590D\u6309\u6469\u786E\u5B9E\u53EF\u4EE5\u6539\u5584\u90E8\u5206\u5B69\u5B50\u7684\u75C7\u72B6\uFF0C\u63D0\u5347\u751F\u6D3B\u80FD\u529B\u3002</p><p>\u6211\u4E0D\u77E5\u9053\u89C6\u9891\u91CC\u8FD9\u4E2A\u5B69\u5B50\u7684\u5177\u4F53\u75C5\u60C5\uFF0C\u4E0D\u597D\u5224\u65AD\u505A\u9888\u90E8\u626D\u6643\u8FD9\u5F20\u52A8\u4F5C\u662F\u5426\u5FC5\u8981\uFF0C\u4F46\u5373\u4FBF\u9700\u8981\u505A\uFF0C\u4E5F\u6CA1\u5FC5\u8981\u8FD9\u6837\u7C97\u66B4\u5267\u70C8\uFF0C\u539F\u56E0\u6211\u4E5F\u8BF4\u8FC7\uFF0C\u56E0\u4E3A\u6709\u9020\u6210\u9888\u690E\u635F\u4F24\u548C\u5A74\u513F\u6447\u6643\u7EFC\u5408\u5F81\u7684\u98CE\u9669\uFF0C\u66F4\u4E3B\u8981\u7684\uFF0C\u76EE\u524D\u6CA1\u6709\u8BC1\u636E\u8BC1\u660E\u8FD9\u79CD\u7C97\u66B4\u5267\u70C8\u7684\u64CD\u4F5C\u6709\u4EC0\u4E48\u989D\u5916\u7684\u597D\u5904\u3002</p><p>\u81F3\u4E8E\u533B\u9662\u8BF4\u7684\u4EC0\u4E48\u6309\u6469\u9891\u7387\u53EF\u4EE5\u6839\u636E\u5B69\u5B50\u75C5\u60C5\u6765\uFF0C\u4E0D\u8FC7\u662F\u7ED9\u81EA\u5DF1\u627E\u7406\u7531.\u4EFB\u4F55\u4E00\u4E2A\u5EB7\u590D\u6307\u5357\u91CC\u4E5F\u6CA1\u6709\u8BF4\u5BF9\u54EA\u79CD\u75C5\u60C5\u9700\u8981\u5FEB\u901F\u5267\u70C8\u7684\u7275\u5F15\uFF0C\u4E5F\u6CA1\u6709\u8BC1\u636E\u8BC1\u660E\u8FD9\u79CD\u5FEB\u901F\u7684\u626D\u52A8\u5BF9\u75C5\u60C5\u6709\u4EC0\u4E48\u989D\u5916\u7684\u5E2E\u52A9\uFF0C\u75C5\u4EBA\u592A\u591A\uFF0C\u533B\u751F\u56FE\u5FEB\u624D\u505A\u8FD9\u79CD\u5FEB\u901F\u800C\u7C97\u66B4\u7684\u626D\u52A8\u5012\u662F\u53EF\u80FD\u6027\u5F88\u5927\u3002</p><p>\u5728\u6F84\u6E05\u7684\u62A5\u9053\u91CC\uFF0C\u8FD8\u63D0\u5230\u4E00\u4E9B\u5BB6\u957F\u5BF9\u201C\u533B\u751F\u8650\u7AE5\u201D\u7684\u8BF4\u6CD5\u611F\u5230\u6C14\u6124\uFF0C\u7F51\u4E0A\u8FD8\u6709\u4EBA\u732E\u8EAB\u8BF4\u6CD5\u8BF4\u81EA\u5DF1\u5B69\u5B50\u7684\u8111\u762B\u5C31\u662F\u5728\u90A3\u4E2A\u533B\u751F\u6CBB\u597D\u7684\u3002\u4E5F\u6709\u4EBA\u8BF4\u90A3\u4E2A\u8BCA\u5BA4\u91CC\u6302\u6EE1\u4E86\u9526\u65D7\uFF0C\u8BF4\u660E\u8FD9\u4E2A\u533B\u751F\u6C34\u5E73\u9AD8\uFF0C\u4EE5\u6B64\u5224\u65AD\u8FD9\u4E2A\u533B\u751F\u7684\u505A\u6CD5\u662F\u6709\u9053\u7406\u7684\u3002</p><p>\u4E0A\u9762\u8FD9\u4E9B\u8BF4\u6CD5\u662F\u6CA1\u6709\u903B\u8F91\u7684\uFF0C\u4E00\u4E2A\u533B\u751F\u7684\u53E3\u7891\u548C\u6001\u5EA6\u3001\u5FFD\u60A0\u3001\u6280\u672F\u7B49\u76F8\u5173\uFF0C\u533B\u751F\u53E3\u7891\u597D\u4E0D\u4EE3\u8868\u533B\u672F\u597D\uFF0C\u4E5F\u4E0D\u4EE3\u8868\u4ED6\u91C7\u7528\u7684\u6CBB\u7597\u662F\u6709\u6548\u7684\u3002\u7528\u201C\u5FFD\u60A0\u201D\u8FD9\u4E2A\u8BCD\u662F\u6709\u4E2A\u4F9D\u636E\u7684\uFF0C\u6211\u53BB\u8FD9\u5BB6\u533B\u9662\u5F53\u4E8B\u79D1\u5BA4\u7684\u89E3\u91CA\u91CC\u770B\u4E86\u4E00\u4E0B\uFF0C\u5728\u8FD9\u4E2A\u79D1\u5BA4\u7ADF\u7136\u5BA3\u4F20\u8111\u762B\u6CBB\u6108\u7387\u572880%\u4EE5\u4E0A\u3002</p><p><em>\uFF08\u622A\u56FE\u81EAhttp://www.hbfy.com\uFF09</em></p><p>\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u8111\u762B\u662F\u5927\u8111\u53D1\u80B2\u5F02\u5E38\u6216\u53D7\u635F\u5BB3\u5F15\u8D77\u7684\uFF0C\u75C7\u72B6\u6709\u5404\u79CD\u8868\u73B0\uFF0C\u75C5\u60C5\u4E5F\u6709\u8F7B\u6709\u91CD\uFF0C\u76EE\u524D\u6240\u6709\u7684\u6CBB\u7597\u53EA\u80FD\u6539\u5584\u75C7\u72B6\uFF0C\u63D0\u9AD8\u5B69\u5B50\u751F\u6D3B\u8D28\u91CF\uFF0C\u65E9\u671F\u5E72\u9884\u7684\u6548\u679C\u4E5F\u4F1A\u66F4\u597D\uFF0C\u6709\u4E9B\u75C7\u72B6\u6BD4\u8F83\u8F7B\u7684\u5B69\u5B50\u901A\u8FC7\u5E72\u9884\uFF0C\u53EF\u4EE5\u63A5\u8FD1\u6B63\u5E38\u4EBA\u7684\u751F\u6D3B\u3002\u4F46\u8111\u762B\u4E0D\u80FD\u591F\u6CBB\u6108\uFF0C\u7F8E\u56FD\u56FD\u7ACB\u795E\u7ECF\u75C5\u53CA\u4E2D\u98CE\u7814\u7A76\u6240[1]\uFF0C\u7F8E\u56FD\u75BE\u75C5\u63A7\u5236\u4E2D\u5FC3\u4E0A[2]\u90FD\u660E\u786E\u8BF4\u4E86\u8FD9\u4E00\u70B9\u3002</p><p>\u5982\u679C\u4E00\u5BB6\u533B\u7597\u673A\u6784\u8BF4\u81EA\u5DF1\u7684\u8111\u762B\u6CBB\u6108\u7387\u572880%\u4EE5\u4E0A\uFF0C\u8981\u4E48\u662F\u5FFD\u60A0\uFF0C\u8981\u4E48\u662F\u4ED6\u4EEC\u5B58\u5728\u8FC7\u5EA6\u8BCA\u65AD\uFF0C\u5C06\u5F88\u591A\u672C\u4E0D\u662F\u8111\u762B\u7684\u5B69\u5B50\u8BCA\u65AD\u4E3A\u8111\u762B\uFF0C\u7136\u540E\u505A\u672C\u53EF\u4EE5\u4E0D\u505A\u7684\u6CBB\u7597\u3002</p><p>\u5A74\u513F\u7684\u4F53\u68C0\u4E0A\u5F88\u96BE\u914D\u5408\u533B\u751F\uFF0C\u67E5\u4F53\u7684\u65F6\u5019\u5B69\u5B50\u4E00\u7528\u529B\uFF0C\u533B\u751F\u5C31\u5224\u65AD\u4E3A\u808C\u5F20\u529B\u9AD8\uFF0C\u7136\u540E\u4E0B\u4E2A\u53EF\u7591\u8111\u762B\u7684\u8BCA\u65AD\uFF0C\u7136\u540E\u5C31\u5F00\u59CB\u6CBB\u7597\uFF0C\u6CBB\u7597\u4E00\u6BB5\u65F6\u95F4\u540E\uFF0C\u53D1\u73B0\u5B69\u5B50\u6839\u672C\u5C31\u6CA1\u95EE\u9898\uFF0C\u7136\u540E\u5C31\u8BF4\u628A\u8111\u762B\u6CBB\u6108\u4E86\uFF0C\u8FD9\u53EF\u80FD\u5C31\u662F\u6CBB\u6108\u7387\u572880%\u4EE5\u4E0A\u7684\u79D8\u5BC6\u4E86\u3002</p><p>\u8FD9\u91CC\u81EA\u7136\u6709\u533B\u60A3\u5173\u7CFB\u7D27\u5F20\uFF0C\u533B\u751F\u62C5\u5FC3\u6F0F\u8BCA\u628A\u8BCA\u65AD\u6307\u5F81\u653E\u5BBD\u4E86\u56E0\u7D20\uFF0C\u4E5F\u6709\u4E00\u4E9B\u662F\u6709\u9010\u5229\u7684\u9700\u6C42\uFF0C\u6BD4\u5982\u8FD9\u5BB6\u5F53\u4E8B\u533B\u9662\uFF0C\u5BF9\u4E8E\u8111\u762B\u6709\u201C\u4EE5\u4E2D\u533B\u7279\u8272\u63A8\u62FF\u3001\u5EB7\u590D\u8FD0\u52A8\u8BAD\u7EC3\uFF08PT\uFF09\u3001\u9488\u7078\u3001\u4E2D\u836F\u53CA\u6C34\u9488\u7B49\u201D\u8BF8\u591A\u6CBB\u7597\uFF0C\u800C\u5176\u4E2D\u5F88\u591A\u662F\u6CA1\u6709\u8BC1\u636E\u8BC1\u660E\u6709\u6548\u7684\u6CBB\u7597\u3002</p><p>\u5B69\u5B50\u751F\u75C5\u4E86\uFF0C\u53EA\u8981\u80FD\u8BA9\u5B69\u5B50\u597D\u8D77\u6765\uFF0C\u5BB6\u957F\u4EC0\u4E48\u90FD\u613F\u610F\u53BB\u8BD5\uFF0C\u800C\u533B\u5B66\u5B58\u5728\u5F88\u5927\u7684\u4E13\u4E1A\u58C1\u5792\uFF0C\u5BB6\u957F\u5E76\u4E0D\u80FD\u5224\u65AD\u5404\u79CD\u7A00\u5947\u53E4\u602A\u7684\u6CBB\u7597\u662F\u4E0D\u662F\u5408\u7406\u7684\uFF0C\u6240\u4EE5\u624D\u4F1A\u51FA\u73B0\u8FD9\u6837\u75AF\u72C2\u626D\u6643\u5B69\u5B50\u7684\u5934\u90E8\uFF0C\u5BB6\u957F\u8FD8\u53EA\u80FD\u914D\u5408\u7684\u62B1\u4F4F\u5B69\u5B50\u4EFB\u51ED\u4ED6\u54ED\u95F9\u3002</p><p>\u5F88\u591A\u4EBA\u770B\u4E86\u90A3\u4E2A\u89C6\u9891\u9A82\u5B69\u5B50\u7684\u5988\u5988\uFF0C\u600E\u4E48\u4F1A\u50BB\u5230\u4EFB\u7531\u533B\u751F\u8FD9\u6837\u53BB\u626D\u6643\u81EA\u5DF1\u7684\u5B69\u5B50\uFF0C\u4F46\u60A3\u8005\u76F8\u4FE1\u533B\u751F\u662F\u5F88\u6B63\u5E38\u7684\uFF0C\u66F4\u4F55\u51B5\uFF0C\u5C31\u8BCA\u7684\u533B\u9662\u8FD8\u662F\u4E00\u5BB6\u516C\u7ACB\u7684\u7701\u7EA7\u5987\u5E7C\u4FDD\u5065\u9662\uFF0C\u6240\u4EE5\u5728\u8FD9\u4EF6\u4E8B\u4E0A\uFF0C\u6211\u89C9\u5F97\u662F\u533B\u751F\u8F9C\u8D1F\u4E86\u5BB6\u957F\u7684\u4FE1\u4EFB\u3002</p><p>\u5199\u4E0A\u9762\u7684\u8FD9\u4E9B\u8BDD\u53EF\u80FD\u4F1A\u8BA9\u60A3\u8005\u66F4\u4E0D\u4FE1\u4EFB\u533B\u9662\uFF0C\u4F46\u8FD9\u662F\u533B\u9662\u672C\u8EAB\u7684\u884C\u4E3A\u635F\u5BB3\u4E86\u60A3\u8005\u7684\u4FE1\u4EFB\u3002\u6628\u5929\u770B\u5230\u67D0\u533B\u9662\u7684\u533B\u751F\u5728\u670B\u53CB\u5708\u91CC\u8BF4\u6211\u4EEC\u8FD9\u4E9B\u4ECE\u516C\u7ACB\u533B\u9662\u8F9E\u804C\u51FA\u6765\u7684\u4EBA\u5C06\u67AA\u5934\u5BF9\u51C6\u516C\u7ACB\u533B\u9662\uFF0C\u4F46\u5176\u5B9E\u8FD9\u6837\u7684\u8BDD\u9898\u6211\u5728\u8F9E\u804C\u524D\u5C31\u5199\u8FC7\u5F88\u591A\uFF0C\u5728\u4F53\u5236\u5185\u7684\u65F6\u5019\u4E5F\u6CA1\u6709\u56DE\u907F\u8FC7\u3002</p><p>\u6211\u4E00\u76F4\u8BA4\u4E3A\uFF0C\u7F13\u89E3\u533B\u60A3\u77DB\u76FE\u4E0D\u662F\u9760\u5BF9\u95EE\u9898\u89C6\u800C\u4E0D\u89C1\u3002\u4F60\u4E0D\u8A00\u6211\u4E0D\u8A00\uFF0C\u4F46\u4E0D\u80FD\u4FDD\u8BC1\u522B\u4EBA\u4E0D\u8A00\uFF0C\u5728\u8D44\u8BAF\u65F6\u4EE3\uFF0C\u975E\u533B\u5B66\u4EBA\u58EB\u83B7\u53D6\u533B\u5B66\u4E13\u4E1A\u77E5\u8BC6\u7684\u80FD\u529B\u5E76\u4E0D\u8F93\u4E8E\u5F88\u591A\u533B\u751F\uFF0C\u7B49\u6700\u540E\u66B4\u9732\u51FA\u6765\uFF0C\u77DB\u76FE\u53EA\u4F1A\u66F4\u6FC0\u5316\u3002\u533B\u60A3\u77DB\u76FE\u7684\u7F13\u89E3\u9700\u8981\u533B\u60A3\u53CC\u65B9\u53BB\u6B63\u89C6\u81EA\u5DF1\u7684\u95EE\u9898\uFF0C\u5171\u540C\u6539\u53D8\uFF0C\u624D\u6709\u53EF\u80FD\u6539\u53D8\u3002\u6211\u8F9E\u804C\u51FA\u6765\uFF0C\u4E5F\u662F\u60F3\u505A\u70B9\u6539\u53D8\u3002</p><hr><p>\u9898\u56FE\u6765\u81EACDC\u7F51\u7AD9\u3002\u53C2\u8003\u8D44\u6599\uFF1A</p><p>1.http://www.ninds.nih.gov/disorders/cerebral_palsy/cerebral_palsy.htm</p><p>2.http://www.cdc.gov/ncbddd/cp/facts.html</p>',24),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626237&idx=1&sn=159abb77c748c63e5446c1d23739c052&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=s("\u67E5\u770B\u539F\u6587");function m(f,w){const p=t("ExternalLinkIcon");return a(),r(n,null,[_,e("p",null,[e("a",h,[l,c(p)])])],64)}var u=d(i,[["render",m]]);export{u as default};
