import{r as a,o as t,c as o,a as p,b as r,F as l,d as n,e as i}from"./app.9aaa2256.js";import{_ as c}from"./plugin-vue_export-helper.21dcd24c.js";const s={},d=n('<h3 id="\u6211\u662F\u5B69\u5B50\u5988\u5988-\u6211\u7ADF\u7136\u60F3\u8FC7\u8BA9\u4ED6\u6B7B" tabindex="-1"><a class="header-anchor" href="#\u6211\u662F\u5B69\u5B50\u5988\u5988-\u6211\u7ADF\u7136\u60F3\u8FC7\u8BA9\u4ED6\u6B7B" aria-hidden="true">#</a> \u6211\u662F\u5B69\u5B50\u5988\u5988\uFF0C\u6211\u7ADF\u7136\u60F3\u8FC7\u8BA9\u4ED6\u6B7B</h3><p>\u201C\u62B1\u7740\u5B69\u5B50\u7684\u65F6\u5019\uFF0C\u6211\u7684\u8111\u6D77\u4E2D\u4F1A\u60F3\uFF1A\u5982\u679C\u6211\u770B\u4F3C\u4E0D\u5C0F\u5FC3\u6CA1\u6709\u628A\u5B69\u5B50\u62B1\u7262\uFF0C\u6545\u610F\u8BA9\u5B69\u5B50\u6389\u5728\u5730\u4E0A\u4F1A\u600E\u4E48\u6837\uFF1F\u201D</p><p>\u5F53\u8FD9\u4E2A\u5FF5\u5934\u51FA\u73B0\u5728\u6211\u8111\u6D77\u4E2D\u4E4B\u540E\uFF0C\u6211\u8131\u624B\u5B69\u5B50\u6389\u5728\u5730\u4E0A\u7684\u753B\u9762\u4F1A\u603B\u662F\u4E0D\u53D7\u63A7\u5236\u7684\u51FA\u73B0\u5728\u6211\u7684\u8111\u6D77\u4E2D\u3002\u6211\u60F3\u8981\u628A\u8FD9\u4E2A\u60F3\u6CD5\u8BF4\u51FA\u6765\uFF0C\u6211\u89C9\u5F97\u8BF4\u51FA\u6765\u5C31\u4F1A\u597D\u5F88\u591A\uFF0C\u4F46\u662F\u6211\u4E0D\u6562\u8BF4\uFF0C\u6211\u6015\u8EAB\u8FB9\u7684\u4EBA\u89C9\u5F97\u6211\u662F\u4E00\u4E2A\u6076\u6BD2\u7684\u5988\u5988\u3002\u6211\u662F\u4E00\u4E2A\u6076\u6BD2\u7684\u5988\u5988\u5417\uFF1F</p><p>\u201C\u5728\u5B69\u5B50\u4F1A\u722C\u884C\u4F46\u662F\u4E0D\u4F1A\u8D70\u8DEF\u7684\u65F6\u5019\uFF0C\u6BCF\u6B21\u4ED6\u722C\u5230\u8FB9\u7F18\u7684\u4F4D\u7F6E\uFF0C\u6211\u4F1A\u60F3\u5982\u679C\u6211\u4E0D\u53BB\u4FDD\u62A4\u4ED6\uFF0C\u4ED6\u662F\u4E0D\u662F\u4F1A\u6389\u4E0B\u53BB\uFF1F\u5982\u679C\u6211\u771F\u7684\u50F5\u4F4F\u4E86\u600E\u4E48\u529E\uFF1F\u6211\u662F\u4E0D\u662F\u5C31\u662F\u60F3\u8BA9\u5B69\u5B50\u53D7\u4F24\uFF1F\u201D</p><p>\u5176\u4ED6\u7684\u60F3\u6CD5\u53EF\u80FD\u8FD8\u5305\u62EC\uFF1A</p><ul><li><p>\u4F7F\u52B2\u6447\u6643\u5B9D\u5B9D\u3002</p></li><li><p>\u7528\u529B\u6253\u5B9D\u5B9D\u3002</p></li><li><p>\u5C06\u5A74\u513F\u6254\u5230\u5730\u4E0A\u6216\u5899\u4E0A\u3002</p></li><li><p>\u523A\u7834\u5A74\u513F\u7684\u56DF\u95E8\u3002</p></li><li><p>\u5C06\u5A74\u513F\u6DF9\u6CA1\u5728\u6D74\u76C6\u4E2D\u3002</p></li><li><p>\u7528\u5976\u74F6\u6216\u6795\u5934\u7B49\u95F7\u4F4F\u5A74\u513F\u3002</p></li><li><p>\u5C06\u5A74\u513F\u8F66\u4E2D\u7684\u5A74\u513F\u63A8\u4E0B\u5C71\u6216\u63A8\u8FDB\u8F66\u6D41\u3002</p></li></ul><p>\u4EBA\u7684\u60F3\u6CD5\u662F\u5947\u5999\u7684\uFF0C\u6211\u4EEC\u7684\u5934\u8111\u4E2D\u6709\u65F6\u4F1A\u6D6E\u73B0\u51FA\u4E00\u4E9B\u4E0E\u4E2A\u4EBA\u4EF7\u503C\u89C2\u76F8\u53CD\u7684\u5FF5\u5934\u3002</p><p>\u6211\u4EEC\u7684\u5927\u8111\u4F1A\u53BB\u601D\u8003\u201C\u6211\u662F\u5426\u4F1A\u53BB\u505A\u8FD9\u4EF6\u4E0D\u597D\u7684\u4E8B\u60C5\uFF1F\u201D\u201C\u8FD9\u4E2A\u4E0D\u597D\u7684\u4E8B\u60C5\u771F\u7684\u4F1A\u53D1\u751F\u5417\uFF1F\u201D\u6709\u65F6\u5019\u8FD9\u4E9B\u5FF5\u5934\u662F\u4E00\u4E2A\u753B\u9762\uFF0C\u7A81\u7136\u6D6E\u73B0\u5728\u8111\u6D77\u4E2D\u3002</p><p>\u4F60\u662F\u5426\u6709\u4EE5\u4E0B\u4F53\u9A8C\uFF1F\u6BD4\u5982\u5728\u8DEF\u8FB9\u7B49\u516C\u4EA4\u8F66\u65F6\uFF0C\u6709\u65F6\u5019\u4F1A\u60F3\u201C\u73B0\u5728\u9A6C\u8DEF\u4E0A\u8F66\u6D41\u5F88\u591A\uFF0C\u5982\u679C\u6211\u8FD9\u4E2A\u65F6\u5019\u51B2\u4E0A\u9A6C\u8DEF\u4F1A\u600E\u6837\uFF1F\u201D\u4F60\u5728\u5750\u7535\u68AF\u7684\u65F6\u5019\uFF0C\u662F\u5426\u60F3\u8FC7\u201C\u5982\u679C\u7535\u68AF\u7A81\u7136\u4E0B\u843D\u600E\u4E48\u529E\uFF1F\u201D\u201C\u5982\u679C\u7535\u68AF\u505C\u4E0B\uFF0C\u5F00\u95E8\u662F\u5835\u5899\u600E\u4E48\u529E\uFF1F\u201D</p><p>\u5176\u5B9E\u5728\u751F\u6D3B\u4E2D\u8FD9\u4E9B\u5947\u602A\u7684\u3001\u7A81\u7136\u51FA\u73B0\u7684\u3001\u7247\u6BB5\u7684\u753B\u9762\u548C\u60F3\u6CD5\u5728\u8111\u6D77\u4E2D\u95EA\u73B0\u662F\u975E\u5E38\u666E\u904D\u7684\uFF0C\u6211\u4EEC\u79F0\u4E4B\u4E3A\u201C\u4FB5\u5165\u6027\u601D\u60F3\u201D\uFF0C\u8FD9\u662F\u5927\u8111\u9884\u6D4B\u4E8B\u60C5\u7ED3\u679C\u3001\u8BC6\u522B\u5371\u9669\u3001\u4FDD\u62A4\u6211\u4EEC\u7684\u5B89\u5168\u7684\u65B9\u5F0F\u3002</p><p>\u5F53\u6211\u4EEC\u521D\u4E3A\u4EBA\u7236\u6BCD\u7684\u65F6\u5019\uFF0C\u8111\u6D77\u4E2D\u53EF\u80FD\u4F1A\u63A7\u5236\u4E0D\u4F4F\u7684\u51FA\u73B0\u4E00\u4E9B\u60F3\u8981\u4F24\u5BB3\u5B69\u5B50\u7684\u60F3\u6CD5\u3001\u6216\u8005\u6D6E\u73B0\u51FA\u5B69\u5B50\u53D7\u4F24\u7684\u753B\u9762\uFF0C\u8FD9\u79CD\u60C5\u51B5\u901A\u5E38\u4F1A\u5728\u5B69\u5B50\u964D\u751F\u540E\u6700\u521D\u7684\u51E0\u5468\u6216\u51E0\u4E2A\u6708\u91CC\u51FA\u73B0\uFF0C\u8FD9\u4E9B\u60F3\u6CD5\u7684\u51FA\u73B0\u53EF\u80FD\u662F\u56E0\u4E3A\u5B69\u5B50\u5BF9\u6211\u4EEC\u6765\u8BF4\u975E\u5E38\u91CD\u8981\uFF0C\u7236\u6BCD\u975E\u5E38\u770B\u91CD\u5B69\u5B50\u662F\u5426\u5B89\u5168\u662F\u5426\u5065\u5EB7\uFF0C\u6211\u4EEC\u4F1A\u7559\u610F\u751F\u6D3B\u4E2D\u4EFB\u4F55\u53EF\u80FD\u4F1A\u4F24\u5BB3\u5230\u5B69\u5B50\u7684\u4E1C\u897F\uFF0C\u5305\u62EC\u6211\u4EEC\u81EA\u5DF1\u3002</p><p>\u6709\u7814\u7A76\u53D1\u73B0\u591A\u8FBE91%\u7684\u65B0\u5988\u5988\u548C88%\u7684\u65B0\u7238\u7238\u90FD\u66FE\u6709\u8FC7\u5A74\u513F\u4F1A\u53D7\u5230\u4F24\u5BB3\u7684\u60F3\u6CD5[1]\u3002\u8FD9\u6765\u81EA\u4E8E\u6211\u4EEC\u5927\u8111\u4E2D\u4E00\u79CD\u672C\u80FD\u7684\u8B66\u89C9\u6027\uFF0C\u6BCD\u4EB2\u672C\u80FD\u7684\u611F\u53D7\u5230\u5A74\u513F\u7684\u8106\u5F31\uFF0C\u672C\u80FD\u7684\u505A\u51FA\u8981\u6392\u9664\u73AF\u5883\u4E2D\u4E00\u5207\u53EF\u80FD\u4F24\u5BB3\u5B69\u5B50\u7684\u56E0\u7D20\uFF0C\u5FC5\u987B\u4FDD\u62A4\u5B69\u5B50\u7684\u51B2\u52A8\u3002</p><p>\u6709\u7684\u5988\u5988\u4F1A\u62C5\u5FC3\uFF0C\u5F53\u6211\u7684\u8111\u6D77\u4E2D\u6709\u8FD9\u4E9B\u60F3\u6CD5\u6216\u8005\u753B\u9762\u7684\u65F6\u5019\uFF0C\u662F\u5426\u610F\u5473\u7740\u6211\u771F\u7684\u4F1A\u5931\u624B\u4F24\u5BB3\u6211\u7684\u5B69\u5B50\uFF1F\u5927\u6982\u7387\u6765\u8BF4\uFF0C\u8FD9\u4E9B\u5FF5\u5934\u548C\u753B\u9762\u4E0D\u4F1A\u53D8\u6210\u73B0\u5B9E\u3002</p><p>\u4E00\u4E9B\u5988\u5988\u770B\u5230\u65B0\u95FB\u62A5\u9053\uFF0C\u6709\u4E9B\u4EA7\u540E\u7684\u5973\u6027\u4F1A\u771F\u7684\u505A\u51FA\u4F24\u5BB3\u5B69\u5B50\u7684\u884C\u4E3A\uFF0C\u6BD4\u5982\u6454\u6253\u5B69\u5B50\u3001\u7528\u5DE5\u5177\u4F24\u5BB3\u5B69\u5B50\u7B49\u7B49\u3002</p><p>\u8FD9\u4E9B\u771F\u7684\u505A\u51FA\u4F24\u5BB3\u5B69\u5B50\u4E8B\u60C5\u7684\u5988\u5988\u53EF\u80FD\u672C\u8EAB\u5728\u7ECF\u5386\u4E25\u91CD\u7684\u60C5\u7EEA\u95EE\u9898\uFF0C\u6BD4\u5982\u91CD\u5EA6\u7684\u4EA7\u540E\u6291\u90C1\uFF0C\u6216\u8005\u8FD9\u4E9B\u5988\u5988\u5728\u6000\u5B55\u4E4B\u524D\u7684\u6027\u683C\u5C31\u6709\u4E00\u4E9B\u504F\u6FC0\u3002</p><p>\u4EA7\u751F\u4F24\u5BB3\u5B69\u5B50\u7684\u4FB5\u5165\u6027\u60F3\u6CD5\u4E0E\u4E25\u91CD\u7684\u4EA7\u540E\u60C5\u7EEA\u95EE\u9898\u4E4B\u95F4\u6709\u4E00\u4E2A\u754C\u9650\uFF0C\u5C31\u662F\uFF1A\u4F60\u662F\u5982\u4F55\u770B\u5F85\u5934\u8111\u4E2D\u51FA\u73B0\u7684\u4F24\u5BB3\u5B69\u5B50\u7684\u60F3\u6CD5\u7684\uFF1F\u6240\u4EE5\uFF0C\u5728\u81EA\u5DF1\u4EA7\u751F\u8FD9\u4E9B\u60F3\u6CD5\u65F6\uFF0C\u4E0D\u59A8\u95EE\u81EA\u5DF1\u51E0\u4E2A\u7B80\u5355\u7684\u95EE\u9898\uFF1A</p><ul><li><p>\u5F53\u4F60\u6709\u8FD9\u4E9B\u60F3\u6CD5\u65F6\uFF0C\u4F60\u4F1A\u600E\u4E48\u505A\uFF1F\u771F\u7684\uFF08\u6216\u66FE\u7ECF\u5C1D\u8BD5\uFF09\u4F1A\u53BB\u4F24\u5BB3\u5B9D\u5B9D\u5417\uFF1F</p></li><li><p>\u6709\u8FD9\u4E9B\u60F3\u6CD5\u5BF9\u4F60\u6765\u8BF4\u610F\u5473\u7740\u4EC0\u4E48\uFF1F\u4F8B\u5982\u6BCF\u5929\u6D88\u8017\u5F88\u591A\u65F6\u95F4\u7EA0\u7ED3\u4E8E\u8FD9\u4E9B\u60F3\u6CD5\u4E2D\uFF1F\u8FD8\u662F\u77AC\u95F4\u5C31\u597D\u4E86\uFF1F</p></li><li><p>\u5F53\u4F60\u6709\u8FD9\u4E9B\u60F3\u6CD5\u7684\u65F6\u5019\uFF0C\u4F60\u611F\u89C9\u81EA\u5DF1\u5904\u4E8E\u4E00\u4E2A\u4EC0\u4E48\u72B6\u6001\uFF1F\u6BD4\u5982\u75B2\u52B3\u3001\u6291\u90C1\u3001\u7126\u8651\u7B49\u7B49\u3002</p></li></ul><p>\u5982\u679C\u81EA\u5DF1\u7ED9\u81EA\u5DF1\u7684\u56DE\u7B54\u90FD\u662F\u6BD4\u8F83\u8D1F\u9762\u7684\uFF0C\u90A3\u4E48\u53EF\u80FD\u6709\u5FC5\u8981\u6C42\u52A9\u66F4\u4E13\u4E1A\u7684\u8D44\u6E90\u4E86\u3002</p><p>\u4ECE\u62C5\u5FC3\u201C\u5B69\u5B50\u662F\u5426\u5403\u7684\u591F\u7761\u7684\u8DB3\uFF1F\u201D\u5230\u201C\u6211\u662F\u5426\u4F1A\u5931\u624B\u6454\u4F24\u5B69\u5B50\uFF1F\u201D\u8FD9\u4E9B\u5185\u5BB9\u8D8A\u6765\u8D8A\u4E25\u91CD\u7684\u62C5\u5FC3\u4E2D\uFF0C\u4F60\u7684\u60F3\u6CD5\u843D\u5728\u54EA\u4E2A\u53EF\u6015\u7A0B\u5EA6\u4E0A\u5E76\u4E0D\u662F\u6700\u91CD\u8981\u7684\uFF0C\u800C\u662F\u4F60\u662F\u5426\u89C9\u5F97\u4F24\u5BB3\u5B69\u5B50\u3001\u5E26\u5B69\u5B50\u79BB\u5F00\u8FD9\u4E2A\u4E16\u754C\u662F\u4E00\u4E2A\u6B63\u786E\u7684\u4E8B\u60C5\uFF1F</p><p>\u90A3\u4E9B\u60C5\u7EEA\u95EE\u9898\u4E25\u91CD\u7684\u6BCD\u4EB2\uFF0C\u5728\u4F24\u5BB3\u5B69\u5B50\u65F6\u5927\u8111\u88AB\u60C5\u7EEA\u63A7\u5236\u800C\u5931\u53BB\u7406\u667A\uFF0C\u6216\u8005\u5BF9\u751F\u6D3B\u7EDD\u671B\u8BA4\u4E3A\u5E26\u5B69\u5B50\u4E00\u8D77\u79BB\u5F00\u8FD9\u4E2A\u4E16\u754C\u662F\u4E24\u4E2A\u4EBA\u7684\u89E3\u8131\u3002\u8FD9\u79CD\u60C5\u51B5\u662F\u9700\u8981\u7ACB\u5373\u770B\u533B\u751F\uFF0C\u5BFB\u6C42\u533B\u5B66\u9014\u5F84\u53BB\u6539\u5584\u6BCD\u4EB2\u7684\u7CBE\u795E\u72B6\u51B5\u7684\u3002</p><p><strong>\u90A3\u4E48\u5982\u4F55\u51CF\u5C11\u4F24\u5BB3\u5B69\u5B50\u7684\u5FF5\u5934\u7684\u53D1\u751F\uFF1F</strong></p><ol><li><p>\u610F\u8BC6\u5230\u5728\u5B9D\u5B9D\u51FA\u751F\u540E\uFF0C\u8111\u6D77\u4E2D\u4EA7\u751F\u8FD9\u4E9B\u5FF5\u5934\u662F\u6B63\u5E38\u7684\u3002\u5927\u6982\u5728\u4E24\u4E2A\u6708\u5DE6\u53F3\u5FF5\u5934\u51FA\u73B0\u7684\u6B21\u6570\u4F1A\u9010\u6E10\u51CF\u5C11\u3002</p></li><li><p>\u964D\u4F4E\u5BF9\u4E8E\u8111\u6D77\u4E2D\u8FD9\u4E9B\u5FF5\u5934\u7684\u6050\u60E7\u548C\u7126\u8651\u3002\u5F53\u8FD9\u4E9B\u5FF5\u5934\u51FA\u73B0\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5C1D\u8BD5\u6DF1\u547C\u5438\uFF0C\u548C\u8EAB\u8FB9\u4FE1\u4EFB\u7684\u4EBA\u804A\u804A\u8FD9\u4E9B\u5FF5\u5934\uFF0C\u5C1D\u8BD5\u628A\u6CE8\u610F\u529B\u653E\u5728\u73B0\u5B9E\u7684\u4E8B\u60C5\u4E0A\uFF08\u770B\u7740\u5B69\u5B50\u7684\u773C\u775B\uFF0C\u89C2\u5BDF\u4ED6\u7684\u547C\u5438\uFF0C\u56DE\u5230\u5F53\u4E0B\u6B63\u5728\u505A\u7684\u4E8B\u60C5\u7B49\u7B49\uFF09\u3002\u5F53\u6211\u4EEC\u8BA9\u8FD9\u4E9B\u5FF5\u5934\u987A\u5176\u81EA\u7136\u7684\u6765\u53BB\uFF0C\u53CD\u800C\u51FA\u73B0\u7684\u6B21\u6570\u4F1A\u51CF\u5C11\u3002</p></li><li><p>\u505A\u4E00\u4E9B\u8BA9\u81EA\u5DF1\u8F7B\u677E\u548C\u5F00\u5FC3\u7684\u4E8B\u60C5\u3002\u4FDD\u6301\u4E00\u4E2A\u7A33\u5B9A\u7684\u60C5\u7EEA\uFF0C\u589E\u52A0\u517B\u80B2\u5B69\u5B50\u7684\u81EA\u4FE1\uFF0C\u4F1A\u8BA9\u4F60\u80FD\u591F\u66F4\u52A0\u8F7B\u677E\u7684\u9762\u5BF9\u8FD9\u4E9B\u5FF5\u5934\u3002</p></li></ol><p>\u5468\u5A31\u83C1\u533B\u751F\u5728\u6021\u79BE\u533B\u7597\u51FA\u8BCA\uFF0C\u6709\u76F8\u5173\u9700\u6C42\u53EF\u70B9\u51FB\u201C<strong>\u9605\u8BFB\u539F\u6587</strong>\u201D\u9884\u7EA6\u533B\u751F\u3002</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p>Kate Rope. Strong as a Mother: How to Stay Healthy, Happy and (Most Importantly) Sane from Pregnancy to Parenthood. St. Martin\u2019s Press. 2018,5.</p><hr><p>\u672C\u6587\u9996\u53D1\u4E8E\u300C\u6021\u79BE\u5FC3\u7406\u300D</p><p>\u5173\u6CE8\u540E\u53EF\u66F4\u597D\u5730\u4E86\u89E3\u81EA\u5DF1\u548C\u5B69\u5B50</p>',28),_={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651632975&idx=1&sn=6a51a9689ac318e6bd942a928a3fa798&chksm=8b5c9cb5bc2b15a3de4225daa267aaf82d3365041cfed380bf6b0bd46fa8df933c3b7725aa17&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=i("\u67E5\u770B\u539F\u6587");function f(m,b){const e=a("ExternalLinkIcon");return t(),o(l,null,[d,p("p",null,[p("a",_,[h,r(e)])])],64)}var g=c(s,[["render",f]]);export{g as default};
