import{r as t,o as n,c as p,a as e,b as o,F as a,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=c('<h3 id="\u5B69\u5B50\u53D1\u70E7\u5230\u5E95\u600E\u6837\u7A7F\u8863\u670D" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u53D1\u70E7\u5230\u5E95\u600E\u6837\u7A7F\u8863\u670D" aria-hidden="true">#</a> \u5B69\u5B50\u53D1\u70E7\u5230\u5E95\u600E\u6837\u7A7F\u8863\u670D\uFF1F</h3><p>\u4F53\u6E29\u662F\u7531\u4E0B\u4E18\u8111\u91CC\u7684\u4F53\u6E29\u4E2D\u67A2\u63A7\u5236\u7684\uFF0C\u5B83\u5C31\u50CF\u6211\u4EEC\u4F53\u6E29\u7684\u9065\u63A7\u5668\uFF0C\u6B63\u5E38\u60C5\u51B5\u4E0B\u5B83\u4F1A\u628A\u4F53\u6E29\u8BBE\u5B9A\u572837\xB0C\u5DE6\u53F3\u3002\u751F\u75C5\u65F6\uFF0C\u5982\u679C\u9065\u63A7\u5668\u628A\u6E29\u5EA6\u8C03\u9AD8\u4E86\uFF0C\u6211\u4EEC\u5C31\u8981\u53D1\u70E7\u4E86\u3002</p><p>\u4F53\u6E29\u4E2D\u67A2\u4E00\u65E6\u628A\u8BBE\u5B9A\u6E29\u5EA6\u8C03\u9AD8\uFF0C\u5927\u8111\u4F1A\u7ED9\u8EAB\u4F53\u53D1\u51FA\u4FE1\u53F7\uFF0C\u8BA9\u4EBA\u611F\u89C9\u51B7\uFF0C\u7136\u540E\u901A\u8FC7\u795E\u7ECF\u548C\u6FC0\u7D20\u7684\u8C03\u8282\uFF0C\u8BA9\u8EAB\u4F53\u901A\u8FC7\u808C\u8089\u98A4\u52A8\uFF0C\u4E5F\u5C31\u662F\u201C\u5BD2\u6218\u201D\u6765\u4EA7\u70ED\uFF0C\u540C\u65F6\u901A\u8FC7\u6536\u7F29\u76AE\u80A4\u8840\u7BA1\u6765\u51CF\u5C11\u6563\u70ED\u3002\u6240\u4EE5\u5728\u5F00\u59CB\u53D1\u70E7\u7684\u65F6\u5019\u4F1A\u53EF\u80FD\u5BD2\u6218\uFF0C\u4F1A\u624B\u811A\u51B0\u51C9\u3002</p><p>\u7B49\u4F53\u6E29\u5230\u4E86\u8BBE\u5B9A\u6E29\u5EA6\uFF0C\u5B69\u5B50\u5C31\u4E0D\u4F1A\u89C9\u5F97\u51B7\u4E86\uFF0C\u8FD9\u65F6\u5BD2\u6218\u5C31\u51CF\u5C11\u4E86\uFF0C\u76AE\u80A4\u8840\u7BA1\u4E5F\u4E0D\u7528\u6536\u7F29\u4E86\uFF0C\u8FD9\u65F6\u4EA7\u70ED\u548C\u6563\u70ED\u5C31\u4F1A\u7EF4\u6301\u4E00\u4E2A\u5E73\u8861\u3002</p><p>\u70E7\u4E86\u4E00\u6BB5\u65F6\u95F4\u540E\uFF0C\u6BD4\u5982\u75C5\u60C5\u63A7\u5236\u4E86\uFF0C\u6216\u8005\u5403\u4E86\u9000\u70E7\u836F\uFF0C\u4F53\u6E29\u4E2D\u67A2\u5C31\u53EF\u80FD\u5C06\u4F53\u6E29\u8C03\u56DE37\u5EA6\u5DE6\u53F3\uFF0C\u4EBA\u4F53\u5B9E\u9645\u6E29\u5EA6\u9AD8\u4E8E\u8BBE\u5B9A\u6E29\u5EA6\uFF0C\u4EBA\u5C31\u4F1A\u89C9\u5F97\u70ED\uFF0C\u7136\u540E\u5927\u8111\u7ED9\u8EAB\u4F53\u53D1\u51FA\u4FE1\u53F7\uFF0C\u505C\u6B62\u5BD2\u6218\uFF0C\u540C\u65F6\u4E5F\u4F1A\u901A\u8FC7\u6269\u5F20\u76AE\u80A4\u8840\u7BA1\uFF0C\u542F\u52A8\u51FA\u6C57\u8FD9\u4E9B\u6563\u70ED\u65B9\u5F0F\uFF0C\u4EE5\u51CF\u5C11\u4EA7\u70ED\uFF0C\u589E\u52A0\u6563\u70ED\uFF0C\u7ED3\u679C\u5C31\u662F\u4F53\u6E29\u6062\u590D\u6B63\u5E38\u3002</p><p><strong>\u5B69\u5B50\u53D1\u70E7\u8981\u600E\u6837\u7A7F\u8863\u670D\uFF1F</strong></p><p>\u5982\u679C\u8BB0\u4F4F\u5E94\u5BF9\u53D1\u70E7\u5C31\u662F\u201C\u5C3D\u91CF\u8BA9\u5B69\u5B50\u4E0D\u96BE\u53D7\u201D\u8FD9\u4E2A\u539F\u5219\uFF0C\u5C31\u5F88\u5BB9\u6613\u77E5\u9053\u7B54\u6848\u3002</p><p>\u5B69\u5B50\u89C9\u5F97\u51B7\u7684\u65F6\u5019\u5C31\u7ED9\u4ED6\u7A7F\u76D6\uFF0C\u76F4\u5230\u4ED6\u4E0D\u89C9\u5F97\u51B7\u4E3A\u6B62\uFF0C\u8FD9\u5176\u5B9E\u4E5F\u662F\u5E2E\u52A9\u5B69\u5B50\u4F53\u6E29\u5C3D\u5FEB\u8FBE\u5230\u4F53\u6E29\u4E2D\u67A2\u6E29\u5EA6\u8BBE\u5B9A\u6E29\u5EA6\u7684\u8FC7\u7A0B\u3002\u4F60\u4E0D\u7ED9\u4ED6\u7A7F\u76D6\uFF0C\u4ED6\u5C31\u8981\u66F4\u591A\u7684\u5BD2\u6218\u6765\u4EA7\u70ED\uFF0C\u6D88\u8017\u4ED6\u66F4\u591A\u7684\u4F53\u529B\u3002\u8FD9\u65F6\u4F60\u7ED9\u4ED6\u7269\u7406\u964D\u6E29\u66F4\u662F\u9002\u5F97\u5176\u53CD\u3002</p><p>\u5F53\u5B69\u5B50\u89C9\u5F97\u70ED\u7684\u65F6\u5019\uFF0C\u8BF4\u660E\u4F53\u6E29\u4E2D\u67A2\u5DF2\u7ECF\u4E0B\u8C03\u4E86\u8BBE\u5B9A\u6E29\u5EA6\uFF0C\u5C31\u5E94\u8BE5\u51CF\u5C11\u8863\u88AB\uFF0C\u589E\u52A0\u6563\u70ED\uFF0C\u5E2E\u52A9\u4ED6\u964D\u6E29\u3002\u4F60\u4E0D\u51CF\u5C11\u8863\u88AB\uFF0C\u70ED\u91CF\u6563\u53D1\u4E0D\u51FA\u53BB\uFF0C\u4F53\u6E29\u5C31\u96BE\u4EE5\u4E0B\u964D\u3002</p><p>\u53D1\u70E7\u7684\u8FC7\u7A0B\uFF0C\u4F53\u6E29\u4F1A\u7ECF\u5386\u4E0A\u5761\u3001\u5E73\u8DEF\u3001\u4E0B\u5761\u7684\u9636\u6BB5\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u53EF\u80FD\u4F1A\u53CD\u590D\u3002\u4E0A\u5761\u9636\u6BB5\u5B69\u5B50\u4F1A\u89C9\u5F97\u51B7\uFF0C\u8FD9\u65F6\u8981\u7A7F\u76D6\u8863\u88AB\uFF0C\u4E0B\u5761\u65F6\u5B69\u5B50\u4F1A\u89C9\u5F97\u70ED\uFF0C\u51FA\u6C57\uFF0C\u8FD9\u65F6\u8981\u51CF\u5C11\u8863\u88AB\u3002</p><p>\u6240\u4EE5\u8BF7\u8BB0\u4F4F\uFF1A\u5728\u7A7F\u8863\u95EE\u9898\u4E0A\uFF0C\u53D1\u70E7\u4E0D\u53D1\u70E7\u7684\u539F\u5219\u662F\u4E00\u6837\u7684**\uFF1A\u51B7\u5C31\u7A7F\uFF0C\u70ED\u5C31\u8131\uFF0C\u600E\u4E48\u8212\u670D\u600E\u4E48\u6765**\u3002</p><p>\u4F1A\u8BF4\u8BDD\u7684\u5927\u5B69\u5B50\u5F88\u5BB9\u6613\u62A4\u7406\u3002\u4F46\u5BF9\u4E8E\u4E0D\u4F1A\u8BF4\u8BDD\u7684\u5A74\u513F\uFF0C\u62A4\u7406\u53D1\u70E7\u5C31\u9700\u8981\u5BB6\u957F\u66F4\u7EC6\u5FC3\uFF0C\u5982\u679C\u5B69\u5B50\u4F53\u6E29\u5DF2\u7ECF\u5230\u4E86\u4E2D\u67A2\u8BBE\u5B9A\u70B9\uFF0C\u4F60\u8FD8\u7EE7\u7EED\u7A7F\u76D6\u5305\u88F9\uFF0C\u5C31\u53EF\u80FD\u5BFC\u81F4\u65E0\u6CD5\u6563\u70ED\uFF0C\u5BFC\u81F4\u8131\u6C34\u3001\u4E2D\u6691\u3001\u6342\u70ED\u7EFC\u5408\u75C7\u7B49\u95EE\u9898\u3002</p><p>\u6240\u4EE5\u5BF9\u4E0D\u4F1A\u8BF4\u8BDD\u7684\u5A74\u513F\uFF0C\u6211\u4EEC\u9700\u8981\u89C2\u5BDF\u5B69\u5B50\u6709\u6CA1\u6709\u5BD2\u6218\uFF0C\u6709\u6CA1\u6709\u624B\u811A\u51B0\u51B7\u8FD9\u4E9B\u60C5\u51B5\u6765\u51B3\u5B9A\uFF0C\u5728\u4E0D\u786E\u5B9A\u7684\u65F6\u5019\u53EF\u4EE5\u91C7\u53D6\u5B81\u5C11\u52FF\u591A\u7684\u539F\u5219\uFF0C\u4E00\u65E6\u53D1\u73B0\u5B69\u5B50\u51FA\u6C57\u3001\u624B\u3001\u9762\u901A\u7EA2\u8981\u53CA\u65F6\u51CF\u8863\u3002</p><p><strong>\u4E3A\u4EC0\u4E48\u53D1\u70E7\u65F6\u611F\u89C9\u5F88\u70ED\uFF0C\u6342\u51FA\u4E00\u8EAB\u6C57\u5C31\u597D\u4E86\uFF1F</strong></p><p>\u89C9\u5F97\u70ED\u662F\u4F53\u6E29\u4E2D\u67A2\u5DF2\u7ECF\u4E0B\u8C03\u4E86\u8BBE\u5B9A\u6E29\u5EA6\uFF0C\u5B9E\u9645\u4F53\u6E29\u9AD8\u4E8E\u8BBE\u5B9A\u6E29\u5EA6\uFF0C\u624D\u4F1A\u542F\u52A8\u51FA\u6C57\u8FD9\u4E9B\u9000\u70ED\u673A\u5236\u3002\u51FA\u6C57\u662F\u9000\u70ED\u7684\u4E00\u79CD\u65B9\u5F0F\uFF0C\u6700\u540E\u7ED9\u4F60\u7684\u611F\u89C9\u662F\u51FA\u4E00\u8EAB\u6C57\u5C31\u597D\u4E86\u3002\u662F\u8981\u597D\u4E86\u624D\u4F1A\u51FA\u6C57\uFF0C\u4E0D\u662F\u51FA\u6C57\u8BA9\u4F60\u597D\u4E86\u3002</p><p><strong>\u4E3A\u4EC0\u4E48\u6211\u5403\u4E86\u9000\u70E7\u836F\u4F1A\u51FA\u4E00\u8EAB\u6C57\uFF1F</strong></p><p>\u9000\u70E7\u836F\u80FD\u4E0B\u8C03\u4F53\u6E29\u4E2D\u67A2\u8BBE\u5B9A\u7684\u6E29\u5EA6\u70B9\uFF0C\u836F\u7269\u8D77\u6548\u540E\uFF0C\u4EBA\u4F53\u5B9E\u9645\u4F53\u6E29\u9AD8\u4E8E\u4F53\u6E29\u4E2D\u67A2\u8BBE\u5B9A\u7684\u6E29\u5EA6\uFF0C\u4F60\u5C31\u4F1A\u89C9\u5F97\u70ED\uFF0C\u7136\u540E\u542F\u52A8\u5305\u62EC\u51FA\u6C57\u5728\u5185\u7684\u6563\u70ED\u673A\u5236\uFF0C\u7ED3\u679C\u5C31\u662F\u4F60\u5403\u9000\u70E7\u836F\u540E\u51FA\u4E00\u8EAB\u6C57\u4E4B\u540E\uFF0C\u53D1\u73B0\u4F53\u6E29\u5DEE\u4E0D\u591A\u6B63\u5E38\u4E86\u3002\u9000\u70E7\u836F\u662F\u901A\u8FC7\u8C03\u4F4E\u4F53\u6E29\u4E2D\u67A2\u6E29\u5EA6\u8BBE\u5B9A\u70B9\u5BFC\u81F4\u4F60\u51FA\u6C57\u7684\u3002 \uFF08\u672C\u6587\u9996\u53D1\u4E8E2016\u5E74\uFF09</p><hr><p>\u513F\u7AE5\u5E38\u89C1\u75BE\u75C5\u7684\u62A4\u7406\uFF0C\u6709\u5F88\u591A\u7684\u7EC6\u8282\uFF0C\u5BB6\u957F\u638C\u63E1\u7684\u7EC6\u8282\u8D8A\u591A\uFF0C\u5B69\u5B50\u4E5F\u5C31\u66F4\u5B89\u5168\u3002\u6021\u79BE\u66FE\u9080\u8BF7 <strong>10\u4F4D</strong> \u4E13\u79D1\u533B\u751F\uFF0C\u8054\u5408\u6253\u9020\u4E86\u4E00\u4E2A\u201C\u7236\u6BCD\u5FC5\u4FEE\u8BFE\u201D\u7684\u4E13\u680F\uFF0C\u6DB5\u76D6\u4E86<strong>\u53D1\u70E7\u3001\u54B3\u55FD\u3001\u8179\u6CFB\u3001\u6E7F\u75B9\u3001\u4FBF\u79D8\u3001\u9F3B\u7AA6\u708E\u3001\u7528\u836F\u3001\u610F\u5916\u4F24\u5BB3\u9632\u62A4</strong>\u8FD9 <strong>10</strong> \u4E2A\u4E3B\u9898\u3002</p><p>\u5B66\u4E60\u8FD9\u4E2A\u4E13\u680F\u57FA\u672C\u80FD\u638C\u63E1\u513F\u7AE5\u75BE\u75C5\u62A4\u7406\u7684\u5927\u90E8\u5206\u77E5\u8BC6\uFF0C\u6709\u9700\u8981\u7684\u53EF\u901A\u8FC7\u4E0B\u9762\u4E8C\u7EF4\u7801\u4E86\u89E3\uFF0C\u6021\u79BE\u4F1A\u5458\u53EF<strong>\u514D\u8D39</strong>\u542C\uFF0C \u9605\u8BFB\u539F\u6587 \u53EF\u4E86\u89E3\u6021\u79BE\u4F1A\u5458\u3002</p>',20),g={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651631916&idx=1&sn=e098c1e03bcc7c16ae6631b3e0869c91&chksm=8b5c98d6bc2b11c00505ac16254d6d90c323b3e92ef5426a88e56c0dd53f032ab17e1603c06b&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function l(m,b){const r=t("ExternalLinkIcon");return n(),p(a,null,[i,e("p",null,[e("a",g,[h,o(r)])])],64)}var u=d(_,[["render",l]]);export{u as default};
