import{r as t,o as n,c as r,a as e,b as o,F as a,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},m=s('<h3 id="\u4F60\u4E86\u89E3\u300C\u64E6\u817F\u7EFC\u5408\u5F81\u300D\u5417" tabindex="-1"><a class="header-anchor" href="#\u4F60\u4E86\u89E3\u300C\u64E6\u817F\u7EFC\u5408\u5F81\u300D\u5417" aria-hidden="true">#</a> \u4F60\u4E86\u89E3\u300C\u64E6\u817F\u7EFC\u5408\u5F81\u300D\u5417\uFF1F</h3><p>\u66FE\u7ECF\u63A5\u5230\u4E00\u4F4D\u5988\u5988\u7684\u54A8\u8BE2\uFF0C\u6EE1\u5C4F\u7684\u611F\u53F9\u53F7\uFF0C\u8BA9\u6211\u9694\u7740\u5C4F\u5E55\u4E5F\u80FD\u611F\u53D7\u5230\u5979\u7684\u7126\u8651\u548C\u65E0\u52A9\u3002</p><p>\u8C08\u5230\u76EE\u524D2\u5C81\u7684\u5973\u513F\uFF0C\u5706\u6DA6\u53EF\u7231\uFF0C\u6D3B\u6CFC\u5F00\u6717\uFF0C\u662F\u5168\u5BB6\u4EBA\u7684\u5F00\u5FC3\u679C\u3002</p><p>\u53EF\u662F\u57281\u5C81\u65F6\uFF0C\u5988\u5988\u5076\u7136\u53D1\u73B0\u5B69\u5B50\u5750\u5728\u6905\u5B50\u4E0A\u4F1A\u5939\u7D27\u53CC\u817F\u6469\u64E6\u3001\u4E24\u624B\u63E1\u62F3\u3001\u4E24\u988A\u6CDB\u7EA2\u3001\u4E24\u773C\u51DD\u89C6\u3001\u8868\u60C5\u4E13\u6CE8\u3002</p><p>\u6BCF\u5F53\u5BB6\u957F\u770B\u5230\u8FD9\u4E2A\u884C\u4E3A\uFF0C\u5C31\u4F1A\u795E\u8272\u7D27\u5F20\u3001\u53CA\u65F6\u5236\u6B62\uFF0C\u201C\u4F60\u600E\u4E48\u53C8\u8FD9\u6837\u4E86\uFF0C\u4E4B\u524D\u4E0D\u662F\u7B54\u5E94\u8FC7\u5988\u5988\uFF0C\u4E0D\u518D\u8FD9\u6837\u505A\u4E86\u5417\uFF1F\u201D</p><p>\u4F46\u662F\u6536\u6548\u751A\u5FAE\uFF0C\u6E10\u6E10\u5730\uFF0C\u8FD9\u4E2A\u884C\u4E3A\u53D1\u751F\u5F97\u8D8A\u6765\u8D8A\u9891\u7E41\uFF0C\u53EA\u8981\u662F\u5750\u5728\u6BD4\u8F83\u786C\u7684\u7269\u4F53\u4E0A\uFF0C\u6BD4\u5982\u9910\u6905\u3001\u5B89\u5168\u5EA7\u6905\u751A\u81F3\u5927\u4EBA\u817F\u4E0A\uFF0C\u90FD\u4F1A\u51FA\u73B0\u7C7B\u4F3C\u8868\u73B0\u3002</p><p>\u4E3A\u4E86\u51CF\u5C11\u5B69\u5B50\u51FA\u73B0\u8FD9\u6837\u7684\u884C\u4E3A\uFF0C\u4E0D\u5F97\u4E0D\u51CF\u5C11\u51FA\u95E8\uFF0C\u8FD9\u8BA9\u4E00\u5BB6\u4EBA\u975E\u5E38\u56F0\u6270\u548C\u82E6\u607C\u3002</p><p>\u8FD9\u79CD\u60C5\u51B5\u53EB\u505A\u4EA4\u53C9\u817F\u7EFC\u5408\u5F81\uFF0C\u4E5F\u53EB\u64E6\u817F\u7EFC\u5408\u5F81\uFF0C\u662F\u513F\u7AE5\u901A\u8FC7\u64E6\u817F/\u5939\u7D27\u817F\u90E8\u5F15\u8D77\u5174\u594B\u7684\u4E60\u60EF\u52A8\u4F5C\uFF0C6\u4E2A\u6708\u5DE6\u53F3\u7684\u5A74\u513F\u5F00\u59CB\u51FA\u73B0\uFF0C\u591A\u53D1\u751F\u57282\u5C81\u4EE5\u540E\uFF0C\u5973\u5B69\u591A\u4E8E\u7537\u5B69\u3002\u53D1\u4F5C\u65F6\u795E\u5FD7\u6E05\u9192\uFF0C\u591A\u5728\u7761\u524D\u3001\u9192\u540E\u53D1\u4F5C\uFF0C\u5728\u5916\u73A9\u800D\u65F6\u4E0D\u53D1\u4F5C\uFF0C\u6CE8\u610F\u529B\u5206\u6563\u65F6\u6B64\u884C\u4E3A\u53EF\u505C\u6B62\u3002</p><p>\u4EA4\u53C9\u817F\u7EFC\u5408\u5F81\u5982\u5403\u624B\u3001\u6253\u4EBA\u4E00\u6837\uFF0C\u53EF\u4EE5\u901A\u8FC7\u5408\u7406\u7684\u5F15\u5BFC\u6D88\u9000\u3002\u6BD4\u8D77\u5403\u624B\u3001\u6253\u4EBA\uFF0C\u9762\u5BF9\u8FD9\u79CD\u60C5\u51B5\u65F6\uFF0C\u5BB6\u957F\u5E38\u5E38\u89C9\u5F97\u66F4\u7D27\u5F20\u3001\u66F4\u7126\u8651\uFF0C\u5F88\u5927\u7A0B\u5EA6\u662F\u56E0\u4E3A\u5BF9\u8FD9\u4E2A\u95EE\u9898\u5B58\u5728\u4E00\u4E9B\u66F2\u89E3\u8BA4\u77E5\u3002\u6700\u5E38\u89C1\u7684\u6709\u4EE5\u4E0B\u4E24\u79CD\u60C5\u51B5\uFF1A</p><p><strong>1</strong></p><p>\u8BA4\u4E3A\u4EA4\u53C9\u817F\u7EFC\u5408\u5F81\u4E0E\u6027\u884C\u4E3A\u3001\u6027\u5E7B\u60F3\u6709\u5173\u3002</p><p>\u867D\u7136\u5B69\u5B50\u53D1\u4F5C\u8D77\u6765\uFF0C\u6709\u7C7B\u4F3C\u6027\u884C\u4E3A\u7684\u8868\u73B0\uFF0C\u4F46\u662F\u5176\u4E0E\u201C\u6027\u201D\u5374\u662F\u98CE\u9A6C\u725B\u4E0D\u76F8\u53CA\uFF0C\u4E00\u70B9\u5173\u7CFB\u90FD\u6CA1\u6709\u3002\u64E6\u817F\u7EFC\u5408\u5F81\u53EA\u662F\u5728\u5076\u5C14\u60C5\u51B5\u4E0B\uFF0C\u5B69\u5B50\u4F53\u9A8C\u5230\u9634\u90E8\u523A\u6FC0\u5E26\u6765\u7684\u5FEB\u611F\uFF0C\u7EE7\u800C\u53D1\u5C55\u6210\u4E00\u79CD\u4E60\u60EF\u52A8\u4F5C\uFF0C\u4E5F\u53EF\u4F5C\u4E3A\u4E00\u79CD\u81EA\u6211\u5B89\u6170\u7684\u65B9\u5F0F\u3002\u5F53\u7136\uFF0C\u5728\u5BB6\u957F\u770B\u6765\uFF0C\u8FD9\u79CD\u65B9\u5F0F\u662F\u4E0D\u5408\u7406\u7684\uFF0C\u6240\u4EE5\u624D\u4F1A\u7279\u522B\u7D27\u5F20\u3002</p><p><strong>2</strong></p><p>\u8FD8\u6709\u4E00\u79CD\u62C5\u5FE7\uFF0C\u8BA4\u4E3A\u64E6\u817F\u4F1A\u5BFC\u81F4\u6027\u65E9\u719F\u3002</p><p>\u76EE\u524D\u8BA4\u4E3A\uFF0C\u64E6\u817F\u7EFC\u5408\u5F81\u7684\u53D1\u751F\u539F\u56E0\u4E0E\u67D0\u4E9B\u4F1A\u9634\u90E8\u75BE\u75C5\u548C\u523A\u6FC0\uFF08\u5982\u6E7F\u75B9\u3001\u5305\u830E\u3001\u86F2\u866B\u75C5\u3001\u5916\u9634\u4E0D\u6D01\u3001\u88E4\u5B50\u8FC7\u7D27\u7B49\u5F15\u8D77\u4F1A\u9634\u90E8\u53D1\u75D2\u800C\u6469\u64E6\uFF0C\u8FDB\u800C\u53D1\u5C55\u4E3A\u4E60\u60EF\u52A8\u4F5C\uFF09\u3001\u5FC3\u7406\u539F\u56E0\uFF08\u5BB6\u5EAD\u6C1B\u56F4\u7D27\u5F20\u3001\u7F3A\u4E4F\u5173\u7231\u3001\u60C5\u7EEA\u7D27\u5F20\u5F97\u4E0D\u5230\u5BA3\u6CC4\u3001\u81EA\u8EAB\u5BFB\u6C42\u523A\u6FC0\u7B49\uFF09\u3001\u5176\u4ED6\u539F\u56E0\uFF08\u8F83\u5927\u513F\u7AE5\u53EF\u80FD\u4E0E\u770B\u8272\u60C5\u4E66\u520A/\u5F55\u50CF\u7B49\uFF09\u6709\u5173\u3002\u4F46\u6CA1\u6709\u4F9D\u636E\u63D0\u793A\u6709\u64E6\u817F\u7EFC\u5408\u5F81\u7684\u5B69\u5B50\u4EE5\u540E\u4F1A\u66F4\u5BB9\u6613\u6027\u65E9\u719F\u3002</p><p>\u90A3\u4E48\uFF0C\u5F53\u5B69\u5B50\u51FA\u73B0\u64E6\u817F\u884C\u4E3A\u65F6\uFF0C\u5BB6\u957F\u5E94\u8BE5\u5982\u4F55\u6B63\u786E\u5F15\u5BFC\u5462\uFF1F</p><p><em><strong>01</strong></em></p><p>\u53CA\u65F6\u7ED9\u5B69\u5B50\u66F4\u6362\u5C3F\u4E0D\u6E7F\uFF0C\u907F\u514D\u5916\u9634\u53D7\u6F6E\u6E7F\u73AF\u5883\u7684\u523A\u6FC0\uFF1B\u5185\u88E4\u5BBD\u677E\u8212\u9002\uFF0C\u907F\u514D\u7D27\u8EAB\uFF0C\u51CF\u5C11\u5C40\u90E8\u523A\u6FC0\u3002</p><p>\u907F\u514D\u7ED9\u5B69\u5B50\u7A7F\u5F00\u88C6\u88E4\uFF0C\u5373\u4F7F\u767D\u5929\u73A9\u800D\u65F6\u4E5F\u5E94\u4F7F\u7528\u7EB8\u5C3F\u88E4\uFF0C\u5C3D\u65E9\u7A7F\u6EE1\u88C6\u88E4\u4FDD\u62A4\u4F1A\u9634\u76AE\u80A4\uFF0C\u4EE5\u907F\u514D\u53D1\u751F\u611F\u67D3\uFF0C\u53CA\u65F6\u89E3\u9664\u4F1A\u9634\u90E8\u7684\u4E0D\u826F\u523A\u6FC0\u56E0\u7D20\u6216\u8005\u75BE\u75C5\u7684\u53EF\u80FD\uFF0C\u5305\u62EC\u86F2\u866B\u75C5\u3001\u5916\u9634\u708E\u3001\u5C40\u90E8\u76AE\u708E\u7B49\u3002</p><p><em><strong>02</strong></em></p><p>\u53D1\u73B0\u5B69\u5B50\u51FA\u73B0\u64E6\u817F\u7EFC\u5408\u5F81\uFF0C\u5BB6\u957F\u4E0D\u8981\u8FC7\u4E8E\u7D27\u5F20\uFF0C\u66F4\u4E0D\u8981\u76F4\u63A5\u6279\u8BC4\u5B69\u5B50\u3002\u8F6C\u79FB\u6CE8\u610F\u529B\uFF0C\u91CD\u70B9\u5728\u201C\u4E0D\u52A8\u58F0\u8272\u201D\uFF0C\u8981\u5E73\u548C\u6DE1\u5B9A\u3001\u82E5\u65E0\u5176\u4E8B\u5730\u5047\u88C5\u6CA1\u770B\u89C1\uFF0C\u81EA\u7136\u5730\u628A\u5B69\u5B50\u62B1\u8D77\u6765\u3001\u5230\u5916\u9762\u8D70\u8D70\uFF0C\u4E5F\u53EF\u4EE5\u8F7B\u8F7B\u5C06\u5176\u4E24\u817F\u5206\u5F00\uFF0C\u7528\u73A9\u5177\u628A\u5B69\u5B50\u7684\u6CE8\u610F\u529B\u81EA\u7136\u800C\u7136\u5730\u5438\u5F15\u8FC7\u6765\uFF0C\u8BA9\u4ED6\u4EEC\u6682\u65F6\u5FD8\u8BB0\u81EA\u5DF1\u6B63\u5728\u505A\u7684\u4E8B\u3002</p><p><em><strong>03</strong></em></p><p>\u5BB6\u957F\u8981\u7EC6\u5FC3\u89C2\u5BDF\u5B69\u5B50\u5E38\u5E38\u5728\u54EA\u4E9B\u60C5\u51B5\u4E0B\u51FA\u73B0\u6B64\u884C\u4E3A\uFF0C\u6BD4\u5982\u4E00\u4E2A\u4EBA\u611F\u5230\u65E0\u804A\u65F6\u3001\u8EBA\u5728\u5E8A\u4E0A\u7761\u4E0D\u7740\u65F6\u3001\u7761\u9192\u540E\u8EBA\u5728\u5E8A\u4E0A\u4E0D\u77E5\u9053\u5E72\u4EC0\u4E48\u65F6\u3001\u60C5\u7EEA\u6CE2\u52A8\u627E\u5B89\u6170\u65F6\u2026\u2026</p><p>\u90A3\u4E48\uFF0C\u5BF9\u7B56\u5C31\u662F\u589E\u52A0\u79EF\u6781\u6709\u6548\u7684\u4EB2\u5B50\u4E92\u52A8\u66FF\u4EE3\u65E0\u804A\u7684\u65F6\u95F4\uFF0C\u6BD4\u5982\u966A\u5B9D\u5B9D\u73A9\u73A9\u5177\u3001\u770B\u770B\u7A97\u5916\u7684\u98CE\u666F\u3001\u5230\u6237\u5916\u6563\u6B65\u7B49\uFF0C\u5C3D\u91CF\u907F\u514D\u51FA\u73B0\u8FD9\u4E2A\u884C\u4E3A\u7684\u5916\u754C\u8BF1\u56E0\uFF0C\u5408\u7406\u5B89\u6392\u5B69\u5B50\u7684\u4F5C\u606F\u3001\u6BCF\u5929\u4FDD\u8BC1\u81F3\u5C111\u5C0F\u65F6\u7684\u966A\u4F34\u548C\u4E92\u52A8\u3001\u589E\u52A0\u5B69\u5B50\u7684\u5404\u79CD\u4F53\u9A8C\u3001\u62D3\u5C55\u4ED6\u7684\u5174\u8DA3\u3001\u6CA1\u6709\u7761\u610F\u6216\u7761\u9192\u540E\u4E0D\u8981\u8EBA\u5728\u5E8A\u4E0A\u3002</p><p><em><strong>04</strong></em></p><p>\u53EF\u4EE5\u914D\u5408\u4E00\u4E9B\u6027\u6559\u80B2\u7684\u7ED8\u672C\uFF0C\u6BD4\u5982\u300A\u6211\u4EEC\u7684\u8EAB\u4F53\u300B\u3001\u300A\u5C0F\u9E21\u9E21\u7684\u6545\u4E8B\u300B\u3001\u300A\u6211\u5B9D\u8D35\u7684\u8EAB\u4F53\u300B\u7B49\uFF0C\u8FD8\u6709\u4E00\u4E9B\u4EBA\u4F53\u79D1\u666E\u6A21\u578B\u6765\u7ED9\u5B69\u5B50\u8BB2\u89E3\uFF0C\u8FD9\u6837\u8BA9\u5B69\u5B50\u66F4\u76F4\u89C2\u4E86\u89E3\u81EA\u5DF1\u7684\u8EAB\u4F53\u548C\u9690\u79C1\u3002\u5982\u679C\u5BB6\u957F\u5728\u9762\u5BF9\u6027\u76F8\u5173\u7684\u95EE\u9898\u906E\u906E\u63A9\u63A9\uFF0C\u6216\u8005\u975E\u5E38\u6B66\u65AD\u5730\u7ED9\u5B69\u5B50\u7684\u884C\u4E3A\u201C\u4E0B\u5B9A\u4E49\u201D\u4E3A\u7F9E\u803B\u3001\u4E11\u964B\uFF0C\u8FD9\u53EA\u4F1A\u589E\u52A0\u5B69\u5B50\u7684\u7126\u8651\uFF0C\u6216\u8005\u66F4\u52A0\u597D\u5947\u7EE7\u7EED\u800C\u4E3A\u4E4B\u3002</p><p><em><strong>05</strong></em></p><p>\u5F53\u7136\uFF0C\u5BB6\u957F\u4E5F\u4E0D\u8981\u5FFD\u89C6\u4E86\u5176\u4ED6\u53EF\u80FD\u5B58\u5728\u7684\u539F\u56E0\uFF0C\u6BD4\u5982\u5B69\u5B50\u7684\u8FD9\u79CD\u884C\u4E3A\uFF0C\u8FD8\u4F34\u968F\u7740\u60C5\u7EEA\u6613\u6CE2\u52A8\u3001\u7EA0\u7F20\u3001\u654F\u611F\u3001\u9000\u7F29\u3001\u7126\u8651\u4E0D\u5B89\u3001\u7D27\u5F20\u3001\u5BB3\u6015\uFF0C\u751A\u81F3\u5165\u7761\u56F0\u96BE\u7B49\u73B0\u8C61\uFF0C\u90A3\u4E48\u5C31\u8981\u7559\u610F\u662F\u5426\u8FD8\u5B58\u5728\u5176\u4ED6\u7684\u5916\u90E8\u539F\u56E0\uFF0C\u4F8B\u5982\u5728\u5B66\u6821\u53D7\u5230\u6B3A\u8D1F\u3002\u800C\u5982\u679C\u957F\u671F\u5B58\u5728\u7C7B\u4F3C\u7684\u884C\u4E3A\uFF0C\u4E5F\u53EF\u4EE5\u8003\u8651\u8FDB\u884C\u4E00\u4E0B\u8111\u7535\u56FE\u68C0\u67E5\u6392\u9664\u5176\u4ED6\u7684\u53EF\u80FD\u6027\uFF0C\u6BD4\u5982\u766B\u75EB\u3002</p><p>2-5\u5C81\u513F\u7AE5\u5BF9\u81EA\u5DF1\u7684\u751F\u6B96\u5668\u5B98\u597D\u5947\u662F\u975E\u5E38\u5E38\u89C1\u7684\uFF0C\u4ED6\u4EEC\u51FA\u73B0\u63A2\u7D22\u81EA\u5DF1\u8EAB\u4F53\u7684\u884C\u4E3A\uFF0C\u5982\u629A\u6478\u751F\u6B96\u5668\u3001\u523A\u6FC0\u81EA\u5DF1\u79C1\u5904\u3001\u5939\u817F\u6469\u64E6\u5916\u9634\u7B49\uFF0C\u53EF\u4EE5\u770B\u4F5C\u662F\u5176\u5065\u5EB7\u6210\u957F\u8FC7\u7A0B\u4E2D\u7684\u4E00\u90E8\u5206\u3002</p><p>\u503C\u5F97\u6CE8\u610F\u7684\u662F\uFF0C\u8FD9\u4E2A\u9636\u6BB5\u7684\u5B69\u5B50\u6B63\u5728\u4E0A\u5E7C\u513F\u56ED\uFF0C\u90A3\u4E48\uFF0C\u5E7C\u513F\u56ED\u8001\u5E08\u6B63\u786E\u7684\u5F15\u5BFC\u548C\u5904\u7406\u540C\u6837\u91CD\u8981\u3002\u4E3A\u5B69\u5B50\u8425\u9020\u4E00\u4E2A\u63A5\u7EB3\u7684\u73AF\u5883\uFF0C\u53CA\u65F6\u8BC6\u522B\u5B69\u5B50\u7684\u884C\u4E3A\uFF0C\u65E0\u9700\u5927\u60CA\u5C0F\u602A\uFF0C\u5E73\u9759\u5730\u8F6C\u79FB\u6CE8\u610F\u529B\uFF1B\u5BF9\u4E8E\u4E0D\u7761\u5348\u89C9\u7684\u5B69\u5B50\uFF0C\u5141\u8BB8\u4ED6\u4EEC\u505A\u4E9B\u5B89\u9759\u7684\u4E8B\u60C5 \uFF08\u5982\u8D34\u8D34\u7EB8\u3001\u6D82\u8272\u3001\u62FC\u63A5\u73A9\u5177\u7B49\uFF09\uFF0C\u4EE5\u514D\u65E0\u804A\u65F6\u5939\u817F\u6765\u6253\u53D1\u65F6\u95F4\u3002\u5F53\u5B69\u5B50\u8FDB\u5165\u5B66\u9F84\u671F\uFF0C\u6709\u4E86\u8EAB\u4F53\u9690\u79C1\u548C\u793E\u4EA4\u754C\u9650\u7684\u610F\u8BC6\u540E\uFF0C\u8FD9\u4E2A\u884C\u4E3A\u4F1A\u9010\u6E10\u51CF\u5C11\u5230\u81EA\u884C\u6D88\u9000\u3002</p><p>\u5982\u5F00\u5934\u7684\u6848\u4F8B\u4E2D\uFF0C\u5BB6\u957F\u770B\u5230\u5B69\u5B50\u7684\u5939\u817F\u884C\u4E3A\uFF0C\u5185\u5FC3\u7684\u7126\u8651\u66B4\u9732\u65E0\u7591\uFF0C\u672C\u80FD\u5730\u76F4\u63A5\u5236\u6B62\u5B69\u5B50\uFF0C\u4F20\u9012\u7ED9\u5B69\u5B50\u7684\u4FE1\u606F\u5C31\u662F\u201C\u4F60\u8FD9\u4E48\u505A\u662F\u975E\u5E38\u7F9E\u803B\u7684\u201D\uFF1B\u8FD9\u53CD\u800C\u5BF9\u5B69\u5B50\u7684\u884C\u4E3A\u8D77\u5230\u8D1F\u5F3A\u5316\u7684\u4F5C\u7528\uFF0C\u4E5F\u662F\u8FD9\u4E2A\u884C\u4E3A\u4E3A\u4EC0\u4E48\u4E00\u76F4\u5C61\u7981\u4E0D\u6B62\u7684\u539F\u7531\u3002</p><p>\u5982\u679C\u5BB6\u957F\u770B\u5230\u5B69\u5B50\u51FA\u73B0\u8FD9\u6837\u7684\u884C\u4E3A\uFF0C\u80FD\u591F\u4E0D\u52A8\u58F0\u8272\u5730\u5F15\u5BFC\uFF0C\u628A\u5979\u62B1\u8D77\u6765\u8D70\u8D70\u3001\u548C\u5979\u73A9\u559C\u6B22\u7684\u73A9\u5177\uFF0C\u8BA9\u5979\u5FD8\u8BB0\u81EA\u5DF1\u6B63\u5728\u505A\u7684\u4E8B\u60C5\uFF0C\u90A3\u4E48\u5939\u817F\u884C\u4E3A\u5E26\u6765\u7684\u523A\u6FC0\u8D8A\u6765\u8D8A\u5C11\uFF0C\u5C31\u6709\u53EF\u80FD\u51CF\u5C11\u8FD9\u7C7B\u884C\u4E3A\u7684\u51FA\u73B0\u3002</p><p>\u5982\u679C\u5B69\u5B50\u51FA\u73B0\u4E0D\u5206\u573A\u5408\u5730\u5939\u817F \uFF08\u5982\u770B\u5230\u9910\u684C\u5C31\u76F4\u767D\u5730\u5939\u817F\uFF09\uFF0C\u6216\u8005\u7528\u5F88\u5371\u9669\u7684\u65B9\u5F0F\u81EA\u6211\u523A\u6FC0 \uFF08\u6BD4\u5982\u7528\u5C16\u9510\u7684\u6728\u68CD\u523A\u6FC0\u751F\u6B96\u5668\u6216\u585E\u5230\u751F\u6B96\u5668\u5185\uFF09\uFF0C\u9700\u8981\u53CA\u65F6\u6C42\u52A9\u4E13\u79D1\u533B\u751F\u7684\u4ECB\u5165\u548C\u5E72\u9884\u3002</p><p>\u603B\u4E4B\uFF0C\u64E6\u817F\u7EFC\u5408\u5F81\u662F\u5B69\u5B50\u6210\u957F\u8FC7\u7A0B\u4E2D\u7684\u5E38\u89C1\u73B0\u8C61\uFF0C\u53EF\u4EE5\u770B\u4F5C\u662F\u4ED6\u4EEC\u81EA\u6211\u63A2\u7D22\u7684\u65B9\u5F0F\uFF0C\u5BB6\u957F\u6216\u8001\u5E08\u5207\u5FCC\u7B80\u5355\u7C97\u66B4\u5730\u5236\u6B62\uFF0C\u5F7B\u5E95\u5730\u5FFD\u89C6\u8FD9\u4E2A\u4E60\u60EF\u884C\u4E3A\uFF0C\u6E29\u548C\u5730\u575A\u6301\u8F6C\u79FB\u6CE8\u610F\u529B\u5373\u53EF\u3002</p><p><strong>\u53C2\u8003\u8D44\u6599</strong></p><p>1.Chen, Chang, et al. &quot;Investigating the relationship between precocious puberty and obesity: a cross-sectional study in Shanghai, China.&quot; BMJ open 7.4 (2017): e014004.</p><p>2.Brown, DeAnna B., Lindsey A. Loomba-Albrecht, and Andrew A. Bremer. &quot;Sexual precocity and its treatment.&quot; World Journal of Pediatrics 9.2 (2013): 103-111.</p><p>3. \u6768\u7389\u51E4\uFF0C\u91D1\u661F\u660E\uFF0C\u9759\u8FDB\u7F16\u8457\u3002\u53D1\u80B2\u884C\u4E3A\u513F\u79D1\u624B\u518C[M]\uFF0C\u6C5F\u82CF\u79D1\u5B66\u6280\u672F\u51FA\u7248\u793E\uFF0C2009\u5E74\u3002</p><hr><p>\u672C\u6587\u9996\u53D1\u4E8E\u300C<strong>\u6021\u79BE\u5FC3\u7406</strong>\u300D\uFF0C\u5173\u6CE8\u540E\u53EF\u67E5\u770B\u66F4\u591A\u513F\u7AE5\u53D1\u80B2\u884C\u4E3A\u76F8\u5173\u6587\u7AE0</p>',40),g={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651632821&idx=1&sn=164d927637b043127f382b10cd429697&chksm=8b5c9d4fbc2b14598f5054735c17da99a72bb89b30b63d80ca3b67a7e53d34b5829ad9fb32a8&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=c("\u67E5\u770B\u539F\u6587");function l(_,b){const p=t("ExternalLinkIcon");return n(),r(a,null,[m,e("p",null,[e("a",g,[h,o(p)])])],64)}var x=d(i,[["render",l]]);export{x as default};
