import{r as a,o as n,c as t,a as e,b as c,F as r,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=o('<h3 id="\u6253\u75AB\u82D7\u8BF1\u53D1\u5DDD\u5D0E\u75C5-\u4F60\u53EF\u80FD\u60F3\u5F97\u6709\u70B9\u591A" tabindex="-1"><a class="header-anchor" href="#\u6253\u75AB\u82D7\u8BF1\u53D1\u5DDD\u5D0E\u75C5-\u4F60\u53EF\u80FD\u60F3\u5F97\u6709\u70B9\u591A" aria-hidden="true">#</a> \u6253\u75AB\u82D7\u8BF1\u53D1\u5DDD\u5D0E\u75C5\uFF1F\u4F60\u53EF\u80FD\u60F3\u5F97\u6709\u70B9\u591A</h3><p>\u8FD9\u4E24\u5929\u5728\u540E\u53F0\u6709\u4E0D\u5C11\u4EBA\u7559\u8A00\u8BF4\u8FD9\u4E2A\u4E8B\uFF0C\u539F\u56E0\u662F\u4E00\u7BC7\u300A\u75AB\u82D7\u540E\u8BF1\u53D1\u5DDD\u5D0E\u75C5\u2014\u90A3\u4E0D\u613F\u56DE\u5FC6\u7684\u5341\u5929\u300B\u7684\u6587\u7AE0\u5728\u670B\u53CB\u5708\u91CC\u5237\u5C4F\u3002</p><p>\u6587\u7AE0\u662F\u4E00\u4F4D\u5988\u5988\u8BB2\u8BC9\u81EA\u5DF1\u5B69\u5B50\u751F\u75C5\u7684\u4E00\u6B21\u7ECF\u5386\uFF1A\u5B69\u5B50\u5728\u6253\u5B8C\u624B\u8DB3\u53E3\u75C5\uFF08EV71\uFF09\u75AB\u82D7\u540E\u7684\u5F53\u5929\u5C31\u51FA\u73B0\u4E86\u53D1\u70E7\uFF0C\u4E4B\u540E\u6301\u7EED\u70E7\u4E865\u5929\uFF0C\u5728\u7B2C6\u5929\u786E\u8BCA\u4E3A\u5DDD\u5D0E\u75C5\uFF0C\u867D\u7136\u8FDB\u884C\u4E86\u89C4\u8303\u6CBB\u7597\uFF0C\u4F46\u8FD8\u662F\u51FA\u73B0\u4E86\u8840\u7BA1\u6269\u5F20\u3002</p><p>\u51FA\u9662\u540E\u5BB6\u957F\u4E0A\u7F51\u6536\u96C6\u76F8\u5173\u8D44\u6599\uFF0C\u770B\u5230\u4E86\u5F88\u591A\u201C\u75AB\u82D7\u540E\u8BF1\u53D1\u5DDD\u5D0E\u75C5\u201D\u7684\u6848\u4F8B\uFF0C\u8BF8\u5982\uFF1A</p><p>\u6253\u5B8C\u4E59\u8111\u75AB\u82D7\u7B2C\u4E8C\u5929\u53D1\u70E7\uFF0C\u4E00\u5468\u540E\u786E\u8BCA\u5DDD\u5D0E\u75C5</p><p>\u6253\u5B8C\u6C34\u75D8\u75AB\u82D7\u7B2C\u4E8C\u5929\u53D1\u70E7\uFF0C10\u5929\u540E\u786E\u8BCA\u5DDD\u5D0E\u75C5</p><p>\u6253\u5B8C\u9EBB\u75B9\u7B2C10\u5929\u786E\u8BCA\u2026..</p><p>\u751A\u81F3\u8FD8\u641C\u5230\u4E00\u4E9B\u6848\u4F8B\u7684\u65B0\u95FB\u62A5\u9053\u3002</p><p>\u5728\u6587\u7AE0\u91CC\uFF0C\u5BB6\u957F\u8BF4\u662F\u8BF4\u53EA\u60F3\u5F04\u6E05\u695A\u75AB\u82D7\u548C\u5DDD\u5D0E\u75C5\u4E4B\u95F4\u662F\u5426\u6709\u56E0\u679C\u5173\u7CFB\uFF0C\u4F46\u6587\u7AE0\u6807\u9898\u5374\u76F4\u63A5\u5199\u4E86<strong>\u75AB\u82D7\u540E\u8BF1\u53D1\u5DDD\u5D0E\u75C5</strong>\uFF0C\u201C\u5982\u679C\u75AB\u82D7\u6709\u8BF1\u53D1\u5DDD\u5D0E\u75C5\u7684\u53EF\u80FD\uFF0C\u54EA\u6015\u8FD9\u4E2A\u673A\u7387\u5F88\u5C0F\uFF0C\u4F46\u5BB6\u957F\u82E5\u4E0D\u77E5\u60C5\uFF0C\u60F3\u5FC5\u4E0D\u4F1A\u518D\u53BB\u6253\u4E8C\u7C7B\u75AB\u82D7\u4EE5\u589E\u52A0\u98CE\u9669\u201D\u4E4B\u7C7B\u7684\u884C\u6587\uFF0C\u4E5F\u5BB9\u6613\u8BA9\u8BFB\u8005\u4EA7\u751F\u80CC\u540E\u6709\u9ED1\u5E55\u7684\u731C\u60F3\uFF0C\u6240\u4EE5\u5F15\u53D1\u4E86\u5F88\u591A\u5BB6\u957F\u6050\u614C\uFF0C\u8FD9\u4E5F\u662F\u8FD9\u7BC7\u6587\u7AE0\u5237\u5C4F\u7684\u539F\u56E0\u3002</p><p>\u75AB\u82D7\u4F1A\u4E0D\u4F1A\u8BF1\u53D1\u5DDD\u5D0E\u75C5\uFF1F</p><p>\u8FD9\u4E2A\u5B69\u5B50\u662F\u5728\u6253\u5B8C\u540E\u51FA\u73B0\u53D1\u70E7\uFF0C\u6700\u540E\u88AB\u786E\u8BCA\u5DDD\u5D0E\u75C5\uFF0C\u8FD9\u70B9\u5927\u5BB6\u90FD\u4E0D\u6000\u7591\uFF0C\u5BB6\u957F\u641C\u7D22\u5230\u7684\u90A3\u4E9B\u6848\u4F8B\u76F8\u4FE1\u7EDD\u5927\u90E8\u5206\u4E5F\u662F\u771F\u5B9E\u7684\u3002\u5E73\u65F6\u597D\u597D\u7684\uFF0C\u6253\u5B8C\u75AB\u82D7\u540E\u4E00\u4E24\u5929\u5C31\u53D1\u70E7\uFF0C\u7136\u540E\u4E00\u76F4\u9AD8\u70E7\u4E0D\u9000\uFF0C\u6700\u540E\u88AB\u8BCA\u65AD\u5DDD\u5D0E\u75C5\uFF0C\u4F5C\u4E3A\u5BB6\u957F\uFF0C\u731C\u6D4B\u662F\u75AB\u82D7\u8BF1\u53D1\u4E86\uFF0C\u4E5F\u662F\u5F88\u6B63\u5E38\u7684\u6000\u7591\u3002</p><p>\u4F46\u8BA4\u5B9A\u75AB\u82D7\u4F1A\u8BF1\u53D1\u5DDD\u5D0E\u75C5\u5C31\u4E0D\u662F\u90A3\u4E48\u5BB9\u6613\u4E86\uFF0C\u56E0\u4E3A\u5148\u540E\u53D1\u751F\u7684\u4E8B\u60C5\uFF0C\u4E0D\u7B49\u4E8E\u5C31\u662F\u56E0\u679C\u5173\u7CFB\u3002\u4E0D\u8BBA\u6253\u4E0D\u6253\u75AB\u82D7\uFF0C\u4EBA\u5C31\u6709\u53EF\u80FD\u751F\u75C5\uFF0C\u4EBA\u7FA4\u53EA\u8981\u8DB3\u591F\u5927\uFF0C\u5C31\u6709\u51FA\u73B0\u611F\u5192\u3001\u6D41\u611F\u3001\u80A0\u708E\u3001\u624B\u8DB3\u53E3\u75C5....\u5DDD\u5D0E\u75C5\u7B49\u75BE\u75C5\u3002</p><p>\u4E0D\u8BF4\u5168\u4E16\u754C\uFF0C\u4EC5\u5728\u56FD\u5185\uFF0C\u6BCF\u5929\u90FD\u6709\u4E0A\u767E\u4E07\u7684\u5B69\u5B50\u6253\u75AB\u82D7\uFF0C\u800C\u6BCF10\u4E07\u4E2A5\u5C81\u4EE5\u4E0B\u5B69\u5B50\u5C31\u670910\u4E2A\u5DE6\u53F3\u4F1A\u5F97\u5DDD\u5D0E\u75C5\uFF0C\u6240\u4EE5\u5168\u56FD\u8303\u56F4\u5185\u6709\u4E0D\u6B62\u4E00\u4E2A\u5B69\u5B50\u6253\u75AB\u82D7\u540E\u5F97\u4E86\u5DDD\u5D0E\u75C5\u662F\u5F88\u6B63\u5E38\u7684\u4E8B\uFF0C\u5C31\u50CF\u6709\u5B69\u5B50\u4E0A\u5E7C\u513F\u56ED\u540E\u5F97\u4E86\u5DDD\u5D0E\u75C5\uFF0C\u6709\u5B69\u5B50\u65C5\u6E38\u540E\u5F97\u4E86\u5DDD\u5D0E\u75C5\u4E00\u6837\u6B63\u5E38\u3002</p><p>\u53EA\u4E0D\u8FC7\uFF0C\u4E0A\u5E7C\u513F\u56ED\u6216\u8005\u65C5\u6E38\u540E\u5F97\u4E86\u5DDD\u5D0E\u75C5\u5927\u5BB6\u4E0D\u4F1A\u89C9\u5F97\u662F\u4E0A\u5B66\u6216\u65C5\u6E38\u5BFC\u81F4\u7684\uFF0C\u4E0D\u4F1A\u8BF4\u4EC0\u4E48\uFF0C\u800C\u6253\u75AB\u82D7\u5BF9\u5BB6\u957F\u6765\u8BF4\u662F\u5C06\u4E0D\u660E\u7684\u6DB2\u4F53\u6253\u8FDB\u5B69\u5B50\u8EAB\u4F53\u91CC\uFF0C\u4E4B\u540E\u53D1\u751F\u4EFB\u4F55\u95EE\u9898\u90FD\u5BB9\u6613\u5F15\u53D1\u731C\u6D4B\uFF0C\u7136\u540E\u5199\u5230\u7F51\u4E0A\u6765\u3002</p><p>\u4E5F\u6B63\u5982\u8FD9\u4F4D\u5988\u5988\u6240\u67E5\u5230\u7684\uFF0C\u5979\u7684\u5B69\u5B50\u4E0D\u662F\u7B2C\u4E00\u4F4D\u5728\u63A5\u79CD\u75AB\u82D7\u540E\u88AB\u8BCA\u65AD\u4E3A\u5DDD\u5D0E\u75C5\u7684\uFF0C\u8FD9\u6837\u7684\u6848\u4F8B\u62A5\u9053\u56FD\u5185\u5916\u5176\u5B9E\u4E00\u76F4\u90FD\u6709\uFF0C\u5BB6\u957F\u4EEC\u6240\u62C5\u5FC3\u7684\uFF0C\u4E5F\u662F\u533B\u751F\u548C\u7814\u7A76\u4EBA\u5458\u4EEC\u6240\u62C5\u5FC3\u7684\uFF0C\u4E3A\u4E86\u89E3\u63A5\u79CD\u75AB\u82D7\u662F\u5426\u4F1A\u589E\u52A0\u5DDD\u5D0E\u75C5\u7684\u98CE\u9669\uFF0C\u65E9\u5C31\u6709\u4EBA\u505A\u4E86\u76F8\u5173\u7684\u7814\u7A76\u3002</p><p>2009\u5E74\u7F8E\u56FDFDA\u7684\u4E00\u9879\u7814\u7A76\uFF0C\u5206\u6790\u4E861990-2007\u5E74\u4E4B\u95F4\u4E0A\u62A5\u768497\u4F8B\u63A5\u79CD\u75AB\u82D7\u540E\u53D1\u751F\u7684\u5DDD\u5D0E\u75C5\uFF0C\u672A\u53D1\u73B0\u63A5\u79CD\u8F6E\u72B6\u75C5\u6BD2\u75AB\u82D7\u6216\u5176\u5B83\u75AB\u82D7\u540E\u53D1\u751F\u5DDD\u5D0E\u75C5\u98CE\u9669\u5347\u9AD8\u3002[1]2015\u5E74\u7F8E\u56FD\u75BE\u75C5\u63A7\u5236\u4E2D\u5FC3\u968F\u8BBF\u5206\u6790\u4E861996-2006\u5341\u5E74\u95F4172\u4E07\u4E2A0-6\u5C81\u5B69\u5B50\uFF0C\u53D1\u73B0\u63A5\u79CD\u75AB\u82D7\u4E0D\u4F1A\u589E\u52A0\u5DDD\u5D0E\u75C5\u98CE\u9669\uFF0C\u76F8\u53CD\uFF0C\u75AB\u82D7\u63A5\u79CD\u540E\u77ED\u671F\u5185\u5DDD\u5D0E\u75C5\u53D1\u75C5\u7387\u53CD\u800C\u6709\u77ED\u6682\u4E0B\u964D\u3002 [2]</p><p>\u867D\u7136\u8FD9\u4E9B\u89C2\u5BDF\u65F6\u95F4\u5DF2\u7ECF\u8DB3\u591F\u957F\uFF0C\u6837\u672C\u4E5F\u975E\u5E38\u5927\uFF0C\u4F46\u4F60\u53EF\u80FD\u8FD8\u4F1A\u89C9\u5F97\u4F1A\u4E0D\u591F\uFF0C\u6211\u7528\u201CVaccines\u201D\u548C\u201CKawasaki disease\u201D\u4E3A\u5173\u952E\u8BCD\u5230Pubmed\u91CC\u68C0\u7D22\u4E86\u4E00\u4E0B\uFF0C\u9664\u4E86\u770B\u5230\u5C11\u6570\u7684\u6848\u4F8B\u62A5\u9053\uFF0C\u4E5F\u6CA1\u6709\u770B\u5230\u54EA\u4E2A\u7814\u7A76\u8BA4\u4E3A\u75AB\u82D7\u4F1A\u8BF1\u53D1\u5DDD\u5D0E\u75C5\u3002</p><p>\u4E8B\u5B9E\u4E0A\u4E5F\u662F\uFF0C\u5DDD\u5D0E\u75C5\u81F3\u4ECA\u4ECD\u662F\u4E00\u79CD\u53D1\u75C5\u539F\u56E0\u4E0D\u660E\u7684\u75BE\u75C5\uFF0C\u867D\u7136\u6709\u4EBA\u731C\u6D4B\u53EF\u80FD\u548C\u7EC6\u83CC\u3001\u75C5\u6BD2\u3001\u6216\u8005\u73AF\u5883\u56E0\u7D20\u6709\u5173\uFF0C\u4F46\u8FD8\u6CA1\u6709\u4EBA\u8BC1\u5B9E\u8FC7\uFF0C\u533B\u751F\u548C\u7814\u7A76\u4EBA\u5458\u4ECD\u5728\u52AA\u529B\u53BB\u5BFB\u627E\u539F\u56E0\uFF0C\u8981\u662F\u8C01\u771F\u80FD\u8BC1\u5B9E\u54EA\u79CD\u75AB\u82D7\u80FD\u8BF1\u53D1\u5DDD\u5D0E\u75C5\uFF0C\u90A3\u4F1A\u662F\u91CD\u5927\u7684\u533B\u5B66\u53D1\u73B0\uFF0C\u800C\u4E0D\u4F1A\u6210\u4E3A\u4E00\u4E2A\u88AB\u523B\u610F\u9690\u7792\u7684\u9ED1\u5E55\u3002</p><p>\u90A3\u80FD\u8BF4\u75AB\u82D7\u7EDD\u5BF9\u4E0D\u4F1A\u8BF1\u53D1\u5DDD\u5D0E\u75C5\u5417\uFF1F\u5012\u4E5F\u4E0D\u662F\uFF0C\u6211\u4EEC\u53EA\u80FD\u8BF4\u76EE\u524D\u6CA1\u6709\u8FD9\u65B9\u9762\u7684\u8BC1\u636E\uFF0C\u800C\u4E14\u73B0\u5728\u6709\u6BD4\u8F83\u5F3A\u7684\u8BC1\u636E\u8BA4\u4E3A\u75AB\u82D7\u4E0D\u4F1A\u589E\u52A0\u8FD9\u4E2A\u98CE\u9669\u3002</p><p>\u548C\u836F\u54C1\u4E00\u6837\uFF0C\u75AB\u82D7\u4E0A\u5E02\u540E\u4E0A\u5E02\u540E\u4E5F\u9700\u8981\u76D1\u6D4B\u4E0D\u826F\u53CD\u5E94\uFF0C\u5C24\u5176\u662FEV71\u75AB\u82D7\u8FD9\u6837\u4E0A\u5E02\u65F6\u95F4\u4E0D\u957F\u7684\u75AB\u82D7\uFF0C\u4E0D\u826F\u53CD\u5E94\u76D1\u6D4B\u66F4\u4E3A\u91CD\u8981\u3002\u63A5\u79CD\u540E\u53D1\u751F\u4E86\u4E0D\u826F\u53CD\u5E94\uFF0C\u90FD\u53EF\u4EE5\u5411\u63A5\u79CD\u90E8\u95E8\u53CD\u9988\uFF0C\u7136\u540E\u88AB\u767B\u8BB0\u4E0A\u62A5\uFF0C\u8FD9\u4E9B\u79EF\u7D2F\u4E0B\u6765\u7684\u6570\u636E\u53EF\u4EE5\u88AB\u7814\u7A76\u4EBA\u5458\u7528\u6765\u8FDB\u4E00\u6B65\u5206\u6790\uFF0C\u4F46\u5728\u6709\u65B0\u7684\u8BC1\u636E\u8BC1\u5B9E\u4E4B\u524D\uFF0C\u6211\u4EEC\u65E0\u987B\u56E0\u4E3A\u6709\u4EBA\u906D\u9047\u4E86\u4E0D\u5E78\u800C\u8FC7\u5EA6\u9050\u60F3\u6216\u56E0\u564E\u5E9F\u98DF\u3002</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p>[1]https://www.ncbi.nlm.nih.gov/pubmed/19755926</p><p>[2]https://www.ncbi.nlm.nih.gov/pubmed/25444786</p>',23),b={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627022&idx=1&sn=42d05270edd708df75fa253b324b872f&chksm=8b5cb7f4bc2b3ee2739c685d9b873cdb57a9dfab8be75ed7942ce9351f0b82745ab9a083c378&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function l(m,f){const p=a("ExternalLinkIcon");return n(),t(r,null,[_,e("p",null,[e("a",b,[h,c(p)])])],64)}var w=d(i,[["render",l]]);export{w as default};
