import{r,o as p,c as a,a as n,b as o,w as g,F as i,e as t,d as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const l={},h=n("h3",{id:"\u5B69\u5B50\u53D1\u70E7\u9996\u9009\u7269\u7406\u964D\u6E29",tabindex:"-1"},[n("a",{class:"header-anchor",href:"#\u5B69\u5B50\u53D1\u70E7\u9996\u9009\u7269\u7406\u964D\u6E29","aria-hidden":"true"},"#"),t(" \u5B69\u5B50\u53D1\u70E7\u9996\u9009\u7269\u7406\u964D\u6E29\uFF1F")],-1),f=t("\u4E09\u5E74\u524D\u6211\u7FFB\u9605\u300A\u5C3C\u5C14\u68EE\u513F\u79D1\u5B66\u300B\uFF0C\u7B2C\u4E00\u6B21\u770B\u5230\u8BF4\u7269\u7406\u63AA\u65BD\u5BF9\u9000\u70E7\u6CA1\u7528\uFF0C\u7B2C\u4E00\u53CD\u5E94\u662F\u53EF\u80FD\u5199\u9519\u4E86\u3002\u7136\u540E\u4E0APubmed\u53BB\u68C0\u7D22\uFF0C\u67E5\u8BC1\u7684\u7ED3\u679C\u98A0\u8986\u4E86\u81EA\u5DF1\u7684\u90A3\u4E2A\u957F\u4E45\u8BA4\u8BC6\uFF1A\u53D1\u70E7\u9996\u5148\u7269\u7406\u964D\u6E29\uFF0C\u7269\u7406\u964D\u6E29\u4E0D\u884C\u518D\u836F\u7269\u964D\u6E29\u3002\u7136\u540E\u6709\u4E86"),_=t("\u300A\u5B69\u5B50\u53D1\u70E7\u9996\u9009\u7269\u7406\u964D\u6E29\uFF1F\u300B"),u=t("\u8FD9\u7BC7\u6587\u7AE0\u3002"),m=c("<p>\u8FD9\u7BC7\u6587\u7AE0\u521A\u5199\u5B8C\u65F6\uFF0C\u81EA\u5DF1\u8FD8\u6709\u70B9\u4E0D\u8E0F\u5B9E\uFF0C\u8BF7\u4E86\u4E24\u4F4D\u513F\u79D1\u533B\u751F\u670B\u53CB\u4E00\u8D77\u5E2E\u5FD9\u5BA1\u9605\uFF0C\u4ED6\u4EEC\u8BFB\u5B8C\u4E5F\u89C9\u5F97\u8BF4\u5F97\u6709\u9053\u7406\uFF0C\u4F46\u8FD8\u662F\u89C9\u5F97\u89C2\u70B9\u592A\u8D85\u524D\uFF0C\u8BA9\u6211\u518D\u8C28\u614E\u4E00\u70B9\u3002\u6211\u53C8\u53CD\u590D\u6838\u67E5\uFF0C\u89C9\u5F97\u7ED3\u8BBA\u5DF2\u7ECF\u591F\u6E05\u695A\u4E86\uFF0C\u5C31\u5728\u5FAE\u535A\u5FAE\u4FE1\u4E0A\u53D1\u5E03\u4E86\u3002</p><p>\u6587\u7AE0\u53CD\u54CD\u5F88\u5927\uFF0C\u6709\u4EBA\u8D5E\u540C\uFF0C\u4F46\u4E5F\u62DB\u6765\u5F88\u591A\u975E\u8BAE\uFF1A\u8BF4\u6211\u6807\u65B0\u7ACB\u5F02\u54D7\u4F17\u53D6\u5BA0\uFF0C\u4F46\u6211\u5BF9\u81EA\u5DF1\u7684\u67E5\u8BC1\u6709\u4FE1\u5FC3\uFF0C\u6240\u4EE5\u4E5F\u6CA1\u6709\u9000\u7F29\u3002\u51E0\u5E74\u4E0B\u6765\uFF0C\u63A5\u53D7\u8FD9\u4E2A\u89C2\u70B9\u7684\u4EBA\u4E5F\u8D8A\u6765\u8D8A\u591A\uFF0C\u5305\u62EC\u5BB6\u957F\uFF0C\u4E5F\u5305\u62EC\u5F88\u591A\u513F\u79D1\u533B\u751F\uFF0C\u6211\u5728\u5FAE\u535A\u4E0A\u518D\u8BF4\u8FD9\u4E2A\u89C2\u70B9\u7684\u65F6\u5019\uFF0C\u5DF2\u7ECF\u5F88\u5C11\u542C\u5230\u9A82\u58F0\u4E86\uFF0C\u6211\u4E5F\u4EE5\u4E3A\u8FD9\u4E2A\u89C2\u70B9\u5DF2\u7ECF\u88AB\u5927\u90E8\u5206\u4EBA\u63A5\u53D7\u4E86\u3002</p><p>\u524D\u4E0D\u4E45\u817E\u8BAF\u5BA2\u6237\u7AEF\u8F6C\u8F7D\u4E86\u8FD9\u7BC7\u6587\u7AE0\uFF0C\u7ED3\u679C\u8BC4\u8BBA\u5168\u662F\u60E8\u4E0D\u5FCD\u7779\u7684\u9A82\u58F0\uFF0C\u5F88\u50CF3\u5E74\u524D\u7684\u5FAE\u535A\uFF0C\u8BA9\u4EBA\u611F\u89C9\u5B58\u5728\u5E73\u884C\u7A7A\u95F4\uFF0C\u5927\u5BB6\u5404\u81EA\u6D3B\u5728\u81EA\u5DF1\u7684\u4E16\u754C\u91CC\u3002\u4E5F\u8BA9\u6211\u610F\u8BC6\u5230\u8BA4\u8BC6\u5230\u4E86\u89E3\u8FD9\u4E2A\u89C2\u70B9\u7684\u4EBA\u4E0D\u662F\u5DF2\u7ECF\u5F88\u591A\u800C\u662F\u592A\u5C11\uFF0C\u540C\u65F6\u6709\u7684\u673A\u6784\u610F\u89C1\u505A\u4E86\u66F4\u65B0\uFF0C\u6240\u4EE5\u5F88\u6709\u5FC5\u8981\u91CD\u53D1\u8FD9\u7BC7\u6587\u7AE0\uFF0C\u4E5F\u5E0C\u671B\u5927\u5BB6\u591A\u8F6C\u53D1\u8BA9\u66F4\u591A\u4EBA\u770B\u5230\uFF0C\u8BA9\u5E73\u884C\u7A7A\u95F4\u53D1\u751F\u4E00\u70B9\u4EA4\u53C9\u548C\u540C\u6B65\uFF0C\u8BA9\u66F4\u591A\u5B69\u5B50\u5C11\u88AB\u6298\u817E\u3002\u4EE5\u4E0B\u662F\u6B63\u6587\u3002</p><p>\u53D1\u70E7\u5148\u7ED9\u5B69\u5B50\u64E6\u4E2A\u6FA1\uFF0C\u7528\u51B0\u888B\u51B0\u5934\uFF0C\u8BA4\u4E3A\u7269\u7406\u964D\u6E29\u5F88\u6709\u6548\u800C\u4E14\u6CA1\u526F\u4F5C\u7528\uFF0C\u9000\u70E7\u5E94\u8BE5\u9996\u9009\u7269\u7406\u964D\u6E29\uFF0C\u7269\u7406\u964D\u6E29\u4E0D\u884C\u518D\u5403\u9000\u70E7\u836F\u3002\u4F60\u662F\u8FD9\u6837\u8BA4\u4E3A\u5417\uFF1F</p><p>\u606D\u559C\u4F60\uFF0C\u5168\u9519\u4E86\u3002\u76EE\u524D\u6CA1\u6709\u4EFB\u4F55\u4E00\u5BB6\u6743\u5A01\u533B\u5B66\u673A\u6784\u628A\u7269\u7406\u964D\u6E29\u5F53\u505A\u9000\u70E7\u7684\u9996\u9009\u65B9\u5F0F\uFF0C\u8FD9\u662F\u4E3A\u4EC0\u4E48\u5462\uFF1F</p><p>\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u5173\u4E8E\u53D1\u70E7\u7684\u6307\u5357[1]\u91CC\uFF0C\u53CD\u590D\u5F3A\u8C03\u7684\u4E00\u70B9\u662F\uFF0C\u9664\u975E\u662F\u8D85\u9AD8\u70ED\uFF0C\u53D1\u70E7\u672C\u8EAB\u5E76\u4E0D\u4F1A\u5BF9\u5B69\u5B50\u9020\u6210\u4F24\u5BB3\uFF0C\u800C\u4E14\u5B83\u6709\u5229\u4E8E\u5BF9\u6297\u611F\u67D3\u3002\u4E00\u4E9B\u5F15\u8D77\u53D1\u70E7\u7684\u75BE\u75C5\uFF0C\u6BD4\u5982\u4E25\u91CD\u7684\u7EC6\u83CC\u611F\u67D3\u662F\u9700\u8981\u9488\u5BF9\u75C5\u56E0\u8FDB\u884C\u6CBB\u7597\u7684\uFF0C\u4F46\u9664\u975E\u5B69\u5B50\u6709\u660E\u663E\u7684\u4E0D\u9002\uFF0C\u5E76\u4E0D\u9700\u8981\u8FDB\u884C\u9000\u70E7\u3002</p><p>\u5B69\u5B50\u8212\u9002\u5EA6\u5F88\u96BE\u7528\u5BA2\u89C2\u7684\u6307\u6807\u53BB\u8861\u91CF\uFF0C\u4E00\u822C\u8BA4\u4E3A\u572839\xB0C\u4EE5\u4E0A\u5B69\u5B50\u4F1A\u6BD4\u8F83\u96BE\u53D7\u3002\u5982\u679C\u5B69\u5B50\u6CA1\u6709\u660E\u663E\u7684\u4E0D\u8212\u670D\uFF0C\u4E5F\u6CA1\u6709\u5176\u4ED6\u57FA\u7840\u75BE\u75C5\uFF0C\u90A3\u4E48\u53D1\u70E7\u572839\xB0C\u4EE5\u4E0B\u901A\u5E38\u4E0D\u9700\u8981\u9000\u70E7\uFF0C\u81EA\u7136\u4E5F\u4E0D\u9700\u8981\u64E6\u6FA1\u548C\u51B0\u888B\u3002</p><p>\u90A3\u5B69\u5B50\u8D85\u8FC739\xB0C\u6216\u611F\u5230\u660E\u663E\u4E0D\u9002\u65F6\uFF0C\u53C8\u8BE5\u5982\u4F55\u5E94\u5BF9\u5462\uFF1F</p><p>\u6709\u4E9B\u63AA\u65BD\u4E0D\u80FD\u9000\u70E7\uFF0C\u4F46\u53EF\u4EE5\u8BA9\u5B69\u5B50\u8212\u670D\u4E00\u70B9\uFF0C\u6BD4\u5982\u5B69\u5B50\u611F\u89C9\u70ED\u65F6\u51CF\u5C11\u8863\u7269\u548C\u5F00\u7A7A\u8C03\uFF0C\u81EA\u7136\u53EF\u4EE5\u91C7\u7528\u3002\u6709\u4E9B\u6CBB\u7597\u63AA\u65BD\u53EF\u4EE5\u9000\u70E7\u4E5F\u53EF\u4EE5\u6539\u5584\u5B69\u5B50\u7684\u8212\u9002\u5EA6\uFF0C\u6BD4\u5982\u5E03\u6D1B\u82AC\u3001\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u8FD9\u6837\u7684\u9000\u70E7\u836F\uFF0C\u5B83\u4EEC\u4E0D\u4F46\u9000\u70E7\u6548\u679C\u597D\uFF0C\u8FD8\u53EF\u4EE5\u7F13\u89E3\u75BC\u75DB\uFF0C\u6539\u5584\u5B69\u5B50\u7684\u8212\u9002\u5EA6\uFF0C\u5F53\u7136\u4E5F\u53EF\u4EE5\u7528\u3002</p><p>\u7269\u7406\u964D\u6E29\u7684\u65B9\u5F0F\u5F88\u591A\uFF0C\u5305\u62EC\u6CE1\u6FA1\u3001\u6E29\u6C34\u64E6\u6D74\u3001\u9152\u7CBE\u64E6\u6D74\u3001\u51B0\u888B\u51B0\u6577\u3001\u9000\u70ED\u8D34\u3001\u5439\u98CE\u6247\u7B49\u7B49\uFF0C\u539F\u7406\u662F\u901A\u8FC7\u76AE\u80A4\u548C\u5916\u754C\u73AF\u5883\u63A5\u89E6\uFF0C\u501F\u52A9\u6D41\u52A8\u7684\u6C34\u6216\u7A7A\u6C14\u5C06\u4EBA\u4F53\u70ED\u91CF\u5E26\u8D70\uFF0C\u8FBE\u5230\u9000\u70ED\u7684\u76EE\u7684\uFF0C\u4F46\u5B83\u4EEC\u7684\u9000\u70ED\u6548\u679C\u5E76\u4E0D\u597D\uFF0C\u53CD\u800C\u53EF\u80FD\u8BA9\u5B69\u5B50\u66F4\u96BE\u53D7\u3002</p><p>\u5176\u4E2D\u6E29\u6C34\u64E6\u6D74\u662F\u7528\u5F97\u6BD4\u8F83\u591A\uFF0C\u4E5F\u662F\u7814\u7A76\u5F97\u6BD4\u8F83\u591A\u7684\u65B9\u5F0F\u3002\u65E9\u57281970\u5E74\uFF0C\u5C31\u6709\u7814\u7A76\u7ED9\u4E24\u7EC4\u5B69\u5B50\u5206\u522B\u91C7\u7528\u4E86\u6E29\u6C34\u64E6\u6D74\u548C\u5403\u5B89\u6170\u5242\u7684\u6CBB\u7597\uFF0C\u7ED3\u679C\u53D1\u73B0\uFF0C1\u5C0F\u65F6\u540E\u9000\u70ED\u7684\u5B69\u5B50\u6570\u91CF\u5728\u4E24\u7EC4\u4E4B\u95F4\u6CA1\u6709\u660E\u663E\u5DEE\u522B[2]\u3002\u5F53\u7136\uFF0C\u8FD9\u9879\u7814\u7A76\u7684\u6837\u672C\u91CF\u5F88\u5C11\uFF0C\u8FD8\u4E0D\u8DB3\u4EE5\u5F97\u51FA\u660E\u786E\u7684\u7ED3\u8BBA\u3002\u5176\u4ED6\u5927\u90E8\u5206\u7814\u7A76[3,4,5]\u8BA4\u4E3A\uFF0C\u64E6\u6D74\u5728\u77ED\u65F6\u95F4\u5185\u662F\u53EF\u4EE5\u964D\u4F4E\u4F53\u6E29\u7684\uFF0C\u4F46\u6548\u679C\u4E3B\u8981\u662F\u4F53\u73B0\u572830\u5206\u949F\u5185\uFF0C\u4E4B\u540E\u4F53\u6E29\u5C31\u9010\u6E10\u56DE\u5347\u4E86\u3002</p><p>\u64E6\u6D74\u7684\u65B9\u5F0F\u6709\u5F88\u5927\u7684\u53D8\u6570\uFF0C\u6301\u7EED\u65F6\u95F4\u3001\u6C34\u6E29\u90FD\u53EF\u80FD\u5F71\u54CD\u6548\u679C\uFF0C\u6781\u7AEF\u4E00\u70B9\uFF0C\u5047\u5982\u8BA9\u5B69\u5B50\u76F4\u63A5\u6CE1\u51B7\u6C34\uFF0C\u53EA\u8981\u65F6\u95F4\u8DB3\u591F\u957F\uFF0C\u4F53\u6E29\u603B\u662F\u80FD\u4E0B\u964D\u7684\uFF0C\u751A\u81F3\u53EF\u4EE5\u8BA9\u5B69\u5B50\u4F53\u6E29\u90FD\u6CA1\u6389\uFF0C\u4F46\u8FD9\u6837\u505A\u5C31\u548C\u6211\u4EEC\u201C\u63D0\u9AD8\u5B69\u5B50\u8212\u9002\u5EA6\u201D\u7684\u76EE\u7684\u80CC\u9053\u800C\u9A70\u4E86\u3002</p><p>\u53D1\u70ED\u662F\u8EAB\u4F53\u5E94\u5BF9\u611F\u67D3\u6216\u8D77\u708E\u75C7\u53CD\u5E94\u7684\u4E00\u4E2A\u673A\u5236\uFF0C\u64E6\u6D74\u5229\u7528\u6C34\u548C\u8EAB\u4F53\u7684\u6E29\u5DEE\u4EE5\u53CA\u6C34\u7684\u84B8\u53D1\u6765\u5E26\u8D70\u70ED\u91CF\uFF0C\u6E29\u5DEE\u5C0F\u6CA1\u6548\u679C\uFF0C\u6E29\u5DEE\u5927\u4E86\u53C8\u4F1A\u5F15\u8D77\u8EAB\u4F53\u4E0D\u9002\uFF0C\u8FD9\u79CD\u505A\u6CD5\u662F\u8FDD\u53CD\u751F\u7406\u7684\uFF08\u56DE\u590D**\u300C\u64E6\u6FA1\u300D**\u4E86\u89E3\u66F4\u8BE6\u7EC6\u89E3\u91CA\uFF09\uFF0C\u5F88\u591A\u7814\u7A76\u4E5F\u8BC1\u5B9E\uFF0C\u64E6\u6D74\u4F1A\u5BFC\u81F4\u4E0D\u5C11\u5B69\u5B50\u54ED\u95F9\u548C\u5BD2\u6218[6]\u3002\u6211\u4EEC\u81EA\u5DF1\u4E5F\u4F53\u4F1A\u8FC7\u53D1\u70E7\uFF0C\u660F\u660F\u6C89\u6C89\u60F3\u7761\u89C9\u65F6\uFF0C\u8C01\u4F1A\u4E50\u610F\u88AB\u4EBA\u7528\u51C9\u6C34\u64E6\u6765\u64E6\u53BB\uFF1F</p><p>\u6CBB\u7597\u76EE\u7684\u662F\u8BA9\u5B69\u5B50\u4E0D\u96BE\u53D7\uFF0C\u6E29\u6C34\u64E6\u6D74\u5374\u53EF\u80FD\u8BA9\u5B69\u5B50\u66F4\u96BE\u53D7\uFF0C\u800C\u4E14\u8FD9\u79CD\u964D\u6E29\u7684\u6548\u679C\u5DEE\uFF0C\u6301\u7EED\u65F6\u95F4\u77ED\uFF0C\u81EA\u7136\u4E0D\u662F\u4E00\u4E2A\u597D\u7684\u6CBB\u7597\u65B9\u5F0F\u3002\u4F60\u4E5F\u8BB8\u4F1A\u89C9\u5F97\u4E0A\u9762\u5F15\u7528\u7684\u7814\u7A76\u4E0D\u5168\u9762\uFF0C\u4F46\u4E13\u4E1A\u6307\u5357\u548C\u5404\u4E2A\u6743\u5A01\u673A\u6784\u5BF9\u7269\u7406\u964D\u6E29\u7684\u8BC4\u4EF7\uFF0C\u662F\u4E1A\u5185\u4E13\u5BB6\u4EEC\u5728\u5168\u9762\u8BC4\u4F30\u5F53\u524D\u7684\u7814\u7A76\u505A\u51FA\u7684\u5EFA\u8BAE\uFF0C\u6211\u4EEC\u53EF\u4EE5\u770B\u770B\u4ED6\u4EEC\u600E\u4E48\u8BF4\u3002</p><p><strong>1.<strong><strong>\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u65D7\u4E0B\u7684\u79D1\u666E\u7F51\u7AD9</strong></strong>healthychildren.org</strong>[7]<strong>\uFF08\u6700\u540E\u66F4\u65B0\u65F6\u95F4\uFF1A<strong><strong>2015</strong></strong>\u5E74<strong><strong>11</strong></strong>\u6708\uFF09</strong></p><p>\u5927\u90E8\u5206\u60C5\u51B5\uFF0C\u8BA9\u53D1\u70E7\u7684\u5B69\u5B50\u66F4\u8212\u670D\u7684\u6700\u65B9\u4FBF\u65B9\u5F0F\u662F\u53E3\u670D\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u6216\u5E03\u6D1B\u82AC\uFF0C\u7136\u800C\uFF0C\u6709\u65F6\u5019\u4F60\u53EF\u80FD\u60F3\u7ED3\u5408\u6E29\u6C34\u64E6\u6D74\u6216\u53EA\u60F3\u6E29\u6C34\u64E6\u6D74\u3002\u6BD4\u5982\u77E5\u9053\u5B69\u5B50\u5BF9\u9000\u70E7\u836F\u8FC7\u654F\uFF0C\u6216\u8005\u4E0D\u80FD\u8010\u53D7\uFF08\u7F55\u89C1\uFF09\u53EF\u4EE5\u4F18\u5148\u9009\u7528\u64E6\u6D74\u3002\u5982\u679C\u53D1\u70E7\u8BA9\u4F60\u7684\u5B69\u5B50\u6781\u5EA6\u4E0D\u9002\uFF0C\u6216\u8005\u5B69\u5B50\u5455\u5410\uFF0C\u53EF\u5EFA\u8BAE\u914D\u5408\u9000\u70E7\u836F\u4F7F\u7528\u64E6\u6D74\u3002</p><p><strong>2.**<strong>\u9999\u6E2F\u536B\u751F\u7F72</strong>[8]</strong>\uFF08\u6700\u540E\u4FEE\u8BA2\u65F6\u95F4\uFF1A<strong><strong>2013</strong></strong>\u5E74<strong><strong>8</strong></strong>\u6708\uFF09**</p><p>\u6E29\u6C34\u6D74\u5E76\u4E0D\u80FD\u5E2E\u52A9\u5B9D\u5B9D\u9000\u70E7\uFF0C\u4F46\u82E5\u5B9D\u5B9D\u6709\u4EE5\u4E0B\u60C5\u51B5\uFF0C\u5F88\u591A\u4EBA\u90FD\u4F1A\u7ED9\u5B9D\u5B9D\u6CE1\u6E29\u6C34\u6D74\u6765\u4EE4\u4ED6\u8212\u670D\u4E00\u70B9\uFF1A\u2460\u4E0D\u80FD\u670D\u7528\u53E3\u670D\u836F\u7269\uFF1B\u2461\u670D\u836F\u540E\u5455\u5410\uFF1B\u2462\u8868\u73B0\u70E6\u8E81\u6216\u975E\u5E38\u4E0D\u9002\u3002</p><p><strong>3.**<strong>\u82F1\u56FD\u56FD\u5BB6\u536B\u751F\u4E0E\u4E34\u5E8A\u4F18\u5316\u7814\u7A76\u6240\uFF08<strong><strong>NICE</strong></strong>\uFF09\u6307\u5357</strong>[9]</strong>\uFF08\u6700\u540E\u66F4\u65B0\u65F6\u95F4\uFF1A<strong><strong>2013</strong></strong>\u5E74<strong><strong>5</strong></strong>\u6708\uFF09**</p><p>\u6E29\u6C34\u64E6\u6D74\u4E0D\u63A8\u8350\u7528\u4E8E\u6CBB\u7597\u53D1\u70ED\u3002</p><p><strong>4.<strong><strong>\u52A0\u62FF\u5927\u513F\u79D1\u533B\u751F\u534F\u4F1A\uFF08\u6700\u540E\u66F4\u65B0\u65F6\u95F4\uFF1A<strong><strong>2013</strong></strong>\u5E74</strong></strong>9****\u6708\uFF09</strong>[10]</p><p>\u4E0D\u63A8\u8350\u7ED9\u5B69\u5B50\u6E29\u6C34\u64E6\u6D74\uFF0C\u9152\u7CBE\u6D74\u6216\u64E6\u6D74\u3002</p><p><strong>5.<strong><strong>\u7B2C</strong></strong>20****\u7248\u300A\u5C3C\u5C14\u68EE\u513F\u79D1\u5B66\u300B\uFF08\u51FA\u7248\u65F6\u95F4\uFF1A<strong><strong>2016</strong></strong>\u5E74\uFF09</strong></p><p>\u4E0D\u8BA4\u4E3A\u6E29\u6C34\u6D74\u548C\u51B7\u6BEF\u8FD9\u6837\u7684\u7269\u7406\u65B9\u5F0F\u5BF9\u9000\u70ED\u6709\u6548\u3002</p><p><strong>6.<strong><strong>\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u300A\u513F\u7AE5\u53D1\u70ED\u4E0E\u9000\u70E7\u836F\u7684\u4F7F\u7528\u300B\u6307\u5357\uFF08\u53D1\u5E03\u65F6\u95F4\uFF1A<strong><strong>2011</strong></strong>\u5E74</strong></strong>3****\u6708\uFF09</strong>[1]</p><p>\u628A\u4F53\u6E29\u964D\u5230\u6B63\u5E38\u662F\u4E0D\u662F\u5C31\u6539\u5584\u4E86\u5B69\u5B50\u7684\u8212\u9002\u5EA6\uFF0C\u6211\u4EEC\u5E76\u4E0D\u6E05\u695A\uFF0C\u5916\u90E8\u964D\u6E29\u65B9\u5F0F\uFF0C\u6BD4\u5982\u6E29\u6C34\u64E6\u6D74\uFF0C\u53EF\u4EE5\u964D\u4F4E\u4F53\u6E29\u4F46\u63D0\u9AD8\u4E0D\u4E86\u8212\u9002\u5EA6\u3002</p><p><strong>7.<strong><strong>\u4E2D\u56FD</strong></strong>0<strong><strong>\u81F3</strong></strong>5****\u5C81\u513F\u7AE5\u75C5\u56E0\u4E0D\u660E\u7684\u6025\u6027\u53D1\u70ED\u8BCA\u65AD\u5904\u7406\u6307\u5357</strong></p><p><strong>2008\u5E7411\u6708\u7248</strong>\uFF1A\u7269\u7406\u964D\u6E29\u6548\u679C\u4E0D\u53CA\u9000\u70ED\u5242\uFF0C\u53EF\u4F5C\u4E3A\u8F85\u52A9\u9000\u70ED\u65B9\u6CD5\uFF0C\u7269\u7406\u964D\u6E29\u4E0E\u9000\u70ED\u5242\u8054\u5408\u5E94\u7528\u65F6\uFF0C\u4F53\u6E29\u4E0B\u964D\u5FEB\u4E8E\u5355\u7528\u9000\u70ED\u5242\uFF0C\u9AD8\u70ED\u65F6\u63A8\u8350\u4F7F\u7528\u9000\u70ED\u5242\u540C\u65F6\u8054\u5408\u6E29\u70ED\u643D\u8EAB\u7684\u7269\u7406\u964D\u6E29\u65B9\u6CD5\u3002</p><p><strong>2016\u5E742\u6708\u7248</strong>\uFF1A\u867D\u7136\u5728\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u9000\u70ED\u57FA\u7840\u4E0A\u8054\u5408\u6E29\u6C34\u64E6\u6D74\u77ED\u65F6\u95F4\u5185\u9000\u70ED\u6548\u679C\u66F4\u597D\u4E9B\uFF0C\u4F46\u4F1A\u660E\u663E\u589E\u52A0\u60A3\u513F\u4E0D\u9002\u611F\uFF0C\u4E0D\u63A8\u8350\u4F7F\u7528\u6E29\u6C34\u64E6\u6D74\u9000\u70ED\uFF0C\u66F4\u4E0D\u63A8\u8350\u51B0\u6C34\u6216\u4E59\u9187\u64E6\u6D74\u65B9\u6CD5\u9000\u70ED\u3002</p><p>\u5373\u4FBF\u662F\u539F\u6765\u4E2D\u56FD\u6307\u5357\uFF0C\u4E5F\u7531\u539F\u6765\u7684\u7269\u7406\u964D\u6E29\u53EF\u4EE5\u4F5C\u4E3A\u8F85\u52A9\u9000\u70ED\u65B9\u6CD5\uFF0C\u8F6C\u53D8\u4E3A\u660E\u786E\u4E0D\u63A8\u8350\u6E29\u6C34\u64E6\u6D74\u4E86\u3002\u800C\u5883\u5916\u7684\u673A\u6784\u8981\u4E48\u660E\u786E\u8BF4\u6E29\u6C34\u64E6\u6D74\u6CA1\u7528\u4E0D\u63A8\u8350\uFF0C\u8981\u4E48\u8BF4\u5728\u9000\u70E7\u836F\u4E0D\u80FD\u7528\u7684\u65F6\u5019\u624D\u53EF\u4EE5\u8003\u8651\uFF0C\u6216\u8005\u5728\u70E7\u5F97\u592A\u5389\u5BB3\u5B69\u5B50\u975E\u5E38\u96BE\u53D7\u65F6\u914D\u5408\u9000\u70E7\u836F\u7528\u4E00\u4E0B\u3002</p><p>\u81F3\u4E8E\u51B0\u888B\u51B0\u6577\u3001\u9000\u70ED\u8D34\u8FD9\u6837\u7684\u5C0F\u9762\u79EF\u63A5\u89E6\u76AE\u80A4\u7684\u964D\u6E29\u65B9\u5F0F\uFF0C\u65E2\u4E0D\u80FD\u964D\u6E29\u53C8\u53EF\u80FD\u8BA9\u5B69\u5B50\u96BE\u53D7\uFF0C\u81EA\u7136\u5E94\u8BE5\u653E\u5F03\u3002\u9152\u7CBE\u64E6\u6D74\u4E5F\u53EF\u80FD\u53EF\u4EE5\u77ED\u6682\u964D\u6E29\uFF0C\u4F46\u9152\u7CBE\u53EF\u901A\u8FC7\u76AE\u80A4\u5438\u6536\uFF0C\u5E26\u6765\u4E2D\u6BD2\u9690\u60A3[11]\uFF0C\u56E0\u6B64\u5E94\u8BE5\u7981\u6B62\u3002</p><p>\u6240\u4EE5\uFF0C\u5BF9\u4E8E\u6CA1\u6709\u5176\u5B83\u5065\u5EB7\u95EE\u9898\u7684\u5B69\u5B50\uFF0C39\xB0C\u4EE5\u4E0B\u6216\u6CA1\u6709\u660E\u663E\u4E0D\u8212\u670D\u8868\u73B0\u65F6\uFF0C\u4E0D\u9700\u8981\u9000\u70E7\uFF0C39\xB0C\u4EE5\u4E0A\u6216\u5B69\u5B50\u660E\u663E\u4E0D\u8212\u670D\u65F6\uFF0C\u9996\u9009\u7684\u662F\u9000\u70E7\u836F\u800C\u4E0D\u662F\u64E6\u6FA1\u8FD9\u6837\u7684\u7269\u7406\u964D\u6E29\u65B9\u5F0F\u3002\u6C38\u8FDC\u8981\u8BB0\u4F4F\uFF1A\u5E94\u5BF9\u5B69\u5B50\u53D1\u70E7\uFF0C\u600E\u4E48\u8212\u670D\u600E\u4E48\u6765\u3002\uFF08\u9898\u56FE\u6765\u81EAPixabay\uFF09</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p>1\u3001Janice E. Sullivan, MD, Henry C.Fever and Antipyretic Use in Children.PEDIATRICS 2011,127: 580 -587.</p><p>2\u3001Steele RW, Tanaka PT, Lara RP, Bass JW. Evaluation of sponging and of oral antipyretic therapy to reduce fever. Journal of Pediatrics 1970;77(5):824\u20139.</p><p>3\u3001Agbolosu NB, Cuevas LE, Milligan P, Broadhead RL, Brewster D, Graham SM. Efficacy of tepid sponging versus paracetamol in reducing temperature in febrile children. Ann Trop Paediatr. 1997;17:283\u20138.</p><p>4\u3001Aksoylar S, Ak\u015Fit S, Ca\u011Flayan S, Yaprak I, Bakiler R, Cetin F. Evaluation of sponging and antipyretic medication to reduce body temperature in febrile children. Acta Paediatr Jpn. 1997;39:215\u20137.</p><p>5\u3001Kinmonth AL, Fulton Y, Campbell MJ. Management of feverish children at home. BMJ. 1992;305:1134\u20136.</p><p>6\u3001Aluka TM,Gyuse AN,Udonwa NE. Comparison of Cold Water Sponging and Acetaminophen in Control of Fever Among Children Attending a Tertiary Hospital in South Nigeria.J Family Med Prim Care. 2013;2(2):153-8</p><p>7\u3001http://www.healthychildren.org/English/health-issues/conditions/fever/Pages/Treating-a-Fever-Without-Medicine.aspx</p><p>8\u3001http://www.fhs.gov.hk/sc_chi/health_info/child/14743.html</p><p>9\u3001https://www.nice.org.uk/guidance/cg160/resources/guidance-feverish-illness-in-children-pdf</p><p>10\u3001http://www.caringforkids.cps.ca/handouts/fever_and_temperature_taking</p><p>11\u3001\u513F\u7AE5\u6025\u6027\u9152\u7CBE\u4E2D\u6BD232\u4F8B\u4E34\u5E8A\u6551\u6CBB\u5206\u6790</p><p>\u70B9\u51FB\u9605\u8BFB\u539F\u6587\uFF0C\u53EF\u5411\u6211\u548C\u5176\u5B83\u533B\u751F\u54A8\u8BE2</p>",45),b={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626661&idx=1&sn=bf6cb2ae56dfa58390f080b50998e5d9&chksm=8b5cb55fbc2b3c49103506c3162a92f9836e8e2221e8a742198f19d53f6156e711eb49a16d02&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},C=t("\u67E5\u770B\u539F\u6587");function w(k,v){const e=r("RouterLink"),s=r("ExternalLinkIcon");return p(),a(i,null,[h,n("p",null,[f,o(e,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=200623156&idx=1&sn=744b3dfe0f7fdf4484db6f67975a275b&scene=21#wechat_redirect/"},{default:g(()=>[_]),_:1}),u]),m,n("p",null,[n("a",b,[C,o(s)])])],64)}var A=d(l,[["render",w]]);export{A as default};
