import{r as p,o,c as n,a as e,b as r,F as i,d as t,e as c}from"./app.9aaa2256.js";import{_ as s}from"./plugin-vue_export-helper.21dcd24c.js";const l={},d=t('<h3 id="\u505A\u5B8C\u5E7D\u95E8\u87BA\u6746\u83CCc14\u547C\u6C14\u8BD5\u9A8C\u540E-\u53D1\u73B0\u81EA\u5DF1\u6000\u5B55\u4E86-\u5B69\u5B50\u8FD8\u80FD\u8981\u5417" tabindex="-1"><a class="header-anchor" href="#\u505A\u5B8C\u5E7D\u95E8\u87BA\u6746\u83CCc14\u547C\u6C14\u8BD5\u9A8C\u540E-\u53D1\u73B0\u81EA\u5DF1\u6000\u5B55\u4E86-\u5B69\u5B50\u8FD8\u80FD\u8981\u5417" aria-hidden="true">#</a> \u505A\u5B8C\u5E7D\u95E8\u87BA\u6746\u83CCC14\u547C\u6C14\u8BD5\u9A8C\u540E\uFF0C\u53D1\u73B0\u81EA\u5DF1\u6000\u5B55\u4E86\uFF0C\u5B69\u5B50\u8FD8\u80FD\u8981\u5417\uFF1F</h3><p>\u5E7D\u95E8\u87BA\u6746\u83CC\uFF08Hp\uFF09\u662F\u76EE\u524D\u6240\u77E5\u552F\u4E00\u80FD\u5728\u4EBA\u80C3\u4E2D\u751F\u5B58\u7684\u5FAE\u751F\u7269\u3002</p><p>\u8FD9\u4E2A\u53E4\u8001\u7684\u7EC6\u83CC\u5DF2\u7ECF\u548C\u4EBA\u7C7B\u5171\u751F\u4E86\u8D85\u8FC758000\u5E74 [1] \u3002\u76EE\u524D\u5168\u7403\u81EA\u7136\u4EBA\u7FA4\u611F\u67D3\u7387\u8D85\u8FC750%\uFF0C\u53D1\u8FBE\u56FD\u5BB6\u611F\u67D3\u7387\u7EA6\u4E3A30%\uFF0C\u5728\u53D1\u5C55\u4E2D\u56FD\u5BB6\u5219\u9AD8\u8FBE80% [2] \u3002</p><p>\u5E7D\u95E8\u87BA\u6746\u83CC\u9633\u6027\u7684\u4EBA\u7FA4\u4E2D\uFF0C\u5C11\u90E8\u5206\u53EF\u80FD\u4F1A\u51FA\u73B0\u8FDB\u98DF\u540E\u4E0A\u8179\u90E8\u9971\u80C0\u3001\u4E0D\u9002\u6216\u75BC\u75DB\uFF0C\u4F34\u6709\u5176\u4ED6\u4E0D\u826F\u75C7\u72B6\uFF0C\u5982\u55F3\u6C14\u3001\u8179\u80C0\u3001\u53CD\u9178\u548C\u98DF\u6B32\u51CF\u9000\u7B49\u3002\u957F\u671F\u611F\u67D3\u540E\u53EF\u80FD\u589E\u52A0\u80C3\u764C\u3001\u80C3\u9ECF\u819C\u76F8\u5173\u6DCB\u5DF4\u7EC4\u7EC7\u6DCB\u5DF4\u7624\u7684\u53D1\u75C5\u98CE\u9669\uFF0C\u4E5F\u53EF\u80FD\u5BFC\u81F4\u6D88\u5316\u6027\u6E83\u75A1\u53CA\u6D3B\u52A8\u6027\u80C3\u708E\u7B49\u75BE\u75C5\u3002</p><p>\u4F46\u7531\u4E8E\u5927\u90E8\u5206\u9633\u6027\u4EBA\u7FA4\u4E0D\u4F34\u968F\u75C7\u72B6\uFF0C\u4E0D\u5C11\u4EBA\u4E3A\u4E86\u6392\u67E5\u81EA\u5DF1\u662F\u5426\u611F\u67D3\u4E86\u5E7D\u95E8\u87BA\u6746\u83CC\uFF0C\u4F1A\u9009\u62E9\u505A\u76F8\u5173\u68C0\u67E5\u3002\u9488\u5BF9\u5E7D\u95E8\u87BA\u6746\u83CC\u6700\u5E38\u89C1\u7684\u68C0\u67E5\u65B9\u6CD5\u4E4B\u4E00\u4E3AC14\u547C\u6C14\u8BD5\u9A8C\u3002</p><p>C14\u547C\u6C14\u8BD5\u9A8C\u5177\u6709\u4E00\u5B9A\u7684\u653E\u5C04\u6027\uFF0C\u4E00\u4E9B\u5973\u6027\u505A\u5B8C\u68C0\u67E5\u540E\u53D1\u73B0\u81EA\u5DF1\u6000\u5B55\u4E86\uFF0C\u7136\u540E\u5F15\u53D1\u4E86\u5F88\u591A\u62C5\u5FC3\uFF0C\u4E0B\u9762\u5C31\u628A\u8FD9\u4E9B\u5E38\u89C1\u95EE\u9898\u89E3\u7B54\u4E00\u4E0B\u3002</p><hr><p><strong>1. \u505A\u5B8CC14\u547C\u6C14\u8BD5\u9A8C\u540E\u53D1\u73B0\u81EA\u5DF1\u6000\u5B55\u4E86\uFF0C\u5B9D\u5B9D\u8FD8\u80FD\u4E0D\u80FD\u8981\uFF1F</strong></p><p>\u7B54\uFF1A\u53EA\u8981\u4EA7\u68C0\u7ED3\u679C\u4E00\u5207\u6B63\u5E38\uFF0C\u662F\u53EF\u4EE5\u7684\u3002</p><p>\u5728\u7F8E\u56FDFDA\u5B55\u671F\u836F\u54C1\u5206\u7C7B\u4E2DC14\u5C5EC\u7EA7\u3002C\u7EA7\u662F\u5BF9\u836F\u7269\u7684\u4E00\u79CD\u5206\u7C7B\uFF0C\u5206\u7C7B\u4E3AC\u7EA7\u7684\u836F\u7269\u5305\u62EC\u4E0B\u8FF0\u60C5\u51B5\u4E4B\u4E00\uFF1A</p><ul><li><p>\u6307\u5728\u52A8\u7269\u7814\u7A76\u4E2D\u8BC1\u660E\u5BF9\u80CE\u513F\u6709\u5371\u5BB3\u6027\uFF08\u81F4\u7578\u6216\u80DA\u80CE\u6B7B\u4EA1\u7B49\uFF09\u7684\u836F\u7269\uFF1B</p></li><li><p>\u5C1A\u65E0\u8BBE\u5BF9\u7167\u7684\u598A\u5A20\u5987\u5973\u7814\u7A76\u7684\u836F\u7269\uFF1B</p></li><li><p>\u5C1A\u672A\u5BF9\u598A\u5A20\u5987\u5973\u53CA\u52A8\u7269\u8FDB\u884C\u7814\u7A76\u7684\u836F\u7269\u3002</p></li></ul><p>\u672C\u7C7B\u836F\u7269\u53EA\u6709\u5728\u6743\u8861\u5BF9\u5B55\u5987\u7684\u76CA\u5904\u5927\u4E8E\u5BF9\u80CE\u513F\u7684\u5371\u5BB3\u4E4B\u540E\uFF0C\u65B9\u53EF\u4F7F\u7528\u3002</p><p>C14\u5C5E\u4E8E\u4E0A\u8FF0\u7B2C3\u79CD\u60C5\u51B5\uFF0C\u5373\uFF1A\u5C1A\u672A\u8FDB\u884C\u52A8\u7269\u548C\u4EBA\u7C7B\u751F\u6B96\u65B9\u9762\u7684\u7814\u7A76\uFF1B\u53E6\u5916\u4ECE\u63A5\u53D7\u653E\u5C04\u6027\u7269\u8D28\u7684\u5B89\u5168\u5242\u91CF\u6765\u770B\uFF0C\u505A\u547C\u6C14\u8BD5\u9A8C\u65F6\u6240\u4F7F\u7528\u7684\u836F\u7269\u5242\u91CF\u6781\u4F4E\uFF0C\u7EA6\u4E3A1\u03BCCi [3] \uFF0C\u8FDC\u8FDC\u4F4E\u4E8E\u5B55\u5987\u901A\u8FC7\u81EA\u7136\u6765\u6E90\u5438\u6536\u7684\u8F90\u5C04\u91CF [4] \u3002</p><p>\u56E0\u6B64\uFF0C\u505A\u5B8C\u5E7D\u95E8\u87BA\u6746\u83CCC14\u547C\u6C14\u8BD5\u9A8C\u540E\uFF0C\u53D1\u73B0\u81EA\u5DF1\u6000\u5B55\u4E86\uFF0C\u5B9D\u5B9D\u662F\u53EF\u4EE5\u8981\u7684\uFF0C\u800C\u4E14\u4E0D\u9700\u8981\u505A\u4EC0\u4E48\u7279\u6B8A\u68C0\u67E5\uFF0C\u6B63\u5E38\u4EA7\u68C0\u5C31\u591F\u4E86\u3002</p><p><strong>2. \u6000\u5B55\u540E\uFF0C\u4E08\u592B\u53D1\u73B0\u4E86\u5E7D\u95E8\u87BA\u6746\u83CC\u9633\u6027\uFF0C\u5B55\u5987\u9700\u8981\u505A\u76F8\u5173\u68C0\u67E5\u5417\uFF1F</strong></p><p>\u7B54\uFF1A\u4E0D\u9700\u8981\u3002</p><p>\u5E7D\u95E8\u87BA\u6746\u83CC\u7B5B\u67E5\u5176\u5B9E\u5E76\u4E0D\u662F\u6BCF\u4E2A\u4EBA\u90FD\u6709\u5FC5\u8981\u505A\u7684\u5E38\u89C4\u4F53\u68C0\u9879\u76EE\u3002\u6839\u636E\u6211\u56FD2019\u5E74\u53D1\u5E03\u7684\u300A\u5E7D\u95E8\u87BA\u6746\u83CC\u611F\u67D3\u57FA\u5C42\u8BCA\u7597\u6307\u5357\u300B\uFF0C\u5E7D\u95E8\u87BA\u6746\u83CC\u7684\u7B5B\u67E5\u5BF9\u8C61\u4E3B\u8981\u662F\u80C3\u764C\u9AD8\u53D1\u533A\u4EBA\u7FA4\uFF08\u6211\u56FD\u80C3\u764C\u53D1\u751F\u7387\u6700\u9AD8\u7684\u5730\u533A\u4E3B\u8981\u96C6\u4E2D\u5728\u897F\u5317\u3001\u4E1C\u5317\u548C\u4E1C\u5357\u6CBF\u6D77\u5730\u533A\uFF09\u548C\u80C3\u764C\u9AD8\u98CE\u9669\u4E2A\u4F53 [5] \u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5982\u679C\u4F60\u4E0D\u662F\u5728\u80C3\u764C\u9AD8\u53D1\u533A\u957F\u671F\u751F\u6D3B\u3001\u6CA1\u6709\u80C3\u764C\u5BB6\u65CF\u53F2\u4E14\u6CA1\u6709\u4EFB\u4F55\u75C7\u72B6\uFF0C\u4E00\u822C\u4E0D\u7528\u505A\u5E7D\u95E8\u87BA\u6746\u83CC\u68C0\u67E5\u3002</p><p>\u6CA1\u80C3\u53E3\u3001\u5B55\u5410\u662F\u5F88\u591A\u5B55\u521D\u671F\u7684\u8868\u73B0\uFF0C\u8FD9\u4E9B\u6D88\u5316\u9053\u75C7\u72B6\u521A\u597D\u4E0E\u611F\u67D3\u5E7D\u95E8\u87BA\u6746\u83CC\u540E\u67D0\u4E9B\u8868\u73B0\u76F8\u4F3C\uFF0C\u8FD9\u65F6\u5019\u5982\u679C\u53E6\u4E00\u534A\u5728\u4F60\u5B55\u671F\u671F\u95F4\u505A\u4E86\u76F8\u5173\u68C0\u67E5\u5E76\u4E14\u53D1\u73B0\u5E7D\u95E8\u87BA\u6746\u83CC\u9633\u6027\uFF0C\u81EA\u5DF1\u9700\u8981\u68C0\u67E5\u5417\uFF1F</p><p>\u5BF9\u4E8E\u975E\u7B5B\u67E5\u5BF9\u8C61\u7684\u666E\u901A\u5B55\u5987\uFF0C\u7531\u4E8E\u5B55\u671F\u4E00\u822C\u4E0D\u505A\u76F8\u5E94\u6CBB\u7597\uFF0C\u6240\u4EE5\u4E0D\u63A8\u8350\u8FDB\u884C\u68C0\u67E5\uFF0C\u53EF\u4EE5\u7B49\u5230\u4EA7\u540E\u6216\u5B8C\u6210\u54FA\u4E73\u540E\u518D\u505A\u76F8\u5173\u68C0\u67E5\u3002</p><p>\u5982\u679C\u75C5\u60C5\u9700\u8981\uFF08\u5982\u598A\u5A20\u5408\u5E76\u6D3B\u52A8\u6027\u6D88\u5316\u6027\u6E83\u75A1\u75C5\uFF09\uFF0C\u90A3\u4E48\u53EF\u4EE5\u9009\u62E9\u7CAA\u4FBF\u6297\u539F\u68C0\u6D4B\u6216\u975E\u653E\u5C04\u602713C\u5C3F\u7D20\u547C\u6C14\u8BD5\u9A8C\u68C0\u6D4B\u3002\u68C0\u6D4B\u6548\u80FD\u548CC14\u5C3F\u7D20\u547C\u6C14\u8BD5\u9A8C\u662F\u4E00\u6837\u7684\u3002</p><p><strong>3. \u5E7D\u95E8\u87BA\u65CB\u6746\u83CC\u5BF9\u6000\u5B55\u6709\u6CA1\u6709\u5F71\u54CD\uFF1F</strong></p><p>\u56DE\u5230\u6587\u7AE0\u5F00\u5934\u7684\u573A\u666F\uFF0C\u5F53\u505A\u5B8C\u68C0\u67E5\u540E\u624D\u53D1\u73B0\u81EA\u5DF1\u6000\u5B55\u4E86\uFF0C\u5982\u679C\u7ED3\u679C\u662F\u5E7D\u95E8\u87BA\u6746\u83CC\u9633\u6027\uFF0C\u8FD9\u5BF9\u6000\u5B55\u662F\u5426\u6709\u5F71\u54CD\u5462\uFF1F</p><p>\u5E7D\u95E8\u87BA\u6746\u83CC\u5BF9\u5988\u5988\u7684\u5F71\u54CD\u53EF\u80FD\u6CA1\u6709\u4EFB\u4F55\u8868\u73B0\uFF0C\u4E5F\u53EF\u80FD\u4F1A\u6709\u55F3\u6C14\u3001\u6D88\u5316\u4E0D\u826F\u7B49\u75C7\u72B6\u51FA\u73B0\uFF0C\u90E8\u5206\u8BC1\u636E\u8868\u660E\u5E7D\u95E8\u87BA\u6746\u83CC\u5728\u598A\u5A20\u671F\u53EF\u5BFC\u81F4\u4E25\u91CD\u7684\u6076\u5FC3\u548C\u5455\u5410 [6] \u3002</p><p>\u90A3\u662F\u5426\u4F1A\u5BFC\u81F4\u4E0D\u826F\u598A\u5A20\u7ED3\u5C40\uFF1F</p><p>2019\u5E74\u4E00\u9879\u5305\u542B\u4E8631\u9879\u7814\u7A76\uFF0C\u5171\u670922845\u540D\u60A3\u8005\u7684\u835F\u8403\u5206\u6790[7]\u53D1\u73B0\uFF1A\u5E7D\u95E8\u87BA\u6746\u83CC\u611F\u67D3\u4E0E\u5B50\u75EB\u524D\u671F\u3001\u80CE\u513F\u751F\u957F\u53D7\u9650\u3001\u598A\u5A20\u671F\u7CD6\u5C3F\u75C5\u3001\u81EA\u7136\u6D41\u4EA7\u548C\u51FA\u751F\u7F3A\u9677\u76F8\u5173\u3002</p><p>\u654F\u611F\u6027\u5206\u6790\u663E\u793A\uFF1A\u5E7D\u95E8\u87BA\u6746\u83CC\u611F\u67D3\u4E0E\u4F4E\u51FA\u751F\u4F53\u91CD\u6709\u663E\u8457\u76F8\u5173\u6027\u3002</p><p>\u4F46\u662F\u8BE5\u5206\u6790\u5E76\u6CA1\u6709\u660E\u786E\u5E7D\u95E8\u87BA\u65CB\u6746\u83CC\u611F\u67D3\u7684\u65F6\u673A\u548C\u65F6\u957F\u4E0E\u8FD9\u4E9B\u4E0D\u826F\u7ED3\u5C40\u7684\u5173\u7CFB\uFF0C\u56E0\u6B64\u5206\u6790\u7684\u7ED3\u8BBA\u4E5F\u5E76\u6CA1\u6709\u63A8\u8350\u5B55\u671F\u505A\u7B5B\u67E5\uFF0C\u800C\u53EA\u662F\u5EFA\u8BAE\u5982\u679C\u5B55\u524D\u5C31\u53D1\u73B0\u4E86\u5E7D\u95E8\u87BA\u6746\u83CC\u611F\u67D3\uFF0C\u8FD8\u662F\u6839\u9664\u540E\u518D\u6000\u5B55\u3002</p><p>\u5982\u679C\u5B55\u671F\u53D1\u73B0\u4E86\u5E7D\u95E8\u87BA\u6746\u83CC\u611F\u67D3\uFF0C\u8F7B\u4E2D\u5EA6\u75C7\u72B6\u7684\u60A3\u8005\u4E00\u822C\u63A8\u8FDF\u5230\u5206\u5A29\u548C\u54FA\u4E73\u671F\u540E\u518D\u8FDB\u884C\u6CBB\u7597\u3002\u75C7\u72B6\u4E25\u91CD\u7684\u60A3\u8005\uFF08\u5982\u4E25\u91CD\u598A\u5A20\u5267\u5410\u7B49\uFF09\uFF0C\u53EF\u4EE5\u5728\u4E13\u4E1A\u533B\u5E08\u6743\u8861\u5229\u5F0A\u540E\u9009\u62E9\u598A\u5A20\u671F\u4F4E\u5371\u836F\u7269\u8FDB\u884C\u6CBB\u7597[8]\u3002</p><p><strong>4. \u5988\u5988\u6709\u5E7D\u95E8\u87BA\u6746\u83CC\uFF0C\u4F1A\u4F20\u67D3\u7ED9\u5B69\u5B50\u5417\uFF1F</strong></p><p>\u5E7D\u95E8\u87BA\u6746\u83CC\u4E3B\u8981\u5B58\u5728\u4E8E\u6211\u4EEC\u7684\u80C3\u90E8\uFF0C\u53EF\u901A\u8FC7\u7CAA\u4FBF\u6392\u51FA\u4F53\u5916\uFF0C\u4F46\u4E0D\u4F1A\u901A\u8FC7\u80CE\u76D8\u4F20\u67D3\u7ED9\u80CE\u513F\u3002</p><p>\u5728\u5B9D\u5B9D\u51FA\u751F\u540E\uFF0C\u5982\u679C\u6BCD\u4EB2\u53E3\u5BF9\u53E3\u7ED9\u5B69\u5B50\u5582\u517B\u98DF\u7269\uFF0C\u6216\u8005\u5582\u98DF\u65F6\u4E0D\u6CE8\u610F\u624B\u536B\u751F\u6709\u53EF\u80FD\u5BFC\u81F4\u5B69\u5B50\u611F\u67D3\u3002\u6240\u4EE5\uFF0C\u5728\u54FA\u4E73\u671F\u7ED3\u675F\u540E\uFF0C\u5EFA\u8BAE\u6709\u5E7D\u95E8\u87BA\u6746\u83CC\u6D3B\u52A8\u6027\u611F\u67D3\u8BC1\u636E\u7684\u6BCD\u4EB2\u5C3D\u65E9\u8FDB\u884C\u6CBB\u7597\uFF0C\u51CF\u5C11\u75C5\u60C5\u5BF9\u81EA\u8EAB\u548C\u5B69\u5B50\u7684\u5F71\u54CD\u3002 \uFF08\u533B\u5B66\u6821\u5BF9\uFF1A\u5F90\u73AB\u4E3D\u533B\u751F\u3001\u5218\u8083\u971E\u533B\u751F\uFF09</p><p><strong>\u53C2\u8003\u6587\u732E</strong></p><p>1.Linz B, Balloux F, Moodley Y, Manica A, Liu H, Roumagnac P, Falush D, Stamer C, Prugnolle F, van der Merwe SW, Yamaoka Y, Graham DY, Perez-Trallero E, Wadstrom T, Suerbaum S, Achtman M. An African origin for the intimate association between humans and Helicobacter pylori. Nature. 2007 Feb 22;445(7130):915-918. doi: 10.1038/nature05562. Epub 2007 Feb 7. PMID: 17287725; PMCID: PMC1847463.</p><p>2. Xie C, Lu NH. Review: clinical management of Helicobacter pylori infection in China. Helicobacter.2015;20(1):1-10. doi:10.1111/hel.12178</p><p>3. Leide-Svegborn S, Stenstr\xF6m K, Olofsson M, et al. Biokinetics and radiation doses for carbon-14 urea in adults and children undergoing the Helicobacter pylori breath test. Eur J Nucl Med. 1999;26(6):573-580. doi:10.1007/s002590050424</p><p>4. Bentur Y, Matsui D, Koren G. Safety of 14C-UBT for diagnosis of Helicobacter pylori infection in pregnancy. Can Fam Physician. 2009;55(5):479-480.</p><p>5. \u4E2D\u534E\u533B\u5B66\u4F1A,\u4E2D\u534E\u533B\u5B66\u4F1A\u6742\u5FD7\u793E,\u4E2D\u534E\u533B\u5B66\u4F1A\u5168\u79D1\u533B\u5B66\u5206\u4F1A,\u4E2D\u534E\u533B\u5B66\u4F1A\u6D88\u5316\u75C5\u5B66\u5206\u4F1A\u5E7D\u95E8\u87BA\u6746\u83CC\u5B66\u7EC4,\u4E2D\u534E\u533B\u5B66\u4F1A\u300A\u4E2D\u534E\u5168\u79D1\u533B\u5E08\u6742\u5FD7\u300B\u7F16\u8F91\u59D4\u5458\u4F1A,\u6D88\u5316\u7CFB\u7EDF\u75BE\u75C5\u57FA\u5C42\u8BCA\u7597\u6307\u5357\u7F16\u5199\u4E13\u5BB6\u7EC4.\u5E7D\u95E8\u87BA\u6746\u83CC\u611F\u67D3\u57FA\u5C42\u8BCA\u7597\u6307\u5357\uFF082019\u5E74\uFF09[J].\u4E2D\u534E\u5168\u79D1\u533B\u5E08\u6742\u5FD7,2020,19(5):397-402.</p><p>6. Grooten IJ, Den Hollander WJ, Roseboom TJ, et al. Helicobacter pylori infection: a predictor of vomiting severity in pregnancy and adverse birth outcome.AmJObstetGynecol.2017;216(5):512.e1-512.e9.doi:10.1016/j.ajog.2017.01.042.</p><p>7. Zhan Y, Si M, Li M, Jiang Y. The risk of Helicobacter pylori infection for adverse pregnancy outcomes: A systematic review and meta-analysis. Helicobacter. 2019;24(2):e12562. doi:10.1111/hel.12562</p><p>8. Nguyen CT, Davis KA, Nisly SA, Li J. Treatment of Helicobacter pylori in Special Patient Populations. Pharmacotherapy. 2019;39(10):1012-1022. doi:10.1002/phar.2318</p><p>\uFF08\u53EF\u4E0A\u4E0B\u6ED1\u52A8\u67E5\u770B\uFF09</p><hr><p>\u672C\u6587\u9996\u53D1\u4E8E\u300C\u6021\u79BE\u79D1\u666E\u300D</p><p>\u5173\u4E8E\u5E7D\u95E8\u87BA\u6746\u83CC\u7684\u9884\u9632\u548C\u6CBB\u7597\uFF0C\u6021\u79BE\u4E0A\u4E5F\u6709\u4E00\u5802\u4E13\u680F\u8BFE\u7A0B</p><p>\u6709\u9700\u8981\u53EF\u901A\u8FC7\u4E0B\u9762\u4E8C\u7EF4\u7801\u4E86\u89E3</p><p>\u6021\u79BE\u4F1A\u5458\u53EF\u514D\u8D39\u542C</p>',47),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651632346&idx=1&sn=8b91a1095f664fba155049b05fa61e45&chksm=8b5c9b20bc2b1236d9b6d080eebfe76aca2d66045a3f5d3934c1c90fcf1a6c4a950f8ce9b271&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},m=c("\u67E5\u770B\u539F\u6587");function f(b,u){const a=p("ExternalLinkIcon");return o(),n(i,null,[d,e("p",null,[e("a",h,[m,r(a)])])],64)}var C=s(l,[["render",f]]);export{C as default};
