import{r,o as t,c as n,a as p,b as o,F as a,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const g={},i=s('<h3 id="\u751F\u75C5\u522B\u786C\u625B-\u54FA\u4E73\u5988\u5988\u53EF\u4EE5\u5403\u8FD9\u4E9B\u836F" tabindex="-1"><a class="header-anchor" href="#\u751F\u75C5\u522B\u786C\u625B-\u54FA\u4E73\u5988\u5988\u53EF\u4EE5\u5403\u8FD9\u4E9B\u836F" aria-hidden="true">#</a> \u751F\u75C5\u522B\u786C\u625B\uFF0C\u54FA\u4E73\u5988\u5988\u53EF\u4EE5\u5403\u8FD9\u4E9B\u836F</h3><p>\u5988\u5988\u7684\u7231\u65E0\u7591\u662F\u4F1F\u5927\u7684\uFF0C\u56E0\u4E3A\u62C5\u5FC3\u836F\u7269\u5BF9\u5B9D\u5B9D\u4EA7\u751F\u4E0D\u597D\u7684\u5F71\u54CD\uFF0C\u5F88\u591A\u54FA\u4E73\u5988\u5988\u751F\u75C5\u4E86\u4E5F\u786C\u6491\u7740\u4E0D\u6562\u5403\u836F\u3002\u5176\u5B9E\uFF0C\u8FD9\u4E9B\u62C5\u5FC3\u5927\u591A\u6570\u90FD\u662F\u591A\u4F59\u7684\uFF0C\u56E0\u4E3A\u5F88\u591A\u836F\u7269\u5BF9\u54FA\u4E73\u7684\u5F71\u54CD\u5F88\u5C0F\uFF0C\u6709\u9700\u8981\u65F6\u5B8C\u5168\u53EF\u4EE5\u5403\uFF0C\u4E5F\u4E0D\u7528\u6682\u505C\u5B9D\u5B9D\u7684\u53E3\u7CAE\u3002\u76F8\u4FE1\u4E00\u4E2A\u4F1F\u5927\u7684\u5988\u5988\uFF0C\u4E5F\u4E00\u5B9A\u662F\u4E00\u4E2A\u79D1\u5B66\u7684\u5988\u5988\u3002</p><p>\u52A0\u5165\u6021\u79BE\u5065\u5EB7\u5DF2\u6709\u6708\u4F59\uFF0C\u5728\u6211\u6536\u5230\u7684\u7528\u836F\u54A8\u8BE2\u4E2D\uFF0C\u5927\u534A\u662F\u54A8\u8BE2\u54FA\u4E73\u671F\u5403\u836F\u7684\u95EE\u9898\u3002\u60F3\u5FC5\u8FD8\u6709\u5F88\u591A\u65B0\u624B\u5988\u5988\u6216\u591A\u6216\u5C11\u4F1A\u9047\u5230\u540C\u6837\u7684\u95EE\u9898\uFF0C\u6211\u5C06\u54FA\u4E73\u671F\u5B89\u5168\u7528\u836F\u7684\u4E00\u4E9B\u5E38\u89C1\u95EE\u9898\u5F52\u7EB3\u603B\u7ED3\uFF0C\u65B9\u4FBF\u5988\u5988\u4EEC\u6536\u85CF\u5907\u7528\u3002</p><p><strong>\u836F\u7269\u5982\u4F55\u5F71\u54CD\u5B9D\u5B9D\uFF1F</strong></p><p>\u836F\u7269\u88AB\u5988\u5988\u670D\u4E0B\u540E\uFF0C\u9700\u8981\u8FC7\u4E94\u5173\u65A9\u516D\u5C06\u624D\u80FD\u5F71\u54CD\u5230\u5B9D\u5B9D\u3002\u6700\u5148\u8981\u7ECF\u5988\u5988\u7684\u80A0\u9053\u5438\u6536\u8FDB\u5165\u5230\u8840\u6DB2\uFF0C\u518D\u7ECF\u8840\u6DB2\u8FDB\u5165\u4E73\u6C41\uFF0C\u7136\u540E\u5B9D\u5B9D\u5438\u4E73\u540E\u53C8\u8981\u7ECF\u8FC7\u80A0\u9053\u5438\u6536\u8FDB\u5165\u5B9D\u5B9D\u7684\u8840\u6DB2\uFF0C\u8FD9\u65F6\u624D\u4F1A\u5BF9\u5B9D\u5B9D\u4EA7\u751F\u5F71\u54CD\u3002\u8FD9\u4E00\u8DEF\u53EF\u8C13\u662F\u5343\u8F9B\u4E07\u82E6\uFF0C\u56E0\u4E3A\u6BCF\u4E00\u6B21\u8DE8\u754C\uFF0C\u5927\u90E8\u5206\u836F\u7269\u90FD\u4F1A\u635F\u5931\u60E8\u91CD\uFF0C\u6700\u7EC8\u5230\u8FBE\u5B9D\u5B9D\u4F53\u5185\u7684\u836F\u91CF\u5DF2\u5FAE\u4E4E\u5176\u5FAE\u3002\u4E5F\u6709\u4E00\u4E9B\u836F\u7269\u4E0D\u7528\u8FDB\u5165\u5B9D\u5B9D\u8840\u6DB2\u4E5F\u53EF\u80FD\u4EA7\u751F\u4E0D\u597D\u7684\u5F71\u54CD\uFF0C\u5927\u591A\u5B9D\u5B9D\u4F1A\u8868\u73B0\u4E3A\u62C9\u809A\u5B50\u7B49\u80C3\u80A0\u9053\u75C7\u72B6\u3002</p><p>\u836F\u7269\u4ECE\u5988\u5988\u5230\u5B9D\u5B9D\u8840\u6DB2\u7684\u91CF\u53D8\u8FC7\u7A0B</p><p><strong>\u54EA\u4E9B\u5E38\u89C1\u836F\u7269\u53EF\u4EE5\u5B89\u5168\u4F7F\u7528\uFF1F</strong></p><p>\u71AC\u8FC7\u8F9B\u82E6\u7684\u5341\u6708\u6000\u80CE\uFF0C\u6F2B\u957F\u7684\u54FA\u4E73\u671F\u5BF9\u4E8E\u5988\u5988\u4EEC\u800C\u8A00\u4E5F\u662F\u4E00\u4E2A\u6311\u6218\u3002\u4E8B\u5B9E\u4E0A\u6BCD\u4E73\u5582\u517B\u9700\u8981\u5988\u5988\u4EEC\u62E5\u6709\u5F88\u591A\u7684\u575A\u6301\u548C\u6BC5\u529B\uFF0C\u8FD9\u4E2A\u5FC3\u8DEF\u5386\u7A0B\u4E0D\u4E00\u5B9A\u5168\u90E8\u90FD\u662F\u5E78\u798F\uFF0C\u4E5F\u4F1A\u63BA\u6742\u4E00\u4E9B\u5C0F\u5C0F\u7684\u7126\u8651\u548C\u8270\u8F9B\uFF0C\u53EF\u80FD\u5728\u8FD9\u671F\u95F4\uFF0C\u6700\u6015\u7684\u5C31\u662F\u8EAB\u4F53\u51FA\u73B0\u4E00\u4E9B\u5C0F\u72B6\u51B5\u3002\u4E0D\u8FC7\u5988\u5988\u4EEC\u4E0D\u8981\u8FC7\u4E8E\u7126\u8651\uFF0C\u751F\u75C5\u4E86\u53EF\u4EE5\u7528\u836F\u65F6\u5E94\u8D81\u65E9\u7528\u836F\uFF0C\u8BA9\u81EA\u5DF1\u65E9\u70B9\u6062\u590D\uFF0C\u8FD9\u6837\u624D\u80FD\u66F4\u597D\u5730\u966A\u4F34\u548C\u7167\u987E\u5B9D\u5B9D\u3002</p><p>\u4E0B\u9762\u4E3A\u5927\u5BB6\u5217\u4E3E\u51E0\u79CD\u5E38\u89C1\u75BE\u75C5\u7684\u6CBB\u7597\u836F\u7269\uFF0C\u8FD9\u4E9B\u5927\u591A\u90FD\u662F\u975E\u5904\u65B9\u836F\uFF08OTC\uFF09\uFF0C\u5F88\u591A\u5728\u836F\u5E97\u5C31\u80FD\u65B9\u4FBF\u4E70\u5230\u3002\u8FD9\u4E9B\u836F\u7269\u5728\u54FA\u4E73\u671F\u5B89\u5168\u6027\u90FD\u975E\u5E38\u9AD8\uFF0C\u5BF9\u5B9D\u5B9D\u5F71\u54CD\u5F88\u5C0F\uFF0C\u670D\u836F\u671F\u95F4\u53EF\u4EE5\u4E0D\u7528\u6682\u505C\u6BCD\u4E73\u3002</p><p>\u273D\u53D1\u70E7\u3001\u75BC\u75DB</p><p>\u54FA\u4E73\u671F\u51FA\u73B0\u5934\u75BC\u8111\u70ED\u662F\u5F88\u5E38\u89C1\u7684\u4E8B\u60C5\uFF0C\u6700\u76F4\u63A5\u7684\u65B9\u6CD5\u662F\u670D\u7528\u89E3\u70ED\u9547\u75DB\u836F\u6765\u7F13\u89E3\u75C7\u72B6\u3002\u65E0\u8BBA\u662F\u53D1\u70E7\u8FD8\u662F\u5404\u79CD\u75BC\u75DB\u300C\u5934\u75DB\u3001\u54BD\u75DB\u3001\u7259\u75DB\u3001\u808C\u8089\u75DB\u3001\u75DB\u7ECF\u300D\uFF0C\u90FD\u53EF\u4EE5\u4F7F\u7528\u300C\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u300D\u6216\u8005\u300C\u5E03\u6D1B\u82AC\u300D\u7F13\u89E3\u75C7\u72B6\u3002</p><p>\u273D\u54B3\u55FD</p><p>\u611F\u5192\u6216\u5176\u4ED6\u539F\u56E0\u5F15\u8D77\u7684\u54B3\u55FD\u6709\u65F6\u5019\u4E5F\u4F1A\u8BA9\u5988\u5988\u4EEC\u96BE\u4EE5\u5FCD\u53D7\uFF0C\u5982\u679C\u662F\u5E72\u54B3\u5E76\u4E25\u91CD\u5F71\u54CD\u751F\u6D3B\u65F6\uFF0C\u53EF\u4EE5\u9009\u62E9\u4F7F\u7528\u300C\u53F3\u7F8E\u6C99\u82AC\u300D\u3002\u4F46\u75F0\u591A\u8005\u5E94\u907F\u514D\u4F7F\u7528\u6216\u907F\u514D\u4E0E\u795B\u75F0\u836F\u5408\u7528\u53F3\u7F8E\u6C99\u82AC\uFF0C\u4E0D\u7136\u75F0\u54B3\u4E0D\u51FA\u6765\u4F1A\u66F4\u52A0\u96BE\u53D7\uFF1B\u54FA\u4E73\u5988\u5988\u7279\u522B\u8981\u6CE8\u610F\u907F\u514D\u4F7F\u7528\u542B\u6709\u53EF\u5F85\u56E0\u7684\u6B62\u54B3\u836F\u3002</p><p>\u7531\u4E8E\u836F\u54C1\u8BF4\u660E\u4E66\u6709\u4E00\u5B9A\u7684\u4FDD\u5B88\u6027\u548C\u6EDE\u540E\u6027\uFF0C\u53F3\u7F8E\u6C99\u82AC\u548C\u5E03\u6D1B\u82AC\u5728\u8BF4\u660E\u4E66\u88AB\u5217\u4E3A\u54FA\u4E73\u671F\u7981\u7528\uFF1B\u5B9E\u9645\u4E0A\u8FD9\u4E24\u4E2A\u836F\u662F\u975E\u5E38\u5B89\u5168\u7684\uFF0C\u54FA\u4E73\u5B89\u5168\u7B49\u7EA7\u90FD\u662FL1\u7EA7\uFF0C\u4E5F\u662FWTO\u63A8\u8350\u54FA\u4E73\u671F\u53EF\u4EE5\u5B89\u5168\u4F7F\u7528\u7684\u836F\u7269[1]\uFF0C\u5988\u5988\u4EEC\u52FF\u9700\u56F0\u60D1\u3002</p><p>\u273D\u8FC7\u654F</p><p>\u6CBB\u7597\u8FC7\u654F\u7684\u4E3B\u8981\u836F\u7269\u662F\u6297\u7EC4\u80FA\u836F\u3002\u5982\u679C\u4E0D\u5C0F\u5FC3\u5F97\u4E86\u8368\u9EBB\u75B9\u7B49\u8FC7\u654F\u6027\u75BE\u75C5\u53EF\u4EE5\u9009\u7528\u300C\u6C2F\u96F7\u4ED6\u5B9A\u3001\u897F\u66FF\u5229\u55EA\u300D\u7B49\u6CBB\u7597\u3002\u54FA\u4E73\u671F\u5E94\u907F\u514D\u4F7F\u7528\u4E00\u4E9B\u8001\u836F\u5982\u6C2F\u82EF\u90A3\u654F\u3001\u82EF\u6D77\u62C9\u660E\uFF0C\u8FD9\u4E24\u4E2A\u836F\u7269\u6709\u53EF\u80FD\u4F1A\u6263\u6389\u5B9D\u5B9D\u7684\u53E3\u7CAE\uFF0C\u5988\u5988\u5403\u4E86\u8FD8\u7279\u60F3\u7761\u89C9\uFF0C\u5B9D\u5B9D\u80AF\u5B9A\u8BE5\u4E0D\u4E50\u610F\u4E86\u3002</p><p>\u273D\u4FBF\u79D8\u3001\u8179\u6CFB</p><p>\u4FBF\u79D8\u3001\u8179\u6CFB\u7684\u6CBB\u7597\u836F\u7269\u4E3B\u8981\u662F\u5728\u80A0\u9053\u5185\u8D77\u4F5C\u7528\uFF0C\u5F88\u5C11\u88AB\u5438\u6536\u5165\u8840\uFF0C\u56E0\u6B64\u53EF\u4EE5\u5B89\u5168\u4F7F\u7528\u3002\u4E3B\u8981\u836F\u7269\u6709\u300C\u805A\u4E59\u4E8C\u9187\u3001\u4E73\u679C\u7CD6\u3001\u5F00\u585E\u9732\uFF1B\u8499\u8131\u77F3\u300D\uFF0C\u53E6\u4E00\u4E2A\u5E38\u7528\u6CBB\u7597\u8179\u6CFB\u7684\u9EC4\u8FDE\u7D20\uFF0C\u7531\u4E8E\u7F3A\u4E4F\u7814\u7A76\u6570\u636E\uFF0C\u4E0D\u5EFA\u8BAE\u4F7F\u7528\u3002</p><p>\u273D\u6297\u83CC\u836F\u7269\uFF08\u6297\u751F\u7D20\uFF09</p><p>\u54FA\u4E73\u671F\u53D1\u751F\u4E73\u817A\u708E\u3001\u4E2D\u8033\u708E\u3001\u7259\u5468\u708E\u7B49\u611F\u67D3\u6027\u75BE\u75C5\u65F6\uFF0C\u9700\u8981\u7528\u5230\u6297\u751F\u7D20\u3002\u5927\u591A\u6570\u6297\u751F\u7D20\u5728\u54FA\u4E73\u671F\u90FD\u662F\u53EF\u4EE5\u5B89\u5168\u4F7F\u7528\u7684\uFF0C\u4F8B\u5982\u300C\u9752\u9709\u7D20\u7C7B\uFF08xx\u897F\u6797\uFF09\u3001\u5934\u5B62\u7C7B\uFF08\u5934\u5B62xx\uFF09\u3001\u963F\u5947\u9709\u7D20\u300D\u7B49\u7B49\u3002</p><p>\u6297\u751F\u7D20\u5C5E\u4E8E\u5904\u65B9\u836F\uFF0C\u9700\u8981\u7ECF\u8FC7\u533B\u751F\u8BCA\u65AD\u65B9\u53EF\u7528\u836F\uFF0C\u5EFA\u8BAE\u5728\u5C31\u8BCA\u65F6\u544A\u77E5\u533B\u751F\u6B63\u5728\u54FA\u4E73\u7684\u60C5\u51B5\uFF0C\u65B9\u4FBF\u533B\u751F\u5F00\u5177\u5B89\u5168\u7684\u836F\u7269\u3002\u6297\u751F\u7D20\u5C5E\u4E8E\u4E0D\u9700\u8981\u8FDB\u5165\u5B9D\u5B9D\u8840\u6DB2\u5C31\u80FD\u8F7B\u5FAE\u5F71\u54CD\u5B9D\u5B9D\u80A0\u9053\u83CC\u7FA4\u7684\u836F\u7269\uFF0C\u54FA\u4E73\u671F\u4F7F\u7528\u6297\u751F\u7D20\u53EF\u80FD\u5BFC\u81F4\u5B9D\u5B9D\u8F7B\u5FAE\u8179\u6CFB\u6216\u76AE\u75B9\uFF0C\u4F46\u6982\u7387\u5F88\u5C0F\u75C7\u72B6\u4E5F\u4E0D\u4E25\u91CD\uFF0C\u51FA\u73B0\u540E\u53CA\u65F6\u505C\u836F\u5373\u53EF\u3002</p><p>\u273D\u5C40\u90E8\u7528\u836F</p><p>\u548C\u6587\u7AE0\u6807\u9898\u4E0D\u4E00\u6837\u7684\u662F\uFF0C\u5C40\u90E8\u7528\u836F\u53EF\u4E0D\u662F\u5403\u7684\u2026\u2026</p><p>\u5C40\u90E8\u7528\u836F\u8303\u56F4\u5F88\u5E7F\uFF0C\u5305\u62EC\u4F46\u4E0D\u9650\u4E8E\u6EF4\u773C\u6DB2\u3001\u6EF4\u8033\u6DB2\u3001\u55B7\u96FE\u5242\u3001\u8F6F\u818F\u3001\u6D17\u6DB2\u7B49\u3002\u8FD9\u4E9B\u836F\u7269\u4E3B\u8981\u5728\u5C40\u90E8\u53D1\u6325\u4F5C\u7528\uFF0C\u53EA\u6709\u6781\u5C11\u91CF\u836F\u4F1A\u88AB\u5438\u6536\u8FDB\u5165\u8840\u6DB2\uFF0C\u80FD\u8FDB\u5165\u4E73\u6C41\u7684\u66F4\u662F\u5C11\u4E4B\u53C8\u5C11\u3002\u56E0\u6B64\uFF0C\u8FD9\u4E00\u5927\u7C7B\u836F\u7269\u5728\u54FA\u4E73\u671F\u662F\u53EF\u4EE5\u653E\u5FC3\u4F7F\u7528\u7684\uFF0C\u6362\u4E2A\u89D2\u5EA6\u60F3\u60F3\uFF0C\u8FD9\u4E0E\u6577\u9762\u819C\u3001\u8D34\u521B\u53EF\u8D34\u4E0D\u5F71\u54CD\u5988\u5988\u54FA\u4E73\u662F\u4E00\u6837\u7684\u601D\u8DEF\u3002</p><p>\u4F46\u8981\u6CE8\u610F\u7684\u662F\u4E5F\u6709\u4F8B\u5916\uFF0C\u6C2F\u9709\u7D20\u5BF9\u5B9D\u5B9D\u526F\u4F5C\u7528\u5F88\u5927\uFF0C\u67D0\u4E9B\u542B\u6709\u5F3A\u6548\u6FC0\u7D20\u7684\u4E73\u818F\u5242\uFF0C\u54FA\u4E73\u671F\u4E5F\u6700\u597D\u907F\u800C\u8FDC\u4E4B\u3002</p><p><strong>\u6709\u54EA\u4E9B\u5751\u4E0D\u80FD\u8E29\uFF1F</strong></p><p>\u611F\u5192\u836F\u8FD9\u4E2A\u5751\u4E0D\u80FD\u8E29\u3002\u5E38\u89C1\u611F\u5192\u836F\u901A\u5E38\u662F\u75313-5\u79CD\u836F\u7269\u7EC4\u6210\u7684\u590D\u65B9\u5236\u5242\uFF0C\u4F8B\u5982\u767D\u52A0\u9ED1\u3001\u6CF0\u8BFA\u3001\u611F\u5EB7\u7247\u7B49\u3002\u8FD9\u4E9B\u836F\u91CC\u4E0D\u5B89\u5168\u7684\u6210\u5206\u4E3B\u8981\u662F\u4F2A\u9EBB\u9EC4\u78B1\u3001\u6C2F\u82EF\u90A3\u654F\uFF0C\u8FD9\u4E24\u4E2A\u836F\u5B89\u5168\u7CFB\u6570\u4F4E\uFF0C\u8FD8\u6709\u53EF\u80FD\u964D\u4F4E\u4E73\u6C41\u4EA7\u91CF\u3002</p><p>\u4E2D\u6210\u836F\u8FD9\u4E2A\u5751\u4E0D\u80FD\u8E29\u3002\u9996\u5148\u4E2D\u6210\u836F\u5BF9\u75BE\u75C5\u7684\u7597\u6548\u4E0D\u786E\u5207\uFF1B\u5176\u6B21\uFF0C\u4E2D\u6210\u836F\u57FA\u672C\u6CA1\u6709\u54FA\u4E73\u671F\u7528\u836F\u7684\u7814\u7A76\u6570\u636E\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5BF9\u5B9D\u5B9D\u7684\u5F71\u54CD\u5F88\u4E0D\u660E\u786E\u3002</p><p><strong>\u50AC\u4E73\u836F\u80FD\u4E0D\u80FD\u7528\uFF1F</strong></p><p>\u5E02\u9762\u4E0A\u6709\u4E00\u4E9B\u50AC\u4E73\u7684\u4E2D\u6210\u836F\u6216\u8005\u4FDD\u5065\u54C1\uFF0C\u7B14\u8005\u5BF9\u8FD9\u4E9B\u4EA7\u54C1\u7684\u6709\u6548\u6027\u548C\u5B89\u5168\u6027\u662F\u5B58\u7591\u7684\uFF0C\u5982\u4E0A\u8FF0\u63D0\u5230\u7684\u7406\u7531\u4E00\u6837\uFF0C\u4E0D\u5EFA\u8BAE\u54FA\u4E73\u671F\u7684\u5988\u5988\u4EEC\u4F7F\u7528\u3002</p><p>\u5F53\u7136\uFF0C\u67D0\u4E9B\u897F\u836F\u4E5F\u6709\u50AC\u4E73\u4F5C\u7528\uFF0C\u5E38\u89C1\u7684\u662F\u80C3\u52A8\u529B\u836F\u7532\u6C27\u6C2F\u666E\u80FA\u548C\u591A\u6F58\u7ACB\u916E\u3002\u7136\u800C\u50AC\u4E73\u4F5C\u7528\u53EA\u662F\u8FD9\u4E24\u4E2A\u836F\u7684\u300C\u526F\u4F5C\u7528\u300D\uFF0C\u4EBA\u5BB6\u7684\u4E3B\u4E1A\u662F\u6CBB\u80C3\u75C5\uFF0C\u50AC\u4E73\u5E76\u4E0D\u662F\u6709\u6C42\u5FC5\u5E94\uFF1B\u518D\u8003\u8651\u5230\u836F\u7269\u7684\u5B89\u5168\u95EE\u9898\uFF0C\u4E0D\u5EFA\u8BAE\u54FA\u4E73\u5988\u5988\u7528\u6B64\u7C7B\u836F\u7269\u50AC\u4E73\u3002</p><p>\u6700\u540E\u60F3\u8BF4\u7684\u662F\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u4E0D\u63A8\u8350\u7528\u836F\u7269\u50AC\u4E73\uFF0C\u65E0\u8BBA\u4E2D\u836F\u6216\u897F\u836F\uFF1B\u800C\u662F\u5EFA\u8BAE\u5988\u5988\u4EEC\u91C7\u7528\u975E\u836F\u7269\u7684\u50AC\u4E73\u65B9\u5F0F\u3002\u5982\u679C\u4E00\u5B9A\u8981\u7528\u836F\u7269\u50AC\u4E73\u7684\u8BDD\uFF0C\u8BF7\u542C\u53D6\u533B\u751F\u6216\u836F\u5E08\u7684\u5EFA\u8BAE\u3002</p><p><strong>\u5C3C\u53E4\u4E01\u3001\u9152\u7CBE\u3001\u5496\u5561/\u53EF\u4E50/\u8336\u2026</strong></p><p>\u663E\u7136\uFF0C\u54FA\u4E73\u671F\u62BD\u70DF\u3001\u559D\u9152\u90FD\u662F\u4E0D\u5408\u9002\u7684\u3002</p><p>\u5F88\u591A\u5988\u5988\u6000\u5B55\u540E\u5C31\u5FCD\u75DB\u5272\u7231\uFF0C\u5C06\u6700\u7231\u7684\u5496\u5561\u675F\u4E4B\u9AD8\u9601\uFF0C\u59D4\u5C48\u81EA\u5DF1\u6210\u5168\u5B9D\u5B9D\u3002</p><p>\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A[2]\u548C\u82F1\u56FDNHS[3]\u8BA4\u4E3A\u54FA\u4E73\u671F\u5988\u5988\u6BCF\u5929\u6444\u5165\u4E0D\u8D85\u8FC7300mg\u5496\u5561\u56E0\u662F\u5F88\u5B89\u5168\u7684\uFF0C\u800C\u7F8E\u56FD\u5987\u4EA7\u79D1\u5B66\u9662[4]\u8BA4\u4E3A\u6BCF\u5929\u6444\u5165\u4F4E\u4E8E200mg\u5496\u5561\u56E0\u7684\u8BDD\u4E0D\u4F1A\u5F71\u54CD\u5230\u5B9D\u5B9D\u3002200mg\u5496\u5561\u56E0\u5927\u6982\u76F8\u5F53\u4E8E1.3\u4EFD\u661F\u5DF4\u514B\u5927\u676F\u62FF\u94C1\u30012.6\u4EFD\u610F\u5F0F\u6D53\u7F29\u5496\u5561\u30016\u74F6\u53EF\u53E3\u53EF\u4E50\u30018\u676F240ml\u7684\u7EFF\u8336\u6240\u542B\u5496\u5561\u56E0\u7684\u91CF\u3002\uFF08\u6570\u636E\u6E90\u4E8Ecaffeineinformer[5]\uFF09</p><p>\u542B200mg\u5496\u5561\u56E0\u7684\u5E38\u89C1\u996E\u54C1\u91CF</p><p>\u65E0\u8BBA\u6743\u5A01\u673A\u6784\u63A8\u8350\u5496\u5561\u56E0\u6BCF\u5929\u4E0A\u9650\u5242\u91CF\u5982\u4F55\uFF0C\u81F3\u5C11\u5496\u5561\u4E0D\u662F\u54FA\u4E73\u671F\u7684\u7981\u5FCC\uFF0C\u5988\u5988\u4EEC\u53EF\u4EE5\u6839\u636E\u81EA\u8EAB\u60C5\u51B5\u8C28\u614E\u5730\u4F7F\u7528\u542B\u5496\u5561\u56E0\u4EA7\u54C1\u3002\u4F46\u662F\u65E9\u4EA7\u513F\u6216\u4F4E\u4F53\u91CD\u513F\u7684\u5988\u5988\u8FD8\u662F\u6700\u597D\u907F\u514D\u559D\u5496\u5561\uFF1B\u53E6\u5916\u5982\u679C\u5B9D\u5B9D\u5728\u54FA\u4E73\u540E\u51FA\u73B0\u5F02\u5E38\u5174\u594B\u7684\u73B0\u8C61\uFF0C\u63D0\u793A\u5B9D\u5B9D\u53EF\u80FD\u5BF9\u5496\u5561\u56E0\u654F\u611F\uFF0C\u5988\u5988\u6700\u597D\u8FD8\u662F\u5148\u5FCD\u75DB\u5272\u7231\u3002</p><p><strong>\u54FA\u4E73\u671F\u53EF\u4E0D\u53EF\u4EE5\u6253\u75AB\u82D7\uFF1F</strong></p><p>\u9664\u4E86\u5C11\u6570\u7279\u6B8A\u75AB\u82D7\uFF08\u9EC4\u70ED\u75C5\u75AB\u82D7\u548C\u5929\u82B1\u75AB\u82D7\uFF09\u4E4B\u5916\uFF0C\u54FA\u4E73\u671F\u5988\u5988\u63A5\u79CD\u75AB\u82D7\u662F\u975E\u5E38\u5B89\u5168\u7684\uFF0C\u5305\u62EC\u767E\u767D\u7834\u75AB\u82D7\u3001\u6D41\u611F\u75AB\u82D7\u3001\u4E59\u809D\u75AB\u82D7\u4EE5\u53CA\u4EBA\u7C7B\u4E73\u5934\u7624\u75C5\u6BD2\u75AB\u82D7\u7B49\u7B49\u3002</p><p>\u5982\u679C\u5B55\u671F\u672A\u63A5\u79CD\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A[6]\u548CCDC[7]\u5EFA\u8BAE\u4EA7\u540E\u5988\u5988\u5E94\u8BE5\u63A5\u79CD\u6D41\u611F\u75AB\u82D7\uFF0C\u53EF\u4EE5\u540C\u65F6\u4FDD\u62A4\u5988\u5988\u548C\u5B9D\u5B9D\uFF0C\u907F\u514D\u5C06\u6D41\u611F\u75C5\u6BD2\u4F20\u67D3\u7ED9\u5B9D\u5B9D\uFF0C\u964D\u4F4E\u5B9D\u5B9D\u60A3\u6D41\u611F\u7684\u98CE\u9669\u3002</p><p><strong>\u600E\u6837\u67E5\u9605\u54FA\u4E73\u671F\u5B89\u5168\u836F\u7269\u7684\u76F8\u5173\u8D44\u6599\uFF1F</strong></p><p>\u7ECF\u8FC7\u5341\u6708\u6000\u80CE\uFF0C\u60F3\u5FC5\u5988\u5988\u4EEC\u90FD\u7EC3\u5C31\u4E86\u67E5\u627E\u9760\u8C31\u80B2\u513F\u77E5\u8BC6\u7684\u672C\u9886\uFF0C\u7528\u836F\u524D\u9664\u4E86\u4ED4\u7EC6\u9605\u8BFB\u836F\u54C1\u8BF4\u660E\u4E66\uFF0C\u8FD8\u53EF\u4EE5\u81EA\u884C\u67E5\u9605\u66F4\u591A\u4E13\u4E1A\u77E5\u8BC6\u3002</p><p>\u6709\u4E00\u672C\u53EB\u505A\u300A\u836F\u7269\u4E0E\u6BCD\u4E73\u5582\u517B\u300B\u7684\u8457\u4F5C[8]\uFF0C\u6536\u5F55\u4E86\u5F88\u591A\u836F\u7269\u5728\u54FA\u4E73\u671F\u7684\u5B89\u5168\u4FE1\u606F\uFF08\u56FE3\uFF09\u3002\u4E66\u4E2D\u5C06\u5404\u4E2A\u836F\u7269\u7684\u54FA\u4E73\u5B89\u5168\u7B49\u7EA7\u5212\u5206\u4E3AL1-L5 \u4E94\u4E2A\u7EA7\u522B\uFF0C\u4F5C\u8005Hale\u6559\u6388\u8BA4\u4E3AL1\u548CL2\u7EA7\u7684\u836F\u7269\u662F\u53EF\u4EE5\u5B89\u5168\u4F7F\u7528\uFF0C\u4E0D\u9700\u8981\u6682\u505C\u54FA\u4E73\u3002</p><p>\u80FD\u63A5\u53D7\u82F1\u8BED\u4FE1\u606F\u7684\u5988\u5988\u8FD8\u53EF\u4EE5\u67E5\u8BE2\u7F8E\u56FD\u56FD\u5BB6\u533B\u5B66\u56FE\u4E66\u7BA1\u7684\u6570\u636E\u5E93\u2014Lactmed\uFF0C\u8FD9\u4E2A\u6570\u636E\u5E93\u63D0\u4F9B\u4E86\u7F51\u9875\u7248[9]\u548C\u624B\u673AAPP\u4E24\u79CD\u67E5\u8BE2\u65B9\u5F0F\uFF0C\u4E0D\u8FC7\u5185\u5BB9\u76F8\u5BF9\u8F83\u4E3A\u4E13\u4E1A\uFF0C\u6709\u5174\u8DA3\u7684\u5988\u5988\u4EEC\u4E5F\u53EF\u4EE5\u5C1D\u8BD5\u67E5\u8BE2\u770B\u3002</p><p>\u4E24\u79CD\u65B9\u5F0F\u67E5\u8BE2\u5E03\u6D1B\u82AC\u7684\u54FA\u4E73\u5B89\u5168\u4FE1\u606F</p><p>\u6700\u540E\uFF0C\u8BF4\u4E2A\u7279\u522B\u91CD\u8981\u7684\u95EE\u9898\u3002\u5982\u679C\u65E9\u4EA7\u6216\u8005\u662F\u4F53\u91CD\u4F4E\u4E8E\u6B63\u5E38\u7684\u5B9D\u5B9D\uFF0C\u90A3\u4E48\u4E73\u6C41\u91CC\u7684\u5FAE\u91CF\u836F\u7269\u5F88\u5BB9\u6613\u5728\u5B9D\u5B9D\u4F53\u5185\u84C4\u79EF\uFF0C\u8FD9\u79CD\u60C5\u51B5\uFF0C\u54FA\u4E73\u671F\u7528\u836F\u95EE\u9898\u5E94\u54A8\u8BE2\u4E13\u4E1A\u4EBA\u58EB\u7684\u5EFA\u8BAE\u3002\uFF08\u9898\u56FE\u6765\u81EAPixabay\uFF09</p><hr><p>\u4F5C\u8005\u9B4F\u51EF\u662F\u89E3\u653E\u519B\u6B66\u6C49\u603B\u533B\u9662\u7684\u836F\u5E08\uFF0C\u5173\u6CE8\u4E0B\u9762\u300C<strong>\u6021\u79BE\u5065\u5EB7</strong>\u300D\u516C\u4F17\u53F7\u540E\uFF0C\u8FDB\u5165\u54A8\u8BE2\u4E3B\u9875\u540E\u5728<strong>\u7528\u836F\u54A8\u8BE2</strong>\u7EC4\u91CC\u53EF\u5411<strong>\u9B4F\u51EF\u836F\u5E08</strong>\u54A8\u8BE2\u3002</p>',49),_={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626571&idx=1&sn=127bd9a6f28f4f4e530a23f22845cb1a&chksm=8b5cb5b1bc2b3ca7be21eaa211827449f8d2626de2da54310666b56e182e2a66451926408cad&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},m=c("\u67E5\u770B\u539F\u6587");function l(f,h){const e=r("ExternalLinkIcon");return t(),n(a,null,[i,p("p",null,[p("a",_,[m,o(e)])])],64)}var u=d(g,[["render",l]]);export{u as default};
