import{r,o as t,c as n,a as e,b as o,F as a,d as s,e as c}from"./app.9aaa2256.js";import{_}from"./plugin-vue_export-helper.21dcd24c.js";const d={},i=s('<h3 id="\u56DF\u95E8\u95EE\u9898-\u770B\u8FD9\u7BC7\u5C31\u591F\u4E86" tabindex="-1"><a class="header-anchor" href="#\u56DF\u95E8\u95EE\u9898-\u770B\u8FD9\u7BC7\u5C31\u591F\u4E86" aria-hidden="true">#</a> \u56DF\u95E8\u95EE\u9898\uFF0C\u770B\u8FD9\u7BC7\u5C31\u591F\u4E86</h3><p>\u5F00\u901A\u4E86\u5206\u7B54\u4E00\u6BB5\u65F6\u95F4\uFF0C\u56DE\u7B54\u4E86\u4E00\u767E\u591A\u4E2A\u95EE\u9898\uFF0C\u6709\u4E2A\u4F53\u4F1A\u5C31\u662F\u5F88\u591A\u95EE\u9898\u5728\u6211\u770B\u6765\u7B97\u4E0D\u4E0A\u95EE\u9898\uFF0C\u4F46\u5BB6\u957F\u5374\u5F88\u5728\u610F\uFF0C\u4EE5\u81F3\u4E8E\u613F\u610F\u82B1\u8FD1\u51E0\u5341\u5757\u94B1\u6765\u95EE\uFF0C\u4E5F\u6709\u4E00\u767E\u591A\u4EBA\u82B1\u94B1\u6765\u542C\uFF0C\u6BD4\u5982\u4E0B\u9762\u8FD9\u4E2A\u95EE\u9898\u3002</p><p>\u613F\u610F\u82B1\u94B1\u6765\u5B66\u7684\u77E5\u8BC6\uFF0C\u8BF4\u660E\u662F\u771F\u9700\u6C42\uFF0C\u6240\u4EE5\u5C31\u5199\u5199\u4E00\u4E9B\u5173\u4E8E\u56DF\u95E8\u7684\u95EE\u9898\u3002</p><p>\u5B69\u5B50\u51FA\u751F\u540E\uFF0C\u5BB6\u957F\u53EF\u80FD\u90FD\u4F1A\u6CE8\u610F\u5230\uFF0C\u5B69\u5B50\u5934\u9876\u4E0A\u6709\u4E24\u5757\u8F6F\u584C\u584C\u7684\u5730\u65B9\uFF0C\u8FD9\u5C31\u662F\u56DF\u95E8\uFF0C\u524D\u56DF\u7684\u5F62\u72B6\u6709\u70B9\u50CF\u94BB\u77F3\uFF0C\u540E\u56DF\u50CF\u4E09\u89D2\u5F62\u3002</p><p>\u51FA\u751F\u65F6\u56DF\u95E8\u6709\u5927\u6709\u5C0F\uFF0C\u968F\u7740\u5E74\u9F84\u7684\u589E\u957F\u4F1A\u9010\u6E10\u7F29\u5C0F\u6700\u540E\u95ED\u5408\u3002\u540E\u56DF\u6BD4\u8F83\u5C0F\uFF0C\u6709\u7684\u5728\u51FA\u751F\u65F6\u90FD\u5DF2\u7ECF\u95ED\u5408\u4E86\uFF0C\u5982\u679C\u6CA1\u95ED\u5408\uFF0C\u901A\u5E38\u4F1A\u57286-8\u5468\u65F6\u95ED\u5408\u3002\u524D\u56DF\u51FA\u751F\u65F6\u5927\u5C0F\u7EA61-3cm\uFF0C\u95ED\u5408\u65F6\u95F4\u4E2A\u4F53\u5DEE\u5F02\u4E5F\u5F88\u5927\uFF0C\u5E73\u5747\u572818\u4E2A\u6708\u65F6\u95ED\u5408\uFF0C\u4F46\u65E9\u7684\u53EF\u4EE5\u57289\u4E2A\u6708\uFF0C\u665A\u7684\u53EF\u4EE5\u52302\u5C81\u3002</p><p><strong>\u56DF\u95E8\u5927\u5C0F\u548C\u95ED\u5408\u95EE\u9898</strong></p><p>\u56DF\u95E8\u4E00\u76F4\u5F88\u5C0F\uFF0C\u6216\u8005\u5F88\u65E9\u5C31\u95ED\u5408\u4E86\uFF0C\u53EF\u80FD\u548C\u9885\u7F1D\u65E9\u95ED\uFF0C\u5934\u5C0F\u7578\u5F62\u7B49\u95EE\u9898\u6709\u5173\u3002\u56DF\u95E8\u6BD4\u8F83\u5927\uFF0C\u6301\u7EED\u4E0D\u7F29\u5C0F\uFF0C\u53EF\u80FD\u548C\u65E9\u4EA7\u3001\u8111\u79EF\u6C34\u3001\u4F5D\u507B\u75C5\u6216\u4E00\u4E9B\u67D3\u8272\u4F53\u5F02\u5E38\u76F8\u5173\u7578\u5F62\u6709\u5173\u3002\u56DF\u95E8\u5927\u6216\u8005\u5C0F\uFF0C\u95ED\u5408\u65E9\u6216\u8005\u665A\uFF0C\u53EF\u4EE5\u662F\u751F\u7406\u6027\u7684\u4E2A\u4F53\u5DEE\u5F02\uFF0C\u4E5F\u53EF\u4EE5\u662F\u4E00\u4E9B\u75BE\u75C5\u7684\u8868\u73B0\uFF0C\u662F\u5426\u6709\u95EE\u9898\uFF0C\u4E0D\u80FD\u5355\u7EAF\u770B\u56DF\u95E8\uFF0C\u8FD8\u8981\u770B\u5176\u5B83\u60C5\u51B5\u7EFC\u5408\u5224\u65AD\uFF0C\u5982\u679C\u62C5\u5FC3\u5C31\u627E\u513F\u79D1\u533B\u751F\u770B\u770B\u3002</p><p><strong>\u56DF\u95E8\u51F9\u51F8\u7684\u95EE\u9898</strong></p><p>\u56DF\u95E8\u901A\u5E38\u662F\u7A0D\u51F9\u7684\uFF0C\u5B69\u5B50\u5267\u70C8\u54ED\u95F9\u3001\u8EBA\u4E0B\u7684\u65F6\u5019\u56DF\u95E8\u4E5F\u4F1A\u53EF\u80FD\u4F1A\u7A0D\u51F8\u8D77\uFF0C\u5982\u679C\u5B89\u9759\u4E0B\u6765\u6216\u8005\u7AD6\u7740\u62B1\u8D77\u6765\u5C31\u6062\u590D\u4E86\u5C31\u6CA1\u4E8B\u3002\u4F46\u5982\u679C\u5B69\u5B50\u88AB\u7AD6\u7740\u62B1\u800C\u4E14\u5B89\u9759\u7684\u65F6\u5019\u56DF\u95E8\u8FD8\u662F\u660E\u663E\u81A8\u8D77\uFF0C\u90A3\u8981\u8B66\u60D5\u8111\u79EF\u6C34\u4E4B\u7C7B\u7684\u95EE\u9898\uFF0C\u9700\u8981\u627E\u533B\u751F\u770B\u770B\u3002\u5B69\u5B50\u62C9\u809A\u5B50\u7684\u65F6\u5019\uFF0C\u56DF\u95E8\u53EF\u80FD\u4F1A\u660E\u663E\u51F9\u9677\uFF0C\u5BF9\u4E8E\u8179\u6CFB\u7684\u5B69\u5B50\uFF0C\u533B\u751F\u4E5F\u4F1A\u89C2\u5BDF\u56DF\u95E8\u6765\u5224\u65AD\u5B69\u5B50\u662F\u4E0D\u662F\u6709\u8131\u6C34\u3002</p><p><strong>\u8981\u4E0D\u8981\u6234\u5E3D\u5B50</strong></p><p>\u56DF\u95E8\u6478\u8D77\u6765\u8F6F\u584C\u584C\uFF0C\u611F\u89C9\u4E0B\u9762\u5C31\u662F\u5927\u8111\uFF0C\u5F88\u591A\u4EBA\u62C5\u5FC3\u4F1A\u51BB\u5230\u5927\u8111\uFF0C\u4E0D\u7BA1\u5B69\u5B50\u62FF\u4E2A\u5B63\u8282\u51FA\u751F\u7684\uFF0C\u54EA\u6015\u662F\u5927\u70ED\u5929\u4E5F\u8981\u7ED9\u5B69\u5B50\u6234\u4E2A\u5E3D\u5B50\u4FDD\u6696\u3002</p><p>\u5176\u5B9E\u5E3D\u5B50\u7684\u7A7F\u6234\u548C\u7A7F\u8863\u3001\u7A7F\u889C\u4E00\u6837\uFF0C\u9075\u7167\u8212\u9002\u7684\u539F\u5219\u5C31\u597D\u3002\u56DF\u95E8\u4E5F\u5C31\u90A3\u4E48\u5927\uFF0C\u5916\u9762\u8FD8\u6709\u5934\u76AE\uFF0C\u5C31\u7B97\u8FD9\u8FD9\u51E0\u5398\u7C73\u7684\u5730\u65B9\u6CA1\u6709\u9AA8\u5934\u7684\u4FDD\u6696\uFF0C\u5927\u8111\u91CC\u7684\u8840\u6D41\u6D41\u52A8\u4E5F\u4F1A\u5E26\u6765\u65B0\u7684\u70ED\u91CF\uFF0C\u4E0D\u4F1A\u56E0\u4E3A\u6CA1\u6709\u76D6\u4F4F\u56DF\u95E8\u5C31\u51BB\u574F\u8111\u5B50\u7684\u3002\u5929\u51B7\u7684\u65F6\u5019\u5927\u4EBA\u6234\u5E3D\u5B50\uFF0C\u5B9D\u5B9D\u81EA\u7136\u4E5F\u53EF\u4EE5\u6234\uFF0C\u4F46\u6CA1\u5FC5\u8981\u4E3A\u4FDD\u62A4\u56DF\u95E8\u505A\u989D\u5916\u7684\u4FDD\u6696\u63AA\u65BD\u3002\u5929\u70ED\u7684\u65F6\u5019\uFF0C\u5927\u4EBA\u81EA\u5DF1\u5439\u7A7A\u8C03\u6247\u6247\u5B50\uFF0C\u7ED9\u5B69\u5B50\u5374\u6234\u4E2A\u5E3D\u5B50\u662F\u4E0D\u4EBA\u9053\u7684\u3002</p><p><strong>\u56DF\u95E8\u6E05\u6D17\u7684\u95EE\u9898</strong></p><p>\u56E0\u4E3A\u56DF\u95E8\u8FD9\u91CC\u6CA1\u6709\u9AA8\u5934\uFF0C\u5F88\u591A\u5BB6\u957F\u78B0\u5230\u8FD9\u91CC\u4F1A\u635F\u4F24\u5927\u8111\uFF0C\u6240\u4EE5\u4E0D\u6562\u78B0\u5B69\u5B50\u56DF\u95E8\uFF0C\u6D17\u5934\u7684\u65F6\u5019\u4E5F\u7279\u610F\u907F\u5F00\u8FD9\u4E2A\u90E8\u4F4D\u3002</p><p>\u76F8\u5BF9\u6709\u9885\u9AA8\u4FDD\u62A4\u7684\u90E8\u4F4D\u6765\u8BF4\uFF0C\u56DF\u95E8\u80AF\u5B9A\u662F\u66F4\u8584\u5F31\uFF0C\u5916\u9762\u662F\u6709\u5934\u76AE\u7684\uFF0C\u5468\u56F4\u4E5F\u6709\u9885\u9AA8\uFF0C\u4E0D\u81F3\u4E8E\u5A07\u5AE9\u5230\u4E0D\u80FD\u89E6\u78B0\u548C\u6E05\u6D17\u7684\u7A0B\u5EA6\uFF0C\u8981\u77E5\u9053\u751F\u5B69\u5B50\u65F6\u4EA7\u9053\u6324\u538B\u7684\u529B\u91CF\u8FDC\u5927\u4E8E\u4F60\u6D17\u5934\u65F6\u5019\u7684\u529B\u91CF\u3002\u629A\u6478\u548C\u6D17\u5934\u662F\u4E0D\u4F1A\u4F24\u5BB3\u5B69\u5B50\u5927\u8111\u7684\uFF0C\u957F\u65F6\u95F4\u4E0D\u6E05\u6D17\u5012\u662F\u53EF\u80FD\u5F15\u53D1\u4E00\u4E9B\u76AE\u80A4\u95EE\u9898\u3002</p><p><strong>\u4E73\u75C2\u7684\u95EE\u9898</strong></p><p>\u6709\u7684\u5BB6\u957F\u53EF\u80FD\u4E5F\u4F1A\u53D1\u73B0\u5B69\u5B50\u5934\u76AE\u4E0A\u6709\u4E9B\u9EC4\u9EC4\u7684\u810F\u4E1C\u897F\uFF0C\u5927\u5BB6\u4E00\u822C\u53EB\u5B83\u201C\u4E73\u75C2\u201D\uFF0C\u533B\u5B66\u4E0A\u53EB\u5A74\u513F\u8102\u6EA2\u6027\u76AE\u708E\u3002\u5F88\u591A\u4EBA\u770B\u5230\u8FD9\u4E9B\u810F\u516E\u516E\u7684\u4E1C\u897F\u4F1A\u8BA4\u4E3A\u548C\u6CA1\u7ED9\u5B69\u5B50\u597D\u597D\u6D17\u5934\u6709\u5173\uFF0C\u4F46\u4E8B\u5B9E\u4E0A\u4E73\u75C2\u53D1\u751F\u7684\u539F\u56E0\u4E0D\u662F\u5F88\u6E05\u695A\uFF0C\u6709\u4EBA\u63A8\u6D4B\u53EF\u80FD\u662F\u56E0\u4E3A\u53D7\u5988\u5988\u8EAB\u4E0A\u5E26\u6765\u7684\u6FC0\u7D20\u7684\u5F71\u54CD\uFF0C\u5BFC\u81F4\u76AE\u8102\u817A\u5206\u6CCC\u65FA\u76DB\u6240\u81F4\u3002</p><p>\u4E73\u75C2\u770B\u8D77\u6765\u96BE\u770B\uFF0C\u4F46\u4E00\u822C\u4E0D\u75DB\u4E0D\u75D2\uFF0C\u53EF\u4EE5\u6B63\u5E38\u6D17\u5934\uFF0C\u5BF9\u5B69\u5B50\u4E5F\u6CA1\u4EC0\u4E48\u5176\u5B83\u5F71\u54CD\uFF0C\u4E00\u822C\u8FC7\u6BB5\u65F6\u95F4\u4F1A\u81EA\u5DF1\u6162\u6162\u6D88\u5931\uFF0C\u6240\u4EE5\u4E0D\u505A\u5904\u7406\u4E5F\u53EF\u4EE5\uFF0C\u5982\u679C\u5ACC\u96BE\u770B\uFF0C\u6D17\u6FA1\u7684\u65F6\u5019\u53EF\u4EE5\u7528\u6E29\u548C\u7684\u6D17\u53D1\u6C34\u548C\u8F6F\u5237\u591A\u6E05\u6D17\u4E00\u4E0B\u3002\u4E5F\u53EF\u4EE5\u62B9\u70B9\u5148\u6D82\u70B9\u77F3\u8721\u6CB9\u518D\u7528\u6D17\u53D1\u6C34\u6E05\u6D17\u3002\u4E0D\u8981\u7528\u6307\u7532\u6216\u5176\u5B83\u786C\u7269\u53BB\u522E\uFF0C\u56E0\u4E3A\u5BB9\u6613\u635F\u4F24\u5934\u76AE\u5F15\u53D1\u611F\u67D3\u3002</p><p>\u4EE5\u4E0A\u662F\u5173\u4E8E\u56DF\u95E8\u548C\u5934\u90E8\u7684\u4E00\u4E9B\u5E38\u89C1\u95EE\u9898\u7684\u89E3\u7B54\uFF0C\u8F6C\u53D1\u5230\u670B\u53CB\u5708\u53EF\u80FD\u53EF\u4EE5\u5E2E\u670B\u53CB\u4EEC\u7701\u94B1\u3002</p><p>\u613F\u610F\u4ED8\u8D39\u7684\u95EE\u9898\u662F\u771F\u7684\u95EE\u9898\uFF0C\u6211\u4EEC\u4E0A\u6B21\u7684\u4ED8\u8D39\u80B2\u513F\u54A8\u8BE2\u5185\u6D4B\u5F88\u591A\u4EBA\u61CA\u607C\u6CA1\u4E70\u4E0A\uFF0C\u73B0\u5728\u6B63\u5F0F\u670D\u52A1\u5FEB\u5F00\u59CB\u4E86\uFF0C\u5982\u679C\u4E0D\u60F3\u9519\u8FC7\uFF0C\u53EF\u4EE5\u957F\u6309\u4E0B\u9762\u4E8C\u7EF4\u7801\u63D0\u524D\u5173\u6CE8\u8FD9\u4E2A\u5E73\u53F0\u3002</p>',20),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626227&idx=1&sn=7b2007473e981255e07afa42ea1ac012&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=c("\u67E5\u770B\u539F\u6587");function m(g,f){const p=r("ExternalLinkIcon");return t(),n(a,null,[i,e("p",null,[e("a",l,[h,o(p)])])],64)}var b=_(d,[["render",m]]);export{b as default};
