import{r as t,o as s,c,a as e,b as p,w as n,F as i,d as l,e as a}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},h=l('<h3 id="\u8B66\u60D5\u6EE5\u7528\u7684\u513F\u7AE5\u704C\u80A0\u6CBB\u7597" tabindex="-1"><a class="header-anchor" href="#\u8B66\u60D5\u6EE5\u7528\u7684\u513F\u7AE5\u704C\u80A0\u6CBB\u7597" aria-hidden="true">#</a> \u8B66\u60D5\u6EE5\u7528\u7684\u513F\u7AE5\u704C\u80A0\u6CBB\u7597</h3><blockquote><p>7\u4E2A\u6708\u7684\u5A74\u513F\uFF0C\u56E0\u4E3A\u54B3\u55FD\u88AB\u5E26\u5230\u9644\u8FD1\u7684\u4E2D\u533B\u95E8\u8BCA\u90E8\u770B\u75C5\uFF0C\u5F00\u4E86\u51E0\u5242\u818F\u836F\u6CA1\u597D\uFF0C\u7136\u540E\u505A\u4E86\u4E24\u6B21\u704C\u80A0\uFF0C\u704C\u80A0\u540E\u5B69\u5B50\u54ED\u7684\u5F88\u5389\u5BB3\uFF0C\u7B2C\u4E8C\u5929\u7A81\u7136\u8EAB\u4EA1\u3002</p></blockquote><p>\u8FD9\u662F\u4ECA\u5929\u5FAE\u535A\u4E0A\u770B\u5230\u7684\u65B0\u95FB\u3002</p><p>\u5728\u8FDB\u4E00\u6B65\u8C03\u67E5\u660E\u786E\u6B7B\u4EA1\u539F\u56E0\u4E4B\u524D\uFF0C\u6211\u4EEC\u65E0\u6CD5\u65AD\u5B9A\u8FD9\u4E2A\u5B69\u5B50\u5C31\u662F\u704C\u80A0\u5BFC\u81F4\u7684\u6B7B\u4EA1\uFF0C\u4F46\u53EF\u4EE5\u786E\u5B9A\u7684\u662F\uFF0C\u704C\u80A0\u5BF9\u8FD9\u4E2A\u5B69\u5B50\u662F\u4E0D\u5FC5\u8981\u7684\uFF0C\u800C\u4E14\u662F\u6709\u5BFC\u81F4\u6B7B\u4EA1\u98CE\u9669\u7684\u3002</p><p>\u5173\u4E8E\u513F\u7AE5\u704C\u80A0\u6CBB\u7597\uFF0C\u6211\u57282015\u5E74\u5199\u8FC7\u4E00\u7BC7\u6587\u7AE0\uFF0C\u5F88\u6709\u5FC5\u8981\u91CD\u6E29\u4E00\u4E0B\uFF0C\u4EE5\u4E0B\u662F\u539F\u6587\uFF0C\u7565\u6709\u5220\u6539\u3002</p><hr><p>\u704C\u80A0\u6CBB\u7597\u5728\u4E00\u4E9B\u5730\u533A\u5F88\u6D41\u884C\uFF0C\u800C\u4E14\u82B1\u6837\u767E\u51FA\uFF0C\u611F\u5192\u3001\u53D1\u70E7\u704C\u80A0\uFF0C\u4FBF\u79D8\u3001\u8179\u6CFB\u4E5F\u704C\u80A0\uFF0C\u704C\u7684\u6709\u76D0\u6C34\u3001\u6FC0\u7D20\uFF0C\u6297\u751F\u7D20\u3001\u6297\u75C5\u6BD2\u836F\uFF0C\u4E5F\u6709\u4E2D\u836F\uFF0C\u751A\u81F3\u4E2D\u836F\u6CE8\u5C04\u6DB2\u3002</p><p>\u5F00\u5C55\u8FD9\u9879\u6CBB\u7597\u7684\u533B\u751F\u8BF4\u6CD5\u4E5F\u4E0D\u4E00\uFF0C\u6709\u7684\u8BF4\u5403\u836F\u4E0D\u89C1\u6548\u5C31\u704C\uFF0C\u6709\u7684\u8BF4\u704C\u80A0\u5B69\u5B50\u4E0D\u53D7\u7F6A\uFF0C\u6548\u679C\u66F4\u597D\uFF0C\u4E5F\u6709\u7684\u8BF4\u66F4\u5B89\u5168\uFF0C\u6709\u7684\u8FD8\u6253\u7740\u201C\u7EFF\u8272\u7597\u6CD5\u201D\u7684\u65D7\u53F7\u3002</p><p>\u704C\u80A0\u80FD\u7528\u6765\u6CBB\u75C5\u5417\uFF1F\u704C\u80A0\u6709\u4EC0\u4E48\u795E\u5947\u4E4B\u5904\uFF1F</p><p>\u6240\u8C13\u704C\u80A0\u6CBB\u7597\uFF0C\u5C31\u662F\u628A\u6DB2\u4F53\u6216\u836F\u7269\u901A\u8FC7\u809B\u95E8\u6CE8\u5165\u80A0\u7BA1\u6765\u8FBE\u5230\u6E05\u6D01\u6216\u6CBB\u7597\u76EE\u7684\uFF0C\u8BF4\u767D\u4E86\u5C31\u662F\u4E00\u79CD\u6E05\u6D01\u80A0\u9053\u6216\u7ED9\u836F\u7684\u65B9\u5F0F\uFF0C\u8FD9\u79CD\u65B9\u6CD5\u5728\u6B63\u89C4\u533B\u9662\u6709\u4F7F\u7528\u5417\uFF1F\u6709\uFF0C\u53EA\u662F\u4E0D\u90A3\u4E48\u5E38\u7528\uFF0C\u5728\u513F\u79D1\u9886\u57DF\uFF0C\u9700\u8981\u704C\u80A0\u7684\u5927\u6982\u6709\u4E09\u7C7B\u60C5\u51B5\u3002</p><p><strong>\u5B69\u5B50\u54EA\u4E9B\u60C5\u51B5\u9700\u8981\u704C\u80A0</strong></p><p>\u7B2C\u4E00\u7C7B\uFF1A\u6E05\u6D01\u80A0\u9053</p><p>\u4E3B\u8981\u89C1\u4E8E\u4E00\u4E9B\u7279\u6B8A\u75C5\u4EBA\uFF0C\u6BD4\u5982\u5DE8\u7ED3\u80A0\u7684\u5B69\u5B50\u56E0\u4E3A\u6392\u4FBF\u56F0\u96BE\u9700\u8981\u751F\u7406\u76D0\u6C34\u704C\u80A0\u6765\u5E2E\u52A9\u6392\u4FBF\u3002\u8FD8\u6709\u4E9B\u60C5\u51B5\u662F\u4E00\u4E9B\u4E25\u91CD\u4FBF\u79D8\uFF0C\u9700\u8981\u5F80\u80A0\u5B50\u91CC\u6CE8\u5165\u76D0\u6C34\u628A\u7CAA\u5757\u3001\u7CAA\u77F3\u51B2\u51FA\u6765\uFF0C\u80A0\u955C\u68C0\u67E5\u524D\u5076\u5C14\u4E5F\u9700\u8981\u8FD9\u6837\u505A\uFF0C\u76EE\u7684\u662F\u6E05\u6D01\u80A0\u7BA1\u4EE5\u4FBF\u68C0\u67E5\uFF0C\u4F46\u73B0\u5728\u4E5F\u57FA\u672C\u9760\u53E3\u670D\u805A\u4E59\u4E8C\u9187\u4E4B\u7C7B\u7684\u836F\u7269\u5C31\u53EF\u4EE5\u89E3\u51B3\u800C\u4E0D\u518D\u9700\u8981\u704C\u80A0\u4E86\u3002</p><p>\u7B2C\u4E8C\u7C7B\uFF1A\u4E00\u4E9B\u68C0\u67E5\u6216\u8005\u7279\u6B8A\u6CBB\u7597</p><p>\u6BD4\u5982\u5DE8\u7ED3\u80A0\u9020\u5F71\u68C0\u67E5\uFF0C\u9700\u8981\u628A\u9020\u5F71\u5242\u704C\u5165\u7ED3\u80A0\u7136\u540E\u663E\u5F71\u7ED3\u80A0\u3002\u7279\u6B8A\u6CBB\u7597\u5C31\u662F\u80A0\u5957\u53E0\u7684\u5B69\u5B50\uFF0C\u9700\u8981\u4ECE\u809B\u95E8\u704C\u5165\u6C14\u4F53\u6216\u8005\u6C34\uFF0C\u9760\u6C14\u4F53\u6216\u8005\u6C34\u7684\u538B\u529B\u628A\u5957\u8FDB\u53BB\u7684\u80A0\u5B50\u51B2\u51FA\u6765\u3002</p><p>\u8FD8\u6709\u5C31\u662F\u4E00\u79CD\u65B0\u751F\u513F\u75BE\u75C5\u53EB\u80CE\u7CAA\u6027\u80A0\u6897\u963B\uFF0C\u56FD\u5916\u4E5F\u6709\u901A\u8FC7\u4ECE\u809B\u95E8\u704C\u5165\u836F\u7269\u6765\u7A00\u91CA\u6EB6\u89E3\u80CE\u4FBF\u6765\u89E3\u9664\u6897\u963B\u7684\uFF0C\u4F46\u8FD9\u4E2A\u75C5\u56FD\u5185\u975E\u5E38\u5C11\u89C1\uFF0C\u6240\u4EE5\u4E5F\u5F88\u5C11\u6709\u5E94\u7528\u3002</p><p>\u7B2C\u4E09\u7C7B\uFF1A\u76F4\u80A0\u7ED9\u836F</p><p>\u6700\u5E38\u89C1\u7684\u5C31\u662F\u5F00\u585E\u9732\uFF0C\u628A\u7518\u6CB9\u4E4B\u7C7B\u7684\u836F\u7269\u6324\u5165\u80A0\u9053\u523A\u6FC0\u76F4\u80A0\u5F15\u8D77\u6392\u4FBF\uFF0C\u8FD8\u6709\u5927\u5BB6\u90FD\u77E5\u9053\u7684\u5BF9\u9000\u70ED\u6813\u9000\u70ED\u4E86\uFF0C\u5728\u5B69\u5B50\u4E0D\u80FD\u53E3\u670D\u6216\u8005\u4E0D\u613F\u53E3\u670D\u7684\u65F6\u5019\u624D\u4F1A\u7528\u3002</p><p><strong>\u704C\u80A0\u7ED9\u836F\u4E3A\u4EC0\u4E48\u4E0D\u5E38\u7528</strong></p><p>\u6211\u4EEC\u77E5\u9053\u6700\u5E38\u7528\u7684\u7ED9\u836F\u65B9\u5F0F\u6709\u53E3\u670D\u548C\u6CE8\u5C04\u7ED9\u836F\u4E24\u79CD\uFF0C\u76F8\u6BD4\u4E4B\u4E0B\u4E3A\u4EC0\u4E48\u704C\u80A0\u7ED9\u836F\u4E0D\u90A3\u4E48\u5E38\u7528\u5462\uFF1F</p><p>\u9996\u5148\u53E3\u670D\u66F4\u65B9\u4FBF\u3001\u5B89\u5168\uFF0C\u800C\u4E14\u4E0D\u9700\u8981\u989D\u5916\u7684\u8D39\u7528\uFF0C\u800C\u4E14\u901A\u5E38\u53E3\u670D\u5438\u6536\u7684\u901F\u5EA6\u548C\u7A0B\u5EA6\u90FD\u8981\u9AD8\u4E8E\u76F4\u80A0\u7ED9\u836F [1] \u3002\u6CE8\u5C04\u7ED9\u836F\u66F4\u5FEB\u6377\uFF0C\u800C\u4E14\u836F\u7269\u4E0D\u4F1A\u88AB\u80C3\u80A0\u9053\u6240\u6D88\u5316\u7834\u574F\uFF0C\u7528\u836F\u91CF\u53EF\u4EE5\u5F88\u7CBE\u51C6\uFF0C\u4F46\u56E0\u4E3A\u6709\u8F93\u6DB2\u53CD\u5E94\u7B49\u98CE\u9669\uFF0C\u6240\u4EE5\u4E3B\u8981\u7528\u4E8E\u6025\u91CD\u75C7\u75C5\u4EBA\u6216\u4E00\u4E9B\u4E0D\u80FD\u53E3\u670D\u7684\u60C5\u51B5\u3002</p><p>\u76F4\u80A0\u7ED9\u836F\u64CD\u4F5C\u9EBB\u70E6\uFF0C\u836F\u7269\u5BB9\u6613\u5916\u6CC4\uFF0C\u5438\u6536\u9762\u79EF\u5C0F\uFF0C\u5438\u6536\u4E0D\u89C4\u5219\uFF0C\u4E0D\u6613\u63A7\u5236\u7528\u91CF\u7B49\u95EE\u9898\uFF0C\u6709\u4E9B\u836F\u7269\u523A\u6FC0\u8FD8\u53EF\u80FD\u9020\u6210\u5C40\u90E8\u6E83\u75A1\uFF0C\u5982\u679C\u662F\u63D2\u7BA1\u704C\u80A0\u8FD8\u53EF\u80FD\u5F15\u8D77\u80A0\u9053\u635F\u4F24\uFF0C\u5BFC\u81F4\u51FA\u73B0\u751A\u81F3\u7A7F\u5B54\u7B49\u98CE\u9669\u3002\u800C\u4E14\u4E0D\u662F\u836F\u7269\u90FD\u53EF\u4EE5\u76F4\u80A0\u7ED9\u836F\uFF0C\u9664\u975E\u662F\u4E13\u95E8\u8BBE\u8BA1\u4E3A\u76F4\u80A0\u7ED9\u836F\u7684\u6813\u5242\uFF0C\u6216\u8005\u836F\u54C1\u8BF4\u660E\u4E66\u91CC\u6CE8\u660E\u53EF\u4EE5\u704C\u80A0\u7ED9\u836F\u624D\u53EF\u4EE5\u7528\uFF0C\u6240\u4EE5\u5728\u6B63\u89C4\u533B\u9662\u91CC\u5E94\u7528\u5E76\u4E0D\u591A\u3002</p><p>\u90A3\u76F4\u80A0\u7ED9\u836F\uFF0C\u662F\u5426\u5B58\u5728\u4E00\u4E9B\u7279\u522B\u4E4B\u5904\u5462\uFF1F\u6BD4\u5982\u50CF\u4E00\u4E9B\u533B\u751F\u5BA3\u79F0\u7684\u201C\u6CA1\u6709\u7ECF\u8FC7\u6D88\u5316\u9053\u548C\u809D\u810F\uFF0C\u6BD4\u53E3\u670D\u836F\u7269\u6548\u679C\u8981\u5FEB\uFF0C\u4E0E\u9000\u70E7\u7684\u897F\u836F\u76F8\u6BD4\uFF0C\u526F\u4F5C\u7528\u5C11\u201D\uFF1F</p><p>\u836F\u7269\u88AB\u585E\u5165\uFF08\u6216\u704C\u5165\uFF09\u76F4\u80A0\u540E\uFF0C\u88AB\u76F4\u80A0\u4E2D\u4E0B\u6BB5\u7C98 \u819C\u5438\u6536\u7684\u836F\u7269\uFF0C\u901A\u8FC7\u76F4\u80A0\u4E2D\u3001\u4E0B\u9759\u8109\u8FDB\u5165\u9AC2\u5185\u9759\u8109\u518D\u8FDB\u5165\u4E0B\u8154\u9759\u8109\uFF0C\u5728\u836F\u7269\u5438\u6536\u8FC7\u7A0B\u4E2D\u786E\u5B9E\u6CA1\u6709\u7ECF\u8FC7\u809D\u810F\u7684\u7B2C\u4E00\u6B21\u4EE3\u8C22\u800C\u76F4\u63A5\u8FDB\u5165\u4E86\u4F53\u5FAA\u73AF\uFF0C\u4F46\u8FD9\u4EC5\u5C40\u9650\u4E8E\u76F4\u80A0\u4E2D\u4E0B\u90E8\u90A3\u77ED\u77ED\u7684\u51E0\u5398\u7C73\uFF0C\u5982\u679C\u662F\u63D2\u7BA1\u704C\u5165\u7684\u836F\u7269\u5438\u6536\u5230\u80A0\u7CFB\u819C\u9759\u8109\u4E86\uFF0C\u90A3\u4E00\u6837\u8981\u7ECF\u8FC7\u95E8\u9759\u8109\u7CFB\u7EDF\u8FDB\u5165\u809D\u5FAA\u73AF\uFF0C\u548C\u53E3\u670D\u5438\u6536\u7684\u65B9\u5F0F\u6CA1\u4EC0\u4E48\u533A\u522B\u3002</p><p>\u800C\u4E14\u5C31\u7B97\u5728\u76F4\u80A0\u4E2D\u4E0B\u9759\u8109\u5438\u6536\u8FDB\u5165\u4F53\u5FAA\u73AF\uFF0C\u907F\u514D\u4E86\u5438\u6536\u540E\u5728\u809D\u810F\u7684\u4E00\u6B21\u4EE3\u8C22\u6392\u6CC4\uFF0C\u4F46\u5927\u90E8\u5206\u836F\u7269\u4EE3\u8C22\u662F\u7ECF\u8FC7\u5F88\u65E0\u6570\u6B21\u8840\u6DB2\u5FAA\u73AF\u9010\u6E10\u4EE3\u8C22\u6D88\u9664\u7684\u3002\u5438\u6536\u540E\u7B2C\u4E00\u6B21\u8FDB\u5165\u809D\u810F\u5C31\u88AB\u5927\u91CF\u4EE3\u8C22\u6389\uFF0C\u5B58\u5728\u8FD9\u79CD\u201C\u9996\u5173\u6D88\u9664\u201D\u73B0\u8C61\u7684\u53EA\u6709\u785D\u9178\u7518\u6CB9\u3001\u666E\u8418\u6D1B\u5C14\u3001\u5229\u591A\u5361\u56E0\u7B49\u5C11\u6570\u836F\u7269\uFF0C\u5728\u513F\u79D1\u91CC\u5B9E\u5728\u60F3\u4E0D\u51FA\u90A3\u79CD\u75BE\u75C5\u8FD8\u5FC5\u987B\u901A\u8FC7\u76F4\u80A0\u7ED9\u836F\u8FD9\u79CD\u65B9\u5F0F\u6765\u63D0\u9AD8\u836F\u7269\u6765\u907F\u514D\u9996\u5173\u6D88\u9664\u3002</p><p><strong>\u9000\u70ED\u6813\u4E3A\u4EC0\u4E48\u4E0D\u5E38\u7528</strong></p><p>\u770B\u8FC7\u6211\u4EE5\u524D\u5199\u8FC7\u7684\u5173\u4E8E\u53D1\u70E7\u7684\u6587\u7AE0\u5E94\u8BE5\u77E5\u9053\uFF0C\u76EE\u524D\u5B89\u5168\u6709\u6548\u7684\u9000\u70E7\u836F\u5C31\u662F\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u548C\u5E03\u6D1B\u82AC\uFF0C\u8FD9\u4E24\u79CD\u836F\u5E38\u89C1\u7684\u662F\u53E3\u670D\u5242\u578B\uFF0C\u6813\u5242\u4E5F\u6709\u4F46\u6BD4\u8F83\u5C11\u3002</p><p>\u5982\u679C\u704C\u80A0\u7528\u836F\u8FD9\u4E48\u795E\u5947\uFF0C\u4E3A\u4EC0\u4E48\u5382\u5BB6\u4E0D\u591A\u751F\u4EA7\u4E00\u70B9\u6813\u5242\u5462\uFF0C\u96BE\u9053\u6709\u94B1\u4E0D\u60F3\u8D5A\uFF1F\u4E8B\u5B9E\u4E0A\u836F\u5382\u4E00\u70B9\u4E5F\u4E0D\u7B28\uFF0C\u56E0\u4E3A\u8FD9\u4E24\u79CD\u836F\u548C\u5927\u90E8\u5206\u836F\u7269\u4E00\u6837\uFF0C\u76F4\u80A0\u7ED9\u836F\u6CA1\u6709\u4EC0\u4E48\u7279\u522B\u4F18\u52BF\uFF0C\u4F7F\u7528\u539F\u5219\u4E5F\u662F\u80FD\u53E3\u670D\u5C31\u53E3\u670D\u3002</p><p>\u6B63\u5982\u524D\u9762\u8BB2\u8FC7\u7684\uFF0C\u9996\u5148\u53E3\u670D\u65B9\u4FBF\uFF0C\u64CD\u4F5C\u7B80\u5355\uFF0C\u5382\u5BB6\u901A\u8FC7\u52A0\u5165\u8C03\u5473\u5242\u5C06\u836F\u54C1\u8C03\u6210\u5B69\u5B50\u559C\u6B22\u7684\u53E3\u5473\uFF0C\u5B69\u5B50\u4E0D\u9700\u8981\u627F\u53D7\u4EC0\u4E48\u75DB\u82E6\u5C31\u53EF\u4EE5\u5B8C\u6210\u670D\u836F\u3002\u5E03\u6D1B\u82AC\u6709\u4E9B\u5B69\u5B50\u4F1A\u6709\u80C3\u80A0\u9053\u7684\u4E0D\u9002\uFF0C\u4F46\u4E00\u822C\u6BD4\u8F83\u8F7B\u5FAE\uFF0C\u800C\u4E14\u53E3\u670D\u8FD8\u53EF\u4EE5\u6839\u636E\u5B69\u5B50\u7684\u4F53\u91CD\u7CBE\u786E\u5438\u53D6\u7528\u836F\u91CF\u3002</p><p>\u800C\u4E14\u53E3\u670D\u7ED9\u836F\u7684\u9000\u70ED\u6548\u679C\u4E00\u70B9\u4E5F\u4E0D\u8F93\u4E8E\u76F4\u80A0\u7ED9\u836F\u3002\u6709\u968F\u673A\u5BF9\u7167\u7814\u7A76\u8868\u660E\uFF0C\u540C\u6837\u5242\u91CF\u7684\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\uFF0C\u53E3\u670D\u548C\u76F4\u80A0\u7ED9\u836F\u7684\u9000\u70ED\u6548\u679C\u4E00\u6837\uFF0C\u800C\u4E14\u548C\u53CC\u500D\u53CA\u4EE5\u4E0A\u5242\u91CF\u76F4\u80A0\u7ED9\u836F\u6548\u679C\u4E5F\u4E00\u6837 [2\uFF0C3] \u3002\u65E2\u7136\u6548\u679C\u4E00\u6837\uFF0C\u53E3\u670D\u65B9\u4FBF\u63A5\u53D7\u5EA6\u597D\uFF0C\u5F53\u7136\u4F18\u5148\u9009\u62E9\u53E3\u670D\u4E86\u3002</p><p>\u800C\u4E14\u6813\u5242\u5242\u91CF\u51FA\u5382\u65F6\u5019\u90FD\u662F\u56FA\u5B9A\u7684\uFF0C\u4E0D\u597D\u6839\u636E\u5B69\u5B50\u4F53\u91CD\u51C6\u786E\u8C03\u6574\u7528\u836F\u91CF\uFF0C\u8FD9\u4E5F\u662F\u9000\u70ED\u6813\u4E0D\u5E38\u7528\u7684\u539F\u56E0\u3002\u800C\u4E14\u5E03\u6D1B\u82AC\u76F4\u80A0\u7ED9\u836F\u7684\u751F\u7269\u5229\u7528\u5EA6\u53EA\u6709\u53E3\u670D\u7ED9\u836F\u768463% [4] \uFF0C\u6240\u4EE5\u5E02\u9762\u4E0A\u5E03\u6D1B\u82AC\u6813\u5242\u5C31\u66F4\u5C11\u4E86\u3002</p><p><strong>\u6709\u54EA\u4E9B\u4E0D\u5408\u7406\u7684\u704C\u80A0\u7ED9\u836F\uFF1F</strong></p><p>\u6297\u83CC\u7D20\u80FD\u591F\u5C40\u90E8\u4F7F\u7528\u7684\u60C5\u51B5\u5F88\u5C11\uFF0C\u56E0\u4E3A\u5438\u6536\u6709\u9650\u800C\u4E14\u5BB9\u6613\u4EA7\u751F\u8FC7\u654F\u53CA\u8010\u836F\uFF0C\u6211\u56FD\u7684\u300A\u6297\u83CC\u836F\u7269\u4E34\u5E8A\u4F7F\u7528\u6307\u5BFC\u539F\u5219\u300B\u91CC\uFF0C\u6839\u672C\u4E5F\u4E0D\u5B58\u5728\u7ECF\u809B\u95E8\u704C\u5165\u8FD9\u79CD\u7ED9\u836F\u65B9\u5F0F\uFF0C\u4E5F\u6CA1\u6709\u54EA\u79CD\u6297\u83CC\u7D20\u7684\u8BF4\u660E\u4E66\u91CC\u5199\u4E86\u53EF\u4EE5\u704C\u80A0\u7ED9\u836F\uFF0C\u628A\u53E3\u670D\u836F\u548C\u6CE8\u5C04\u9488\u5242\u6297\u83CC\u7D20\u62FF\u6765\u704C\u80A0\u65E0\u4E00\u4E0D\u662F\u8FDD\u89C4\u7528\u836F\u3002</p><p>\u6297\u75C5\u6BD2\u836F\u4E5F\u4E00\u6837\uFF0C\u800C\u4E14\u9664\u4E86\u6D41\u611F\u5916\uFF0C\u513F\u7AE5\u9700\u8981\u4F7F\u7528\u6297\u75C5\u6BD2\u836F\u7684\u60C5\u51B5\u6781\u5C11\uFF0C\u5229\u5DF4\u97E6\u6797\u4E4B\u7C7B\u7684\u6297\u75C5\u6BD2\u836F\uFF0C\u4E0D\u8BBA\u53E3\u670D\u8FD8\u662F\u96FE\u5316\u57FA\u672C\u4E5F\u90FD\u662F\u6EE5\u7528\uFF0C\u704C\u80A0\u5219\u5168\u90E8\u662F\u6EE5\u7528\u3002</p><p>\u6FC0\u7D20\u704C\u80A0\uFF0C\u7ED9\u836F\u65B9\u5F0F\u5C31\u4E0D\u518D\u5410\u69FD\u4E86\u3002\u7CD6\u76AE\u8D28\u6FC0\u7D20\u672C\u6765\u5C31\u4E0D\u662F\u9000\u70ED\u836F\uFF0C\u4E2D\u56FD2008\u5E74\u7684\u53D1\u70ED\u6307\u5357\u90FD\u660E\u786E\u8BF4\u4E86\uFF1A\u53CD\u5BF9\u4F7F\u7528\u7CD6\u76AE\u8D28\u6FC0\u7D20\u4F5C\u4E3A\u9000\u70ED\u5242\u7528\u4E8E\u513F\u7AE5\u9000\u70ED\u3002</p><p>\u81F3\u4E8E\u672C\u8EAB\u5C31\u6CA1\u6709\u7ECF\u8FC7\u4E25\u683C\u4E34\u5E8A\u8BD5\u9A8C\u9A8C\u8BC1\u8FC7\u5B89\u5168\u6027\u548C\u6709\u6548\u6027\u7684\u4E2D\u836F\uFF0C\u522B\u8BF4\u662F\u704C\u80A0\uFF0C\u5C31\u662F\u6253\u8FDB\u8840\u7BA1\u91CC\u4E5F\u4E0D\u4F1A\u6709\u7528\uFF0C\u800C\u4E14\u81F4\u547D\u6027\u7684\u8FC7\u654F\u53CD\u5E94\u98CE\u9669\u4E5F\u6BD4\u8F83\u9AD8\u3002</p><p><strong>\u4E3A\u4EC0\u4E48\u6709\u7684\u5B69\u5B50\u704C\u80A0\u5C31\u597D\u4E86\u5462\uFF1F</strong></p><p>\u4E00\u704C\u80A0\u5C31\u597D\u4E86\u5F53\u7136\u53EF\u80FD\u4F1A\u6709\uFF0C\u800C\u4E14\u5E94\u8BE5\u4F1A\u4E0D\u5C11\u3002</p><p>\u4E00\u662F\u5B69\u5B50\u672C\u8EAB\u81EA\u6108\u6027\u75BE\u75C5\u5360\u7684\u6BD4\u91CD\u5F88\u5927\uFF0C\u5C24\u5176\u662F\u90A3\u4E9B\u5E38\u89C1\u75C5\u591A\u53D1\u75C5\uFF0C\u4E0D\u7BA1\u704C\u4E0D\u704C\u80A0\uFF0C\u90FD\u6709\u53EF\u80FD\u4F1A\u81EA\u5DF1\u597D\u3002\u53E6\u5916\u704C\u5165\u7684\u4E00\u4E9B\u836F\u7269\u6BD4\u5982\u611F\u5192\u836F\u91CC\u5F88\u591A\u542B\u6709\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\uFF0C\u8FD9\u4E9B\u836F\u88AB\u5438\u6536\u5165\u4E5F\u53EF\u4EE5\u8D77\u5230\u9000\u70E7\u6548\u679C\u3002</p><p>\u6B64\u5916\u4EBA\u4F53\u672C\u8EAB\u6709\u4F53\u6E29\u8C03\u8282\u673A\u5236\uFF0C\u5373\u4FBF\u4E0D\u5403\u836F\uFF0C\u6E29\u5EA6\u9AD8\u5230\u4E00\u5B9A\u7A0B\u5EA6\u7EDD\u5927\u90E8\u5206\u4E5F\u4F1A\u81EA\u5DF1\u8C03\u8282\u4E0B\u6765\uFF0C\u8FD9\u4E5F\u662F\u4E3A\u4EC0\u4E48\u8D85\u9AD8\u70ED\u5F88\u5C11\u89C1\u7684\u539F\u56E0\u3002\u5982\u679C\u7528\u76D0\u6C34\u4E4B\u7C7B\u7684\u6DB2\u4F53\u704C\u80A0\uFF0C\u5E26\u8D70\u90E8\u5206\u70ED\u91CF\u53EF\u4EE5\u5E2E\u52A9\u77ED\u65F6\u95F4\u964D\u6E29\uFF0C\u4F46\u4F1A\u7ED9\u5B69\u5B50\u5E26\u6765\u5F88\u5927\u7684\u4E0D\u9002\uFF0C\u9664\u975E\u662F\u8D85\u9AD8\u70ED\u6216\u4E2D\u6691\u4E5F\u4E0D\u5EFA\u8BAE\u7528\u3002</p><p>\u6240\u4EE5\u704C\u80A0\u7528\u836F\u6CA1\u6709\u4EFB\u4F55\u795E\u5947\u4E4B\u5904\uFF0C\u9664\u4E86\u4FBF\u79D8\u7528\u5F00\u585E\u9732\uFF0C\u53D1\u70ED\u5728\u4E0D\u80FD\u53E3\u670D\u7684\u60C5\u51B5\u4E0B\u9009\u7528\u9000\u70ED\u6813\uFF0C\u5176\u4ED6\u7684\u704C\u80A0\u7ED9\u836F\uFF0C\u65E0\u8BBA\u7528\u7684\u662F\u4EC0\u4E48\u836F\u7269\uFF0C\u90FD\u4E0D\u4F1A\u7ED9\u5B69\u5B50\u5E26\u6765\u989D\u5916\u7684\u6536\u76CA\u3002</p><p><strong>\u6EE5\u7528\u704C\u80A0\u6CBB\u7597\u7684\u5371\u5BB3</strong></p><p>\u585E\u4E2A\u9000\u70ED\u6813\u4E0D\u81F3\u4E8E\u6709\u4EC0\u4E48\u5371\u9669\uFF0C\u4F46\u63D2\u7BA1\u6253\u6DB2\u4F53\u548C\u836F\u7269\u704C\u80A0\u5C31\u53EF\u80FD\u9020\u6210\u7A7F\u5B54\u3001\u51FA\u8840\u7B49\u80A0\u7BA1\u635F\u4F24\uFF0C\u5B69\u5B50\u56E0\u4E3A\u80A0\u58C1\u66F4\u8584\u5F31\uFF0C\u51FA\u73B0\u8FD9\u79CD\u98CE\u9669\u66F4\u5927\u3002</p><p>\u7ED9\u5B69\u5B50\u7528\u4E0D\u8BE5\u7528\u7684\u6FC0\u7D20\u3001\u6297\u751F\u7D20\uFF0C\u75C5\u6BD2\u836F\uFF0C\u4E2D\u6210\u836F\uFF0C\u968F\u610F\u6539\u53D8\u836F\u7269\u4F7F\u7528\u65B9\u5F0F\uFF0C\u53EF\u80FD\u589E\u52A0\u8FC7\u654F\u3001\u8010\u836F\u98CE\u9669\uFF0C\u5C40\u90E8\u836F\u7269\u523A\u6FC0\u4E5F\u53EF\u80FD\u5F15\u53D1\u7ED3\u80A0\u708E\uFF0C\u704C\u80A0\u8FD8\u53EF\u80FD\u6539\u53D8\u80A0\u9053\u6B63\u5E38\u83CC\u7FA4\u73AF\u5883\uFF0C\u8BF1\u53D1\u80A0\u9053\u611F\u67D3\uFF0C\u957F\u65F6\u95F4\u4FDD\u7559\u5927\u91CF\u6DB2\u4F53\u704C\u80A0\u8FD8\u53EF\u80FD\u5BFC\u81F4\u5B69\u5B50\u6C34\u7535\u89E3\u8D28\u7D0A\u4E71\uFF0C\u751A\u81F3\u6B7B\u4EA1\uFF0C\u6240\u4EE5\u53D1\u751F\u65B0\u95FB\u91CC\u7684\u90A3\u79CD\u4E8B\u4E5F\u4E0D\u5947\u602A\u3002</p><p><strong>\u704C\u80A0\u6CBB\u7597\u6EE5\u7528\u7684\u539F\u56E0</strong></p><p>\u65E2\u7136\u6CA1\u4EC0\u4E48\u7279\u522B\u597D\u5904\uFF0C\u8FD8\u6709\u98CE\u9669\uFF0C\u4E3A\u4EC0\u4E48\u56FD\u5185\u8FD9\u4E48\u591A\u533B\u751F\u8FD8\u7ED9\u5B69\u5B50\u704C\u80A0\uFF1F</p><p>\u6211\u731C\u53EF\u80FD\u662F\u56E0\u4E3A\u5B69\u5B50\u5C0F\uFF0C\u6709\u65F6\u5019\u4E0D\u914D\u5408\u5403\u836F\uFF0C\u5B69\u5B50\u8840\u7BA1\u5C0F\u4E0D\u597D\u6253\u9488\uFF0C\u7126\u8651\u7684\u5BB6\u957F\u518D\u52A0\u4E0A\u4E0D\u8010\u70E6\u7684\u533B\u751F\uFF0C\u65E2\u7136\u5582\u836F\u5F88\u96BE\u5C31\u809B\u95E8\u63D2\u7BA1\u704C\u836F\u5427\uFF0C\u8FD9\u53EF\u80FD\u662F\u704C\u80A0\u6CBB\u7597\u5728\u513F\u79D1\u8FD9\u4E48\u6D41\u884C\u7684\u4E3B\u8981\u539F\u56E0\u3002</p><p>\u53E6\u5916\u53E3\u670D\u836F\u4E00\u5F00\uFF0C\u5B69\u5B50\u81EA\u5DF1\u56DE\u53BB\u5403\uFF0C\u533B\u9662\u4E0D\u80FD\u53E6\u5916\u6536\u8D39\uFF0C\u5982\u679C\u704C\u80A0\u7ED9\u836F\uFF0C\u5C31\u53EF\u4EE5\u6536\u704C\u80A0\u6CBB\u7597\u7684\u8D39\u7528\uFF0C\u8FD9\u4E5F\u53EF\u80FD\u662F\u8FD9\u4E9B\u6CBB\u7597\u5728\u4E00\u4E9B\u5C0F\u8BCA\u6240\u6D41\u884C\u7684\u539F\u56E0\u3002\u53E6\u5916\u5728\u4E00\u4E9B\u504F\u8FDC\u843D\u540E\u5730\u533A\uFF0C\u5F88\u591A\u533B\u751F\u6CA1\u6709\u5F97\u5230\u6B63\u89C4\u7684\u533B\u5B66\u8BAD\u7EC3\uFF0C\u5E76\u4E0D\u77E5\u9053\u8FD9\u4E9B\u505A\u6CD5\u662F\u5426\u6709\u4F9D\u636E\uFF0C\u662F\u5426\u5408\u7406\uFF0C\u53EA\u662F\u770B\u5230\u522B\u4EBA\u8FD9\u6837\u505A\u81EA\u5DF1\u4E5F\u5C31\u8DDF\u98CE\u505A\u3002</p><p>\u8FD9\u4E9B\u56E0\u7D20\u5171\u540C\u9020\u5C31\u4E86\u704C\u80A0\u6CBB\u7597\u7684\u4E71\u8C61\uFF0C\u9762\u5BF9\u8FD9\u79CD\u73B0\u72B6\uFF0C\u4F5C\u4E3A\u5BB6\u957F\u9664\u4E86\u8FDC\u79BB\u5F00\u5C55\u8FD9\u4E9B\u6CBB\u7597\u7684\u8BCA\u6240\u548C\u533B\u9662\uFF0C\u6CA1\u6709\u592A\u591A\u7684\u9009\u62E9\u3002\u8F6C\u53D1\u8FD9\u7BC7\u6587\u7AE0\u8BA9\u66F4\u591A\u5BB6\u957F\u77E5\u9053\uFF0C\u6216\u8BB8\u53EF\u4EE5\u51CF\u5C11\u4E00\u4E9B\u65B0\u95FB\u91CC\u90A3\u6837\u7684\u60B2\u5267\u3002</p><p>\u53C2\u8003\u8D44\u6599:</p><p>[1.]van Hoogdalem E, de Boer AG, Breimer DD.Pharmacokinetics of rectal drug administration, Part I. General considerations and clinical applications of centrally acting drugs. Clin Pharmacokinet. 1991 Jul;21(1):11-26.</p><p>[2.]Scolnik D, Kozer E, Jacobson S, et al.Comparison of oral versus normal and high-dose rectal acetaminophen in the treatment of febrile children. Pediatrics. 2002;110:553\u2013556.</p><p>[3.]Walson PD1, Halvorsen M, Edge J.et al.Pharmacokinetic comparison of acetaminophen elixir versus suppositories in vaccinated infants (aged 3 to 36 months): a single-dose, open-label, randomized, parallel-group design.Clin Ther. 2013 Feb;35(2):135-40.</p><p>[4.]Vilenchik R, Berkovitch M, Jossifoff A,et al.Oral versus rectal ibuprofen in healthy volunteers.J Popul Ther Clin Pharmacol. 2012;19(2):e179-86.</p><hr><p>\u76F8\u5173\u9605\u8BFB\uFF1A</p>',56),f=a("\u8F93\u6DB2\u662F\u4E0D\u662F\u4E0D\u592A\u597D\uFF1F"),m=a("\u96FE\u5316\u6BD4\u8F93\u6DB2\u5371\u5BB3\u8FD8\u5927\uFF1F"),u=a("\u513F\u7AE5\u96FE\u5316\u8981\u4E0D\u8981\u505A\uFF1F\u600E\u4E48\u505A\uFF1F"),b={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651629550&idx=1&sn=9a4f2cec687f6ba5e500f15159b3b308&chksm=8b5c8e14bc2b0702aae7ffa049ba4baf0fec60fcf09425d024d297142eef50a13cdad99892c0&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},g=a("\u67E5\u770B\u539F\u6587");function k(x,z){const o=t("RouterLink"),r=t("ExternalLinkIcon");return s(),c(i,null,[h,e("p",null,[p(o,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651629101&idx=1&sn=f96e1e417e1ba5aea4cadc4c12c785ed/"},{default:n(()=>[f]),_:1})]),e("p",null,[p(o,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651627183&idx=1&sn=bd3c9ba3891186a2426790048487456e/"},{default:n(()=>[m]),_:1})]),e("p",null,[p(o,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651629307&idx=1&sn=28ce7a2d7c54cc0e50066a45ceb585eb/"},{default:n(()=>[u]),_:1})]),e("p",null,[e("a",b,[g,p(r)])])],64)}var O=d(_,[["render",k]]);export{O as default};
