import{r as a,o as r,c as t,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=c('<h3 id="\u4ED6\u5403\u4EC0\u4E48\u5976\u7C89" tabindex="-1"><a class="header-anchor" href="#\u4ED6\u5403\u4EC0\u4E48\u5976\u7C89" aria-hidden="true">#</a> \u4ED6\u5403\u4EC0\u4E48\u5976\u7C89\uFF1F</h3><p>\u524D\u6BB5\u65F6\u95F4\u5728\u4E2D\u56FD\u65C5\u884C\uFF0C\u5728\u4E0A\u6D77\u7684\u4E00\u5929\u65E9\u4E0A\uFF0C\u6211\u4EEC\u8D77\u5F97\u5F88\u665A\u3002\u597D\u5728\u4E2D\u56FD\u662F\u98DF\u5BA2\u7684\u5929\u5802\uFF0C\u5F88\u5FEB\u6211\u4EEC\u5C31\u5728\u5BBE\u9986\u9644\u8FD1\u627E\u5230\u4E86\u5BB6\u5C0F\u997A\u5B50\u9986\uFF0C\u5403\u4E2A\u65E9\u5348\u9910\u3002\u8FD9\u5BB6\u5E97\u7684\u997A\u5B50\u7565\u5E26\u751C\u5473\uFF0C\u9887\u6709\u4E0A\u6D77\u98CE\u5473\uFF0C\u4E5F\u6B63\u5408\u6211\u7684\u53E3\u5473\u3002</p><p>\u5E97\u4E3B\u592B\u5987\u6BB7\u52E4\u597D\u5BA2\uFF0C\u4E00\u4F1A\u513F\u5C31\u7AEF\u4E0A\u4E86\u7F8E\u5473\u7684\u997A\u5B50\uFF0C\u6211\u4EEC\u5403\u4E86\u4F1A\uFF0C\u6709\u4E00\u7537\u4E00\u5973\u63A8\u95E8\u8FDB\u6765\u3002\u4E24\u4EBA\u90FD\u6BD4\u8F83\u5BCC\u6001\uFF0C\u4E00\u8FDB\u6765\u5C31\u8BA9\u4EBA\u611F\u89C9\u7A7A\u95F4\u4F3C\u4E4E\u5C0F\u4E86\u4E9B\u3002\u4ED6\u4EEC\u70B9\u83DC\u65F6\u5E26\u7740\u7684\u5E7F\u4E1C\u53E3\u97F3\u3002\u6211\u731C\u6D4B\u4ED6\u4EEC\u6765\u81EA\u534E\u5357\u3002</p><p>\u6211\u4EEC\u4E00\u5C81\u534A\u7684\u5C0F\u513F\u5B50\uFF0C\u8FD9\u65F6\u521A\u5403\u5B8C\uFF0C\u6B63\u5728\u684C\u5B50\u8FB9\u7814\u7A76\u4ED6\u8FD9\u9635\u5B50\u9887\u4E3A\u7740\u8FF7\u7684\u7535\u98CE\u6247\u3002</p><p>\u201C\u8FD9\u5C0F\u5B69\u957F\u5F97\u597D\uFF0C\u4ED6\u5403\u4EC0\u4E48\u5976\u7C89\uFF1F\u201D \u8FD9\u4F4D\u5E7F\u4E1C\u53E3\u97F3\u7537\u5B50\u9694\u7740\u684C\u5B50\u95EE\u9053\u3002</p><p>\u201C\u54E6\uFF0C\u4ED6\u4EC0\u4E48\u5976\u7C89\u90FD\u6CA1\u5403\u8FC7\uFF0C\u662F\u5168\u6BCD\u4E73\u5582\u517B\uFF0C\u5C0F\u5B69\u5E76\u4E0D\u9700\u8981\u5403\u5976\u7C89\uFF0C\u6211\u4EEC\u5BB6\u4E09\u4E2A\u5C0F\u5B69\u90FD\u6CA1\u6709\u5403\u8FC7\u4E00\u53E3\u5976\u7C89\uFF0C\u90FD\u662F\u5168\u6BCD\u4E73\u5582\u517B\u7684\u3002\u201D \u6211\u592A\u592A\u56DE\u7B54\u5230\u3002</p><p>\u8FD9\u4F4D\u7537\u5B50\u6CA1\u518D\u8BF4\u4EC0\u4E48\u3002\u56E0\u4E3A\u6211\u4E00\u8D2F\u63D0\u5021\u6BCD\u4E73\u5582\u517B\uFF0C\u4E8E\u662F\u63A5\u8FC7\u8BDD\u9898\uFF0C\u8BF4\uFF1A\u6CA1\u5FC5\u8981\u7ED9\u5C0F\u5B69\u5976\u7C89\uFF0C\u5403\u5976\u7C89\u6CA1\u4EC0\u4E48\u597D\u5904\uFF0C\u5403\u5976\u7C89\u5BB9\u6613\u8BA9\u5B69\u5B50\u53D1\u80D6\uFF0C\u5403\u6BCD\u4E73\u5C31\u884C\u3002\u201D</p><p>\u6211\u592A\u592A\u7528\u6CD5\u8BED\u8F7B\u58F0\u63D0\u9192\u6211\u8BF4\u8BDD\u5C0F\u5FC3\u4E9B\uFF0C\u4E0D\u8981\u8BA9\u4EBA\u5BB6\u4E0D\u8212\u670D\uFF0C\u56E0\u4E3A\u8FD9\u4E24\u4F4D\u90FD\u660E\u663E\u5730\u8D85\u91CD\uFF0C\u8BF4\u4E0D\u5B9A\u4ED6\u4EEC\u7684\u5B69\u5B50\u5C31\u662F\u5403\u5976\u7C89\u957F\u5927\u7684\u8D85\u91CD\u513F\u7AE5\u3002\u4E5F\u662F\uFF0C\u6211\u5F97\u6CE8\u610F\u4E00\u4E0B\u3002\u4E8E\u662F\u6211\u4EEC\u6CA1\u518D\u4EA4\u6362\u8BDD\u9898\uFF0C\u4ED6\u4EEC\u53C8\u548C\u5E97\u4E3B\u804A\u4E1C\u804A\u897F\u8BF4\u4E86\u4E9B\u8BDD\uFF0C\u5F88\u5FEB\u5403\u5B8C\u5C31\u8D70\u4E86\u3002</p><p>\u8FD9\u65F6\u5E97\u4E3B\u624D\u8BF4\uFF0C\u4F60\u77E5\u9053\u4ED6\u4EEC\u4E3A\u4EC0\u4E48\u95EE\u4F60\u5C0F\u5B69\u5403\u4EC0\u4E48\u5976\u7C89\u5417\uFF1F\u4ED6\u4EEC\u662F\u5728\u56FD\u5BB6\u4F1A\u5C55\u4E2D\u5FC3\u53C2\u52A0\u5A74\u7AE5\u5C55\u7684\uFF0C\u4ED6\u4EEC\u5728\u90A3\u5356\u5976\u7C89\uFF0C\u6765\u6211\u8FD9\u5403\u4E86\u51E0\u6B21\u4E86\u3002</p><p>\u554A\uFF01\u602A\u4E0D\u5F97\u4ED6\u4EEC\u4E4B\u540E\u4EC0\u4E48\u90FD\u4E0D\u8BF4\u4E86\u3002\u4ED6\u4EEC\u662F\u60F3\u77E5\u9053\u4E3A\u4EC0\u4E48\u6211\u4EEC\u7684\u5C0F\u5B69\u957F\u5F97\u90A3\u4E48\u597D\u5417\uFF1F\u5927\u6982\u4E0D\u662F\u3002\u4ED6\u4EEC\u662F\u60F3\u7ED9\u81EA\u5DF1\u7684\u914D\u65B9\u5976\u7C89\u505A\u5E7F\u544A\u5427\uFF0C\u597D\u66FF\u6362\u6389\u6211\u4EEC\u7528\u7684\u5976\u7C89\uFF08\u4ED6\u4EEC\u4EE5\u4E3A\u7684\uFF09\uFF1F\u6CA1\u60F3\u5230\uFF0C\u4ED6\u4EEC\u641E\u9519\u4E86\u76EE\u6807\uFF0C\u6211\u4EEC\u4E0D\u4F46\u4E0D\u4E70\u5976\u7C89\uFF0C\u53CD\u800C\u7ED9\u4ED6\u4EEC\u4E0A\u8BFE\u3002</p><p>\u6211\u4EEC\u968F\u540E\u8BA1\u5212\u53BB\u770B\u770B\u8FD9\u4E2A\u6709\u610F\u601D\u7684\u5A74\u7AE5\u5C55\uFF0C\u53EF\u6700\u7EC8\u56E0\u4E3A\u65F6\u95F4\u5173\u7CFB\u6CA1\u80FD\u6210\u884C\uFF0C\u4E5F\u5C31\u9519\u8FC7\u4E86\u9886\u7565\u90A3\u7247\u5E02\u573A\u98CE\u5149\u7684\u673A\u4F1A\u3002</p><p>\u6211\u4ECE\u4E0D\u7406\u4F1A\u5976\u7C89\u516C\u53F8\u90A3\u4E00\u5957\u7F8E\u5999\u8BF4\u8F9E\uFF1A\u4EC0\u4E48\u914D\u65B9\u5976\u5F15\u7528\u6700\u65B0\u6280\u672F\uFF0C\u4EC0\u4E48\u542B\u6709DHA\u3001\u76CA\u751F\u83CC\uFF0C\u4EC0\u4E48\u6DFB\u52A0\u4E86\u8FD9\u4E2A\u90A3\u4E2A\u6210\u5206\uFF0C\u4EC0\u4E48\u8BA9\u5B69\u5B50\u66F4\u5065\u5EB7\uFF0C\u66F4\u806A\u660E\u3002\u6211\u77E5\u9053\u4E8B\u5B9E\u5C31\u662F\u8FD9\u4E48\u7B80\u5355\uFF1A\u6CA1\u6709\u4EFB\u4F55\u5A74\u513F\u98DF\u54C1\u80FD\u80DC\u8FC7\u6BCD\u4E73\uFF0C\u6BCD\u4E73\u5582\u517B\u7684\u5B69\u5B50\u6700\u5065\u5EB7\u3002</p><p>\u53EF\u662F\uFF0C\u4E0A\u8FF0\u7684\u8BF4\u6CD5\u9887\u6709\u5E02\u573A\uFF0C\u7279\u522B\u662F\u5728\u90A3\u4E9B\u6559\u80B2\u6C34\u5E73\u76F8\u5BF9\u8F83\u4F4E\uFF0C\u4FE1\u606F\u83B7\u53D6\u9014\u5F84\u76F8\u5BF9\u6709\u9650\u7684\u4EBA\u7FA4\u91CC\u3002</p><p>\u5728\u4E16\u754C\u5404\u5904\u5F88\u591A\u519C\u6751\u6216\u8005\u57CE\u5E02\u8D2B\u56F0\u4EBA\u7FA4\u4E2D\uFF0C\u6BCD\u4EB2\u4EEC\u6CA1\u6709\u592A\u591A\u9009\u62E9\uFF0C\u6240\u4EE5\u5988\u5988\u7684\u5976\u662F\u5A74\u513F\u7684\u4E3B\u8981\u98DF\u7269\u6765\u6E90\u3002\u6BCD\u4E73\u633D\u6551\u4E86\u65E0\u6570\u5C0F\u5B69\u7684\u751F\u547D\uFF0C\u4F7F\u4ED6\u4EEC\u514D\u4E8E\u8425\u517B\u4E0D\u826F\uFF0C\u4E5F\u5C11\u751F\u4F20\u67D3\u6027\u75BE\u75C5\uFF08\u7279\u522B\u662F\u8179\u6CFB\uFF09\u3002</p><p>\u5728\u9AD8\u5EA6\u57CE\u5E02\u5316\u7684\u5730\u533A\u6216\u56FD\u5BB6\uFF0C\u5982\u6B27\u7F8E\u7B49\uFF0C\u4E16\u4EE3\u90FD\u751F\u6D3B\u5728\u57CE\u91CC\u7684\u4EBA\u4EEC\u867D\u7136\u6709\u66F4\u591A\u9009\u62E9\uFF0C\u4F46\u591A\u6570\u4EBA\u9009\u62E9\u6BCD\u4E73\u5582\u517B\u3002\u5728\u8FD9\u4E9B\u5730\u65B9\uFF0C\u5582\u517B\u4E0D\u5173\u4E4E\u751F\u6B7B\u95EE\u9898\u3002\u5988\u5988\u4EEC\u77E5\u9053\u7ED9\u5A74\u513F\u6BCD\u4E73\u7684\u8BDD\uFF0C\u4ED6\u4EEC\u4E0D\u5BB9\u6613\u751F\u75C5\u3001\u4F4F\u9662\uFF1B\u5979\u4EEC\u4E5F\u77E5\u9053\u53CA\u65F6\u7ED9\u5B69\u5B50\u6DFB\u52A0\u8F85\u98DF\uFF0C\u5B69\u5B50\u51FA\u73B0\u8FC7\u654F\u7684\u673A\u4F1A\u5C31\u66F4\u5C11\uFF1B\u5979\u4EEC\u4E5F\u660E\u767D\u6BCD\u4E73\u5582\u517B\u53EF\u4EE5\u9884\u9632\u80A5\u80D6\u53CA\u968F\u4E4B\u800C\u6765\u7684\u957F\u671F\u5065\u5EB7\u95EE\u9898\u3002\u56E0\u6B64\uFF0C\u53D7\u6559\u80B2\u7684\u57CE\u5E02\u5988\u5988\u4EEC\u9009\u62E9\u6BCD\u4E73\u5582\u517B\u3002</p><p>\u5728\u8FC5\u901F\u57CE\u5E02\u5316\u7684\u56FD\u5BB6\uFF0C\u5982\u4E2D\u56FD\uFF0C\u6216\u8005\u6B27\u6D32\u5927\u91CF\u7684\u5916\u6765\u79FB\u6C11\u4EBA\u7FA4\u4E2D\uFF0C\u60C5\u51B5\u5C31\u5927\u4E0D\u76F8\u540C\u3002\u914D\u65B9\u5976\u5546\u4EBA\u5BF9\u8FD9\u79CD\u60C5\u51B5\u4E5F\u4E86\u7136\u4E8E\u80F8\uFF0C\u81EA\u7136\u8981\u597D\u597D\u5229\u7528\u4E00\u756A\u3002</p><p>\u5728\u6211\u73B0\u5728\u5DE5\u4F5C\u7684\u5E03\u9C81\u585E\u5C14\u7684\u51E0\u5BB6\u533B\u9662\u91CC\uFF0C\u6211\u5E38\u78B0\u5230\u65B0\u7238\u65B0\u5988\u548C\u4ED6\u4EEC\u7684\u5C0F\u5A74\u513F\u3002\u4E0A\u9762\u8BB2\u5230\u60C5\u51B5\u4E5F\u5728\u53D1\u751F\uFF1A\u5F53\u5730\u7684\u53D7\u8FC7\u6559\u80B2\u7684\u5988\u5988\u4EEC\u901A\u5E38\u5168\u6BCD\u4E73\u5582\u517B\uFF1B\u65B0\u5230\u7684\u79FB\u6C11\uFF0C\u5C24\u5176\u662F\u6765\u81EA\u975E\u6D32\u7684\u5988\u5988\u4EEC\uFF0C\u4F1A\u6DF7\u5408\u5582\u517B\u6216\u8005\u53EA\u7ED9\u914D\u65B9\u5976\u3002</p><p>\u4ED6\u4EEC\u8FD9\u4E48\u505A\u7684\u7406\u7531\u591A\u79CD\u591A\u6837\uFF0C\u6BD4\u5982\uFF1A\u5988\u5988\u5976\u4E0D\u591F\uFF1B\u6216\u8005\u8BA4\u4E3A\u914D\u65B9\u5976\u8425\u517B\u4E30\u5BCC\uFF0C\u53EF\u4EE5\u5BF9\u6BCD\u4E73\u8FDB\u884C\u8865\u5145\uFF1B\u6216\u8005\u5B9D\u5B9D\u5403\u914D\u65B9\u5976\u53EF\u4EE5\u7761\u4E45\u4E9B\uFF0C\u534A\u591C\u4E0D\u4F1A\u9192\u3002</p><p>\u6211\u5728\u5317\u4EAC\u5DE5\u4F5C\u7684\u5341\u5E74\u95F4\uFF0C\u542C\u5230\u8FC7\u4E00\u6A21\u4E00\u6837\u7684\u7406\u7531\u3002\u6709\u4E00\u70B9\u66F4\u4E3A\u7279\u522B\u7684\u662F\uFF0C\u5F88\u591A\u4E2D\u56FD\u5BB6\u5EAD\u8BA4\u5B9A\u5988\u5988\u7684\u5976\u4E0D\u591F\uFF0C\u8BA4\u4E3A\u6BCD\u4E73\u548C\u914D\u65B9\u5976\u7684\u6DF7\u5408\u5582\u517B\u5BF9\u5B69\u5B50\u66F4\u597D\u3002</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u751F\u4EA7\u540E\u5988\u5988\u5976\u5F88\u5C11\u6216\u8005\u6CA1\u6709\u5976\u5F88\u6B63\u5E38\uFF0C\u6BCD\u4E73\u901A\u5E38\u5728\u5988\u5988\u751F\u4EA7\u540E\u7684\u4E09\u5929\u5185\u5230\u6765\uFF0C\u4F46\u5B9D\u5B9D\u90A3\u65F6\u5BF9\u5976\u91CF\u7684\u9700\u6C42\u4E5F\u5E76\u4E0D\u5927\uFF0C\u5C11\u91CF\u521D\u4E73\u53EF\u4EE5\u6EE1\u8DB3\u4ED6\u7684\u9700\u6C42\u3002\u5982\u679C\u56E0\u4E3A\u62C5\u5FC3\u5B9D\u5B9D\u5403\u4E0D\u591F\uFF0C\u5728\u8FD9\u5173\u952E\u7684\u4E09\u5929\u91CC\u5982\u679C\u7ED9\u4E86\u5A74\u513F\u914D\u65B9\u5976\uFF0C\u5C31\u4F1A\u5EF6\u7F13\u6BCD\u4E73\u7684\u5206\u6CCC\uFF0C\u6700\u7EC8\u53CD\u800C\u5BFC\u81F4\u5988\u5988\u4EEC\u5976\u91CF\u51CF\u5C11\u6216\u6CA1\u6709\u5976\uFF0C\u7136\u540E\u53EA\u597D\u4F9D\u8D56\u914D\u65B9\u5976\u4E86\u3002</p><p>\u6240\u4EE5\uFF0C\u5982\u679C\u60A8\u9009\u62E9\u6BCD\u4E73\u5582\u517B\uFF0C\u6700\u597D\u7684\u65B9\u5F0F\u662F\u5168\u6BCD\u4E73\u5582\u517B\u3002\u8981\u60F3\u505C\u6B62\u6BCD\u4E73\u5582\u517B\uFF0C\u6700\u6709\u6548\u7684\u65B9\u6CD5\u662F\u7ED9\u914D\u65B9\u5976\u3002</p><p>\u5927\u5BB6\u9700\u8981\u77E5\u9053\u7684\u662F\uFF0C\u914D\u65B9\u5976\u5C31\u50CF\u836F\uFF0C\u53EA\u6709\u5728\u5988\u5988\u771F\u6B63\u6CA1\u6709\u6BCD\u4E73\u7684\u65F6\u5019\u624D\u9700\u8981\u7ED9\uFF1B\u6216\u8005\u5728\u6781\u5C11\u6570\u60C5\u51B5\u4E0B\uFF0C\u5988\u5988\u6216\u5B9D\u5B9D\u56E0\u4E3A\u5065\u5EB7\u95EE\u9898\uFF0C\u4E0D\u80FD\u6BCD\u4E73\u65F6\u624D\u9700\u8981\u7ED9\u3002</p><p>\u914D\u65B9\u5976\u6700\u6F02\u4EAE\u7684\u90E8\u5206\u5B9E\u9645\u4E0A\u662F\u5B83\u7684\u9521\u76D2\u5305\u88C5\uFF0C\u7CBE\u7F8E\u7684\u8BBE\u8BA1\u5438\u5F15\u4EBA\u7684\u773C\u7403\uFF0C\u8BF1\u60D1\u4EBA\u4EEC\u53BB\u8D2D\u4E70\u3002\u8981\u662F\u628A\u540C\u6837\u7684\u5976\u7C89\u653E\u5230\u7070\u8272\u7684\u94C1\u76D2\u5B50\u91CC\uFF0C\u5BA3\u4F20\u8BED\u5C31\u53EA\u4E00\u53E5\u9192\u76EE\u7684\uFF1A\u201C\u6BCD\u4E73\u662F\u6700\u597D\u7684\u5A74\u513F\u98DF\u54C1\u201D\uFF0C \u770B\u770B\u8FD8\u6709\u591A\u5C11\u4EBA\u4F1A\u4E70\u3002</p><hr><p>\u4F5C\u8005\u7B80\u4ECB\uFF1A\u90A3\u5510\u5143\uFF08Dr. Goldman\uFF09\uFF0C\u6BD4\u5229\u65F6\u513F\u79D1\u533B\u751F\uFF0C\u5317\u4EAC\u548C\u7766\u5BB6\u533B\u9662\u4EFB\u513F\u79D1\u533B\u751F10\u5E74\uFF0C\u8457\u6709\u300A\u5B69\u5B50\u751F\u75C5\u600E\u4E48\u529E\u300B\u3002\u516C\u4F17\u5FAE\u4FE1\u53F7\u201C\u5B69\u5B50\u751F\u75C5\u600E\u4E48\u529E\u201D \u3002</p><p>\u90A3\u533B\u751F\u64C5\u957F\u4E8E\u513F\u79D1\u5E38\u89C1\u75C5\u3001\u751F\u957F\u53D1\u80B2\u3001\u8425\u517B\u95EE\u9898\u53CA\u4F20\u67D3\u6027\u75BE\u75C5\u3002\u957F\u6309\u4E0B\u9762\u4E8C\u7EF4\u7801\u5173\u6CE8\u540E\u8FDB\u5165\u5E95\u90E8\u83DC\u5355\uFF0C\u53EF\u5411\u90A3\u533B\u751F\u54A8\u8BE2\u3002</p>',26),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626277&idx=1&sn=4c24bd6fd164593a06fb69c4c410b5ff&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function f(m,x){const p=a("ExternalLinkIcon");return r(),t(o,null,[i,e("p",null,[e("a",l,[h,n(p)])])],64)}var k=d(_,[["render",f]]);export{k as default};
