import{r,o as a,c,a as e,b as t,F as n,d as o,e as i}from"./app.9aaa2256.js";import{_ as l}from"./plugin-vue_export-helper.21dcd24c.js";const s={},d=o('<h3 id="\u8BFE\u7A0B-\u4EBA\u751F\u7684\u7EC8\u6781\u80FD\u529B\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u8BFE\u7A0B-\u4EBA\u751F\u7684\u7EC8\u6781\u80FD\u529B\u662F\u4EC0\u4E48" aria-hidden="true">#</a> \u8BFE\u7A0B \uFF5C\u4EBA\u751F\u7684\u7EC8\u6781\u80FD\u529B\u662F\u4EC0\u4E48\uFF1F</h3><p>\u8BFB\u4E66\u7684\u65F6\u5019\uFF0C\u6211\u5F88\u559C\u6B22\u7535\u5B50\u4EA7\u54C1\uFF0C\u90A3\u65F6\u5019\u6CA1\u94B1\uFF0C\u53EA\u80FD\u73A9\u201C\u6587\u66F2\u661F\u201D\u4EE5\u53CA\u6CA1\u6709\u7535\u8BDD\u529F\u80FD\u7684\u667A\u80FD\u673A\uFF0C\u6BD5\u4E1A\u65F6\u6700\u5927\u7684\u613F\u671B\u662F\u6709\u4E2A\u6570\u7801\u76F8\u673A\u548C\u7B14\u8BB0\u672C\u7535\u8111\u3002</p><p>\u8FD9\u4E2A\u613F\u671B\u5728\u5DE5\u4F5C\u540E\u5F88\u5FEB\u5C31\u5B9E\u73B0\u4E86\uFF0C\u4F46\u5F88\u5FEB\u4E5F\u5931\u53BB\u4E86\u5BF9\u8FD9\u4E9B\u7535\u5B50\u4EA7\u54C1\u7684\u5174\u8DA3\uFF0C\u7136\u540E\u540E\u9762\u671F\u671B\u6709\u4E00\u5957\u623F\u5B50\uFF0C\u8FD9\u4E2A\u613F\u671B\u5728\u5DE5\u4F5C\u51E0\u5E74\u540E\u4E5F\u5B9E\u73B0\u4E86\uFF0C\u4F46\u540C\u6837\u5F88\u5FEB\u5BF9\u623F\u5B50\u4E5F\u65E0\u611F\u4E86\u3002</p><p>\u7C7B\u4F3C\u7684\u613F\u671B\u6709\u8FC7\u5F88\u591A\uFF0C\u4E0D\u8BBA\u662F\u7269\u8D28\u4E0A\u7684\uFF0C\u8FD8\u662F\u804C\u4E1A\u53D1\u5C55\u4E0A\uFF0C\u613F\u671B\u6709\u5C0F\u6709\u5927\uFF0C\u5B9E\u73B0\u7684\u8FC7\u7A0B\u6216\u957F\u6216\u77ED\uFF0C\u4E5F\u5B9E\u73B0\u8FC7\u5F88\u591A\u3002</p><p>\u4F46\u613F\u671B\u5B9E\u73B0\u4E4B\u540E\uFF0C\u6240\u5E26\u6765\u7684\u5FEB\u4E50\u5374\u603B\u662F\u5F88\u77ED\u6682\u7684\uFF0C\u76F8\u53CD\uFF0C\u7559\u4E0B\u5370\u8C61\u7684\u66F4\u591A\u662F\u5B9E\u73B0\u8FC7\u7A0B\u4E2D\u7684\u90A3\u4E9B\u611F\u53D7\uFF0C\u8FD9\u4E9B\u6C89\u79EF\u4E0B\u6765\u7684\u559C\u6012\u54C0\u4E50\uFF0C\u5012\u66F4\u50CF\u8FC7\u53BB\u5C81\u6708\u7684\u5168\u90E8\u3002</p><p>\u6240\u4EE5\u5E74\u9F84\u8D8A\u662F\u589E\u957F\uFF0C\u8D8A\u662F\u610F\u8BC6\u5230\uFF0C\u4EBA\u7C7B\u662F\u6E3A\u5C0F\u7684\uFF0C\u4EBA\u7684\u4E00\u751F\u4E5F\u662F\u77ED\u6682\u7684\uFF0C\u80FD\u505A\u6210\u4EC0\u4E48\uFF0C\u80FD\u5B9E\u73B0\u4EC0\u4E48\uFF0C\u4F3C\u4E4E\u5E76\u4E0D\u90A3\u4E48\u91CD\u8981\uFF0C\u66F4\u91CD\u8981\u7684\u662F\u81EA\u5DF1\u60F3\u505A\u4EC0\u4E48\uFF0C\u4EE5\u53CA\u5728\u505A\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u81EA\u5DF1\u80FD\u4E0D\u80FD\u627E\u5230\u5FEB\u4E50\u3002</p><p>\u80FD\u5FEB\u4E50\uFF0C\u80FD\u81EA\u6211\u6EE1\u8DB3\u7684\u80FD\u529B\uFF0C\u624D\u662F\u4EBA\u751F\u7684\u7EC8\u6781\u80FD\u529B\u3002</p><hr><p>\u6EE1\u8DB3\u548C\u5FEB\u4E50\u7684\u80FD\u529B\uFF0C\u5F88\u5927\u7A0B\u5EA6\u53D6\u51B3\u4E8E\u6211\u4EEC\u7684\u5FC3\u7406\u9700\u6C42\u4E0A\u7684\u9608\u503C\uFF0C\u9608\u503C\u8D8A\u4F4E\uFF0C\u4E5F\u5C31\u8D8A\u5BB9\u6613\u88AB\u6EE1\u8DB3\uFF0C\u800C\u5FC3\u7406\u4E0A\u7684\u9700\u6C42\uFF0C\u6700\u5E95\u5C42\u7684\u5176\u5B9E\u662F\u5B89\u5168\u611F\u3002</p><p>\u6216\u8BB8\u6CA1\u6709\u610F\u8BC6\u5230\uFF0C\u4F46\u6211\u4EEC\u53EF\u80FD\u90FD\u88AB\u5B89\u5168\u611F\u7684\u95EE\u9898\u56F0\u6270\u8FC7\uFF0C\u6BD4\u5982</p><ul><li><p>\u5C0F\u65F6\u5019\uFF0C\u7236\u6BCD\u4E25\u5389\u6559\u80B2\u4E0B\uFF0C\u62C5\u5FC3\u88AB\u8D23\u9A82\uFF0C\u9047\u5230\u56F0\u96BE\u4E0D\u6562\u8DDF\u5BB6\u91CC\u8BF4\uFF0C\u53EA\u80FD\u81EA\u5DF1\u9ED8\u9ED8\u6D88\u5316\uFF0C\u5982\u4ECA\u4E0E\u7236\u6BCD\u4E5F\u53EF\u80FD\u6E10\u884C\u6E10\u8FDC\uFF1B</p></li><li><p>\u957F\u5927\u540E\uFF0C\u5728\u5A5A\u59FB\u4E2D\uFF0C\u5982\u679C\u53E6\u4E00\u534A\u63D0\u4F9B\u4E0D\u4E86\u81EA\u5DF1\u60F3\u6709\u7684\u5B89\u5168\u611F\uFF0C\u5BF9\u65B9\u53BB\u4E2A\u540C\u5B66\u805A\u4F1A\u3001\u5E94\u916C\u4E5F\u4F1A\u5404\u79CD\u5185\u5FC3\u620F\u3001\u5FD0\u5FD1\u4E0D\u5B89\uFF1B</p></li><li><p>\u60F3\u5410\u69FD\u81EA\u5DF1\u751F\u6D3B\u65F6\uFF0C\u5411\u670B\u53CB\u4E00\u53E3\u6C14\u53D1\u597D\u51E0\u6761\u4FE1\u606F\uFF0C\u7126\u8651\u5730\u7B49\u5F85\uFF0C\u5E0C\u671B\u5F97\u5230\u8FC5\u901F\u56DE\u5E94\uFF0C\u5BFB\u6C42\u5BF9\u65B9\u7684\u53CD\u9988\u548C\u8BA4\u53EF\u3002</p></li></ul><p>\u8FD9\u4E9B\u5185\u5FC3\u611F\u53D7\uFF0C\u5176\u5B9E\u4E0E\u5C0F\u65F6\u5019\u7236\u6BCD\u5BF9\u6211\u4EEC\u7684\u517B\u80B2\u6A21\u5F0F\u6709\u4E00\u5B9A\u5173\u7CFB\u3002\u968F\u7740\u6211\u4EEC\u9605\u5386\u4E30\u5BCC\uFF0C\u8BA4\u8BC6\u66F4\u591A\u7684\u4EBA\uFF0C\u7236\u6BCD\u5BF9\u6211\u4EEC\u5F71\u54CD\u53EF\u80FD\u88AB\u7A00\u91CA\uFF0C\u4F46\u4E5F\u8BB8\u662F\u6211\u4EEC\u82B1\u4E86\u66F4\u5927\u529B\u6C14\u3001\u7ECF\u5386\u8FC7\u5185\u5FC3\u8D77\u8D77\u843D\u843D\uFF0C\u624D\u514B\u670D\u4E86\u7AE5\u5E74\u8FD9\u79CD\u201C\u4E0D\u5B89\u5168\u611F\u201D\u5BF9\u751F\u6D3B\u7684\u5F71\u54CD\u3002</p><p>\u5982\u4ECA\uFF0C\u8F6E\u5230\u6211\u4EEC\u6210\u4E3A\u7236\u6BCD\uFF0C\u6211\u4EEC\u4F1A\u5E0C\u671B\u4ED6\u5FEB\u5FEB\u4E50\u4E50\u6210\u957F\uFF0C\u4F46\u5B69\u5B50\u7684\u60C5\u7EEA\u4E5F\u4E00\u76F4\u5904\u4E8E\u53D8\u5316\u4E4B\u4E2D\uFF1A</p><ul><li><p>\u5B69\u5B50\u53EF\u80FD\u4F1A\u8BF4\u4E0D\u559C\u6B22\u5988\u5988</p></li><li><p>\u95EE\u4E3A\u4EC0\u4E48\u4E0D\u5F00\u5FC3\uFF0C\u5B69\u5B50\u5374\u4E0D\u613F\u56DE\u7B54</p></li><li><p>\u9762\u5BF9\u964C\u751F\u4EBA\u53EA\u60F3\u8EB2\u5728\u5988\u5988\u540E\u9762</p></li><li><p>\u5988\u5988\u79BB\u5F00\uFF0C\u5B69\u5B50\u54ED\u95F9\u4E0D\u6B62<br> \u2026\u2026</p></li></ul><p>\u5F53\u5B69\u5B50\u51FA\u73B0\u6D88\u6781\u60C5\u7EEA\u65F6\uFF0C\u6211\u4EEC\u4F1A\u4E3A\u81EA\u5DF1\u63D0\u4F9B\u4E0D\u4E86\u592A\u591A\u5E2E\u52A9\u800C\u611F\u5230\u7126\u8651\uFF0C\u4E5F\u4F1A\u60F3\uFF1A\u8F9B\u8F9B\u82E6\u82E6\u751F\u51FA\u6765\u7684\u5B69\u5B50\uFF0C\u4E3A\u4EC0\u4E48\u4E0D\u559C\u6B22\u81EA\u5DF1\uFF1F\u5B69\u5B50\u4E3A\u4EC0\u4E48\u4E0D\u613F\u610F\u5BF9\u81EA\u5DF1\u655E\u5F00\u5FC3\u6249\uFF1F\u5B69\u5B50\u6015\u751F\uFF0C\u662F\u4E0D\u662F\u6211\u6CA1\u6709\u7ED9\u5B69\u5B50\u8DB3\u591F\u7684\u5B89\u5168\u611F\uFF1F</p><p>\u6021\u79BE\u9080\u8BF7\u7F8E\u56FD\u7231\u8377\u534E\u5927\u5B66\u513F\u79D1\u7CFB\u53D1\u80B2\u884C\u4E3A\u513F\u79D1\u535A\u58EB\u540E\u3001\u7F8E\u56FD\u884C\u4E3A\u5206\u6790\u534F\u4F1A\u8BA4\u8BC1\u884C\u4E3A\u5206\u6790\u5E08<strong>\u5F90\u6842\u51E4</strong>\u533B\u751F\uFF0C\u5F00\u8BBE\u4E86\u4E00\u5802\u5173\u4E8E\u4EB2\u5B50\u4F9D\u604B\u517B\u80B2\u7684\u4E13\u680F\uFF0C\u6559\u5927\u5BB6\u6B63\u786E\u8BA4\u77E5\u4EB2\u5B50\u4F9D\u604B\u5173\u7CFB\uFF0C\u5E2E\u5B69\u5B50\u5EFA\u7ACB\u5B89\u5168\u611F\u3002</p><p>\u5982\u679C\u4F60\u6709\u9700\u8981\uFF0C\u53EF\u901A\u8FC7\u4E0B\u9762\u4E8C\u7EF4\u7801\u8BE6\u7EC6\u4E86\u89E3\uFF0C3\u4EBA\u62FC\u56E2\u6709\u4F18\u60E0\uFF0C\u6021\u79BE\u4F1A\u5458<strong>5\u6298</strong>\u542C\u3002</p>',17),_={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651630736&idx=1&sn=47fd31f0130dd1a2571c7ebd0c3116d9&chksm=8b5c856abc2b0c7ca27420023b4da995745b924c04c550e6934c489fddb7f88b5ecc49e3d431&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=i("\u67E5\u770B\u539F\u6587");function f(m,b){const p=r("ExternalLinkIcon");return a(),c(n,null,[d,e("p",null,[e("a",_,[h,t(p)])])],64)}var g=l(s,[["render",f]]);export{g as default};
