import{r as o,o as r,c as i,a as t,b as a,w as s,F as _,d as n,e}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const m={},h=n('<h3 id="\u4E73\u7CD6\u9176\u80FD\u6CBB\u8179\u6CFB\u5417" tabindex="-1"><a class="header-anchor" href="#\u4E73\u7CD6\u9176\u80FD\u6CBB\u8179\u6CFB\u5417" aria-hidden="true">#</a> \u4E73\u7CD6\u9176\u80FD\u6CBB\u8179\u6CFB\u5417\uFF1F</h3><p>\u4E73\u7CD6\u662F\u4E00\u79CD\u53CC\u7CD6\uFF0C\u662F\u6BCD\u4E73\u548C\u725B\u5976\u4E2D\u7684\u4E3B\u8981\u7CD6\u5206\uFF0C\u5FC5\u987B\u88AB\u6D88\u5316\u6210\u5355\u7CD6\u624D\u53EF\u4EE5\u88AB\u5438\u6536\u3002\u60F3\u8C61\u4E00\u4E0B\uFF0C\u5F53\u6211\u4EEC\u628A\u725B\u5976\u559D\u8FDB\u809A\u5B50\uFF0C\u4F1A\u6709\u4E00\u628A\u526A\u5200\u628A\u4E73\u7CD6\u4E00\u526A\u4E3A\u4E8C\uFF0C\u7136\u540E\u628A\u5B83\u5438\u6536\uFF0C\u8FD9\u628A\u526A\u5200\u5C31\u662F\u4E73\u7CD6\u9176\u3002</p><p>\u4E16\u754C\u4E0A\u51E0\u4E4E\u6240\u6709\u7684\u5A74\u513F\u90FD\u80FD\u5F88\u597D\u5730\u4EA7\u751F\u4E73\u7CD6\u9176\uFF0C\u8FDB\u800C\u6D88\u5316\u4E73\u7CD6\uFF0C\u53EA\u6709\u51FA\u73B0\u7F55\u89C1\u7684\u57FA\u56E0\u95EE\u9898\u65F6\uFF0C\u624D\u4F1A\u5BFC\u81F4\u5148\u5929\u6027\u4E73\u7CD6\u9176\u7F3A\u5931\uFF0C\u5BFC\u81F4\u5B69\u5B50\u4E0D\u80FD\u6D88\u5316\u4E73\u7CD6\uFF0C\u4E5F\u610F\u5473\u7740\u4E0D\u80FD\u6D88\u5316\u6BCD\u4E73\u6216\u725B\u5976\u3002</p><p>\u4F46\u662F\uFF0C\u4E16\u754C\u4E0A\u8BB8\u591A\u5B69\u5B50\uFF0C\u968F\u7740\u5E74\u9F84\u7684\u589E\u957F\uFF0C\u7EA6_2-5_\u5C81\u7684\u5E74\u9F84\u6BB5\u5F00\u59CB\uFF0C\u4E73\u7CD6\u9176\u4F1A\u9010\u6E10\u51CF\u5C11\uFF0C\u7136\u540E\u5931\u53BB\u5927\u91CF\u6D88\u5316\u4E73\u7CD6\u7684\u80FD\u529B\uFF0C\u51FA\u73B0\u4E73\u7CD6\u4E0D\u8010\u53D7\uFF0C\u725B\u5976\u591A\u559D\u4E00\u70B9\u5C31\u4F1A\u51FA\u73B0\u80C0\u6C14\u3001\u8179\u75DB\u3001\u8179\u6CFB\u7684\u8868\u73B0\u3002\u53EA\u6709\u5317\u6B27\u548C\u975E\u6D32\u4E00\u5C0F\u90E8\u5206\u4EBA\u53EF\u5728\u6210\u5E74\u540E\u4ECD\u80FD\u5927\u91CF\u6D88\u5316\u725B\u5976\u3002\u7EC6\u60F3\u4E00\u4E0B\uFF0C\u8FD9\u6216\u8BB8\u662F\u4EBA\u7C7B\u8FDB\u5316\u5BFC\u81F4\u7684\u7ED3\u679C\uFF0C\u56E0\u4E3A\u5976\u672C\u8D28\u4E0A\u662F\u6BCD\u4EB2\u7ED9\u5B9D\u5B9D\u7684\u98DF\u7269\u3002</p><p>\u7B80\u800C\u8A00\u4E4B\uFF0C\u5BF9\u5927\u9F84\u513F\u7AE5\u548C\u6210\u5E74\u4EBA\u6765\u8BF4\uFF0C\u4E0D\u80FD\u6D88\u5316\u4E73\u7CD6\u6BD4\u80FD\u6D88\u5316\u4E73\u7CD6\u66F4\u4E3A\u5E38\u89C1\u3002\u5982\u679C\u6211\u7ED9\u6211\u5988\u6216\u59E8\u5988\u4E00\u676F\u725B\u5976\uFF0C\u5979\u4EEC\u51E0\u4E4E\u90FD\u4F1A\u60F3\u5410\u3002\u518D\u95EE\u95EE\u4F60\u8EAB\u8FB9\u7684\u4EBA\uFF0C\u770B\u770B\u6709\u591A\u5C11\u6210\u4EBA\u4E0D\u559C\u6B22\u6216\u559D\u4E0D\u4E0B\u725B\u5976\u3002</p><p>\u4F46\u4ECA\u5929\u60F3\u804A\u7684\u662F\uFF0C\u5728\u56FD\u5185\u513F\u7AE5\u6025\u6027\u8179\u6CFB\u6CBB\u7597\u4E2D\uFF0C\u4E73\u7CD6\u9176\u4F5C\u4E3A\u836F\u7269\u7684\u6EE5\u7528\u3002\u4E0D\u8BA1\u5176\u6570\u7684\u75C5\u4EBA\u95EE\u8FC7\u6211\u4E73\u7CD6\u9176\u8981\u5403\u591A\u957F\u65F6\u95F4\uFF0C\u597D\u50CF\u4E73\u7CD6\u9176\u975E\u5F97\u662F\u8179\u6CFB\u6CBB\u7597\u7684\u4E00\u90E8\u5206\u3002</p><p>\u5BF9\u4E8E\u8FD9\u4E2A\u95EE\u9898\uFF0C\u6211\u793C\u8C8C\u5730\u544A\u8BC9\u4ED6\u4EEC\uFF0C\u5BF9\u4E8E\u4E73\u7CD6\u9176\u6CBB\u7597\u8179\u6CFB\u7684\u7814\u7A76\u6839\u672C\u6CA1\u591A\u5C11\uFF0C<strong>\u7528\u4E73\u7CD6\u9176\u6CBB\u8179\u6CFB\u4E5F\u4E0D\u662F\u6807\u51C6\u505A\u6CD5</strong>\uFF0C\u56E0\u4E3A\u7528\u4E73\u7CD6\u9176\u5F88\u53EF\u80FD\u6CA1\u4EFB\u4F55\u4F5C\u7528\u6216\u4F5C\u7528\u4E0D\u5927\uFF0C\u8179\u6CFB\u66F4\u5927\u7684\u53EF\u80FD\u662F\u81EA\u884C\u505C\u6B62\u3002</p><p>\u6211\u56DE\u7B54\u75C5\u4EBA\u65F6\u6BD4\u8F83\u793C\u8C8C\uFF0C\u4F46\u5FC3\u91CC\u53EF\u6CA1\u90A3\u4E48\u5BA2\u6C14\uFF1A<em>\u201C<em>\u641E\u4EC0\u4E48\u9B3C\uFF0C\u53C8\u6709\u4E2A\u533B\u751F\u5F00\u4E73\u7CD6\u9176\u6CBB\u8179\u6CFB\uFF01\uFF1F</em>\u201D <em>\u51E0\u79D2\u949F\u540E\u6211\u5FC3\u91CC\u7684\u56DE\u7B54\u5C31\u51FA\u6765\u4E86\uFF1A</em>\u201C</em> \u5927\u6982\u8FD8\u662F\u94B1\u7684\u529B\u91CF\uFF0C\u6709\u4EBA\u5728\u540E\u9762\u5927\u529B\u5BA3\u626C\u5457\uFF01\u201D</p><p>\u6CA1\u9519\uFF0C\u8179\u6CFB\u65F6\u8EAB\u4F53\u7684\u786E\u4F1A\u51FA\u73B0\u4E00\u5B9A\u7A0B\u5EA6\u7684\u4E73\u7CD6\u4E0D\u8010\u53D7\uFF0C\u4F46\u5F88\u5C11\u51FA\u73B0\u4E34\u5E8A\u75C7\u72B6\u6216\u8005\u75C7\u72B6\u5F88\u8F7B\uFF0C\u800C\u4E14\u80AF\u5B9A\u80FD\u81EA\u884C\u6062\u590D\u3002\u9664\u975E\u662F\u6301\u7EED\u65F6\u95F4\u8D85\u8FC72\u5468\u7684\u6162\u6027\u8179\u6CFB\uFF0C\u914D\u65B9\u5976\u5582\u517B\u7684\u5B69\u5B50\u53EF\u4EE5\u8003\u8651\u6362\u201C\u53BB\u4E73\u7CD6\u996E\u98DF\u201D\uFF0C\u5426\u5219\u90FD\u4E0D\u9700\u8981\u523B\u610F\u56DE\u907F\u542B\u4E73\u7CD6\u7684\u4E73\u5236\u54C1\u3002</p>',9),l=e("\u800C\u5403\u4E73\u7CD6\u9176\u548C\u53BB\u4E73\u7CD6\u996E\u98DF\u8FD8\u4E0D\u4E00\u6837\uFF0C\u56E0\u4E3A\u53E3\u670D\u7684\u4E73\u7CD6\u9176\u4E0D\u592A\u53EF\u80FD\u50CF\u80A0\u9053\u672C\u8EAB\u5206\u6CCC\u7684\u4E73\u7CD6\u9176\u90A3\u6837\u5747\u5300\u5206\u5E03\u5E76\u5145\u5206\u4F5C\u7528\u4E8E\u98DF\u7269\uFF0C\u6240\u4EE5\u53E3\u670D\u4E73\u7CD6\u9176\u6216\u8BB8\u53EF\u4EE5\u6D88\u5316\u4E00\u70B9\u4E73\u7CD6\uFF0C\u4F46\u5B9E\u9645\u7684\u6548\u679C\u5F88\u6709\u9650\u3002\u66F4\u4F55\u51B5\uFF0C\u5373\u4FBF\u662F\u5B69\u5B50\u91C7\u7528\u201C\u53BB\u4E73\u7CD6\u996E\u98DF\u201D\uFF0C\u5BF9\u8179\u6CFB\u75C7\u72B6\u7684\u6539\u5584\u6548\u679C\u4E5F\u5F88\u6709\u9650\u3002\uFF08\u53C2\u89C1\uFF1A"),f=e("\u5B69\u5B50\u62C9\u809A\u5B50\uFF0C\u8981\u4E0D\u8981\u5403\u201C\u8179\u6CFB\u5976\u7C89\u201D\uFF1F"),u=e("\uFF09"),b=n("<p>\u4E8B\u5B9E\u4E5F\u662F\uFF0C\u7528\u4E73\u7CD6\u9176\u6CBB\u7597\u8179\u6CFB\u7684\u7814\u7A76\u6781\u5C11\u3002\u6211\u5148\u5728_Pubmed_ \uFF08\u7F8E\u56FD\u533B\u5B66\u51FA\u7248\u7269\u6570\u636E\u5E93\uFF09\u91CC\u8F93\u5165\u5173\u952E\u8BCD_\u201C_ \u4E73\u7CD6\u9176\u3001\u6025\u6027\u8179\u6CFB_\u201D_\uFF0C\u641C\u7D22\u7ED3\u679C\u6709\u4E24\u9875\uFF0C\u5176\u4E2D\u4E00\u7BC7\u6587\u7AE0\u8BB2\u5230\u4E73\u7CD6\u9176\u548C\u5582\u517B\uFF0C\u4F46\u5E76\u975E\u771F\u6B63\u8C08\u8BBA\u6709\u5173\u8179\u6CFB\u7684\u6CBB\u7597\uFF0C\u800C\u662F\u8BC4\u4F30\u4E73\u7CD6\u9176\u4F5C\u4E3A\u836F\u7269\u5BF9\u65E9\u4EA7\u513F\u7684\u4F5C\u7528\uFF0C\u7528\u4E8E\u589E\u52A0\u5582\u517B\u8010\u53D7\u6027\u3002<em>Cochrane</em> \u5BF9\u8BE5\u6587\u7AE0\u7684\u8BC4\u4EF7\u4E3A\uFF0C\u5B83\u6CA1\u6709\u63D0\u4F9B\u4EFB\u4F55\u8BC1\u636E\u8868\u660E\uFF0C\u6DFB\u52A0\u4E73\u7CD6\u9176\u5BF9\u65E9\u4EA7\u513F\u5A74\u513F\u5582\u517B\u6709\u4EFB\u4F55\u663E\u8457\u76CA\u5904\u3002</p><p>\u4F46\u8FD9\u4E24\u9875\u641C\u7D22\u7ED3\u679C\u91CC\u6CA1\u6709\u4E00\u7BC7\u6587\u7AE0\u662F\u63A2\u8BA8\u4E73\u7CD6\u9176\u4F5C\u4E3A\u836F\u7269\u5728\u8179\u6CFB\u4E2D\u7684\u4F5C\u7528\u3002\u7528\u4E73\u7CD6\u9176\u6CBB\u8179\u6CFB\u4F3C\u4E4E\u6839\u672C\u6CA1\u88AB\u6D4B\u8BD5\u8FC7\uFF0C\u5982\u679C\u60A8\u627E\u5230\u76F8\u5173\u6D4B\u8BD5\u7814\u7A76\uFF0C\u8FD8\u8BF7\u544A\u8BC9\u6211\u3002</p><p>\u6211\u53C8\u67E5\u770B\u4E86\u4E0B\u9762\u8FD9\u4E9B\u513F\u7AE5\u8179\u6CFB\u6CBB\u7597\u7684\u76F8\u5173\u7684\u6307\u5357\uFF1A</p><p>\u2022 UpToDate\u4E34\u5E8A\u987E\u95EE\u82F1\u6587\u7248\uFF0FUpToDate</p><p>\u2022 \u4E16\u754C\u80A0\u80C3\u75C5\u5B66\u7EC4\u7EC7/ World Gastroenterology Organization</p><p>\u2022 \u4E16\u754C\u536B\u751F\u7EC4\u7EC7\u8179\u6CFB\u6CBB\u7597\u6307\u5357\uFF0FWHO manual for physician for treatment of Diarrhea</p><p>\u2022 \u52A0\u62FF\u5927\u513F\u79D1\u5B66\u4F1A\uFF0FCanadian Paediatric Society</p><p>\u2022 \u6B27\u6D32\u513F\u79D1\u80C3\u80A0\u75C5\u5B66\u3001\u809D\u75C5\u5B66\u548C\u8425\u517B\u5B66\u4F1A\uFF0F<em>ESPGHAN</em></p><p>\u2022 \u6B27\u6D32\u513F\u79D1\u4F20\u67D3\u75C5\u5B66\u4F1A\uFF0F<em>ESPID</em></p><p>\u2022 \u82F1\u56FD\u56FD\u5BB6\u536B\u751F\u4E0E\u4E34\u5E8A\u6280\u672F\u4F18\u5316\u7814\u7A76\u6240\u6307\u5357\uFF0F <em>NICE Guideline</em></p><p>\u2022 \u7F8E\u56FD\u75BE\u63A7\u4E2D\u5FC3\uFF0F<em>USCDC</em></p><p>\u2022 \u6FB3\u5927\u5229\u4E9A\uFF0F\u513F\u7AE5\u5455\u5410\u6216\u65E0\u5455\u5410\u8179\u6CFB\u7BA1\u7406\u5FAA\u8BC1\u6307\uFF0FEvidence based guideline for the management of diarrhoea with or without vomiting in children</p><p>\u53EA\u6709\u4E00\u5904\u63D0\u5230\u4E86\u4E73\u7CD6\u9176\uFF0C\u5C31\u662F\u5728\u4E16\u754C\u536B\u751F\u7EC4\u7EC7\u8179\u6CFB\u6CBB\u7597\u6307\u5357\u91CC\uFF0C\u4F46\u5B83\u4E0D\u662F\u628A\u4E73\u7CD6\u9176\u7528\u4E8E\u6CBB\u7597\uFF0C\u800C\u662F\u5728\u63D0\u5230\u8F6E\u72B6\u75C5\u6BD2\u8179\u6CFB\u53D1\u751F\u65F6\uFF0C\u80A0\u9053\u4E2D\u4E73\u7CD6\u9176\u7684\u5929\u7136\u6D3B\u6027\u6709\u6240\u964D\u4F4E\uFF0C\u540C\u65F6\u8FD8\u79F0_\u201C_\u901A\u5E38\u6CA1\u6709\u4E34\u5E8A\u610F\u4E49\uFF0C\u80A0\u9053\u7684\u5F62\u6001\u548C\u5438\u6536\u80FD\u529B\u5728_2-3_\u5468\u7684\u65F6\u95F4\u5C31\u6062\u590D\u6B63\u5E38\u201D\u3002</p><p>\u6240\u4EE5\uFF0C\u770B\u5B8C\u4E86\u8FD9\u4E9B\uFF0C\u6211\u4EEC\u57FA\u672C\u53EF\u4EE5\u786E\u5B9A\uFF0C\u53E3\u670D\u4E73\u7CD6\u9176\u5BF9\u4E8E\u8179\u6CFB\u7684\u4F5C\u7528\u6CA1\u6709\u6D4B\u8BD5\u8FC7\uFF0C\u4E5F\u4E0D\u662F\u6CBB\u7597\u8179\u6CFB\u7684\u6807\u51C6\u505A\u6CD5\uFF0C\u522B\u7684\u56FD\u5BB6\u4E5F\u6CA1\u4EBA\u7528\u5B83\u6765\u6CBB\u7597\u8179\u6CFB**\u3002**\uFF08\u7F16\u8BD1\uFF1A\u65BC\u5A55\uFF09</p><hr><p>\u672C\u6587\u4F5C\u8005\u90A3\u5510\u5143\u4E3A\u6BD4\u5229\u65F6\u513F\u79D1\u533B\u751F\uFF0C\u957F\u6309\u4E0B\u9762\u4E8C\u7EF4\u7801\u5173\u6CE8\u6021\u79BE\u5065\u5EB7\uFF0C\u53EF\u5411\u90A3\u533B\u751F\u54A8\u8BE2\u3002</p><p>\u70B9<strong>\u9605\u8BFB\u539F\u6587</strong>\uFF0C\u53EF\u76F4\u63A5\u8FDB\u54A8\u8BE2\u9875\u9762</p>",17),x={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627074&idx=1&sn=933c2ac2485dce431616930874d555e1&chksm=8b5cb7b8bc2b3eaeb9e2cc1e6fe24c0d8cc4315d2c9979bf684998c13bfcecc99cc76a30a3c9&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},g=e("\u67E5\u770B\u539F\u6587");function E(k,N){const c=o("RouterLink"),p=o("ExternalLinkIcon");return r(),i(_,null,[h,t("p",null,[l,a(c,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651626855&idx=1&sn=c909284be64c33de6953ce5afcd23625&scene=21#wechat_redirect/"},{default:s(()=>[f]),_:1}),u]),b,t("p",null,[t("a",x,[g,a(p)])])],64)}var C=d(m,[["render",E]]);export{C as default};
