import{r,o as t,c as n,a as e,b as o,F as a,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=s('<h3 id="\u6297\u751F\u7D20\u548C\u513F\u7AE5\u4E0A\u611F-\u5BB6\u957F\u5E94\u8BE5\u77E5\u9053\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u6297\u751F\u7D20\u548C\u513F\u7AE5\u4E0A\u611F-\u5BB6\u957F\u5E94\u8BE5\u77E5\u9053\u4EC0\u4E48" aria-hidden="true">#</a> \u6297\u751F\u7D20\u548C\u513F\u7AE5\u4E0A\u611F\uFF0C\u5BB6\u957F\u5E94\u8BE5\u77E5\u9053\u4EC0\u4E48\uFF1F</h3><p>\u5F88\u591A\u4EBA\u53CD\u5E94\u4E0A\u7BC7\u4E34\u5E8A\u6307\u5357\u592A\u4E13\u4E1A\uFF0C\u8BFB\u4E0D\u61C2\u3002\u8FD9\u79CD\u6307\u5357\u672C\u6765\u5C31\u662F\u7ED9\u533B\u751F\u770B\u7684\uFF0C\u5BB6\u957F\u8BFB\u4E0D\u61C2\u662F\u6B63\u5E38\u7684\u3002\u4E4B\u6240\u4EE5\u9009\u62E9\u627E\u4EBA\u7FFB\u8BD1\u8FD9\u7BC7\u6307\u5357\uFF0C\u662F\u56E0\u4E3A\u513F\u7AE5\u4E0A\u547C\u5438\u9053\u611F\u67D3\u592A\u5E38\u89C1\u4E86\uFF0C\u5F88\u591A\u5B69\u5B50\u56E0\u4E3A\u666E\u901A\u611F\u5192\u800C\u88AB\u7528\u4E86\u6297\u83CC\u7D20\uFF0C\u7F8E\u56FD\u4E5F\u662F\u5982\u6B64\uFF0C\u4E2D\u56FD\u66F4\u5982\u6B64\u3002</p><p>\u65E2\u7136\u8FD9\u4E2A\u8D26\u53F7\u7684\u4E3B\u8981\u8BFB\u8005\u5B69\u5B50\u5BB6\u957F\uFF0C\u4E3A\u5145\u5206\u5229\u7528\u8D44\u6E90\uFF0C\u6211\u5C31\u628A\u8FD9\u7BC7\u6307\u5357\u628A\u91CC\u4E00\u4E9B\u5BF9\u5BB6\u957F\u6709\u610F\u4E49\u7684\u77E5\u8BC6\uFF0C\u7ED3\u5408\u6211\u4EEC\u7684\u56FD\u60C5\u89E3\u8BFB\u4E00\u4E0B\u5206\u4EAB\u7ED9\u5927\u5BB6\u3002</p><p><strong>\u4E0D\u8981\u81EA\u884C\u7ED9\u5B69\u5B50\u5403\u6297\u751F\u7D20</strong></p><p>\u4EFB\u4F55\u4E00\u4E2A\u836F\uFF0C\u7ED9\u5B69\u5B50\u5403\u4E4B\u524D\u90FD\u8981\u5206\u6790\u4E00\u4E0B\uFF0C\u8FD9\u4E2A\u836F\u4F1A\u4F1A\u7ED9\u5B69\u5B50\u4EC0\u4E48\u597D\u5904\uFF0C\u4F1A\u5E26\u6765\u4EC0\u4E48\u574F\u5904\u3002\u8FD9\u4E2A\u5206\u6790\u7684\u57FA\u7840\u662F\u5BF9\u5B69\u5B50\u75C5\u60C5\u6709\u6B63\u786E\u7684\u5224\u65AD\uFF0C\u5BF9\u836F\u7269\u7684\u4F5C\u7528\u548C\u526F\u4F5C\u7528\u6709\u5145\u5206\u7684\u4E86\u89E3\uFF0C\u5982\u679C\u4F60\u5BF9\u75C5\u60C5\u5224\u65AD\u4E0D\u6E05\uFF0C\u5BF9\u836F\u54C1\u4E0D\u4E86\u89E3\uFF0C\u5C31\u4E0D\u8981\u968F\u4FBF\u7ED9\u5B69\u5B50\u5403\u836F\uFF0C\u6297\u751F\u7D20\u8FD9\u6837\u7684\u5904\u65B9\u836F\u66F4\u662F\u5982\u6B64\u3002</p><p>\u548C\u5176\u4ED6\u56FD\u5BB6\u4E00\u6837\uFF0C\u6297\u83CC\u7D20\u5728\u6211\u56FD\u4E5F\u662F\u5904\u65B9\u836F\uFF0C\u4F46\u663E\u7136\u7BA1\u7406\u4E0D\u90A3\u4E48\u89C4\u8303\uFF0C\u6CA1\u6709\u5904\u65B9\u4E0A\u836F\u5E97\u4E5F\u5E38\u5E38\u80FD\u4E70\u5230\uFF0C\u5F88\u591A\u5BB6\u5EAD\u90FD\u5E38\u5907\u7740\u6297\u751F\u7D20\u3002\u5B69\u5B50\u4E00\u751F\u75C5\u5BB6\u957F\u5C31\u5FC3\u6025\u5982\u711A\uFF0C\u80E1\u4E71\u7ED9\u5B69\u5B50\u5403\u6297\u751F\u7D20\u5F88\u5E38\u89C1\uFF0C\u603B\u89C9\u5F97\u5403\u70B9\u836F\u603B\u6BD4\u4E0D\u5403\u5F3A,\u4E5F\u4E0D\u7BA1\u662F\u4E0D\u662F\u7EC6\u83CC\u611F\u67D3\uFF0C\u4E5F\u4E0D\u77E5\u9053\u5403\u7684\u836F\u6709\u4EC0\u4E48\u4F5C\u7528\uFF0C\u53EF\u80FD\u4F1A\u6709\u4EC0\u4E48\u5371\u5BB3\u3002</p><p>\u4E0A\u547C\u5438\u9053\u611F\u67D3\uFF0C\u5927\u90E8\u5206\u662F\u75C5\u6BD2\u611F\u67D3\u5F15\u8D77\u7684\uFF0C\u4E0D\u662F\u7EC6\u83CC\u611F\u67D3\u7684\u8BDD\u5403\u6297\u751F\u7D20\u5BF9\u75C5\u60C5\u6CA1\u6709\u4EFB\u4F55\u4F5C\u7528\uFF0C\u6D6A\u8D39\u94B1\u4E0D\u8BF4\uFF0C\u53CD\u800C\u53EF\u80FD\u51FA\u73B0\u8FC7\u654F\u3001\u8179\u6CFB\u7B49\u95EE\u9898\uFF0C\u5B69\u5B50\u671F\u95F4\u63A5\u89E6\u6297\u751F\u7D20\u8FD8\u4F1A\u5BF9\u5065\u5EB7\u9020\u6210\u957F\u671F\u5F71\u54CD\uFF0C\u5BFC\u81F4\u5982\u708E\u75C7\u6027\u80A0\u75C5\u3001\u80A5\u80D6\u3001\u6E7F\u75B9\u548C\u54EE\u5598\u7B49\u98CE\u9669\u589E\u5927\u3002\u5B66\u533B\u8FD9\u4E48\u591A\u5E74\u7684\u533B\u751F\uFF0C\u4E5F\u7ECF\u5E38\u5224\u65AD\u4E0D\u51C6\uFF0C\u7136\u540E\u7528\u9519\u836F\uFF0C\u6CA1\u6709\u533B\u5B66\u57FA\u7840\u7684\u5BB6\u957F\u66F4\u6CA1\u6CD5\u5224\u65AD\u51C6\u786E\uFF0C\u7528\u9519\u836F\u66F4\u662F\u96BE\u514D\uFF0C\u7231\u5B50/\u5973\u4E4B\u5FC3+\u7126\u8651+\u65E0\u77E5=\u5BB3\u4E86\u5B69\u5B50\u3002</p><p><strong>\u8BE5\u7528\u6297\u751F\u7D20\u7684\u65F6\u5019\u8981\u7528</strong></p><p>\u56E0\u4E3A\u6297\u751F\u7D20\u7684\u6EE5\u7528\uFF0C\u6709\u4E9B\u5BB6\u957F\u53C8\u4ECE\u4E00\u4E2A\u6781\u7AEF\u8D70\u5411\u4E86\u53E6\u5916\u4E00\u4E2A\u6781\u7AEF\uFF0C\u8C08\u6297\u751F\u7D20\u8272\u53D8,\u4E0D\u7BA1\u4EC0\u4E48\u60C5\u51B5\uFF0C\u54EA\u6015\u771F\u7684\u7EC6\u83CC\u611F\u67D3\uFF0C\u533B\u751F\u5F00\u7684\u6297\u751F\u7D20\u4E5F\u4E0D\u7ED9\u5B69\u5B50\u5403\uFF0C\u5DE5\u4F5C\u4E2D\u4E5F\u5E38\u78B0\u5230\u8FD9\u6837\u7684\u5BB6\u957F\u3002</p><p>\u6297\u751F\u7D20\u867D\u7136\u6709\u526F\u4F5C\u7528\uFF0C\u4F46\u81EA\u9752\u9709\u7D20\u53D1\u660E\u4EE5\u6765\uFF0C\u6297\u751F\u7D20\u5DF2\u62EF\u6551\u4E86\u65E0\u6570\u4EBA\u7684\u751F\u547D\uFF0C\u6B63\u5982\u8FD9\u7BC7\u6307\u5357\u91CC\u4E5F\u5217\u4E3E\u4E86\u4E00\u4E9B\u60C5\u51B5\uFF0C\u4F7F\u7528\u6B63\u786E\u7684\u8BDD\uFF0C\u6297\u751F\u7D20\u662F\u5229\u5927\u4E8E\u5F0A\u7684\u3002\u75C5\u60C5\u9700\u8981\u7528\u7684\u65F6\u5019\u5982\u679C\u4E0D\u7528\uFF0C\u5C31\u53EF\u80FD\u5EF6\u8BEF\u75C5\u60C5\uFF0C\u6700\u540E\u5403\u4E8F\u7684\u8FD8\u662F\u81EA\u5DF1\u5B69\u5B50\u3002\u54EA\u4E9B\u60C5\u51B5\u8981\u7528\uFF0C\u8981\u7531\u533B\u751F\u6765\u51B3\u5B9A\uFF0C\u4E0D\u662F\u9760\u5BB6\u957F\u81EA\u5DF1\u51ED\u76F4\u89C9\u51ED\u611F\u60C5\u6765\u51B3\u5B9A\u7528\u4E0D\u7528\u3002</p><p><strong>\u533B\u751F\u4E71\u5F00\u836F\u600E\u4E48\u529E</strong></p><p>\u6297\u751F\u7D20\u662F\u5904\u65B9\u836F\uFF0C\u5E94\u8BE5\u7531\u533B\u751F\u6765\u51B3\u5B9A\u7528\u4E0D\u7528\uFF0C\u4F46\u533B\u751F\u6EE5\u5F00\u6297\u751F\u7D20\u4E5F\u662F\u4E0D\u4E89\u7684\u4E8B\u5B9E\uFF0C\u6B63\u5982\u8FD9\u7BC7\u6307\u5357\u91CC\u6240\u8BF4\uFF0C\u5373\u4FBF\u5728\u7F8E\u56FD\uFF0C\u6BCF\u5E74\u4E3A\u6CBB\u7597\u547C\u5438\u7CFB\u7EDF\u75BE\u75C5\u5F00\u51FA\u6CA1\u6709\u6CBB\u7597\u610F\u4E49\u7684\u6297\u751F\u7D20\u5904\u65B9\u8FBE\u4E0A\u5343\u4E07\u4EFD\u4E4B\u591A\uFF0C\u4E2D\u56FD\u80AF\u5B9A\u8FDC\u4E0D\u6B62\u8FD9\u4E2A\u6570\u5B57\u3002</p><p>\u6EE5\u7528\u7684\u539F\u56E0\u6709\u5F88\u591A\uFF0C\u6709\u5BA2\u89C2\u7684\u4E5F\u6709\u4E3B\u89C2\u7684\uFF0C\u6709\u65F6\u662F\u75C5\u60C5\u590D\u6742\uFF0C\u5F88\u96BE\u5224\u65AD\u662F\u7EC6\u83CC\u6027\u8FD8\u662F\u75C5\u6BD2\u6027\u611F\u67D3\uFF0C\u6709\u65F6\u53EF\u80FD\u548C\u4E5F\u533B\u751F\u6C34\u5E73\u6709\u5173\uFF0C\u5BF9\u75BE\u75C5\u548C\u836F\u54C1\u4E86\u89E3\u4E0D\u591F\u6DF1\u5165\uFF0C\u5F53\u7136\u6709\u65F6\u5019\u4E5F\u662F\u56E0\u4E3A\u533B\u751F\u81EA\u5DF1\u7684\u5229\u76CA\u3002</p><p>\u4EFB\u4F55\u533B\u5B66\u4E34\u5E8A\u6307\u5357\u91CC\u5BF9\u4E00\u4E2A\u836F\u7269\u4F7F\u7528\u7684\u6548\u76CA\u548C\u98CE\u9669\u5206\u6790\uFF0C\u90FD\u662F\u4ECE\u60A3\u8005\u89D2\u5EA6\u51FA\u53D1\uFF0C\u4F46\u5728\u73B0\u5B9E\u91CC\uFF0C\u533B\u751F\u505A\u533B\u7597\u9009\u62E9\u7684\u65F6\u5019\u96BE\u514D\u4E5F\u4F1A\u6743\u8861\u4E00\u4E0B\u81EA\u5DF1\u7684\u5229\u76CA\u3002\u5728\u533B\u60A3\u5173\u7CFB\u7D27\u5F20\u7684\u73AF\u5883\u4E0B\uFF0C\u5F53\u81EA\u5DF1\u533B\u7597\u51B3\u7B56\u53EF\u80FD\u5F71\u54CD\u5230\u81EA\u5DF1\u7684\u4EBA\u8EAB\u5B89\u5168\u65F6\uFF0C\u4E3A\u907F\u514D\u6F0F\u8BCA\u8BEF\u8BCA\u5E26\u6765\u7684\u75C5\u60C5\u5EF6\u8BEF\uFF0C\u533B\u751F\u5F88\u53EF\u80FD\u66F4\u613F\u610F\u9009\u62E9\u66F4\u79EF\u6781\u7684\u6CBB\u7597\uFF0C\u628A\u53EF\u7591\u7684\u7EC6\u83CC\u611F\u67D3\u5F53\u7EC6\u83CC\u611F\u67D3\u6765\u6CBB\u7597\uFF0C\u8BA9\u81EA\u5DF1\u5B89\u5FC3\u4E00\u70B9\u3002\u53E6\u5916\u5728\u6211\u56FD\u4EE5\u836F\u517B\u533B\u7684\u533B\u7597\u4F53\u5236\u4E0B\uFF0C\u8BCA\u7597\u8D39\u7528\u4F4E\u5EC9\uFF0C\u533B\u9662\u548C\u533B\u751F\u8981\u9760\u836F\u54C1\u6765\u7EF4\u6301\u6536\u5165\uFF0C\u5F53\u7136\u4E5F\u66F4\u5BB9\u6613\u5BFC\u81F4\u836F\u7269\u7684\u6EE5\u7528\u3002</p><p>\u6297\u751F\u7D20\u81EA\u5DF1\u4E0D\u80FD\u4E71\u5403\uFF0C\u4E0A\u533B\u9662\u9047\u5230\u533B\u751F\u4E71\u5F00\u836F\uFF0C\u8FD9\u662F\u60A3\u8005\u65E0\u6CD5\u6539\u53D8\u7684\u73B0\u5B9E\uFF0C\u6240\u80FD\u505A\u7684\u662F\u627E\u5230\u66F4\u503C\u5F97\u4FE1\u4EFB\u7684\u533B\u9662\u548C\u533B\u751F\u3002\u56FD\u5185\u7684\u6559\u6750\u3001\u6307\u5357\u548C\u53D1\u8FBE\u56FD\u5BB6\u76F8\u6BD4\u660E\u663E\u843D\u540E\uFF0C\u533B\u751F\u5B66\u4E60\u66F4\u65B0\u77E5\u8BC6\u9014\u5F84\u5F88\u6709\u9650\uFF0C\u8FD9\u4E5F\u662F\u6211\u627E\u4EBA\u7FFB\u8BD1\u8FD9\u4E9B\u6307\u5357\u7684\u4E00\u4E2A\u539F\u56E0\uFF0C\u5E76\u4E0D\u662F\u56E0\u4E3A\u4EC0\u4E48\u60C5\u6000\uFF0C\u800C\u662F\u56E0\u4E3A\u4ED8\u8D39\u9605\u8BFB\u6709\u4E86\u4E00\u4E9B\u6536\u5165\uFF0C\u6211\u53EF\u4EE5\u62FF\u90E8\u5206\u94B1\u7528\u8FD9\u79CD\u65B9\u5F0F\u56DE\u9988\u5927\u5BB6\uFF0C\u540C\u65F6\u53EF\u4EE5\u4E30\u5BCC\u4E00\u4E0B\u8FD9\u4E2A\u8D26\u53F7\u7684\u5185\u5BB9\uFF0C\u5229\u4EBA\u5229\u5DF1\u3002</p><p><strong>\u4E86\u89E3\u4E0A\u611F\u76F8\u5173\u5E38\u8BC6</strong></p><p>\u56E0\u4E3A\u513F\u7AE5\u4E0A\u547C\u5438\u9053\u611F\u67D3\u5F88\u5E38\u89C1\uFF0C\u5BB6\u957F\u7ECF\u5E38\u8981\u9762\u5BF9\uFF0C\u6240\u4EE5\u4E86\u89E3\u4E00\u4E9B\u76F8\u5173\u77E5\u8BC6\uFF0C\u8FD8\u662F\u4F1A\u5F88\u6709\u5E2E\u52A9\u3002\u6BD4\u5982\u8FD9\u7BC7\u6307\u5357\u91CC\u63D0\u5230\u7684\uFF1A\u611F\u5192\u6D41\u9F3B\u6D95\u6301\u7EED10\u5929\u4EE5\u4E0A\u8981\u8003\u8651\u9F3B\u7AA6\u708E\uFF0C\u5E94\u8BE5\u627E\u533B\u751F\u53BB\u68C0\u67E5\uFF1B\u9F3B\u7AA6\u708E\u8BCA\u65AD\u4E0D\u9700\u8981\u5E38\u89C4\u62CD\u7247\u6216\u8005\u505ACT\u3001\u78C1\u5171\u632F\uFF1B\u666E\u901A\u611F\u5192\u3001\u6025\u6027\u652F\u6C14\u7BA1\u708E\u8FD9\u4E9B\u5927\u5BB6\u7ECF\u5E38\u542C\u5230\u7684\u75C5\u7B49\u662F\u75C5\u6BD2\u611F\u67D3\uFF0C\u6CBB\u7597\u662F\u4EE5\u7F13\u89E3\u75C7\u72B6\u4E3A\u4E3B\uFF0C\u4E0D\u9700\u8981\u7528\u6297\u751F\u7D20\uFF1B\u963F\u9F50\u9709\u7D20\u4E0D\u662F\u6CBB\u7597\u4EFB\u4F55\u513F\u7AE5\u4E0A\u547C\u5438\u9053\u611F\u67D3\u7684\u4E00\u7EBF\u6297\u751F\u7D20\u3002\u7B49\u7B49...\u4E86\u89E3\u5F97\u8D8A\u591A\uFF0C\u81EA\u5DF1\u4E71\u7528\u836F\u7684\u53EF\u80FD\u6027\u5C31\u8D8A\u4F4E\uFF0C\u5B69\u5B50\u4E5F\u66F4\u5B89\u5168\u3002</p><p>\u4EE5\u4E0A\u5927\u6982\u662F\u8FD9\u7BC7\u6307\u5357\u5BF9\u5BB6\u957F\u4EEC\u7684\u610F\u4E49\uFF0C\u53E6\u5916\u6307\u5357\u91CC\u6709\u4E2A\u603B\u7ED3\u8868\uFF0C\u7CBE\u7B80\u5F97\u5F88\u597D\uFF0C\u4E0A\u6B21\u5FD8\u4E86\u8D34\u8FDB\u53BB\uFF0C\u91CD\u65B0\u5236\u56FE\u8865\u5145\u5728\u8FD9\u91CC\uFF0C\u6709\u5174\u8DA3\u7684\u53EF\u4EE5\u770B\u770B\u3002</p><p><strong>\u6743\u5A01\u7684\u80B2\u513F\u79D1\u666E\uFF0C\u6DF1\u5EA6\u7684\u533B\u5B66\u4EBA\u6587</strong></p><p><strong>\u4EC5\u63A8\u9001\u539F\u521B\u6587\u7AE0\uFF0C\u6B22\u8FCE\u5173\u6CE8drpeihg</strong></p>',20),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=204140161&idx=1&sn=e631506b130e7a130b93b4e089fa3d77&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},g=c("\u67E5\u770B\u539F\u6587");function h(m,f){const p=r("ExternalLinkIcon");return t(),n(a,null,[_,e("p",null,[e("a",l,[g,o(p)])])],64)}var b=d(i,[["render",h]]);export{b as default};
