import{r as t,o as n,c as r,a as e,b as a,F as o,d as i,e as s}from"./app.9aaa2256.js";import{_ as l}from"./plugin-vue_export-helper.21dcd24c.js";const c={},d=i('<h3 id="\u5B69\u5B50\u8BF4\u8BDD\u665A-\u53EF\u4EE5\u8BD5\u8BD5\u8FD93\u62DB" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u8BF4\u8BDD\u665A-\u53EF\u4EE5\u8BD5\u8BD5\u8FD93\u62DB" aria-hidden="true">#</a> \u5B69\u5B50\u8BF4\u8BDD\u665A\uFF0C\u53EF\u4EE5\u8BD5\u8BD5\u8FD93\u62DB</h3><p>\u5728\u6021\u79BE\u505A\u54A8\u8BE2\u5FEB\u4E00\u5E74\u4E86\uFF0C\u9047\u5230\u6BD4\u8F83\u591A\u7684\u4E00\u7C7B\u95EE\u9898\u662F\u62C5\u5FC3\u5B69\u5B50\u8BF4\u8BDD\u665A\uFF0C\u770B\u5230\u540C\u9F84\u7684\u5B69\u5B50\u54BF\u5440\u5B66\u8BED\uFF0C\u81EA\u5DF1\u7684\u5B69\u5B50\u5374\u8FD8\u662F\u201C\u95F7\u6CB9\u74F6\u201D\uFF0C\u786E\u5B9E\u5F88\u5BB9\u6613\u8BA9\u5BB6\u957F\u7126\u8651\u548C\u62C5\u5FC3\u3002\u600E\u6837\u624D\u80FD\u8BA9\u201C\u95F7\u6CB9\u74F6\u201D\u53D8\u6210\u201C\u5C0F\u8BDD\u75E8\u201D\uFF1F</p><p>\u8981\u56DE\u7B54\u8FD9\u4E2A\u95EE\u9898\uFF0C\u9996\u5148\u8981\u5224\u65AD\u5B69\u5B50\u8BF4\u8BDD\u662F\u4E0D\u662F\u771F\u7684\u665A\u3002\u56E0\u4E3A\u6BCF\u4E2A\u5B69\u5B50\u5728\u5404\u80FD\u533A\u7684\u53D1\u5C55\u6A21\u5F0F\u548C\u901F\u5EA6\u4E0D\u540C\uFF0C\u7279\u5B9A\u5E74\u9F84\u6BB5\u7684\u8BED\u8A00\u8868\u8FBE\u4E5F\u4F1A\u6709\u4E00\u4E9B\u4E2A\u4F53\u5316\u5DEE\u5F02\uFF0C\u6240\u4EE5\u5982\u679C\u5355\u5355\u76EF\u7740\u67D0\u4E2A\u5B69\u5B50\u7684\u8BED\u8A00\u6C34\u5E73\u53BB\u6BD4\u8F83\uFF0C\u6709\u53EF\u80FD\u4F1A\u589E\u52A0\u4E0D\u5FC5\u8981\u7684\u538B\u529B\u3002</p><p>\u4E0B\u8868\u4E3A0-2\u5C81\u5B69\u5B50\u8BED\u8A00\u53D1\u5C55\u7684\u91CC\u7A0B\u7891\uFF0C\u5927\u5BB6\u53EF\u4EE5\u4F5C\u4E3A\u53C2\u8003\uFF1A</p><p>\u56FE\u7247\u6765\u6E90\uFF1ADevelopment Through the Lifespan \u5F90\u6842\u51E4\u8BD1</p><p>\u53C2\u8003\u8FD9\u4E2A\u8868\u683C\uFF0C\u5982\u679C\u5B69\u5B50\u5728\u67D0\u4E2A\u6708\u9F84\u7684\u8BED\u8A00\u53D1\u5C55\u4E25\u91CD\u843D\u540E\uFF0C\u6BD4\u598212\u4E2A\u6708\u7684\u5B69\u5B50\u8FD8\u6CA1\u6709\u8FBE\u52306\u4E2A\u6708\u7684\u6C34\u5E73\uFF0C\u6216\u80052\u5C81\u7684\u5B69\u5B50\u8FD8\u6CA1\u6709\u8FBE\u52301\u5C81\u6216\u80051\u5C81\u534A\u7684\u6807\u51C6\uFF0C\u5EFA\u8BAE\u5E94\u627E\u4E13\u4E1A\u7684\u8BED\u8A00\u8BC4\u4F30\u6CBB\u7597\u5E08\u8FDB\u884C\u8BC4\u4F30\u3002\u90A3\u5982\u679C\u8BC4\u4F30\u771F\u7684\u662F\u8BED\u8A00\u53D1\u80B2\u843D\u540E\uFF0C\u6211\u4EEC\u8BE5\u600E\u4E48\u529E\u5462\uFF1F</p><p>\u8BED\u8A00\u53D7\u9057\u4F20\u3001\u6587\u5316\u4EE5\u53CA\u517B\u80B2\u65B9\u5F0F\u7684\u5F71\u54CD\u6BD4\u8F83\u5927\uFF0C\u5F88\u591A\u5728\u7279\u5B9A\u5E74\u9F84\u6BB5\u8BED\u8A00\u53D1\u80B2\u843D\u540E\u7684\u5B69\u5B50\u901A\u8FC7\u517B\u80B2\u65B9\u5F0F\u8C03\u6574\uFF0C\u662F\u53EF\u4EE5\u5FEB\u901F\u8FFD\u8D76\u4E0A\u540C\u9F84\u4EBA\u7684\u6C34\u5E73\u7684\u3002\u5177\u4F53\u53EF\u4EE5\u4ECE\u4EE5\u4E0B\u4E09\u4E2A\u65B9\u9762\u7740\u624B\uFF1A</p><p><strong>\u4E00\u3001\u63D0\u9AD8\u517B\u80B2\u8005\u7684\u8A00\u8BED\u4E30\u5BCC\u6027</strong></p><p>\u7814\u7A76\u53D1\u73B0\uFF0C\u517B\u80B2\u8005\u548C\u5B69\u5B50\u4E92\u52A8\u7684\u8BED\u8A00\u8D8A\u4E30\u5BCC\uFF0C\u5C31\u8D8A\u6709\u76CA\u4E8E\u5B69\u5B50\u7684\u8BED\u8A00\u53D1\u5C55\u3002\u5982\u679C\u6211\u4EEC\u548C\u5B69\u5B50\u7684\u4E92\u52A8\u53EA\u662F\u5C40\u9650\u4E8E\u65E5\u5E38\u751F\u6D3B\u5185\u5BB9\uFF0C\u90A3\u4E48\u6240\u4F7F\u7528\u7684\u8BED\u8A00\u5176\u5B9E\u662F\u5F88\u6709\u9650\u7684\uFF0C\u66F4\u591A\u662F\u5173\u4E8E\u5403\u559D\u62C9\u6492\u7B49\u51E0\u4E2A\u65B9\u9762\uFF0C\u800C\u8FD9\u8FD8\u8FDC\u8FDC\u4E0D\u591F\u3002\u8FD9\u79CD\u60C5\u51B5\u53EF\u4EE5\u4ECE\u51E0\u4E2A\u65B9\u9762\u7740\u624B\u6539\u53D8\uFF1A</p><ul><li><p>\u901A\u8FC7\u7ED8\u672C\u6545\u4E8B\u6765\u62D3\u5C55\u8BED\u8A00\u7684\u4E30\u5BCC\u6027\uFF1B</p></li><li><p>\u901A\u8FC7\u591A\u5E26\u5B69\u5B50\u51FA\u53BB\uFF0C\u6293\u4F4F\u5C3D\u53EF\u80FD\u591A\u7684\u673A\u4F1A\u5411\u5B69\u5B50\u4ECB\u7ECD\u65B0\u9C9C\u4E8B\u7269\uFF1B</p></li><li><p>\u901A\u8FC7\u9002\u5408\u5B69\u5B50\u5E74\u9F84\u7684\u6B4C\u66F2\u54FC\u5531\u6765\u5F15\u8D77\u5B69\u5B50\u5BF9\u65B0\u9C9C\u8BCD\u8BED\u53D1\u97F3\u7684\u5174\u8DA3\u3002</p></li></ul><p>\u6709\u5BB6\u957F\u53EF\u80FD\u4F1A\u53E6\u95EE\uFF1A\u53EF\u4E0D\u53EF\u4EE5\u8BA9\u5B69\u5B50\u770B\u7535\u89C6\u6765\u8BA9\u5176\u63A5\u89E6\u66F4\u591A\u7684\u8BED\u8A00\uFF1F</p><p>\u5173\u4E8E\u8FD9\u4E2A\u95EE\u9898\uFF0C\u5176\u5B9E\u5DF2\u7ECF\u6709\u4E0D\u5C11\u7684\u76F8\u5173\u7814\u7A76\u3002\u7814\u7A76\u7ED3\u679C\u53D1\u73B0\uFF0C\u6CA1\u6709\u4EA4\u4E92\u6027\u7684\u7535\u89C6\u8282\u76EE\u5BF9\u5B69\u5B50\u7684\u8BED\u8A00\u5E76\u6CA1\u6709\u660E\u663E\u4FC3\u8FDB\u4F5C\u7528\uFF0C\u5982\u679C\u5B69\u5B50\u6BCF\u5929\u770B\u7535\u89C6\u7684\u65F6\u95F4\u8FC7\u957F\uFF0C\u53CD\u800C\u4F1A\u56E0\u4E3A\u51CF\u5C11\u4E86\u4E0E\u5B69\u5B50\u7684\u4E92\u52A8\u65F6\u95F4\uFF0C\u800C\u5F71\u54CD\u4ED6\u4EEC\u7684\u8BED\u8A00\u53D1\u5C55\u3002</p><p>\u66F4\u63A8\u8350\u7684\u505A\u6CD5\u662F\uFF1A\u9009\u62E9\u9002\u5408\u5B69\u5B50\u5E74\u9F84\u7684\u3001\u6709\u4EA4\u4E92\u6027\u7684\u7535\u89C6\u8282\u76EE\uFF0C\u5E76\u4E14\u7531\u5BB6\u957F\u966A\u540C\u89C2\u770B\u3002\u5BB6\u957F\u966A\u540C\u89C2\u770B\uFF0C\u9664\u4E86\u53EF\u4EE5\u53CA\u65F6\u8C03\u6574\u8282\u76EE\u5185\u5BB9\uFF0C\u63A7\u5236\u89C2\u770B\u65F6\u95F4\uFF0C\u8FD8\u53EF\u4EE5\u5728\u7535\u89C6\u8282\u76EE\u7ED3\u675F\u540E\u5C31\u8282\u76EE\u4E2D\u7684\u5185\u5BB9\u548C\u5B69\u5B50\u4E92\u52A8\u8BA8\u8BBA\uFF0C\u5C06\u7535\u89C6\u8282\u76EE\u5BF9\u5B69\u5B50\u8BED\u8A00\u53D1\u5C55\u7684\u6709\u5229\u5F71\u54CD\u6700\u5927\u5316\u3002</p><p><strong>\u4E8C\u3001\u6539\u5584\u7236\u6BCD\u7684\u56DE\u5E94\u654F\u611F\u5EA6\u548C\u56DE\u5E94\u65B9\u5F0F</strong></p><p>\u5F88\u591A\u4EBA\u8BA4\u4E3A\u5B69\u5B50\u4EC0\u4E48\u90FD\u542C\u4E0D\u61C2\uFF0C\u6240\u4EE5\u758F\u4E8E\u548C\u5B69\u5B50\u7684\u8BED\u8A00\u4E92\u52A8\uFF0C\u5176\u5B9E\u8FD9\u79CD\u8BA4\u77E5\u662F\u4E0D\u5BF9\u7684\u3002\u7814\u7A76\u53D1\u73B0\uFF0C\u7236\u6BCD\u5BF9\u5B69\u5B50\u7684\u5404\u79CD\u4EA4\u6D41\u6027\u884C\u4E3A\u548C\u53D1\u97F3\u505A\u51FA\u654F\u611F\u7684\u56DE\u5E94\uFF0C\u80FD\u591F\u4FC3\u8FDB\u5B69\u5B50\u7684\u8BCD\u6C47\u589E\u52A0\u548C\u8BED\u8A00\u80FD\u529B\u53D1\u5C55\u3002</p><p>\u65B0\u751F\u513F\u5C31\u53EF\u4EE5\u533A\u5206\u5BF9\u8BDD\u6027\u8BED\u8A00\u548C\u975E\u8A00\u8BED\u6027\u53D1\u58F0\uFF0C5\u4E2A\u6708\u7684\u5A74\u513F\u5C31\u80FD\u5206\u8FA8\u51FA\u53E5\u5B50\u4E2D\u7684\u8BCD\u8BED\u91CD\u97F3\u5728\u54EA\u91CC\uFF0C10\u4E2A\u6708\u7684\u5B69\u5B50\u5C31\u53EF\u4EE5\u5728\u542C\u5230\u7684\u4E00\u53E5\u4E2D\u5206\u522B\u51FA\u4ED6\u7ECF\u5E38\u542C\u5230\u7684\u8BCD\u8BED\u3002\u8FD9\u4E9B\u90FD\u662F\u540E\u671F\u8868\u8FBE\u6027\u8BED\u8A00\u53D1\u5C55\u7684\u91CD\u8981\u57FA\u7840\u3002</p><p>\u5A74\u5E7C\u513F\u548C\u517B\u80B2\u8005\u7ECF\u5386\u7684\u5171\u540C\u6CE8\u610F\uFF08\u53CC\u65B9\u7684\u6CE8\u610F\u529B\u90FD\u5728\u540C\u4E00\u4E2A\u4E8B\u7269\u4E0A\uFF09\u8D8A\u9891\u7E41\uFF0C\u5B69\u5B50\u4EEC\u7684\u6CE8\u610F\u529B\u7EF4\u6301\u65F6\u95F4\u5C31\u4F1A\u8D8A\u957F\uFF0C\u80FD\u7406\u89E3\u66F4\u591A\u7684\u8BED\u8A00\uFF0C\u66F4\u65E9\u4EA7\u751F\u6709\u610F\u4E49\u7684\u80A2\u4F53\u8BED\u8A00\u548C\u53E3\u5934\u5B57\u8BCD\uFF0C\u8BCD\u6C47\u91CF\u7684\u6269\u589E\u4E5F\u4F1A\u66F4\u5927\u3002\u56E0\u4E3A\u901A\u5E38\u7236\u6BCD\u4F1A\u6559\u5B69\u5B50\u4ED6\u4EEC\u6240\u5171\u540C\u6CE8\u610F\u7684\u4E8B\u7269\u7684\u540D\u79F0\uFF0C\u6216\u8005\u5BF9\u5176\u505A\u51FA\u63CF\u8FF0\u548C\u89E3\u91CA\u3002\u4EB2\u5B50\u53CC\u65B9\u4E5F\u4F1A\u56E0\u4E3A\u5171\u540C\u6CE8\u610F\u4EA7\u751F\u66F4\u591A\u7684\u4E92\u52A8\u4EA4\u6D41\u3002</p><p>\u517B\u80B2\u8005\u5BF9\u5B69\u5B50\u54BF\u5440\u53D1\u97F3\u56DE\u5E94\u7684\u5E38\u89C1\u65B9\u5F0F\u6709\u4E24\u79CD\u3002\u4E00\u79CD\u662F\u91CD\u590D\u4E2D\u62D3\u5C55\u3002\u6BD4\u5982\u5B69\u5B50\u8BF4\uFF1A\u201C\u82F9\u679C\u201D\uFF0C\u517B\u80B2\u8005\u53EF\u4EE5\u8BF4:\u201C\u662F\u7684\uFF0C\u8FD9\u662F\u4E00\u4E2A\u7EA2\u8272\u7684\u5927\u82F9\u679C\u201D\u3002\u53E6\u4E00\u79CD\u662F\u91CD\u590D\u4E2D\u4FEE\u6B63\uFF0C\u6BD4\u5982\u5B69\u5B50\u8BF4\u201C\u6905\u5B50\u4E0A\u5B9D\u5B9D\u201D\uFF0C\u517B\u80B2\u8005\u53EF\u4EE5\u8BF4\uFF1A\u201C\u54E6\uFF0C\u5B9D\u5B9D\u5750\u5728\u81EA\u5DF1\u7684\u6905\u5B50\u4E0A\u5462\uFF01\u201D\u8FD9\u4E24\u79CD\u65B9\u5F0F\u90FD\u80FD\u5BF9\u5B9D\u5B9D\u7684\u8BED\u8A00\u5B66\u4E60\u4EA7\u751F\u6B63\u9762\u7684\u5F71\u54CD\u3002</p><p><strong>\u4E09\u3001\u5C0A\u91CD\u513F\u7AE5\u6C14\u8D28\u6027\u683C\u7279\u5F81\u7684\u5F71\u54CD</strong></p><p>\u6BCF\u4E2A\u5B69\u5B50\u90FD\u6709\u81EA\u5DF1\u72EC\u7279\u7684\u6C14\u8D28\u6027\u683C\u7279\u5F81\uFF0C\u6709\u4E9B\u5B69\u5B50\u542C\u5230\u65B0\u7684\u8BCD\u8BED\u53D1\u97F3\uFF0C\u5C31\u4F1A\u9A6C\u4E0A\u5F20\u53E3\u91CD\u590D\u6A21\u4EFF\uFF0C\u800C\u6709\u4E9B\u5B69\u5B50\u5728\u6CA1\u6709\u7EDD\u5BF9\u628A\u63E1\u524D\u5219\u7EDD\u4E0D\u4F1A\u5F00\u53E3\uFF0C\u4ED6\u4EEC\u66F4\u503E\u5411\u5728\u81EA\u5DF1\u72EC\u5904\u7684\u65F6\u5019\u201C\u52E4\u5B66\u82E6\u7EC3\u201D\u3002</p><p>\u5EFA\u8BAE\u5BB6\u957F\u5728\u548C\u5B69\u5B50\u4E92\u52A8\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u8981\u5C0A\u91CD\u5B69\u5B50\u7684\u7279\u70B9\uFF0C\u4EE5\u9F13\u52B1\u5F15\u5BFC\u4E3A\u4E3B\uFF0C\u5E76\u907F\u514D\u4EE5\u4E0B\u505A\u6CD5\uFF1A</p><ul><li><p>\u5BF9\u5B69\u5B50\u7684\u53D1\u97F3\u5632\u7B11\u6253\u51FB\uFF1B</p></li><li><p>\u5728\u5B69\u5B50\u4E0D\u613F\u610F\u5F00\u53E3\u65F6\u53CD\u590D\u50AC\u903C\uFF1B</p></li><li><p>\u5F3A\u8FEB\u5B69\u5B50\u5F53\u4F17\u8868\u6F14\u80CC\u8BD7\u5531\u6B4C\uFF1B</p></li><li><p>\u5728\u5B69\u5B50\u601D\u7D22\u8BED\u8A00\u642D\u914D\u65F6\u6025\u4E8E\u5E2E\u5FD9</p></li><li><p>\u56E0\u4E3A\u5B69\u5B50\u8BED\u8A00\u53D1\u5C55\u521D\u671F\u7684\u8BCD\u8BED\u91CD\u590D\uFF08\u6682\u65F6\u6027\u53E3\u5403\uFF09\u5C31\u6279\u8BC4\u60E9\u7F5A\u3002</p></li></ul><p>\u9047\u5230\u5B69\u5B50\u5BB3\u6015\u8BF4\u8BDD\uFF0C\u751A\u81F3\u62D2\u7EDD\u5F00\u53E3\u7684\u60C5\u51B5\uFF0C\u5BB6\u957F\u7684\u7126\u6025\u5FC3\u60C5\u53EF\u4EE5\u7406\u89E3\uFF0C\u4F46\u662F\u5982\u679C\u65B9\u6CD5\u4E0D\u5F53\uFF0C\u7ED3\u679C\u5F88\u53EF\u80FD\u9002\u5F97\u5176\u53CD\u3002\u6240\u4EE5\uFF0C\u8FD8\u662F\u6211\u4EEC\u5E38\u8BF4\u7684\uFF0C\u4E0D\u6D88\u6781\u5FFD\u7565\uFF0C\u4E0D\u63E0\u82D7\u52A9\u957F\uFF0C\u53EA\u6709\u201C\u9002\u5EA6\u704C\u6E89\u201D\uFF0C\u624D\u80FD\u9759\u5F85\u82B1\u5F00\u3002\uFF08\u672C\u6587\u4F5C\u8005\u5F90\u6842\u51E4\uFF0C\u5979\u7684\u5FAE\u4FE1\u516C\u4F17\u53F7<strong>xuguifeng82</strong>\uFF09</p>',23),_={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627279&idx=1&sn=6e6f81a476133d708d88bf89a8a3338d&chksm=8b5cb6f5bc2b3fe35d065d8df5d5902659cd9a3ae67b4efe066a18e441258057cb7e726f9d42&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},f=s("\u67E5\u770B\u539F\u6587");function h(m,u){const p=t("ExternalLinkIcon");return n(),r(o,null,[d,e("p",null,[e("a",_,[f,a(p)])])],64)}var b=l(c,[["render",h]]);export{b as default};
