import{r as p,o as r,c as t,a as e,b as c,F as n,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=o('<h3 id="\u4E00\u6B21\u79BB\u5947\u7684\u80A0\u955C-\u4E0B" tabindex="-1"><a class="header-anchor" href="#\u4E00\u6B21\u79BB\u5947\u7684\u80A0\u955C-\u4E0B" aria-hidden="true">#</a> \u4E00\u6B21\u79BB\u5947\u7684\u80A0\u955C\uFF08\u4E0B\uFF09</h3><p>\u4F5C\u8005\uFF1A\u6DF1\u5733\u5E02\u513F\u7AE5\u533B\u9662 \u88F4\u6D2A\u5C97\u533B\u751F</p><p>\uFF08\u63A5\u4E0A\u7BC7\uFF09\u95EE\u9898\u65E0\u6CD5\u9003\u907F\uFF0C\u6211\u81EA\u5DF1\u731C\u60F3\u6700\u5927\u7684\u53EF\u80FD\u6027\u5C31\u662F\u8FD8\u6709\u4E00\u4E2A\u606F\u8089\u6CA1\u53D1\u73B0\uFF0C\u56DE\u60F3\u7B2C\u4E00\u4E2A\u606F\u8089\u88AB\u5207\u4E0B\u6765\u540E\u968F\u7740\u6211\u9000\u51FA\u7684\u955C\u5B50\u6EDA\u52A8\u4E86\u4E00\u6BB5\uFF0C\u4E5F\u8BB8\u662F\u6211\u9000\u955C\u65F6\u6ED1\u8FC7\u4E00\u4E2A\u80A0\u76B1\u8936\u770B\u5230\u7684\u662F\u53E6\u5916\u4E00\u4E2A\u606F\u8089\uFF0C\u5374\u628A\u5B83\u5F53\u6210\u4E86\u6211\u5207\u4E0B\u540E\u6EDA\u4E0B\u6765\u7684\u90A3\u4E2A\u3002</p><p>\u4E0D\u7BA1\u600E\u4E48\u8BF4\uFF0C\u5982\u679C\u81EA\u5DF1\u9057\u6F0F\u4E86\u75C5\u7076\u80AF\u5B9A\u662F\u5931\u8BEF\u3002\u6211\u5982\u5B9E\u5411\u5BB6\u957F\u544A\u77E5\u6211\u7684\u60F3\u6CD5\uFF0C\u5E76\u544A\u8BC9\u4ED6\u4EEC\u8FD8\u5FC5\u987B\u518D\u505A\u4E00\u6B21\u80A0\u955C\u53BB\u786E\u8BA4\uFF0C\u5982\u679C\u771F\u7684\u8FD8\u6709\u4E00\u4E2A\u8FD8\u5F97\u5207\u4E00\u6B21\u3002\u53EF\u80FD\u4E5F\u662F\u770B\u6211\u6001\u5EA6\u5F88\u8BDA\u6073\uFF0C\u5BB6\u957F\u867D\u6709\u4E9B\u4E0D\u6EE1\u5012\u4E5F\u901A\u60C5\u8FBE\u7406\uFF0C\u8BF4\u6CA1\u5173\u7CFB\uFF0C\u53EA\u8981\u628A\u606F\u8089\u53D6\u6389\uFF0C\u5B69\u5B50\u6CA1\u4E8B\u5C31\u597D\u3002</p><p>\u4E8E\u662F\u5B89\u6392\u80A0\u9053\u51C6\u5907\uFF0C\u7981\u98DF\uFF0C\u5728\u5FD0\u5FD1\u4E0D\u5B89\u548C\u7126\u8651\u4E2D\u5EA6\u8FC7\u4E86\u51E0\u4E2A\u5C0F\u65F6\uFF0C\u518D\u6B21\u5F00\u59CB\u80A0\u955C\u68C0\u67E5\uFF0C\u987A\u5229\u8FDB\u955C\uFF0C\u5165\u518525cm\uFF0C\u770B\u5230\u4E86\u80A0\u58C1\u4E0A\u6628\u5929\u5207\u9664\u7684\u606F\u8089\u6B8B\u7AEF\uFF0C\u521B\u9762\u53D1\u767D\uFF0C\u5468\u56F4\u5145\u8840\uFF0C\u6C34\u80BF\u589E\u539A\uFF0C\u8BC1\u5B9E\u4E86\u6628\u5929\u7684\u606F\u8089\u662F\u5B8C\u6574\u5207\u9664\u7684\uFF0C\u4F46\u6CA1\u6709\u770B\u5230\u5176\u5B83\u7684\u606F\u8089\u554A\u3002\u7EE7\u7EED\u8FDB\u955C\u523070cm\uFF0C\u5230\u56DE\u76F2\u90E8\uFF0C\u7F13\u6162\u9000\u955C\uFF0C\u4ED4\u7EC6\u68C0\u67E5\u5347\u7ED3\u80A0\u3001\u6A2A\u7ED3\u80A0\u3001\u964D\u7ED3\u80A0\uFF0C\u4E59\u72B6\u7ED3\u80A0\uFF0C\u76F4\u80A0\uFF0C\u80A0\u58C1\u5149\u6ED1\uFF0C\u6CA1\u6709\u606F\u8089\uFF01\u6211\u624B\u5FC3\u5192\u6C57\u4E86\uFF0C\u5361\u4F4F\u8FC7\u7684\u606F\u8089\u54EA\u91CC\u53BB\u4E86\uFF1F\u56E0\u4E3A\u80A0\u9053\u51C6\u5907\u6709\u4E9B\u5306\u5FD9\uFF0C\u80A0\u8154\u5185\u6709\u5C11\u91CF\u5927\u4FBF\uFF0C\u6211\u6015\u5F71\u54CD\u89C6\u91CE\uFF0C\u5C31\u5728\u624B\u672F\u5BA4\u7ED9\u5B69\u5B50\u6D17\u8D77\u4E86\u80A0\uFF0C\u6D17\u5B8C\u540E\u518D\u8FDB\u955C\uFF0C\u4E00\u76F4\u5230\u56DE\u76F2\u90E8\uFF0C\u518D\u9000\u955C\uFF0C\u7741\u5927\u773C\u775B\uFF0C\u5C4F\u4F4F\u547C\u5438\u7684\u53C8\u770B\u4E86\u4E00\u904D\uFF0C\u9664\u4E86\u90A3\u4E2A\u5207\u9664\u540E\u7684\u521B\u9762\uFF0C\u4EC0\u4E48\u4E5F\u6CA1\u6709\u770B\u5230\u3002</p><p>\u6CA1\u6709\u606F\u8089\uFF0C\u8131\u51FA\u7684\u5305\u5757\u4E5F\u4E0D\u50CF\u76F4\u80A0\u8131\u5782\u90A3\u6837\u4E00\u5708\u80A0\u7BA1\uFF0C\u90A3\u5361\u5728\u809B\u95E8\u53E3\u7684\u662F\u4EC0\u4E48\u5462\uFF1F\u7A81\u7136\u60F3\u8D77\u539F\u6765\u90A3\u4E2A\u5305\u5757\u4E2D\u592E\u8FD8\u6709\u4E2A\u53D1\u767D\u7684\u521B\u9762\uFF0C\u4E0D\u662F\u548C\u73B0\u5728\u80A0\u955C\u4E0B\u770B\u5230\u7684\u606F\u8089\u521B\u9762\u4E00\u6837\u5417\uFF01\u5B69\u5B50\u5F88\u6D88\u7626\uFF0C\u4E59\u72B6\u7ED3\u80A0\u53EF\u80FD\u5F88\u677E\u5F1B\u6E38\u79BB\uFF0C\u5B69\u5B50\u5728\u672F\u524D\u5C31\u66FE\u7ECF\u6709\u8FC7\u606F\u8089\u8131\u51FA\u5361\u4F4F\u7684\u75C5\u53F2\uFF0C\u8BF4\u660E\u8FD9\u4E2A\u9AD8\u5EA6\u662F\u53EF\u4EE5\u8131\u51FA\u7684\uFF0C\u672F\u540E\u56E0\u4E3A\u606F\u8089\u5207\u9664\u540E\u7684\u521B\u9762\u5468\u56F4\u708E\u75C7\u53CD\u5E94\uFF0C\u4ECD\u7136\u6C34\u80BF\u80A5\u539A\uFF0C\u606F\u8089\u57FA\u5E95\u8FD9\u4E00\u5C0F\u5757\u6C34\u80BF\u80A5\u539A\u7684\u80A0\u58C1\u518D\u6B21\u8131\u51FA\u5361\u4F4F\u4E86\uFF0C\u8FD9\u79CD\u4E2D\u516D\u5408\u5F69\u822C\u7684\u673A\u4F1A\u7ED9\u6211\u78B0\u5230\u4E86\u3002</p><p>\u6211\u4E0D\u77E5\u9053\u662F\u8BE5\u9AD8\u5174\u8FD8\u662F\u96BE\u8FC7\u3002\u9AD8\u5174\u7684\u662F\u5B69\u5B50\u7684\u75DB\u82E6\u4E0D\u662F\u81EA\u5DF1\u9020\u6210\u7684\uFF0C\u96BE\u8FC7\u7684\u662F\u672F\u524D\u8DDF\u5BB6\u957F\u8BF4\u8FD8\u6709\u606F\u8089\uFF0C\u73B0\u5728\u5374\u53C8\u6CA1\u6709\uFF0C\u53C8\u8BE5\u5982\u4F55\u53BB\u89E3\u91CA\uFF1F\u4F46\u6CA1\u6709\u8FD8\u662F\u6BD4\u6709\u5F3A\uFF0C\u6BD5\u7ADF\u80A0\u5B50\u91CC\u6709\u80BF\u7269\u8131\u51FA\u5361\u538B\uFF0C\u5728\u4E0D\u660E\u786E\u4EC0\u4E48\u539F\u56E0\u7684\u60C5\u51B5\u4E0B\u505A\u80A0\u955C\u770B\u6E05\u695A\u80AF\u5B9A\u662F\u66F4\u5B89\u5168\u7684\u9009\u62E9\u3002</p><p>\u6211\u51FA\u95E8\u628A\u68C0\u67E5\u7684\u7ED3\u679C\u548C\u81EA\u5DF1\u7684\u5206\u6790\u8DDF\u5BB6\u957F\u8BB2\u4E86\u4E00\u4E0B\uFF0C\u5BB6\u957F\u5012\u4E5F\u9AD8\u5174\uFF0C\u6BD5\u7ADF\u4E0D\u7528\u518D\u5207\u606F\u8089\u4E86\uFF0C\u4F46\u4ED6\u4ECD\u7136\u6709\u4E9B\u62C5\u5FE7\u90A3\u4E2A\u5361\u4F4F\u7684\u662F\u4E0D\u662F\u606F\u8089\uFF0C\u6211\u8BF4\u60A8\u8981\u8FD8\u4E0D\u653E\u5FC3\u5C31\u8DDF\u7740\u4E00\u8D77\u518D\u770B\u4E00\u904D\u80A0\u955C\u4E00\u8D77\u6838\u5B9E\u5427\uFF0C\u5BB6\u957F\u8BF4\u597D\uFF0C\u6211\u4EEC\u53C8\u4E00\u8D77\u770B\u4E86\u4E00\u904D\uFF0C\u6838\u5B9E\u65E0\u8BEF\u540E\uFF0C\u7ED3\u675F\u4E86\u68C0\u67E5\uFF0C\u7B2C\u4E8C\u5929\u5B69\u5B50\u987A\u5229\u51FA\u9662\u4E86\uFF0C\u4E4B\u540E\u4E5F\u6CA1\u6709\u4EFB\u4F55\u5F02\u5E38\u3002</p><p>\u56DE\u5934\u60F3\u8FD9\u79CD\u60C5\u51B5\u4E3A\u4EC0\u4E48\u4F1A\u5224\u65AD\u9519\u8BEF\u5462\uFF0C\u9996\u5148\u8FD9\u4E2A\u5B69\u5B50\u521A\u521A\u505A\u4E86\u606F\u8089\u5207\u9664\uFF0C\u800C\u8FD9\u5361\u4F4F\u7684\u5305\u5757\u5916\u89C2\u786E\u5B9E\u4E5F\u5F88\u50CF\u606F\u8089\uFF0C\u6839\u636E\u6982\u7387\u5927\u5C0F\u6765\u63A8\u65AD\u9996\u5148\u80AF\u5B9A\u4E5F\u5E94\u8BE5\u8003\u8651\u606F\u8089\uFF0C\u5B69\u5B50\u4E4B\u524D\u8FD8\u6709\u8FC7\u606F\u8089\u8131\u51FA\u75C5\u53F2\uFF0C\u800C\u6211\u4EEC\u5207\u6389\u7684\u606F\u8089\u4F4D\u7F6E\u53C8\u90A3\u4E48\u9AD8\uFF0C\u505A\u80A0\u955C\u9057\u6F0F\u606F\u8089\uFF0C\u867D\u7136\u6211\u4EE5\u524D\u4E5F\u6CA1\u78B0\u5230\u8FC7\uFF0C\u4F46\u8FD9\u79CD\u53EF\u80FD\u6027\u8FD8\u662F\u5B58\u5728\u7684\u3002\u867D\u7136\u6709\u7591\u8651\uFF0C\u4F46\u6211\u4EEC\u4E09\u4E2A\u533B\u751F\u90FD\u7B2C\u4E00\u53CD\u5E94\u628A\u5B83\u5F53\u6210\u4E86\u606F\u8089\u3002\u6C34\u80BF\u7684\u80A0\u58C1\u8131\u51FA\u5361\u5728\u809B\u95E8\u53E3\u8FD9\u79CD\u60C5\u51B5\u4ECE\u6765\u6CA1\u89C1\u8FC7\uFF0C\u522B\u8BF4\u6839\u672C\u5C31\u60F3\u4E0D\u5230\uFF0C\u5C31\u7B97\u60F3\u5230\u4E86\uFF0C\u8FD9\u79CD\u5C11\u89C1\u7684\u60C5\u51B5\u4E5F\u80AF\u5B9A\u4E0D\u4F1A\u4F5C\u4E3A\u7B2C\u4E00\u8003\u8651\u3002</p><p>\u4E0D\u8BBA\u662F\u54EA\u4E00\u79CD\u60C5\u51B5\uFF0C\u518D\u505A\u4E00\u6B21\u80A0\u955C\u518D\u660E\u786E\u4E00\u4E0B\u539F\u56E0\u518D\u51B3\u5B9A\u600E\u4E48\u5904\u7406\u80AF\u5B9A\u8FD8\u662F\u66F4\u5408\u7406\uFF0C\u6240\u4EE5\u4E8B\u540E\u8FD8\u662F\u5F88\u5E86\u5E78\uFF0C\u867D\u7136\u627F\u53D7\u4E86\u5BB6\u957F\u7684\u4E00\u4E9B\u538B\u529B\uFF0C\u4F46\u8FD8\u662F\u505A\u4E86\u8FD9\u4E2A\u6B63\u786E\u7684\u9009\u62E9\uFF0C\u800C\u6CA1\u6709\u4E3A\u4E86\u7167\u987E\u5BB6\u957F\u60C5\u7EEA\u800C\u76F4\u63A5\u5728\u75C5\u623F\u53BB\u7ED3\u624E\u5207\u9664\u90A3\u4E2A\u5361\u4F4F\u7684\u201C\u606F\u8089\u201D\uFF0C\u4E0D\u7136\u5C31\u662F\u5207\u9664\u4E00\u5757\u80A0\u58C1\uFF0C\u540E\u679C\u5C31\u662F\u80A0\u7A7F\u5B54\u3002\u867D\u7136\u5207\u4E4B\u524D\u4E5F\u53EF\u80FD\u4F1A\u518D\u68C0\u67E5\u786E\u8BA4\uFF0C\u4F46\u5728\u5B69\u5B50\u4E0D\u914D\u5408\uFF0C\u81EA\u5DF1\u53C8\u7126\u8651\u7D27\u5F20\u7684\u72B6\u6001\u4E0B\uFF0C\u8C01\u80FD\u4FDD\u8BC1\u4E0D\u5934\u8111\u53D1\u70ED\u518D\u72AF\u9519\u5462\uFF1F</p><p>\u4EFB\u4F55\u4E00\u4E2A\u75C5\u60C5\uFF0C\u4F1A\u6709\u5F88\u591A\u53EF\u80FD\u6027\uFF0C\u533B\u751F\u6839\u636E\u6536\u96C6\u7684\u8BC1\u636E\uFF0C\u7EFC\u5408\u81EA\u5DF1\u7684\u77E5\u8BC6\u548C\u7ECF\u9A8C\u63A8\u7B97\u5404\u79CD\u53EF\u80FD\u6027\uFF0C\u6982\u7387\u5927\u7684\u6392\u5728\u524D\u9762\uFF0C\u6982\u7387\u5C0F\u7684\u6392\u5728\u540E\u9762\uFF0C\u6CA1\u78B0\u5230\u8FC7\u7684\u6839\u672C\u5C31\u60F3\u4E0D\u5230\u3002\u5C0F\u6982\u7387\u4E8B\u4EF6\u53D1\u751F\u4E86\uFF0C\u533B\u751F\u51FA\u73B0\u5224\u65AD\u9519\u8BEF\u7684\u673A\u4F1A\u5C31\u4F1A\u589E\u5927\uFF0C\u8FD8\u53EF\u80FD\u4F1A\u78B0\u5230\u4E00\u4E9B\u6781\u5C11\u89C1\u7684\u75C5\u60C5\uFF0C\u78B0\u5230\u4E86\u5C31\u662F\u4E00\u4E2A\u51B0\u7A9F\u7ABF\u3002\u91C7\u53D6\u5904\u7406\u63AA\u65BD\u7684\u65F6\u5019\u5FC5\u987B\u517C\u987E\u5404\u79CD\u53EF\u80FD\u6027\uFF0C\u5426\u5219\u5C31\u53EF\u80FD\u4E07\u52AB\u4E0D\u590D\u3002</p><p>\u884C\u533B\u7684\u98CE\u9669\u4E4B\u4E00\u662F\u4F60\u6C38\u8FDC\u4E0D\u77E5\u9053\u51B0\u7A9F\u7ABF\u5728\u54EA\u91CC\uFF0C\u53EA\u6709\u65F6\u523B\u5982\u5C65\u8584\u51B0\u3002</p><p>\uFF08\u63D0\u793A\uFF1A1.\u9898\u56FE\u6765\u6E90\u4E8E\u7F51\u7EDC2.\u4E86\u89E3\u66F4\u591A\u513F\u7AE5\u5916\u79D1\u75BE\u75C5\u77E5\u8BC6\uFF0C\u656C\u8BF7\u5173\u6CE8\uFF1Adrpei\uFF09</p>',13),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=10001681&idx=1&sn=5dccc295a9ac087732144f690cebb74b&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function m(f,x){const a=p("ExternalLinkIcon");return r(),t(n,null,[_,e("p",null,[e("a",l,[h,c(a)])])],64)}va