import{r,o as n,c as a,a as e,b as s,F as c,d as o,e as p}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const d={},h=o('<h3 id="\u9000\u70ED\u8D34\u80FD\u9000\u70ED\u5417" tabindex="-1"><a class="header-anchor" href="#\u9000\u70ED\u8D34\u80FD\u9000\u70ED\u5417" aria-hidden="true">#</a> \u9000\u70ED\u8D34\u80FD\u9000\u70ED\u5417\uFF1F</h3><p>\u4E0D\u77E5\u4F55\u65F6\u5F00\u59CB\uFF0C\u9000\u70ED\u8D34\u5DF2\u7ECF\u6210\u4E3A\u53D1\u70E7\u6807\u914D\uFF0C\u65E0\u8BBA\u5927\u4EBA\u5C0F\u5B69\uFF0C\u53EA\u8981\u53D1\u70E7\uFF0C\u65E0\u8BBA\u4F53\u6E29\u3001\u72B6\u6001\u5982\u4F55\uFF0C\u5148\u7ED9\u989D\u5934\u6765\u4E0A\u4E00\u8D34\u3002\u81F3\u4E8E\u8D34\u4E0A\u5230\u5E95\u6709\u6CA1\u6709\u7528\uFF0C\u4F1A\u7ED9\u5B69\u5B50\u5E26\u6765\u4EC0\u4E48\u611F\u53D7\uFF0C\u5E76\u6CA1\u6709\u4EBA\u5728\u610F\u3002</p><p>\u4E4B\u524D\u7684\u5728\u300A\u5982\u4F55\u5E94\u5BF9\u5B69\u5B50\u53D1\u70E7\u300B\u4E00\u6587(\u56DE\u590D48\u53EF\u67E5\u770B\uFF09\u91CC\u8BF4\u8FC7\uFF0C\u4F53\u6E29\u5347\u9AD8\u53EF\u4EE5\u51CF\u5C11\u5B69\u5B50\u4F53\u5185\u5FAE\u751F\u7269\u7684\u590D\u5236\u548C\u7E41\u6B96\uFF0C\u4E5F\u53EF\u4EE5\u63D0\u9AD8\u4EBA\u4F53\u7684\u708E\u75C7\u53CD\u5E94\uFF0C\u6709\u5229\u4E8E\u81F4\u75C5\u5FAE\u751F\u7269\u7684\u6E05\u9664\uFF0C\u53D1\u70E7\u5BF9\u5B69\u5B50\u7684\u75C5\u60C5\u6062\u590D\u662F\u6709\u5229\u7684\uFF0C\u4E00\u53D1\u70E7\u5C31\u60F3\u7740\u7ED9\u5B69\u5B50\u9000\u70E7\u672C\u6765\u5C31\u4E0D\u5BF9\uFF0C\u5173\u6CE8TA\u7684\u7CBE\u795E\u53CD\u5E94\u6BD4\u5173\u6CE8\u4F53\u6E29\u66F4\u91CD\u8981\u3002</p><p>\u5B69\u5B50\u53D1\u70E7\uFF0C\u7B2C\u4E00\u539F\u5219\u662F\u8981\u5C3D\u91CF\u8BA9\u5B69\u5B50\u8212\u670D\uFF0C\u6309\u7167\u8FD9\u6761\u539F\u5219\uFF0C\u5F53\u5B69\u5B50\u6CA1\u6709\u660E\u663E\u7684\u4E0D\u8212\u670D\u65F6\uFF0C\u9664\u4E86\u8BA9\u4ED6\u591A\u559D\u6C34\uFF0C\u5C11\u7A7F\u8863\uFF0C\u5E76\u4E0D\u9700\u8981\u505A\u4EC0\u4E48\u4EFB\u4F55\u6CBB\u7597\uFF0C\u81EA\u7136\u4E5F\u4E0D\u9700\u8981\u8D34\u9000\u70ED\u8D34\u3002\u5373\u4FBF\u771F\u7684\u4F53\u6E29\u9AD8\u4E86\uFF0C\u8D85\u8FC739\xB0C\u6216\u5B69\u5B50\u5F88\u96BE\u53D7\uFF0C\u9700\u8981\u9000\u70E7\u4E86\uFF0C\u9000\u70ED\u8D34\u6709\u4F5C\u7528\u5417\uFF1F</p><p>\u9000\u70ED\u7684\u65B9\u6CD5\u5982\u679C\u6709\u6548\uFF0C\u81EA\u7136\u4F1A\u6709\u4E13\u4E1A\u673A\u6784\u63A8\u8350\uFF0C\u76EE\u524D\u516C\u8BA4\u6709\u6548\u7684\u9000\u70E7\u65B9\u6CD5\u662F\u9000\u70E7\u836F\uFF0C\u4E5F\u5C31\u662F\u5E03\u6D1B\u82AC\u548C\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u3002\u7528\u9000\u70ED\u836F\u7684\u4E3B\u8981\u76EE\u7684\u4E5F\u662F\u4E3A\u4E86\u7F13\u89E3\u5B69\u5B50\u7684\u75BC\u75DB\uFF0C\u800C\u4E0D\u662F\u7EAF\u7CB9\u4E3A\u4E86\u9000\u70E7\u3002[1]\u5173\u4E8E\u9000\u70E7\u7684\u6CBB\u7597\uFF0C\u65E0\u8BBA\u662F\u4E2D\u56FD\u7684\u513F\u7AE5\u53D1\u70ED\u6307\u5357\uFF0C\u8FD8\u662F\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A[2]\u3001\u52A0\u62FF\u5927\u513F\u79D1\u533B\u751F\u534F\u4F1A[1]\u3001\u8FD8\u662F\u82F1\u56FDNICE[3]\u91CC\u90FD\u6CA1\u6709\u63D0\u5230\u9000\u70ED\u8D34\uFF0C\u9000\u70ED\u8D34\u4E3B\u8981\u6D41\u884C\u4E8E\u4E1C\u4E9A\u7684\u56FD\u5BB6\u3002</p><p>\u9000\u70ED\u8D34\u600E\u4E48\u9000\u70E7\uFF1F\u6709\u7684\u8BF4\u660E\u4E66\u4E0A\u8BF4\u662F\u9760\u6C34\u51DD\u80F6\u7684\u6C34\u6C7D\u6325\u53D1\u5E26\u8D70\u70ED\u91CF\uFF0C\u8FD9\u5176\u5B9E\u5C31\u662F\u7269\u7406\u964D\u6E29\u7684\u65B9\u5F0F\u3002\u800C\u4E8B\u5B9E\u4E0A\uFF0C\u522B\u8BF4\u4E00\u5757\u8FD8\u6CA1\u5DF4\u638C\u5927\u7684\u6C34\u51DD\u80F6\uFF0C\u5C31\u662F\u628A\u5B69\u5B50\u6CE1\u5728\u6E29\u6C34\u91CC\u964D\u6E29\u6548\u679C\u4E5F\u975E\u5E38\u6709\u9650\u3002\u5C3C\u5C14\u68EE\u513F\u79D1\u5B66\u660E\u786E\u8BF4\u7269\u7406\u964D\u6E29\u5E76\u6CA1\u6709\u6548\u679C\uFF0C\u82F1\u56FD[3]\u548C\u52A0\u62FF\u5927[1]\u90FD\u4E0D\u63A8\u8350\u6E29\u6C34\u64E6\u6D74\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u4E5F\u662F\u8BA4\u4E3A\u7EDD\u5927\u90E8\u5206\u60C5\u51B5\u4E0B\u836F\u7269\u964D\u6E29\u66F4\u65B9\u4FBF\uFF0C\u5F53\u5B69\u5B50\u5BF9\u9000\u70E7\u836F\u4E0D\u8010\u53D7\u6216\u8FC7\u654F\u624D\u53EF\u4EE5\u4F18\u5148\u8003\u8651\u6E29\u6C34\u64E6\u6D74[4]\u3002</p><p>\u6709\u4E9B\u5BB6\u957F\u559C\u6B22\u7528\u9000\u70ED\u8D34\u7684\u53E6\u5916\u4E00\u4E2A\u539F\u56E0\u53EF\u80FD\u662F\u89C9\u5F97\u53D1\u70E7\u8981\u4FDD\u62A4\u8111\u888B\uFF0C\u56E0\u4E3A\u5F88\u591A\u4EBA\u8BA4\u4E3A\u53D1\u70E7\u4F1A\u70E7\u574F\u8111\u888B\uFF0C\u800C\u4E8B\u5B9E\u662F\u9664\u975E\u662F\u8D85\u8FC741\xB0C\u7684\u8D85\u9AD8\u70ED\u6216\u9885\u5185\u611F\u67D3\u5F15\u8D77\u7684\u53D1\u70E7\uFF0C\u53D1\u70E7\u5E76\u4E0D\u4F1A\u70E7\u574F\u8111\u5B50\u3002\u800C\u4E14\u4EBA\u4F53\u5185\u7684\u6E29\u5EA6\u4F1A\u901A\u8FC7\u8840\u6DB2\u7684\u6D41\u52A8\u8FBE\u5230\u5E73\u8861\u7684\uFF0C\u4E0D\u53EF\u80FD\u901A\u8FC7\u7ED9\u989D\u5934\u7684\u4E00\u5C0F\u5757\u76AE\u80A4\u964D\u6E29\u6765\u964D\u4F4E\u9885\u5185\u7684\u6E29\u5EA6\u7684\u3002\u8FD8\u6709\u4E9B\u9000\u70ED\u8D34\u91CC\u9762\u6DFB\u52A0\u4E86\u51B0\u7247\u3001\u8584\u8377\u3001\u6849\u53F6\u6CB9\u4E4B\u7C7B\u7684\u7269\u8D28\uFF0C\u8FD9\u4E9B\u4E1C\u897F\u4F1A\u8BA9\u5C40\u90E8\u76AE\u80A4\u611F\u89C9\u51C9\u610F\uFF0C\u4F46\u5E76\u4E0D\u662F\u771F\u7684\u7684\u964D\u6E29\u3002</p><p>\u6211\u4EEC\u77E5\u9053\u9000\u70ED\u6700\u4E3B\u8981\u7684\u539F\u5219\u662F\u8BA9\u5B69\u5B50\u8212\u670D\uFF0C\u9000\u70ED\u8D34\u7ED9\u989D\u5934\u5C40\u90E8\u5E26\u6765\u7684\u8FD9\u79CD\u51C9\u610F\u5E76\u4E0D\u662F\u6240\u6709\u5B69\u5B50\u90FD\u80FD\u8010\u53D7\uFF0C\u6709\u4E9B\u5BB6\u957F\u4E5F\u4F1A\u53D1\u73B0\u5B69\u5B50\u6297\u62D2\u8FD9\u4E2A\u4E1C\u897F\uFF0C\u5176\u5B9E\u81EA\u5DF1\u4E5F\u53EF\u4EE5\u611F\u53D7\u4E00\u4E0B\uFF0C\u5728\u81EA\u5DF1\u989D\u5934\u4E0A\u7CCA\u4E00\u5757\u9ECF\u7CCA\u7CCA\u51B0\u51B0\u51C9\u7684\u4E1C\u897F\u4F1A\u4E0D\u4F1A\u8212\u670D\u3002\u5F53\u7136\u8212\u4E0D\u8212\u670D\u5B69\u5B50\u81EA\u5DF1\u8BF4\u4E86\u7B97\uFF0C\u5982\u679C\u4F60\u5B69\u5B50\u5C31\u662F\u559C\u6B22\u8D34\u8FD9\u4E2A\u90A3\u4E5F\u4E0D\u662F\u4E0D\u53EF\u4EE5\uFF0C\u4F46\u8981\u77E5\u9053\u4E0D\u662F\u6240\u6709\u7684\u5B69\u5B50\u90FD\u80FD\u8868\u8FBE\uFF0C\u6709\u65F6\u5019\u4ED6\u53EA\u662F\u65E0\u6CD5\u62D2\u7EDD\u4F60\u3002\u4F46\u4F60\u4E5F\u8981\u77E5\u9053\uFF0C\u6709\u4E9B\u5B69\u5B50\u8D34\u5B8C\u8FD9\u4E2A\u8FD8\u8FC7\u654F\u3002[5]\u90A3\u5C31\u662F\u662F\u82B1\u94B1\u8BA9\u5B69\u5B50\u53D7\u7F6A\u3002\u4F60\u81EA\u5DF1\u53EF\u4EE5\u7528\u201C\u9000\u70ED\u8D34\u8FC7\u654F\u201D\u4E0A\u7F51\u641C\u4E00\u4E0B\u3002</p><p>\u90A3\u9000\u70ED\u8D34\u662F\u4E0D\u662F\u4E00\u70B9\u597D\u5904\u90FD\u6CA1\u6709\u5462\uFF1F\u53EF\u80FD\u4E5F\u4E0D\u80FD\u8FD9\u4E48\u8BF4\uFF0C\u8DDF\u5B55\u5987\u7A7F\u7740\u8F90\u5C04\u670D\u4E00\u6837\uFF0C\u5B69\u5B50\u8D34\u7740\u9000\u70ED\u8D34\u5C31\u50CF\u8D34\u7740\u4E00\u4E2A\u201C\u6211\u751F\u75C5\u4E86\u201D\u7684\u6807\u7B7E\uFF0C\u4E5F\u8BB8\u53EF\u4EE5\u8BA9\u522B\u7684\u5B69\u5B50\u79BBTA\u8FDC\u4E00\u70B9\u3002\u53E6\u5916\uFF0C\u5B69\u5B50\u4E00\u53D1\u70E7\u5F88\u591A\u5BB6\u957F\u5C31\u5F88\u7126\u8651\uFF0C\u4E0D\u77E5\u9053\u81EA\u5DF1\u8BE5\u600E\u4E48\u529E\uFF0C\u7ED9\u5B69\u5B50\u8D34\u4E0A\u4E2A\u9000\u70ED\u8D34\uFF0C\u597D\u6B79\u89C9\u5F97\u81EA\u5DF1\u505A\u4E86\u70B9\u4EC0\u4E48\u3002</p><p>\u5F88\u591A\u5BB6\u957F\u4E5F\u4F1A\u8BF4\u5230\u4E86\u533B\u9662\u533B\u751F\u4E5F\u662F\u5148\u7ED9\u9000\u70ED\u8D34\u554A\uFF0C\u786E\u5B9E\uFF0C\u5C31\u8DDF\u5F88\u591A\u533B\u751F\u76F8\u4FE1\u4E2D\u836F\u6709\u7528\u4E00\u6837\uFF0C\u6709\u4E9B\u533B\u751F\u4E5F\u76F8\u4FE1\u9000\u70ED\u8D34\u6709\u7528\uFF0C\u867D\u7136\u4ED6\u4EEC\u4E5F\u6CA1\u770B\u5230\u8FC7\u8BC1\u636E\u3002\u53E6\u5916\uFF0C\u5F88\u591A\u53D1\u70E7\u7684\u5B69\u5B50\u672C\u6765\u5C31\u4E0D\u9700\u8981\u4EC0\u4E48\u7279\u6B8A\u6CBB\u7597\uFF0C\u4F46\u5BB6\u957F\u7126\u8651\uFF0C\u533B\u751F\u4E5F\u4F1A\u5F00\u70B9\u8FD9\u4E9B\u53EF\u80FD\u6CA1\u597D\u5904\u7684\u4E1C\u897F\u6765\u5B89\u629A\u4E00\u4E0B\u5BB6\u957F\uFF0C\u4F60\u770B\uFF0C\u6211\u4E5F\u597D\u6B79\u4E3A\u4F60\u5B69\u5B50\u505A\u4E86\u70B9\u4EC0\u4E48\uFF0C\u8BF4\u767D\u4E86\u5C31\u662F\u4E00\u4E2A\u5B89\u6170\u5242\uFF0C\u53EA\u4E0D\u8FC7\u6210\u672C\u6709\u70B9\u5927\uFF0C\u6709\u7684\u4E00\u76D2\u597D\u51E0\u5341\u5757\u94B1\u5462\uFF0C\u9047\u5230\u8FC7\u654F\u7684\u5C31\u66F4\u51A4\u4E86\u3002\uFF08\u56FE\u7247\u6765\u81EA\u7F51\u7EDC\uFF09</p><p><strong>\u53C2\u8003\u8D44\u6599:</strong></p><p><strong>1</strong>.http://www.caringforkids.cps.ca/handouts/fever_and_temperature_taking</p><p><strong>2</strong>.http://pediatrics.aappublications.org/content/127/3/580.full?sid=7a715590-d88f-44c5-9a0e-9032c2481cc1</p><p><strong>3</strong>.https://www.nice.org.uk/guidance/cg160/resources/guidance-feverish-illness-in-children-pdf</p><p><strong>4</strong>.http://www.healthychildren.org/English/health-issues/conditions/fever/Pages/Treating-a-Fever-Without-Medicine.aspx</p><p><strong>5</strong>.http://wap.deyi.com/thread-3426126-1-1.html</p><p><strong>\u6743\u5A01\u7684\u80B2\u513F\u79D1\u666E\uFF0C\u6DF1\u5EA6\u7684\u533B\u5B66\u4EBA\u6587</strong></p><p><strong>\u4EC5\u63A8\u9001\u539F\u521B\u6587\u7AE0\uFF0C\u6B22\u8FCE\u5173\u6CE8drpeihg</strong></p>',18),g={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=200608995&idx=1&sn=1f8c868246b5db3c63ec7bd9c2a83b20&chksm=1ea5251929d2ac0f0f069b4cecb564966fd7e4baec1532c16b0ac2f2effe7c19a87e4ff6f45d&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=p("\u67E5\u770B\u539F\u6587");function f(_,u){const t=r("ExternalLinkIcon");return n(),a(c,null,[h,e("p",null,[e("a",g,[l,s(t)])])],64)}var w=i(d,[["render",f]]);export{w as default};
