import{r,o as t,c as n,a as p,b as o,F as a,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=s('<h3 id="\u5B9D\u5B9D\u7761\u524D-\u53C8\u6478\u53C8\u54AC-\u600E\u4E48\u529E" tabindex="-1"><a class="header-anchor" href="#\u5B9D\u5B9D\u7761\u524D-\u53C8\u6478\u53C8\u54AC-\u600E\u4E48\u529E" aria-hidden="true">#</a> \u5B9D\u5B9D\u7761\u524D\u201C\u53C8\u6478\u53C8\u54AC\u201D\uFF0C\u600E\u4E48\u529E\uFF1F</h3><p>\u201C\u6211\u5BB6\u5B9D\u5B9D\u6BCF\u5929\u7761\u89C9\u524D\u4E00\u5B9A\u8981\u6478\u7740\u6211\u7684\u8138\u624D\u80FD\u7761\u7740\uFF0C\u5982\u679C\u4E0D\u8BA9\u6478\uFF0C\u5979\u5C31\u4F1A\u5927\u54ED\uFF0C\u600E\u4E48\u90FD\u4E0D\u7761\uFF0C\u8BF7\u95EE\u6709\u4EC0\u4E48\u529E\u6CD5\u53EF\u4EE5\u6539\u6389\u5979\u8FD9\u4E2A\u574F\u4E60\u60EF\u5462\uFF1F\u201D</p><p>\u7C7B\u4F3C\u7684\u54A8\u8BE2\u5176\u5B9E\u5F88\u591A\uFF0C\u5F88\u591A\u5988\u5988\u53D1\u73B0\u81EA\u5DF1\u5B9D\u5B9D\u5165\u7761\u6709\u5F88\u591A\u201C\u5C0F\u52A8\u4F5C\u201D\uFF0C\u6BD4\u5982\uFF1A\u6478\u5988\u5988\u7684\u8138\u3001\u8033\u6735\u3001\u4E73\u623F\uFF1B\u62A0\u5988\u5988\u7684\u9F3B\u5B54\u3001\u8033\u6D1E\uFF1B\u5403\u624B\u3001\u5438\u542E\u81EA\u5DF1\u7684\u5634\u5507\u3001\u5543\u54AC\u8863\u7269\u7B49\u3002\u4E3A\u4E86\u4E0D\u8BA9\u5B69\u5B50\u8FD9\u6837\u505A\uFF0C\u5C1D\u8BD5\u4E86\u5F88\u591A\u65B9\u6CD5\u90FD\u6CA1\u4EC0\u4E48\u6548\u679C\uFF0C\u5BB6\u957F\u89C9\u5F97\u5F88\u82E6\u607C\u3002</p><p><strong>\u4E3A\u4EC0\u4E48\u5B9D\u5B9D\u4F1A\u6709\u8FD9\u4E9B\u5165\u7761\u524D\u7684\u201C\u5C0F\u52A8\u4F5C\u201D\u5462\uFF1F</strong></p><p>\u5B9D\u5B9D\u7684\u8FD9\u79CD\u884C\u4E3A\uFF0C\u901A\u5E38\u8BA4\u4E3A\u662F\u7761\u524D\u7684\u81EA\u6211\u5B89\u629A\u884C\u4E3A\u3002\u5165\u7761\u524D\u5F80\u5F80\u662F\u5B69\u5B50\u5728\u4E00\u5929\u4E2D\u5B89\u5168\u611F\u6700\u4F4E\u7684\u65F6\u5019\uFF0C\u5F88\u591A\u5B69\u5B50\u4F1A\u6297\u62D2\u5165\u7761\uFF0C\u9700\u8981\u4F7F\u7528\u4E00\u4E9B\u81EA\u6211\u5B89\u629A\u884C\u4E3A\u624D\u80FD\u8BA9\u81EA\u5DF1\u5B89\u5B9A\u4E0B\u6765\u3002</p><p>\u6709\u7684\u5B9D\u5B9D\u9009\u62E9\u624B\u90E8\u7684\u52A8\u4F5C\uFF0C\u6BD4\u5982\u6478\u3001\u634F\u3001\u6320\u6765\u5B89\u629A\u81EA\u5DF1\uFF0C\u6709\u7684\u5B9D\u5B9D\u5219\u9009\u62E9\u5438\u542E\u6216\u8005\u5543\u54AC\uFF0C\u5C24\u5176\u662F\u4E00\u76F4\u4E60\u60EF\u5976\u7761\u7684\u5B9D\u5B9D\uFF0C\u65E0\u8BBA\u662F\u6BCD\u4E73\u5582\u517B\u8FD8\u662F\u5976\u7C89\u5582\u517B\u7684\u5B9D\u5B9D\u90FD\u53EF\u80FD\u51FA\u73B0\u3002</p><p>\u662F\u7684\uFF0C\u5B9D\u5B9D\u5BF9\u4F60\u7684\u6478\u3001\u6293\u3001\u6320\u3001\u54AC\u90FD\u662F\u4ED6\u4E0B\u610F\u8BC6\u7684\u81EA\u6211\u5B89\u629A\u4EE5\u53CA\u5BF9\u4F60\u6EE1\u6EE1\u7684\u4F9D\u604B\uFF0C\u5E76\u4E0D\u662F\u6545\u610F\u8981\u201C\u4F24\u5BB3\u201D\u4F60\u5462\u3002</p><p>\u6709\u7684\u5988\u5988\u4F1A\u5F88\u81EA\u8D23\u5730\u95EE\uFF1A\u201C\u6211\u542C\u522B\u4EBA\u8BF4\uFF0C\u592A\u65E9\u7ED9\u5B69\u5B50\u65AD\u5976\u4F1A\u5F71\u54CD\u5B69\u5B50\u7684\u5B89\u5168\u611F\uFF0C\u662F\u56E0\u4E3A\u6211\u65AD\u5976\u592A\u65E9\uFF0C\u624D\u8BA9\u5B69\u5B50\u51FA\u73B0\u8FD9\u79CD\u60C5\u51B5\u5417\uFF1F\u201D</p><p>\u5B69\u5B50\u5982\u679C\u6709\u8FD9\u4E2A\u95EE\u9898\uFF0C\u5927\u53EF\u4E0D\u5FC5\u81EA\u8D23\u3002\u6211\u4EEC\u786E\u5B9E\u63D0\u5021\u81EA\u7136\u79BB\u4E73\uFF0C\u5728\u4E0D\u5F97\u4E0D\u505C\u6B62\u6BCD\u4E73\u7684\u65F6\u5019\u63D0\u5021\u4EE5\u6E29\u548C\u7684\u65B9\u5F0F\u9010\u6E10\u79BB\u4E73\uFF0C\u4F46\u5DF2\u7ECF\u7528\u201C\u6BCD\u5B50\u5206\u79BB\u6CD5\u201D\u65AD\u5976\u7684\u5988\u5988\u4EEC\u4E5F\u4E0D\u5FC5\u56E0\u6B64\u800C\u7EA0\u7ED3\u3002\u5B69\u5B50\u7684\u884C\u4E3A\u7279\u5F81\u662F\u7531\u5404\u79CD\u5148\u5929\u540E\u5929\u56E0\u7D20\u957F\u671F\u4F5C\u7528\u7684\u7ED3\u679C\uFF0C\u4E00\u4E2A\u72EC\u7ACB\u7684\u56E0\u7D20\u5F88\u96BE\u6709\u51B3\u5B9A\u6027\u7684\u4F5C\u7528\u3002</p><p><strong>\u90A3\u4E48\uFF0C\u6211\u4EEC\u5E94\u8BE5\u600E\u4E48\u529E\u5462\uFF1F</strong></p><p>\u6709\u7684\u5988\u5988\u5BF9\u5B9D\u5B9D\u7684\u8FD9\u79CD\u884C\u4E3A\u91C7\u7528\u4E86\u575A\u51B3\u5236\u6B62\u7684\u65B9\u6CD5\uFF0C\u6BD4\u5982\u63E1\u4F4F\u5B69\u5B50\u7684\u624B\uFF0C\u6216\u8005\u4E0D\u7ED9\u5B69\u5B50\u63D0\u4F9B\u53EF\u4EE5\u5543\u54AC\u7684\u7269\u4F53\uFF0C\u751A\u81F3\u6709\u7684\u5988\u5988\u4E3A\u4E86\u4E0D\u8BA9\u5B9D\u5B9D\u5438\u542E\u624B\u6307\u7ED9\u5B69\u5B50\u6234\u4E0A\u624B\u5957\uFF0C\u6216\u8005\u5728\u624B\u4E0A\u6D82\u4E0A\u82E6\u8FA3\u7684\u6DB2\u4F53\uFF0C\u7ED3\u679C\u5374\u53D1\u73B0\u5B69\u5B50\u975E\u5E38\u7126\u8E81\u4E0D\u5B89\uFF0C\u4F9D\u7136\u96BE\u4EE5\u5165\u7761\uFF0C\u800C\u4E14\u4E00\u65E6\u505C\u7528\u8FD9\u4E9B\u65B9\u6CD5\u540E\uFF0C\u5B69\u5B50\u7684\u7761\u524D\u5C0F\u52A8\u4F5C\u4F1A\u66F4\u9891\u7E41\u3002</p><p>\u5176\u5B9E\uFF0C\u8981\u6539\u53D8\u8FD9\u79CD\u60C5\u51B5\u5E76\u4E0D\u96BE\uFF0C\u53EA\u662F\u9700\u8981\u591A\u4E00\u70B9\u7684\u8010\u5FC3\u548C\u575A\u6301\uFF0C\u6211\u4EEC\u53EF\u4EE5\u6309\u4E0B\u9762\u4E09\u6B65\u6765\u505A\u3002</p><p><strong>\u7B2C\u4E00\u6B65\uFF1A\u4E0D\u5E72\u9884\u539F\u6709\u7684\u7761\u524D\u884C\u4E3A\uFF0C\u540C\u65F6\u5F15\u5165\u53E6\u4E00\u79CD\u5927\u4EBA\u5B69\u5B50\u90FD\u80FD\u63A5\u53D7\u7684\u5B89\u629A\u884C\u4E3A\u3002</strong></p><p>\u6BD4\u5982\uFF0C\u5B9D\u5B9D\u5982\u679C\u4E60\u60EF\u5728\u7761\u524D\u6478\u7740\u4F60\u7684\u4E73\u623F\u5165\u7761\uFF0C\u4F60\u53EF\u4EE5\u5728\u4E0D\u5E72\u9884\u73B0\u6709\u7761\u524D\u884C\u4E3A\u7684\u57FA\u7840\u4E0A\uFF0C\u5728\u7761\u524D\u7528\u624B\u8F7B\u8F7B\u63E1\u7740\u5B69\u5B50\u7684\u53E6\u4E00\u53EA\u624B\uFF0C\u8F7B\u8F7B\u5730\u629A\u6478\u4ED6\u7684\u624B\u80CC\u6216\u8005\u624B\u6307\u3002\u4F60\u4E5F\u53EF\u4EE5\u5F15\u5165\u5176\u4ED6\u5B89\u6170\u7269\uFF0C\u8F6C\u79FB\u5B69\u5B50\u7684\u6CE8\u610F\u529B\u3002</p><p><strong>\u7B2C\u4E8C\u6B65\uFF1A\u8BA9\u4E24\u79CD\u7761\u524D\u5B89\u629A\u884C\u4E3A\u6216\u8005\u5B89\u629A\u7269\u4F53\u540C\u65F6\u5B58\u5728\u4E00\u6BB5\u65F6\u95F4\u3002</strong></p><p>\u8981\u5B9E\u73B0\u89C4\u5F8B\u7761\u7720\uFF0C\u5B69\u5B50\u9700\u8981\u4E00\u4E2A\u56FA\u5B9A\u7684\u201C\u7761\u524D\u8DEF\u5F84\u201D\u6216\u8005\u201C\u7761\u524D\u7A0B\u5E8F\u201D\u3002\u8FD9\u79CD\u7761\u524D\u8DEF\u5F84\u548C\u7A0B\u5E8F\u662F\u6307\u5B69\u5B50\u5728\u6BCF\u6B21\u7761\u7720\u4E4B\u524D\u90FD\u4F1A\u6309\u7167\u6B21\u5E8F\u53BB\u6267\u884C\u7684\u4E8B\u60C5\uFF0C\u5305\u62EC\u6D17\u6FA1\u3001\u5237\u7259\u3001\u8BB2\u6545\u4E8B\u7B49\uFF0C\u4E00\u65E6\u5B69\u5B50\u5F62\u6210\u4E86\u56FA\u5B9A\u7684\u7761\u524D\u8DEF\u5F84\u548C\u7A0B\u5E8F\uFF0C\u5F80\u5F80\u5C31\u4F1A\u5BF9\u5176\u5F62\u6210\u4F9D\u8D56\uFF0C\u8FDB\u5165\u5230\u67D0\u4E2A\u7761\u524D\u7A0B\u5E8F\u4EE5\u540E\uFF0C\u5C31\u4F1A\u5F62\u6210\u5FC3\u7406\u6697\u793A\uFF1A\u5F85\u4F1A\u8981\u7761\u89C9\u4E86\u3002</p><p>\u5B69\u5B50\u7761\u89C9\u524D\u7684\u8FD9\u4E9B\u201C\u5C0F\u52A8\u4F5C\u201D\u5176\u5B9E\u4E5F\u5DF2\u7ECF\u662F\u4ED6\u5165\u7761\u524D\u7A0B\u5E8F\u7684\u4E00\u90E8\u5206\u4E86\uFF0C\u6211\u4EEC\u8981\u505A\u7684\u662F\u8BA9\u65B0\u7684\u5B89\u629A\u884C\u4E3A\u6216\u8005\u7269\u4F53\u4E5F\u6210\u4E3A\u4ED6\u7761\u524D\u7A0B\u5E8F\u7684\u4E00\u90E8\u5206\uFF0C\u575A\u6301\u4E00\u81F4\u5730\u7ED9\u5B69\u5B50\u65B0\u7684\u5B89\u629A\u884C\u4E3A\u6216\u8005\u5B89\u629A\u7269\u4F53\uFF0C\u800C\u4E14\u8BA9\u5B83\u548C\u539F\u6709\u7684\u7761\u524D\u52A8\u4F5C\u5E76\u5B58\uFF0C\u8FD9\u6837\u53EF\u4EE5\u4E3A\u540E\u9762\u7684\u6B65\u9AA4\u505A\u597D\u94FA\u57AB\u3002</p><p><strong>\u7B2C\u4E09\u6B65\uFF1A\u9010\u6B65\u62BD\u79BB\u5B69\u5B50\u539F\u6709\u7684\u7761\u524D\u201C\u5C0F\u52A8\u4F5C\u201D\u3002</strong></p><p>\u901A\u8FC7\u524D\u9762\u7684\u4E24\u6B65\uFF0C\u5B69\u5B50\u7761\u524D\u7684\u72B6\u6001\u662F\uFF1A\u4E24\u79CD\u5B89\u629A\u65B9\u6CD5\u6216\u5B89\u629A\u7269\u4F53\u5E76\u5B58\uFF0C\u8FD9\u4E2A\u65F6\u5019\uFF0C\u5C31\u53EF\u4EE5\u8BD5\u7740\u963B\u65AD\u5B69\u5B50\u539F\u6765\u7684\u7761\u524D\u5B89\u629A\u884C\u4E3A\u4E86\u3002</p><p>\u5982\u679C\u4F60\u53D1\u73B0\uFF0C\u4E0D\u63D0\u4F9B\u65B0\u5F15\u5165\u7684\u7761\u7720\u5B89\u629A\u65B9\u6CD5\uFF0C\u5B69\u5B50\u5373\u4F7F\u7528\u539F\u6765\u7684\u7761\u524D\u5C0F\u52A8\u4F5C\u4E5F\u8868\u73B0\u4E0D\u5B89\uFF0C\u5C31\u66F4\u597D\u5E72\u9884\u4E86\uFF0C\u4F46\u5E76\u4E0D\u662F\u6BCF\u4E2A\u5B69\u5B50\u90FD\u4F1A\u51FA\u73B0\u8FD9\u79CD\u72B6\u6001\u3002</p><p>\u8981\u6CE8\u610F\u7684\u662F\uFF1A\u5728\u963B\u65AD\u6216\u8005\u62BD\u79BB\u539F\u6765\u7761\u524D\u884C\u4E3A\u7684\u540C\u65F6\uFF0C\u4E00\u5B9A\u8981\u4FDD\u8BC1\u6211\u4EEC\u540E\u671F\u52A0\u5165\u7684\u5B89\u629A\u884C\u4E3A\u6216\u7269\u4F53\u540C\u65F6\u5B58\u5728\u3002</p><p>\u4E3E\u4E2A\u5177\u4F53\u7684\u4F8B\u5B50\uFF0C\u5982\u679C\u5B69\u5B50\u539F\u6765\u7684\u7761\u524D\u884C\u4E3A\u662F\u5438\u542E\u624B\u6307\u5934\uFF0C\u5BB6\u957F\u901A\u8FC7\u7B2C\u4E00\u6B65\u548C\u7B2C\u4E8C\u6B65\u52A0\u5165\u7684\u7761\u524D\u5B89\u629A\u65B9\u5F0F\u662F\u653E\u4E00\u4E2A\u97F3\u4E50\u7761\u7720\u516C\u4ED4\uFF0C\u90A3\u4E48\u5728\u7B2C\u4E09\u6B65\u7684\u5177\u4F53\u505A\u6CD5\u5C31\u662F\uFF1A\u5BB6\u957F\u6253\u5F00\u97F3\u4E50\u7761\u7720\u516C\u4ED4\uFF0C\u540C\u65F6\u8F7B\u8F7B\u63E1\u4F4F\u5B69\u5B50\u7684\u624B\uFF0C\u5FC5\u8981\u65F6\u53EF\u4EE5\u7528\u81EA\u5DF1\u7684\u624B\u6307\u629A\u6478\u5B69\u5B50\u7684\u624B\uFF0C\u963B\u6B62\u5B69\u5B50\u628A\u624B\u653E\u8FDB\u4ED6\u7684\u5634\u5DF4\u91CC\u3002</p><p>\u4E4B\u6240\u4EE5\u7B49\u5230\u7B2C\u4E09\u6B65\u624D\u963B\u65AD\u6216\u62BD\u79BB\u5B69\u5B50\u539F\u6765\u7684\u7761\u524D\u884C\u4E3A\uFF0C\u662F\u56E0\u4E3A\u5728\u6539\u53D8\u5B69\u5B50\u5165\u7761\u5B89\u629A\u884C\u4E3A\u7684\u65F6\u5019\uFF0C\u5F80\u5F80\u90FD\u4F1A\u53D7\u5230\u5B69\u5B50\u7684\u5F3A\u70C8\u53CD\u6297\uFF0C\u800C\u5728\u5F15\u5165\u65B0\u7684\u66FF\u4EE3\u884C\u4E3A\u7684\u57FA\u7840\u4E0A\uFF0C\u8FD9\u79CD\u53CD\u6297\u7684\u5F3A\u5EA6\u4F1A\u76F8\u5BF9\u5C0F\u4E00\u4E9B\uFF0C\u6301\u7EED\u65F6\u95F4\u4E5F\u4F1A\u66F4\u77ED\u3002</p><p>\u4EE5\u4E0A\u65B9\u6CD5\uFF0C\u7528\u884C\u4E3A\u5B66\u539F\u7406\u6765\u89E3\u91CA\uFF0C\u5C31\u662F\u7ADE\u4E89\u6027\u884C\u4E3A\u7684\u66FF\u4EE3\u5B58\u5728\u4EE5\u53CA\u539F\u6709\u884C\u4E3A\u7684\u6D88\u9000**\u3002**\u64CD\u4F5C\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u6709\u4EE5\u4E0B\u51E0\u4E2A\u6CE8\u610F\u4E8B\u9879\uFF1A</p><p>\u4E00\u3001\u8FD9\u4E2A\u65B9\u6CD5\u4E3B\u8981\u662F\u9488\u5BF9\u8BA9\u7236\u6BCD\u89C9\u5F97\u4E0D\u9002\u7684\u7761\u524D\u5B89\u629A\u884C\u4E3A\uFF0C\u662F\u5426\u7EA0\u6B63\u5B69\u5B50\u5165\u7761\u524D\u7684\u81EA\u6211\u5B89\u629A\u884C\u4E3A\uFF0C\u7531\u5BB6\u957F\u81EA\u884C\u51B3\u5B9A\u3002\u5982\u679C\u5BB6\u957F\u548C\u5B69\u5B50\u90FD\u4E0D\u89C9\u5F97\u73B0\u6709\u7684\u7761\u524D\u5B89\u629A\u884C\u4E3A\u6709\u4EC0\u4E48\u95EE\u9898\uFF0C\u65E0\u9700\u5F3A\u8FEB\u6539\u53D8\u3002</p><p>\u4E8C\u3001\u5F15\u5165\u7684\u66FF\u4EE3\u5B89\u629A\u884C\u4E3A\u5FC5\u987B\u662F\u5988\u5988\u548C\u5B69\u5B50\u90FD\u53EF\u4EE5\u63A5\u53D7\u7684\uFF0C\u4E5F\u9002\u5408\u957F\u671F\u4F7F\u7528\u7684\u3002\u6211\u4EEC\u4E0D\u5E0C\u671B\u6539\u53D8\u4E86\u4E00\u79CD\u7761\u524D\u5B89\u629A\u884C\u4E3A\uFF0C\u53C8\u8981\u4E3A\u53E6\u4E00\u79CD\u6211\u4EEC\u81EA\u5DF1\u57F9\u517B\u8D77\u6765\u7684\u5B89\u629A\u884C\u4E3A\u53D1\u6101\u3002</p><p>\u4E09\u3001\u5E72\u9884\u7684\u8FC7\u7A0B\u53EF\u80FD\u53D7\u5230\u5B69\u5B50\u7684\u62B5\u6297\uFF0C\u5BB6\u957F\u9700\u8981\u591A\u70B9\u8010\u5FC3\uFF0C\u5C24\u5176\u662F\u7B2C\u4E09\u6B65\uFF0C\u53EF\u80FD\u9700\u8981\u8FDB\u884C\u591A\u6B21\u7684\u5C1D\u8BD5\u624D\u4F1A\u6210\u529F\u3002</p><p>\u56DB\u3001\u5982\u679C\u4F60\u7684\u5B69\u5B50\u7761\u524D\u6CA1\u6709\u4EFB\u4F55\u81EA\u6211\u5B89\u629A\u7684\u884C\u4E3A\uFF0C\u5BB6\u957F\u65E0\u9700\u7279\u610F\u5F15\u5165\u5B89\u629A\u7269\u3002</p><p>\u4E94\u3001\u8FD9\u7BC7\u6587\u7AE0\u6240\u9488\u5BF9\u7684\u662F\u7761\u524D\u5C0F\u52A8\u4F5C\uFF0C\u5982\u679C\u4F60\u7684\u5B69\u5B50\u5728\u5176\u4ED6\u65F6\u95F4\u6BB5\u4E5F\u9891\u7E41\u51FA\u73B0\u7C7B\u4F3C\u884C\u4E3A\uFF0C\u5219\u9700\u8981\u54A8\u8BE2\u4E13\u4E1A\u7684\u533B\u751F\uFF0C\u627E\u51FA\u539F\u56E0\u548C\u9488\u5BF9\u6027\u7684\u5E72\u9884\u65B9\u6CD5\u3002</p><p>\u9898\u56FE\u6765\u81EAPixabay\uFF0C\u6388\u6743\u57FA\u4E8ECC0\u534F\u8BAE\uFF0C\u672C\u6587\u4F5C\u8005\u4E3A\u7F8E\u56FD\u513F\u7AE5\u5FC3\u7406\u533B\u751F\uFF0C\u5979\u7684\u5FAE\u4FE1\u516C\u4F17\u53F7\u4E3A\uFF1Axuguifeng82</p><hr><p>\u9488\u5BF9\u5B69\u5B50\u7761\u7720\u95EE\u9898\uFF0C<strong>\u6FB3\u6D32\u5987\u5E7CDr\u97E9</strong>\u51C6\u5907\u4E86\u4E00\u573A\u79D1\u666E\u8BFE\u7A0B\uFF0C\u542C\u4ECE\u5927\u5BB6\u7684\u5EFA\u8BAE\uFF0C\u6211\u4EEC\u5C06\u7EBF\u4E0B\u8BFE\u7A0B\u6539\u6210<strong>\u7EBF\u4E0A\u76F4\u64AD\u79D1\u666E</strong>\uFF0C\u5982\u679C\u4F60\u88AB\u5B69\u5B50\u7761\u7720\u95EE\u9898\u56F0\u6270\uFF0C\u6B22\u8FCE\u62A5\u540D\u53C2\u52A0\u3002</p>',32),g={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626381&idx=1&sn=9ec0f27e94787474f75e29543e20128e&chksm=8b5cb277bc2b3b617dc92ef12f695077aa6c7744269bd4ac16416d3ad4fb0f8f2d241adc41b7&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},f=c("\u67E5\u770B\u539F\u6587");function h(l,m){const e=r("ExternalLinkIcon");return t(),n(a,null,[_,p("p",null,[p("a",g,[f,o(e)])])],64)}var u=d(i,[["render",h]]);export{u as default};
