import{r as o,o as s,c as a,a as e,b as n,w as c,F as d,e as t,d as _}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const f={},g=e("h3",{id:"\u5B69\u5B50\u611F\u51928\u5927\u8BEF\u533A-\u4F60\u72AF\u8FC7\u51E0\u4E2A",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#\u5B69\u5B50\u611F\u51928\u5927\u8BEF\u533A-\u4F60\u72AF\u8FC7\u51E0\u4E2A","aria-hidden":"true"},"#"),t(" \u5B69\u5B50\u611F\u51928\u5927\u8BEF\u533A\uFF0C\u4F60\u72AF\u8FC7\u51E0\u4E2A\uFF1F")],-1),h=t("\u611F\u5192\u662F\u513F\u7AE5\u6700\u5E38\u89C1\u7684\u75BE\u75C5\uFF0C\u4E5F\u662F\u6BCF\u4F4D\u5BB6\u957F\u90FD\u8981\u9762\u5BF9\u7684\u95EE\u9898\uFF0C\u4F46\u5BF9\u8FD9\u4E2A\u6700\u5E38\u89C1\u75BE\u75C5\u5927\u5BB6\u7684\u8BEF\u533A\u5374\u4E0D\u5C11\uFF0C\u9664\u4E86\u524D\u9762\u5199\u8FC7\u7684"),l=t("\u611F\u5192\u548C\u5FC3\u808C\u708E"),m=t("\u5916\u8FD8\u6709\u5F88\u591A\uFF0C\u4ECA\u5929\u96C6\u4E2D\u603B\u7ED3\u4E00\u4E0B\u6700\u5E38\u89C1\u7684\u516B\u4E2A\u3002"),b=_("<p><strong>\u4E00.\u53D7\u5BD2\u7740\u51C9\u4F1A\u5BFC\u81F4\u611F\u5192</strong></p><p>\u8FD9\u5E94\u8BE5\u662F\u975E\u5E38\u666E\u904D\u7684\u8BEF\u533A\uFF0C\u611F\u5192\u79CB\u51AC\u5B63\u8282\u662F\u66F4\u591A\u53D1\uFF0C\u4F46\u5E76\u4E0D\u610F\u5473\u662F\u53D7\u5BD2\u7740\u51C9\u5F15\u8D77\u7684\u3002</p><p>\u4E8B\u5B9E\u4E0A\u611F\u5192\u5DF2\u7ECF\u88AB\u8BC1\u5B9E\u662F\u75C5\u6BD2\u611F\u67D3\u5F15\u8D77\u7684\uFF0C\u4F4E\u6E29\u73AF\u5883\u4E5F\u786E\u5B9E\u53EF\u4EE5\u8BA9\u4E00\u4E9B\u611F\u5192\u75C5\u6BD2\u66F4\u5BB9\u6613\u590D\u5236\uFF0C\u4F46\u76EE\u524D\u6CA1\u6709\u660E\u786E\u8BC1\u636E\u8BC1\u660E\u5BD2\u51B7\u6216\u53D7\u51C9\u4F1A\u76F4\u63A5\u5BFC\u81F4\u611F\u5192\uFF0C\u79CB\u51AC\u5B63\u8282\u611F\u5192\u591A\u53D1\u53EF\u80FD\u548C\u5927\u5BB6\u5728\u5BA4\u5185\u5446\u5F97\u591A\uFF0C\u66F4\u5BB9\u6613\u5BFC\u81F4\u75C5\u6BD2\u4F20\u64AD\u6709\u5173\uFF0C\u533B\u5B66\u754C\u4E3B\u6D41\u89C2\u70B9\u662F\u5BD2\u51B7\u4E0D\u4F1A\u5BFC\u81F4\u611F\u5192\u3002</p><p><strong>\u4E8C\u3001\u6253\u55B7\u568F\u5C31\u662F\u611F\u5192\u4E86</strong></p><p>\u611F\u5192\u4F1A\u5BFC\u81F4\u9F3B\u9ECF\u819C\u6C34\u80BF\u5145\u8840\uFF0C\u9F3B\u8154\u5206\u6CCC\u7269\u589E\u591A\uFF0C\u8FD9\u4E9B\u523A\u6FC0\u4F1A\u5F15\u53D1\u55B7\u568F\uFF0C\u4F46\u80FD\u5F15\u8D77\u55B7\u568F\u7684\u539F\u56E0\u9664\u4E86\u611F\u5192\u8FD8\u6709\u5F88\u591A\uFF0C\u5149\u7EBF\u3001\u51B7\u7A7A\u6C14\u3001\u523A\u6FC0\u6027\u6C14\u4F53\u90FD\u53EF\u80FD\u5F15\u8D77\u55B7\u568F\uFF0C\u6240\u4EE5\u6211\u4EEC\u4E0D\u9700\u8981\u56E0\u4E3A\u770B\u5230\u5B69\u5B50\u6253\u55B7\u568F\u5C31\u8BA4\u4E3A\u662F\u611F\u5192\uFF0C\u7136\u540E\u5C31\u8BA9\u5403\u611F\u5192\u836F\u3002</p><p><strong>\u4E09\u3001\u611F\u5192\u4E0D\u5403\u836F\u597D\u5F97\u6162</strong></p><p>\u5728\u5F88\u591A\u4EBA\u7684\u89C2\u5FF5\u91CC\uFF0C\u751F\u75C5\u4E86\u5C31\u8981\u5403\u836F\uFF0C\u5403\u836F\u4E86\u5C31\u597D\u5F97\u66F4\u5FEB\uFF0C\u5BF9\u4E00\u4E9B\u75BE\u75C5\u4E5F\u786E\u5B9E\u5982\u6B64\u3002\u4F46\u611F\u5192\u662F\u81EA\u9650\u6027\u75BE\u75C5\uFF0C\u4E0D\u7BA1\u7528\u4E0D\u7528\u836F\uFF0C\u5230\u4E86\u4E00\u5468\u5927\u90E8\u5206\u90FD\u80FD\u597D\uFF0C\u5BF9\u5927\u4EBA\u6765\u8BF4\uFF0C\u611F\u5192\u836F\u53EF\u4EE5\u6539\u5584\u75C7\u72B6\uFF0C\u8BA9\u611F\u5192\u671F\u95F4\u6CA1\u6709\u90A3\u4E48\u96BE\u53D7\uFF0C\u4F46\u5403\u611F\u5192\u836F\u5E76\u4E0D\u80FD\u8BA9\u611F\u5192\u597D\u5F97\u66F4\u5FEB\uFF0C\u5BF9\u513F\u7AE5\u6765\u8BF4\u611F\u5192\u836F\u6548\u679C\u8FD8\u4E0D\u786E\u5207\uFF0C\u526F\u4F5C\u7528\u8FD8\u66F4\u5927\u3002</p><p><strong>\u56DB\u3001\u5B69\u5B50\u7ECF\u5E38\u611F\u5192\uFF0C\u8BF4\u660E\u514D\u75AB\u529B\u4F4E\u4E0B</strong></p><p>\u5F88\u591A\u5BB6\u957F\uFF0C\u770B\u5230\u5B69\u5B50\u6BCF\u9694\u4E00\u4E24\u4E2A\u6708\u5C31\u611F\u5192\u4E00\u6B21\uFF0C\u5C31\u8BA4\u4E3A\u5B69\u5B50\u514D\u75AB\u4E0D\u597D\uFF0C\u7136\u540E\u5C31\u8981\u7ED9\u5B69\u5B50\u5403\u8865\u54C1\u6765\u63D0\u9AD8\u514D\u75AB\u529B\u3002</p><p>\u5B69\u5B50\u5982\u679C\u6709\u514D\u75AB\u7F3A\u9677\uFF0C\u786E\u5B9E\u66F4\u5BB9\u6613\u53D1\u751F\u5305\u62EC\u611F\u5192\u5728\u5185\u7684\u611F\u67D3\u6027\u75BE\u75C5\uFF0C\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u5E7C\u513F\u5E73\u5747\u6BCF\u5E74\u4F1A\u53D1\u751F6-8\u6B21\u611F\u5192\uFF0C10-15%\u7684\u5B69\u5B50\u6BCF\u5E74\u8981\u611F\u5192\u81F3\u5C1112\u6B21\uFF0C\u6240\u4EE5\u95F4\u96941-2\u4E2A\u6708\u611F\u5192\u4E00\u6B21\u662F\u5F88\u6B63\u5E38\u7684\u3002</p><p>\u771F\u6B63\u5B58\u5728\u5148\u5929\u514D\u75AB\u7F3A\u9677\u7684\u5B69\u5B50\u975E\u5E38\u5C11\uFF0C\u5982\u679C\u5B69\u5B50\u4E00\u5E74\u53D1\u751F2\u6B21\u6216\u4EE5\u4E0A\u7684\u4E25\u91CD\u547C\u5438\u9053\u6216\u7EC6\u83CC\u611F\u67D3\uFF0C\u6BD4\u5982\u80BA\u708E\u3001\u8702\u7A9D\u7EC7\u708E\u3001\u8113\u80BF\u662F\u9700\u8981\u8003\u8651\u514D\u75AB\u7F3A\u9677\u95EE\u9898\uFF0C\u4F46\u5373\u4FBF\u771F\u7684\u662F\u514D\u75AB\u7F3A\u9677\uFF0C\u4E5F\u6CA1\u6709\u4EC0\u4E48\u8865\u54C1\u80FD\u6539\u5584\u514D\u75AB\u529F\u80FD\u3002</p><p><strong>\u4E94\u3001\u611F\u5192\u4E0D\u597D\u597D\u6CBB\uFF0C\u4F1A\u53D1\u5C55\u4E3A\u80BA\u708E</strong></p><p>\u80BA\u708E\u521A\u5F00\u59CB\u7684\u8868\u73B0\u901A\u5E38\u4E5F\u662F\u4E0A\u547C\u5438\u9053\u75C7\u72B6\uFF0C\u6240\u4EE5\u548C\u5FC3\u808C\u708E\u4E00\u6837\uFF0C\u5F88\u591A\u4EBA\u4E5F\u8BA4\u4E3A\u611F\u5192\u548C\u80BA\u708E\u4E5F\u662F\u56E0\u679C\u5173\u7CFB\uFF0C\u201C\u611F\u5192\u75C7\u72B6\u201D\u548C\u80BA\u708E\u8868\u73B0\u5176\u5B9E\u53EF\u4EE5\u662F\u75C5\u6BD2\u611F\u67D3\u5728\u4E0D\u540C\u9636\u6BB5\u7684\u8868\u73B0\u3002</p><p>\u6211\u4EEC\u786E\u5B9E\u4E0D\u80FD\u6392\u9664\u6709\u4E9B\u75C5\u4EBA\u662F\u611F\u5192\u540E\u5E76\u53D1\u4E86\u80BA\u708E\uFF0C\u4F46\u611F\u5192\u662F\u5426\u53D1\u5C55\u4E3A\u80BA\u708E\uFF0C\u66F4\u591A\u662F\u7531\u4EBA\u81EA\u8EAB\u6761\u4EF6\u6761\u4EF6\u51B3\u5B9A\u7684\uFF0C\u6BD4\u5982\u65E9\u4EA7\u3001\u4F4E\u9F84\u513F\u7AE5\uFF0C\u5B58\u5728\u6C14\u9053\u7578\u5F62\u6216\u5FC3\u80BA\u529F\u80FD\u4E0D\u5168\u3001\u6709\u514D\u75AB\u7F3A\u9677\u7684\u5B69\u5B50\u66F4\u5BB9\u6613\u51FA\u73B0\u80BA\u708E\uFF0C\u800C\u548C\u662F\u5426\u597D\u597D\u6CBB\u7597\u611F\u5192\u6CA1\u6709\u5173\u7CFB\uFF0C\u56E0\u4E3A\u611F\u5192\u6CA1\u6709\u7279\u6548\u836F\uFF0C\u6CBB\u7597\u4E5F\u662F\u5BF9\u75C7\u6CBB\u7597\uFF0C\u518D\u597D\u597D\u6CBB\u4E5F\u4E0D\u80FD\u6539\u53D8\u75C5\u60C5\u53D1\u5C55\u65B9\u5411\u3002</p><p><strong>\u516D\u3001\u611F\u5192\u63D0\u524D\u7528\u6297\u751F\u7D20\uFF0C\u53EF\u4EE5\u9884\u9632\u5E76\u53D1\u7EC6\u83CC\u611F\u67D3</strong></p><p>\u611F\u5192\u786E\u5B9E\u53EF\u80FD\u5E76\u53D1\u4E00\u4E9B\u7EC6\u83CC\u611F\u67D3\uFF0C\u6BD4\u5982\u4E2D\u8033\u708E\u3001\u9F3B\u7AA6\u708E\u751A\u81F3\u80BA\u708E\uFF0C\u4F46\u6211\u4EEC\u4E0D\u80FD\u9884\u4F30\u54EA\u4E9B\u611F\u5192\u7684\u75C5\u4EBA\u4F1A\u51FA\u73B0\u7EC6\u83CC\u611F\u67D3\uFF0C\u611F\u5192\u662F\u975E\u5E38\u5E38\u89C1\u7684\u75BE\u75C5\uFF0C\u5728\u6CA1\u6709\u7EC6\u83CC\u611F\u67D3\u7684\u60C5\u51B5\u4E0B\u4F7F\u7528\u6297\u751F\u7D20\uFF0C\u4E0D\u4F46\u4E0D\u80FD\u9884\u9632\u7EC6\u83CC\u611F\u67D3\uFF0C\u53CD\u800C\u4F1A\u5BFC\u81F4\u8FC7\u654F\u3001\u8179\u6CFB\u3001\u8010\u836F\u7B49\u95EE\u9898\uFF0C\u5371\u5BB3\u66F4\u5927\u3002</p><p><strong>\u4E03\u3001\u611F\u5192\u53EF\u4EE5\u7528\u6297\u75C5\u6BD2\u836F</strong></p><p>\u611F\u5192\u786E\u5B9E\u662F\u75C5\u6BD2\u611F\u67D3\uFF0C\u4F46\u80FD\u5F15\u8D77\u611F\u5192\u7684\u75C5\u6BD2\u6709\u4E24\u767E\u591A\u79CD\uFF0C\u800C\u4E14\u76EE\u524D\u6CA1\u6709\u9488\u5BF9\u8FD9\u4E9B\u75C5\u6BD2\u7684\u6297\u75C5\u6BD2\u836F\uFF0C\u6240\u4EE5\u4E0D\u9700\u8981\u7528\u6297\u75C5\u6BD2\u836F\uFF0C\u58F0\u79F0\u80FD\u6297\u75C5\u6BD2\u7684\u611F\u5192\u836F\uFF0C\u4E0D\u7BA1\u662F\u897F\u836F\u8FD8\u662F\u4E2D\u836F\uFF0C\u6BD4\u5982\u91D1\u521A\u70F7\u80FA\u3001\u6297\u75C5\u6BD2\u53E3\u670D\u6DB2\u90FD\u662F\u4E0D\u5FC5\u8981\u7684\u3002</p><p>\u4F46\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u6D41\u884C\u6027\u611F\u5192\u548C\u666E\u901A\u611F\u5192\u662F\u4E0D\u540C\u7684\u75BE\u75C5\uFF0C\u6D41\u611F\u662F\u6709\u6297\u75C5\u6BD2\u836F\u53EF\u7528\u7684\u3002</p><p><strong>\u516B\u3001\u7EF4\u751F\u7D20C\u80FD\u9884\u9632\u611F\u5192</strong></p><p>\u5747\u8861\u996E\u98DF\uFF0C\u591A\u5403\u6C34\u679C\u852C\u83DC\u5BF9\u5065\u5EB7\u662F\u6709\u597D\u5904\u3002\u7EF4\u751F\u7D20C\u4E5F\u5E38\u88AB\u7528\u4E8E\u6CBB\u7597\u548C\u9884\u9632\u611F\u5192\uFF0C\u4F46\u6548\u679C\u5E76\u672A\u8BC1\u5B9E\u3002</p><p>\u80FD\u5F15\u8D77\u611F\u5192\u7684\u75C5\u6BD2\u975E\u5E38\u591A\uFF0C\u6240\u4EE5\u73B0\u5728\u8FD8\u6CA1\u6709\u80FD\u9884\u9632\u611F\u5192\u7684\u836F\u7269\u6216\u75AB\u82D7\uFF0C\u4F46\u52E4\u6D17\u624B\uFF0C\u907F\u514D\u548C\u611F\u5192\u75C5\u4EBA\u5BC6\u5207\u63A5\u89E6\u8FD8\u662F\u80FD\u964D\u4F4E\u611F\u5192\u7684\u98CE\u9669\u3002</p><hr><p>\u5BF9\u8BDD\u6846\u56DE\u590D**\u300C\u8BEF\u533A\u300D<strong>\uFF0C\u53EF\u4E86\u89E3</strong>\u5B69\u5B50\u53D1\u70E7\u5341\u5927\u8BEF\u533A**</p><p>\u70B9<strong>\u9605\u8BFB\u539F\u6587</strong>\uFF0C\u53EF\u5411\u6211\u548C\u5176\u5B83\u533B\u751F\u54A8\u8BE2</p>",25),x={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627070&idx=1&sn=0954ff433875e6cbbdf2e148e0acdc6d&chksm=8b5cb7c4bc2b3ed2fcf746f9d4a4fe38af18b79fe2688b995384328f36e4e38ca1cf9ea07091&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},u=t("\u67E5\u770B\u539F\u6587");function k(z,N){const p=o("RouterLink"),r=o("ExternalLinkIcon");return s(),a(d,null,[g,e("p",null,[h,n(p,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651627060&idx=1&sn=56ef43127d22a9da9716ac87ac0f50d5/"},{default:c(()=>[l]),_:1}),m]),b,e("p",null,[e("a",x,[u,n(r)])])],64)}var C=i(f,[["render",k]]);export{C as default};
