import{r as t,o as n,c as a,a as p,b as o,F as r,d as s,e as i}from"./app.9aaa2256.js";import{_ as c}from"./plugin-vue_export-helper.21dcd24c.js";const l={},g=s('<h3 id="\u4EC0\u4E48\u76AE\u80A4\u95EE\u9898\u8BE5\u7528\u53BB\u75A4\u818F" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u76AE\u80A4\u95EE\u9898\u8BE5\u7528\u53BB\u75A4\u818F" aria-hidden="true">#</a> \u4EC0\u4E48\u76AE\u80A4\u95EE\u9898\u8BE5\u7528\u53BB\u75A4\u818F\uFF1F</h3><p>\u5927\u4F17\u7ECF\u5E38\u8BEF\u7528\u7845\u51DD\u80F6\u4E00\u7C7B\u7684\u53BB\u75A4\u4EA7\u54C1\uFF0C\u53EF\u80FD\u8BEF\u7528\u4E8E\u8272\u7D20\u6C89\u7740\u3001\u866B\u54AC\u76AE\u708E\u3001\u6E7F\u75B9\u3001\u8F7B\u5FAE\u70EB\u4F24\u7B49\u3002\u8FD9\u7BC7\u6587\u7AE0\u53EF\u4EE5\u544A\u8BC9\u5927\u5BB6\u53BB\u75A4\u818F\u7684\u201C\u9002\u5E94\u8BC1\u201D\u2014\u2014\u54EA\u4E9B\u76AE\u80A4\u95EE\u9898\u8BE5\u7528\uFF0C\u4EE5\u53CA\u54EA\u4E9B\u95EE\u9898\u4E0D\u8BE5\u7528\u3002</p><p>\u53BB\u75A4\u818F\u4E00\u822C\u662F\u7531\u533B\u7528\u7EA7\u6709\u673A\u7845\u5236\u6210\u7684\u7845\u51DD\u80F6\u3001\u7845\u80F6\u7247\u7B49\u4EA7\u54C1\u7684\u4EE3\u540D\u8BCD\u30021981\u5E74\uFF0C\u6FB3\u5927\u5229\u4E9A\u7684\u963F\u5FB7\u83B1\u5FB7\u513F\u7AE5\u533B\u9662\u9996\u6B21\u628A\u7845\u80F6\u7247\u5E94\u7528\u4E8E\u70E7\u4F24\u7622\u75D5\u7684\u6CBB\u7597[1]\u3002\u6B64\u540E\uFF0C\u6709\u673A\u7845\u4EE5\u5404\u79CD\u5F62\u5F0F\u751F\u4EA7\uFF0C\u5305\u62EC\u6709\u673A\u7845\u4E73\u818F\u5316\u5408\u7269\u3001\u6DFB\u52A0\u7EF4\u751F\u7D20E\u7B49\u6210\u5206\u7684\u7845\u6CB9\u6216\u51DD\u80F6\u7B49\u3002</p><p>\u76EE\u524D\u5E38\u7528\u7684\u6709\u4E24\u79CD\uFF0C\u4E00\u79CD\u662F\u67D4\u8F6F\u7684\u81EA\u9ECF\u6027\u534A\u5C01\u95ED\u8D34\u7247\uFF0C\u53EF\u91CD\u590D\u4F7F\u7528\uFF1B\u53E6\u4E00\u79CD\u662F\u6DB2\u4F53\u7845\u51DD\u80F6\uFF0C\u8584\u8584\u4E00\u5C42\u6D82\u5728\u76AE\u80A4\u4E0A\uFF0C\u5E72\u71E5\u540E\u5F62\u6210\u900F\u660E\u800C\u67D4\u8F6F\u7684\u7845\u80F6\u7247\u3002</p><p><strong>01</strong></p><p><strong>\u9002\u5E94\u8BC1\uFF1A\u8FD9\u4E9B\u60C5\u51B5\u53EF\u4EE5\u7528\u53BB\u75A4\u818F</strong></p><p>\u53BB\u75A4\u818F\u4E2D\u7684\u201C\u75A4\u201D\u5728\u533B\u5B66\u4E0A\u6307\u7684\u662F\u589E\u751F\u6027\u7622\u75D5\u548C\u7622\u75D5\u7599\u7629\uFF0C\u662F\u4EBA\u4F53\u5BF9\u771F\u76AE\u635F\u4F24\u7684\u8FC7\u5EA6\u7EC4\u7EC7\u53CD\u5E94\uFF0C\u8868\u73B0\u4E3A\u9686\u8D77\u76AE\u80A4\u8868\u9762\u7684\u771F\u76AE\u75C5\u7076\uFF0C\u5E38\u4F34\u968F\u7619\u75D2\u3001\u75BC\u75DB\u7B49\u75C7\u72B6[2]\u3002</p><p>\u589E\u751F\u6027\u7622\u75D5\u53EF\u5728\u624B\u672F\u3001\u70E7\u4F24\u3001\u708E\u75C7\u6216\u611F\u67D3\u6027\u76AE\u80A4\u75BE\u75C5\u57FA\u7840\u4E0A\u7EE7\u53D1\uFF0C\u75C5\u7076\u5C40\u9650\u4E8E\u539F\u59CB\u521B\u9762\u5185\uFF0C\u5E76\u53EF\u80FD\u968F\u7740\u65F6\u95F4\u63A8\u79FB\u800C\u81EA\u884C\u6D88\u9000\uFF08\u56FE1\uFF09\u3002</p><p>\u56FE1. \u5200\u4F24\u5F15\u8D77\u7684\u589E\u751F\u6027\u7622\u75D5</p><p>\u5DE6\u6ED1</p><p>\u9AD8\u80FD\u9884\u8B66</p><p>\u6765\u6E90\uFF1AJean L. Bolognia, et al. <em>Dermatology</em>,</p><p>4th edition, p1714.</p><p>\u7622\u75D5\u7599\u7629\u5219\u53EF\u80FD\u53D1\u751F\u4E8E\u8F83\u5C0F\u7684\u76AE\u80A4\u635F\u4F24\u5904\uFF0C\u6BD4\u5982\u7A7F\u8033\u6D1E\u3002\u75C5\u7076\u4F1A\u8D85\u51FA\u539F\u59CB\u521B\u9762\u8FB9\u754C\uFF0C\u4E3B\u8981\u53D1\u751F\u4E8E\u4E0A\u80F8\u90E8\u3001\u80A9\u80CC\u90E8\u3001\u9888\u90E8\u3001\u8033\u6735\u3002\u7622\u75D5\u4F1A\u968F\u65F6\u95F4\u63A8\u79FB\u800C\u8FDB\u884C\u6027\u589E\u5927\uFF0C\u4E0D\u80FD\u81EA\u884C\u6D88\u9000\uFF08\u56FE2\uFF09\u3002</p><p>\u56FE2. \u7622\u75D5\u7599\u7629\uFF1A\u8D85\u51FA\u539F\u59CB\u521B\u9762\u8FB9\u754C\uFF0C\u5411\u5468\u56F4\u5EF6\u4F38</p><p>\uFF0C\u5448\u87F9\u722A\u6837\u5916\u89C2\uFF0C\u4E5F\u79F0\u4F5C\u201C\u87F9\u8DB3\u80BF\u201D</p><p>\u5DE6\u6ED1</p><p>\u9AD8\u80FD\u9884\u8B66</p><p>\u6765\u6E90\uFF1AJean L. Bolognia, et al. <em>Dermatology</em>,</p><p>4th edition, p1714.</p><p>\u7845\u51DD\u80F6/\u7845\u80F6\u7247\u7528\u4E8E\u9884\u9632\u548C\u6CBB\u7597\u7622\u75D5\u7684\u83B7\u76CA\u5FAA\u8BC1\u7B49\u7EA7\u8F83\u5F31\uFF08\u8868\u73B0\u4E3A\u4E34\u5E8A\u8BD5\u9A8C\u8D28\u91CF\u8F83\u5DEE\uFF0C\u6837\u672C\u91CF\u592A\u5C0F\uFF0C\u6216\u8005\u5BB9\u6613\u53D1\u751F\u504F\u501A\uFF09[3]\uFF0C\u4F46\u8003\u8651\u5230\u5176\u5B89\u5168\u3001\u7B80\u4FBF\uFF0C\u6240\u4EE5\u60A3\u8005\u63A5\u53D7\u5EA6\u6BD4\u8F83\u9AD8\uFF0C\u4E34\u5E8A\u5E94\u7528\u4E5F\u5E7F\u6CDB\u3002</p><p>\u666E\u904D\u8BA4\u4E3A\u7845\u80F6\u51DD\u548C\u7845\u80F6\u7247\u662F\u7622\u75D5\u6CBB\u7597\u7684\u91D1\u6807\u51C6\u3002\u7622\u75D5\u6CBB\u7597\u7684\u6700\u65B0\u6307\u5357\u4E5F\u5C06\u5176\u4F5C\u4E3A\u65E0\u521B\u6027\u6CBB\u7597\u548C\u9884\u9632\u589E\u751F\u6027\u7622\u75D5\u548C\u7622\u75D5\u7599\u7629\u7684\u4E00\u7EBF\u9009\u62E9[4,5]\u3002</p><p><strong>02</strong></p><p><strong>\u4F5C\u7528\u673A\u5236\uFF1A\u53BB\u75A4\u818F\u662F\u600E\u6837\u53D1\u6325\u4F5C\u7528\u7684\uFF1F</strong></p><p>\u53BB\u75A4\u4EA7\u54C1\u53EF\u80FD\u5B58\u5728\u591A\u79CD\u673A\u5236\uFF0C\u4E3B\u8981\u673A\u5236\u53EF\u80FD\u662F\u5B83\u5BF9\u89D2\u8D28\u5C42\u7684\u5C01\u5305\u548C\u6C34\u5408\u4F5C\u7528\u3002</p><p>\u5176\u4ED6\u53EF\u80FD\u673A\u5236\u6709\u5347\u9AD8\u76AE\u80A4\u8868\u9762\u6E29\u5EA6\uFF0C\u589E\u52A0\u80F6\u539F\u9176\u6D3B\u6027\uFF0C\u4FC3\u8FDB\u80F6\u539F\u5206\u89E3\u3002\u53E6\u5916\uFF0C\u76AE\u80A4\u4E0E\u542B\u7845\u4EA7\u54C1\u4E4B\u95F4\u4F1A\u5F62\u6210\u8D1F\u9759\u7535\u573A\uFF0C\u4F7F\u80F6\u539F\u91CD\u6392\uFF0C\u7622\u75D5\u7F29\u5C0F[6,7]\u3002</p><p><strong>\u5982\u4F55\u4F7F\u7528\u53BB\u75A4\u818F</strong></p><p>\u5BF9\u4E8E\u9AD8\u98CE\u9669\u4EBA\u7FA4\uFF08\u7622\u75D5\u4F53\u8D28\uFF09\u6216\u9AD8\u98CE\u9669\u90E8\u4F4D\uFF08\u9888\u3001\u80A9\u3001\u80F8\u90E8\uFF09\u7684\u624B\u672F\u540E\u9884\u9632\uFF0C\u901A\u5E38\u662F\u5728\u624B\u672F\u62C6\u7EBF\u540E\u5F00\u59CB\u4F7F\u7528\u53BB\u75A4\u4EA7\u54C1\u3002</p><p>\u5176\u4E2D\uFF0C\u7845\u51DD\u80F6\u548C\u7845\u80F6\u7247\u7684\u6548\u679C\u6CA1\u6709\u5DEE\u5F02[8]\u3002\u53EF\u4EE5\u6839\u636E\u7622\u75D5\u90E8\u4F4D\u548C\u5927\u5C0F\uFF0C\u4EE5\u53CA\u4E2A\u4EBA\u559C\u597D\u6765\u9009\u62E9\u5408\u9002\u7684\u7C7B\u578B\u3002\u7845\u51DD\u80F6\u9002\u7528\u4E8E\u53E3\u5468\u3001\u9888\u90E8\u7B49\u6D3B\u52A8\u8F83\u591A\u7684\u90E8\u4F4D\uFF0C\u6BCF\u65E52\u6B21\u3002\u7845\u80F6\u7247\u5219\u6BCF\u65E5\u8D34\u657712~24\u5C0F\u65F6\uFF0C\u6301\u7EED2~6\u4E2A\u6708[5,6,8]\u3002</p><p>\u5982\u679C\u662F\u6CBB\u7597\u7EA2\u8272\u672A\u6210\u719F\u7622\u75D5\uFF08\u591A\u4E3A1\u5E74\u4EE5\u5185\uFF09\u548C\u7622\u75D5\u7599\u7629\uFF0C\u7845\u51DD\u80F6\u53EF\u4EE5\u6BCF\u65E52\u6B21\uFF0C\u6216\u7845\u80F6\u7247\u88C1\u526A\u6210\u80FD\u8986\u76D6\u7622\u75D5\u7684\u5927\u5C0F\uFF0C\u6BCF\u65E5\u8D34\u657712~24\u5C0F\u65F6\u3002\u4E00\u76F4\u6301\u7EED\u7528\u81F3\u7622\u75D5\u6210\u719F\uFF0C\u5373\u989C\u8272\u4E0E\u5468\u56F4\u76AE\u80A4\u76F8\u4F3C\uFF0C\u8868\u9762\u65E0\u6269\u5F20\u7684\u6BDB\u7EC6\u8840\u7BA1\uFF0C\u539A\u5EA6\u53D8\u8584\uFF0C\u8D28\u5730\u53D8\u8F6F\uFF0C\u65E0\u75D2\u75DB\u7B49\u4E0D\u9002\u75C7\u72B6[5,6]\u3002</p><p><strong>03</strong></p><p><strong>\u8FD9\u4E9B\u60C5\u5F62\u4E0D\u9700\u8981/\u4E0D\u9002\u5408\u4F7F\u7528\u53BB\u75A4\u818F</strong></p><p>\u6709\u7684\u95EE\u9898\u53EF\u80FD\u4E0D\u662F\u7622\u75D5\uFF0C\u6216\u8005\u662F\u7279\u6B8A\u7C7B\u578B\u7684\u7622\u75D5\uFF0C\u5E76\u4E0D\u9002\u5408\u9009\u62E9\u53BB\u75A4\u4EA7\u54C1\u3002</p><p><strong>\u708E\u75C7\u540E\u8272\u7D20\u6C89\u7740</strong></p><p>\u8FD9\u662F\u76AE\u80A4\u708E\u75C7\u540E\u51FA\u73B0\u7684\u53CD\u5E94\u6027\u8272\u7D20\u6C89\u7740\uFF0C\u662F\u76AE\u80A4\u4E2D\u916A\u6C28\u9178\u9176\u6D3B\u6027\u589E\u52A0\u3001\u4EA7\u751F\u8FC7\u591A\u8272\u7D20\u7684\u7ED3\u679C\u3002\u866B\u54AC\u76AE\u708E\u3001\u6E7F\u75B9\u3001\u75E4\u75AE\u7B49\u5404\u79CD\u76AE\u80A4\u708E\u75C7\u5747\u53EF\u80FD\u7EE7\u53D1\u708E\u75C7\u540E\u8272\u7D20\u6C89\u7740\u3002</p><p>\u591A\u6570\u60C5\u51B5\u4E0B\u8272\u7D20\u6C89\u7740\u4F1A\u968F\u7740\u65F6\u95F4\u63A8\u79FB\u6700\u7EC8\u81EA\u884C\u6D88\u9000\u3002</p><p>\u9632\u6CBB\u7B56\u7565\u4E3B\u8981\u662F\u9632\u6652\u3001\u6CBB\u7597\u57FA\u7840\u75BE\u75C5\uFF0C\u5FC5\u8981\u65F6\u5916\u7528\u6C22\u918C\u3001\u7EF4A\u9178\u3001\u58EC\u4E8C\u9178\u7B49\u4FC3\u8FDB\u8272\u6C89\u6D88\u9000\u3002\u76EE\u524D\u6CA1\u6709\u8BC1\u636E\u663E\u793A\u7845\u51DD\u80F6/\u7845\u80F6\u7247\u80FD\u6539\u5584\u8FD9\u7C7B\u708E\u75C7\u540E\u8272\u7D20\u6C89\u7740\uFF0C\u6240\u4EE5\u9488\u5BF9\u75D8\u5370\u3001\u76AE\u708E\u6E7F\u75B9\u540E\u7684\u201C\u9ED1\u5370\u5B50\u201D\uFF0C\u4E0D\u9700\u8981\u4F7F\u7528\u53BB\u75A4\u818F[9]\u3002</p><p><strong>\u6D45\u8868\u70E7\u4F24</strong></p><p>\u5927\u90E8\u5206\u7684\u70ED\u70E7\u4F24\uFF08\u5305\u62EC\u706B\u7130\u3001\u9AD8\u6E29\u6DB2/\u56FA\u4F53\u3001\u84B8\u6C7D\uFF09\u4EC5\u7D2F\u53CA\u8868\u76AE\u548C\u90E8\u5206\u771F\u76AE\u3002</p><ul><li><p>\u4EC5\u7D2F\u53CA\u8868\u76AE\u7684\u2160\u5EA6\u70E7\u4F24\uFF0C\u4E0D\u8D77\u6C34\u75B1\uFF0C\u5C40\u90E8\u53D1\u7EA2\u3001\u5E72\u71E5\u3001\u75BC\u75DB\uFF0C\u591A\u57281\u5468\u5185\u75CA\u6108\uFF0C\u4E0D\u7559\u8272\u7D20\u6C89\u7740\u548C\u7622\u75D5\u3002</p></li><li><p>\u771F\u76AE\u6D45\u5C42\u53D7\u7D2F\u7684\u6D45\u2161\u5EA6\u70E7\u4F24\uFF0C\u53EF\u572824\u5C0F\u65F6\u5185\u8868\u771F\u76AE\u4E4B\u95F4\u5F62\u6210\u6C34\u75B1\uFF0C\u521B\u9762\u6E7F\u6DA6\u53D1\u7EA2\u3001\u75BC\u75DB\u3001\u751A\u81F3\u5C11\u8BB8\u6E17\u51FA\uFF0C\u591A\u57281~3\u5468\u5185\u6108\u5408\uFF0C\u6108\u5408\u540E\u53EF\u80FD\u51FA\u73B0\u8272\u7D20\u5F02\u5E38\uFF0C\u4F46\u6781\u5C11\u7622\u75D5\u589E\u751F\u3002</p></li></ul><p>\u4E0A\u9762\u8FD9\u4E24\u7C7B\u6D45\u8868\u7684\u70ED\u70E7\u4F24\u4E00\u822C\u4E0D\u9700\u8981\u7528\u5230\u7845\u51DD\u80F6/\u7845\u80F6\u7247\u3002\u7D2F\u53CA\u771F\u76AE\u6DF1\u5C42\u53CA\u4EE5\u4E0B\u7684\u6DF1\u5EA6\u70E7\u4F24\u7559\u75A4\u98CE\u9669\u6781\u9AD8\uFF0C\u9700\u8981\u5728\u4E13\u4E1A\u6307\u5BFC\u4E0B\u8054\u5408\u591A\u79CD\u65B9\u6CD5\uFF08\u5305\u62EC\u7845\u51DD\u80F6/\u7845\u80F6\u7247\uFF09\u6765\u6CBB\u7597\uFF0C\u624D\u80FD\u83B7\u5F97\u6EE1\u610F\u6548\u679C[10]\u3002</p><p><strong>\u840E\u7F29\u6027\u7622\u75D5</strong></p><p>\u5E38\u89C1\u7684\u840E\u7F29\u6027\u7622\u75D5\u4FBF\u662F\u840E\u7F29\u6027\u75E4\u75AE\u7622\u75D5\uFF0C\u4E5F\u5C31\u662F\u4FD7\u79F0\u7684\u201C\u75D8\u5751\u201D\u3002</p><p>\u7845\u51DD\u80F6/\u7845\u80F6\u7247\u5BF9\u8FD9\u7C7B\u840E\u7F29\u6027\u7622\u75D5\u65E0\u6548\u3002\u5982\u679C\u6709\u840E\u7F29\u6027\u7622\u75D5\uFF0C\u4E00\u822C\u9700\u8981\u591A\u79CD\u65B9\u6CD5\u8054\u5408\u5E94\u7528\uFF0C\u5305\u62EC\u6FC0\u5149\u3001\u6CE8\u5C04\u586B\u5145\u3001\u5316\u5B66\u5265\u8131\u548C\u624B\u672F\u5207\u9664\u7B49\u65B9\u5F0F\uFF0C\u7ED3\u5408\u81EA\u8EAB\u60C5\u51B5\u5236\u5B9A\u4E2A\u4F53\u5316\u6CBB\u7597\u65B9\u6848[11]\u3002</p><p><strong>04</strong></p><p><strong>\u9664\u4E86\u53BB\u75A4\u818F\uFF0C\u8FD8\u6709\u54EA\u4E9B\u6539\u5584\u63AA\u65BD\uFF1F</strong></p><p>\u5176\u5B9E\uFF0C\u53BB\u75A4\u4EA7\u54C1\u5E76\u4E0D\u662F\u7279\u6548\u836F\uFF0C\u4E0D\u80FD\u5B8C\u5168\u8FBE\u5230\u201C\u53BB\u201D\u75A4\u7684\u4F5C\u7528\uFF0C\u66F4\u591A\u662F\u8F85\u52A9\u6539\u5584\u800C\u5DF2\u3002\u9664\u4E86\u4F7F\u7528\u53BB\u75A4\u4EA7\u54C1\u5916\uFF0C\u4E0B\u5217\u63AA\u65BD\u4E5F\u4F1A\u6709\u6539\u5584\uFF08\u6216\u8005\u9884\u9632\uFF09\u7684\u4F5C\u7528\uFF1A</p><ul><li><p>\u7622\u75D5\u4F53\u8D28\u8005\uFF1A\u907F\u514D\u4E0D\u5FC5\u8981\u7684\u624B\u672F\u6216\u521B\u4F24\uFF0C\u6BD4\u5982\u7A7F\u8033\u6D1E\u3001\u5207\u75E3\u3002\u76AE\u80A4\u95EE\u9898\u6BD4\u5982\u75E4\u75AE\u3001\u611F\u67D3\u5E94\u5C3D\u65E9\u5904\u7406\uFF0C\u5C3D\u5FEB\u63A7\u5236\u708E\u75C7\uFF0C\u6700\u5927\u7A0B\u5EA6\u7F29\u5C0F\u708E\u75C7\u533A\u57DF\u3002</p></li><li><p>\u624B\u672F\u9884\u9632\u63AA\u65BD\uFF1A\u4FDD\u6301\u521B\u9762\u6E7F\u6DA6\uFF0C\u51CF\u5C11\u521B\u9762\u5F20\u529B\uFF0C\u5B9A\u671F\u6362\u836F\uFF0C\u907F\u514D\u611F\u67D3\u3002</p></li><li><p>\u9632\u6652\uFF1A\u521B\u9762\u6108\u5408\u540E\u6613\u89C1\u8272\u7D20\u6C89\u7740\uFF0C\u65E5\u6652\u4F1A\u52A0\u91CD\u3002\u9632\u6652\u63AA\u65BD\u5305\u62EC\u8986\u76D6\u4F24\u53E3\uFF0C\u4F7F\u7528\u5E7F\u8C31\u7684\u9632\u6652\u971C\u7B49[2]\u3002</p></li></ul><p>\u70B9\u51FB\u53EF\u201C\u95EE\u533B\u751F\u201D</p><p>\u53C2\u8003\u8D44\u6599</p><p>[1] Perkins K, Davey RB, Wallis KA. Silicone gel: a new treatment for burn scars and contractures. Burns Incl Therm Inj. 1983; 9(3): 201-204.</p><p>[2] UpToDate\u4E34\u5E8A\u987E\u95EE\uFF1A\u7622\u75D5\u7599\u7629\u548C\u589E\u751F\u6027\u7622\u75D5.</p><p>[3] O&#39;Brien L, Jones DJ. Silicone gel sheeting for preventing and treating hypertrophic and keloid scars. Cochrane Database Syst Rev. 2013; 2013(9): CD003826.</p><p>[4] Meaume S, Le Pillouer-Prost A, Richert B, et al. Management of scars: updated practical guidelines and use of silicones. Eur J Dermatol. 2014; 24(4): 435-443.</p><p>[5] Kim S, Choi TH, Liu W, et al. Update on scar management: guidelines for treating Asian patients. Plast Reconstr Surg. 2013; 132(6): 1580-1589.</p><p>[6] de Oliveira GV, Gold MH. Silicone sheets and new gels to treat hypertrophic scars and keloids: A short review. Dermatol Ther. 2020; 33(4): e13705.</p><p>[7] Mustoe TA. Evolution of silicone therapy and mechanism of action in scar management. Aesthetic Plast Surg. 2008; 32(1): 82-92.</p><p>[8] Wang F, Li X, Wang X, et al. Efficacy of topical silicone gel in scar management: A systematic review and meta-analysis of randomised controlled trials. Int Wound J. 2020; 17(3): 765-773.</p><p>[9] UpToDate\u4E34\u5E8A\u987E\u95EE\uFF1A\u70E7\u4F24\u7684\u8BC4\u4F30\u548C\u5206\u7C7B\uFF1B\u70E7\u4F24\u540E\u7684\u589E\u751F\u6027\u7622\u75D5\u548C\u7622\u75D5\u7599\u7629.</p><p>[10] UpToDate\u4E34\u5E8A\u987E\u95EE\uFF1A\u708E\u75C7\u540E\u8272\u7D20\u6C89\u7740\u8FC7\u5EA6.</p><p>[11] UpToDate\u4E34\u5E8A\u987E\u95EE\uFF1A\u75E4\u75AE\u7622\u75D5\u7684\u6CBB\u7597.</p><p>**\u533B\u5B66\u6821\u5BF9\uFF1A**\u76AE\u80A4\u79D1\u5510\u6559\u6E05\u533B\u751F</p><p>**\u6392\u7248\uFF1A**\u5F20\u99A8\u96C5</p><hr><p>\u672C\u6587\u9996\u53D1\u4E8E\u300C <strong>\u6021\u79BE\u76AE\u80A4</strong>\u300D\u516C\u4F17\u53F7\uFF0C\u8FD9\u662F\u4E00\u4E2A\u7531 \u76AE\u80A4\u79D1\u533B\u751F\u4EEC\u4F9B\u7A3F\uFF0C\u9075\u5FAA <strong>\u4E13\u4E1A\u53EF\u9760\u3001\u901A\u4FD7\u6613\u61C2</strong>\u7684\u6807\u51C6\u7684\u79D1\u666E\u8D26\u53F7\uFF0C \u5982\u679C\u4F60\u88AB\u76AE\u80A4\u95EE\u9898\u56F0\u6270\uFF0C\u6216\u4E00\u76F4\u5173\u6CE8\u533B\u7F8E\u76F8\u5173\u77E5\u8BC6\uFF0C\u6BD4\u5982\u8272\u6591\u3001\u76B1\u7EB9\u3001\u62A4\u80A4\u54C1\u7B49\uFF0C\u63A8\u8350\u5173\u6CE8\u3002</p>',65),d={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651635253&idx=1&sn=970e33a02a0c9ebf8aa9dcdb6a101d39&chksm=8b5c97cfbc2b1ed9232f159bd5cb9ac764451c28ab149951cebdbd371309559cca0b12cf0631&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},m=i("\u67E5\u770B\u539F\u6587");function h(u,f){const e=t("ExternalLinkIcon");return n(),a(r,null,[g,p("p",null,[p("a",d,[m,o(e)])])],64)}var D=c(l,[["render",h]]);export{D as default};
