import{r as a,o as t,c as n,a as e,b as r,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=c('<h3 id="\u628A\u5B69\u5B50\u72EC\u7559\u8F66\u5185-\u5230\u5E95\u6709\u591A\u5371\u9669" tabindex="-1"><a class="header-anchor" href="#\u628A\u5B69\u5B50\u72EC\u7559\u8F66\u5185-\u5230\u5E95\u6709\u591A\u5371\u9669" aria-hidden="true">#</a> \u628A\u5B69\u5B50\u72EC\u7559\u8F66\u5185\uFF0C\u5230\u5E95\u6709\u591A\u5371\u9669\uFF1F</h3><p>\u6BCF\u5E74\u590F\u5929\uFF0C\u90FD\u4F1A\u9891\u9891\u770B\u5230\u5B69\u5B50\u88AB\u9057\u5FD8\u8F66\u5185\u81F4\u6B7B\u7684\u65B0\u95FB\u3002\u6BD4\u5982\u4ECA\u5E74\uFF0C\u57286\u670828\u65E5\u52307\u670813\u65E5\u8FD9\u534A\u4E2A\u6708\u5185\uFF0C\u4EC5\u6CB3\u5317\u7701\u4E00\u7701\u5C31\u8FDE\u7EED\u53D1\u751F\u4E86\u56DB\u8D77\uFF0C\u90FD\u662F\u5E7C\u513F\u56ED\u7684\u5B69\u5B50\u88AB\u9057\u5FD8\u5728\u6821\u8F66\u5185\u88AB\u6D3B\u6D3B\u70ED\u6B7B\u3002</p><p>\u6B63\u597D\u81EA\u5DF1\u66FE\u5199\u8FC7\u76F8\u5173\u6587\u7AE0\uFF0C\u6240\u4EE5\u91CD\u63A8\u4E00\u4E0B\uFF0C\u5E0C\u671B\u80FD\u8B66\u9192\u66F4\u591A\u4EBA\uFF0C\u8BA9\u8FD9\u6837\u7684\u60B2\u5267\u5C11\u4E00\u4E9B\uFF0C\u4EE5\u4E0B\u662F\u7565\u6709\u4FEE\u6539\u539F\u6587\u3002</p><p>2016\u5E74\uFF0C\u676D\u5DDE\u6709\u4E2A5\u5C81\u7684\u7537\u5B69\u88AB\u53CD\u9501\u5728\u8F66\u91CC\uFF0C\u54ED\u6210\u4E86\u6CEA\u4EBA\uFF0C\u540E\u6765\u627E\u5230\u5BB6\u957F\uFF0C\u53D1\u73B0\u4E24\u53E3\u5B50\u662F\u533B\u9662\u7684\uFF0C\u5B69\u5B50\u75C5\u4E86\u6CA1\u4EBA\u5E26\uFF0C\u800C\u533B\u9662\u53C8\u89C4\u5B9A\u4E0D\u80FD\u5E26\u5B69\u5B50\u5230\u533B\u9662\u4E0A\u73ED\uFF0C\u5B69\u5B50\u7238\u7238\u60F3\u7740\u81EA\u5DF1\u67E5\u623F\u53EA\u8981\u534A\u4E2A\u5C0F\u65F6\uFF0C\u5C31\u628A\u751F\u75C5\u7684\u5B69\u5B50\u53CD\u9501\u5728\u8F66\u91CC\u4E86\u3002</p><p>\u8FD9\u4E2A\u4E8B\u8BA9\u5F88\u591A\u4EBA\u4E3A\u533B\u751F\u7684\u8F9B\u82E6\u611F\u5230\u550F\u5618\uFF0C\u5C24\u5176\u662F\u4E00\u4E9B\u7236\u6BCD\u4EEC\uFF0C\u56E0\u4E3A\u5F88\u591A\u4EBA\u90FD\u7ECF\u5386\u8FC7\u5B69\u5B50\u6CA1\u4EBA\u5E26\u81EA\u5DF1\u5374\u8981\u4E0A\u73ED\u7684\u7A98\u5883\u3002\u8FD9\u4E2A\u533B\u751F\u7236\u4EB2\u4E4B\u6240\u4EE5\u8FD9\u6837\u505A\uFF0C\u53EF\u80FD\u6027\u6BD4\u8F83\u5927\u7684\u662F\u4ED6\u6CA1\u610F\u8BC6\u5230\u8FD9\u6837\u505A\u6709\u591A\u5371\u9669\uFF0C\u5426\u5219\u54EA\u6015\u662F\u8981\u4E22\u5DE5\u4F5C\u4E5F\u4E0D\u4F1A\u8FD9\u6837\u505A\u3002</p><p>\u628A\u5B69\u5B50\u7559\u5728\u8F66\u5185\u600E\u4E48\u4F1A\u6709\u751F\u547D\u5371\u9669\uFF1F</p><p>\u6211\u4EEC\u53BB\u5BA4\u5916\u53D6\u8F66\u7684\u65F6\u5019\uFF0C\u6253\u5F00\u8F66\u95E8\u5C31\u80FD\u611F\u53D7\u5230\u6251\u9762\u800C\u6765\u7684\u70ED\u6C14\uFF0C\u5EA7\u6905\u6478\u8D77\u6765\u90FD\u53D1\u70EB\uFF0C\u8981\u5439\u5F88\u4E45\u7A7A\u8C03\u624D\u6562\u8FDB\u53BB\uFF0C\u8FD9\u662F\u56E0\u4E3A\u8F66\u8F86\u505C\u5728\u5BA4\u5916\uFF0C\u9633\u5149\u4ECE\u8F66\u73BB\u7483\u900F\u5C04\u8FDB\u8F66\u5185\uFF0C\u70ED\u91CF\u5374\u65E0\u6CD5\u53D1\u6563\u51FA\u53BB\uFF0C\u8F66\u5185\u6E29\u5EA6\u4F1A\u8D8A\u6765\u8D8A\u9AD8\uFF0C\u8FD9\u6E29\u5BA4\u6548\u5E94\u7684\u7ED3\u679C\u3002</p><p>( http://www.nws.noaa.gov)</p><p>\u5982\u679C\u5B69\u5B50\u88AB\u5355\u72EC\u7559\u5728\u8F66\u5185\uFF0C\u4ED6\u4EEC\u81EA\u5DF1\u4E0D\u80FD\u89E3\u5F00\u5B89\u5168\u5E26\uFF0C\u6253\u5F00\u8F66\u95E8\u9003\u751F\uFF0C\u91CC\u9762\u8D8A\u6765\u8D8A\u70ED\uFF0C\u800C\u4ED6\u4EEC\u7684\u4F53\u8868\u9762\u79EF\u76F8\u5BF9\u4F53\u91CD\u66F4\u5927\uFF0C\u5728\u9AD8\u6E29\u73AF\u5883\u4E0B\u66F4\u5BB9\u6613\u4E22\u5931\u6C34\u5206\uFF0C\u4F53\u6E29\u8C03\u63A7\u80FD\u529B\u53C8\u4E0D\u5982\u5927\u4EBA\uFF0C\u5BF9\u5916\u754C\u7684\u9AD8\u6E29\u4E5F\u66F4\u654F\u611F\uFF0C\u5F88\u5FEB\u5C31\u4F1A\u53D1\u751F\u8131\u6C34\uFF0C\u4E2D\u6691\u3001\u4F11\u514B\uFF0C\u751A\u81F3\u6B7B\u4EA1\uFF0C\u770B\u770B\u4E0B\u9762\u7684\u89C6\u9891\u4F60\u53EF\u80FD\u66F4\u6709\u4F53\u4F1A\u3002</p><p>\u89C6\u9891\u91CC\u6700\u540E\u7684\u6587\u5B57\u8BF4\uFF0C\u5728\u7F8E\u56FD\uFF0C\u6BCF10\u5929\u5C31\u6709\u4E00\u4E2A\u5B69\u5B50\u88AB\u7559\u5728\u8F66\u5185\u88AB\u70ED\u6B7B\uFF0C\u4ECE1998\u8D77\u5DF2\u7ECF\u6709575\u4E2A\u5B69\u5B50\u56E0\u6B64\u6B7B\u4EA1\uFF0C\u53EA\u9700\u898115\u5206\u949F\u5C31\u53EF\u4EE5\u7ED9\u5B69\u5B50\u5E26\u6765\u81F4\u547D\u6027\u7684\u8111\u3001\u80BE\u635F\u4F24\u3002\u5728\u5F88\u591A\u56FD\u5BB6\uFF0C\u5355\u72EC\u628A\u5B69\u5B50\u7559\u5728\u8F66\u5185\u662F\u8981\u4E0A\u6CD5\u5EAD\u7684\uFF0C\u5982\u679C\u662F\u6545\u610F\u8FD9\u6837\u505A\uFF0C\u90A3\u5C31\u662F\u72AF\u7F6A\u3002</p><p>\u7F8E\u56FD\u6BCF\u5E74\u88AB\u70ED\u6B7B\u5728\u8F66\u5185\u7684\u5B69\u5B50\u6570\u91CF</p><p>\u4E0A\u56FE\u662F\u7F8E\u56FD1998-2009\u5E74\u6BCF\u5E74\u88AB\u70ED\u6B7B\u5728\u8F66\u5185\u7684\u5B69\u5B50\u6570\u91CF\u3002\u6709\u4EBA\u53EF\u80FD\u4F1A\u89C9\u5F97\u53EA\u6709\u628A\u8F66\u7A97\u5173\u95ED\u653E\u5728\u592A\u9633\u5E95\u4E0B\u6652\u624D\u4F1A\u6709\u8FD9\u79CD\u5371\u9669\uFF0C\u8FD9\u4E2A\u7236\u4EB2\u7ED9\u8F66\u7A97\u7559\u4E86\u7F1D\uFF0C\u800C\u4E14\u662F\u5728\u4E0B\u96E8\u5929\u7684\u65E9\u4E0A\uFF0C\u534A\u4E2A\u5C0F\u65F6\u662F\u6CA1\u4E8B\u7684\uFF0C\u4F46\u4E8B\u5B9E\u5E76\u975E\u5982\u6B64\u3002</p><p>\u6709\u4EBA\u505A\u8FC7\u8BD5\u9A8C\uFF0C\u4E00\u8F86\u84DD\u9ED1\u8272\u8F66\u5173\u4E0A\u8F66\u7A97\uFF0C\u4E00\u8F86\u6D45\u7070\u8272\u8F66\u5F00\u7740\u8F66\u7A97\uFF0C\u653E\u572834\u2103\u7684\u5BA4\u6E29\u4E0B\uFF0C\u5F00\u7740\u7A97\u7684\u8F66\u5185\u6E29\u5EA6\u662F\u8981\u4F4E\u4E00\u4E9B\uFF0C\u4F46\u6E29\u5EA6\u4E0A\u5347\u5E45\u5EA6\u548C\u901F\u5EA6\u5DEE\u522B\u5E76\u4E0D\u5927\uFF0C\u53EA\u9700\u898120\u5206\u949F\uFF0C\u4E24\u8F86\u8F66\u5185\u7684\u6E29\u5EA6\u90FD\u8D85\u8FC7\u4E8652\u2103\u3002</p><p>\uFF08http://www.injuryprevention.org\uFF09</p><p>\u80FD\u63A5\u6536\u9633\u5149\u7684\u8F66\u73BB\u7483\u9762\u79EF\u5F88\u5927\uFF0C\u800C\u8F66\u7A97\u80FD\u6563\u53D1\u7684\u70ED\u91CF\u5374\u5F88\u5C11\uFF0C\u8F66\u8F86\u505C\u4E0B\u6765\u540E\u7A7A\u6C14\u5BF9\u6D41\u4E5F\u5F88\u5DEE\uFF0C\u8F66\u5185\u6E29\u5EA6\u5FEB\u901F\u4E0A\u5347\u4E5F\u662F\u96BE\u514D\u7684\uFF0C\u6211\u4EEC\u81EA\u5DF1\u53EF\u80FD\u4E5F\u6709\u8FD9\u6837\u7684\u7ECF\u9A8C\uFF0C\u8F66\u8F86\u5728\u5F00\u52A8\u7684\u65F6\u5019\u4E0D\u89C9\u5F97\u70ED\uFF0C\u4F46\u4E00\u505C\u4E0B\u6765\u5C31\u5F88\u5FEB\u95F7\u70ED\u4E86\u3002</p><p>\u90A3\u662F\u4E0D\u662F\u5728\u9634\u96E8\u5929\u5C31\u5B89\u5168\u7684\u5462\uFF1F\u4E8B\u5B9E\u4E0A\u8F66\u5185\u6E29\u5EA6\u4E3B\u8981\u548C\u5916\u90E8\u6E29\u5EA6\u6709\u5173\uFF0C\u800C\u4E0D\u662F\u5929\u6C14\u72B6\u51B5\uFF0C\u770B\u770B\u4E0B\u9762\u7684\u56FE\u5C31\u66F4\u6E05\u695A\u4E86\uFF0C\u53EA\u8981\u5230\u4E86\u6A59\u8272\u533A\u57DF\u7684\u6E29\u5EA6\uFF0C\u5B69\u5B50\u5C31\u4F1A\u6709\u751F\u547D\u5371\u9669\u300220\u5EA6\u5BA4\u5916\u6C14\u6E29\uFF0C\u5E94\u8BE5\u662F\u5F88\u8212\u9002\u5B9C\u4EBA\u4E86\uFF0C\u4F46\u4ECE\u56FE\u4E2D\u53EF\u4EE5\u770B\u51FA\uFF0C\u54EA\u6015\u662F\u5728\u8FD9\u6837\u7684\u6C14\u6E29\u4E0B\uFF0C\u8F66\u8F86\u5728\u5BA4\u5916\u505C\u7559\u4E00\u4E2A\u5C0F\u65F6\uFF0C\u8F66\u5185\u7684\u6E29\u5EA6\u90FD\u53EF\u4EE5\u8FBE\u523046\u5EA6\u3002</p><p>\uFF08liliput-lounge.de,\u7FFB\u8BD1\uFF1A@Olika-\u5FB7\u56FD\uFF09</p><p>\u800C\u4E14\uFF0C\u6211\u4EEC\u7684\u8BB0\u5FC6\u603B\u662F\u4F1A\u51FA\u5DEE\u9519\u7684\uFF0C\u6211\u4EEC\u672C\u6765\u53EF\u80FD\u60F3\u7740\u4E0B\u8F66\u4E70\u70B9\u4E1C\u897F\u5C31\u4E0A\u8F66\uFF0C\u53BB\u67E5\u4E2A\u623F\u5C31\u56DE\u6765\uFF0C\u4F46\u8C01\u80FD\u4FDD\u8BC1\u4E0D\u4F1A\u4E00\u4E0B\u8F66\u5C31\u5FD8\u8BB0\u4E86\u8F66\u4E0A\u7684\u5B69\u5B50\u4E86\u5462\uFF1F\u90A3\u4E9B\u5728\u8F66\u5185\u88AB\u6D3B\u6D3B\u70ED\u6B7B\u7684\u5B69\u5B50\uFF0C\u7EDD\u5927\u591A\u6570\u4E0D\u662F\u88AB\u6545\u610F\u5173\u5728\u91CC\u9762\uFF0C\u800C\u662F\u56E0\u4E3A\u5927\u4EBA\u4EEC\u7684\u8BB0\u5FC6\u51FA\u73B0\u4E86\u5DEE\u9519\u3002\u66F4\u4F55\u51B5\uFF0C\u5728\u6211\u4EEC\u7684\u6CBB\u5B89\u73AF\u5883\u4E0B\uFF0C\u8BA9\u5B69\u5B50\u5355\u72EC\u5446\u7740\uFF0C\u4E07\u4E00\u6709\u4EBA\u628A\u4ED6\u62B1\u8D70\u4E86\u5462\uFF1F</p><p>\u6240\u4EE5\uFF0C\u4EFB\u4F55\u65F6\u5019\uFF0C\u65E0\u8BBA\u4EC0\u4E48\u7406\u7531\uFF0C\u90FD\u4E0D\u8981\u628A\u5B69\u5B50\u5355\u72EC\u7559\u5728\u8F66\u5185\uFF0C\u54EA\u6015\u4E00\u5C0F\u4F1A\u3002\u5982\u679C\u4F60\u5F00\u8F66\u5E26\u5B69\u5B50\uFF0C\u4E5F\u53EF\u4EE5\u653E\u70B9\u91CD\u8981\u7269\u54C1\u5728\u5B69\u5B50\u8EAB\u8FB9\uFF0C\u4E0B\u8F66\u62FF\u8FD9\u4E9B\u4E1C\u897F\u65F6\u80FD\u591A\u770B\u5230\u4E00\u6B21\u5B69\u5B50\u3002\u5982\u679C\u53D1\u73B0\u6709\u5B69\u5B50\u88AB\u72EC\u81EA\u5173\u5728\u8F66\u5185\uFF0C\u8981\u7ACB\u5373\u62A5\u8B66\u5E76\u65BD\u6551\u3002</p>',19),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626841&idx=1&sn=e3ae90297bfabae3b0d13ad51219204f&chksm=8b5cb4a3bc2b3db526b5788864cbcf24071a730eba51d53f414a0f4f93f69291a58b377ce73b&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function b(f,m){const p=a("ExternalLinkIcon");return t(),n(o,null,[_,e("p",null,[e("a",l,[h,r(p)])])],64)}var w=d(i,[["render",b]]);export{w as default};
