import{r as t,o as a,c as r,a as e,b as n,F as c,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=o('<h3 id="\u5A74\u513F\u8F85\u98DF\u8981\u4E0D\u8981\u52A0\u76D0" tabindex="-1"><a class="header-anchor" href="#\u5A74\u513F\u8F85\u98DF\u8981\u4E0D\u8981\u52A0\u76D0" aria-hidden="true">#</a> \u5A74\u513F\u8F85\u98DF\u8981\u4E0D\u8981\u52A0\u76D0\uFF1F</h3><p>\u4E00\u5C81\u4EE5\u5185\u7684\u5B69\u5B50\u8F85\u98DF\u4E0D\u9700\u8981\u52A0\u76D0\uFF0C\u56E0\u4E3A\u5BF9\u4E8E\u8FD9\u4E2A\u5E74\u9F84\u6BB5\u7684\u5B69\u5B50\uFF0C\u901A\u8FC7\u996E\u98DF\u53EF\u4EE5\u83B7\u5F97\u8EAB\u4F53\u6240\u9700\u8981\u7684\u94A0\u3002</p><p>\u52A0\u5165\u76D0\u4E4B\u7C7B\u7684\u8C03\u5473\u5242\uFF0C\u4E00\u65B9\u9762\u4F1A\u589E\u52A0\u5B69\u5B50\u80BE\u810F\u8D1F\u62C5\uFF0C\u53E6\u4E00\u65B9\u9762\u53EF\u80FD\u5F71\u54CD\u5B69\u5B50\u7684\u4EE5\u540E\u7684\u996E\u98DF\u4E60\u60EF\uFF0C\u91CD\u76D0\u7684\u996E\u98DF\u4E60\u60EF\u53EF\u4F1A\u589E\u52A0\u6210\u5E74\u540E\u9AD8\u8840\u538B\u7B49\u5FC3\u8840\u7BA1\u75BE\u75C5\u98CE\u9669\uFF0C\u7C7B\u4F3C\u7684\u89C2\u70B9\u786E\u5B9E\u5F88\u591A\u533B\u751F\u90FD\u79D1\u666E\u8FC7\u3002</p><p>\u4ECA\u5929\uFF0C\u6211\u4EEC\u770B\u5230\u4E00\u7BC7\u300A\u91CD\u78C5\uFF5C\u5A74\u513F\u8F85\u98DF\u4E0D\u80FD\u52A0\u76D0\uFF1F\u513F\u79D1\u4E34\u5E8A\u6848\u4F8B\u544A\u8BC9\u4F60\uFF1A\u5F88\u53EF\u80FD\u662F\u8BEF\u5BFC\uFF01\u300B\u7684\u516C\u4F17\u53F7\u63A8\u6587\uFF0C\u6587\u7AE0\u662F\u4E00\u4F4D\u81EA\u79F0\u4E3A\u533B\u751F\u7684\u4F5C\u8005\u5199\u7684\uFF0C\u5F00\u7BC7\u8BF4\u81EA\u5DF1\u63A5\u8BCA\u4E86\u51E0\u4E2A\u5947\u602A\u75C5\u4F8B\uFF1A</p><p>\u8FD9\u4F4D\u4F5C\u8005\u8FD8\u8BF4\uFF1A\u8FD9\u51E0\u4E2A\u5B9D\u5B9D\u90FD\u6709\u4E2A\u5171\u540C\u7279\u70B9\uFF1A\u8F85\u98DF\u4E2D\u4E00\u7C92\u76D0\u90FD\u6CA1\u52A0\u8FC7\uFF01</p><p>\u4F5C\u8005\u56E0\u6B64\u9AD8\u5EA6\u6000\u7591\u8FD9\u4E9B\u75C5\u4F8B\u548C\u65E0\u76D0\u996E\u98DF\u6709\u5173\u3002\u540C\u65F6\uFF0C\u4F5C\u8005\u5F15\u8D77\u4E86\u4E00\u4E9B\u6570\u636E\uFF0C\u5E76\u6839\u636E\u8FD9\u4E2A\u5E74\u9F84\u6BB5\u7684\u5B69\u5B50\u996E\u98DF\u7279\u70B9\u8BA1\u7B97\u4E86\u4E00\u4E0B\u53EF\u80FD\u7684Na\u7684\u6444\u5165\u91CF\uFF0C\u7136\u540E\u63A8\u7B97\u51FA\u5982\u679C9\u4E2A\u6708\u7684\u5B69\u5B50\u5982\u679C\u8F85\u98DF\u4E0D\u52A0\u76D0\uFF0C\u76D0\u7684\u6444\u5165\u91CF\u80AF\u5B9A\u6EE1\u8DB3\u4E0D\u4E86\u6307\u5357\u7684\u63A8\u8350\u91CF\uFF0C\u636E\u6B64\u5F97\u51FA\u5A74\u513F\u8F85\u98DF\u4E0D\u52A0\u76D0\u7684\u7ED3\u8BBA\u662F\u8BEF\u5BFC\u3002</p><p>\u8FD9\u7BC7\u6587\u7AE0\u770B\u8D77\u6765\u8BBA\u8BC1\u4E25\u5BC6\uFF0C\u6709\u7406\u6709\u636E\uFF0C\u5F88\u591A\u8BFB\u8005\u770B\u4E86\u4E5F\u7EB7\u7EB7\u7559\u8A00\u8868\u793A\uFF1A\u201C\u5B66\u4E60\u4E86\u201D\u3001\u201C\u5373\u5C06\u6539\u5199\u5386\u53F2\uFF0C\u4E86\u4E0D\u8D77\u201D\u3002\u679C\u771F\u5982\u6B64\u5417\uFF1F\u6211\u4EEC\u4E00\u8D77\u6765\u5206\u6790\u4E00\u4E0B\u3002</p><p><strong>6-12\u6708\u9F84\u7684\u5B9D\u5B9D\uFF0C\u6BCF\u5929\u94A0\u6444\u5165\u7684\u9002\u5B9C\u91CF\u662F\u591A\u5C11\uFF1F</strong></p><p>\u867D\u7136\u6587\u7AE0\u4E2D\u8FDB\u884C\u4E86\u5927\u91CF\u7684\u63A8\u7B97\u548C\u6570\u636E\u5F15\u7528\uFF0C\u4F46\u662F\uFF0C\u4ECE\u6839\u672C\u4E0A\u6765\u8BB2\uFF0C\u4F5C\u8005\u5BF96-12\u6708\u9F84\u5A74\u513F\u6BCF\u65E5\u94A0\u6444\u5165\u9002\u5B9C\u91CF\u7684\u7406\u89E3\u662F\u6709\u504F\u5DEE\u7684\u3002</p><p>\u56FE\u7247\u622A\u5C4F\u81EA\u516C\u53F7\u63A8\u6587</p><p>\u4F5C\u8005\u5F15\u7528\u4E862013\u7248\u300A\u4E2D\u56FD\u5C45\u6C11\u81B3\u98DF\u8425\u517B\u7D20\u53C2\u8003\u6444\u5165\u91CF\u300B\uFF0C\u4EE59\u6708\u9F84\u5B9D\u5B9D\u4E3A\u4F8B\uFF0C\u5F97\u51FA\u94A0\u7684\u6BCF\u65E5\u9002\u5B9C\u6444\u5165\u91CF\u662F500\u6BEB\u514B\uFF0C\u53C8\u6839\u636E\u300A2010\u5E74\u7F8E\u56FD\u81B3\u98DF\u6307\u5357\u300B\uFF0C\u5F97\u51FA9\u6708\u9F84\u5B9D\u5B9D\u6BCF\u5929\u94A0\u7684\u9002\u5B9C\u6444\u5165\u91CF\u662F680\u6BEB\u514B\u3002</p><p>\u4F5C\u8005\u4E3E\u4F8B\u76849\u6708\u9F84\u5B9D\u5B9D\uFF0C\u5C31\u4F5C\u8005\u5F15\u7528\u7684\u4E24\u4E2A\u6307\u5357\u6765\u770B\uFF0C\u9002\u914D\u7684\u5E74\u9F84\u6BB5\u662F6-12\u6708\u9F84\uFF0C\u57282013\u7248\u300A\u4E2D\u56FD\u5C45\u6C11\u81B3\u98DF\u8425\u517B\u7D20\u53C2\u8003\u6444\u5165\u91CF\u300B\u91CC\uFF0C\u6BCF\u65E5\u9002\u5B9C\u6444\u5165\u91CF\u662F350\u6BEB\u514B\uFF1B\u300A2010\u5E74\u7F8E\u56FD\u81B3\u98DF\u6307\u5357\u300B\u6BCF\u65E5\u9002\u5B9C\u6444\u5165\u91CF\u662F370\u6BEB\u514B\u3002</p><p>\u4F46\u662F\uFF0C\u4F5C\u8005\u5374**\u6839\u636E\u4E24\u4E2A\u6708\u9F84\u8282\u70B9\u7684\u6BCF\u65E5\u9002\u5B9C\u6444\u5165\u91CF\u6570\u503C\u5DEE\uFF0C\u9664\u4EE5\u6708\u6570\u63A8\u7B97\u51FA9\u6708\u9F84\u5A74\u513F\u7684\u6BCF\u65E5\u94A0\u9002\u5B9C\u6444\u5165\u91CF500\u6BEB\u514B\u6216\u8005680\u6BEB\u514B\uFF0C**\u663E\u7136\u8D85\u51FA\u4E86\u6307\u5357\u76F8\u5E94\u5E74\u9F84\u6BB5\u7684\u9002\u5B9C\u6444\u5165\u91CF\u4E86\uFF0C\u4E5F\u662F\u5BF9\u6307\u5357\u63A8\u8350\u7684\u53C2\u8003\u6444\u5165\u91CF\u7684\u9519\u8BEF\u89E3\u8BFB\u3002</p><p><strong>\u5BF99\u4E2A\u6708\u5B69\u5B50\u94A0\u6444\u5165\u91CF\u7684\u63A8\u7B97\u5408\u7406\u5417\uFF1F</strong></p><p>\u4F5C\u8005\u8BA4\u4E3A\u4E0D\u52A0\u76D09\u6708\u9F84\u5B69\u5B50\u94A0\u6444\u5165\u91CF\u4F1A\u4E0D\u8FBE\u6807\uFF0C\u662F\u56E0\u4E3A\u4F5C\u8005\u6839\u636E\u4E00\u4E2A\u98DF\u8C31\u7684\u94A0\u6444\u5165\u91CF\u505A\u4E86\u63A8\u7B97\uFF0C\u4ED6\u63A8\u7B97\u7684\u7ED3\u679C\uFF0C\u4EBA\u5DE5\u5582\u517B\u5E73\u5747\u4E3A210mg\uFF0C\u6BCD\u4E73\u5582\u5E73\u5747\u4E3A150mg\uFF0C\u770B\u8D77\u6765\u4F3C\u4E4E\u8FD8\u662F\u4F4E\u4E8E\u6307\u5357\u7684\u7684\u9002\u5B9C\u6444\u5165\u91CF\uFF0C\u662F\u4E0D\u662F\u8BF4\u660E\u8FD8\u662F\u8981\u52A0\u76D0\u5462\uFF1F</p><p>\u8FD9\u91CC\u7684\u9519\u8BEF\u5728\u4E8E\uFF0C\u4F5C\u8005\u628A\u4E00\u4EFD\u56FA\u5B9A\u98DF\u8C31\u52A0\u4E00\u4E2A\u56FA\u5B9A\u5976\u91CF\u4F5C\u4E3A\u6807\u51C6\u5957\u7528\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u6BCF\u4E00\u4E2A\u5B9D\u5B9D\u5403\u591A\u5C11\u4EE5\u53CA\u5403\u4EC0\u4E48\u662F\u9AD8\u5EA6\u4E2A\u4F53\u5316\u7684\u3002\u518D\u8005\uFF0C\u5C31\u7B97\u662F\u540C\u4E00\u4E2A\u5B9D\u5B9D\uFF0C\u4ECA\u5929\u5403\u4EC0\u4E48\uFF0C\u660E\u5929\u5403\u4EC0\u4E48\uFF0C\u90FD\u662F\u52A8\u6001\u53D8\u5316\u7684\u3002\u56E0\u6B64\uFF0C\u4E14\u4E0D\u8BBA\u4F5C\u8005\u6587\u7AE0\u793A\u4F8B\u91CC\u63D0\u5230\u7684\u98DF\u8C31\u548C\u5976\u91CF\u662F\u5426\u5408\u7406\uFF0C\u4EC5\u51ED\u4E00\u4E2A\u793A\u4F8B\u6765\u8BA1\u7B97\u5E76\u5F97\u51FA\u4E00\u4E2A\u5B69\u5B50\u7684\u94A0\u6444\u5165\u91CF\u662F\u5426\u5145\u8DB3\u7684\u505A\u6CD5\u662F\u4E0D\u5408\u7406\u7684\u3002</p><p>\u5728\u4E00\u4E2A\u4E0D\u5408\u7406\u7684\u65B9\u5F0F\u63A8\u7B97\u5B69\u5B50\u7684\u94A0\u6444\u5165\u91CF\uFF0C\u81EA\u7136\u5F97\u51FA\u4E0D\u51C6\u786E\u7684\u6570\u503C\uFF0C\u7136\u540E\u518D\u7528\u8FD9\u4E2A\u4E0D\u51C6\u786E\u7684\u6570\u503C\u53BB\u548C\u4E00\u4E2A\u9519\u8BEF\u89E3\u8BFB\u7684\u94A0\u6444\u5165\u91CF\uFF0C\u81EA\u7136\u4F1A\u5F97\u51FA\u4E00\u4E2A\u94A0\u6444\u5165\u4E0D\u8DB3\u7684\u7ED3\u8BBA\u3002</p><p>\u51B5\u4E14\uFF0C\u8FD9\u4E2A\u4F5C\u8005\u81EA\u5DF1\u63D0\u4F9B\u7684\u8FD9\u4E9B\u75C5\u4F8B\u7279\u70B9\u91CC\uFF0C\u672C\u8EAB\u5C31\u5B58\u5728\u77DB\u76FE\u4E4B\u5904\uFF0C\u4E00\u9762\u8BF4\u5B69\u5B50\u201C\u808C\u5F20\u529B\u8F83\u4F4E\u201D\uFF0C\u4E00\u9762\u8BF4\u201C\u4F53\u68C0\u672A\u53D1\u73B0\u6709\u795E\u7ECF\u7CFB\u7EDF\u9633\u6027\u8BC1\u636E\u201D\u8FD9\u5DF2\u7ECF\u662F\u5728\u81EA\u5DF1\u6253\u8138\u4E86\u3002</p><p>\u5176\u5B9E\uFF0C\u201C\u4E00\u5C81\u4EE5\u5185\u7684\u5B69\u5B50\u8F85\u98DF\u4E0D\u9700\u8981\u52A0\u76D0\u201D\u8FD9\u89C2\u70B9\uFF0C\u5E76\u4E0D\u662F\u54EA\u4E00\u4E2A\u533B\u751F\u6216\u8005\u5927V\u7684\u89C2\u70B9\uFF0C\u800C\u662F\u4E2D\u56FD2016\u5E74\u53D1\u5E03\u7684\u6700\u65B0\u7248\u300A\u4E2D\u56FD\u5C45\u6C11\u81B3\u98DF\u6307\u5357\u300B\u91CC\u7684\u610F\u89C1\uFF0C\u91CC\u9762\u660E\u786E\u8BF4\u4E86\uFF1A\u201C\u5468\u5C81\u5185\u5A74\u513F\u53EF\u4EE5\u901A\u8FC7\u5976\uFF08\u6BCD\u4E73\u6216\u8005\u914D\u65B9\u5976\uFF09\u4EE5\u53CA\u8F85\u98DF\u53EF\u4EE5\u6EE1\u8DB3350\u6BEB\u514B\u6BCF\u5929\u7684\u9002\u5B9C\u6444\u5165\u91CF\u3002\u201D</p><p>\u6765\u6E90\uFF1A2016\u7248\u300A\u4E2D\u56FD\u5C45\u6C11\u81B3\u98DF\u6307\u5357\u300B</p><p>\u6B64\u5916\uFF0C\u4E16\u754C\u536B\u751F\u7EC4\u7EC42015\u5E74\u7684\u6587\u4EF6\u91CC\uFF0C\u4E5F\u660E\u786E\u8BF4\u4E86\u201C\u57286\u4E2A\u6708\u540E\uFF0C\u5E94\u5728\u6BCD\u4E73\u5582\u517B\u7684\u540C\u65F6\uFF0C\u6DFB\u52A0\u5404\u79CD\u8425\u517B\u5145\u5206\u7684\u5B89\u5168\u7A20\u98DF\u3002\u4E0D\u5E94\u5728\u8F85\u98DF\u4E2D\u6DFB\u52A0\u76D0\u548C\u7CD6\u3002\u201D</p><p>http://www.who.int/mediacentre/factsheets/fs394/zh</p><p>\u8FD9\u4F4D\u533B\u751F\u5E94\u8BE5\u81EA\u5DF1\u6CA1\u6709\u8BFB\u8FC7\u4E16\u754C\u536B\u751F\u7EC4\u7EC7\u7684\u610F\u89C1\uFF0C\u4E5F\u6CA1\u6709\u8BFB\u8FC7\u4E2D\u56FD\u6700\u65B0\u7248\u81B3\u98DF\u6307\u5357\u3002</p><p>\u4E5F\u8BB8\uFF0C\u6709\u5BB6\u957F\u4F1A\u95EE\uFF0C\u90A3\u4E07\u4E00\u5B69\u5B50\u4E0D\u597D\u597D\u5403\u996D\uFF0C\u4E0D\u5403\u52A8\u7269\u6E90\u6027\u98DF\u7269\u5462\uFF1F\u90A3\u662F\u4E0D\u662F\u5C31\u94A0\u6444\u5165\u6709\u95EE\u9898\u4E86\u3002\u7684\u786E\u662F\u6709\u53EF\u80FD\u7684\uFF0C\u4F46\u662F\uFF0C\u5047\u5982\u5B69\u5B50\u4E0D\u597D\u597D\u5403\u996D\uFF0C\u5582\u517B\u4E0D\u5F53\uFF0C\u53EF\u80FD\u6700\u62C5\u5FC3\u7684\u5C31\u4E0D\u662F\u94A0\u5230\u5E95\u5403\u4E86\u591A\u5C11\u6BEB\u514B\u4E86\uFF0C\u800C\u662F\u8981\u770B\u5B69\u5B50\u662F\u4E0D\u662F\u4F53\u91CD\u548C\u751F\u957F\u51FA\u95EE\u9898\uFF0C\u662F\u4E0D\u662F\u53D1\u751F\u8D2B\u8840\u7B49\u95EE\u9898\u4E86\u3002</p><p>\u6B64\u5916\uFF0C\u8FD9\u7BC7\u6587\u7AE0\u91CC\u8FD8\u8BF4\u201C1\u5C81\u4EE5\u4E0B\u5A74\u513F\u516C\u8BA4\u4E0D\u63A8\u8350\u8FDB\u98DF\u9E21\u86CB\u767D\u548C\u867E\u87F9\uFF0C\u6D77\u9C9C\uFF0C\u56E0\u4E3A\u6781\u6613\u8FC7\u654F\u201D\uFF0C\u8FD9\u4E2A\u4F5C\u8005\u53EF\u80FD\u4E0D\u77E5\u9053\uFF0C\u5979\u6240\u8BF4\u7684\u201C\u516C\u8BA4\u201D\u672C\u6765\u5C31\u662F\u4E00\u4E2A\u8001\u9EC4\u5386\u4E86\uFF0C\u56E0\u4E3A\u6839\u636E\u6700\u65B0\u7684\u7814\u7A76\u8BC1\u636E\uFF0C\u63A8\u8FDF\u6DFB\u52A0\u8FD9\u4E9B\u9AD8\u81F4\u654F\u98CE\u9669\u7684\u98DF\u7269\uFF0C\u5E76\u4E0D\u80FD\u964D\u4F4E\u5B69\u5B50\u53D1\u751F\u8FC7\u654F\u75BE\u75C5\u7684\u98CE\u9669\uFF0C\u6B63\u5E38\u6DFB\u52A0\u8FD9\u4E9B\u98DF\u7269\uFF0C\u53CD\u800C\u53EF\u80FD\u5E2E\u52A9\u964D\u4F4E\u53D1\u751F\u98DF\u7269\u8FC7\u654F\u7684\u98CE\u9669\u3002\u6240\u4EE5\u6700\u65B0\u7684\u6307\u5357\u7684\u8BA4\u4E3A\uFF1A\u54EA\u6015\u662F\u6709\u9AD8\u81F4\u654F\u98CE\u9669\u7684\u98DF\u7269\uFF0C\u6BD4\u5982\u6D77\u9C9C\uFF0C\u725B\u5976\uFF0C\u9E21\u86CB\uFF0C\u82B1\u751F, \u575A\u679C\u7B49\uFF0C\u907F\u514D\u7A92\u606F\u98CE\u9669\u7684\u524D\u63D0\u4E0B\uFF0C\u90FD\u53EF\u4EE5\u5982\u5E38\u6DFB\u52A0\u3002</p><p>https://www.allergy.org.au/; \u6FB3\u5927\u5229\u4E9A\u4E34\u5E8A\u514D\u75AB\u5B66\u53CA\u8FC7\u654F\u5B66\u4F1A</p><p>\u6240\u4EE5\uFF0C\u603B\u7684\u6765\u8BB2\uFF0C\u90A3\u7BC7\u770B\u4F3C\u8BE6\u5B9E\uFF0C\u6709\u7406\u6709\u636E\u7684\u6587\u7AE0\uFF0C\u5176\u5B9E\u662F\u7ECF\u4E0D\u8D77\u63A8\u6572\u3002\u5A74\u513F\u8F85\u98DF\u4E0D\u52A0\u76D0\u662F\u5F53\u524D\u6700\u65B0\u6307\u5357\u7684\u610F\u89C1\uFF0C\u4E5F\u662F\u6709\u4F9D\u636E\u7684\u505A\u6CD5\uFF0C\u5E76\u4E0D\u662F\u8BEF\u5BFC\uFF0C\u6839\u636E\u4E00\u4E9B\u9519\u8BEF\u89E3\u8BFB\u548C\u63A8\u65AD\u8BA4\u4E3A\u5A74\u513F\u8981\u52A0\u76D0\u624D\u662F\u8BEF\u5BFC\u3002\u4F5C\u4E3A\u5BB6\u957F\uFF0C\u770B\u5230\u7C7B\u4F3C\u7684\u89C2\u70B9\u65F6\uFF0C\u591A\u95EE\u771F\u7684\u5417\uFF1F\u662F\u8FD9\u6837\u5417\uFF1F\u53EF\u80FD\u53EF\u4EE5\u8BA9\u6211\u4EEC\u770B\u5F97\u66F4\u6E05\u6670\uFF0C\u505A\u5F97\u66F4\u575A\u5B9A\u3002</p><p>\u672C\u6587\u8F6C\u8F7D\u81EA\u516C\u4F17\u53F7\uFF1A\u6FB3\u6D32\u5987\u5E7CDr\u97E9\uFF0C\u6709\u5220\u6539</p>',28),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627119&idx=1&sn=dcc090e82c67a38756ed512ad3d27b5f&chksm=8b5cb795bc2b3e83b2746eec1693fa856819015596d5911b5a2e22e5a1cdd28467b95cb6ed6f&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=s("\u67E5\u770B\u539F\u6587");function m(f,b){const p=t("ExternalLinkIcon");return a(),r(c,null,[_,e("p",null,[e("a",h,[l,n(p)])])],64)}var u=d(i,[["render",m]]);export{u as default};
