import{r,o as a,c as t,a as e,b as n,F as o,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=s('<h3 id="\u4E4C\u9F99\u540E\u8BB0" tabindex="-1"><a class="header-anchor" href="#\u4E4C\u9F99\u540E\u8BB0" aria-hidden="true">#</a> \u4E4C\u9F99\u540E\u8BB0</h3><p>\u5199\u4E0A\u4E00\u7BC7\u6587\u7AE0\u7684\u65F6\u5019\uFF0C\u5728\u53D1\u73B0\u90A3\u4E2A\u6307\u5357\u7684\u4E4C\u9F99\u4E4B\u540E\uFF0C\u6709\u70B9\u4E0D\u6562\u76F8\u4FE1\uFF0C\u867D\u7136\u6211\u81EA\u5DF1\u53CD\u590D\u770B\u4E86\u51E0\u904D\u786E\u8BA4\u5C31\u662F\u4E00\u4E2A\u4E4C\u9F99\uFF0C\u4F46\u987E\u53CA\u5230\u90A3\u662F\u4E00\u4E2A\u53C8\u4F17\u591A\u4E1A\u5185\u4E13\u5BB6\u5171\u540C\u5236\u5B9A\u7684\u4E13\u4E1A\u6307\u5357\uFF0C\u6240\u4EE5\u4ECD\u7136\u6709\u70B9\u62C5\u5FC3\u662F\u4E0D\u662F\u81EA\u5DF1\u770B\u8D70\u773C\u4E86\uFF0C\u6240\u4EE5\u6587\u7AE0\u5199\u5B8C\u4E4B\u540E\u53D1\u7ED9\u51E0\u4E2A\u540C\u884C\u786E\u8BA4\u4E4B\u540E\u624D\u53D1\u5E03\u3002</p><p>\u6587\u7AE0\u53D1\u5E03\u540E\uFF0C\u6211\u539F\u672C\u9884\u8BA1\u4F1A\u5F15\u8D77\u5F88\u5927\u53CD\u54CD\uFF0C\u4E34\u5E8A\u6307\u5357\u7F16\u64B0\u5F15\u7528\u6587\u732E\u7684\u5185\u5BB9\uFF0C\u771F\u662F\u8038\u4EBA\u542C\u95FB\uFF0C\u4F46\u5B9E\u9645\u53CD\u54CD\u5374\u8FDC\u4F4E\u4E8E\u6211\u7684\u9884\u671F\u3002\u662F\u7684\uFF0C\u8FD9\u91CC\u6BD5\u7ADF\u662F\u4E2D\u56FD\uFF0C\u4EC0\u4E48\u4E8B\u5927\u5BB6\u90FD\u53F8\u7A7A\u89C1\u602A\u4E86\u3002\u76F4\u5230\u4E01\u9999\u56ED\u5FAE\u4FE1\u8F6C\u53D1\u4E4B\u540E\uFF0C\u6709\u4E9B\u513F\u79D1\u540C\u884C\u7ED9\u6211\u4F20\u6765\u4ED6\u4EEC\u540C\u4E8B\u7684\u53CD\u9988\uFF0C\u4E45\u4E0D\u8054\u7CFB\u7684\u540C\u5B66\u7A81\u7136\u8054\u7CFB\u6211\uFF0C\u6211\u77E5\u9053\u5B83\u8FD8\u662F\u5728\u533B\u7597\u754C\u5F15\u8D77\u4E86\u4E00\u4E9B\u6CE2\u6F9C\u3002</p><p>\u540C\u884C\u4EEC\u7684\u53CD\u9988\u5927\u591A\u8BA4\u540C\u6211\u7684\u770B\u6CD5\uFF0C\u56FD\u5185\u5229\u5DF4\u97E6\u6797\u7684\u6EE5\u7528\u771F\u662F\u592A\u4E25\u91CD\u4E86\uFF0C\u4E0D\u4EC5\u4EC5\u662F\u611F\u5192\uFF0C\u624B\u8DB3\u53E3\u75C5\u3001\u8F6E\u72B6\u75C5\u6BD2\u8179\u6CFB\u4E5F\u5F88\u591A\u5728\u7528\uFF0C\u800C\u4E14\u5F88\u591A\u90FD\u662F\u76F4\u63A5\u9759\u8109\u70B9\u6EF4\uFF0C\u4E0D\u4EC5\u4EC5\u5728\u57FA\u5C42\u533B\u9662\uFF0C\u8FDE\u5317\u4EAC\u4E0A\u6D77\u8FD9\u4E9B\u5927\u57CE\u5E02\u7684\u513F\u7AE5\u4E13\u79D1\u533B\u9662\u4E5F\u5728\u7528\uFF0C\u5F53\u7136\u6211\u4EEC\u533B\u9662\u4E5F\u4E00\u6837\u5728\u7528\u3002\u4F46\u4E5F\u6709\u4E3A\u90A3\u4E9B\u4E13\u5BB6\u9E23\u4E0D\u5E73\u7684\uFF0C\u8BA4\u4E3A\u4E0D\u80FD\u56E0\u4E3A\u4E00\u4E2A\u5C0F\u9519\u8BEF\u5C31\u5426\u8BA4\u4ED6\u4EEC\u7684\u6240\u6709\uFF0C\u6BD5\u7ADF\u540D\u5355\u4E0A\u8FD9\u4E9B\u4E13\u5BB6\u5BF9\u513F\u7AE5\u547C\u5438\u505A\u51FA\u4E86\u975E\u5E38\u591A\u7684\u8D21\u732E\u3002</p><p>\u8BF4\u5B9E\u5728\u7684\uFF0C\u5229\u5DF4\u97E6\u6797\u7684\u6EE5\u7528\u4E5F\u4E0D\u662F\u56E0\u4E3A\u8FD9\u7BC7\u6307\u5357\u624D\u5F00\u59CB\uFF0C\u5927\u6982\u662F\u56E0\u4E3A\u5229\u5DF4\u97E6\u6797\u53E6\u4E00\u4E2A\u540D\u5B57\u53EB\u201C\u75C5\u6BD2\u5511\u201D\uFF0C\u836F\u54C1\u8BF4\u660E\u4E66\u53C8\u5C06\u547C\u5438\u9053\u611F\u67D3\u5217\u4E3A\u5B83\u7684\u9002\u5E94\u75C7\uFF0C\u6240\u4EE5\u533B\u751F\u5728\u9009\u836F\u7684\u65F6\u5019\u67E5\u5230\u8BF4\u660E\u4E66\uFF0C\u4E5F\u53D1\u73B0\u4E0D\u4E86\u4EC0\u4E48\u95EE\u9898\u3002\u8981\u81EA\u5DF1\u4E00\u4E2A\u53BB\u6838\u5B9E\u836F\u76D1\u5C40\u6279\u51C6\u7684\u9002\u5E94\u75C7\uFF0C\u8FD9\u5BF9\u5927\u591A\u6570\u533B\u751F\u6765\u8BF4\u7684\u786E\u5F88\u96BE\u3002\u6240\u4EE5\u5728\u5229\u5DF4\u97E6\u6797\u6CBB\u611F\u5192\u8FD9\u4E2A\u95EE\u9898\u4E0A\uFF0C\u533B\u751F\u3001\u5236\u5B9A\u611F\u5192\u6307\u5357\u7684\u4E13\u5BB6\u4EEC\u3001\u836F\u76D1\u90E8\u95E8\u90FD\u6709\u8D23\u4EFB\uFF0C\u4F46\u4E3B\u8981\u8D23\u4EFB\u8FD8\u662F\u6211\u4EEC\u7684\u836F\u76D1\u90E8\u95E8\u3002</p><p>\u81F3\u4E8E\u8FD9\u4E2A\u6307\u5357\uFF0C\u5B83\u8D77\u7684\u4F5C\u7528\u53EF\u80FD\u6CA1\u6709\u90A3\u4E48\u5927\uFF0C\u56E0\u4E3A\u5F88\u591A\u533B\u751F\u6839\u672C\u5C31\u662F\u51ED\u611F\u89C9\u7528\u836F\uFF0C\u6839\u672C\u4E5F\u4E0D\u770B\u4EC0\u4E48\u6307\u5357\u3002\u8FD9\u4E2A\u6307\u5357\u53D1\u5E03\u4E5F\u4E0D\u662F\u4E00\u5929\u4E24\u5929\u4E86\uFF0C\u800C\u662F\u4E00\u5E74\u591A\u4E86\uFF0C\u8FD9\u4E48\u5947\u7279\u7684\u8BF4\u6CD5\u5C45\u7136\u6CA1\u6709\u4E00\u4F4D\u513F\u5185\u79D1\u533B\u751F\u53D1\u73B0\u672C\u8EAB\u4E5F\u53EF\u4EE5\u8BF4\u660E\u4E00\u4E9B\u95EE\u9898\uFF0C\u4F46\u717D\u98CE\u70B9\u706B\u7684\u4F5C\u7528\u80AF\u5B9A\u6709\uFF0C\u5982\u679C\u4F60\u8D28\u7591\u533B\u751F\u7ED9\u611F\u5192\u7684\u5B69\u5B50\u5F00\u5229\u5DF4\u97E6\u6797\uFF0C\u4ED6\u5C31\u53EF\u4EE5\u7406\u76F4\u6C14\u58EE\u628A\u6307\u5357\u7FFB\u51FA\u6765\u7ED9\u4F60\u770B\uFF1A\u547C\u5438\u4E13\u4E1A\u7684\u6743\u5A01\u4E13\u5BB6\u4EEC\u90FD\u8BA4\u4E3A\u53EF\u80FD\u6709\u7528\u3002</p><p>\u81F3\u4E8E\u56FD\u5185\u7684\u6307\u5357\u600E\u4E48\u5F62\u6210\uFF0C\u4F5C\u4E3A\u4E1A\u5185\u4EBA\u58EB\u6211\u5927\u6982\u4E5F\u4E86\u89E3\u4E00\u70B9\u3002\u533B\u5B66\u4F1A\u662F\u5F88\u70ED\u8877\u4E8E\u7EC4\u7EC7\u5404\u7C7B\u5B66\u672F\u4F1A\u8BAE\u7684\uFF0C\u539F\u59CB\u52A8\u529B\u5F53\u7136\u4E0D\u5168\u662F\u4E3A\u4E86\u63A8\u52A8\u533B\u5B66\u7684\u53D1\u5C55\uFF0C\u800C\u662F\u56E0\u4E3A\u6BCF\u4E00\u4E2A\u4F1A\u8BAE\u90FD\u662F\u6709\u5229\u53EF\u56FE\u3002\u636E\u5BA1\u8BA1\u7F72\u7EDF\u8BA1\uFF0C\u4E2D\u534E\u533B\u5B66\u4F1A\u4EC5\u5728\u53BB\u5E74\uFF0C\u5C31\u53EC\u5F00\u4E86160\u4E2A\u5B66\u672F\u4F1A\u8BAE\uFF0C\u5E73\u57472\u5929\u4E00\u4E2A\u4F1A\uFF0C\u4E5F\u6536\u4E86\u533B\u836F\u4F01\u4E1A8.2\u4EBF\u5143\u5E7F\u544A\u8D39\u3002\u6B63\u662F\u56E0\u4E3A\u6709\u8FD9\u4E48\u5927\u7684\u751C\u5934\uFF0C\u5B66\u672F\u4F1A\u8BAE\u4E5F\u6210\u4E3A\u4E86\u9999\u997D\u997D\uFF0C\u8FDE\u533B\u5E08\u534F\u4F1A\u4E5F\u201C\u70ED\u5FC3\u201D\u53C2\u4E0E\u8FDB\u6765\uFF0C\u5404\u7C7B\u4F1A\u8BAE\u8FDE\u7EF5\u4E0D\u7EDD\u3002\u800C\u53C2\u4E0E\u8FD9\u4E9B\u4F1A\u8BAE\u7684\u4E13\u5BB6\uFF0C\u81EA\u7136\u6709\u4E9B\u662F\u771F\u6B63\u7684\u5B66\u672F\u6743\u5A01\uFF0C\u4F46\u591A\u5E74\u4E0D\u67E5\u623F\u4E0D\u505A\u624B\u672F\u4E5F\u4E0D\u770B\u95E8\u8BCA\u7684\u5927\u6709\u4EBA\u5728\uFF0C\u8FD9\u90E8\u5206\u4EBA\u6709\u7684\u5C31\u662F\u533B\u9662\u7684\u884C\u653F\u9886\u5BFC\uFF0C\u6709\u7684\u5C31\u662F\u5C31\u662F\u6BCF\u5929\u98DE\u6765\u98DE\u53BB\u5230\u5904\u8D76\u4F1A\u573A\u7684\u201C\u7A7A\u4E2D\u4E13\u5BB6\u201D\u3002</p><p>\u5728\u5B98\u672C\u4F4D\u7684\u533B\u7597\u884C\u4E1A\u91CC\uFF0C\u6709\u884C\u653F\u804C\u4F4D\u5C31\u5F88\u5BB9\u6613\u83B7\u5F97\u5B66\u672F\u5934\u8854\uFF0C\u4E5F\u5BB9\u6613\u83B7\u5F97\u5404\u79CD\u5927\u5C0F\u4F1A\u8BAE\u7684\u53C2\u4F1A\u8D44\u683C\uFF0C\u4ECE\u800C\u53C2\u4E0E\u5404\u79CD\u6307\u5357\u3001\u6807\u51C6\u7684\u5236\u5B9A\uFF0C\u6240\u4EE5\u7ED3\u679C\u5F80\u5F80\u662F\u90A3\u4E9B\u4E0D\u770B\u75C5\u7684\u9886\u5BFC\u53BB\u53C2\u4E0E\u8FD9\u4E9B\u6307\u5357\u7684\u5236\u5B9A\uFF0C\u800C\u771F\u6B63\u8EAB\u5904\u4E34\u5E8A\u4E00\u7EBF\u7684\u533B\u751F\u8FDE\u53C2\u4E0E\u8BA8\u8BBA\u7684\u8D44\u683C\u90FD\u4E0D\u4F1A\u6709\u3002</p><p>\u800C\u4E8B\u5B9E\u4E5F\u662F\uFF0C\u8FD9\u6837\u7684\u6307\u5357\u5236\u5B9A\u4E5F\u5E76\u4E0D\u9700\u8981\u591A\u5C11\u5B66\u672F\u6C34\u5E73\uFF0C\u56E0\u4E3A\u6307\u5357\u7684\u5F71\u54CD\u529B\u5F88\u5927\uFF0C\u4E5F\u4F1A\u7275\u6D89\u5230\u5F88\u591A\u5229\u76CA\uFF0C\u6BD4\u5982\u836F\u5382\uFF0C\u5176\u5B9E\u53EF\u80FD\u5728\u8BA8\u8BBA\u4F1A\u4E4B\u524D\uFF0C\u6307\u5357\u5185\u5BB9\u90FD\u5DF2\u7ECF\u5199\u597D\u4E86\uFF0C\u8FD9\u4E9B\u53C2\u4F1A\u8005\u4E5F\u53EA\u662F\u88AB\u8BF7\u8FC7\u53BB\u80CC\u80CC\u4E66\u7B7E\u4E2A\u5B57\uFF0C\u5F88\u5C11\u4F1A\u6709\u5927\u5BB6\u60F3\u8C61\u4E2D\u7684\u4EC0\u4E48\u70ED\u70C8\u8BA8\u8BBA\u3002\u9664\u975E\u4F60\u5DF2\u7ECF\u5DF2\u7ECF\u6709\u4E86\u4E00\u8A00\u4E5D\u9F0E\u7684\u5B66\u672F\u5730\u4F4D\uFF0C\u4F60\u6B63\u513F\u516B\u7ECF\u53BB\u8F83\u771F\uFF0C\u63D0\u51FA\u4FEE\u6539\u610F\u89C1\uFF0C\u662F\u4E0D\u53D7\u6B22\u8FCE\u7684\uFF0C\u4F1A\u8BAE\u4E3B\u529E\u65B9\u8D5E\u52A9\u65B9\u53EF\u80FD\u4F1A\u4E0D\u5F00\u5FC3\uFF0C\u4E0B\u6B21\u4ED6\u4EEC\u5C31\u53EF\u80FD\u4E0D\u8BF7\u4F60\u53C2\u52A0\u4E86\uFF0C\u5728\u5B98\u573A\u4E0A\u5982\u9C7C\u5F97\u6C34\u7684\u4EBA\u53C8\u600E\u4F1A\u4E0D\u8C19\u8FD9\u4E9B\u6F5C\u89C4\u5219\u3002\u6240\u4EE5\u8FD9\u4E2A\u4F53\u7CFB\u5C31\u662F\u4E00\u4E2A\u52A3\u5E01\u9010\u826F\u5E01\u7684\u4F53\u7CFB\u3002</p><p>\u56DE\u5230\u533B\u751F\u7684\u5C42\u9762\uFF0C\u5982\u679C\u81EA\u5DF1\u4E5F\u662F\u4E00\u4E2A\u513F\u5185\u79D1\u533B\u751F\uFF0C\u5982\u679C\u4E0A\u7EA7\u533B\u751F\uFF0C\u5176\u5B83\u540C\u4E8B\u90FD\u5728\u7528\u8FD9\u6837\u7528\u836F\uFF0C\u518D\u52A0\u4E0A\u4E00\u70B9\u5229\u76CA\u7684\u8BF1\u60D1\uFF0C\u5373\u4FBF\u81EA\u5DF1\u5F00\u59CB\u6709\u70B9\u7591\u8651\uFF0C\u4F46\u67E5\u770B\u836F\u54C1\u8BF4\u660E\u4E66\u548C\u6307\u5357\u90FD\u652F\u6301\uFF0C\u5408\u7406\u5408\u6CD5\uFF0C\u4E5F\u4F1A\u6162\u6162\u6253\u6D88\u7591\u8651\uFF0C\u4E45\u800C\u4E45\u4E4B\u5C31\u4F1A\u628A\u9519\u8BEF\u5F53\u4F5C\u6B63\u786E\uFF0C\u6700\u540E\u9020\u6210\u73B0\u5728\u5E7F\u6CDB\u6EE5\u7528\u7684\u5C40\u9762\u3002</p><p>\u5229\u5DF4\u97E6\u6797\u7684\u6EE5\u7528\u53EA\u662F\u8FD9\u4E2A\u8FD9\u4E2A\u533B\u7597\u4F53\u7CFB\u95EE\u9898\u7684\u4E00\u4E2A\u7F29\u5F71\uFF0C\u88AB\u6EE5\u7528\u7684\u4E5F\u7EDD\u4E0D\u4EC5\u4EC5\u4E0A\u8FD9\u4E00\u4E2A\u836F\u7269\uFF0C\u6B63\u5982\u6211\u4E4B\u524D\u300A\u4E00\u5F20\u5904\u65B9\u300B\u91CC\u6240\u5206\u6790\u7684\uFF0C\u771F\u6B63\u8BA4\u771F\u53BB\u8003\u7A76\uFF0C\u6211\u4F30\u8BA1\u4E0D\u5408\u7406\u7528\u836F\u7684\u6570\u91CF\u4E0D\u4F1A\u5C11\u4E8E\u5408\u7406\u7528\u836F\uFF0C\u8FD9\u5C31\u662F\u6211\u4EEC\u533B\u7597\u73B0\u72B6\u3002\u8FD9\u5E76\u4E0D\u662F\u533B\u751F\u4EEC\u53D8\u574F\u4E86\uFF0C\u800C\u662F\u7531\u6211\u4EEC\u7684\u533B\u7597\u4F53\u7CFB\u6C34\u5E73\u51B3\u5B9A\u7684\uFF0C\u800C\u6211\u4EEC\u7684\u533B\u7597\u4F53\u7CFB\u53C8\u662F\u7531\u793E\u4F1A\u653F\u6CBB\u4F53\u7CFB\u51B3\u5B9A\u7684\u3002\u4F60\u8981\u76F8\u4FE1\u533B\u751F\u8FD9\u4E2A\u7FA4\u4F53\u7684\u603B\u4F53\u6587\u5316\u6C34\u5E73\u4E0D\u4F1A\u4F4E\u4E8E\u793E\u4F1A\u5E73\u5747\u6C34\u5E73\uFF0C\u4ED6\u4EEC\u8FDB\u5165\u533B\u5B66\u9662\u6821\u4E4B\u524D\u4E5F\u662F\u8DDF\u4F60\u4E00\u6837\u7684\u666E\u901A\u4EBA\uFF0C\u6CA1\u6709\u7406\u7531\u4ED6\u4EEC\u7684\u9053\u5FB7\u6C34\u5E73\u4F1A\u4F4E\u4E8E\u793E\u4F1A\u5E73\u5747\u6C34\u5E73\uFF0C\u6240\u4EE5\u6362\u4E86\u4F60\u6765\u505A\u533B\u751F\u4E5F\u672A\u5FC5\u4F1A\u6BD4\u4ED6\u4EEC\u505A\u5F97\u66F4\u597D\uFF0C\u4E0D\u6539\u53D8\u6211\u4EEC\u7684\u653F\u6CBB\u793E\u4F1A\u4F53\u7CFB\uFF0C\u5C31\u5F88\u96BE\u6539\u53D8\u533B\u7597\u4F53\u7CFB\uFF0C\u4E5F\u5C31\u5F88\u96BE\u6539\u53D8\u533B\u7597\u73B0\u72B6\u3002</p><p>\u6211\u5199\u90A3\u7BC7\u6587\u7AE0\uFF0C\u77ED\u671F\u5185\u4E5F\u8BB8\u53EF\u80FD\u6539\u53D8\u4E00\u70B9\u70B9\u4E1C\u897F\uFF0C\u8FD9\u6B21\u88AB\u6652\u51FA\u6765\u7684\u51E0\u5341\u4E2A\u4E13\u5BB6\u518D\u4E3A\u8FD9\u6837\u7684\u6307\u5357\u80CC\u4E66\u7684\u65F6\u5019\uFF0C\u4E3A\u4E86\u9632\u6B62\u518D\u88AB\u6652\uFF0C\u4E0B\u6B21\u53EF\u80FD\u4F1A\u4E0A\u70B9\u5FC3\uFF0C\u90A3\u4E9B\u518D\u7ED9\u611F\u5192\u5B69\u5B50\u5F00\u8FD9\u4E2A\u836F\u7684\u533B\u751F\uFF0C\u53EF\u80FD\u4E5F\u4F1A\u591A\u659F\u914C\u4E00\u4E0B\uFF0C\u81F3\u5C11\u4ED6\u4EEC\u4E5F\u8981\u62C5\u5FC3\u4E00\u4E0B\u4F60\u4EEC\u8FD9\u4E9B\u88AB\u79D1\u666E\u8FC7\u7684\u5BB6\u957F\u4EEC\u7684\u8D28\u7591\u4E86\uFF0C\u4F46\u6307\u51FA\u5229\u5DF4\u97E6\u6797\u6EE5\u7528\u7684\u6587\u7AE0\u5DF2\u7ECF\u6709\u5F88\u591A\uFF0C\u6307\u671B\u9760\u79D1\u666E\u6587\u7AE0\u6765\u6539\u5584\u533B\u7597\u4F53\u7CFB\u662F\u4E0D\u592A\u73B0\u5B9E\u7684\uFF0C\u9876\u591A\u662F\u7ED9\u5BB6\u957F\u4EEC\u63D0\u4E2A\u9192\u3002</p><p>\u800C\u4F60\u4EEC\u4ECA\u5929\u80FD\u770B\u5230\u8FD9\u4E9B\u6587\u5B57\uFF0C\u5E76\u4E0D\u662F\u56E0\u4E3A\u6211\u6709\u591A\u4E48\u52C7\u6562\u548C\u9AD8\u5C1A\uFF0C\u6211\u4F1A\u5199\u51FA\u6765\u56E0\u4E3A\u6211\u662F\u4E0D\u770B\u611F\u5192\u4E5F\u4E0D\u5F00\u5229\u5DF4\u97E6\u6797\u7684\u513F\u5916\u79D1\u533B\u751F\uFF0C\u4E5F\u4E0D\u592A\u9700\u8981\u62C5\u5FC3\u513F\u5185\u79D1\u6743\u5A01\u4EEC\u7ED9\u6211\u7A7F\u5C0F\u978B\u3002\u6211\u81EA\u5DF1\u4F5C\u4E3A\u533B\u7597\u884C\u4E1A\u7684\u4E00\u5458\uFF0C\u4F5C\u4E3A\u513F\u79D1\u533B\u751F\u7684\u4E00\u5458\uFF0C\u6211\u5F53\u7136\u5E0C\u671B\u81EA\u5DF1\u7684\u884C\u4E1A\u3001\u81EA\u5DF1\u7684\u804C\u4E1A\u662F\u53D7\u4EBA\u5C0A\u656C\u7684\uFF0C\u4F46\u73B0\u5B9E\u662F\u6211\u7684\u884C\u4E1A\u6211\u7684\u804C\u4E1A\u5728\u793E\u4F1A\u4E0A\u662F\u9971\u53D7\u8BDF\u75C5\uFF0C\u81EA\u5DF1\u505A\u533B\u751F\u7684\u65F6\u5019\u62C5\u5FC3\u75C5\u4EBA\u7684\u653B\u51FB\uFF0C\u81EA\u5DF1\u505A\u5BB6\u957F\u7684\u65F6\u5019\u4E5F\u4F1A\u62C5\u5FC3\u5B69\u5B50\u5F97\u5230\u7684\u533B\u7597\u8D28\u91CF\uFF0C\u800C\u8981\u6539\u53D8\u8FD9\u4E9B\u4E0D\u80FD\u5168\u9760\u7C89\u9970\u592A\u5E73\uFF0C\u76F4\u9762\u73B0\u5B9E\uFF0C\u624D\u6709\u53EF\u80FD\u6539\u53D8\u73B0\u72B6\u3002</p><p>\u5F53\u7136\u66F4\u73B0\u5B9E\u4E00\u70B9\u7684\u539F\u56E0\uFF0C\u662F\u56E0\u4E3A\u65E0\u610F\u4E2D\u8BA9\u6211\u770B\u5230\u4E86\u533B\u5B66\u754C\u513F\u79D1\u9891\u9053\u5FAE\u4FE1\u91CC\u90A3\u7BC7\u5229\u5DF4\u97E6\u6797\u7684\u8F6F\u6587\uFF0C\u624D\u8FDB\u4E00\u6B65\u6316\u6398\u51FA\u4E86\u8FD9\u4E2A\u4E4C\u9F99\uFF0C\u7ED9\u8FD9\u4E2A\u5343\u75AE\u767E\u5B54\u7684\u884C\u4E1A\u505A\u4E00\u4E0B\u5544\u6728\u9E1F\uFF0C\u4F55\u4E50\u800C\u4E0D\u4E3A\uFF0C\u5F53\u7136\u4F60\u4EEC\u7684\u652F\u6301\u4E5F\u662F\u6211\u7EE7\u7EED\u5199\u4E0B\u53BB\u7684\u52A8\u529B\u4E4B\u4E00\u3002</p><p>\u987A\u4FBF\u9884\u544A\u4E00\u4E0B\uFF0C\u56E0\u4E3A\u611F\u5192\u662F\u4E00\u4E2A\u5F88\u5E38\u89C1\u7684\u95EE\u9898\uFF0C\u4E5F\u6709\u5F88\u591A\u9677\u9631\uFF0C\u4E0B\u4E00\u7BC7\u6587\u7AE0\u5C31\u6B63\u513F\u516B\u7ECF\u5199\u5199\u611F\u5192\u5427\uFF0C\u4E0D\u8FC7\u56E0\u4E3A\u8981\u5B8C\u6210\u5FAE\u535A\u4E0A\u7684\u4ED8\u8D39\u9605\u8BFB\u4EFB\u52A1\uFF0C\u6682\u65F6\u4E0D\u4F1A\u5728\u5FAE\u4FE1\u63A8\u9001\u4E86\u3002\uFF08\u539F\u521B\u6587\u7AE0\u672A\u7ECF\u8BB8\u53EF\u8C22\u7EDD\u8F6C\u8F7D\uFF09</p><p><strong>\u6743\u5A01\u7684\u80B2\u513F\u79D1\u666E\uFF0C\u6DF1\u5EA6\u7684\u533B\u5B66\u4EBA\u6587</strong></p><p><strong>\u4EC5\u63A8\u9001\u539F\u521B\u6587\u7AE0\uFF0C\u6B22\u8FCE\u5173\u6CE8drpeihg</strong></p>',17),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=201272958&idx=1&sn=f42cda300b5a2f08a9793707f2e5e5e5&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=c("\u67E5\u770B\u539F\u6587");function f(m,x){const p=r("ExternalLinkIcon");return a(),t(o,null,[_,e("p",null,[e("a",l,[h,n(p)])])],64)}var b=d(i,[["render",f]]);export{b as default};
