import{r as a,o as r,c as t,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=c('<h3 id="\u5C0F\u5E0C\u671B\u7684\u5E0C\u671B\u5728\u54EA\u91CC" tabindex="-1"><a class="header-anchor" href="#\u5C0F\u5E0C\u671B\u7684\u5E0C\u671B\u5728\u54EA\u91CC" aria-hidden="true">#</a> \u5C0F\u5E0C\u671B\u7684\u5E0C\u671B\u5728\u54EA\u91CC</h3><p>\u8FD1\u65E5\uFF0C\u56E0\u4E3A\u4E00\u573A\u672A\u8FDB\u884C\u7684\u8BC9\u8BBC\uFF0C\u6C89\u5BC2\u4E86\u51E0\u5E74\u7684\u201C\u5C0F\u5E0C\u671B\u4E8B\u4EF6\u201D\u53C8\u8FDB\u5165\u4E86\u8206\u8BBA\u573A\u3002\u8FD9\u4E2A\u4E8B\u4EF6\u55A7\u56A3\u4E00\u65F6\u7684\u65F6\u5019\uFF0C\u6211\u5BF9\u7F51\u7EDC\u5E76\u6CA1\u592A\u591A\u5174\u8DA3\uFF0C\u4E5F\u6CA1\u600E\u4E48\u5173\u6CE8\uFF0C\u53BB\u5E74\u56E0\u4E3A\u5199\u4E00\u7BC7\u5173\u4E8E\u5F03\u5A74\u7684\u6587\u7AE0\uFF0C\u5927\u81F4\u4E86\u89E3\u4E86\u4E00\u4E0B\u8FC7\u7A0B\uFF0C\u603B\u4F53\u89C9\u5F97\uFF0C\u51ED\u201D\u7231\u5FC3\u201C\u53BB\u62A2\u522B\u4EBA\u7684\u5B69\u5B50\u53BB\u6CBB\u7597\u8FD9\u79CD\u4E8B\u662F\u4E0D\u53EF\u7406\u55BB\u7684\uFF0C\u4F46\u5BF9\u4E8E\u7EC6\u8282\u5E76\u4E0D\u6E05\u695A\u3002</p><p>\u6628\u5929\u770B\u4E86\u8089\u5510\u50E7\u7684\u90A3\u7BC7\u6252\u76AE\u5E16\uFF0C\u4E86\u89E3\u4E86\u4E00\u4E9B\u5B69\u5B50\u75BE\u75C5\u7684\u7EC6\u8282\uFF0C\u4F5C\u4E3A\u5C0F\u513F\u5916\u79D1\u533B\u751F\uFF0C\u809B\u95E8\u95ED\u9501\u6211\u4EEC\u7ECF\u5E38\u8BCA\u6CBB\uFF0C\u4F46\u90A3\u7BC7\u6252\u76AE\u5E16\u91CC\u5BF9\u5B69\u5B50\u75C5\u60C5\u5206\u6790\u8FD8\u662F\u592A\u591A\u4E3B\u89C2\u7684\u81C6\u65AD\uFF0C\u6240\u4EE5\u5FCD\u4E0D\u4F4F\u8BF4\u4E86\u51E0\u53E5\uFF0C\u4E5F\u5F15\u53D1\u4E86\u53CC\u65B9\u9635\u8425\u7684\u4E00\u4E9B\u975E\u8BAE\uFF0C\u5C31\u5E72\u8106\u5199\u7BC7\u957F\u5FAE\u535A\u6765\u8BF4\u8BF4\u8FD9\u4E2A\u4E8B\u3002</p><p>\u4ECE\u53CC\u65B9\u62AB\u9732\u7684\u4FE1\u606F\u6765\u770B\uFF0C\u53EF\u4EE5\u660E\u786E\u7684\u662F\uFF0C\u5B69\u5B50\u6709\u5148\u5929\u6027\u809B\u95E8\u95ED\u9501\uFF0C\u76F4\u80A0\u672B\u7AEF\u6709\u7618\u53E3\uFF0C\u8FD9\u5BF9\u5973\u5A74\u6765\u8BF4\u5F88\u5E38\u89C1\uFF0C\u6211\u4EEC\u5E73\u65F6\u63A5\u8BCA\u809B\u95E8\u95ED\u9501\u7684\u5B69\u5B50\u7B2C\u4E00\u4EF6\u4E8B\u5C31\u662F\u53BB\u786E\u5B9A\u6709\u4E48\u6709\u7618\uFF0C\u7618\u53E3\u5728\u54EA\u91CC\uFF0C\u7618\u53E3\u5927\u4E0D\u5927\uFF0C\u56E0\u4E3A\u8FD9\u76F4\u63A5\u5173\u7CFB\u5230\u5B69\u5B50\u7684\u6CBB\u7597\u65B9\u6848\u3002\u6B63\u662F\u56E0\u4E3A\u8FD9\u4E2A\u5B69\u5B50\u6709\u7618\u53E3\uFF0C\u624D\u6709\u53EF\u80FD\u5728\u751F\u540E\u8FD9\u4E48\u4E45\u6CA1\u505A\u624B\u672F\u8FD8\u80FD\u6D3B\u7740\uFF0C\u5426\u5219\u5927\u4FBF\u4F1A\u628A\u80A0\u5B50\u6491\u7A7F\u5B54\u3002</p><p>\u5BF9\u5973\u5A74\u6765\u8BF4\u7618\u53E3\u5F80\u5F80\u4F4D\u4E8E\u9634\u9053\u4E0B\u65B9\u7684\u821F\u72B6\u7A9D\u4F4D\u7F6E\uFF0C\u6709\u7684\u662F\u5F00\u53E3\u4E8E\u9634\u9053\uFF0C\u4E5F\u6709\u7684\u662F\u5C3F\u9053\u3001\u9634\u9053\u3001\u76F4\u80A0\u5171\u4E00\u4E2A\u5F00\u53E3\uFF0C\u4E5F\u79F0\u4E3A\u6CC4\u6B96\u8154\u7578\u5F62\u6216\u4E00\u7A74\u809B\uFF0C\u65B0\u751F\u513F\u56E0\u4E3A\u5916\u9634\u5F88\u5C0F\uFF0C\u809B\u95E8\u95ED\u9501\u7684\u6709\u4E9B\u5B69\u5B50\u5916\u9634\u4E5F\u53D1\u80B2\u4E0D\u826F\uFF0C\u7A7A\u95F4\u5F88\u5C0F\u6709\u65F6\u5019\u5F88\u96BE\u770B\u6E05\u695A\uFF0C\u6709\u65F6\u5019\u53EA\u80FD\u770B\u5230\u5927\u4FBF\u4ECE\u5916\u9634\u6392\u51FA\u6765\uFF0C\u5374\u641E\u4E0D\u6E05\u695A\u7618\u53E3\u5728\u54EA\u91CC\uFF0C\u4E3A\u4E86\u660E\u786E\uFF0C\u533B\u751F\u5F80\u5F80\u4F1A\u7528\u7EC6\u7BA1\u53BB\u63A2\u67E5\u7618\u53E3\uFF0C\u4F46\u6709\u65F6\u5019\u4E5F\u5F88\u96BE\u63A2\u6E05\u695A\uFF0C\u8FD9\u4E5F\u662F\u4E3A\u4EC0\u4E48\u5929\u6D25\u513F\u7AE5\u533B\u9662\u548C\u548C\u7766\u5BB6\u7684\u8BF4\u6CD5\u4E0D\u7EDF\u4E00\u3002</p><p>\u6709\u65F6\u5019\u4E3A\u4E86\u660E\u786E\u7618\u53E3\u7684\u4F4D\u7F6E\uFF0C\u9700\u8981\u9EBB\u9189\u4E0B\u4F5C\u5185\u955C\u68C0\u67E5\uFF0C\u4F46\u4E0D\u662F\u6240\u6709\u7684\u5B69\u5B50\u90FD\u9700\u8981\u5728\u7B2C\u4E00\u65F6\u95F4\u660E\u786E\u7618\u53E3\u7684\u4F4D\u7F6E\uFF0C\u80FD\u660E\u786E\u66F4\u597D\uFF0C\u660E\u786E\u4E0D\u4E86\u5C31\u8981\u770B\u7618\u53E3\u901A\u7545\u7A0B\u5EA6\u4E86\u3002\u5982\u679C\u627E\u5230\u7618\u53E3\u800C\u4E14\u7618\u53E3\u6BD4\u8F83\u5C0F\uFF0C\u5927\u4FBF\u6392\u5F97\u4E0D\u901A\u7545\uFF0C\u5C31\u53EF\u4EE5\u8BD5\u7740\u4ECE\u7618\u53E3\u91CC\u63D2\u7BA1\u6216\u8005\u8BD5\u7740\u6269\u5927\u7618\u53E3\u6765\u5E2E\u52A9\u6392\u4FBF\u7F13\u89E3\u5B69\u5B50\u7684\u8179\u80C0\uFF0C\u5982\u679C\u8FD9\u4E9B\u65B9\u6CD5\u8FD8\u4E0D\u884C\uFF0C\u90A3\u5C31\u9700\u8981\u5148\u505A\u9020\u7618\u624B\u672F\uFF0C\u4E5F\u5C31\u662F\u628A\u5B69\u5B50\u7684\u5927\u80A0\u63D0\u5230\u809A\u76AE\u4E0A\u53BB\u7136\u540E\u5F00\u4E2A\u53E3\u5B50\uFF0C\u7ED9\u5927\u4FBF\u4E00\u4E2A\u4E34\u65F6\u51FA\u53E3\uFF0C\u540C\u65F6\u4E5F\u53EF\u4EE5\u907F\u514D\u5927\u4FBF\u5F80\u4E0B\u6D41\u7ED9\u7B2C\u4E8C\u6B21\u809B\u95E8\u624B\u672F\u9020\u6210\u6C61\u67D3\u3002\u5982\u679C\u7618\u53E3\u6BD4\u8F83\u5927\uFF0C\u5927\u4FBF\u6392\u5F97\u5F88\u901A\u7545\uFF0C\u90A3\u5C31\u53EF\u4EE5\u76F4\u63A5\u7ED9\u5B69\u5B50\u5582\u517B\uFF0C\u800C\u4E0D\u7740\u6025\u505A\u624B\u672F\uFF0C\u7B49\u5B69\u5B50\u5927\u4E00\u70B9\u4E86\u518D\u505A\u809B\u95E8\u624B\u672F\uFF0C\u56FD\u5185\u5F88\u591A\u533B\u9662\u90FD\u662F\u8FD9\u6837\u505A\uFF0C\u5F53\u7136\u4E5F\u6709\u533B\u751F\u8FD8\u662F\u5EFA\u8BAE\u505A\u9020\u7618\u624B\u672F\u4E3A\u4E8C\u6B21\u809B\u95E8\u624B\u672F\u521B\u9020\u66F4\u597D\u6761\u4EF6\u3002\u5728\u6709\u7618\u53E3\u7684\u60C5\u51B5\u4E0B\uFF0C\u5582\u5976\u5BFC\u81F4\u76F4\u80A0\u9634\u9053\u7618\u90A3\u51E0\u4E4E\u4E0D\u53EF\u80FD\uFF0C\u56E0\u4E3A\u76F8\u5BF9\u4E8E\u76C6\u5E95\u7EC4\u7EC7\uFF0C\u80A0\u58C1\u8981\u8584\u5F31\u5F97\u591A\uFF0C\u8981\u7A7F\u5B54\u4E5F\u662F\u7A7F\u80A0\u5B50\u800C\u4E0D\u662F\u5F62\u6210\u76F4\u80A0\u9634\u9053\u7618\u6216\u4F1A\u9634\u7618\u3002</p><p>\uFF08\u56FE\u7247\u6765\u81EA\u5C3C\u5C14\u68EE\u513F\u79D1\u5B66\uFF09</p><p>\u6839\u636E\u63D0\u4F9B\u7684\u4FE1\u606F\uFF0C\u8FD9\u4E2A\u5B69\u5B50\u9664\u4E86\u809B\u95E8\u95ED\u9501\uFF0C\u8FD8\u6709\u5FC3\u810F\u5375\u5706\u5B54\u95ED\u5408\u4E0D\u5168\uFF0C\u52A8\u8109\u5BFC\u7BA1\u672A\u95ED\uFF0C\u4E09\u5C16\u74E3\u8FD4\u6D41\uFF0C\u53CC\u4FA7\u80BE\u76C2\u79EF\u6C34\uFF0C\u810A\u67F1\u5E73\u76F4\uFF0C\u690E\u4F53\u7578\u5F62\u7B49\u95EE\u9898\uFF0C\u8FD9\u4E9B\u5408\u5E76\u7578\u5F62\u90FD\u662F\u5728\u809B\u95E8\u95ED\u9501\u7684\u5B69\u5B50\u4E2D\u6BD4\u8F83\u5E38\u89C1\uFF0C\u6240\u4EE5\u533B\u5B66\u4E0A\u53C8\u53EBVACTERL\u8054\u5408\u7578\u5F62\uFF0C\u5305\u62ECVertebral\uFF08\u810A\u67F1\uFF09, Anal\uFF08\u809B\u95E8\uFF09, Cardiac\uFF08\u5FC3\u810F\uFF09, Tracheal\uFF08\u6C14\u7BA1\uFF09, Esophageal\uFF08\u98DF\u7BA1\uFF09, Renal\uFF08\u80BE\u810F\uFF09, Limb\uFF08\u56DB\u80A2\uFF09 \u3002\u5E76\u4E0D\u662F\u6BCF\u4E2A\u809B\u95E8\u95ED\u9501\u7684\u5B69\u5B50\u90FD\u5408\u5E76\u6240\u6709\u7684\u8FD9\u4E9B\u7578\u5F62\uFF0C\u6709\u7684\u5408\u5E76\u5176\u4E2D\u4E00\u4E2A\u4E24\u4E2A\uFF0C\u6709\u7684\u5168\u90E8\u90FD\u6709\uFF0C\u6240\u4EE5\u6CBB\u7597\u8D77\u6765\u786E\u5B9E\u6F2B\u957F\u800C\u75DB\u82E6\uFF0C\u9700\u8981\u5F88\u5927\u7684\u7CBE\u529B\u548C\u8D22\u529B\u624D\u80FD\u652F\u6491\u3002\u6CBB\u6216\u8005\u4E0D\u6CBB\uFF0C\u8FD9\u4E2A\u95EE\u9898\u4EA4\u7ED9\u8C01\u90FD\u662F\u4E00\u4E2A\u5F88\u96BE\u7684\u9009\u62E9\u3002</p><p>\uFF08\u56FE\u7247\u6765\u81EAtheapms.com)</p><p>\u5BF9\u4E8E\u533B\u751F\u6765\u8BF4\uFF0C\u9762\u5BF9\u8FD9\u4E9B\u590D\u6742\u7684\u7578\u5F62\uFF0C\u5982\u679C\u5BB6\u957F\u7ECF\u6D4E\u6761\u4EF6\u5F88\u597D\uFF0C\u6CBB\u7597\u610F\u613F\u5F88\u79EF\u6781\uFF0C\u90A3\u533B\u751F\u4E5F\u5C31\u65E0\u6240\u8C13\uFF0C\u533B\u751F\u7684\u804C\u8D23\u672C\u6765\u5C31\u662F\u6CBB\u75C5\uFF0C\u4F60\u6709\u9700\u6C42\u533B\u751F\u81EA\u7136\u4E5F\u4F1A\u5C3D\u529B\uFF0C\u4F46\u533B\u7597\u5B58\u5728\u5F88\u5927\u7684\u4E0D\u786E\u5B9A\u6027\uFF0C\u809B\u95E8\u95ED\u9501\u7684\u5728\u56FD\u5185\u5916\u6CBB\u7597\u90FD\u6709\u53EF\u80FD\u51FA\u73B0\u809B\u95E8\u529F\u80FD\u4E0D\u826F\uFF0C\u4E25\u91CD\u7684\u751A\u81F3\u809B\u95E8\u5931\u7981\uFF0C\u5E76\u4E0D\u662F\u6BCF\u4E2A\u4EBA\u90FD\u80FD\u63A5\u53D7\u94B1\u82B1\u4E86\u5B69\u5B50\u9884\u540E\u4E0D\u597D\u7684\u7ED3\u5C40\uFF0C\u5728\u533B\u60A3\u5173\u7CFB\u672C\u6765\u5C31\u7D27\u5F20\u7684\u73AF\u5883\u4E0B\uFF0C\u533B\u751F\u628A\u8BDD\u8BF4\u5F97\u4FDD\u5B88\u4E00\u70B9\u662F\u5E38\u6001\u3002</p><p>\u5BF9\u4E8E\u7ECF\u6D4E\u6761\u4EF6\u4E0D\u592A\u597D\u7684\u5BB6\u5EAD\uFF0C\u5BB6\u957F\u7838\u9505\u5356\u94C1\u7684\u6CBB\uFF0C\u6CBB\u5230\u4E00\u534A\u5C71\u7A77\u6C34\u5C3D\u4E86\uFF0C\u7136\u540E\u628A\u5B69\u5B50\u4ECD\u7ED9\u533B\u9662\u4E0D\u7BA1\u4E86\uFF0C\u6211\u4EEC\u56FD\u5BB6\u53C8\u89C4\u5B9A\u8C01\u90FD\u4E0D\u80FD\u653E\u5F03\u6CBB\u7597\uFF0C\u90A3\u5C31\u53EA\u6709\u533B\u9662\u81EA\u5DF1\u638F\u94B1\u7EE7\u7EED\u6CBB\u4E86\uFF0C\u6700\u7EC8\u7684\u7ED3\u679C\u53EF\u80FD\u662F\u533B\u751F\u81EA\u5DF1\u638F\u94B1\u53BB\u6CBB\uFF0C\u8FD9\u79CD\u7ECF\u5386\u5F88\u591A\u533B\u751F\u90FD\u6709\uFF0C\u533B\u751F\u8BF4\u8BDD\u5C31\u53EF\u80FD\u66F4\u4FDD\u5B88\u4E86\u3002\u6240\u4EE5\u5C0F\u5E0C\u671B\u7B2C\u4E00\u6B21\u53BB\u533B\u9662\uFF0C\u9762\u5BF9\u8FD9\u4E48\u591A\u7578\u5F62\uFF0C\u5BB6\u957F\u7ECF\u6D4E\u6761\u4EF6\u53C8\u4E0D\u592A\u597D\uFF0C\u533B\u751F\u8BF4\u6CBB\u7597\u610F\u4E49\u4E0D\u5927\uFF0C\u4E5F\u4E0D\u7528\u5947\u602A\u3002\u81F3\u4E8E\u540E\u6765\u53BB\u548C\u7766\u5BB6\uFF0C\u533B\u751F\u8BF4\u80FD\u6CBB\u4E5F\u6CA1\u6709\u9519\uFF0C\u5728\u8D44\u91D1\u6709\u4FDD\u969C\uFF0C\u5728\u5A92\u4F53\u5E7F\u6CDB\u5173\u6CE8\u7684\u60C5\u51B5\u4E0B\uFF0C\u79C1\u7ACB\u533B\u9662\u6CBB\u7597\u8FD9\u6837\u7684\u75C5\u4EBA\u53EF\u4EE5\u83B7\u5F97\u5E7F\u544A\u6548\u5E94\uFF0C\u8BF4\u80FD\u6CBB\u7597\u4E5F\u4E0D\u5947\u602A\u3002</p><p>\u90A3\u8FD9\u4E2A\u5B69\u5B50\u5230\u5E95\u80FD\u4E0D\u80FD\u6CBB\uFF1F\u8089\u5510\u50E7\u8BF4\u81EA\u5DF1\u4EE5\u524D\u662F\u4E2A\u533B\u751F\uFF0C\u6240\u4EE5\u6839\u636E\u6839\u636E\u5B69\u5B50\u5FC3\u810F\u8D85\u58F0\u63D0\u793A\u6709\u52A8\u8109\u5BFC\u7BA1\u672A\u95ED\u3001\u5375\u5706\u5B54\u672A\u95ED\u3001\u4E09\u5C16\u74E3\u8FD4\u6D41\uFF0C\u8BA4\u4E3A\u6709\u81F4\u547D\u7684\u5FC3\u8840\u7BA1\u7CFB\u7EDF\u95EE\u9898\uFF0C\u7406\u7531\u662F\u5B69\u5B50\u521A\u51FA\u751F\u6CA1\u51E0\u5929\u5C31\u5F97\u4E0A\u4E86\u80BA\u708E\uFF0C\u5F3A\u70C8\u63D0\u793A\u4E86\u80BA\u52A8\u8109\u9AD8\u538B\uFF0C\u8BA4\u4E3A\u8FD9\u662F\u52A8\u8109\u5BFC\u7BA1\u672A\u95ED\u3001\u5375\u5706\u5B54\u95ED\u5408\u4E0D\u5168\u548C\u4E09\u5C16\u74E3\u8FD4\u6D41\u7684\u7ED3\u679C\u3002</p><p>\u6211\u4E0D\u662F\u5FC3\u810F\u4E13\u79D1\u533B\u751F\uFF0C\u4F46\u513F\u79D1\u533B\u751F\u5E94\u8BE5\u90FD\u77E5\u9053\uFF0C\u52A8\u8109\u5BFC\u7BA1\u672A\u95ED\u3001\u5375\u5706\u5B54\u672A\u95ED\u5728\u521D\u751F\u7684\u5B69\u5B50\u5F88\u5E38\u89C1\uFF0C\u800C\u4E14\u5F88\u591A\u5728\u751F\u540E\u51E0\u4E2A\u6708\u5C31\u4F1A\u81EA\u884C\u95ED\u5408\uFF0C\u6240\u4EE5\u4E0D\u80FD\u6839\u636E\u8FD9\u4E2A\u5C31\u5224\u65AD\u5FC3\u8840\u7BA1\u7CFB\u7EDF\u75BE\u75C5\u662F\u81F4\u547D\u7684\u3002\u5373\u4FBF\u662F\u6076\u6027\u80BF\u7624\uFF0C\u5206\u671F\u4E0D\u540C\uFF0C\u9884\u540E\u4E5F\u4F1A\u4E0D\u540C\u7684\uFF0C\u5355\u7EAF\u6839\u636E\u4E00\u4E2A\u8BCA\u65AD\u6765\u5224\u65AD\u75C5\u60C5\u662F\u4E0D\u4E13\u4E1A\u7684\u3002\u80BA\u52A8\u8109\u9AD8\u538B\u5F80\u5F80\u662F\u5FC3\u810F\u75C5\u540E\u671F\u624D\u51FA\u73B0\uFF0C\u51FA\u751F\u51E0\u5929\u7684\u5B69\u5B50\u5F88\u5C11\u89C1\uFF0C\u800C\u65B0\u751F\u513F\u80BA\u708E\u5012\u662F\u5F88\u5E38\u89C1\uFF0C\u513F\u79D1\u533B\u751F\u9996\u5148\u4F1A\u8003\u8651\u5438\u5165\u6027\u80BA\u708E\uFF0C\u800C\u4E0D\u4F1A\u7B2C\u4E00\u8003\u8651\u80BA\u52A8\u8109\u9AD8\u538B\u3002</p><p>\u8089\u5510\u50E7\u8FD8\u8BF4\u76F4\u80A0\u672B\u7AEF\u4E0E\u8180\u80F1\u76F8\u8FDE\uFF0C\u901A\u8FC7\u7EC6\u7EC6\u7684\u8F93\u5C3F\u7BA1\u5411\u5916\u6392\u4FBF\u663E\u7136\u662F\u6781\u5EA6\u4E0D\u7545\u7684\u3002\u76F4\u80A0\u672B\u7AEF\u548C\u8180\u80F1\u76F8\u8FDE\u662F\u5929\u6D25\u513F\u7AE5\u533B\u9662\u8003\u8651\u7684\u8BCA\u65AD\uFF0C\u6B63\u5982\u6211\u524D\u9762\u8BF4\u8FC7\uFF0C\u7618\u53E3\u5F88\u96BE\u770B\u6E05\u695A\uFF0C\u4F46\u53EF\u4EE5\u80AF\u5B9A\u7684\u662F\u8FD9\u4E2A\u8BCA\u65AD\u53EA\u662F\u533B\u751F\u731C\u6D4B\u7684\uFF0C\u56E0\u4E3A\u8180\u80F1\u5728\u91CC\u9762\uFF0C\u8981\u660E\u786E\u76F4\u80A0\u548C\u8180\u80F1\u76F8\u901A\uFF0C\u9664\u975E\u505A\u624B\u672F\u770B\u5230\u4E86\uFF0C\u5426\u5219\u4E0D\u53EF\u80FD\u660E\u786E\u3002\u6211\u4E0D\u662F\u5F88\u786E\u5B9A\u662F\u5426\u5B58\u5728\u5973\u5A74\u809B\u95E8\u95ED\u9501\u662F\u5426\u6709\u76F4\u80A0\u548C\u8180\u80F1\u76F8\u540C\u7684\u53EF\u80FD\u6027\uFF0C\u4F46\u5DE5\u4F5C\u8FD9\u4E48\u591A\u5E74\uFF0C\u6211\u4EEC\u533B\u9662\u6BCF\u5E74\u63A5\u8BCA\u4E0D\u4E0B\u51E0\u5341\u4F8B\u809B\u95E8\u95ED\u9501\uFF0C\u7537\u5A74\u5B58\u5728\u76F4\u80A0\u8180\u80F1\u7618\u9047\u5230\u8FC7\uFF0C\u5973\u5A74\u8FD8\u771F\u6CA1\u6709\u9047\u5230\u8FC7\uFF0C\u7406\u8BBA\u4E0A\u662F\u4E0D\u592A\u53EF\u80FD\u3002\u5373\u4FBF\u8FD9\u4E2A\u5B69\u5B50\u771F\u7684\u662F\u76F4\u80A0\u8180\u80F1\u7618\uFF0C\u6392\u4FBF\u4E5F\u662F\u901A\u8FC7\u5C3F\u9053\u5411\u5916\u6392\u800C\u4E0D\u662F\u901A\u8FC7\u8F93\u5C3F\u7BA1\u5411\u5916\u6392\uFF0C\u540E\u9762\u7684\u63A8\u8BBA\u5C31\u4E0D\u503C\u5F97\u8BF4\u4E86\u3002</p><p>\u809B\u95E8\u95ED\u9501\u7684\u5B69\u5B50\u505A\u9020\u7618\u624B\u672F\u540E\u56E0\u4E3A\u5927\u4FBF\u4ECE\u809A\u5B50\u6392\u51FA\uFF0C\u6240\u4EE5\u8981\u5E26\u7CAA\u888B\uFF0C\u4F46\u9020\u7618\u624B\u672F\u672C\u8EAB\u662F\u4E00\u4E2A\u59D1\u606F\u624B\u672F\uFF0C\u4E3A\u5927\u4FBF\u4E34\u65F6\u627E\u4E00\u4E2A\u51FA\u53E3\uFF0C\u540C\u65F6\u4E3A\u4E0B\u4E00\u6B21\u505A\u809B\u95E8\u624B\u672F\u521B\u9020\u6709\u5229\u6761\u4EF6\u3002\u505A\u597D\u809B\u95E8\u540E\u8FD8\u662F\u8981\u628A\u7F1D\u5728\u809A\u5B50\u4E0A\u7684\u80A0\u5B50\u9001\u56DE\u53BB\uFF0C\u7CAA\u888B\u53EA\u9700\u8981\u5E26\u51E0\u4E2A\u6708\u800C\u4E0D\u7528\u7EC8\u8EAB\u5E26\u3002\u81F3\u4E8E\u810A\u67F1\u5E73\u76F4\u7684\u95EE\u9898\uFF0C\u4F30\u8BA1\u53EF\u80FD\u662F\u5B69\u5B50\u6CA1\u6709\u6B63\u5E38\u7684\u810A\u67F1\u751F\u7406\u5F2F\u66F2\uFF0C\u4F46\u5E76\u4E0D\u4E00\u5B9A\u610F\u5473\u7740\u5404\u4E2A\u690E\u4F53\u662F\u56FA\u5B9A\u4E0D\u80FD\u6D3B\u52A8\u7684\uFF0C\u4E5F\u4E0D\u610F\u5473\u7740\u5979\u4E00\u8F88\u5B50\u76F4\u4E0D\u8D77\u6765\uFF0C\u7EC8\u751F\u5904\u4E8E\u4E00\u4E2A\u201C\u6361\u4E1C\u897F\u201D\u7684\u59FF\u6001\u3002</p><p>\u90A3\u8FD9\u4E2A\u5B69\u5B50\u5C31\u5982\u9648\u5C9A\u6240\u8BF4\uFF0C\u5B69\u5B50\u662F\u4EE5\u540E\u5B8C\u5168\u6709\u673A\u4F1A\u5EB7\u590D\u7684\u5417\uFF1F\u6211\u4F5C\u4E3A\u6709\u6CBB\u7597\u809B\u95E8\u95ED\u9501\u5B69\u5B50\u7ECF\u9A8C\u7684\u5C0F\u513F\u5916\u79D1\u533B\u751F\uFF0C\u6211\u4E0D\u77E5\u9053\uFF0C\u6211\u53EA\u80FD\u8BF4\u5C31\u76EE\u524D\u7684\u533B\u5B66\u53D1\u5C55\u6C34\u5E73\uFF0C\u809B\u95E8\u95ED\u9501\u662F\u53EF\u4EE5\u6CBB\u7597\u7684\uFF0C\u5927\u90E8\u5206\u9884\u540E\u8FD8\u4E0D\u9519\uFF0C\u4F46\u5B69\u5B50\u662F\u5426\u662F\u6CC4\u6B96\u8154\u7578\u5F62\uFF0C\u672F\u540E\u809B\u95E8\u529F\u80FD\u3001\u751F\u6D3B\u8D28\u91CF\u5982\u4F55\uFF0C\u6211\u65E0\u6CD5\u6839\u636E\u8FD9\u4E9B\u7B80\u5355\u7684\u4FE1\u606F\u53BB\u505A\u51FA\u5224\u65AD\uFF0C\u66F4\u4F55\u51B5\u5FC3\u810F\u3001\u810A\u67F1\u7684\u95EE\u9898\u66F4\u4E0D\u662F\u6211\u7684\u4E13\u4E1A\uFF0C\u6211\u66F4\u65E0\u6CD5\u8BC4\u4F30\u75C5\u60C5\u3002</p><p>\u90A3\u5929\u6D25\u513F\u7AE5\u533B\u9662\u548C\u548C\u7766\u5BB6\u533B\u9662\u7684\u8BC4\u4F30\u51C6\u786E\u5417\uFF1F\u80AF\u5B9A\u6BD4\u6211\u8FD9\u79CD\u6CA1\u770B\u8FC7\u75C5\u4EBA\u7684\u533B\u751F\u66F4\u51C6\u786E\uFF0C\u4F46\u6B63\u5982\u6211\u524D\u9762\u6240\u8BF4\uFF0C\u63A5\u8BCA\u533B\u751F\u7ED9\u7684\u8BC4\u4F30\u4E5F\u4F1A\u53D7\u5916\u754C\u56E0\u7D20\u7684\u5E72\u6270\uFF0C\u6240\u4EE5\u4ED6\u4EEC\u7ED9\u51FA\u4E0D\u4E00\u6837\u7684\u5224\u65AD\u3002\u5BF9\u8FD9\u79CD\u591A\u5668\u5B98\u7578\u5F62\uFF0C\u603B\u4F53\u9884\u540E\u80AF\u5B9A\u4E5F\u9700\u8981\u591A\u4E13\u4E1A\u7684\u533B\u751F\u5171\u540C\u4F1A\u8BCA\u540E\u624D\u597D\u8BC4\u4F30\u3002\u76F8\u5173\u4E13\u4E1A\u7684\u533B\u751F\u68C0\u67E5\u4E86\u5B69\u5B50\u90FD\u5F88\u96BE\u8BC4\u4F30\u51C6\u786E\u7684\u4E8B\uFF0C\u6211\u4EEC\u7684\u201C\u7231\u5FC3\u4EBA\u58EB\u201D\u5C31\u65AD\u5B9A\u5B69\u5B50\u5B8C\u5168\u6709\u673A\u4F1A\u5EB7\u590D\uFF0C\u5C31\u7B97\u6709\u673A\u4F1A\u5EB7\u590D\uFF0C\u4F60\u7231\u5FC3\u7206\u8868\uFF0C\u4F46\u4F60\u4E0D\u77E5\u9053\u62A2\u522B\u4EBA\u5BB6\u5B69\u5B50\u4E5F\u662F\u8FDD\u6CD5\u5417\uFF1F\u66F4\u4F55\u51B5\u4E4B\u524D\u8B66\u5BDF\u5DF2\u7ECF\u4ECB\u5165\u8FC7\u4E86\u3002\u5373\u4FBF\u5582\u5976\u672A\u5FC5\u4E0D\u53EF\u4EE5\uFF0C\u4F46\u80AF\u5B9A\u8981\u533B\u751F\u5148\u8BC4\u4F30\u5927\u4FBF\u901A\u7545\u7A0B\u5EA6\u540E\u624D\u80FD\u5B9A\uFF0C\u800C\u4ECE\u8FD9\u5B69\u5B50\u5F00\u59CB\u8179\u80C0\u3001\u548C\u7766\u5BB6\u63D2\u7BA1\u3001\u6700\u540E\u5929\u6D25\u513F\u7AE5\u533B\u9662\u505A\u9020\u7618\u6765\u8BF4\uFF0C\u8FD9\u4E2A\u5B69\u5B50\u6392\u4FBF\u80AF\u5B9A\u4E0D\u4F1A\u592A\u901A\u7545\uFF0C\u8FD9\u6837\u5582\u5976\u4E5F\u662F\u5728\u8C0B\u5BB3\u5B69\u5B50\u3002</p><p>\u75C5\u60C5\u8BC4\u4F30\u662F\u4E00\u4E2A\u95EE\u9898\uFF0C\u8981\u4E0D\u8981\u6CBB\u7597\u7684\u51B3\u5B9A\u5C31\u66F4\u590D\u6742\u4E86\uFF0C\u8FD9\u7275\u6D89\u5230\u4E00\u4E2A\u793E\u4F1A\u6574\u4F53\u7684\u4EF7\u503C\u89C2\u95EE\u9898\u3002\u56E0\u4E3A\u5B69\u5B50\u8FDC\u671F\u751F\u6D3B\u8D28\u91CF\u53D7\u75C5\u60C5\uFF0C\u533B\u5B66\u6C34\u5E73\uFF0C\u5BB6\u5EAD\u7ECF\u6D4E\u6761\u4EF6\uFF0C\u5BB6\u957F\u5BF9\u5B69\u5B50\u9884\u540E\u7684\u63A5\u53D7\u7A0B\u5EA6\uFF0C\u793E\u4F1A\u5BF9\u6B8B\u75BE\u4EBA\u7684\u63A5\u53D7\u7A0B\u5EA6\u7B49\u591A\u56E0\u7D20\u5F71\u54CD\u3002\u540C\u6837\u7684\u75C5\u60C5\uFF0C\u5728\u6211\u4EEC\u56FD\u5BB6\u6CBB\u7597\u548C\u5728\u7F8E\u56FD\u8FD9\u6837\u7684\u53D1\u8FBE\u56FD\u5BB6\u6CBB\u7597\uFF0C\u9884\u540E\u4E5F\u53EF\u80FD\u4E0D\u4E00\u6837\uFF0C\u6709\u94B1\u7684\u5BB6\u5EAD\uFF0C\u5B69\u5B50\u5F97\u5230\u7684\u6CBB\u7597\u6761\u4EF6\u4E5F\u4E0D\u4E00\u6837\uFF0C\u5728\u6CA1\u6709\u5B8C\u5584\u793E\u4F1A\u4FDD\u969C\u4F53\u7CFB\u7684\u793E\u4F1A\uFF0C\u4E00\u4E2A\u6B8B\u75BE\u513F\u7AE5\u53EF\u80FD\u727A\u7272\u6574\u4E2A\u5BB6\u5EAD\u7684\u5E78\u798F\uFF0C\u5E26\u7740\u7CAA\u888B\u5728\u5728\u4E00\u4E9B\u56FD\u5BB6\u88AB\u5F53\u602A\u7269\u770B\u5F85\uFF0C\u5728\u53E6\u5916\u4E00\u4E9B\u56FD\u5BB6\u53EF\u4EE5\u5927\u80C6\u5C55\u793A\u3002\u4E0D\u540C\u7684\u56FD\u60C5\u4E4B\u4E0B\uFF0C\u540C\u6837\u7684\u75C5\u60C5\uFF0C\u751F\u6D3B\u8D28\u91CF\u80AF\u5B9A\u4E5F\u4E0D\u4E00\u6837\u3002\u6240\u4EE5\u6211\u4EEC\u4E5F\u4E0D\u80FD\u5B8C\u5168\u53C2\u7167\u53D1\u8FBE\u56FD\u5BB6\u7684\u8981\u6C42\u4E0D\u8BA1\u4E00\u5207\u4EE3\u4EF7\u53BB\u8FFD\u6C42\u513F\u7AE5\u798F\u5229\uFF0C\u5373\u4FBF\u53D1\u8FBE\u56FD\u5BB6\uFF0C\u4E5F\u6709\u652F\u6301\u7ED9\u4E25\u91CD\u7578\u5F62\u5B69\u5B50\u5B89\u4E50\u6B7B\uFF0C\u6BD4\u5982\u8377\u5170\uFF0C\u4F46\u7A0B\u5E8F\u80AF\u5B9A\u5F88\u4E25\u683C\u3002</p><p>(\u56FE\u7247\u6765\u81EA@\u52FF\u602A\u5E78\uFF09</p><p>\u5BF9\u4E8E\u7231\u5FC3\u3001\u60C5\u6000\u8FD9\u6837\u7684\u4E1C\u897F\u6211\u5411\u6765\u5F88\u8B66\u60D5\uFF0C\u5C24\u5176\u662F\u81EA\u5DF1\u672C\u6765\u5C31\u4E0D\u662F\u4EC0\u4E48\u94B1\u591A\u5F97\u6CA1\u5730\u82B1\u7684\u751A\u81F3\u81EA\u5DF1\u94B1\u90FD\u4E0D\u591F\u82B1\u7684\u4EBA\u641E\u6148\u5584\uFF0C\u6211\u5411\u6765\u4E0D\u770B\u597D\uFF0C\u5C0F\u5E0C\u671B\u4EEC\u7684\u5E0C\u671B\u80AF\u5B9A\u4E0D\u5728\u201C\u5C0F\u5E0C\u671B\u4E4B\u5BB6\u201D\u4E4B\u7C7B\u7684\u6C11\u95F4\u7EC4\u7EC7\u3002\u5BF9\u4E8E\u51ED\u4E00\u77E5\u534A\u89E3\u4E0B\u7ED3\u8BBA\uFF0C\u6839\u636E\u5929\u9A6C\u884C\u7A7A\u7684\u60F3\u8C61\u5C31\u6252\u76AE\u7684\u4EBA\u641E\u653F\u6CBB\uFF0C\u6211\u4E5F\u4E0D\u770B\u597D\u3002\u4F46\u4ED6\u4EEC\u8FD9\u6B21\u505A\u4E86\u4E00\u4EF6\u597D\u4E8B\u5C31\u662F\u63A8\u52A8\u4E86\u516C\u4F17\u5BF9\u5F03\u5A74\u95EE\u9898\u7684\u5173\u6CE8\uFF0C\u4E5F\u95F4\u63A5\u63A8\u52A8\u4E86\u793E\u4F1A\u7684\u8FDB\u6B65\u3002</p><p>\u6211\u4EEC\u73B0\u5728\u7684\u6CD5\u5F8B\u662F\u89C4\u5B9A\u9057\u5F03\u5C31\u662F\u8FDD\u6CD5\uFF0C\u4F46\u6CA1\u94B1\u6CBB\u8C01\u51FA\u94B1\u5374\u6CA1\u6709\u5236\u5EA6\uFF0C\u7ED3\u5C40\u5C31\u662F\u9057\u5F03\u7F6A\u5C31\u662F\u4E00\u7EB8\u7A7A\u6587\uFF0C\u8FD9\u4E5F\u662F\u4E3A\u4EC0\u4E48\u62A5\u8B66\u4E4B\u540E\u5C0F\u5E0C\u671B\u8FD8\u662F\u88AB\u7EE7\u7EED\u88AB\u9057\u5F03\u3002\u6CD5\u5F8B\u662F\u793E\u4F1A\u4EF7\u503C\u89C2\u7684\u4F53\u73B0\uFF0C\u6CD5\u89C4\u662F\u4E00\u4E2A\u653F\u5E9C\u7BA1\u7406\u6C34\u5E73\u7684\u4F53\u73B0\uFF0C\u8FD9\u6837\u7C97\u66B4\u7684\u5236\u5EA6\u662F\u6211\u4EEC\u7C97\u653E\u793E\u4F1A\u7684\u5FC5\u7136\u7684\u4EA7\u7269\u3002</p><p>\u5728\u8FD9\u6837\u7684\u6CD5\u5F8B\u4F53\u7CFB\u4E0B\uFF0C\u521A\u5F00\u59CB\u505A\u533B\u751F\u6211\u4E5F\u63A5\u53D7\u4E0D\u4E86\u5BB6\u957F\u653E\u5F03\u81EA\u5DF1\u5B69\u5B50\uFF0C\u4F46\u540E\u6765\u4E5F\u89C1\u8FC7\u592A\u591A\u751F\u540E\u4E25\u91CD\u7578\u5F62\uFF0C\u6709\u7684\u751A\u81F3\u662F\u5148\u5929\u611A\u578B\u5408\u5E76\u591A\u53D1\u7578\u5F62\uFF0C\u5BB6\u957F\u5BB6\u957F\u4E0D\u8BA1\u6210\u672C\u7684\u6CBB\u7597\uFF0C\u6CBB\u5230\u6700\u540E\u5C71\u7A77\u6C34\u5C3D\uFF0C\u5BB6\u957F\u8D70\u6295\u65E0\u8DEF\uFF0C\u5B69\u5B50\u5927\u4E00\u70B9\u4E86\u6709\u611F\u60C5\u4E86\uFF0C\u4F46\u75C5\u60C5\u548C\u5BB6\u5EAD\u6761\u4EF6\u6CE8\u5B9A\u4ED6\u4EEC\u8FD8\u662F\u8981\u5206\u624B\uFF0C\u6211\u5F00\u59CB\u7406\u89E3\u90A3\u4E9B\u5F53\u521D\u5C31\u9009\u62E9\u653E\u5F03\u7684\u5BB6\u957F\u3002\u5728\u65E0\u5948\u7684\u73B0\u5B9E\u4E4B\u4E0B\uFF0C\u653E\u624B\u867D\u7136\u75DB\uFF0C\u4F46\u672A\u5C1D\u4E0D\u662F\u4E00\u79CD\u7231\u3002</p><p>\u5982\u679C\u6211\u4EEC\u7684\u6CD5\u5F8B\u89C4\u5B9A\uFF0C\u5BF9\u4E8E\u4E25\u91CD\u7578\u5F62\u9884\u540E\u4E0D\u597D\u7684\u5B69\u5B50\uFF0C\u5BB6\u957F\u6709\u653E\u5F03\u6CBB\u7597\u610F\u613F\u7684\uFF0C\u53EF\u4EE5\u7533\u8BF7\u7531\u76F8\u5173\u6743\u5A01\u533B\u751F\u548C\u6CD5\u5F8B\u4F26\u7406\u4E13\u4E1A\u4EBA\u58EB\u7EC4\u6210\u7684\u72EC\u7ACB\u673A\u6784\u8FDB\u884C\u8BC4\u4F30\uFF0C\u5728\u5BA1\u6838\u5B69\u5B50\u7684\u5BB6\u5EAD\u7ECF\u6D4E\u72B6\u51B5\u57FA\u7840\u4E0A\uFF0C\u51B3\u5B9A\u662F\u5426\u6279\u51C6\u5B89\u4E50\u6B7B\u6216\u653E\u5F03\u6CBB\u7597\uFF0C\u90A3\u81EA\u7136\u6700\u597D\u3002\u5982\u679C\u8BA4\u4E3A\u4E0D\u80FD\u653E\u5F03\u6CBB\u7597\uFF0C\u5BB6\u957F\u4ECD\u7136\u4E00\u610F\u5B64\u884C\u90A3\u5C31\u5E94\u8BE5\u628A\u9057\u5F03\u7F6A\u843D\u5230\u5B9E\u5904\u800C\u4E0D\u662F\u50CF\u73B0\u5728\u4E00\u6837\u51E0\u4E4E\u662F\u4E00\u7EB8\u7A7A\u6587\uFF0C\u540C\u65F6\u4E5F\u8981\u5EFA\u7ACB\u5B8C\u5584\u6551\u52A9\u673A\u5236\uFF0C\u8FD9\u4E9B\u90FD\u5728\u6211\u4EE5\u524D\u5199\u7684\u90A3\u7BC7\u300A\u88AB\u9057\u5F03\u7684\u5929\u4F7F\u300B\uFF08\u56DE\u590D102\u53EF\u67E5\u770B\uFF09\u4E00\u6587\u79CD\u8BE6\u7EC6\u9610\u8FF0\u8FC7\u3002</p><p>\u5236\u5EA6\u90FD\u662F\u4EBA\u5236\u5B9A\u7684\uFF0C\u6709\u4EC0\u4E48\u6837\u7684\u4EBA\u6C11\u4E5F\u4F1A\u6709\u4EC0\u4E48\u6837\u7684\u5236\u5EA6\uFF0C\u6587\u660E\u7406\u667A\u7684\u793E\u4F1A\u624D\u53EF\u80FD\u4EA7\u751F\u5408\u7406\u7684\uFF0C\u53EF\u6267\u884C\u7684\u5236\u5EA6\uFF0C\u6539\u53D8\u4EBA\u6C11\u624D\u80FD\u6539\u53D8\u5236\u5EA6\uFF0C\u6BCF\u4E2A\u8BA4\u6E05\u4E86\u81EA\u5DF1\u7684\u6743\u5229\u548C\u4E49\u52A1\u7684\u8FB9\u754C\uFF0C\u5C0F\u5E0C\u671B\u4EEC\u624D\u4F1A\u6709\u5E0C\u671B\u3002\uFF08\u539F\u521B\u6587\u7AE0\u672A\u7ECF\u8BB8\u53EF\u8C22\u7EDD\u8F6C\u8F7D\uFF0C\u8BF4\u597D\u7684\u5E74\u5E95\u6700\u540E\u4E00\u7BC7\u98DF\u8A00\u4E86\uFF0C\u53EA\u597D\u518D\u795D\u798F\u4E00\u6B21\u65B0\u5E74\u5FEB\u4E50\uFF1A\uFF09\uFF09</p><p><strong>\u6743\u5A01\u7684\u80B2\u513F\u79D1\u666E\uFF0C\u6DF1\u5EA6\u7684\u533B\u5B66\u4EBA\u6587</strong></p><p><strong>\u4EC5\u63A8\u9001\u539F\u521B\u6587\u7AE0\uFF0C\u6B22\u8FCE\u5173\u6CE8drpeihg</strong></p>',26),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=202238061&idx=1&sn=07bece69d4b849438ab39b793f6b5ab4&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function m(b,f){const p=a("ExternalLinkIcon");return r(),t(o,null,[_,e("p",null,[e("a",l,[h,n(p)])])],64)}var u=d(i,[["render",m]]);export{u as default};
