import{r,o as t,c as n,a as e,b as o,F as a,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=s('<h3 id="\u4FDD\u62A4\u5B69\u5B50-\u8FDC\u79BB\u5B66\u6B65\u8F66" tabindex="-1"><a class="header-anchor" href="#\u4FDD\u62A4\u5B69\u5B50-\u8FDC\u79BB\u5B66\u6B65\u8F66" aria-hidden="true">#</a> \u4FDD\u62A4\u5B69\u5B50\uFF0C\u8FDC\u79BB\u5B66\u6B65\u8F66</h3><p>\u5B66\u6B65\u8F66\u6709\u4E2A\u5F88\u597D\u7684\u540D\u5B57\uFF0C\u5F88\u591A\u7236\u6BCD\u4E00\u542C\uFF0C\u4EE5\u4E3A\u53EF\u4EE5\u5E2E\u52A9\u5B69\u5B50\u5B66\u8D70\u6B65\uFF0C\u5C31\u4E70\u4E86\u4E00\u4E2A\uFF0C\u751A\u81F3\u53BB\u770B\u671B\u670B\u53CB\u7684\u5B69\u5B50\u65F6\u4E5F\u62FF\u8FD9\u4E2A\u5F53\u793C\u7269\u3002\u8FD8\u4E0D\u80FD\u72EC\u81EA\u7AD9\u7ACB\u7684\u5B69\u5B50\uFF0C\u5F80\u8F66\u5B50\u91CC\u4E00\u653E\uFF0C\u5B69\u5B50\u811A\u5C16\u8F7B\u8F7B\u4E00\u70B9\u5730\uFF0C\u5C31\u53EF\u4EE5\u6EE1\u5730\u8DD1\u4E86\u3002\u5B69\u5B50\u73A9\u5F97\u5F00\u5FC3\uFF0C\u5BB6\u957F\u4E5F\u89C9\u5F97\u8F7B\u677E\uFF0C\u8FD9\u573A\u666F\u4F3C\u4E4E\u770B\u4E0A\u53BB\u5F88\u7F8E\u3002</p><p>\u7136\u800C\uFF0C\u5F88\u591A\u7236\u6BCD\u5E76\u6CA1\u6709\u610F\u8BC6\u5230\uFF0C\u5C06\u5B69\u5B50\u653E\u8FDB\u5B66\u6B65\u8F66\u5176\u5B9E\u4E5F\u610F\u5473\u7740\u8BA9\u4ED6\u4EEC\u66B4\u9732\u4E8E\u5371\u9669\u4E4B\u4E2D\uFF0C\u6709\u65F6\u8FD9\u4E9B\u5371\u9669\u8FD8\u662F\u81F4\u547D\u7684\u3002\u524D\u51E0\u5929\u592E\u89C6\u62A5\u9053\u4E86\u4E00\u4E2A\u5B69\u5B50\u5750\u5728\u5B66\u6B65\u8F66\u91CC\u4ECE\u53F0\u9636\u4E0A\u6454\u4E0B\uFF0C\u5BFC\u81F4\u9885\u8111\u635F\u4F24\u7684\u6848\u4F8B\uFF0C\u5F15\u8D77\u5F88\u591A\u4EBA\u7684\u5173\u6CE8\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u8FD9\u6837\u7684\u4E8B\u4EF6\u7EDD\u975E\u5076\u7136\u3002\u5B66\u6B65\u8F66\u5BF9\u513F\u7AE5\u201C\u5B66\u6B65\u201D\u5E76\u65E0\u5E2E\u52A9\uFF0C\u76F8\u53CD\u5B83\u8FD8\u4F1A\u5E26\u6765\u610F\u5916\u4F24\u5BB3\u7684\u98CE\u9669\uFF0C\u5B9E\u5728\u4E0D\u662F\u4E00\u4E2A\u660E\u667A\u7684\u9009\u62E9\u3002</p><p><strong>\u5B66\u6B65\u8F66\u5E26\u6765\u7684\u610F\u5916\u4F24\u5BB3</strong></p><p>\u513F\u7AE5\u5B66\u6B65\u8F66\u7684\u5B89\u5168\u95EE\u9898\u7531\u6765\u5DF2\u4E45\uFF0C\u5728\u56FD\u5916\u5F88\u65E9\u5C31\u5F15\u8D77\u4E86\u533B\u5B66\u754C\u7684\u91CD\u89C6\u30021973-1998\u5E74\u4E4B\u95F4\uFF0C\u7F8E\u56FD\u6D88\u8D39\u8005\u4EA7\u54C1\u5B89\u5168\u59D4\u5458\u4F1A\u5171\u63A5\u5230\u4E8634\u4F8B\u548C\u5B66\u6B65\u8F66\u4F7F\u7528\u6709\u5173\u7684\u6B7B\u4EA1\u62A5\u544A\u3002\u6709\u4EBA\u7EDF\u8BA11049\u4F8B2\u5C81\u5185\u7684\u513F\u7AE5\u610F\u5916\u4F24\u5BB3\uFF0C\u53D1\u73B0\u548C\u5B66\u6B65\u8F66\u76F8\u5173\u7684\u4F24\u5BB3\u670922\u4F8B\uFF0C\u8FD9\u4E2A\u6570\u5B57\u548C\u8F66\u7978\u5916\u4F24\u76F8\u5F53\u30021990\uFF0D1994\u5E74, \u7F8E\u56FD\u5E73\u5747\u6BCF\u5E74\u53D1\u751F23000 \u4F8B\u5B66\u6B65\u8F66\u76F8\u5173\u7684\u4F24\u5BB3\u3002\u4ECE1990-2001\u5E74\uFF0C\u7F8E\u56FD\u603B\u5171\u6709\u7EA6197200\u4E2A15\u6708\u4EE5\u4E0B\u5B69\u5B50\u56E0\u4E3A\u5B66\u6B65\u8F66\u76F8\u5173\u4F24\u5BB3\u5230\u6025\u8BCA\u5BA4\u5C31\u8BCA\u3002</p><p>\u4E3A\u4EC0\u4E48\u5B66\u6B65\u8F66\u4F1A\u9020\u6210\u8FD9\u4E48\u591A\u4F24\u5BB3\uFF1F\u5B66\u6B65\u8F66\u7ED9\u4E86\u5B69\u5B50\u548C\u4ED6\u5E74\u9F84\u3001\u5371\u9669\u610F\u8BC6\u4E0D\u76F8\u79F0\u7684\u901F\u5EA6\u548C\u7A7A\u95F4\uFF0C\u662F\u9020\u6210\u5404\u79CD\u4F24\u5BB3\u7684\u539F\u56E0\u3002\u5B66\u6B65\u8F66\u7684\u8F66\u8F6E\u4F7F\u8FD8\u4E0D\u4F1A\u8D70\u8DEF\u7684\u5B69\u5B50\u80FD\u8F7B\u6613\u5730\u4EE5\u8F83\u9AD8\u901F\u5EA6\u79FB\u52A8\uFF08\u901F\u5EA6\u53EF\u4EE5\u8D85\u8FC71\u7C73/\u79D2\uFF09\uFF0C\u7136\u800C\u8FD9\u4E48\u5927\u7684\u5B69\u5B50\u5BF9\u5371\u9669\u6CA1\u6709\u591A\u5C11\u610F\u8BC6\uFF0C\u4E5F\u4E0D\u4F1A\u81EA\u4E3B\u63A7\u5236\u8F66\u5B50\uFF0C\u968F\u65F6\u53EF\u80FD\u671D\u5371\u9669\u7684\u5730\u65B9\u51B2\u8FC7\u53BB\u3002\u7EDD\u5927\u591A\u6570\u5B66\u6B65\u8F66\u90FD\u6CA1\u6709\u5236\u52A8\u88C5\u7F6E\uFF0C\u5373\u4FBF\u6709\uFF0C\u5B69\u5B50\u4E5F\u4E0D\u4F1A\u4F7F\u7528\u3002\u6709\u4E9B\u5BB6\u957F\u89C9\u5F97\u53EA\u8981\u81EA\u5DF1\u770B\u597D\u4E86\u5C31\u4E0D\u4F1A\u6709\u4E8B\uFF0C\u800C\u4E8B\u5B9E\u662F\u901F\u5EA6\u8FD9\u4E48\u5FEB\u5373\u4FBF\u5BB6\u957F\u4E00\u76F4\u5728\u76D1\u89C6\u4E5F\u53EF\u80FD\u53CD\u5E94\u4E0D\u8FC7\u6765\uFF0C\u7F8E\u56FD\u7684\u7EDF\u8BA1\u53D1\u73B069%\u7684\u76F8\u5173\u5B66\u6B65\u8F66\u610F\u5916\u90FD\u662F\u5728\u5927\u4EBA\u770B\u62A4\u4E0B\u53D1\u751F\u3002\u66F4\u4F55\u51B5\u5F88\u591A\u5BB6\u957F\u5C31\u662F\u62B1\u7740\u7701\u529B\u7701\u5FC3\u7684\u60F3\u6CD5\u624D\u7ED9\u5B69\u5B50\u7528\u7684\uFF0C\u628A\u5B69\u5B50\u5F80\u8F66\u91CC\u4E00\u653E\u5C31\u8D70\u5F00\u4E86\uFF0C\u98CE\u9669\u5C31\u66F4\u5927\u4E86\u3002</p><p>\u56E0\u4E3A\u901F\u5EA6\u8FC7\u5FEB\u76F4\u63A5\u51B2\u4E0B\u697C\u68AF\u6216\u53F0\u9636\uFF0C\u7136\u540E\u7FFB\u5012\u9020\u6210\u5B69\u5B50\u7684\u5916\u4F24\uFF0C\u8FD9\u662F\u5B66\u6B65\u8F66\u76F8\u5173\u4F24\u5BB3\u6700\u5E38\u89C1\u7684\u7C7B\u578B\u3002\u6839\u636E\u7F8E\u56FD\u7684\u6570\u636E\uFF0C\u4ECE\u697C\u68AF\u4E0A\u6454\u4E0B\u9020\u6210\u7684\u5916\u4F24\u5360\u4E86\u5B66\u6B65\u8F66\u76F8\u5173\u4F24\u5BB375%\u4EE5\u4E0A\uFF0C\u5F88\u591A\u90FD\u662F\u5934\u9888\u90E8\u7684\u5916\u4F24\uFF0C\u5305\u62EC\u9885\u9AA8\u9AA8\u6298\uFF0C\u9885\u5185\u51FA\u8840\u7B49\u7B49\uFF0C\u800C\u4E14\u51E0\u4E4E\u6240\u4EE5\u7684\u4E25\u91CD\u5916\u4F24\u90FD\u662F\u8FD9\u79CD\u65B9\u5F0F\u9020\u6210\u7684\u3002\u592E\u89C6\u62A5\u9053\u4E2D\u7684\u90A3\u4E2A\u5B69\u5B50\u4E5F\u5C5E\u4E8E\u8FD9\u4E00\u7C7B\u3002</p><p>\u5B66\u6B65\u8F66\u76F8\u5173\u7684\u4F24\u5BB3\u4E8B\u4EF6\u4E2D\uFF0C\u70EB\u4F24\u4E5F\u6BD4\u8F83\u5E38\u89C1\u3002\u501F\u52A9\u5B66\u6B65\u8F66\uFF0C\u5B69\u5B50\u53EF\u4EE5\u53BB\u4E00\u4E9B\u4EE5\u524D\u4ED6\u4E0D\u80FD\u53BB\u7684\u5730\u65B9\uFF0C\u6BD4\u5982\u53A8\u623F\uFF0C\u536B\u751F\u95F4\u3002\u5728\u8FD9\u4E9B\u5730\u65B9\uFF0C\u78B0\u7FFB\u70ED\u6C34\u74F6\uFF0C\u6389\u8FDB\u6D74\u7F38\u3001\u6C34\u6876\u91CC\u7684\u4E8B\u60C5\u90FD\u6709\u53EF\u80FD\u53D1\u751F\u3002\u5B66\u6B65\u8F66\u4E5F\u7ED9\u539F\u672C\u53EA\u80FD\u722C\u884C\u7684\u5B9D\u5B9D\u521B\u9020\u4E86\u65B0\u7684\u9AD8\u5EA6\uFF0C\u4ED6\u5C31\u53EF\u80FD\u63A5\u89E6\u5230\u4E00\u4E9B\u4EE5\u524D\u4E0D\u4F1A\u78B0\u5230\u7684\u5371\u9669\u56E0\u7D20\uFF0C\u6BD4\u5982\u626F\u4E0B\u684C\u5E03\u6253\u7FFB\u684C\u4E0A\u7684\u70ED\u8336\u30022012\u5E7412\u6708\u5C71\u4E1C\u804A\u57CE\u4E00\u5B69\u5B50\u5E7C\u513F\u5750\u5B66\u6B65\u8F66\u649E\u7FFB\u5F00\u6C34\u9505\u88AB\u70EB\u4F24\uFF0C2013\u5E745\u6708\u8425\u53E38\u4E2A\u6708\u5B69\u5B50\u5B66\u6B65\u8F66\u4E2D\u6253\u7FFB\u9505\uFF0C\u534A\u9505\u70ED\u6C34\u5012\u5728\u5B69\u5B50\u8EAB\u4E0A\uFF0C\u8FD9\u7C7B\u65B0\u95FB\u5E76\u4E0D\u5C11\u89C1\uFF0C\u800C\u56FD\u5916\u4E5F\u65E9\u6709\u7C7B\u4F3C\u7684\u6587\u732E\u62A5\u9053\u3002</p><p>\u56E0\u4E3A\u901F\u5EA6\u8FC7\u5FEB\uFF0C\u5373\u4FBF\u6CA1\u6709\u7FFB\u5012\uFF0C\u5B66\u6B65\u8F66\u4E5F\u5BB9\u6613\u53D1\u751F\u5404\u79CD\u64E6\u78B0\uFF0C\u540C\u6837\u4F1A\u7ED9\u5B69\u5B50\u9020\u6210\u5404\u79CD\u4F24\u5BB3\uFF0C\u6BD4\u5982\u78D5\u7834\u5934\uFF0C\u78D5\u4F24\u7259\u9F7F\uFF0C\u5212\u7834\u624B\u6307\u7B49\uFF0C\u8FD9\u7C7B\u75C5\u4EBA\u5728\u6211\u4EEC\u7684\u5916\u79D1\u6025\u8BCA\u4E5F\u4E0D\u5C11\u89C1\u3002</p><p><strong>\u5B66\u6B65\u8F66\u771F\u80FD\u201C\u5B66\u6B65\u201D\u5417\uFF1F</strong></p><p>\u5F88\u591A\u5BB6\u957F\u8BA4\u4E3A\u5B69\u5B50\u5B66\u6B65\u8F66\u53EF\u4EE5\u5E2E\u52A9\u5B69\u5B50\u65E9\u70B9\u5B66\u4F1A\u8D70\u8DEF\uFF0C\u4FC3\u8FDB\u53D1\u80B2\uFF0C\u4F46\u4E8B\u5B9E\u4E0A\u8FD9\u4E00\u70B9\u4E5F\u5E76\u4E0D\u5982\u4EBA\u613F\u3002\u5B69\u5B50\u5728\u5B66\u6B65\u8F66\u91CC\u8EAB\u4F53\u9760\u5750\u57AB\u652F\u6491\uFF0C\u884C\u8D70\u65F6\u5019\u4E3B\u8981\u4F9D\u9760\u8DB3\u5C16\u6ED1\u52A8\uFF0C\u201C\u7AD9\u7ACB\u201D\u548C\u201C\u884C\u8D70\u201D\u7684\u6A21\u5F0F\u90FD\u4E0D\u540C\u4E8E\u771F\u6B63\u7684\u8D70\u8DEF\uFF0C\u8FD9\u6837\u7684\u8FD0\u52A8\u4E5F\u5F88\u96BE\u8BF4\u80FD\u5BF9\u5B66\u8D70\u8DEF\u4EA7\u751F\u4EC0\u4E48\u6B63\u9762\u4F5C\u7528\u3002\u751A\u81F3\u8FD8\u6709\u4E00\u4E9B\u7814\u7A76\u663E\u793A\uFF0C\u5B66\u6B65\u8F66\u53EF\u80FD\u5BF9\u5B69\u5B50\u7684\u8FD0\u52A8\u53D1\u80B2\u9020\u6210\u8D1F\u9762\u5F71\u54CD\u3002</p><p>\u65E9\u57281977\u5E74\uFF0C\u6709\u4EBA\u5C066\u5BF9\u53CC\u80DE\u80CE\u5B69\u5B50\u968F\u673A\u5206\u7EC4\uFF0C\u53D1\u73B0\u6BCF\u5929\u4F7F\u7528\u5B66\u6B65\u8F662\u5C0F\u65F6\u7684\u5B69\u5B50\u51FA\u73B0\u5F88\u591A\u9519\u8BEF\u7684\u808C\u7535\u8868\u73B0\uFF0C\u660E\u663E\u4E0D\u540C\u4E8E\u4E0D\u4F7F\u7528\u5B66\u6B65\u8F66\u7684\u5B69\u5B50\u3002\u968F\u540E\u57281982\u5E74\uFF0C\u6709\u4EBA\u5BF915\u5BF9\u53CC\u80DE\u80CE\u968F\u673A\u5206\u7EC4\uFF0C\u4ECE4\u4E2A\u6708\u5927\u65F6\u5F00\u59CB\uFF0C\u4E00\u7EC4\u6BCF\u5929\u81F3\u5C11\u75281\u5C0F\u65F6\u5B66\u6B65\u8F66\uFF0C\u53E6\u4E00\u7EC4\u4E0D\u4F7F\u7528\uFF0C\u53D1\u73B0\u8FD9\u4E24\u7EC4\u5B69\u5B50\u5F00\u59CB\u8D70\u8DEF\u7684\u65F6\u95F4\u6CA1\u6709\u660E\u663E\u5DEE\u522B\u30022002\u5E74\u53D1\u8868\u4E8E\u82F1\u56FD\u533B\u5B66\u671F\u520A\uFF08BMJ)\u7684\u4E00\u7BC7\u6587\u7AE0\u8BA4\u4E3A\uFF0C\u4F7F\u7528\u5B66\u6B65\u8F66\u7684\u5B69\u5B50\u722C\u884C\u3001\u72EC\u7ACB\u7AD9\u7ACB\u3001\u72EC\u7ACB\u884C\u8D70\u7684\u65F6\u95F4\u5747\u665A\u4E8E\u4E0D\u7528\u5B66\u6B65\u8F66\u7684\u5B69\u5B50\uFF0C\u6BCF\u7D2F\u8BA1\u4F7F\u752824\u5C0F\u65F6\u5B66\u6B65\u8F66\uFF0C\u5F00\u59CB\u72EC\u7ACB\u884C\u8D70\u7684\u65F6\u95F4\u8981\u6BD4\u4E0D\u7528\u5B66\u6B65\u8F66\u7684\u5B69\u5B50\u5E73\u5747\u665A3.3\u5929\uFF0C\u72EC\u7ACB\u7AD9\u7ACB\u8981\u665A3.7\u5929\u3002\u4F46\u8FD9\u9879\u7814\u7A76\u4E0D\u662F\u968F\u673A\u5BF9\u7167\u5B9E\u9A8C\uFF0C\u6240\u4EE5\u4E5F\u6709\u4EBA\u8D28\u7591\u7ED3\u8BBA\uFF0C\u8BA4\u4E3A\u9700\u8981\u8FDB\u4E00\u6B65\u7814\u7A76\u3002</p><p>\u5B66\u6B65\u8F66\u5BF9\u5B69\u5B50\u53D1\u80B2\u4E0D\u5229\u7684\u8BC1\u636E\u8FD8\u4E0D\u5145\u5206\uFF0C\u4F46\u8003\u8651\u5230\u76EE\u524D\u6CA1\u6709\u53D1\u73B0\u5B83\u5BF9\u5B69\u5B50\u6709\u5229\u7684\u8BC1\u636E\uFF0C\u800C\u5BFC\u81F4\u610F\u5916\u4F24\u5BB3\u7684\u8BC1\u636E\u5374\u76F8\u5F53\u5145\u5206\uFF0C\u5BF9\u5B69\u5B50\u800C\u8A00\u5B83\u8FD8\u662F\u6709\u5F0A\u65E0\u5229\u7684\u3002</p><p><strong>\u51CF\u5C11\u5371\u9669\uFF0C\u8FDC\u79BB\u5B66\u6B65\u8F66</strong></p><p>\u4E3A\u4E86\u964D\u4F4E\u76F8\u5173\u610F\u5916\u4F24\u5BB3\uFF0C\u7F8E\u56FD\u4E8E1997\u5E74\u5BF9\u5B66\u6B65\u8F66\u51FA\u53F0\u4E86\u65B0\u7684\u6807\u51C6\uFF0C\u8981\u6C42\u5B66\u6B65\u8F66\u505A\u5F97\u66F4\u5BBD\u5927\uFF08\u8FD9\u6837\u5C31\u4E0D\u80FD\u7A7F\u95E8\u800C\u51FA\uFF09\uFF0C\u6216\u8005\u80FD\u5728\u53F0\u9636\u8FB9\u7F18\u81EA\u5DF1\u5236\u52A8\uFF0C\u4EE5\u51CF\u5C11\u610F\u5916\u53D1\u751F\u3002\u65B0\u6807\u51C6\u5B9E\u65BD\u4E4B\u540E\uFF0C\u5B66\u6B65\u8F66\u76F8\u5173\u4F24\u5BB3\u5927\u5E45\u4E0B\u964D\uFF0C\u4F46\u76F4\u52302001\u5E74\uFF0C\u4ECD\u67095001\u4F8B\u76F8\u5173\u4F24\u5BB3\u53D1\u751F\u3002</p><p>\u4E3A\u4E86\u5F7B\u5E95\u907F\u514D\u4F24\u5BB3\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u65D7\u4E0B\u7F51\u7AD9healthychildren.org\u5EFA\u8BAE\u201C\u628A\u5B66\u6B65\u8F66\u6254\u51FA\u53BB\u201D\u3002\u52A0\u62FF\u5927\u653F\u5E9C\u8D70\u5F97\u66F4\u8FDC\uFF0C\u4ED6\u4EEC\u4E8E2004\u5E74\u5F00\u59CB\u7981\u6B62\u4F7F\u7528\u548C\u9500\u552E\u5B66\u6B65\u8F66\uFF0C\u5176\u5B83\u56FD\u5BB6\u7684\u533B\u5B66\u754C\u4EBA\u58EB\u4E5F\u547C\u5401\u653F\u5E9C\u5411\u52A0\u62FF\u5927\u5B66\u4E60\u3002\u8457\u540D\u533B\u5B66\u671F\u520A\u300A\u67F3\u53F6\u5200\u300B\u66FE\u520A\u6587\u547C\u5401\u52A0\u5927\u5BF9\u5B66\u6B65\u8F66\u5371\u5BB3\u7684\u5BA3\u4F20\u30022011\u5E74\u6211\u56FD\u539F\u536B\u751F\u90E8\u53D1\u5E03\u7684\u300A\u513F\u7AE5\u8DCC\u5012\u5E72\u9884\u6280\u672F\u6307\u5357\u300B\u4E2D\u4E5F\u660E\u786E\u6307\u51FA\uFF0C\u4E0D\u5EFA\u8BAE\u5A74\u513F\u4F7F\u7528\u5B66\u6B65\u8F66\u3002</p><p>\u5BF9\u5B69\u5B50\u6709\u5BB3\u65E0\u5229\u7684\u5B66\u6B65\u8F66\uFF0C\u4E3A\u4EC0\u4E48\u8FD8\u6709\u8FD9\u4E48\u591A\u5BB6\u5EAD\u4F7F\u7528\uFF1F\u4E00\u65B9\u9762\u662F\u5BA3\u4F20\u4E0D\u8DB3\uFF0C\u5F88\u591A\u7236\u6BCD\u6CA1\u6709\u610F\u8BC6\u5230\u5B66\u6B65\u8F66\u7684\u5371\u5BB3\uFF0C\u53E6\u4E00\u65B9\u9762\u5B66\u6B65\u8F66\u786E\u5B9E\u4E5F\u7ED9\u7236\u6BCD\u5E26\u6765\u4E86\u65B9\u4FBF\u3002\u5373\u4FBF\u662F\u5728\u653F\u5E9C\u7981\u7528\u540E\uFF0C\u52A0\u62FF\u5927\u6709\u5B69\u5B50\u7684\u5BB6\u5EAD\u4E2D\u6709\u5B66\u6B65\u8F66\u7684\u8FD8\u662F\u5360\u5230\u4E8621%\u3002\u7531\u6B64\u53EF\u89C1\uFF0C\u5A74\u513F\u7684\u8F85\u52A9\u884C\u8D70\u5DE5\u5177\u8FD8\u662F\u6709\u5F88\u5927\u7684\u73B0\u5B9E\u9700\u6C42\u3002\u90A3\u4E48\uFF0C\u6709\u6CA1\u6709\u65E2\u80FD\u907F\u514D\u5371\u5BB3\u53C8\u80FD\u6EE1\u8DB3\u8FD9\u79CD\u9700\u6C42\u7684\u4EA7\u54C1\u5462\uFF1F\u5B66\u6B65\u8F66\u6700\u5927\u7684\u98CE\u9669\u6765\u81EA\u8F66\u8F6E\uFF0C\u6240\u4EE5healthychildren.org\u5EFA\u8BAE\uFF0C\u53EF\u4EE5\u9009\u7528\u56FA\u5B9A\u5F0F\u7684\u6D3B\u52A8\u4E2D\u5FC3\uFF08\u5982\u4E0B\u56FE\uFF09\u3002</p><p>\u6E38\u620F\u573A\u548C\u6E38\u620F\u56F4\u680F\uFF08\u5982\u4E0B\u56FE\uFF09\uFF0C\u4F9B\u5B69\u5B50\u5728\u5176\u4E2D\u7EC3\u4E60\u5750\u3001\u722C\u3001\u7AD9\u3001\u8D70\u3002</p><p>\u5B9E\u5728\u62B1\u7D2F\u4E86\uFF0C\u5927\u4E00\u70B9\u7684\u5B69\u5B50\u4E5F\u53EF\u4EE5\u8BA9\u4ED6\u5750\u5728\u9AD8\u6905\u4E0A\u73A9\u3002</p><p>\u770B\u5230\u8FD9\u91CC\u5927\u5BB6\u53EF\u80FD\u4E5F\u6CE8\u610F\u5230\uFF0C\u6587\u7AE0\u91CC\u6240\u5F15\u7528\u7684\u6570\u636E\u90FD\u662F\u6765\u81EA\u56FD\u5916\uFF0C\u4E0D\u662F\u6211\u6709\u610F\u4E3A\u4E4B\uFF0C\u800C\u56FD\u5185\u51E0\u4E4E\u6CA1\u6709\u76F8\u5173\u6570\u636E\u3002\u4E3A\u6B64\u6211\u5728\u8FD9\u91CC\u505A\u8FC7\u4E00\u4E2A\u8C03\u67E5\uFF08\u987A\u4FBF\u611F\u8C22\u53C2\u52A0\u6295\u7968\u7684\u8BF8\u4F4D\uFF09\uFF0C\u603B\u5171\u6709803\u4EBA\u53C2\u4E0E\u6295\u7968\u3002</p><p>\u4ECE\u8C03\u67E5\u7ED3\u679C\u6765\u770B\uFF0C\u4F3C\u4E4E\u4F7F\u7528\u5B66\u6B65\u8F66\u7684\u5BB6\u5EAD\u6BD4\u4F8B\u5E76\u4E0D\u9AD8\uFF0C\u53EA\u670923.5%\uFF0C\u4F46\u4F30\u8BA1\u5173\u6CE8\u8FD9\u4E2A\u80B2\u513F\u4E3B\u9898\u8D26\u53F7\u7684\u79D1\u5B66\u7D20\u8D28\u66F4\u9AD8\uFF0C\u4F46\u4F7F\u7528\u4E86\u5B66\u6B65\u8F66\u7684\u5BB6\u5EAD\u4E2D\u53D1\u751F\u76F8\u5173\u4F24\u5BB3\u7684\u7684\u6BD4\u4F8B\u8FD8\u662F\u8FBE\u5230\u4E869.5%\uFF0C\u8003\u8651\u5230\u56FD\u5185\u5DE8\u5927\u7684\u4EBA\u53E3\u57FA\u6570\uFF0C\u8FD9\u79CD\u610F\u5916\u4F24\u5BB3\u7684\u6570\u91CF\u4E5F\u662F\u60CA\u4EBA\u7684\u3002\u6240\u4EE5\u5B66\u6B65\u8F66\u76F8\u5173\u4F24\u5BB3\u5E76\u4E0D\u662F\u56FD\u5185\u6CA1\u6709\u53D1\u751F\uFF0C\u800C\u662F\u5728\u56FD\u5185\u8FD8\u6CA1\u6709\u5F97\u5230\u8DB3\u591F\u91CD\u89C6\u3002\u4E0D\u4EC5\u662F\u666E\u901A\u5BB6\u957F\uFF0C\u5F88\u591A\u513F\u79D1\u533B\u751F\u4E5F\u6CA1\u6709\u4E13\u95E8\u5173\u6CE8\u8FC7\u8FD9\u4E2A\u95EE\u9898\u3002\u4E3A\u4E86\u51CF\u5C11\u5B66\u6B65\u8F66\u5BF9\u5B69\u5B50\u7684\u4F24\u5BB3\uFF0C\u65E0\u8BBA\u5728\u7814\u7A76\u8FD8\u662F\u5BA3\u4F20\u65B9\u9762\uFF0C\u6211\u4EEC\u90FD\u6709\u5F88\u8FDC\u7684\u8DEF\u8981\u8D70\u3002</p><p><strong>\u63D0\u793A\uFF1A</strong></p><p>1.\u56FE\u7247\u6765\u6E90\u4E8E\u7F51\u7EDC\uFF0C\u672C\u6587\u5DF2\u53D1\u8868\u4E8E\u679C\u58F3\u7F51\uFF0C\u8F6C\u8F7D\u8BF7\u8054\u7CFB\u679C\u58F3\u7F51\u3002</p><p>2.\u540E\u53F0\u5F88\u591A\u4EBA\u67E5\u8BE2\u53D1\u70E7\uFF0C\u6240\u4EE5\u51C6\u5907\u5355\u72EC\u5199\u4E00\u7BC7\u53D1\u70E7\u7684\u79D1\u666E\uFF0C\u5173\u4E8E\u53D1\u70E7\u5927\u5BB6\u60F3\u77E5\u9053\u4EC0\u4E48\u53EF\u4EE5\u56DE\u590D\u6211\uFF0C\u9488\u5BF9\u96C6\u4E2D\u7684\u95EE\u9898\u6211\u5728\u6587\u7AE0\u91CC\u5199\u5199\u3002</p><p>3.\u70B9\u2193<strong>\u9605\u8BFB\u539F\u6587</strong>\u53EF\u67E5\u770B\u5FAE\u4FE1\u76EE\u5F55\u3002</p>',25),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=200408948&idx=1&sn=19d81c99599db1ce9cce376e75f78ac3&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=c("\u67E5\u770B\u539F\u6587");function g(m,f){const p=r("ExternalLinkIcon");return t(),n(a,null,[_,e("p",null,[e("a",h,[l,o(p)])])],64)}var b=d(i,[["render",g]]);export{b as default};
