import{r as p,o as a,c,a as e,b as n,F as o,d as r,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},l=r('<h3 id="\u8354\u679D\u597D\u5403-\u5B69\u5B50\u80FD\u5403\u5417" tabindex="-1"><a class="header-anchor" href="#\u8354\u679D\u597D\u5403-\u5B69\u5B50\u80FD\u5403\u5417" aria-hidden="true">#</a> \u8354\u679D\u597D\u5403\uFF0C\u5B69\u5B50\u80FD\u5403\u5417\uFF1F</h3><p>\u8354\u679D\u6E05\u751C\u53EF\u53E3\u3001\u6ED1\u5AE9\u591A\u6C41\uFF0C\u5927\u4EBA\u7231\u5403\uFF0C\u5B69\u5B50\u4E5F\u559C\u6B22\u3002\u53C8\u5230\u4E86\u8354\u679D\u4E0A\u5E02\u5B63\u8282\uFF0C\u5927\u5BB6\u53EF\u80FD\u4E5F\u542C\u8BF4\u8FC7\uFF0C\u6709\u7684\u4EBA\u5403\u4E86\u8354\u679D\u4E4B\u540E\u4E0D\u8212\u670D\uFF0C\u6BD4\u5982\u5934\u6655\u3001\u51FA\u6C57\u3001\u53E3\u6E34\u3001\u6CA1\u529B\u6C14\u7B49\u95EE\u9898\uFF0C\u90A3\u5B69\u5B50\u80FD\u5403\u8354\u679D\u5417\uFF1F</p><p>\u8354\u679D\u539F\u4EA7\u4E8E\u6211\u56FD\uFF0C\u6211\u56FD\u4E5F\u662F\u6700\u5927\u7684\u8354\u679D\u751F\u4EA7\u56FD\uFF0C\u5176\u6B21\u662F\u5370\u5EA6\u3002\u56FD\u4EBA\u901A\u5E38\u8BA4\u4E3A\uFF0C\u5403\u591A\u4E86\u8354\u679D\u4F1A\u4E0D\u8212\u670D\uFF0C\u662F\u56E0\u4E3A\u8354\u679D\u6709\u201C\u70ED\u6C14\u201D\uFF0C\u5403\u591A\u4E86\u4F1A\u201C\u4E0A\u706B\u201D\uFF0C\u90A3\u5370\u5EA6\u4EBA\u5403\u8354\u679D\u4F1A\u4E0D\u4F1A\u201C\u4E0A\u706B\u201D\u5462\uFF1F</p><p>\u5728\u5370\u5EA6\u7684\u6700\u5927\u8354\u679D\u4EA7\u533A\u7A46\u624E\u6CD5\u5C14\u5E03\u5C14\uFF0C\u6BCF\u5E745\u6708\u662F\u8354\u679D\u4E0A\u5E02\u7684\u5B63\u8282\uFF0C\u5728\u8FC7\u53BB\u7684\u4E8C\u5341\u5E74\u91CC\uFF0C\u6BCF\u5230\u8FD9\u4E2A\u5B63\u8282\uFF0C\u5F53\u5730\u90FD\u4F1A\u53D1\u751F\u4E00\u79CD\u73B0\u8C61\uFF1A\u4E00\u4E9B\u5B69\u5B50\u5728\u51CC\u6668\u51FA\u73B0\u5C16\u53EB\u3001\u62BD\u6410\u3001\u610F\u8BC6\u6539\u53D8\u3001\u660F\u8FF7\uFF0C\u751A\u81F3\u6B7B\u4EA1\u3002</p><p>\u6BCF\u5E74\u4E0A\u767E\u4E2A\u5B69\u5B50\u4E0D\u660E\u539F\u56E0\u7684\u6B7B\u4EA1\uFF0C\u5F15\u53D1\u4E86\u5F53\u5730\u4EBA\u7684\u6050\u614C\uFF0C\u6240\u4EE5\u79D1\u5B66\u5BB6\u4EEC\u4E00\u76F4\u60F3\u627E\u5230\u539F\u56E0\u3002\u5370\u5EA6\u4EBA\u6CA1\u6709\u201C\u4E0A\u706B\u201D\u7684\u89C2\u5FF5\uFF0C\u56E0\u4E3A\u8FD9\u4E9B\u5B69\u5B50\u795E\u7ECF\u7CFB\u7EDF\u75C7\u72B6\u660E\u663E\uFF0C\u4ED6\u4EEC\u66FE\u731C\u6D4B\u8FC7\u201C\u65E5\u672C\u8111\u708E\u201D\u3001\u4E2D\u6691\u3001\u6216\u4E00\u4E9B\u672A\u77E5\u75C5\u6BD2\u3001\u7EC6\u83CC\u611F\u67D3\u7B49\u539F\u56E0\uFF0C\u4E5F\u731C\u6D4B\u8FC7\u53EF\u80FD\u548C\u8354\u679D\u6709\u5173\uFF0C\u4F46\u4EC5\u662F\u731C\u6D4B\u800C\u5DF2\u3002</p><p>\u8FD9\u4E2A\u95EE\u9898\u4E5F\u5F15\u8D77\u4E86\u7F8E\u56FD\u79D1\u5B66\u5BB6\u7684\u5173\u6CE8\uFF0C\u5728\u7F8E\u56FD\u75BE\u75C5\u63A7\u5236\u4E2D\u5FC3\u7684\u8D44\u52A9\u4E0B\uFF0C\u5370\u5EA6\u548C\u7F8E\u56FD\u7684\u79D1\u7814\u4EBA\u5458\u5BF9\u8FD9\u4E9B\u5B69\u5B50\u505A\u4E86\u6DF1\u5165\u8C03\u67E5\u3002</p><p>2014\u5E74\u5F53\u5730\u6709390\u4E2A\u5B69\u5B50\u56E0\u4E3A\u8FD9\u4E9B\u95EE\u9898\u88AB\u9001\u5230\u533B\u9662\uFF0C\u5176\u4E2D122\u4E2A\u5B69\u5B50\u6B7B\u4EA1\u3002\u7ED9\u8FD9\u4E9B\u5B69\u5B50\u505A\u611F\u67D3\u6027\u75C5\u539F\u4F53\u548C\u519C\u836F\u68C0\u6D4B\uFF0C\u90FD\u6CA1\u6709\u53D1\u73B0\u5F02\u5E38\uFF0C\u4F46\u8FD9\u4E9B\u5B69\u5B50\u90FD\u5403\u4E86\u8354\u679D\uFF0C\u800C\u4E14\u6CA1\u5403\u665A\u996D\u7684\u5B69\u5B50\u75C5\u60C5\u66F4\u4E25\u91CD\u3002</p><p>\u7814\u7A76\u4EBA\u5458\u540C\u65F6\u53D1\u73B0\uFF0C\u5165\u9662\u65F6\u8FD9\u4E9B\u5B69\u5B50\u5F88\u591A\u90FD\u6709\u4F4E\u8840\u7CD6\uFF0C\u5C3F\u6DB2\u91CC\u4E5F\u68C0\u6D4B\u51FA\u8354\u679D\u91CC\u542B\u6709\u7684\u6B21\u7518\u6C28\u9178A\u548C\uFF08\u6216\uFF09MCPG\uFF0C\u6B21\u7518\u6C28\u9178A\u548CMCPG\u662F\u5DF2\u77E5\u7684\u539F\u6BD2\u7D20\uFF0C\u5B83\u4EEC\u8FDB\u5165\u4EBA\u4F53\u540E\u7ECF\u8FC7\u4EE3\u8C22\u53EF\u4EE5\u5F62\u6210\u4E00\u4E9B\u5F71\u54CD\u8840\u7CD6\u751F\u6210\u7684\u7269\u8D28\uFF0C\u4ECE\u800C\u5BFC\u81F4\u4F4E\u8840\u7CD6\u75C7\uFF0C\u8FDB\u800C\u5BFC\u81F4\u4F4E\u8840\u7CD6\u8111\u75C5\uFF0C\u7136\u540E\u8FD9\u4E9B\u5B69\u5B50\u51FA\u73B0\u4E86\u62BD\u6410\u3001\u660F\u8FF7\u7B49\u95EE\u9898\u3002\u5982\u679C\u665A\u4E0A\u6CA1\u5403\u665A\u996D\uFF0C\u8840\u7CD6\u5C31\u66F4\u4F4E\uFF0C\u4E5F\u5C31\u66F4\u5371\u9669\u3002</p><p>\u6839\u636E\u8FD9\u4E2A\u53D1\u73B0\uFF0C\u5F53\u5730\u7684\u5065\u5EB7\u673A\u6784\u8BA9\u5BB6\u957F\u4EEC\u786E\u4FDD\u5B69\u5B50\u90FD\u5403\u665A\u996D\uFF0C\u5E76\u4E14\u9650\u5236\u8FDB\u98DF\u8354\u679D\u7684\u91CF\uFF0C\u51FA\u73B0\u8FD9\u4E2A\u95EE\u9898\u7684\u5B69\u5B50\u5C31\u5927\u5E45\u4E0B\u964D\u4E86\u3002\u8FD9\u4E2A\u7ED9\u5F53\u5730\u4EBA\u9020\u6210\u6050\u614C\uFF0C\u56F0\u60D1\u79D1\u7814\u4EBA\u5458\u51E0\u5341\u5E74\u7684\u8C1C\u56E2\u5C31\u6B64\u89E3\u5F00\u4E86\uFF0C\u987A\u9053\u4E5F\u5E2E\u6211\u4EEC\u89E3\u5F00\u4E86\u4E00\u4E2A\u201C\u8354\u679D\u4E0A\u706B\u201D\u7684\u8C1C\u56E2\u3002\u8FD9\u4E2A\u7814\u7A76\u53D1\u8868\u5728\u4ECA\u5E742\u6708\u300A\u67F3\u53F6\u5200\u5168\u7403\u5065\u5EB7\u300B\u4E0A[1]\u3002</p><p>\u6240\u4EE5\u5B69\u5B50\u786E\u5B9E\u4E0D\u80FD\u5403\u5F88\u591A\u8354\u679D\uFF0C\u5C24\u5176\u662F\u4E0D\u80FD\u7A7A\u8179\u5403\uFF0C\u4F46\u4E0D\u662F\u56E0\u4E3A\u201C\u4E0A\u706B\u201D\uFF0C\u800C\u662F\u56E0\u4E3A\u8354\u679D\u91CC\u542B\u6709\u80FD\u5F71\u54CD\u8840\u7CD6\u7684\u7269\u8D28\uFF0C\u5BB9\u6613\u5BFC\u81F4\u4F4E\u8840\u7CD6\uFF0C\u6B21\u7518\u6C28\u9178A\u5728\u672A\u719F\u900F\u7684\u8354\u679D\u91CC\u542B\u91CF\u66F4\u9AD8\uFF0C\u6240\u4EE5\u5373\u4FBF\u8981\u5403\u4E5F\u5C3D\u91CF\u5403\u719F\u900F\u7684\u3002</p><p>\u9664\u4E86\u8354\u679D\uFF0C\u9F99\u773C\u3001\u7EA2\u6BDB\u4E39\u7B49\u65E0\u60A3\u5B50\u79D1\u6C34\u679C\u540C\u6837\u542B\u6709\u6B21\u7518\u6C28\u9178A\uFF0C\u540C\u6837\u4E0D\u80FD\u591A\u5403\uFF0C\u7F8E\u56FDFDA\u57282014\u5E74\u5F00\u59CB\u7981\u6B62\u6B21\u7518\u6C28\u9178A\u542B\u91CF100ppm\u4EE5\u4E0A\u7684\u897F\u975E\u8354\u679D\u679C\u4EA7\u54C1\u8FDB\u53E3[2]\u3002</p><p>\u6B64\u5916\uFF0C\u8354\u679D\u3001\u9F99\u773C\u7B49\u4E5F\u662F\u5BB9\u6613\u5BFC\u81F4\u513F\u7AE5\u6C14\u7BA1\u5F02\u7269\u7684\u6C34\u679C\uFF0C\u6BCF\u5E74\u90FD\u6709\u5B69\u5B50\u56E0\u4E3A\u8FD9\u7C7B\u6C14\u7BA1\u5F02\u7269\u7A92\u606F\u6B7B\u4EA1\uFF0C\u800C4\u5C81\u4EE5\u4E0B\u5B69\u5B50\u662F\u66F4\u5BB9\u6613\u53D1\u751F\u6C14\u7BA1\u5F02\u7269\u7684\u4EBA\u7FA4\uFF0C\u6240\u4EE5\u4E0D\u5EFA\u8BAE\u5403\u3002\uFF08\u9898\u56FE\u6765\u81EAPixabay\uFF09</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p>[1]http://www.thelancet.com/journals/langlo/article/PIIS2214-109X(17)30035-9/fulltext</p><p>[2]https://www.fda.gov/Food/NewsEvents/ConstituentUpdates/ucm393073.htm</p><hr><p>\u56DE\u590D**\u300C\u5F02\u7269\u300D**\uFF0C\u53EF\u9605\u8BFB\uFF1A\u8B66\u60D5\u593A\u547D\u7684\u6C14\u7BA1\u5F02\u7269</p><p>\u70B9\u51FB\u9605\u8BFB\u539F\u6587\uFF0C\u53EF\u5411\u6211\u548C\u5176\u5B83\u533B\u751F\u54A8\u8BE2</p>',18),_={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626707&idx=1&sn=d877c02ff2051c2b5b3857d5c8f35744&chksm=8b5cb529bc2b3c3f4b3fa1d4d8ebd26d4eafc198d27f75897fd45b446d2398a4f6d09bc51557&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},f=s("\u67E5\u770B\u539F\u6587");function h(m,b){const t=p("ExternalLinkIcon");return a(),c(o,null,[l,e("p",null,[e("a",_,[f,n(t)])])],64)}var w=d(i,[["render",h]]);export{w as default};
