import{r,o as t,c as n,a as p,b as o,F as s,d as a,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const g={},_=a('<h3 id="\u6253\u75AB\u82D7\u540E\u53D1\u70ED-\u5403\u9000\u70E7\u836F\u4F1A\u5F71\u54CD\u75AB\u82D7\u6548\u679C\u5417" tabindex="-1"><a class="header-anchor" href="#\u6253\u75AB\u82D7\u540E\u53D1\u70ED-\u5403\u9000\u70E7\u836F\u4F1A\u5F71\u54CD\u75AB\u82D7\u6548\u679C\u5417" aria-hidden="true">#</a> \u6253\u75AB\u82D7\u540E\u53D1\u70ED\uFF0C\u5403\u9000\u70E7\u836F\u4F1A\u5F71\u54CD\u75AB\u82D7\u6548\u679C\u5417\uFF1F</h3><p>\u63A5\u79CD\u6D41\u611F\u75AB\u82D7\u540E\uFF0C\u90E8\u5206\u5B69\u5B50\u53EF\u80FD\u4F1A\u51FA\u73B0\u53D1\u70ED\u7684\u60C5\u51B5\u3002\u90A3\u53D1\u70ED\u662F\u4E0D\u662F\u63A5\u79CD\u75AB\u82D7\u5F15\u8D77\u7684\u5462\uFF1F\u5403\u9000\u70E7\u836F\u4F1A\u4E0D\u4F1A\u5F71\u54CD\u63A5\u79CD\u6548\u679C\uFF1F\u5B69\u5B50\u611F\u5192\u4E86\u53EF\u4EE5\u63A5\u79CD\u75AB\u82D7\u5417\uFF1F\u4ECA\u5929\u5206\u4EAB\u4E00\u4E2A\u6021\u79BE\u4E0A\u7684\u75AB\u82D7\u63A5\u79CD\u76F8\u5173\u7684\u54A8\u8BE2\uFF0C\u4F9B\u5927\u5BB6\u53C2\u8003\u3002</p><p><strong>XX\u5988\u5988\u7684\u63D0\u95EE</strong></p><p>\u533B\u751F\u60A8\u597D\uFF0C\u5973\u5B9D\uFF0C3\u5C811\u4E2A\u6708\uFF0C11.12\u4E0B\u5348\u4E09\u70B9\u5DE6\u53F3\u5E26\u5B9D\u5B9D\u53BB\u63A5\u79CD\u4E86\u6D41\u611F\u75AB\u82D7\uFF0C\u4E0B\u5348\u665A\u4E9B\u65F6\u5019\u5B9D\u5B9D\u5F00\u59CB\u51FA\u73B0\u4E86\u53D1\u70ED\u7684\u60C5\u51B5\uFF0C\u5B9D\u5B9D\u4E00\u76F4\u54FC\u54FC\u5527\u5527\u7684\uFF0C\u521A\u521A\u91CF\u4E86\u4F53\u6E29\u670938.8\u5EA6\u3002</p><p>\u8BF7\u95EE\uFF1A\u5982\u679C\u4F53\u6E29\u7EE7\u7EED\u5347\u9AD8\uFF0C\u53EF\u4EE5\u670D\u7528\u9000\u70E7\u836F\u5417\uFF1F</p><p>**\u738B\u533B\u751F\u7684\u56DE\u590D<br> **</p><p>\u60A8\u597D\uFF0C\u611F\u8C22\u60A8\u7684\u54A8\u8BE2\uFF01\u5B9D\u5B9D3\u5C81\u4E86\uFF0C\u4ECA\u5929\u4E0B\u53483\u70B9\u5DE6\u53F3\u63A5\u79CD\u4E86\u6D41\u611F\u75AB\u82D7\uFF0C\u4E0B\u5348\u665A\u4E9B\u65F6\u5019\u51FA\u73B0\u53D1\u70ED\uFF0C\u521A\u521A\u4F53\u6E2938.8\u2103\uFF0C\u5B9D\u5B9D\u60C5\u51B5\u5DF2\u4E86\u89E3\u3002</p><p>\u5B9D\u5B9D\u76EE\u524D\u6CA1\u6709\u54B3\u55FD\u3001\u6D41\u9F3B\u6D95\u7B49\u75C7\u72B6\u7684\u8BDD\uFF0C\u521D\u6B65\u8003\u8651\u53D1\u70ED\u662F\u63A5\u79CD\u6D41\u611F\u75AB\u82D7\u5F15\u8D77\u7684\uFF0C\u5C5E\u4E8E\u75AB\u82D7\u7684\u6B63\u5E38\u53CD\u5E94\uFF0C\u60A8\u4E0D\u7528\u62C5\u5FC3\uFF0C\u5177\u4F53\u5206\u6790\u5982\u4E0B\uFF1A</p><p><strong>\u4E00\u3001\u6D41\u611F\u75AB\u82D7\u7684\u5E38\u89C1\u4E0D\u826F\u53CD\u5E94</strong></p><p>1.\u5168\u8EAB\u4E0D\u826F\u53CD\u5E94\uFF1A\u53D1\u70ED\uFF08\u5341\u5206\u5E38\u89C1\uFF09\uFF1B\u5076\u89C1\u5934\u75DB\u3001\u75B2\u52B3\u4E4F\u529B\u3001\u6076\u5FC3\u5455\u5410\u7B49\u3002</p><p>2.\u5C40\u90E8\u4E0D\u826F\u53CD\u5E94\uFF1A\u5E38\u89C1\u63A5\u79CD\u90E8\u4F4D\u7684\u75BC\u75DB\u3001\u80BF\uFF1B\u5076\u89C1\u7EA2\u3001\u786C\u7ED3\u3001\u7619\u75D2\u3002</p><p>\u6D41\u611F\u75AB\u82D7\u662F\u706D\u6D3B\u75AB\u82D7\uFF0C\u75AB\u82D7\u4E2D\u6CA1\u6709\u6D3B\u7684\u6D41\u611F\u75C5\u6BD2\uFF0C\u6253\u5B8C\u6D41\u611F\u75AB\u82D7\u4E0D\u4F1A\u5BFC\u81F4\u6D41\u611F\u3002</p><p><strong>\u4E8C\u3001\u5173\u4E8E\u53D1\u70ED</strong></p><p>1.\u90E8\u5206\u5B9D\u5B9D\u6253\u5B8C\u706D\u6D3B\u75AB\u82D7\uFF08\u5982\u6D41\u611F\u75AB\u82D7\uFF09\u4F1A\u5728\u63A5\u79CD\u540E\u6570\u5C0F\u65F6\u621624\u5C0F\u65F6\u5DE6\u53F3\u51FA\u73B0\u4F53\u6E29\u5347\u9AD8\uFF0C\u8FD9\u4E2A\u662F\u5E38\u89C1\u7684\u63A5\u79CD\u75AB\u82D7\u540E\u4E00\u822C\u53CD\u5E94\uFF0C\u5C5E\u4E8E\u6B63\u5E38\u53CD\u5E94\u3002\u63A5\u79CD\u75AB\u82D7\u540E\u5F15\u8D77\u7684\u53D1\u70ED\u5927\u591A\u4E3A\u4E00\u8FC7\u6027\u53D1\u70ED\uFF0C\u6301\u7EED\u4E0D\u8D85\u8FC72\u5929\uFF0C\u53EF\u81EA\u884C\u7F13\u89E3\u3002</p><p>2.\u63A5\u79CD\u75AB\u82D7\u540E\u53D1\u70ED\u7684\u5904\u7406\u65B9\u6CD5\uFF1A</p><p>A.\u591A\u559D\u6C34\uFF0C\u51CF\u5C11\u6D3B\u52A8\uFF0C\u591A\u4F11\u606F\u3002</p><p>B.\u5982\u679C\u53D1\u70ED\u5F88\u96BE\u53D7\u3001\u7CBE\u795E\u72B6\u6001\u4E0D\u597D\uFF08\u70E6\u8E81\u3001\u54ED\u95F9\u7B49\uFF09\uFF0C\u53EF\u4EE5\u670D\u9000\u70E7\u836F\uFF08\u7F8E\u6797\u6216\u6CF0\u8BFA\u6797\uFF09\u3002\u75AB\u82D7\u63A5\u79CD\u5F15\u8D77\u7684\u53D1\u70ED\u670D\u7528\u9000\u70E7\u836F\u6709\u6548\uFF0C\u800C\u4E14\u9000\u70E7\u836F\u4E0D\u4F1A\u7834\u574F\u75AB\u82D7\u7684\u6709\u6548\u6210\u5206\uFF0C\u4E0D\u5F71\u54CD\u75AB\u82D7\u7684\u4FDD\u62A4\u6548\u679C\u3002</p><p>C.\u75AB\u82D7\u5F15\u8D77\u7684\u53D1\u70ED\u5927\u591A\u4E3A\u4E00\u8FC7\u6027\u53D1\u70ED\uFF0C\u6301\u7EED\u65F6\u95F4\u4E00\u822C\u4E0D\u8D85\u8FC748\u5C0F\u65F6\uFF0C\u53EF\u81EA\u884C\u7F13\u89E3\u3002</p><p>3.\u63A5\u79CD\u75AB\u82D7\u53EF\u4EE5\u5F15\u8D77\u53D1\u70ED\uFF0C\u611F\u67D3\u5F15\u8D77\u7684\u611F\u5192\u3001\u6241\u6843\u4F53\u708E\u3001\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u7B49\u4E5F\u53EF\u4EE5\u5F15\u8D77\u53D1\u70ED\u3002\u5355\u7EAF\u53EA\u662F\u53D1\u70ED\uFF0C\u5728\u6CA1\u6709\u51FA\u73B0\u5176\u4ED6\u75C7\u72B6\u4E4B\u524D\uFF0C\u662F\u5F88\u96BE\u533A\u5206\u53D1\u70ED\u662F\u56E0\u4E3A\u63A5\u79CD\u75AB\u82D7\u5F15\u8D77\u8FD8\u662F\u5176\u4ED6\u75BE\u75C5\u5BFC\u81F4\u7684\u3002</p><p>4.\u4E00\u822C\u6765\u8BF4\uFF0C\u5982\u679C\u5B9D\u5B9D\u9664\u4E86\u53D1\u70ED\u6CA1\u6709\u54B3\u55FD\u3001\u6D41\u9F3B\u6D95\u7B49\u5176\u4ED6\u75C7\u72B6\uFF0C\u901A\u5E38\u8BA4\u4E3A\u7531\u63A5\u79CD\u75AB\u82D7\u5F15\u8D77\u53D1\u70ED\u53CD\u5E94\u53EF\u80FD\u6027\u5927\u3002\u5982\u679C\u5728\u53D1\u70ED\u7684\u540C\u65F6\u8FD8\u6709\u54B3\u55FD\u3001\u6D41\u9F3B\u6D95\u7B49\u75C7\u72B6\uFF0C\u5E76\u4E0D\u9996\u5148\u8003\u8651\u4E0E\u63A5\u79CD\u75AB\u82D7\u6709\u5173\uFF0C\u4E0D\u6392\u9664\u5176\u4ED6\u539F\u56E0\u5F15\u8D77\u53D1\u70ED\u7684\u53EF\u80FD\uFF0C\u5982\u611F\u5192\u3001\u6241\u6843\u4F53\u53D1\u708E\u7B49\u3002</p><p>\u60A8\u4E0D\u7528\u523B\u610F\u533A\u5206\u662F\u63A5\u79CD\u75AB\u82D7\u5F15\u8D77\u7684\u53D1\u70ED\u8FD8\u662F\u5176\u4ED6\u75BE\u75C5\u5F15\u8D77\u7684\u53D1\u70ED\uFF0C\u53D1\u70ED\u7684\u5E94\u5BF9\u65B9\u6CD5\u662F\u4E00\u6837\u7684\u3002</p><p><strong>\u4E09.\u4EC0\u4E48\u60C5\u51B5\u4E0B\u9700\u8981\u53BB\u533B\u9662\uFF1F</strong></p><p>\u5982\u679C\u5B9D\u5B9D\u7CBE\u795E\u72B6\u6001\u6BD4\u8F83\u597D\uFF0C\u80FD\u81EA\u5DF1\u73A9\uFF0C\u5B9D\u5B9D\u7684\u5403\u548C\u7761\u4E0E\u5E73\u5E38\u76F8\u5DEE\u4E0D\u5927\uFF0C\u53EF\u4EE5\u5728\u5BB6\u7EE7\u7EED\u89C2\u5BDF\uFF1B\u540C\u65F6\uFF0C\u5B9D\u5B9D\u8981\u591A\u559D\u6C34\uFF0C\u591A\u4F11\u606F\uFF0C\u51CF\u5C11\u6D3B\u52A8\u3002</p><p>\u5982\u679C\u5B9D\u5B9D\u7684\u4F53\u6E29\u53D1\u70ED\u4E0D\u9000\uFF08\u53D1\u70ED\u6301\u7EED\u8D85\u8FC748\u5C0F\u65F6\u6216\u53CD\u590D\u51FA\u73B0\u9AD8\u70ED\uFF09\u3001\u54ED\u95F9\u4E0D\u65AD\u3001\u7CBE\u795E\u72B6\u6001\u4E0D\u597D\u6216\u6709\u5176\u5B83\u660E\u663E\u4E0D\u9002\uFF08\u54B3\u55FD\u3001\u76AE\u75B9\u7B49\uFF09\uFF0C\u8FD9\u65F6\u6211\u4EEC\u9700\u8981\u8003\u8651\u5176\u4ED6\u539F\u56E0\uFF08\u5982\u611F\u5192\u3001\u6241\u6843\u4F53\u53D1\u708E\u3001\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u7B49\uFF09\u5F15\u8D77\u53D1\u70E7\u7684\u53EF\u80FD\uFF0C\u5EFA\u8BAE\u53CA\u65F6\u53BB\u533B\u9662\u5C31\u8BCA\u3002</p><p><strong>\u56DB.\u7ED3\u8BBA\u548C\u5EFA\u8BAE</strong></p><p>1. \u5B9D\u5B9D\u73B0\u5728\u4F53\u6E2938.8\u5EA6\uFF0C\u76EE\u524D\u6CA1\u6709\u6D41\u9F3B\u6D95\u3001\u54B3\u55FD\u7B49\u75C7\u72B6\uFF0C\u53D1\u70ED\u7684\u65F6\u95F4\u6BB5\u7B26\u5408\u63A5\u79CD\u6D41\u611F\u75AB\u82D7\u5F15\u8D77\u7684\u53D1\u70ED\u7279\u70B9\uFF0C\u521D\u6B65\u5224\u65AD\u5B9D\u5B9D\u7684\u53D1\u70ED\u662F\u63A5\u79CD\u6D41\u611F\u75AB\u82D7\u5F15\u8D77\uFF0C\u5EFA\u8BAE\u7ED9\u5B9D\u5B9D\u591A\u559D\u6C34\uFF0C\u591A\u4F11\u606F\uFF0C\u51CF\u5C11\u6D3B\u52A8\u3002</p><p>2.\u5B9D\u5B9D\u53D1\u70ED\u5982\u679C\u5F88\u96BE\u53D7\u3001\u7CBE\u795E\u4E0D\u597D\u3001\u70E6\u8E81\u53EF\u4EE5\u5403\u9000\u70E7\u836F\uFF0C\u7F8E\u6797\u6216\u6CF0\u8BFA\u6797\u90FD\u884C\u3002\u9000\u70E7\u836F\u4E0D\u5F71\u54CD\u6D41\u611F\u75AB\u82D7\u7684\u4FDD\u62A4\u6548\u679C\u3002</p><p>\u4EE5\u4E0A\u662F\u6211\u7684\u7B54\u590D\u3002\u795D\u60A8\u7684\u5B9D\u5B9D\u65E9\u65E5\u5EB7\u590D\u3002\u6709\u95EE\u9898\u6B22\u8FCE\u7EE7\u7EED\u8FFD\u95EE\u3002</p><p><strong>XX\u5988\u5988\u7684\u7B2C\u4E00\u6B21\u8FFD\u95EE</strong></p><p>\u8C22\u8C22\u533B\u751F\u8BE6\u7EC6\u7684\u89E3\u7B54\uFF0C\u53E6\u5916\u60F3\u8BF7\u6559\u60A8\uFF1A\u5B9D\u5B9D\u5982\u679C\u751F\u75C5\u4E86\uFF0C\u6BD4\u5982\u4E0D\u662F\u5F88\u4E25\u91CD\u7684\u611F\u5192\uFF0C\u53EF\u4EE5\u63A5\u79CD\u75AB\u82D7\u5417\uFF1F</p><p>**\u738B\u533B\u751F\u7684\u56DE\u590D<br> **</p><p>\u60A8\u5BA2\u6C14\u4E86!\u8F7B\u5FAE\u611F\u5192\u3001\u8F7B\u5FAE\u8179\u6CFB\u3001\u8F7B\u5FAE\u6E7F\u75B9\u3001\u611F\u5192\u7684\u5EB7\u590D\u671F\u7B49\u662F\u53EF\u4EE5\u63A5\u79CD\u75AB\u82D7\u7684\uFF1B\u611F\u5192\u6025\u6027\u671F\u6216\u4E25\u91CD\u611F\u5192\uFF0C\u7279\u522B\u662F\u4F34\u6709\u53D1\u70ED\u7684\u60C5\u51B5\u4E0B\uFF0C\u5219\u5EFA\u8BAE\u63A8\u8FDF\u63A5\u79CD\u75AB\u82D7\u3002</p><p><strong>\u4E00.\u8F7B\u5FAE\u611F\u5192\u3001\u611F\u5192\u5EB7\u590D\u671F\u53EF\u4EE5\u63A5\u79CD\u75AB\u82D7</strong></p><p>1. \u8F7B\u5FAE\u611F\u5192\u6216\u611F\u5192\u5EB7\u590D\u671F\uFF0C\u5982\u679C\u5B9D\u5B9D\u53D1\u70ED\uFF1C38.5\u2103\u3001\u4E0D\u54B3\u55FD\u6216\u5076\u5C14\u54B3\u55FD\u3001\u7CBE\u795E\u72B6\u6001\u597D\uFF0C\u662F\u4E0D\u5F71\u54CD\u5B9D\u5B9D\u63A5\u79CD\u75AB\u82D7\u7684\uFF1B\u6D41\u9F3B\u6D95\u3001\u5076\u5C14\u54B3\u55FD\u7B49\u4E5F\u4E0D\u5F71\u54CD\u75AB\u82D7\u7684\u4FDD\u62A4\u6548\u679C\uFF0C\u4E0D\u4F1A\u589E\u52A0\u75AB\u82D7\u4E0D\u826F\u53CD\u5E94\u7684\u98CE\u9669\u3002</p><p>2. \u4E16\u754C\u536B\u751F\u7EC4\u7EC7\uFF08WHO\uFF09\u4E00\u76F4\u5F3A\u8C03\u75AB\u82D7\u4E0D\u5E94\u8BE5\u6709\u5F88\u591A\u7684\u7981\u5FCC\u75C7\uFF0C\u5E38\u89C4\u4F7F\u7528\u75AB\u82D7\u7684\u6536\u76CA\u8FDC\u8FDC\u9AD8\u4E8E\u53D1\u751F\u4E0D\u826F\u53CD\u5E94\u7684\u5371\u9669\u6027\u3002</p><p>\u4F4E\u70ED\u3001\u4E0A\u547C\u5438\u9053\u611F\u67D3\uFF08\u611F\u5192\uFF09\u7B49\u8FD9\u4E9B\u8F7B\u5FAE\u7684\u75C7\u72B6\u6216\u75BE\u75C5\u5728\u5A74\u5E7C\u513F\u548C\u513F\u7AE5\u65F6\u671F\u5F88\u5E38\u89C1\uFF0C\u82E5\u5BF9\u6709\u8F7B\u75C7\u75BE\u75C5\u7684\u5B69\u5B50\u63A8\u8FDF\u63A5\u79CD\u75AB\u82D7\uFF0C\u5219\u4F1A\u8BA9\u5B69\u5B50\u9519\u8FC7\u4E86\u75AB\u82D7\u4FDD\u62A4\u7684\u673A\u4F1A\uFF0C\u53EF\u80FD\u5BFC\u81F4\u76F8\u5E94\u7684\u75BE\u75C5\uFF0C\u751A\u81F3\u5F15\u8D77\u75BE\u75C5\u7684\u7206\u53D1\uFF1B\u6240\u4EE5\uFF0C\u5E94\u5229\u7528\u6BCF\u6B21\u673A\u4F1A\u7ED9\u7B26\u5408\u9002\u5E94\u75C7\u7684\u513F\u7AE5\u63A5\u79CD\u75AB\u82D7\u3002</p><p>3.\u7F8E\u56FD\u75BE\u75C5\u9884\u9632\u63A7\u5236\u4E2D\u5FC3\u4E5F\u8BA4\u4E3A\uFF0C\u8F7B\u5FAE\u6025\u6027\u75BE\u75C5\u4E0D\u4F1A\u51CF\u4F4E\u75AB\u82D7\u6548\u679C\u6216\u589E\u52A0\u75AB\u82D7\u7684\u4E0D\u826F\u53CD\u5E94\uFF0C\u60A3\u6709\u8F7B\u5FAE\u6025\u6027\u75BE\u75C5\u5982\u4E0A\u547C\u5438\u9053\u611F\u67D3\u3001\u8F7B\u5EA6\u8179\u6CFB\u7B49\u7684\u513F\u7AE5\u5E94\u6309\u514D\u75AB\u7A0B\u5E8F\u63A5\u79CD\u75AB\u82D7\u3002</p><p>4.\u611F\u5192\u7684\u5EB7\u590D\u671F\uFF0C\u5982\u679C\u5168\u8EAB\u75C7\u72B6\u57FA\u672C\u75CA\u6108\u4E86\uFF0C\u53EF\u4EE5\u63A5\u79CD\u75AB\u82D7\u3002</p><p><strong>\u4E8C.\u611F\u5192\u7684\u6025\u6027\u671F\u5EFA\u8BAE\u63A8\u8FDF\u63A5\u79CD\u75AB\u82D7</strong></p><p>\u611F\u5192\u7684\u6025\u6027\u671F\uFF0C\u7279\u522B\u662F\u4F34\u6709\u53D1\u70ED\u7684\u60C5\u51B5\u4E0B\uFF0C\u5E94\u63A8\u8FDF\u63A5\u79CD\u75AB\u82D7\u3002</p><p>\u611F\u5192\u671F\u95F4\u53EF\u80FD\u6709\u53D1\u70ED\u3001\u4E4F\u529B\u3001\u5934\u75DB\u3001\u808C\u8089\u9178\u75DB\u7B49\u5168\u8EAB\u75C7\u72B6\uFF0C\u800C\u6253\u5B8C\u75AB\u82D7\u4E5F\u53EF\u80FD\u51FA\u73B0\u53D1\u70ED\u3001\u4E4F\u529B\u3001\u5934\u75DB\u3001\u808C\u8089\u9178\u75DB\u7B49\u75C7\u72B6\u3002\u5982\u679C\u5728\u611F\u5192\u7684\u6025\u6027\u671F\u63A5\u79CD\u75AB\u82D7\uFF0C\u5219\u53EF\u80FD\u4F1A\u52A0\u5F3A\u8FD9\u4E9B\u75C7\u72B6\uFF0C\u52A0\u91CD\u8EAB\u4F53\u7684\u4E0D\u8212\u670D\u3002</p><p>\u4EE5\u4E0A\u662F\u6211\u7684\u7B54\u590D\uFF0C\u6709\u5176\u4ED6\u95EE\u9898\u6B22\u8FCE\u7EE7\u7EED\u8FFD\u95EE\u3002</p><p><strong>XX\u5988\u5988\u7684\u7B2C\u4E8C\u6B21\u8FFD\u95EE</strong></p><p>\u5B9D\u5B9D\u8FD9\u6BB5\u65F6\u95F4\u4E00\u76F4\u6CA1\u6709\u611F\u5192\u75C7\u72B6\uFF0C\u53EA\u662F\u6628\u5929\u548C\u4ECA\u5929\u65E9\u4E0A\u8D77\u5E8A\u7684\u65F6\u5019\u54B3\u55FD\u4E86\u51E0\u4E0B\uFF0C\u5176\u4ED6\u65F6\u95F4\u4E5F\u6CA1\u542C\u5230\u5979\u54B3\u55FD\u3002</p><p>\u54B3\u55FD\u548C\u75AB\u82D7\u63A5\u79CD\u6709\u5173\u7CFB\u5417\uFF1F\u6709\u6CA1\u6709\u4EC0\u4E48\u7F13\u89E3\u54B3\u55FD\u7684\u529E\u6CD5\uFF1F\u9700\u4E0D\u9700\u8981\u5403\u836F\uFF1F</p><p>**\u738B\u533B\u751F\u7684\u56DE\u590D<br> **</p><p>\u60A8\u597D\uFF0C\u62B1\u6B49\u8BA9\u60A8\u4E45\u7B49\u4E86\uFF0C\u6211\u7684\u7B54\u590D\u5982\u4E0B\uFF1A</p><p>1.\u6D41\u611F\u75AB\u82D7\u662F\u706D\u6D3B\u75AB\u82D7\uFF0C\u75AB\u82D7\u4E2D\u6CA1\u6709\u6D3B\u7684\u75C5\u6BD2\uFF0C\u63A5\u79CD\u540E\u4E0D\u4F1A\u5BFC\u81F4\u54B3\u55FD\u6216\u52A0\u91CD\u54B3\u55FD\uFF1B\u5B9D\u5B9D\u76EE\u524D\u7684\u54B3\u55FD\u548C\u6D41\u611F\u75AB\u82D7\u63A5\u79CD\u6CA1\u6709\u4EC0\u4E48\u5173\u7CFB\u3002</p><p>2.\u5173\u4E8E\u54B3\u55FD\u7684\u5177\u4F53\u6CBB\u7597\u60A8\u53EF\u4EE5\u54A8\u8BE2\u4E00\u4E0B\u8FD9\u65B9\u9762\u66F4\u4E3A\u4E13\u4E1A\u7684\u513F\u79D1\u533B\u751F\uFF0C\u8FD9\u91CC\u53EA\u80FD\u7ED9\u60A8\u4E00\u4E9B\u539F\u5219\u6027\u5EFA\u8BAE\uFF1A</p><p>A.1\u5C81\u4EE5\u4E0A\u7684\u5B69\u5B50\uFF0C\u53EF\u4EE5\u5403\u8702\u871C\u6765\u7F13\u89E3\u54B3\u55FD\uFF0C\u6BCF\u6B212.5-5\u6BEB\u5347\uFF080.5-1\u8336\u5319\uFF09\uFF0C\u76F4\u63A5\u5403\u8702\u871C\u5C31\u884C\uFF0C\u6BCF\u5929\u53EF\u4EE53-4\u6B21\u3002</p><p>B.\u4F7F\u7528\u52A0\u6E7F\u5668\uFF0C\u8BA9\u73AF\u5883\u76F8\u5BF9\u6E7F\u6DA6\u53EF\u80FD\u5BF9\u7F13\u89E3\u54B3\u55FD\u6709\u5E2E\u52A9\u3002</p><p>C.\u5E38\u7528\u7684\u975E\u5904\u65B9\u9547\u54B3\u836F\u662F\u53F3\u7F8E\u6C99\u82AC\uFF0C\u4F46\u4E0D\u6EE16\u5C81\u4E0D\u5EFA\u8BAE\u5403\u975E\u5904\u65B9\u54B3\u55FD\u836F\uFF0C\u73B0\u6709\u8BC1\u636E\u63D0\u793A\u4F7F\u7528\u6B62\u54B3\u836F\u5BF9\u611F\u5192\u5F15\u8D77\u7684\u513F\u7AE5\u54B3\u55FD\u5F0A\u5927\u4E8E\u5229\u3002</p><p>\u4EE5\u4E0A\u662F\u6211\u7684\u7B54\u590D\uFF0C\u795D\u60A8\u7684\u5B9D\u5B9D\u65E9\u65E5\u5EB7\u590D\uFF0C\u6709\u95EE\u9898\u60A8\u518D\u4E0E\u6211\u8054\u7CFB\u3002</p><p><strong>\u533B\u5B66\u6821\u5BF9\uFF1A</strong></p><p>\u75AB\u82D7\u79D1\u9648\u4F1A\u6770\u533B\u751F</p><p>\u4EE5\u4E0A\u5C31\u662F\u6574\u4E2A\u54A8\u8BE2\u7684\u5185\u5BB9\uFF0C\u4E3A\u65B9\u4FBF\u9605\u8BFB\u6709\u6240\u5220\u6539\uFF0C\u5E0C\u671B\u5BF9\u5927\u5BB6\u6709\u7528\u3002\u9700\u8981\u63D0\u9192\u7684\u662F\uFF0C\u533B\u751F\u7684\u56DE\u590D\u662F\u9488\u5BF9\u5355\u4E2A\u4E2A\u4F53\u5316\u5EFA\u8BAE\uFF0C\u4E0D\u4E00\u5B9A\u9002\u7528\u4E8E\u81EA\u5DF1\u3002\u533B\u751F\u7ED9\u51FA\u610F\u89C1\u4EC5\u4EE3\u8868\u533B\u751F\u4E2A\u4EBA\u89C2\u70B9\uFF0C\u4E0D\u4EE3\u8868\u6021\u79BE\u89C2\u70B9\u3002\u6021\u79BE\u6240\u6709\u54A8\u8BE2\u53EF\u80FD\u4F1A\u88AB\u8D28\u63A7\u4EBA\u5458\u8C03\u9605\u4EE5\u4F5C\u54A8\u8BE2\u8D28\u91CF\u63A7\u5236\uFF0C\u4F46\u6211\u4EEC\u4F1A\u4E25\u683C\u4FDD\u62A4\u54A8\u8BE2\u8005\u9690\u79C1\uFF0C\u672A\u7ECF\u5141\u8BB8\u4E0D\u4F1A\u516C\u5F00\u4EFB\u4F55\u5185\u5BB9\u3002</p><hr><p>\u901A\u8FC7\u4E0B\u9762\u4E8C\u7EF4\u7801\u5173\u6CE8\u6021\u79BE\uFF0C\u53EF\u54A8\u8BE2\u533B\u751F</p>',58),i={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651629316&idx=1&sn=abc1d3151f72e219e463d57ad0fce42a&chksm=8b5c8efebc2b07e8b45131c680df0cfb3002bdc92c9a9806c6b2a27bad6d02dfbc62362aebea&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},b=c("\u67E5\u770B\u539F\u6587");function h(l,f){const e=r("ExternalLinkIcon");return t(),n(s,null,[_,p("p",null,[p("a",i,[b,o(e)])])],64)}var u=d(g,[["render",h]]);export{u as default};
