import{r as a,o as D,c as t,a as e,b as r,F as n,d as c,e as o}from"./app.9aaa2256.js";import{_ as s}from"./plugin-vue_export-helper.21dcd24c.js";const i={},d=c('<h3 id="\u5B69\u5B50\u5927\u817F\u76AE\u7EB9\u4E0D\u5BF9\u79F0\u6709\u95EE\u9898\u5417" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u5927\u817F\u76AE\u7EB9\u4E0D\u5BF9\u79F0\u6709\u95EE\u9898\u5417" aria-hidden="true">#</a> \u5B69\u5B50\u5927\u817F\u76AE\u7EB9\u4E0D\u5BF9\u79F0\u6709\u95EE\u9898\u5417\uFF1F</h3><p>\u968F\u7740\u513F\u79D1\u77E5\u8BC6\u7684\u666E\u53CA\uFF0C\u5F88\u591A\u5BB6\u957F\u5BF9\u4E00\u4E9B\u75BE\u75C5\u7684\u8B66\u60D5\u6027\u90FD\u5F88\u9AD8\uFF0C\u6BD4\u5982\u6709\u4E9B\u5BB6\u957F\u53D1\u73B0\u5B69\u5B50\u81C0\u90E8\u76AE\u7EB9\u4E0D\u5BF9\u79F0\uFF0C\u7ACB\u9A6C\u60F3\u5230\u5B69\u5B50\u662F\u4E0D\u662F\u6709\u53D1\u80B2\u6027\u9ACB\u5173\u8282\u8131\u4F4D\uFF08DDH\uFF09\uFF0C\u76F8\u5BF9\u4EE5\u524D\u5F88\u591A\u9ACB\u5173\u8282\u8131\u4F4D\u7684\u5B69\u5B50\u62D6\u4E86\u5F88\u4E45\u6CA1\u53D1\u73B0\uFF0C\u6700\u540E\u505A\u5F88\u5927\u7684\u624B\u672F\u6765\u77EB\u6B63\uFF0C\u8FD9\u662F\u4E00\u79CD\u8FDB\u6B65\u3002</p><p>\u4F46\u8B66\u60D5\u6027\u7684\u63D0\u9AD8\u4E5F\u7ED9\u5F88\u591A\u5BB6\u957F\u5E26\u6765\u4E86\u65B0\u7684\u7126\u8651\uFF0C\u76AE\u7EB9\u4E0D\u5BF9\u79F0\u662F\u4E0D\u662F\u5C31\u662FDDH\uFF1F\u8981\u600E\u4E48\u6392\u9664\uFF1F\u9700\u8981\u505A\u54EA\u4E9B\u68C0\u67E5\uFF1F\u8981\u4E0D\u8981\u6CBB\u7597\uFF1F\u8FD9\u4E9B\u95EE\u9898\u5E38\u5E38\u6298\u78E8\u7740\u5BB6\u957F\u4EEC\uFF0C\u8FD9\u4E2A\u95EE\u9898\u6211\u81EA\u5DF1\u4E5F\u7ECF\u5386\u8FC7\uFF0C\u6240\u4EE5\u548C\u5927\u5BB6\u5206\u4EAB\u4E00\u4E0B\u3002</p><p>\u5973\u513F\u51FA\u751F\u4E0D\u4E45\uFF0C\u6211\u5C31\u53D1\u73B0\u5979\u7684\u5927\u817F\u76AE\u7EB9\u4E0D\u5BF9\u79F0\uFF0C\u56E0\u4E3A\u81EA\u5DF1\u4EE5\u524D\u5728\u513F\u7AE5\u9AA8\u79D1\u8F6E\u8F6C\u8FC7\uFF0C\u6211\u77E5\u9053\u5973\u6027\u6027\u522B\u662FDDH\u9AD8\u5371\u56E0\u7D20\u4E4B\u4E00\uFF0C\u5973\u5B69\u7684\u53D1\u75C5\u7387\u662F\u7537\u5B69\u76842-5\u500D\uFF0C\u6211\u8FD8\u77E5\u9053\u6240\u6709\u9650\u5236\u80CE\u513F\u6D3B\u52A8\u7A7A\u95F4\u7684\u56E0\u7D20\uFF0C\u6BD4\u5982\u7B2C\u4E00\u80CE\u3001\u51FA\u751F\u4F53\u91CD\u504F\u91CD\uFF0C\u7F8A\u6C34\u8FC7\u5C11\uFF0C\u90FD\u53EF\u80FD\u589E\u5927DDH\u7684\u53D1\u75C5\u98CE\u9669\uFF0C\u800C\u8FD9\u4E09\u4E2A\u56E0\u7D20\u91CC\u5979\u5360\u4E86\u4E24\u4E2A\uFF0C\u5979\u662F\u5934\u80CE\uFF0C\u5728\u5B55\u540E\u671F\u4E5F\u662F\u7F8A\u6C34\u504F\u5C11\u3002</p><p>\u5927\u817F\u76AE\u7EB9\u4E0D\u5BF9\u79F0\u548CDDH\u7684\u5173\u7CFB\u5982\u4F55\u5462\uFF1F\u56FD\u5185DDH\u7684\u53D1\u75C5\u7387\u5730\u533A\u5DEE\u5F02\u6027\u5F88\u5927\uFF0C\u5E73\u5747\u5927\u7EA60.1%\uFF0C\u5929\u6D25\u7B5B\u67E5\u4E00\u4E07\u591A\u4E2A6\u4E2A\u6708\u5185\u65B0\u751F\u513F\uFF0C\u53D1\u73B0\u5B58\u5728\u76AE\u7EB9\u4E0D\u5BF9\u79F0\u7684\u5B69\u5B50\u5927\u7EA6\u4E3A1.4%\uFF0C\u8D85\u58F0\u7B5B\u67E5\u53EF\u7591\u6216\u786E\u8BCADDH\u7684\u4E3A11.9%\uFF1B\u90D1\u5DDE\u6709\u533B\u751F\u5BF9\u4E00\u5343\u591A\u4E2A\u5927\u817F\u76AE\u7EB9\u4E0D\u5BF9\u79F0\u7684\u5B69\u5B50\u8FDB\u884C\u8D85\u58F0\u7B5B\u67E5\uFF0C\u53D1\u73B09\u4E2A\u6708\u4EE5\u4E0B\u7684\u5B69\u5B50\u8BCA\u65AD\u4E3ADDH\u7684\u5927\u7EA6\u4E3A2.3%\uFF0C9\u4E2A\u6708\uFF5E1\u5C81\u7684\u5B69\u5B50\u5982\u679C\u8FD8\u6709\u76AE\u7EB9\u4E0D\u5BF9\u79F0\uFF0CDDH\u8FBE\u52309.5%\uFF0C1\u5C81\u4EE5\u4E0A\u7684\u5B69\u5B50\u5219\u662F40%\u4EE5\u4E0A\u3002</p><p>\u6240\u4EE5\u76AE\u7EB9\u4E0D\u5BF9\u79F0\u7684\u5B69\u5B50\u5B58\u5728DDH\u7684\u98CE\u9669\u6BD4\u522B\u7684\u5B69\u5B50\u8981\u9AD8\u5F88\u591A\uFF0C\u800C\u4E14\u6301\u7EED\u7684\u5E74\u9F84\u8D8A\u5927\uFF0C\u98CE\u9669\u8D8A\u5927\u3002\u4ECE\u8FD9\u4E00\u70B9\u6765\u8BF4\uFF0C\u5BB6\u957F\u4EEC\u7684\u62C5\u5FC3\u4E0D\u662F\u6CA1\u6709\u9053\u7406\u7684\u3002</p><p>DDH\u5728\u65B0\u751F\u513F\u671F\u662F\u53EF\u4EE5\u6CA1\u6709\u4EFB\u4F55\u75C7\u72B6\u7684\uFF0C\u4E3A\u907F\u514D\u8D3B\u8BEF\u6CBB\u7597\u5916\u89C2\u5B8C\u5168\u6B63\u5E38\u7684\u65B0\u751F\u513F\u4E5F\u5E94\u8BE5\u7B5B\u67E5\uFF0C\u5B58\u5728\u5927\u817F\u76AE\u7EB9\u4E0D\u5BF9\u79F0\u7684\u5B69\u5B50\u81EA\u7136\u66F4\u5E94\u8BE5\u597D\u597D\u68C0\u67E5\u4E00\u4E0B\u3002\u597D\u5728\u81EA\u5DF1\u5C31\u662F\u513F\u5916\u79D1\u533B\u751F\uFF0C\u53EF\u4EE5\u7ACB\u9A6C\u52A8\u624B\u7ED9\u5979\u505A\u3002</p><p>\u9ACB\u5173\u8282\u4E3B\u8981\u662F\u7531\u9ACB\u81FC\u548C\u80A1\u9AA8\u5934\u7EC4\u6210\uFF0C\u4ED6\u4EEC\u7684\u7ED3\u6784\u5C31\u50CF\u4E0B\u56FE\u4E2D\u7684\u6775\u81FC\uFF0C\u9ACB\u81FC\u5C31\u662F\u81FC\uFF0C\u80A1\u9AA8\u5934\u5C31\u662F\u6775\u3002\u4E0D\u540C\u7684\u662F\u9ACB\u5173\u8282\u6709\u5173\u8282\u56CA\u548C\u97E7\u5E26\u628A\u6775\u56FA\u5B9A\u5728\u81FC\u4E4B\u4E2D\uFF0CDDH\u53D1\u75C5\u5C31\u662F\u7531\u4E8E\u79CD\u79CD\u539F\u56E0\uFF0C\u6775\u6CA1\u80FD\u56FA\u5B9A\u5728\u81FC\u4E4B\u4E2D\uFF0C\u800C\u662F\u53EF\u4EE5\u6ED1\u52A8\uFF0C\u8DD1\u5230\u81FC\u5916\u53BB\u3002</p><p>\u5728\u65B0\u751F\u513F\u671F\u95F4\uFF0C\u7B5B\u67E5DDH\u6700\u7B80\u5355\u7684\u65B9\u6CD5\u662F\u4F53\u683C\u68C0\u67E5\u3002\u9ACB\u81FC\u56FA\u5B9A\u5728\u9AA8\u76C6\u4E0A\u52A8\u4E0D\u4E86\uFF0C\u6B63\u5E38\u7684\u9ACB\u5173\u8282\u662F\u7A33\u5B9A\u7684\uFF0C\u80A1\u9AA8\u5934\u4E5F\u4E0D\u80FD\u8DD1\u52A8\uFF0C\u6211\u4EEC\u7684\u68C0\u67E5\u4E3B\u8981\u662F\u770B\u80A1\u9AA8\u5934\u4F1A\u4E0D\u4F1A\u52A8\uFF0C\u5982\u679C\u80A1\u9AA8\u5934\u80FD\u6ED1\u52A8\u5C31\u8BF4\u660E\u9ACB\u5173\u8282\u6709\u95EE\u9898\u3002</p><p>\u5728\u6696\u548C\u7684\u623F\u95F4\u91CC\uFF0C\u8BA9\u5B69\u5B50\u5E73\u8EBA\u5728\u5E8A\u4E0A\uFF0C\u8131\u53BB\u8863\u88E4\uFF0C\u5728\u5B69\u5B50\u5B89\u9759\u7684\u65F6\u5019\u50CF\u4E0B\u56FE\u4E00\u6837\u63E1\u4F4F\u5B69\u5B50\u7684\u4E0B\u80A2\uFF0C\u5C48\u819D\uFF0C\u8BA9\u9ACB\u5173\u8282\u5185\u6536\uFF0C\u62C7\u6307\u50CF\u7BAD\u5934\u65B9\u5411\u4E00\u6837\u7528\u529B\uFF0C\u8FD9\u53EB\u505ABarlow\u5B9E\u9A8C\u3002\u5982\u679C\u611F\u89C9\u5230\u9AA8\u5934\u7684\u5F39\u54CD\u6216\u8DF3\u52A8\uFF0C\u90A3\u5C31\u53EF\u80FD\u662F\u6709\u95EE\u9898\u3002<img src="http://mmbiz.qpic.cn/mmbiz/B36m2ANfbIcg4SLkAYa1mUIvwwOAhj4hRRHNuntOUiczia1Njw4U5cJfe4BlVp1vc0GIX6znnKU2PaWTrDFrypGQ/0" alt="" title="undefined"></p><p>\u76F8\u53CD\uFF0C\u50CF\u4E0B\u56FE\u4E00\u6837\uFF0C\u5728\u5C06\u9ACB\u5173\u8282\u5916\u5C55\u7684\u540C\u65F6\uFF0C\u9760\u63E1\u4F4F\u5927\u817F\u5916\u4FA7\u7684\u6307\u5934\uFF0C\u6CBF\u7BAD\u5934\u65B9\u5411\u7528\u529B\uFF0C\u5982\u679C\u611F\u89C9\u5230\u9AA8\u5934\u7684\u5F39\u54CD\u6216\u8DF3\u52A8\uFF0C\u90A3\u53EF\u80FD\u662F\u80A1\u9AA8\u5934\u4ECE\u5916\u9762\u6ED1\u8FDB\u4E86\u9ACB\u81FC\uFF0C\u8FD9\u4E5F\u8BF4\u660E\u53EF\u80FD\u6709\u8131\u4F4D\uFF0C\u8FD9\u53EBOrtolani \u5B9E\u9A8C\u3002</p><p>\u4F46\u8FD9\u4E24\u4E2A\u68C0\u67E5\u4E3B\u8981\u9002\u7528\u4E8E\u5173\u8282\u76F8\u5BF9\u677E\u5F1B\u7684\u65B0\u751F\u513F\uFF0C\u5B69\u5B50\u5230\u4E86\u4E24\u4E09\u4E2A\u6708\uFF0C\u5173\u8282\u6BD4\u8F83\u7D27\u4E86\uFF0C\u4E0A\u9762\u4E24\u4E2A\u68C0\u67E5\u5C31\u4E0D\u53EF\u9760\u4E86\u3002\u4F46\u8FD8\u6709\u522B\u7684\u68C0\u67E5\u65B9\u6CD5\uFF0C\u6700\u5E38\u7528\u7684\u662F\u8BA9\u5B69\u5B50\u53CC\u9ACB\u53CC\u819D\u5C48\u66F2\u5E73\u8EBA\u5982\u4E0B\u56FE\uFF0C\u7136\u540E\u5BF9\u6BD4\u4E24\u4E2A\u819D\u76D6\u7684\u9AD8\u4F4E\uFF0C\u6709\u8131\u4F4D\u7684\u4E00\u4FA7\u819D\u76D6\u4F1A\u6BD4\u8F83\u4F4E\uFF0C\u8FD9\u53EBAllis&#39;\u5F81\u6216Galeazzi \u5F81\u3002</p><p>\u53E6\u4E00\u4E2A\u6BD4\u8F83\u53EF\u9760\u7684\u68C0\u67E5\u662F\u9ACB\u5173\u8282\u7684\u5916\u5C55\u5B9E\u9A8C\uFF0C\u6B63\u5E38\u7684\u9ACB\u5173\u8282\u53EF\u4EE5\u5916\u5C55\u523080\uFF5E90\u5EA6\uFF0CDDH\u7684\u5B69\u5B50\u5916\u5C55\u5219\u660E\u663E\u53D7\u9650\uFF0C\u6709\u7684\u53EA\u80FD\u5230\u5916\u5C5540\uFF5E50\u5EA6\u3002</p><p>\u6211\u7ED9\u5973\u513F\u505A\u4E86\u8FD9\u4E9B\u68C0\u67E5\uFF0C\u5E76\u6CA1\u6709\u53D1\u73B0\u4EC0\u4E48\u95EE\u9898\uFF0C\u4F46\u8FD8\u662F\u6709\u4E9B\u4E0D\u653E\u5FC3\uFF0C\u90A3\u5C31\u53EA\u6709\u5230\u533B\u9662\u518D\u505A\u68C0\u67E5\u4E86\u3002\u5982\u679C\u5B69\u5B50\u5DF2\u7ECF6\u4E2A\u591A\u6708\u4E86\uFF0C\u62CD\u7247\u5B50\u76F4\u63A5\u770B\u9AA8\u5934\u4F1A\u6BD4\u8F83\u53EF\u9760\uFF0C\u6839\u636EX\u7EBF\u4E0B\u80A1\u9AA8\u5934\u548C\u9ACB\u81FC\u7684\u5173\u7CFB\uFF0C\u57FA\u672C\u53EF\u4EE5\u786E\u5B9A\u662F\u5426\u6709\u8131\u4F4D\u548C\u8131\u4F4D\u7684\u7A0B\u5EA6\u3002\u4F46\u57286\u4E2A\u6708\u4E4B\u524D\uFF0C\u5B69\u5B50\u7684\u80A1\u9AA8\u5934\u8FD8\u4E3B\u8981\u662F\u8F6F\u9AA8\uFF0C\u5728X\u7EBF\u4E0B\u770B\u4E0D\u5230\uFF0C\u6CA1\u6CD5\u5224\u65AD\u4F4D\u7F6E\uFF0C\u4F46\u8D85\u58F0\u6CE2\u662F\u53EF\u4EE5\u63A2\u5230\u8F6F\u9AA8\u7684\u3002</p><p>\u5728\u6B27\u6D32\uFF0C\u4E00\u4E9B\u56FD\u5BB6\u65B0\u751F\u513F\u4F1A\u5E38\u89C4\u8FDB\u884C\u8D85\u58F0\u7B5B\u67E5DDH\uFF0C\u8D85\u58F0\u6CA1\u6709\u5C04\u7EBF\uFF0C\u8FD8\u53EF\u4EE5\u68C0\u6D4B\u5230X\u7EBF\u68C0\u6D4B\u4E0D\u5230\u7684\u8F6F\u9AA8\uFF0C\u8FD9\u662F\u5B83\u7684\u4F18\u52BF\uFF0C\u4F46\u5B83\u53D7\u68C0\u67E5\u533B\u751F\u7684\u7ECF\u9A8C\u5F71\u54CD\u6BD4\u8F83\u5927\uFF0C\u800C\u4E14\u5047\u9633\u6027\u6BD4\u8F83\u9AD8\uFF0C\u6709\u4E9B\u6B63\u5E38\u7684\u5B69\u5B50\u505A\u8D85\u58F0\u4E5F\u4F1A\u62A5\u544A\u5F02\u5E38\uFF0C\u5C24\u5176\u662F\u65B0\u751F\u513F\uFF0C\u7ED3\u679C\u5BFC\u81F4\u5BF9\u8FD9\u4E9B\u6B63\u5E38\u7684\u5B69\u5B50\u505A\u4E86\u4E0D\u5FC5\u8981\u7684\u6CBB\u7597\uFF0C\u8FD8\u6709\u7814\u7A76\u53D1\u73B0\u5BF9\u65B0\u751F\u513F\u6765\u8BF4\uFF0C\u8D85\u58F0\u5E76\u4E0D\u6BD4\u4F53\u683C\u68C0\u67E5\u66F4\u53EF\u9760\uFF0C\u6240\u4EE5\u7F8E\u56FD\u5E76\u4E0D\u63A8\u8350\u6240\u6709\u7684\u65B0\u751F\u513F\u5E38\u89C4\u505A\u8D85\u58F0\u7B5B\u67E5\uFF0CDDH\u9EC4\u79CD\u4EBA\u53D1\u75C5\u7387\u8FDC\u4F4E\u4E8E\u767D\u79CD\u4EBA\uFF0C\u6240\u4EE5\u66F4\u4E0D\u9700\u8981\u5E38\u89C4\u8D85\u58F0\u7B5B\u67E5\u3002</p><p>\u4F46\u540C\u6837\uFF0C\u4F53\u683C\u68C0\u67E5\u4E5F\u53D7\u5B69\u5B50\u7684\u72B6\u6001\u548C\u68C0\u67E5\u8005\u7684\u7ECF\u9A8C\u7684\u5F71\u54CD\uFF0C\u5982\u679C\u5B69\u5B50\u54ED\u95F9\u3001\u62B5\u6297\u4E5F\u53EF\u80FD\u5F71\u54CD\u68C0\u67E5\u7ED3\u679C\uFF0C\u68C0\u67E5\u8005\u7528\u529B\u8FC7\u5927\u6015\u635F\u4F24\u5B69\u5B50\uFF0C\u7528\u529B\u8FC7\u5C0F\u53C8\u53EF\u80FD\u68C0\u67E5\u4E0D\u51C6\uFF0C\u6240\u4EE5\u4E5F\u4F53\u68C0\u4E5F\u53EA\u80FD\u4F5C\u4E3A\u521D\u6B65\u7684\u7B5B\u67E5\uFF0C\u4E00\u65E6\u4F53\u683C\u68C0\u67E5\u6000\u7591\u6709\u95EE\u9898\uFF0C\u6216\u8005\u6709DDH\u7684\u9AD8\u5371\u56E0\u7D20\uFF0C\u6BD4\u5982\u81C0\u4F4D\u4EA7\uFF0C\u6709DDH\u5BB6\u65CF\u53F2, \u659C\u9888\u7684\u5B69\u5B50\u90A3\u5C31\u8FD8\u662F\u9700\u8981\u8D85\u58F0\u6216X\u7EBF\u6765\u8FDB\u4E00\u6B65\u6392\u67E5\u3002</p><p>DDH\u5982\u679C\u53D1\u73B0\u5F97\u65E9\uFF0C\u6CBB\u7597\u8D77\u6765\u8981\u7B80\u5355\u4E00\u4E9B\uFF0C\u5B69\u5B50\u53D7\u7684\u75DB\u82E6\u4E5F\u66F4\u5C0F\uFF0C\u6240\u4EE5\u6211\u8FD8\u662F\u53BB\u7ED9\u5973\u513F\u505A\u4E86\u8D85\u58F0\u68C0\u67E5\uFF0C\u5E86\u5E78\u7684\u662F\u6CA1\u6709\u95EE\u9898\u3002DDH\u4EE5\u524D\u53EB\u5148\u5929\u6027\u9ACB\u5173\u8282\u8131\u4F4D\uFF08CDH\uFF09\uFF0C\u540E\u6765\u53D1\u73B0\u8FD9\u4E2A\u75C5\u5E76\u4E0D\u662F\u5B8C\u5168\u7531\u5148\u5929\u56E0\u7D20\u9020\u6210\u7684\uFF0C\u6240\u4EE5\u6539\u6210\u4E86\u53D1\u80B2\u6027\u9ACB\u5173\u8282\u8131\u4F4D\uFF08DDH\uFF09\uFF0C\u8BF4\u660E\u751F\u540E\u7684\u56E0\u7D20\u4E5F\u8D77\u4E86\u5F88\u5927\u7684\u4F5C\u7528\uFF0C\u6BD4\u5982\u53CC\u4E0B\u80A2\u6346\u7ED1\u5E76\u7528\u8941\u8913\uFF0C\u5B69\u5B50\u7684\u4E0B\u80A2\u88AB\u957F\u671F\u56FA\u5B9A\u5728\u4E0D\u6070\u5F53\u7684\u4F53\u4F4D\uFF0C\u90FD\u4F1A\u589E\u52A0DDH\u7684\u53D1\u75C5\u98CE\u9669\uFF0C\u8FD9\u4E5F\u662F\u5317\u65B9\u5730\u533A\u53D1\u75C5\u8FDC\u9AD8\u4E8E\u5357\u65B9\u5730\u533A\u7684\u539F\u56E0\u3002</p><p>\u6240\u4EE5\u4E3A\u4FDD\u9669\u8D77\u89C1\uFF0C\u6211\u5631\u5490\u5BB6\u4EBA\u4E0D\u8981\u7528\u8941\u8913\u5305\u5B69\u5B50\uFF0C\u591A\u7528\u80CC\u5E26\u80CC\u5B69\u5B50\uFF0C\u8BA9\u5B69\u5B50\u7684\u4E0B\u80A2\u50CF\u9752\u86D9\u4E00\u6837\u5C55\u5F00\uFF0C\u8FD9\u6837\u80A1\u9AA8\u5934\u5C31\u53EF\u4EE5\u7D27\u8D34\u9ACB\u81FC\uFF0C\u4EE5\u964D\u4F4EDDH\u7684\u53D1\u751F\u7684\u98CE\u9669\uFF0C\u5B69\u5B50\u8D70\u8DEF\u4E4B\u540E\u4E5F\u6CA1\u6709\u53D1\u73B0\u4EFB\u4F55\u7684\u5F02\u5E38\uFF0C\u6240\u4EE5\u5C31\u5B8C\u5168\u4E0D\u7528\u62C5\u5FC3\u4E86\u3002</p><p>\u4F46\u5982\u679C\u4E00\u65E6\u5B69\u5B50\u8BCA\u65AD\u4E3ADDH\uFF0C\u5C31\u9700\u8981\u8FDB\u884C\u6CBB\u7597\uFF0C\u6CBB\u7597\u7684\u539F\u5219\u662F\u8BA9\u6775\u56FA\u5B9A\u5728\u81FC\u4E2D\uFF0C\u8BA9\u6775\u548C\u81FC\u4E92\u76F8\u523A\u6FC0\u53D1\u80B2\uFF0C\u6784\u6210\u4E00\u4E2A\u7A33\u5B9A\u7684\u5173\u8282\u3002</p><p>\u8D8A\u65E9\u6CBB\u7597\uFF0C\u6CBB\u7597\u65B9\u6CD5\u8D8A\u7B80\u5355\uFF0C\u6548\u679C\u4E5F\u597D\uFF0C\u6709\u7684\u53EA\u9700\u8981\u7528\u7279\u6B8A\u7684\u652F\u5177\u56FA\u5B9A\u4E0B\u80A2\uFF0C\u6709\u7684\u9700\u8981\u77F3\u818F\u56FA\u5B9A\u3002\u5982\u679CDDH\u6CA1\u6709\u53CA\u65F6\u53D1\u73B0\uFF0C\u7B49\u5B69\u5B50\u5F00\u59CB\u8D70\u8DEF\u4E4B\u540E\u5C31\u4F1A\u51FA\u73B0\u8DDB\u884C\uFF0C\u56E0\u4E3A\u53CC\u4E0B\u80A2\u4E0D\u7B49\u957F\u4E14\u627F\u53D7\u7684\u529B\u91CF\u4E0D\u5747\u5300\uFF0C\u8D70\u8DEF\u5C31\u50CF\u9E2D\u5B50\u4E00\u6837\u4E00\u6447\u4E00\u6446\uFF0C\u5173\u8282\u7684\u7578\u5F62\u4E5F\u4F1A\u56E0\u4E3A\u8D1F\u91CD\u8FDB\u4E00\u6B65\u52A0\u91CD\uFF0C\u8FD9\u4E2A\u65F6\u5019\u518D\u53BB\u6CBB\u7597\u96BE\u5EA6\u4F1A\u589E\u5927\u5F88\u591A\uFF0C\u6709\u4E9B\u5B69\u5B50\u8FD8\u8981\u627F\u53D7\u624B\u672F\u7684\u521B\u4F24\uFF0C\u4E25\u91CD\u7684\u8FD8\u9700\u8981\u622A\u9AA8\uFF0C\u98CE\u9669\u5927\u4E0D\u8BF4\uFF0C\u6548\u679C\u8FD8\u4E0D\u4E00\u5B9A\u597D\u3002</p><p>\u6240\u4EE5\uFF0C\u5B69\u5B50\u505A\u513F\u7AE5\u4FDD\u5065\uFF0C\u533B\u751F\u4E00\u822C\u90FD\u4F1A\u5E38\u89C4\u68C0\u67E5\u4E00\u4E0B\u9ACB\u5173\u8282\u3002\u4E00\u65E6\u6000\u7591\u5B69\u5B50\u6709DDH\uFF0C\u8981\u5C3D\u65E9\u627E\u513F\u7AE5\u9AA8\u79D1\u533B\u751F\u8BCA\u6CBB\u3002</p><hr><p>* \u672C\u6587\u5199\u4E8E2014\u5E74\uFF0C\u6536\u5165\u300A\u5728\u5B69\u5B50\u4E0B\u6B21\u751F\u75C5\u524D\u300B\u4E00\u4E66\uFF0C\u5404\u5927\u7535\u5546\u5E73\u53F0\u6709\u552E\u3002</p><p>* \u957F\u6309\u8BC6\u522B\u4E0B\u9762\u4E8C\u7EF4\u7801\u52A0\u5173\u6CE8\uFF0C\u53EF\u5411\u513F\u7AE5\u9AA8\u79D1\u533B\u751F<strong>\u7FC1\u5218\u5176\u3001\u738B\u5EFA\u80DC</strong>\u54A8\u8BE2DDH\u3001\u9A6C\u8E44\u8DB3\u3001\u591A\u6307\u3001\u9AA8\u6298\u7B49\u9AA8\u79D1\u95EE\u9898\u3002</p>',24),H={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626457&idx=1&sn=66c1ffdbff7e608430b7db60c4965d9b&chksm=8b5cb223bc2b3b35eb5ffb330df5993d0af75922923ac1917deca2bafa91db1faa922b950a23&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=o("\u67E5\u770B\u539F\u6587");function f(_,b){const p=a("ExternalLinkIcon");return D(),t(n,null,[d,e("p",null,[e("a",H,[l,r(p)])])],64)}var x=s(i,[["render",f]]);export{x as default};
