import{r as a,o as c,c as n,a as e,b as r,F as t,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=o('<h3 id="\u79D1\u666E\u8BFE\u7A0B-\u5982\u4F55\u8BA9\u5B69\u5B50\u597D\u597D\u5403\u996D-\u5403\u597D\u996D" tabindex="-1"><a class="header-anchor" href="#\u79D1\u666E\u8BFE\u7A0B-\u5982\u4F55\u8BA9\u5B69\u5B50\u597D\u597D\u5403\u996D-\u5403\u597D\u996D" aria-hidden="true">#</a> \u79D1\u666E\u8BFE\u7A0B|\u5982\u4F55\u8BA9\u5B69\u5B50\u597D\u597D\u5403\u996D\uFF0C\u5403\u597D\u996D\uFF1F</h3><p>\u6021\u79BE\u5065\u5EB7\u6210\u7ACB\u4EE5\u6765\uFF0C\u548C\u6240\u6709\u7684\u521B\u4E1A\u8005\u4E00\u6837\uFF0C\u6700\u8BA9\u6211\u4EEC\u7126\u8651\u662F\u5982\u4F55\u8BA9\u6021\u79BE\u6D3B\u4E0B\u53BB\u3002\u5728\u601D\u8003\u548C\u63A2\u7D22\u540E\uFF0C\u6211\u4EEC\u89C9\u5F97\uFF0C\u4F5C\u4E3A\u4E13\u4E1A\u4EBA\u5458\uFF0C\u6211\u4EEC\u7684\u4F18\u52BF\u5728\u6211\u4EEC\u7684\u4E13\u4E1A\u6027\u3002</p><p>\u7528\u6211\u4EEC\u7684\u4E13\u4E1A\u77E5\u8BC6\u53BB\u670D\u52A1\u597D\u7528\u6237\uFF0C\u5C31\u4F1A\u6709\u6211\u4EEC\u7684\u751F\u5B58\u7A7A\u95F4\uFF0C\u5C24\u5176\u5728\u4E13\u4E1A\u4E3B\u4E49\u7CBE\u795E\u532E\u4E4F\u7684\u56FD\u5185\uFF0C\u6240\u4EE5\u6211\u4EEC\u7684\u4E1A\u52A1\u90FD\u662F\u56F4\u7ED5\u6211\u4EEC\u7684\u4E13\u4E1A\u6765\u5F00\u5C55\u3002</p><p>\u6211\u4EEC\u7684\u7B2C\u4E00\u9879\u4E1A\u52A1\u662F\u5728\u7EBF\u54A8\u8BE2\uFF0C\u662F\u7531\u6211\u4E3B\u8981\u8D1F\u8D23\u3002\u5728\u957F\u671F\u7684\u79D1\u666E\u4E2D\uFF0C\u6211\u4EEC\u4E00\u76F4\u575A\u6301\u81EA\u5DF1\u7684\u4E13\u4E1A\u7406\u5FF5\uFF0C\u5DF2\u7ECF\u805A\u62E2\u4E86\u5F88\u591A\u548C\u6211\u4EEC\u7406\u5FF5\u76F8\u8FD1\u7684\u533B\u751F\u3002\u6211\u4EEC\u5236\u5B9A\u4E86\u7B26\u5408\u6211\u4EEC\u7406\u5FF5\u7684\u64CD\u4F5C\u89C4\u8303\uFF0C\u5E76\u5411\u5E73\u53F0\u4E0A\u7684\u533B\u751F\u704C\u8F93\u4F20\u64AD\uFF0C\u8BA9\u533B\u751F\u4EEC\u63D0\u4F9B\u66F4\u53EF\u9760\u3001\u66F4\u4F18\u8D28\u7684\u54A8\u8BE2\u670D\u52A1\u3002</p><p>\u8FD9\u9879\u4E1A\u52A1\u4E0A\u7EBF\u4E24\u4E2A\u6708\uFF0C\u54A8\u8BE2\u91CF\u5728\u8282\u8282\u6500\u5347\uFF0C\u867D\u7136\u8FD8\u9700\u8981\u7EE7\u7EED\u6253\u78E8\uFF0C\u4F46\u5DF2\u7ECF\u6536\u5230\u4E86\u5F88\u591A\u7528\u6237\u7684\u597D\u8BC4\uFF0C\u4E00\u4E9B\u5988\u5988\u5728\u4F7F\u7528\u540E\u81EA\u53D1\u5730\u5E2E\u6211\u4EEC\u505A\u5BA3\u4F20\u63A8\u5E7F\u3002</p><p>\u6211\u4EEC\u7684\u7B2C\u4E8C\u9879\u4E1A\u52A1\u5C31\u662F\u79D1\u666E\u8BFE\u7A0B\uFF0C\u662F\u7531\u97E9\u73CA\u73CA\u533B\u751F\u4E3B\u8981\u8D1F\u8D23\u3002\u97E9\u533B\u751F\u5728\u6FB3\u6D32\u5B8C\u6210\u4E86\u5B8C\u6574\u7684\u5987\u4EA7\u79D1\u548C\u4E34\u5E8A\u8425\u517B\u5B66\u8BAD\u7EC3\uFF0C\u5728\u6FB3\u6D32\u4E34\u5E8A\u4E00\u7EBF\u5DE5\u4F5C\uFF0C\u540C\u65F6\u4E5F\u662F\u591A\u5BB6SCI\u671F\u520A\u7684\u5BA1\u7A3F\u4EBA\uFF0C\u4E13\u4E1A\u80FD\u529B\u6BCB\u5EB8\u7F6E\u7591\u3002</p><p>\u540C\u65F6\u5979\u4E5F\u662F\u4E00\u4E2A\u5F88\u6709\u7ECF\u9A8C\u7684\u8BB2\u8005\uFF0C\u5728\u6FB3\u6D32\u53CA\u65B0\u897F\u5170\u4E3A\u4E34\u5E8A\u533B\u751F\u548C\u52A9\u4EA7\u58EB\u6388\u8FC7\u8BFE\uFF0C\u7EC4\u7EC7\u8FC7\u6559\u5B66\u6D3B\u52A8\uFF0C\u4E5F\u53D7\u9080\u4E3A\u963F\u5FB7\u83B1\u5FB7\u5927\u5B66\u533B\u5B66\u9662\u7684\u533B\u5B66\u751F\u8BB2\u8FC7\u8BFE\u3002\u6B64\u5916\uFF0C\u5979\u4E5F\u4E3A\u5F53\u5730\u5988\u5988\u4EEC\u63D0\u4F9B\u8FC7\u5404\u7C7B\u79D1\u666E\u4E92\u52A8\u5C0F\u8BFE\u3002</p><p>7\u6708\u4EFD\u97E9\u533B\u751F\u56DE\u5230\u56FD\u5185\uFF0C\u505A\u4E86\u4E00\u6B21\u5173\u4E8E\u5B69\u5B50\u5403\u996D\u95EE\u9898\u7684\u7EBF\u4E0B\u8BB2\u5EA7\uFF0C\u6211\u8DDF\u7740\u4E00\u8D77\u542C\u8BFE\u3002\u5979\u8BB2\u8BFE\u7684\u6D41\u7545\u5EA6\uFF0C\u73B0\u573A\u638C\u63A7\u80FD\u529B\u53CA\u4E92\u52A8\u5E94\u53D8\u80FD\u529B\u5BF9\u6211\u6765\u8BF4\u662F\u671B\u5C18\u83AB\u53CA\uFF08\u4E5F\u53EF\u80FD\u6211\u81EA\u5DF1\u4E0D\u5584\u8BB2\u5427\uFF09\u3002\u56DB\u5341\u5206\u949F\u7684\u8BB2\u8BFE\uFF0C\u518D\u52A0\u4E0A\u8BFE\u540E\u7684\u9010\u4E2A\u7B54\u7591\uFF0C\u6574\u4E2A\u6D3B\u52A8\u6301\u7EED\u4E86\u5C06\u8FD13\u5C0F\u65F6\u3002\u867D\u7136\u662F\u63A5\u8FD1200\u5143\u7684\u6536\u8D39\u8BFE\u7A0B\uFF0C\u4F46\u6211\u4EEC\u8FD8\u662F\u6536\u5230\u4E86\u975E\u5E38\u6B63\u9762\u7684\u53CD\u9988\u3002</p><p>\u56E0\u4E3A\u5730\u7406\u548C\u65F6\u95F4\u7684\u9650\u5236\uFF0C\u6211\u4EEC\u51C6\u5907\u7B2C\u4E8C\u6B21\u7EBF\u4E0B\u8BFE\u7A0B\u7684\u65F6\u5019\uFF0C\u6536\u5230\u4E86\u5F88\u591A\u547C\u58F0\u8981\u6C42\u6539\u4E3A\u7EBF\u4E0A\u8BFE\u7A0B\uFF0C\u6240\u4EE5\u5C31\u6709\u4E86\u4E0A\u4E2A\u6708\u7684\u7B2C\u4E00\u6B21\u5173\u4E8E\u5B69\u5B50\u7761\u7720\u95EE\u9898\u7684\u7EBF\u4E0A\u76F4\u64AD\u8BFE\u7A0B\uFF0C\u6536\u8D3930\u5143\u7684\u8BFE\u7A0B\uFF0C\u5230\u8BFE\u7A0B\u7ED3\u675F\u7684\u65F6\u5019\u5DF2\u7ECF\u6709\u4E00\u5343\u591A\u4EBA\u8FDB\u6765\u542C\u8BFE\u4E86\u3002</p><p>\u539F\u672C\u9884\u8BA130\u5206\u949F\u7684\u8BFE\u7A0B\uFF0C\u6700\u540E\u8BB2\u4E86\u5C06\u8FD1\u4E00\u5C0F\u65F6\uFF0C\u8BFE\u540E\u9009\u62E9\u4E86\u4E00\u4E9B\u95EE\u9898\u8FDB\u884C\u7B54\u7591\uFF0C\u6700\u540E\u8FD8\u5EFA\u4E86\u4E92\u52A8\u4EA4\u6D41\u7FA4\u3002\u8BFE\u7A0B\u7ED3\u675F\u540E\uFF0C\u5728\u4E92\u52A8\u4EA4\u6D41\u7FA4\u53CA\u76F4\u64AD\u95F4\u91CC\u9646\u7EED\u6536\u5230\u5988\u5988\u4EEC\u7684\u6B63\u9762\u53CD\u9988\uFF0C\u6709\u7684\u8BF4\u81EA\u5DF1\u7ED3\u5408\u8BFE\u7A0B\u5185\u5BB9\u5B9E\u8DF5\uFF0C\u5B69\u5B50\u7684\u7761\u7720\u7EC8\u4E8E\u5F97\u5230\u4E86\u6539\u5584\u3002\u770B\u5230\u81EA\u5DF1\u5206\u4EAB\u7684\u77E5\u8BC6\u771F\u5207\u7684\u5E2E\u52A9\u5230\u4E86\u522B\u4EBA\uFF0C\u6211\u4EEC\u4E5F\u5F88\u5F00\u5FC3\u3002\u540C\u65F6\u6211\u4EEC\u4E5F\u6536\u5230\u4E86\u6570\u4E2A\u8D1F\u9762\u7684\u53CD\u9988\uFF0C\u5305\u62EC\u77E5\u8BC6\u6CA1\u65B0\u610F\uFF0C\u81EA\u5DF1\u5728\u7F51\u4E0A\u90FD\u80FD\u641C\u5230\uFF0C\u64CD\u4F5C\u6027\u4E0D\u591F\u5F3A\uFF0C\u4E0D\u80FD\u89E3\u51B3\u81EA\u5DF1\u7684\u95EE\u9898\u7B49\u3002</p><p>\u4ECE\u9009\u9898\u5230\u8BFE\u4EF6\u7684\u51C6\u5907\uFF0C\u5230\u8BB2\u8BFE\uFF0C\u6211\u4E5F\u6709\u53C2\u4E0E\uFF0C\u6211\u4EEC\u81EA\u8BA4\u4E3A\u90FD\u662F\u8BDA\u5FC3\u8BDA\u610F\u53BB\u505A\u7684\uFF0C\u6240\u4EE5\u8FD9\u4E9B\u8D1F\u9762\u53CD\u9988\u8BA9\u6211\u4EEC\u5F88\u6709\u632B\u8D25\u611F\uFF0C\u540C\u65F6\u6211\u4EEC\u4E5F\u5728\u5206\u6790\u548C\u601D\u8003\u3002\u7EBF\u4E0A\u7684\u4E92\u52A8\u6027\u4E0D\u5982\u7EBF\u4E0B\u597D\u662F\u4E00\u65B9\u9762\uFF0C\u53E6\u4E00\u65B9\u9762\uFF0C\u4E00\u5BF9\u591A\u7684\u8BB2\u5EA7\uFF0C\u5C24\u5176\u662F\u7EBF\u4E0A\u9762\u4E34\u4E0A\u5343\u4E2A\u542C\u4F17\uFF0C\u867D\u7136\u6548\u7387\u66F4\u9AD8\uFF0C\u4F46\u4F53\u9A8C\u4E0D\u5982\u4E00\u5BF9\u4E00\u7684\u54A8\u8BE2\u6216\u8005\u7EBF\u4E0B\u8BB2\u5EA7\u4E5F\u662F\u5FC5\u7136\u7684\u3002</p><p>\u4E00\u5BF9\u4E00\u7684\u54A8\u8BE2\uFF0C\u4F60\u63D0\u51FA\u4E2D\u5B66\u6C34\u5E73\u7684\u95EE\u9898\uFF0C\u533B\u751F\u53EF\u4EE5\u9488\u5BF9\u6027\u56DE\u7B54\uFF0C\u63D0\u51FA\u5927\u5B66\u6C34\u5E73\u7684\u95EE\u9898\uFF0C\u533B\u751F\u4F9D\u7136\u53EF\u4EE5\u9488\u5BF9\u6027\u56DE\u7B54\uFF0C\u53EA\u9700\u8981\u638C\u63A7\u597D\u4E86\u533B\u751F\u7684\u56DE\u590D\u8D28\u91CF\uFF0C\u5C31\u53EF\u4EE5\u5F97\u5230\u6BD4\u8F83\u9AD8\u7684\u6EE1\u610F\u5EA6\u3002\u7EBF\u4E0B\u51E0\u5341\u4EBA\u89C4\u6A21\u7684\u8BB2\u5EA7\uFF0C\u57FA\u672C\u4E0A\u4E5F\u53EF\u4EE5\u4FDD\u8BC1\u6BCF\u4E2A\u4EBA\u7684\u7591\u95EE\u90FD\u5F97\u5230\u9762\u5BF9\u9762\u7684\u54CD\u5E94\u3002</p><p>\u4F46\u7EBF\u4E0A\u8BFE\u7A0B\uFF0C\u540C\u65F6\u9762\u5BF9\u4E0A\u5343\u4EBA\uFF0C\u800C\u4E14\u8FD9\u4E9B\u542C\u4F17\u5BF9\u8FD9\u4E2A\u8BFE\u7A0B\u4E3B\u9898\u7684\u4E86\u89E3\u7A0B\u5EA6\u4E0D\u4E00\u6837\uFF0C\u6709\u7684\u5728\u5C0F\u5B66\u6C34\u5E73\u3001\u6709\u7684\u5728\u4E2D\u5B66\u6C34\u5E73\uFF0C\u6709\u7684\u5728\u5927\u5B66\u6C34\u5E73\u3002\u800C\u8BFE\u5802\u5185\u5BB9\u53EA\u80FD\u9488\u5BF9\u4E3B\u6D41\u5B66\u751F\u7684\u666E\u904D\u6027\u95EE\u9898\u4F5C\u8BB2\u89E3\uFF0C\u8981\u5339\u914D\u6240\u6709\u5B66\u751F\u7684\u9700\u6C42\u662F\u4E0D\u73B0\u5B9E\u7684\u3002</p><p>\u6BD4\u5982\u5BF9\u4E8E\u7761\u7720\u95EE\u9898\uFF0C\u6709\u7684\u662F\u51C6\u5988\u5988\uFF0C\u5BF9\u5B69\u5B50\u7761\u7720\u95EE\u9898\u8FD8\u6CA1\u6709\u6982\u5FF5\uFF0C\u60F3\u5148\u50A8\u5907\u4E00\u70B9\u77E5\u8BC6\uFF1B\u6709\u7684\u662F\u65B0\u624B\u5988\u5988\uFF0C\u60F3\u73B0\u5B66\u73B0\u7528\uFF1B\u6709\u7684\u662F\u5B69\u5B50\u5B58\u5728\u7761\u7720\u95EE\u9898\uFF0C\u5DF2\u7ECF\u5728\u7F51\u4E0A\u5230\u5904\u6536\u96C6\u67E5\u770B\u8FC7\u76F8\u5173\u77E5\u8BC6\u7684\u5988\u5988\uFF0C\u4E00\u5802\u8981\u540C\u65F6\u6EE1\u8DB3\u8FD9\u4E9B\u4E0D\u540C\u9700\u6C42\u7684\u542C\u4F17\u80AF\u5B9A\u4F1A\u6709\u56F0\u96BE\u3002</p><p>\u81F3\u4E8E\u8BF4\u77E5\u8BC6\u6709\u6CA1\u6709\u65B0\u610F\uFF0C\u90FD\u662F\u53EF\u4EE5\u7F51\u4E0A\u627E\u5F97\u5230\u7684\uFF0C\u6709\u8FD9\u79CD\u611F\u89C9\u7684\u542C\u4F17\u53EA\u80FD\u8BF4\u662F\u5DF2\u7ECF\u5BF9\u8FD9\u4E2A\u4E3B\u9898\u8FDB\u884C\u6DF1\u5165\u5B66\u4E60\u8FC7\u7684\uFF0C\u540C\u65F6\u5BF9\u533B\u5B66\u6CA1\u6709\u6B63\u786E\u7684\u8BA4\u8BC6\u3002\u5176\u5B9E\u5BF9\u4E8E\u4EFB\u4F55\u4E00\u4E2A\u533B\u5B66\u95EE\u9898\uFF0C\u5982\u679C\u9075\u7167\u5FAA\u8BC1\u533B\u5B66\u7684\u7406\u5FF5\uFF0C\u6240\u6709\u7684\u7B54\u6848\u90FD\u4F1A\u8D8B\u4E8E\u4E00\u81F4\uFF0C\u800C\u4E14\u53EA\u8981\u4F60\u6709\u8DB3\u591F\u7684\u68C0\u7D22\u80FD\u529B\uFF0C\u76F8\u5173\u77E5\u8BC6\u90FD\u662F\u53EF\u4EE5\u5728\u7F51\u4E0A\u627E\u5230\u7684\u3002\u5982\u679C\u4E00\u4E2A\u533B\u751F\u63D0\u51FA\u4E00\u4E2A\u72EC\u521B\u6027\u7684\u89C2\u70B9\u548C\u65B9\u6CD5\uFF0C\u6CA1\u6709\u7ECF\u8FC7\u5176\u5B83\u533B\u751F\u91CD\u590D\u5E76\u8BA4\u540C\uFF0C\u90A3\u53CD\u800C\u662F\u53EF\u4FE1\u5EA6\u4F4E\u7684\u3002</p><p>\u542C\u5177\u6709\u5B9E\u9645\u64CD\u4F5C\u7ECF\u9A8C\u7684\u533B\u751F\u8BB2\u8BFE\uFF0C\u4F18\u52BF\u5728\u4E8E\u5979/\u4ED6\u80FD\u628A\u8FD9\u4E2A\u4E3B\u9898\u76F8\u5173\u7684\u6700\u5173\u952E\u4FE1\u606F\u8FDB\u884C\u7CFB\u7EDF\u8BB2\u89E3\uFF0C\u800C\u4E14\u8BB2\u89E3\u7684\u77E5\u8BC6\u70B9\u4EB2\u81EA\u6838\u67E5\u8FC7\u7684\uFF0C\u8FD9\u6837\u53EF\u4EE5\u907F\u514D\u81EA\u5DF1\u4E0A\u7F51\u76F2\u76EE\u641C\u7D22\u7684\u8FC7\u5EA6\u9605\u8BFB\uFF0C\u4EE5\u53CA\u907F\u514D\u4E00\u4E9B\u9519\u8BEF\u4FE1\u606F\u7684\u5E72\u6270\u3002</p><p>\u5982\u679C\u6709\u65B0\u7684\u77E5\u8BC6\u70B9\uFF0C\u90A3\u662F\u67E5\u7F3A\u8865\u6F0F\uFF0C\u662F\u6536\u83B7\uFF0C\u5982\u679C\u81EA\u5DF1\u4E86\u89E3\u8FC7\u7684\u5185\u5BB9\u88AB\u8BB2\u5230\u4E86\uFF0C\u5F97\u5230\u786E\u8BA4\uFF0C\u8FD9\u540C\u6837\u662F\u6536\u83B7\uFF0C\u8FD9\u5C31\u662F\u6E29\u6545\u77E5\u65B0\u5427\u3002\u81F3\u4E8E\u8FD9\u4E9B\u6536\u83B7\u662F\u5426\u503C\u8FD9\u4E2A\u4EF7\u683C\uFF0C\u90A3\u771F\u7684\u662F\u5F88\u96BE\u53BB\u8861\u91CF\uFF0C\u56E0\u4E3A\u6BCF\u4E2A\u4EBA\u5BF9\u77E5\u8BC6\u4EF7\u503C\u7684\u5224\u65AD\u6807\u51C6\u4F1A\u6709\u5F88\u5927\u7684\u5DEE\u5F02\u3002</p><p>\u8BA4\u4E3A\u8BFE\u7A0B\u6CA1\u6709\u5B9E\u64CD\u6027\uFF0C\u4E0D\u80FD\u89E3\u51B3\u81EA\u5DF1\u7684\u95EE\u9898\u7684\uFF0C\u53EF\u80FD\u662F\u5BF9\u8BFE\u7A0B\u62B1\u6709\u5F88\u9AD8\u7684\u671F\u671B\u3002\u8BFE\u7A0B\u80FD\u8BB2\u7684\u771F\u7684\u53EA\u80FD\u662F\u4E00\u4E9B\u666E\u904D\u6027\u7684\u64CD\u4F5C\u539F\u5219\uFF0C\u7136\u540E\u81EA\u5DF1\u53BB\u6478\u7D22\u5B9E\u8DF5\uFF0C\u4E5F\u53EF\u4EE5\u53C2\u52A0\u76F8\u5173\u4E3B\u9898\u7684\u4E92\u52A9\u4EA4\u6D41\u7FA4\u3002\u5982\u679C\u8981\u5BF9\u6BCF\u4E00\u4E2A\u542C\u4F17\u5177\u4F53\u7684\u60C5\u51B5\u8FDB\u884C\u8BB2\u89E3\u5206\u6790\u6307\u5BFC\uFF0C\u90A3\u4F30\u8BA1\u51E0\u5929\u51E0\u591C\u591C\u8BB2\u4E0D\u5B8C\uFF0C\u4E5F\u4E0D\u662F\u82B130\u5757\u94B1\u80FD\u4E70\u5230\u7684\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u542C\u5B8C\u7761\u7720\u7684\u8BFE\u7A0B\uFF0C\u5F88\u591A\u5BB6\u957F\u5728\u540E\u6765\u7684\u5B9E\u8DF5\u8FD0\u7528\u4E2D\uFF0C\u53D1\u73B0\u8FD9\u4E9B\u77E5\u8BC6\u8FD8\u662F\u8D77\u4E86\u4F5C\u7528\uFF0C\u5305\u62EC\u4E00\u4E9B\u521A\u542C\u5B8C\u8BFE\u89C9\u5F97\u6CA1\u6536\u83B7\u7684\u5BB6\u957F\u3002</p><p>\u540C\u65F6\uFF0C\u6211\u4EEC\u4E5F\u975E\u5E38\u611F\u8C22\u8FD9\u4E9B\u8D1F\u9762\u7684\u53CD\u9988\uFF0C\u8BA9\u6211\u4EEC\u77E5\u9053\u66F4\u591A\u7528\u6237\u7684\u8BC9\u6C42\uFF0C\u6211\u4EEC\u4E5F\u4F1A\u5C1D\u8BD5\u6539\u8FDB\uFF0C\u6BD4\u5982\u9002\u5F53\u5EF6\u957F\u8BFE\u7A0B\u7684\u65F6\u95F4\uFF0C\u589E\u52A0\u4E00\u4E9B\u4E92\u52A8\u548C\u8BA8\u8BBA\uFF0C\u8BFE\u540E\u5EFA\u7ACB\u4E3B\u9898\u79D1\u666E\u7FA4\uFF0C\u4E3A\u5BB6\u957F\u63D0\u4F9B\u4E00\u4E2A\u4F18\u8D28\u800C\u6709\u9488\u5BF9\u6027\u7684\u4E92\u52A9\u8BA8\u8BBA\u5E73\u53F0\u3002\u540C\u65F6\uFF0C\u5728\u80FD\u529B\u8303\u56F4\u5185\uFF0C\u542C\u53D6\u66F4\u591A\u53CD\u9988\u548C\u5EFA\u8BAE\uFF0C\u4E89\u53D6\u5728\u4E00\u6B21\u6B21\u7684\u5B9E\u8DF5\u6478\u7D22\u4E2D\uFF0C\u6253\u9020\u6807\u51C6\u5316\u7684\u79D1\u666E\u8BFE\u7A0B\u3002\u6211\u4EEC\u7684\u79D1\u666E\u8BFE\u7A0B\u7684\u76EE\u6807\u662F\u63D0\u4F9B\u6709\u636E\u53EF\u5FAA\u7684\u517B\u80B2\u6307\u5BFC\uFF08Evidence-based parenting\uFF09\u3002</p><p>\u4E5F\u8BB8\uFF0C\u5F88\u591A\u5BB6\u957F\u89C9\u5F97\u75BE\u75C5\u6CBB\u7597\u624D\u6709\u5FAA\u8BC1\u533B\u5B66\u8FD9\u4E2A\u6982\u5FF5\uFF0C\u5B69\u5B50\u7684\u5403\u559D\u62C9\u6492\u7761\u5B8C\u5168\u51ED\u7ECF\u9A8C\u5C31\u53EF\u4EE5\u3002\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u4E0E\u5065\u5EB7\u76F8\u5173\u7684\u4E3B\u9898\uFF0C\u53EA\u8981\u6709\u76F8\u5173\u7814\u7A76\u8BC1\u636E\uFF0C\u9075\u5FAA\u6700\u4F73\u8BC1\u636E\uFF0C\u540C\u6837\u80FD\u8BA9\u5B69\u5B50\u5F97\u5230\u66F4\u597D\u7684\u517B\u80B2\u548C\u5475\u62A4\u3002\u6021\u79BE\u7684\u79D1\u666E\u8BFE\u7A0B\uFF0C\u4E5F\u4F1A\u4EE5\u5FAA\u8BC1\u4E3A\u57FA\u77F3\u3002</p><p>\u4E0A\u4E00\u6B21\u6211\u4EEC\u5173\u4E8E<strong>0-3\u5C81\u5B69\u5B50\u5403\u7684\u95EE\u9898</strong>\u7684\u7EBF\u4E0B\u8BFE\u7A0B\u7ED3\u675F\u540E\uFF0C\u5FAE\u535A\u4E0A\u5F88\u591A\u5916\u5730\u7684\u5BB6\u957F\u7559\u8A00\u5E0C\u671B\u80FD\u5728\u7EBF\u4E0A\u8BB2\u4E00\u6B21\uFF0C\u6240\u4EE5\u8FD9\u6B21\uFF0C\u6211\u4EEC\u5C31\u5148\u5C06\u5B69\u5B50\u5403\u996D\u95EE\u9898\u7684\u8BFE\u7A0B\u5728\u7EBF\u4E0A\u518D\u8BB2\u4E00\u6B21\u3002\u5173\u4E8E\u5B69\u5B50\u5403\u7684\u8BDD\u9898\uFF0C\u4E3B\u8981\u7684\u5173\u952E\u6027\u77E5\u8BC6\u70B9\u548C\u64CD\u4F5C\u5EFA\u8BAE\uFF0C\u57FA\u672C\u4F1A\u5728\u8FD9\u6B21\u8BFE\u7A0B\u4E2D\u6DB5\u76D6\u3002</p><p>\u7EBF\u4E0A\u8BFE\u7A0B\u4F1A\u6BD4\u7EBF\u4E0B\u8BFE\u7A0B\u4FBF\u5B9C\u5F88\u591A\uFF0C\u4F46\u662F\u4E5F\u540C\u6837\u8981\u63D0\u9192\u5927\u5BB6\uFF0C\u7EBF\u4E0A\u542C\u4F17\u592A\u591A\uFF0C\u4E0D\u80FD\u4FDD\u8BC1\u6240\u6709\u5BB6\u957F\u7684\u63D0\u95EE\u90FD\u5F97\u5230\u7B54\u590D\uFF0C\u6240\u8BB2\u5185\u5BB9\u53EF\u80FD\u4E0D\u662F\u81EA\u5DF1\u6240\u671F\u671B\u7684\u3002\u4F46\u6709\u5174\u8DA3\u7684\u5BB6\u957F\uFF0C\u53EF\u4EE5\u53C2\u7167\u4E0B\u9762\u6D77\u62A5\u63D0\u793A\uFF0C\u5E76<strong>\u957F\u6309\u6D77\u62A5\u8BC6\u522B\u4E8C\u7EF4\u7801****\u5173\u6CE8</strong>\u53C2\u4E0E\uFF0C\u4E5F\u6B22\u8FCE\u8F6C\u53D1\u7ED9\u6709\u9700\u8981\u7684\u5BB6\u957F\u3002</p>',22),f={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626421&idx=1&sn=20374de2f1255e290adb6b83ae25cb47&chksm=8b5cb24fbc2b3b59b8c8a8f378ec1f2ca29fac46d2a56ce23270d8c1f26fc84caf2c61e4313a&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=s("\u67E5\u770B\u539F\u6587");function h(b,m){const p=a("ExternalLinkIcon");return c(),n(t,null,[_,e("p",null,[e("a",f,[l,r(p)])])],64)}var u=d(i,[["render",h]]);export{u as default};
