import{r as a,o as r,c as t,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const d={},_=c('<h3 id="\u73B0\u5728\u8FD8\u80FD\u4E0D\u80FD\u5B66\u533B" tabindex="-1"><a class="header-anchor" href="#\u73B0\u5728\u8FD8\u80FD\u4E0D\u80FD\u5B66\u533B" aria-hidden="true">#</a> \u73B0\u5728\u8FD8\u80FD\u4E0D\u80FD\u5B66\u533B\uFF1F</h3><p>\u4ECA\u5E74\u9AD8\u8003\u5DF2\u7ECF\u7ED3\u675F\u4E86\uFF0C\u5B66\u5B50\u4EEC\u653E\u677E\u4E00\u9635\u540E\uFF0C\u5F88\u5FEB\u5C31\u8981\u8003\u8651\u586B\u62A5\u4E13\u4E1A\u5FD7\u613F\u4E86\u3002</p><p>\u533B\u751F\u5728\u5F88\u591A\u4EBA\u773C\u91CC\u8FD8\u7B97\u4E00\u4E2A\u4E0D\u9519\u7684\u804C\u4E1A\uFF0C\u6709\u4E0D\u9519\u7684\u6536\u5165\uFF0C\u6709\u4E00\u5B9A\u7684\u793E\u4F1A\u5730\u4F4D\uFF0C\u5DE5\u4F5C\u4E5F\u5F88\u7A33\u5B9A\uFF0C\u8FD8\u662F\u5C11\u6570\u51E0\u4E2A\u8FD8\u6709\u7F16\u5236\u80FD\u5403\u201C\u516C\u7CAE\u201D\u7684\u5DE5\u4F5C\uFF0C\u6240\u4EE5\u5F88\u591A\u7236\u6BCD\u4E5F\u671F\u671B\u81EA\u5DF1\u5B69\u5B50\u53BB\u5B66\u533B\u3002\u4F46\u73B0\u5728\u5927\u5BB6\u4E5F\u90FD\u77E5\u9053\u533B\u60A3\u5173\u7CFB\u7D27\u5F20\uFF0C\u533B\u751F\u4E5F\u4E0D\u597D\u5F53\uFF0C\u65F6\u5E38\u8FD8\u6709\u533B\u751F\u88AB\u6253\u751A\u81F3\u88AB\u6740\u7684\u4E8B\u53D1\u751F\u3002</p><p>\u73B0\u5728\u5F53\u533B\u751F\u597D\u4E0D\u597D\uFF1F\u8981\u4E0D\u8981\u5B66\u533B\uFF1F\u4E0D\u540C\u7684\u4EBA\u53EF\u80FD\u4F1A\u6709\u4E0D\u540C\u7684\u770B\u6CD5\u3002\u4F5C\u4E3A\u4E00\u4E2A\u5B66\u8FC7\u533B\uFF0C\u5728\u516C\u7ACB\u533B\u9662\u5F53\u8FC7\u5341\u51E0\u5E74\u533B\u751F\uFF0C\u73B0\u5728\u51FA\u6765\u533B\u7597\u521B\u4E1A\u7684\u8FC7\u6765\u4EBA\uFF0C\u5206\u4EAB\u4E00\u4E0B\u6211\u7684\u7ECF\u5386\u548C\u770B\u6CD5\uFF0C\u53EF\u80FD\u5BF9\u5B66\u5B50\u548C\u5BB6\u957F\u4EEC\u6709\u4E9B\u53C2\u8003\u610F\u4E49\u3002</p><p>\u6211\u53C2\u52A0\u9AD8\u8003\u90A3\u4F1A\uFF0C\u4E92\u8054\u7F51\u8FD8\u672A\u666E\u53CA\uFF0C\u6211\u4EEC\u4E5F\u6CA1\u6709\u7535\u8111\u548C\u624B\u673A\uFF0C\u4E0D\u80FD\u50CF\u73B0\u5728\u7684\u8003\u751F\u90A3\u6837\u53EF\u4EE5\u8F7B\u6613\u7684\u83B7\u5F97\u5404\u79CD\u8D44\u8BAF\uFF0C\u4E86\u89E3\u5404\u4E2A\u884C\u4E1A\u7684\u53D1\u5C55\u60C5\u51B5\u3002</p><p>\u90A3\u65F6\u7684\u6211\u4E5F\u5E74\u5E7C\u65E0\u77E5\uFF0C\u5BF9\u5404\u4E2A\u804C\u4E1A\u7684\u7406\u89E3\u4E5F\u662F\u5F88\u80A4\u6D45\uFF0C\u6839\u672C\u4E0D\u77E5\u9053\u54EA\u4E2A\u804C\u4E1A\u6709\u4EC0\u4E48\u597D\u574F\uFF0C\u81EA\u5DF1\u4E5F\u6CA1\u6709\u7279\u522B\u60F3\u4ECE\u4E8B\u7684\u884C\u4E1A\uFF0C\u5FD7\u613F\u9009\u62E9\u5C31\u7D22\u6027\u4EA4\u7ED9\u7236\u4EB2\u548C\u8001\u5E08\u53BB\u51B3\u5B9A\u3002\u7B2C\u4E00\u5FD7\u613F\u62A5\u7684\u662F\u5DE5\u79D1\uFF0C\u4F46\u6CA1\u8003\u4E0A\uFF0C\u88AB\u7B2C\u4E8C\u5FD7\u613F\u7684\u533B\u5B66\u9662\u5F55\u53D6\u4E86\uFF0C\u6240\u4EE5\u5C31\u8FD9\u4E48\u7A00\u91CC\u7CCA\u6D82\u53BB\u5B66\u4E86\u533B\u3002</p><p>\u5B66\u533B\u548C\u5176\u5B83\u5927\u591A\u6570\u4E13\u4E1A\u4E0D\u540C\u7684\u662F\u672C\u79D1\u8981\u8BFB5\u5E74\uFF0C\u662F\u4E0D\u662F\u6BD4\u5176\u5B83\u4E13\u4E1A\u66F4\u8F9B\u82E6\u6211\u4E0D\u6E05\u695A\uFF0C\u4F46\u8BFE\u7A0B\u597D\u50CF\u6BD4\u5B66\u5176\u5B83\u4E13\u4E1A\u7684\u9AD8\u4E2D\u540C\u5B66\u8981\u591A\u4E00\u4E9B\u3002\u5B66\u533B\u4E4B\u524D\u4F60\u8FD8\u5F97\u8003\u8651\u4E00\u70B9\uFF0C\u5B66\u5B8C5\u5E74\u672C\u79D1\u627E\u4EFD\u5DE5\u4F5C\u5E76\u4E0D\u96BE\uFF0C\u4F46\u8FDB\u5927\u4E00\u70B9\u533B\u9662\u5F88\u96BE\uFF0C\u9664\u975E\u4F60\u518D\u53BB\u8BFB\u4E2A\u7855\u58EB\u6216\u8005\u535A\u58EB\uFF0C\u6240\u4EE5\u610F\u5473\u7740\u4F60\u8981\u8BFB\u4E2A7-12\u5E74\u3002</p><p>\u533B\u9662\u662F\u4E00\u4E2A\u5F88\u8BB2\u7A76\u8BBA\u8D44\u6392\u8F88\u7684\u5730\u65B9\uFF0C\u56FD\u5185\u7684\u4F4F\u9662\u533B\u751F\u57F9\u8BAD\u4E5F\u4E0D\u592A\u89C4\u8303\uFF0C\u57FA\u672C\u4E0A\u662F\u628A\u4F4F\u9662\u533B\u751F\u5F53\u5EC9\u4EF7\u7684\u52B3\u52A8\u529B\u4F7F\u7528\uFF0C\u52A8\u624B\u673A\u4F1A\u5F88\u5C11\u3002\u6240\u4EE5\u5F53\u533B\u751F\u4E4B\u524D\u8981\u6709\u5FC3\u7406\u51C6\u5907\uFF0C\u4F60\u60F3\u8C61\u4E2D\u5F53\u533B\u751F\u662F\u770B\u75C5\u67E5\u623F\u505A\u624B\u672F\uFF0C\u4F46\u5B9E\u9645\u4E0A\u5DE5\u4F5C\u7684\u524D\u51E0\u5E74\uFF0C\u4F60\u4E3B\u8981\u505A\u7684\u662F\u5199\u75C5\u5386\u3001\u5F00\u533B\u5631\uFF0C\u505A\u624B\u672F\u4E5F\u662F\u62C9\u62C9\u52FE\u3001\u6276\u6276\u955C\u5B50\u3002\u800C\u89C4\u57F9\u671F\u95F4\u7684\u5DE5\u8D44\u5F88\u4F4E\uFF0C\u5C11\u7684\u4E00\u4E24\u5343\uFF0C\u591A\u7684\u4E94\u516D\u5343\u3002</p><p>\u5F53\u7136\u6162\u6162\u71AC\uFF0C\u673A\u4F1A\u603B\u4F1A\u6709\u7684\uFF0C\u71AC\u5230\u4E86\u4E00\u5B9A\u5E74\u9650\uFF0C\u603B\u4F1A\u6709\u51FA\u5934\u7684\u65F6\u5019\uFF0C\u72EC\u7ACB\u770B\u75C5\u3001\u505A\u624B\u672F\u7684\u673A\u4F1A\u4E5F\u4F1A\u8D8A\u6765\u8D8A\u591A\uFF0C\u6536\u5165\u4E5F\u4F1A\u9010\u6E10\u589E\u591A\u3002\u4F46\u603B\u4F53\u800C\u8A00\uFF0C\u533B\u751F\u7684\u6536\u5165\u5728\u793E\u4F1A\u6574\u4F53\u4E2D\u5927\u6982\u5C11\u662F\u4E2D\u7B49\u504F\u4E0A\u7684\u5427\uFF0C\u6211\u4E2A\u4EBA\u89C9\u5F97\u8FD9\u4E2A\u804C\u4E1A\u7684\u56DE\u62A5\u548C\u4ED8\u51FA\u662F\u4E0D\u6210\u6B63\u6BD4\u7684\u3002</p><p>\u53EF\u80FD\u662F\u548C\u751F\u4E8E\u6B7B\u6253\u4EA4\u9053\u6BD4\u8F83\u591A\uFF0C\u533B\u751F\u8FD9\u4E2A\u804C\u4E1A\u4E5F\u6709\u4E00\u4E9B\u5176\u5B83\u804C\u4E1A\u5F88\u96BE\u6709\u7684\u6210\u5C31\u611F\u3002\u5BF9\u6211\u4EEC\u65B0\u751F\u513F\u5916\u79D1\u533B\u751F\u6765\u8BF4\uFF0C\u4E00\u4E9B\u6D88\u5316\u9053\u7A7F\u5B54\u7684\u5B69\u5B50\uFF0C\u9001\u5230\u533B\u9662\u7684\u65F6\u5019\u5944\u5944\u4E00\u606F\uFF0C\u4F46\u7ECF\u8FC7\u6211\u4EEC\u7684\u624B\u672F\u548C\u540E\u7EE7\u6CBB\u7597\uFF0C\u53EF\u4EE5\u770B\u7740\u5B69\u5B50\u4E00\u70B9\u70B9\u6D3B\u8FC7\u6765\uFF0C\u51E0\u4E2A\u6708\u540E\u5BB6\u957F\u5E26\u7740\u5B69\u5B50\u627E\u4F60\u590D\u67E5\uFF0C\u4F60\u770B\u5230\u90A3\u4E2A\u6D3B\u6CFC\u53EF\u7231\u7684\u5B69\u5B50\u8DDF\u4F60\u7B11\uFF0C\u7136\u540E\u542C\u7740\u5BB6\u957F\u8BF4\u90A3\u4E9B\u53D1\u81EA\u5185\u5FC3\u7684\u611F\u6FC0\u8BDD\uFF0C\u5FC3\u91CC\u90A3\u4EFD\u6210\u5C31\u611F\u662F\u5176\u5B83\u804C\u4E1A\u627E\u4E0D\u5230\u7684\u3002</p><p>\u4F46\u53EF\u80FD\u4E5F\u662F\u56E0\u4E3A\u79BB\u751F\u6B7B\u7279\u522B\u8FD1\uFF0C\u6240\u4EE5\u8FD9\u4E2A\u804C\u4E1A\u4E5F\u8981\u627F\u53D7\u5F88\u591A\u522B\u7684\u804C\u4E1A\u6240\u4E0D\u5177\u5907\u7684\u538B\u529B\uFF0C\u56E0\u4E3A\u4F60\u7684\u4E00\u4E2A\u5FF5\u5934\u53EF\u80FD\u4F1A\u5F71\u54CD\u522B\u4EBA\u7684\u751F\u6B7B\u3002\u6240\u4EE5\u54EA\u6015\u4F60\u4E0B\u73ED\u56DE\u5BB6\u4E5F\u4F1A\u60F3\uFF0C\u6628\u665A\u653E\u56DE\u53BB\u90A3\u4E2A\u5B69\u5B50\u4E0D\u662F\u8179\u819C\u708E\u5427\uFF1F\u90A3\u4E2A\u5455\u5410\u7684\u5B69\u5B50\u5FD8\u8BB0\u770B\u8179\u80A1\u6C9F\u4E86\uFF0C\u4E0D\u4F1A\u662F\u759D\u6C14\u5361\u4F4F\u4E86\u6CA1\u53D1\u73B0\u5427\uFF1F\u767D\u5929\u505A\u7684\u90A3\u4E2A\u80A0\u543B\u5408\u5E94\u8BE5\u4E0D\u4F1A\u7618\u5427\uFF1F\u8981\u662F\u5728\u533B\u9662\u95E8\u53E3\u770B\u5230\u6709\u62C9\u6A2A\u5E45\u70E7\u94B1\u7EB8\u7684\uFF0C\u4F60\u53EF\u80FD\u4F1A\u5FD0\u5FD1\u4E0D\u5B89\u7684\u53BB\u786E\u8BA4\u4E00\u4E0B\u4E0D\u662F\u627E\u81EA\u5DF1\u7684\uFF0C\u4F60\u624D\u4F1A\u677E\u4E00\u53E3\u6C14\u3002</p><p>\u81F3\u4E8E\u88AB\u60A3\u8005\u6253\u9A82\uFF0C\u98CE\u9669\u80AF\u5B9A\u662F\u4F1A\u6709\u7684\u3002\u533B\u751F\u548C\u60A3\u8005\u7684\u4EA4\u6D41\u80FD\u529B\u548C\u6001\u5EA6\uFF0C\u4F1A\u5F71\u54CD\u81EA\u5DF1\u6240\u906D\u9047\u66B4\u529B\u4F24\u5BB3\u7684\u98CE\u9669\uFF0C\u4F46\u533B\u60A3\u5173\u7CFB\u7D27\u5F20\u7684\u6839\u6E90\u8FD8\u662F\u73B0\u5728\u7684\u533B\u7597\u4F53\u5236\uFF0C\u533B\u751F\u505A\u5F97\u6700\u597D\u4F9D\u7136\u4F1A\u6709\u88AB\u6253\u88AB\u6740\u7684\u98CE\u9669\u3002\u800C\u4E14\u5C31\u5F53\u524D\u7684\u5F62\u52BF\u6765\u770B\uFF0C\u77ED\u671F\u5185\u8FD8\u533B\u60A3\u5173\u7CFB\u8FD8\u662F\u4E0D\u592A\u53EF\u80FD\u6709\u6539\u5584\uFF0C\u8FD9\u4E2A\u65F6\u5019\u5F53\u533B\u751F\u5E38\u5E38\u4F1A\u611F\u89C9\u6CA1\u6709\u5C0A\u4E25\uFF0C\u4F9D\u7136\u8981\u505A\u597D\u88AB\u9A82\u88AB\u6253\uFF0C\u751A\u81F3\u88AB\u6740\u7684\u5FC3\u7406\u51C6\u5907\u3002</p><p>\u53E6\u5916\u4E00\u4E2A\u9700\u8981\u505A\u597D\u5FC3\u7406\u51C6\u5907\u7684\u5C31\u662F\u8981\u71AC\u591C\u73ED\uFF0C\u534A\u591C\u770B\u6025\u8BCA\uFF0C\u534A\u591C\u505A\u624B\u672F\u65F6\u5E38\u6709\u7684\u4E8B\u3002\u6709\u65F6\u5019\u56F0\u5F97\u773C\u775B\u90FD\u7741\u4E0D\u5F00\uFF0C\u8FD8\u5F97\u5728\u624B\u672F\u53F0\u4E0A\u526A\u7EBF\uFF0C\u4E0B\u4E86\u53F0\u5728\u5730\u4E0A\u8EBA\u51E0\u5206\u949F\u7136\u540E\u63A5\u7740\u505A\u4E0B\u4E00\u53F0\u3002\u6709\u65F6\u5019\u521A\u7761\u7740\u5C31\u88AB\u53EB\u8D77\u6765\uFF0C\u4F1A\u611F\u89C9\u5168\u8EAB\u7684\u8840\u5F80\u4E0A\u6D8C\uFF0C\u6709\u65F6\u5019\u78B0\u4E0A\u4E2A\u5B89\u9759\u7684\u591C\u73ED\u4F60\u5374\u7741\u7740\u773C\u775B\u7B49\u547C\u53EB\u5668\u7684\u54CD\u8D77\u3002\u4E00\u4E2A\u591C\u73ED\u6709\u65F6\u5019\u8981\u51E0\u5929\u624D\u80FD\u628A\u4F5C\u606F\u8C03\u56DE\u6765\uFF0C\u6709\u65F6\u4E0A\u4E00\u4E2A\u591C\u73ED\u8FD8\u6CA1\u8C03\u56DE\u6765\u4E0B\u4E2A\u591C\u73ED\u53C8\u5F00\u59CB\u4E86\uFF0C\u5BF9\u4E00\u4E9B\u4EBA\u624B\u7D27\u7F3A\u7684\u79D1\u5BA4\u6765\u8BF4\uFF0C\u8FD9\u79CD\u5012\u591C\u73ED\u7684\u751F\u6D3B\u8981\u6301\u7EED\u5230\u56DB\u4E94\u5341\u5C81\uFF0C\u8BF4\u662F\u7528\u751F\u547D\u5728\u4E0A\u73ED\u90FD\u4E0D\u4E3A\u8FC7\u3002</p><p>\u5F53\u7136\u6240\u6709\u7684\u538B\u529B\uFF0C\u4E5F\u662F\u533B\u751F\u7684\u6210\u957F\u52A8\u529B\uFF0C\u6240\u7ECF\u5386\u7684\u78E8\u96BE\uFF0C\u4E5F\u662F\u533B\u751F\u79EF\u7D2F\u7ECF\u9A8C\u548C\u89C1\u8BC6\u7684\u8FC7\u7A0B\u3002\u533B\u751F\u8FD9\u4E2A\u804C\u4E1A\u662F\u4E00\u4E2A\u548C\u4EBA\u6253\u4EA4\u9053\u7684\u804C\u4E1A\uFF0C\u5F53\u533B\u751F\u4E45\u4E86\u4F1A\u9605\u4EBA\u65E0\u6570\uFF0C\u800C\u4E14\u5927\u90E8\u4EBA\u613F\u610F\u4EA4\u4E2A\u533B\u751F\u670B\u53CB\uFF0C\u5BF9\u4E8E\u613F\u610F\u53BB\u505A\u793E\u4EA4\u7684\u533B\u751F\u6765\u8BF4\uFF0C\u5F88\u5BB9\u6613\u5EFA\u7ACB\u81EA\u5DF1\u7684\u793E\u4F1A\u5173\u7CFB\u7F51\u3002</p><p>\u6CBB\u75C5\u6551\u4EBA\u4E5F\u662F\u4E00\u4E2A\u53D1\u73B0\u95EE\u9898\uFF0C\u89E3\u51B3\u95EE\u9898\u7684\u8FC7\u7A0B\u3002\u5B66\u4E86\u533B\uFF0C\u63A5\u53D7\u4E86\u533B\u7597\u6559\u80B2\uFF0C\u505A\u4E86\u533B\u751F\uFF0C\u5177\u5907\u4E86\u4E34\u5E8A\u80FD\u529B\u7684\u4EBA\uFF0C\u4E5F\u5F80\u5F80\u5177\u5907\u89E3\u51B3\u5176\u5B83\u95EE\u9898\u7684\u80FD\u529B\uFF0C\u6240\u4EE5\u5373\u4FBF\u4F60\u60F3\u6539\u884C\u6362\u4E2A\u804C\u4E1A\uFF0C\u4E5F\u4E0D\u4F1A\u505A\u5F97\u592A\u5DEE\u3002</p><p>\u800C\u4E14\u76EE\u524D\u533B\u7597\u884C\u4E1A\u5728\u6574\u4E2A\u793E\u4F1A\u5C5E\u4E8E\u53D1\u5C55\u6BD4\u8F83\u6162\u7684\u884C\u4E1A\uFF0C\u548C\u5176\u5B83\u884C\u4E1A\u76F8\u6BD4\u7684\u53D1\u5C55\u7684\u843D\u5DEE\u4E5F\u51B3\u5B9A\u4E86\u8FD9\u4E2A\u884C\u4E1A\u8FDF\u65E9\u8981\u4F5C\u51FA\u5927\u7684\u53D8\u9769\uFF0C\u8FD9\u4E5F\u610F\u5473\u7740\u8FD9\u4E2A\u884C\u4E1A\u8FD8\u662F\u6709\u5F88\u5927\u53D1\u5C55\u673A\u4F1A\uFF0C\u73B0\u5728\u533B\u7597\u5DF2\u7ECF\u6210\u4E3A\u65B0\u7684\u6295\u8D44\u70ED\u70B9\u4E5F\u8BF4\u660E\u4E86\u8FD9\u4E00\u70B9\u3002\u6709\u533B\u5B66\u80CC\u666F\u7684\u4EBA\u673A\u4F1A\u4E5F\u4F1A\u591A\u5F88\u591A\uFF0C\u6240\u4EE5\u54EA\u6015\u4F60\u5B66\u4E86\u533B\u4E0D\u5F53\u533B\u751F\uFF0C\u627E\u4EFD\u5DE5\u4F5C\u4E5F\u4E0D\u592A\u96BE\u3002</p><p>\u603B\u7ED3\u4E0B\u6765\uFF0C\u5B66\u533B\u7684\u5B66\u4E60\u5468\u671F\u5F88\u957F\uFF0C\u5DE5\u4F5C\u6BD4\u8F83\u597D\u627E\uFF0C\u4F46\u804C\u4E1A\u6210\u957F\u901F\u5EA6\u5F88\u6162\u3002\u533B\u751F\u6536\u5165\u4E2D\u7B49\u504F\u4E0A\uFF0C\u6536\u5165\u5F88\u7A33\u5B9A\uFF0C\u5DE5\u4F5C\u538B\u529B\u5927\uFF0C\u4F46\u6BD4\u8F83\u5BB9\u6613\u6709\u6210\u5C31\u611F\u3002\u76EE\u524D\u793E\u4F1A\u5730\u4F4D\u8FD8\u53EF\u4EE5\uFF0C\u4F46\u5904\u4E8E\u4E0B\u964D\u901A\u9053\u4E4B\u4E2D\uFF0C\u73B0\u5728\u5F53\u533B\u751F\u4F9D\u7136\u6709\u4E00\u5B9A\u4EBA\u8EAB\u98CE\u9669\uFF0C\u4F46\u5B66\u4E86\u533B\u53D1\u5C55\u673A\u4F1A\u8FD8\u662F\u6BD4\u8F83\u591A\u3002</p><p>\u603B\u4F53\u800C\u8A00\uFF0C\u533B\u751F\u8FD9\u4E2A\u804C\u4E1A\u5C5E\u4E8E\u6027\u4EF7\u6BD4\u4E0D\u9AD8\u7684\u804C\u4E1A\uFF0C\u76EE\u524D\u8FD8\u53EA\u9002\u5408\u6709\u7406\u60F3\uFF0C\u6709\u60C5\u6000\u7684\u4EBA\u53BB\u9009\u62E9\u3002\u5982\u679C\u6211\u7684\u81EA\u5DF1\u5B69\u5B50\u4ECA\u5E74\u9AD8\u8003\uFF0C\u6211\u4E0D\u5EFA\u8BAE\u5979\u53BB\u5B66\u533B\uFF0C\u4F46\u5982\u679C\u5979\u613F\u610F\uFF0C\u6211\u4F1A\u5C0A\u91CD\u5979\u7684\u9009\u62E9\uFF0C\u5B66\u4E86\u533B\u6211\u4E5F\u4E0D\u5EFA\u8BAE\u5979\u505A\u4E34\u5E8A\u533B\u751F\uFF0C\u4F46\u5982\u679C\u5979\u613F\u610F\uFF0C\u6211\u4F9D\u7136\u4F1A\u5C0A\u91CD\u5979\u7684\u9009\u62E9\u3002</p><hr><p>\u9898\u56FE\u6765\u81EApixabay\uFF0C\u6388\u6743\u57FA\u4E8ECC0\u534F\u8BAE\u3002</p><p>\u5B66\u4E86\u533B\u5373\u4FBF\u4E0D\u60F3\u505A\u533B\u751F\u4E86\uFF0C\u4F9D\u7136\u4F1A\u6709\u5F88\u591A\u533B\u5B66\u76F8\u5173\u804C\u4E1A\u53EF\u4F9B\u9009\u62E9\uFF0C\u6BD4\u5982\u6211\u73B0\u5728\u5C31\u5728\u62DB\u4E00\u540D\u5168\u804C\u533B\u5B66\u7F16\u8F91\uFF0C\u5982\u679C\u4F60\u6709\u533B\u5B66\u672C\u79D1\u4EE5\u4E0A\u5B66\u5386\uFF1B\u9075\u4ECE\u5FAA\u8BC1\u533B\u5B66\u7406\u5FF5\uFF0C\u6709\u6587\u732E\u68C0\u7D22\u548C\u903B\u8F91\u601D\u7EF4\u80FD\u529B\uFF0C\u80FD\u9605\u8BFB\u82F1\u6587\u6587\u732E\uFF1B\u6587\u5B57\u529F\u5E95\u597D\uFF0C\u6B22\u8FCE\u53D1\u7B80\u5386\u5230\u6211\u7684\u90AE\u7BB1drpei@hotmail.com\uFF0C\u5DE5\u4F5C\u5730\u70B9\u6DF1\u5733\uFF0C\u5F85\u9047\u9762\u8BAE\u3002</p>',21),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626168&idx=1&sn=92eac75186f898ee1da11bbe45115813&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function m(x,f){const p=a("ExternalLinkIcon");return r(),t(o,null,[_,e("p",null,[e("a",l,[h,n(p)])])],64)}var k=i(d,[["render",m]]);export{k as default};
