import{r as n,o as p,c as a,a as t,b as o,w as c,F as d,e,d as i}from"./app.9aaa2256.js";import{_}from"./plugin-vue_export-helper.21dcd24c.js";const g={},l=t("h3",{id:"\u590F\u65E5\u6765\u4E34-\u600E\u6837\u7ED9\u5B69\u5B50\u505A\u597D\u9632\u6652",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#\u590F\u65E5\u6765\u4E34-\u600E\u6837\u7ED9\u5B69\u5B50\u505A\u597D\u9632\u6652","aria-hidden":"true"},"#"),e(" \u590F\u65E5\u6765\u4E34\uFF0C\u600E\u6837\u7ED9\u5B69\u5B50\u505A\u597D\u9632\u6652\uFF1F")],-1),h=t("p",null,"\u4E94\u6708\u7684\u9633\u5149\u8D8A\u6765\u8D8A\u5F3A\u70C8\u4E86\u3002",-1),f=e("\u9633\u5149\u53EF\u4EE5\u4FC3\u8FDB\u76AE\u80A4\u5408\u6210\u7EF4\u751F\u7D20D\uFF0C\u7136\u540E\u4FC3\u8FDB\u5B69\u5B50\u9499\u7684\u5438\u6536\uFF0C\u540C\u65F6\uFF0C\u5145\u8DB3\u7684\u6237\u5916\u6D3B\u52A8\uFF0C\u8FD8\u80FD\u964D\u4F4E\u5B69\u5B50\u8FD1\u89C6\u7684\u98CE\u9669\u3002\u4F46\u540C\u65F6\uFF0C\u9633\u5149\u91CC\u7684\u7D2B\u5916\u7EBF\uFF0C\u4E5F\u4F1A\u5BF9\u5B69\u5B50\u7684\u76AE\u80A4\u548C\u773C\u775B\u9020\u6210\u635F\u4F24\uFF0C\u5173\u4E8E"),m=e("\u6234\u592A\u9633\u955C\u4FDD\u62A4\u5B69\u5B50\u773C\u775B"),b=e("\u7684\u95EE\u9898\uFF0C\u773C\u79D1\u533B\u751F\u5DF2\u7ECF\u79D1\u666E\u8FC7\uFF0C\u73B0\u5728\u6211\u5C31\u6765\u8BB2\u8BB2\u76AE\u80A4\u7684\u9632\u6652\u95EE\u9898\u3002"),u=i("<p><strong>\u5B69\u5B50\u662F\u5426\u9700\u8981\u9632\u6652\uFF1F</strong></p><p>\u7B54\u6848\u662F\u80AF\u5B9A\u7684\u3002\u9633\u5149\u91CC\u7684\u7D2B\u5916\u7EBFA\uFF08UVA\uFF09\u548C\u7D2B\u5916\u7EBFB\uFF08UVB\uFF09\u4E0D\u4F46\u53EF\u4EE5\u8BA9\u76AE\u80A4\u53D8\u9ED1\uFF0C\u8FD8\u53EF\u80FD\u5BFC\u81F4\u65E5\u6652\u4F24\u3001\u65E5\u5149\u6027\u76AE\u708E\uFF0C\u957F\u65F6\u95F4\u65E5\u6652\u4F1A\u4FC3\u8FDB\u76AE\u80A4\u5149\u8001\u5316\uFF0C\u751A\u81F3\u589E\u52A0\u76AE\u80A4\u764C\u7684\u98CE\u9669\u3002\u513F\u7AE5\u76AE\u80A4\u6BD4\u8F83\u8584\uFF0C\u5BF9\u9633\u5149\u7684\u635F\u4F24\u4E5F\u66F4\u654F\u611F\uFF0C\u635F\u4F24\u66B4\u9732\u540E\u75C5\u53D8\u6301\u7EED\u65F6\u95F4\u66F4\u957F\uFF0C\u6240\u4EE5\u9632\u6652\u5C31\u66F4\u4E3A\u91CD\u8981\u3002</p><p><strong>\u5982\u4F55\u505A\u597D\u5B69\u5B50\u9632\u6652\uFF1F</strong></p><p>\u548C\u5927\u4EBA\u4E00\u6837\uFF0C\u5B69\u5B50\u7684\u9632\u6652\u4E3B\u8981\u901A\u8FC7\u4EE5\u4E0B\u51E0\u4E2A\u65B9\u5F0F\u3002</p><p><strong>1.\u907F\u5F00\u65E5\u7167\u9AD8\u5CF0</strong></p><p>\u5728\u4E0A\u534810\u70B9\u5230\u4E0B\u53484\u70B9\u5C3D\u91CF\u5C11\u5916\u51FA\uFF0C\u5916\u51FA\u4E5F\u5C3D\u91CF\u5446\u5728\u836B\u5904\uFF0C\u6BD4\u5982\u6811\u836B\u4E0B\uFF0C\u5C4B\u6A90\u4E0B\uFF0C\u592A\u9633\u4F1E\u4E0B\u3002</p><p><strong>2.\u8863\u7269\u906E\u63A9</strong></p><p>\u6211\u4EEC\u53EF\u4EE5\u7ED9\u5B69\u5B50\u6234\u80FD\u906E\u63A9\u5230\u8138\u3001\u988A\u3001\u8033\u53CA\u540E\u9888\u90E8\u7684\u5BBD\u6A90\u5E3D\u3002\u5728\u8863\u7269\u4E0A\uFF0C\u53EF\u4EE5\u9009\u62E9\u68C9\u8D28\u6216\u6DA4\u7EB6\u3001\u7F16\u5236\u7D27\u5BC6\u7684\u6DF1\u8272\u5BBD\u677E\u8863\u7269\uFF0C\u5728\u6237\u5916\u6E38\u6CF3\u65F6\u53EF\u4EE5\u7A7F\u7740\u957F\u8896\u957F\u88E4\u8FDE\u4F53\u6B3E\u5F0F\u7684\u6CF3\u8863\u3002</p><p><strong>3.\u4F7F\u7528\u9632\u6652\u971C</strong></p><p>\u9632\u6652\u5242\u662F\u4E00\u4E2A\u91CD\u8981\u7684\u9632\u6652\u63AA\u65BD\u3002\u9632\u6652\u6307\u6570\uFF08SPF\uFF09\u662F\u8861\u91CF\u9632\u6652\u971C\u5BF9UVB\u9632\u62A4\u80FD\u529B\u7684\u56FD\u9645\u6807\u51C6\uFF0C\u65E5\u5E38\u751F\u6D3B\u4E2D\uFF0C\u53EF\u4EE5\u9009\u7528SPF15\u6216\u4EE5\u4E0A\u7684\u9632\u6652\u5242\uFF0C\u6237\u5916\u6D3B\u52A8\uFF0C\u5219\u5EFA\u8BAE\u9009\u62E9SPF30\u6216\u4EE5\u4E0A\u7684\u9632\u6652\u5242\u3002</p><p>\u56FD\u9645\u4E0A\u76EE\u524D\u8FD8\u6CA1\u6709\u8861\u91CF\u9632\u6652\u971C\u5BF9UVA\u9632\u62A4\u80FD\u529B\u7684\u7EDF\u4E00\u6307\u6807\uFF0C\u5EFA\u8BAE\u9009\u62E9\u6807\u660E\u5E7F\u8C31\uFF08broad-spectrum\uFF09\u6216\u80FD\u540C\u65F6\u5BF9UVA\u548CUVB\u63D0\u4F9B\u4FDD\u62A4\u7684\u4EA7\u54C1\u3002</p><p>\u9632\u6652\u5242\u8981\u8DB3\u91CF\u4F7F\u7528\uFF0C<strong>\u6BCF\u5E73\u65B9\u5398\u7C73\u76AE\u80A4\u4E0A\u5927\u6982\u8981\u6D82<strong><strong>2</strong></strong>\u6BEB\u514B\u9632\u6652\u971C\uFF0C\u8981\u5728\u51FA\u95E8\u524D<strong><strong>15-30</strong></strong>\u5206\u949F\u63D0\u524D\u6D82\u597D\uFF0C\u4EE5\u4F7F\u76AE\u80A4\u8868\u9762\u5F62\u6210\u9632\u62A4\u819C\uFF0C\u5E76\u4E14\u6BCF\u9694<strong><strong>2</strong></strong>\u5C0F\u65F6\u8981\u8865\u6D82\u4E00\u6B21\u3002</strong></p><p>\u540C\u65F6\uFF0C\u9632\u6C34\u9632\u6652\u971C\u5E76\u4E0D\u662F\u7EDD\u5BF9\u9632\u6C34\u7684\uFF0C\u51FA\u6C57\u3001\u6E38\u6CF3\u3001\u64E6\u62ED\u76AE\u80A4\u540E\uFF0C\u9700\u8981\u91CD\u65B0\u6D82\u62B9\uFF0C\u4EE5\u4FDD\u8BC1\u8DB3\u591F\u7684\u9632\u62A4\u3002</p><p>\u5BF9\u4E8E\u5A74\u513F\u548C\u513F\u7AE5\uFF0C\u5EFA\u8BAE\u4F7F\u7528\u4EE5\u4E8C\u6C27\u5316\u949B\u548C\u6C27\u5316\u950C\u4E3A\u6709\u6548\u6210\u5206\u7684\u7269\u7406\u9632\u6652\u5242\u3002\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C<strong>\u513F\u7AE5\u5E94\u907F\u514D\u4F7F\u7528\u55B7\u96FE\u578B\u7684\u9632\u6652\u5242\uFF0C\u4EE5\u9632\u5438\u5165\u98CE\u9669</strong>\u3002</p><p>\u53E6\u5916\uFF0C\u5BF9\u4E8E6\u4E2A\u6708\u4EE5\u4E0B\u7684\u5A74\u513F\uFF0C\u5EFA\u8BAE\u5C3D\u91CF\u4F7F\u7528\u7269\u7406\u906E\u6321\u7684\u65B9\u5F0F\u800C\u4E0D\u7528\u9632\u6652\u971C\uFF0C\u53EA\u6709\u9762\u90E8\u3001\u624B\u80CC\u7B49\u4E0D\u5BB9\u6613\u906E\u6321\u7684\u90E8\u4F4D\uFF0C\u53EF\u5EFA\u8BAE\u5C0F\u9762\u79EF\u4F7F\u7528\u3002</p><p>\u540C\u65F6\u6211\u4EEC\u8FD8\u8981\u77E5\u9053\uFF0C\u90A3\u6015\u662F\u9634\u5929\uFF0C\u5982\u679C\u7D2B\u5916\u7EBF\u6307\u6570\u5C5E\u4E8E\u4E2D\u5EA6\uFF08\u4F8B\u5982\u7D2B\u5916\u7EBF\u6307\u6570\u4E3A3\uFF09\u6216\u66F4\u9AD8\u7684\u8BDD\uFF0C\u4E5F\u5E94\u91C7\u53D6\u9632\u6652\u63AA\u65BD\u3002</p><p><strong>\u9632\u6652\u662F\u5426\u4F1A\u5BFC\u81F4\u7F3A\u9499\uFF1F</strong></p><p>\u56E0\u4E3A\u5149\u7167\u53EF\u4EE5\u4FC3\u8FDB\u7EF4\u751F\u7D20D\u7684\u5408\u6210\uFF0C\u4E0D\u53EF\u5426\u8BA4\u7684\u662F\uFF0C\u51CF\u5C11\u65E5\u6652\u786E\u5B9E\u53EF\u80FD\u5BFC\u81F4\u7EF4\u751F\u7D20D\u7F3A\u4E4F\uFF0C\u4F46\u4F7F\u7528\u9632\u6652\u971C\u4E5F\u786E\u5B9E\u53EF\u80FD\u5F71\u54CD\u7EF4\u751F\u7D20D\u7684\u4EA7\u751F\uFF0C\u8FDB\u800C\u5F71\u54CD\u9499\u7684\u5438\u6536\u3002</p><p>\u4F46\u73B0\u5728\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u53E3\u670D\u8865\u5145\u7EF4\u751F\u7D20D\uFF0C\u5A74\u5E7C\u513F\u6BCF\u5929\u8865\u5145400-600IU\u7EF4\u751F\u7D20D3\uFF0C\u5373\u53EF\u4FDD\u8BC1\u8EAB\u4F53\u7684\u9700\u6C42\uFF0C\u540C\u65F6\u518D\u6444\u5165\u9002\u91CF\u5976\u7C7B\u7684\u8BDD\uFF0C\u5C31\u4E0D\u9700\u8981\u62C5\u5FC3\u7F3A\u9499\u3002\u6240\u4EE5\uFF0C\u4E0D\u9700\u8981\u62C5\u5FC3\u9632\u6652\u4F1A\u5BFC\u81F4\u7F3A\u9499\u3002</p><p><strong>\u53C2\u8003\u8D44\u6599\uFF1A</strong></p><p>1.AAP. Policy Statement-Ultraviolet Radiation: A Hazard to Children and Adolescents. 2011.</p><p>2.AAP.Baby Sunburn Prevention. 2015.</p><p>3.\u4E2D\u56FD\u5C45\u6C11\u81B3\u98DF\u6307\u5357\uFF082016\uFF09</p><hr><p>\u5173\u6CE8\u6021\u79BE\u5065\u5EB7\uFF0C\u53EF\u5411\u533B\u751F\u54A8\u8BE2\u9632\u6652\u53CA\u76AE\u80A4\u95EE\u9898</p>",25),x={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651628235&idx=1&sn=95d44e1362c6f6b074f6c771f9173e34&chksm=8b5c8b31bc2b022761ee86a19218e99a3732deb87db5324eaceea55c038197f1707f0329a606&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},A=e("\u67E5\u770B\u539F\u6587");function V(k,B){const r=n("RouterLink"),s=n("ExternalLinkIcon");return p(),a(d,null,[l,h,t("p",null,[f,o(r,{to:"/s/__biz=MzIwMzM3NjI5OQ==&mid=2247485089&idx=1&sn=da05a95a005705e671e311cf6b8f8ce9&scene=21#wechat_redirect/"},{default:c(()=>[m]),_:1}),b]),u,t("p",null,[t("a",x,[A,o(s)])])],64)}var D=_(g,[["render",V]]);export{D as default};
