import{r,o as t,c as n,a as e,b as a,F as o,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=s('<h3 id="\u5B69\u5B50\u54B3\u55FD\u603B\u4E0D\u597D-\u8981\u4E0D\u8981\u67E5\u98DF\u7269\u8FC7\u654F" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u54B3\u55FD\u603B\u4E0D\u597D-\u8981\u4E0D\u8981\u67E5\u98DF\u7269\u8FC7\u654F" aria-hidden="true">#</a> \u5B69\u5B50\u54B3\u55FD\u603B\u4E0D\u597D\uFF0C\u8981\u4E0D\u8981\u67E5\u98DF\u7269\u8FC7\u654F\uFF1F</h3><p>\u54B3\u55FD\u662F\u4E00\u4E2A\u975E\u5E38\u5E38\u89C1\u7684\u95EE\u9898\uFF0C\u6709\u65F6\u770B\u904D\u533B\u9662\uFF0C\u4E5F\u627E\u4E0D\u5230\u786E\u5207\u539F\u56E0\uFF0C\u5C1D\u8BD5\u5404\u79CD\u65B9\u6CD5\uFF0C\u4E5F\u603B\u4E0D\u89C1\u597D\u8F6C\u3002</p><p>\u6709\u7684\u533B\u751F\u8BF4\uFF0C\u53EF\u80FD\u662F\u98DF\u7269\u8FC7\u654F\u5F15\u8D77\u7684\uFF0C\u505A\u4E2A\u8FC7\u654F\u539F\u68C0\u67E5\u5427\uFF0C\u505A\u5B8C\u68C0\u67E5\u540E\uFF0C\u53D1\u73B0\u597D\u51E0\u4E2A\u9633\u6027\uFF0C\u533B\u751F\u5EFA\u8BAE\u56DE\u907F\u8FD9\u4E9B\u98DF\u7269\uFF0C\u6709\u5FC5\u8981\u5417\uFF1F</p><p>\u5148\u8BF4\u7ED3\u8BBA\uFF1A</p><p>1.\u6162\u6027\u54B3\u55FD\uFF0C\u98DF\u7269\u8FC7\u654F\u539F\u68C0\u6D4B\u53C2\u8003\u610F\u4E49\u975E\u5E38\u5C0F\uFF0C\u901A\u5E38\u4E0D\u63A8\u8350\u3002</p><p>2.\u4EC5\u54B3\u55FD\u800C\u6CA1\u6709\u5176\u5B83\u98DF\u7269\u8FC7\u654F\u8868\u73B0\uFF0C\u98DF\u7269\u8FC7\u654F\u53EF\u80FD\u6027\u5F88\u5C0F\u3002</p><p>3.\u8FC7\u654F\u539F\u68C0\u6D4B\u9633\u6027\uFF0C\u4E0D\u7B49\u4E8E\u8BE5\u98DF\u7269\u8FC7\u654F\uFF0C\u4E5F\u4E0D\u9700\u8981\u56E0\u6B64\u5FCC\u53E3\u3002</p><p>4.\u6CA1\u6709\u8FC7\u654F\u76F8\u5173\u75C7\u72B6\uFF0C\u65E0\u9700\u68C0\u6D4B\u8FC7\u654F\u539F\u3002</p><p>\u63A5\u4E0B\u518D\u7EC6\u8BF4\u4E00\u4E0B\u98DF\u7269\u8FC7\u654F\u548C\u54B3\u55FD\u7684\u5173\u7CFB\u3002</p><p>\u98DF\u7269\u8FC7\u654F\u662F\u514D\u75AB\u7CFB\u7EDF\u63A5\u89E6\u98DF\u7269\u6297\u539F\u540E\u7684\u5F02\u5E38\u53CD\u5E94\uFF0C\u5927\u81F4\u5206\u4E3AIgE\u4ECB\u5BFC\u7684\u53CD\u5E94\u6216\u975EIgE\u4ECB\u5BFC\u7684\u53CD\u5E94\uFF0C\u5B83\u4EEC\u4F1A\u5BFC\u81F4\u54B3\u55FD\u5417\uFF1F</p><p><strong>IgE\u4ECB\u5BFC\u7684\u98DF\u7269\u8FC7\u654F\u53CD\u5E94</strong></p><p>\u8FD9\u79CD\u53CD\u5E94\u7684\u7279\u70B9\u662F\u8D77\u6548\u5F88\u5FEB\uFF0C\u7EDD\u5927\u90E8\u5206\u5728\u66B4\u9732\u540E\u6570\u5206\u949F\u81F32\u5C0F\u65F6\u5185\u5F00\u59CB\uFF0C\u53EF\u6D89\u53CA\u76AE\u80A4\u3001\u547C\u5438\u9053\u3001\u80C3\u80A0\u9053\u53CA\u5FC3\u8840\u7BA1\u7CFB\u7EDF\u7B49\uFF0C\u53EF\u4EE5\u51FA\u73B0\u9F3B\u585E\u3001\u6D41\u6D95\u3001\u6253\u55B7\u568F\u3001\u54B3\u55FD\u548C\u5598\u606F\u7B49\u547C\u5438\u9053\u53CD\u5E94\u3002</p><p>\u4F46\u662F\uFF0CIgE\u4ECB\u5BFC\u7684\u98DF\u7269\u8FC7\u654F\u53CD\u5E94\u53EA\u8868\u73B0\u4E3A\u547C\u5438\u9053\u75C7\u72B6\uFF0C\u800C\u6CA1\u6709\u76AE\u80A4\u548C\u6D88\u5316\u9053\u75C7\u72B6\u7684\u5F88\u5C11\u89C1\u3002</p><p><strong>\u975EIgE\u4ECB\u5BFC\u7684\u98DF\u7269\u8FC7\u654F\u53CD\u5E94</strong></p><p>\u8FD9\u7C7B\u98DF\u7269\u8FC7\u654F\u53CD\u5E94\uFF0C\u66F4\u591A\u8868\u73B0\u4E3A\u4E9A\u6025\u6027\u548C/\u6216\u6162\u6027\u75C7\u72B6\uFF0C\u800C\u4E14\u901A\u5E38\u662F\u5B64\u7ACB\u7684\u80C3\u80A0\u9053\u548C/\u6216\u76AE\u80A4\u53CD\u5E94\u3002</p><p>\u56E0\u6B64\uFF0C\u4E0D\u8BBA\u662FIgE\u4ECB\u5BFC\u8FD8\u662F\u975EIgE\u4ECB\u5BFC\u7684\u98DF\u7269\u8FC7\u654F\u53CD\u5E94\uFF0C\u5355\u7EAF\u53EA\u5F15\u8D77\u54B3\u55FD\u800C\u6CA1\u6709\u5176\u4ED6\u8FC7\u654F\u8868\u73B0\u7684\u53EF\u80FD\u6027\u975E\u5E38\u5C0F\uFF0C\u800C\u4E14\u901A\u5E38\u90FD\u662F\u5F88\u77ED\u65F6\u95F4\u5185\u53D1\u751F\u7684\u3002</p><p>\u53E6\u5916\uFF0C\u98DF\u7269\u8FC7\u654F\u5927\u591A\u57282\u5C81\u524D\u5C31\u4F1A\u51FA\u73B0\uFF0C\u513F\u7AE5\u671F\u8FC7\u654F\u7684\u98DF\u7269\uFF0C\u6BD4\u5982\u725B\u5976\u3001\u9E21\u86CB\uFF0C\u5927\u90FD\u4F1A\u968F\u7740\u65F6\u95F4\u63A8\u79FB\u800C\u6D88\u5931\u3002\u540C\u6837\u7684\uFF0C\u4E4B\u524D\u4E0D\u8FC7\u654F\u7684\u98DF\u7269\uFF0C\u4EE5\u540E\u4E5F\u4E00\u822C\u4E0D\u4F1A\u8FC7\u654F\u3002</p><p>\u6240\u4EE5\uFF0C\u5982\u679C\u5B69\u5B50\u4E4B\u524D\u6CA1\u6709\u4EFB\u4F55\u98DF\u7269\u8FC7\u654F\u75C7\u72B6\uFF0C\u6700\u8FD1\u4E5F\u6CA1\u6709\u63A5\u89E6\u8FC7\u65B0\u7684\u98DF\u7269\uFF0C\u90A3\u4E48\u54B3\u55FD\u7684\u539F\u56E0\u57FA\u672C\u4E0D\u7528\u8003\u8651\u98DF\u7269\u8FC7\u654F\u3002</p><p><strong>\u8FC7\u654F\u68C0\u67E5\u9633\u6027\u4E5F\u4E0D\u7528\u62C5\u5FC3\u5417\uFF1F</strong></p><p>\u98DF\u7269\u8FC7\u654F\u67092\u79CD\u5E38\u7528\u68C0\u67E5\uFF0C\u4E00\u79CD\u662F\u76AE\u80A4\u8BD5\u9A8C\uFF0C\u53E6\u5916\u4E00\u79CD\u662F\u98DF\u7269\u7279\u5F02\u6027IgE\u68C0\u6D4B\u3002\u5982\u679C\u5B69\u5B50\u603B\u662F\u54B3\u55FD\uFF0C\u53BB\u505A\u8FD9\u4E24\u4E2A\u68C0\u67E5\u7ED3\u679C\u9633\u6027\uFF0C\u8981\u8003\u8651\u662F\u98DF\u7269\u8FC7\u654F\u5F15\u8D77\u7684\u54B3\u55FD\u5417\uFF1F</p><p>\u901A\u5E38\u4E0D\u9700\u8981\u3002\u8FD9\u662F\u56E0\u4E3A\u98DF\u7269\u8FC7\u654F\u7684\u8BCA\u65AD\uFF0C\u5E76\u4E0D\u662F\u5B8C\u5168\u4F9D\u636E\u8FD9\u4E9B\u68C0\u67E5\u6765\u5224\u65AD\u7684\u3002\u8FD9\u4E9B\u68C0\u67E5\u7ED3\u679C\uFF0C\u4E5F\u4E00\u5B9A\u8981\u7ED3\u5408\u60A3\u8005\u7684\u4E34\u5E8A\u75C5\u53F2\u6765\u9009\u62E9\u548C\u89E3\u8BFB\uFF0C\u56E0\u4E3A\u5B83\u4EEC\u7684\u5047\u9633\u6027\u5F88\u5E38\u89C1\uFF0C\u201C\u9633\u6027\u201D\u68C0\u6D4B\u7ED3\u679C\u5E76\u4E0D\u4E00\u5B9A\u8868\u660E\u5C31\u662F\u201C\u8FC7\u654F\u201D\u3002\u4E0D\u80FD\u4EC5\u51ED\u9633\u6027\u8BD5\u9A8C\u7ED3\u679C\uFF0C\u5C31\u8981\u56DE\u907F\u8BE5\u98DF\u7269\u3002\u8981\u5224\u65AD\u5B69\u5B50\u662F\u5426\u771F\u7684\u5BF9\u98DF\u7269\u8FC7\u654F\uFF0C\u5927\u90E8\u5206\u9700\u8981\u5728\u533B\u751F\u76D1\u7763\u4E0B\u8FDB\u884C\u98DF\u7269\u6FC0\u53D1\u8BD5\u9A8C\u6765\u786E\u5B9A\u3002</p><p><strong>\u98DF\u7269\u8FC7\u654F\u548C\u6162\u6027\u54B3\u55FD\u5B8C\u5168\u6CA1\u5173\u7CFB\u5417\uFF1F</strong></p><p>\u4E5F\u4E0D\u662F\u3002\u5728\u6162\u6027\u54B3\u55FD\u4E2D\uFF0C\u54EE\u5598\u662F\u5E38\u89C1\u7684\u539F\u56E0\u4E4B\u4E00\uFF0C\u98DF\u7269\u8FC7\u654F\u7684\u5B69\u5B50\u53D1\u751F\u54EE\u5598\u7684\u98CE\u9669\u4F1A\u589E\u52A0\u3002</p><p>\u5982\u679C\u4F60\u5B69\u5B50\u88AB\u4E34\u5E8A\u8BCA\u65AD\u4E3A\u54EE\u5598\uFF08\u6216\u54B3\u55FD\u53D8\u5F02\u6027\u54EE\u5598\uFF09\uFF0C\u5E76\u4E14\u6000\u7591\u548C\u98DF\u7269\u8FC7\u654F\u6709\u5173\uFF0C\u4E3A\u4E86\u5BFB\u627E\u8FC7\u654F\u539F\u53BB\u505A\u68C0\u67E5\u662F\u53EF\u4EE5\u7684\uFF0C\u4F46\u9700\u8981\u7528\u51C6\u786E\u7684\u68C0\u67E5\u65B9\u6CD5\u3002</p><p>\u5982\u679C\u5B69\u5B50\u53EA\u662F\u6162\u6027\u54B3\u55FD\uFF0C\u9996\u5148\u53BB\u5BFB\u627E\u75C5\u56E0\uFF0C\u800C\u4E0D\u662F\u76F2\u76EE\u7684\u53BB\u68C0\u6D4B\u98DF\u7269\u8FC7\u654F\u539F\uFF0C\u56E0\u4E3A\u5E2E\u52A9\u975E\u5E38\u5C0F\uFF0C\u751A\u81F3\u56E0\u4E3A\u8FD9\u4E9B\u68C0\u6D4B\u9633\u6027\u800C\u5FCC\u53E3\uFF0C\u90A3\u662F\u66F4\u4E0D\u5FC5\u8981\u7684\u3002</p><hr><p>\u672C\u6587\u4F5C\u8005<strong>\u5B59\u6770\u533B\u751F</strong>\u4E3A\u6021\u79BE\u6DF1\u5733\u8BCA\u6240\u7684\u513F\u79D1\u533B\u751F&amp;\u513F\u7AE5\u547C\u5438\u4E13\u79D1\u533B\u751F\uFF0C\u901A\u8FC7\u4E0B\u9762\u4E8C\u7EF4\u7801\u53EF\u9884\u7EA6\u5B59\u533B\u751F\u5C31\u8BCA\u3002</p>',27),g={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651628124&idx=1&sn=c95a76943e0fe62fa342405135334cff&chksm=8b5c8ba6bc2b02b01f95de9f922ee31822e8825d9fc5730a7eb17b3b3691f7e1f1328ea427f5&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},f=c("\u67E5\u770B\u539F\u6587");function h(l,m){const p=r("ExternalLinkIcon");return t(),n(o,null,[i,e("p",null,[e("a",g,[f,a(p)])])],64)}var x=d(_,[["render",h]]);export{x as default};
