import{r as n,o as t,c as r,a as p,b as a,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},m=c('<h3 id="\u505A\u8D85\u58F0\u53D1\u73B0\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3-\u8981\u5403\u6297\u751F\u7D20\u5417" tabindex="-1"><a class="header-anchor" href="#\u505A\u8D85\u58F0\u53D1\u73B0\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3-\u8981\u5403\u6297\u751F\u7D20\u5417" aria-hidden="true">#</a> \u505A\u8D85\u58F0\u53D1\u73B0\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\uFF0C\u8981\u5403\u6297\u751F\u7D20\u5417\uFF1F</h3><p>\u5B69\u5B50\u809A\u5B50\u75DB\u4E0A\u533B\u9662\uFF0C\u533B\u751F\u95EE\u4E86\u75C5\u60C5\uFF0C\u6478\u4E86\u809A\u5B50\uFF0C\u7136\u540E\u8BA9\u53BB\u505A\u8D85\u58F0\uFF0C\u8D85\u58F0\u62A5\u544A\u8BF4\u770B\u5230\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\uFF0C\u6700\u540E\u8003\u8651\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u8BA9\u5403\u51E0\u5929\u6297\u751F\u7D20\uFF0C\u8FD9\u5E94\u8BE5\u662F\u5F88\u591A\u5B69\u5B50\u7684\u7ECF\u5386\u3002</p><p>\u5230\u5E95\u6709\u6CA1\u6709\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u8FD9\u4E2A\u75C5\uFF1F\u8D85\u58F0\u770B\u5230\u6DCB\u5DF4\u7ED3\uFF0C\u5C31\u662F\u6DCB\u5DF4\u7ED3\u708E\u5417\uFF1F\u5230\u5E95\u8981\u4E0D\u8981\u5403\u6297\u751F\u7D20\uFF1F\u8FD9\u662F\u5F88\u591A\u5BB6\u957F\u7684\u56F0\u60D1\uFF0C\u4E5F\u662F\u4E00\u4E9B\u533B\u751F\u7684\u56F0\u60D1\u3002</p><p><strong>\u6709\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u8FD9\u4E2A\u75C5\u5417\uFF1F</strong></p><p>\u6DCB\u5DF4\u7ED3\u662F\u4EBA\u514D\u75AB\u5668\u5B98\u7684\u4E00\u90E8\u5206\uFF0C\u9664\u4E86\u4F53\u8868\uFF0C\u6BD4\u5982\u8116\u5B50\u3001\u814B\u4E0B\u3001\u8179\u80A1\u6C9F\u8FD9\u4E9B\u5730\u65B9\u6709\u6DCB\u5DF4\u7ED3\uFF0C\u5728\u4EBA\u4F53\u7684\u7EC4\u7EC7\u6DF1\u90E8\uFF0C\u6BD4\u5982\u8179\u819C\u540E\uFF0C\u80A0\u7CFB\u819C\u4E0A\u540C\u6837\u4E5F\u6709\u3002</p><p>\u6DCB\u5DF4\u7ED3\u53EF\u4EE5\u8FC7\u6EE4\u6DCB\u5DF4\u6DB2\u91CC\u7684\u7EC6\u83CC\u548C\u75C5\u6BD2\uFF0C\u5982\u679C\u5B83\u6240\u5728\u533A\u57DF\u7684\u7EC4\u7EC7\u53D1\u751F\u611F\u67D3\uFF0C\u901A\u8FC7\u6DCB\u5DF4\u6DB2\u5165\u4FB5\u8FC7\u6765\u7684\u7EC6\u83CC\u548C\u75C5\u6BD2\u5C31\u53EF\u80FD\u88AB\u6DCB\u5DF4\u7ED3\u8FC7\u6EE4\u6389\uFF0C\u4F46\u4E5F\u53EF\u80FD\u5F15\u53D1\u6DCB\u5DF4\u7ED3\u53D1\u751F\u708E\u75C7\u53CD\u5E94\uFF0C\u5BFC\u81F4\u6DCB\u5DF4\u7ED3\u80BF\u5927\u751A\u81F3\u6DCB\u5DF4\u7ED3\u708E\uFF0C\u4F53\u8868\u7684\u6DCB\u5DF4\u7ED3\u5982\u6B64\uFF0C\u80A0\u7CFB\u819C\u4E0A\u7684\u6DCB\u5DF4\u7ED3\u4E5F\u662F\u5982\u6B64\u3002</p><p>\u513F\u7AE5\u7684\u6DCB\u5DF4\u7CFB\u7EDF\u6BD4\u8F83\u6D3B\u8DC3\uFF0C\u5F88\u591A\u5B69\u5B50\u5728\u9888\u90E8\u53EF\u4EE5\u6478\u5230\u6DCB\u5DF4\u7ED3\uFF0C\u5C24\u5176\u662F\u5728\u53D1\u751F\u611F\u67D3\u4E4B\u540E\uFF0C\u6DCB\u5DF4\u7ED3\u90FD\u53EF\u80FD\u53CD\u5E94\u6027\u7684\u589E\u5927\uFF0C\u6709\u65F6\u8FD8\u51FA\u73B0\u6DCB\u5DF4\u7ED3\u708E\uFF0C\u51FA\u73B0\u7EA2\u80BF\u89E6\u75DB\u3002\u4F53\u8868\u7684\u6DCB\u5DF4\u7ED3\u53D1\u708E\uFF0C\u6BD4\u5982\u9888\u90E8\u3001\u8179\u80A1\u6C9F\u7684\u6DCB\u5DF4\u7ED3\u708E\u5927\u5BB6\u5E76\u4E0D\u964C\u751F\u3002</p><p>\u65E2\u7136\u4F53\u8868\u6DCB\u5DF4\u7ED3\u4F1A\u51FA\u73B0\u6DCB\u5DF4\u7ED3\u708E\uFF0C\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u5F53\u7136\u4E5F\u6709\u53EF\u80FD\uFF0C\u4EE5\u524D\u6211\u4EEC\u505A\u8179\u90E8\u624B\u672F\u7684\u65F6\u5019\u4E5F\u7ECF\u5E38\u4F1A\u770B\u5230\u80A0\u7BA1\u65C1\u6709\u80BF\u5927\u975E\u5E38\u660E\u663E\u6DCB\u5DF4\u7ED3\uFF0C\u5207\u4E0B\u6765\u505A\u75C5\u7406\u68C0\u67E5\u4E5F\u662F\u63D0\u793A\u708E\u75C7\u6539\u53D8\u3002\u4E5F\u6709\u4EBA\u5728\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u91CC\u57F9\u517B\u51FA\u5305\u62EC\u7EC6\u83CC\u548C\u75C5\u6BD2\u7B49\u75C5\u539F\u4F53\u7684\uFF0C\u6240\u4EE5\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u8FD9\u4E2A\u75C5\u81EA\u4ECE1922\u5E74\u88AB\u63D0\u51FA\u6765\u540E\uFF0C\u4E5F\u662F\u88AB\u533B\u5B66\u754C\u6240\u8BA4\u53EF\u7684\u3002</p><p><strong>\u8D85\u58F0\u53D1\u73B0\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\uFF0C\u5C31\u662F\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u5417\uFF1F</strong></p><p>\u8FD9\u5012\u4E0D\u662F\uFF0C\u513F\u7AE5\u672C\u8EAB\u6DCB\u5DF4\u7CFB\u7EDF\u6BD4\u8F83\u53D1\u8FBE\uFF0C\u5F88\u591A\u5B69\u5B50\u9888\u90E8\u90FD\u53EF\u4EE5\u6478\u5230\u6DCB\u5DF4\u7ED3\uFF0C\u5E76\u6CA1\u6709\u660E\u663E\u7EA2\u80BF\u89E6\u75DB\u7684\u4E25\u91CD\u53CD\u5E94\uFF0C\u6240\u4EE5\u8179\u90E8\u63A2\u6D4B\u5230\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u4E5F\u5F88\u6B63\u5E38\uFF0C\u5C24\u5176\u662F\u73B0\u5728\u8D85\u58F0\u8D8A\u6765\u8D8A\u666E\u53CA\uFF0C\u654F\u611F\u5EA6\u4E5F\u8D8A\u6765\u8D8A\u9AD8\u7684\u60C5\u51B5\u4E0B\u3002</p><p>\u56FD\u5185\u66FE\u6709\u4EBA\u7ED9730\u4E2A14\u5C81\u4EE5\u4E0B\u65E0\u75BE\u75C5\u7684\u513F\u7AE5\u505A\u8D85\u58F0\u68C0\u6D4B\uFF0C\u7ED3\u679C**97%**\u7684\u5B69\u5B50\u68C0\u6D4B\u5230\u4E86\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\uFF0C\u5C24\u5176\u662F\u57281-4\u5C81\u7684\u5B69\u5B50\u3002[1] \u6240\u4EE5\u5B69\u5B50\u505A\u8D85\u58F0\u770B\u5230\u4E86\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u662F\u5F88\u6B63\u5E38\u7684\uFF0C\u4E0D\u4EE3\u8868\u6709\u6DCB\u5DF4\u7ED3\u708E\u3002</p><p><strong>\u600E\u6837\u624D\u7B97\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u5462\uFF1F</strong></p><p>\u548C\u4F53\u8868\u7684\u6DCB\u5DF4\u7ED3\u53EF\u4EE5\u89C2\u5BDF\u548C\u89E6\u6478\u611F\u77E5\u5C40\u90E8\u6DCB\u5DF4\u7ED3\u5F62\u6001\u53D8\u5316\u4E0D\u4E00\u6837\u7684\u662F\uFF0C\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u5728\u809A\u5B50\u91CC\uFF0C\u6211\u4EEC\u9694\u7740\u809A\u76AE\u65E0\u6CD5\u89C2\u5BDF\u548C\u611F\u77E5\u5B83\u4EEC\u7684\u5F62\u6001\uFF0C\u540C\u65F6\u4E5F\u6CA1\u6709\u4EC0\u4E48\u7279\u5F81\u6027\u7684\u4E34\u5E8A\u8868\u73B0\uFF0C\u6240\u4EE5\u8BCA\u65AD\u4E0A\u4E00\u662F\u9760\u6392\u9664\u5176\u5B83\u7684\u5668\u8D28\u6027\u75C5\u53D8\uFF0C\u4E8C\u662F\u4F9D\u9760\u8D85\u58F0\u3001CT\u7B49\u5F71\u50CF\u68C0\u67E5\u5BF9\u6DCB\u5DF4\u7ED3\u5927\u5C0F\u548C\u5F62\u6001\u7684\u5224\u65AD\u3002</p><p>\u800C\u76EE\u524D\uFF0C\u65E0\u8BBA\u662F\u56FD\u5185\u8FD8\u662F\u56FD\u5916\uFF0C\u5BF9\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u773C\u4ECD\u6CA1\u6709\u975E\u5E38\u7EDF\u4E00\u7684\u8BCA\u65AD\u6807\u51C6\uFF0C\u6709\u7684\u8BA4\u4E3A\u6DCB\u5DF4\u7ED3\u77ED\u5F84\u226510mm\u53EF\u4EE5\u8BCA\u65AD\uFF0C\u6709\u7684\u8BA4\u4E3A\u53F3\u4E0B\u8179\u6709\u22653\u679A\u22655mm\u6DCB\u5DF4\u7ED3\u53EF\u4EE5\u8BCA\u65AD[2]\uFF0C\u4E5F\u6709\u7684\u8BA4\u4E3A\u8D85\u58F0\u53D1\u73B0\u22653\u679A\u77ED\u5F84\u22658mm\u7684\u6DCB\u5DF4\u7ED3\uFF0C\u800C\u6CA1\u6709\u5176\u5B83\u660E\u786E\u708E\u75C7\u75C5\u7076\u7684\u624D\u7B97[3]\u3002\u8FD9\u4E9B\u8FD8\u662F\u5F71\u50CF\u8BCA\u65AD\u7684\u53C2\u8003\uFF0C\u800C\u5B9E\u9645\u7684\u8BCA\u65AD\u80AF\u5B9A\u662F\u9700\u8981\u7ED3\u5408\u5B69\u5B50\u7684\u75C7\u72B6\uFF0C\u5982\u679C\u6CA1\u6709\u8179\u75DB\u6216\u53D1\u70E7\u75C7\u72B6\uFF0C\u54EA\u6015\u8D85\u58F0\u7B26\u5408\u4E0A\u9762\u7684\u8FD9\u4E9B\u6807\u51C6\uFF0C\u4E5F\u4E0D\u5E94\u8BE5\u8BCA\u65AD\u3002</p><p>\u8BCA\u65AD\u6807\u51C6\u4E0D\u662F\u5F88\u660E\u786E\uFF0C\u800C\u4E14\u6807\u51C6\u4E0D\u7EDF\u4E00\uFF0C\u8FD9\u5E94\u8BE5\u662F\u5BFC\u81F4\u8FC7\u5EA6\u8FD9\u4E2A\u95EE\u9898\u88AB\u8FC7\u5EA6\u8BCA\u65AD\u548C\u6CBB\u7597\u7684\u4E3B\u8981\u539F\u56E0\u3002</p><p><strong>\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u8981\u5403\u6297\u751F\u7D20\u5417\uFF1F</strong></p><p>\u90A3\u6211\u4EEC\u7528\u6700\u4E25\u683C\u7684\u8BCA\u65AD\u6807\u51C6\uFF0C\u8BCA\u65AD\u4E86\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\uFF0C\u662F\u5426\u4E00\u5B9A\u8981\u5403\u6297\u751F\u7D20\u5462\uFF1F</p><p>\u6211\u4EEC\u77E5\u9053\u6297\u751F\u7D20\u662F\u9488\u5BF9\u7EC6\u83CC\u611F\u67D3\u7684\uFF0C\u8981\u4E0D\u8981\u5403\u4E3B\u8981\u770B\u7EC6\u83CC\u611F\u67D3\u7684\u53EF\u80FD\u6027\u6709\u591A\u5927\uFF0C\u4EE5\u53CA\u5403\u4E86\u6709\u591A\u5C11\u6536\u76CA\u3002</p><p>\u4ECE\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u8282\u91CC\u57F9\u517B\u51FA\u7684\u5FAE\u751F\u7269\uFF0C\u65E2\u6709\u7EC6\u83CC\uFF0C\u4E5F\u6709\u75C5\u6BD2\uFF0C\u800C\u4E14\u5F88\u591A\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u90FD\u662F\u51FA\u73B0\u5728\u4E0A\u547C\u5438\u9053\u611F\u67D3\u4E4B\u540E\uFF0C\u5F88\u591A\u533B\u751F\u4E5F\u8BA4\u4E3A\u8FD9\u662F\u75C5\u6BD2\u611F\u67D3\u4E4B\u540E\u6DCB\u5DF4\u7CFB\u7EDF\u7684\u4E00\u4E2A\u53CD\u5E94\uFF0C\u6240\u4EE5\u5E76\u4E0D\u662F\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u90FD\u662F\u7EC6\u83CC\u611F\u67D3\u5F15\u8D77\u7684\uFF0C\u81EA\u7136\u4E0D\u9700\u8981\u90FD\u5403\u6297\u751F\u7D20\u3002</p><p>\u548C\u8BCA\u65AD\u6807\u51C6\u4E0D\u6E05\u6670\u4E00\u6837\uFF0C\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u7684\u6CBB\u7597\u65B9\u6848\u4E5F\u4E0D\u662F\u5F88\u7EDF\u4E00\uFF0C\u8FD9\u4E5F\u662F\u548C\u8FD9\u4E2A\u75C5\u7684\u7814\u7A76\u4E0D\u591F\u5145\u5206\u6709\u5173\uFF0C\u6BD4\u5982\u7EC6\u83CC\u611F\u67D3\u7684\u6982\u7387\u6709\u591A\u9AD8\u5E76\u4E0D\u662F\u5F88\u6E05\u695A\uFF0C\u6240\u4EE5\u54EA\u6015\u533B\u751F\u77E5\u9053\u80BF\u5927\u7684\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u5E76\u4E0D\u7B49\u4E8E\u7EC6\u83CC\u611F\u67D3\uFF0C\u4F46\u53EA\u8981\u5B69\u5B50\u8179\u75DB\u75C7\u72B6\u6BD4\u8F83\u91CD\uFF0C\u53C8\u4E0D\u50CF\u9611\u5C3E\u708E\u7B49\u9700\u8981\u624B\u672F\u5904\u7406\u7684\u95EE\u9898\uFF0C\u53EF\u80FD\u4F1A\u5F00\u4E0A\u6297\u751F\u7D20\u3002</p><p>\u6BD4\u8F83\u8FC7\u7684\u662F\uFF0C\u6709\u7684\u533B\u751F\u6839\u672C\u4E0D\u7EFC\u5408\u5206\u6790\u75C5\u60C5\uFF0C\u809A\u5B50\u75DB\u7684\u5B69\u5B50\uFF0C\u53EA\u8981\u8D85\u58F0\u770B\u5230\u4E86\u6DCB\u5DF4\u7ED3\uFF0C\u5C31\u7ED9\u5F00\u4E0A\u4E86\u6297\u751F\u7D20\uFF0C\u8FD9\u4E5F\u662F\u5BFC\u81F4\u8FD9\u4E2A\u75C5\u88AB\u8FC7\u5EA6\u8BCA\u65AD\u548C\u8FC7\u5EA6\u6CBB\u7597\u7684\u539F\u56E0\u3002</p><p><strong>\u603B\u7ED3</strong></p><p>\u56E0\u4E3A\u8D85\u58F0\u8BBE\u5907\u7684\u666E\u53CA\uFF0C\u8D8A\u6765\u8D8A\u591A\u8179\u75DB\u7684\u5B69\u5B50\u53BB\u505A\u4E86\u8D85\u58F0\uFF0C\u4E5F\u8BA9\u8D8A\u6765\u8D8A\u591A\u7684\u5B69\u5B50\u88AB\u53D1\u73B0\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\uFF0C\u800C\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u7684\u8BCA\u65AD\u548C\u6CBB\u7597\u6807\u51C6\u4E0D\u591F\u6E05\u6670\u3002\u800C\u513F\u7AE5\u8179\u75DB\u7684\u539F\u56E0\u5224\u65AD\u53C8\u6709\u4E00\u5B9A\u56F0\u96BE\uFF0C\u5728\u627E\u4E0D\u5230\u5176\u5B83\u660E\u786E\u75C5\u56E0\u7684\u60C5\u51B5\u4E0B\uFF0C\u5F80\u5F80\u4F1A\u5957\u4E0A\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u708E\u7136\u540E\u7ED9\u4E88\u6297\u611F\u67D3\u6CBB\u7597\uFF0C\u5BFC\u81F4\u4E86\u5F88\u591A\u8FC7\u5EA6\u8BCA\u65AD\u548C\u6CBB\u7597\u3002</p><p>\u56E0\u4E3A\u6297\u751F\u7D20\u7684\u4F7F\u7528\u662F\u7531\u533B\u751F\u6765\u5B9A\u7684\uFF0C\u8179\u75DB\u4E5F\u786E\u5B9E\u5B58\u5728\u9700\u8981\u6297\u611F\u67D3\u6CBB\u7597\u7684\u60C5\u51B5\uFF0C\u6240\u4EE5\u5B69\u5B50\u809A\u5B50\u75DB\uFF0C\u505A\u8D85\u58F0\u770B\u5230\u4E86\u6DCB\u5DF4\u7ED3\uFF0C\u8981\u4E0D\u8981\u7528\u6297\u751F\u7D20\uFF0C\u5E76\u4E0D\u662F\u5BB6\u957F\u81EA\u5DF1\u80FD\u5224\u65AD\u7684\uFF0C\u8FD8\u662F\u53EA\u80FD\u542C\u533B\u751F\u7684\u3002\u8981\u907F\u514D\u8FC7\u5EA6\u6CBB\u7597\uFF0C\u6839\u672C\u8FD8\u662F\u4F9D\u8D56\u4E8E\u533B\u751F\u4EEC\u5BF9\u8FD9\u4E2A\u75BE\u75C5\u6709\u4E86\u66F4\u89C4\u8303\u7684\u8BCA\u65AD\u548C\u6CBB\u7597\u6807\u51C6\u3002</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p>[1]\u738B\u6587\u521A\uFF0C\u7530\u6656\u7B49 \u6B63\u5E38\u5C0F\u513F\u80A0\u7CFB\u819C\u6DCB\u5DF4\u7ED3\u5F69\u8D85\u6D4B\u91CF\u503C\u5206\u6790 \u5357\u65B9\u533B\u79D1\u5927\u5B66\u5B66\u62A5</p><p>[2]https://www.ncbi.nlm.nih.gov/pubmed/11906862</p><p>[3]https://www.ncbi.nlm.nih.gov/pubmed/28261620</p>',28),_={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627273&idx=1&sn=f4fe991c87ad48a01690adc1c4c69216&chksm=8b5cb6f3bc2b3fe5e47ad2e8d452ef01bbe24c7fafb2c6a07a3655df148cf12e3035727145bb&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function f(l,b){const e=n("ExternalLinkIcon");return t(),r(o,null,[m,p("p",null,[p("a",_,[h,a(e)])])],64)}var u=d(i,[["render",f]]);export{u as default};
