import{r as a,o as r,c as t,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=c('<h3 id="\u62C9\u809A\u5B50-\u8981\u5403\u8499\u8131\u77F3\u5417" tabindex="-1"><a class="header-anchor" href="#\u62C9\u809A\u5B50-\u8981\u5403\u8499\u8131\u77F3\u5417" aria-hidden="true">#</a> \u62C9\u809A\u5B50\uFF0C\u8981\u5403\u8499\u8131\u77F3\u5417\uFF1F</h3><p>\u5B69\u5B50\u62C9\u809A\u5B50\u53BB\u533B\u9662\uFF0C\u57FA\u672C\u90FD\u4F1A\u88AB\u5F00\u4E00\u4E2A\u53EB\u8499\u8131\u77F3\u6563\u7684\u836F\u7269\uFF0C\u5B83\u548C\u76CA\u751F\u83CC\u4E00\u6837\uFF0C\u51E0\u4E4E\u662F\u6CBB\u7597\u513F\u7AE5\u8179\u6CFB\u7684\u6807\u914D\u3002</p><p>\u8499\u8131\u77F3\u662F\u4E00\u79CD\u5929\u7136\u77FF\u7269\u571F\uFF0C\u4E0D\u80FD\u88AB\u6D88\u5316\u5438\u6536\u3002\u5173\u4E8E\u5176\u836F\u7406\uFF0C\u901A\u5E38\u662F\u8BF4\u8499\u8131\u77F3\u80FD\u5BF9\u6D88\u5316\u9053\u5185\u7684\u75C5\u6BD2\u3001\u75C5\u83CC\u53CA\u6BD2\u7D20\u4EA7\u751F\u5438\u6536\u548C\u56FA\u5B9A\u7684\u4F5C\u7528\uFF1B\u80FD\u589E\u52A0\u6C34\u7535\u89E3\u8D28\u7684\u5438\u6536\uFF0C\u5BF9\u6D88\u5316\u9053\u7C98\u819C\u6709\u8986\u76D6\u4FDD\u62A4\u4F5C\u7528\u3002</p><p>\u4F46\u8499\u8131\u77F3\u5BF9\u4E8E\u8179\u6CFB\u5230\u5E95\u6709\u6CA1\u6709\u7528\uFF1F\u6709\u4EC0\u4E48\u7528\uFF1F\u6709\u591A\u5927\u4F5C\u7528\u5462\uFF1F\u5F88\u591A\u5BB6\u957F\u90FD\u6709\u7591\u8651\u3002</p><p>\u5C24\u5176\u662F\u4E16\u754C\u536B\u751F\u7EC4\u7EC7\u57282006\u5E74\u7684\u4E00\u4EFD\u6587\u4EF6\u91CC\uFF0C\u5BF9\u4E8E\u5438\u9644\u5242\u7ED9\u51FA\u8FC7\u4E0B\u9762\u8FD9\u6837\u7684\u610F\u89C1,\u8BA9\u5F88\u591A\u533B\u751F\u5BF9\u8FD9\u4E2A\u836F\u7269\u4E5F\u6709\u7591\u8651\u3002</p><p>\u60F3\u60F3\u4E5F\u662F\uFF0C\u6025\u6027\u8179\u6CFB\u7684\u5B69\u5B50\u4E00\u5929\u4EA7\u751F\u7684\u6D88\u5316\u6DB2\u51E0\u5343\u6BEB\u5347\uFF0C\u6309\u7167\u76EE\u524D\u7684\u8BF4\u660E\u4E66\u7684\u7528\u6CD5\uFF0C\u5373\u4FBF\u6309\u7167\u6700\u5927\u91CF\u4F7F\u7528\uFF0C\u6BCF\u5929\u5403\u8FDB\u53BB\u7684\u8499\u8131\u77F3\u662F9\u514B\uFF0C\u628A9\u514B\u7C89\u672B\u6492\u5728\u51E0\u5343\u6BEB\u5347\u7684\u6C34\u91CC\uFF0C\u53BB\u628A\u75C5\u6BD2\u7EC6\u83CC\u548C\u6BD2\u7D20\u5438\u6536\u6389\uFF0C\u8FD9\u6709\u70B9\u8D85\u4E4E\u6211\u7684\u60F3\u8C61\u529B\u3002</p><p>\u540C\u6837\uFF0C\u4EBA\u4F53\u6D88\u5316\u9053\u6709\u51E0\u7C73\u957F\uFF0C\u6D88\u5316\u9053\u5185\u90FD\u662F\u76B1\u58C1\uFF0C\u94FA\u5C55\u5F00\u6765\u7684\u9762\u79EF\u53EF\u4EE5\u8FBE\u5230\u4E0A\u767E\u5E73\u7C73\uFF0C\u628A9\u514B\u7C89\u672B\u6492\u5728100\u5E73\u7C73\u7684\u9762\u79EF\u4E0A\u6765\u8986\u76D6\u4FDD\u62A4\uFF0C\u540C\u6837\u662F\u8D85\u4E4E\u60F3\u8C61\u529B\u7684\uFF0C\u6240\u4EE5\u6211\u4E5F\u4E00\u76F4\u5BF9\u6B64\u7C7B\u836F\u7269\u7684\u4F5C\u7528\u6709\u5F88\u5927\u7591\u8651\u3002</p><p>\u4F46\u7591\u8651\u5F52\u7591\u8651\uFF0C\u903B\u8F91\u4E0A\u5F88\u96BE\u7406\u89E3\u7684\u4E8B\u60C5\uFF0C\u4E0D\u7B49\u4E8E\u4E8B\u5B9E\u4E0A\u4E0D\u5B58\u5728\uFF0C\u8499\u8131\u77F3\u6563\u5BF9\u4E8E\u8179\u6CFB\u6709\u6CA1\u6709\u7528\uFF0C\u8FD8\u662F\u8981\u9760\u8BD5\u9A8C\u7814\u7A76\u6765\u8BF4\u8BDD\u3002</p><p>\u8499\u8131\u77F3\u539F\u6765\u4E3B\u8981\u5728\u6CD5\u56FD\u548C\u4E00\u4E9B\u4E2D\u6B27\u56FD\u5BB6\u4F7F\u7528\uFF0C\u5173\u4E8E\u5B83\u7684\u7814\u7A76\u6709\u4E00\u4E9B\uFF0C\u4F46\u5927\u90E8\u5206\u8D28\u91CF\u4E0D\u9AD8\u30022006\u5E74\u6709\u4EBA\u5BF9\u76F8\u5173\u7814\u7A76\u505A\u4E86\u4E00\u4E2A\u835F\u8403\u5206\u6790\uFF0C\u603B\u5171\u627E\u52309\u4E2A\u968F\u673A\u5BF9\u7167\u7814\u7A76\uFF0C\u67094\u4E2A\u8BBE\u7F6E\u4E86\u5B89\u6170\u5242\u5BF9\u7167\uFF0C\u5BF9\u6CBB\u7597\u6548\u679C\u7684\u8BC4\u5224\u6807\u51C6\u4E5F\u4E0D\u4E00\u81F4\uFF0C\u835F\u8403\u5206\u6790\u7684\u7ED3\u679C\u662F\uFF1A\u8499\u8131\u77F3\u80FD\u7F29\u77ED\u8179\u6CFB\u7684\u6301\u7EED\u65F6\u95F4\u3002</p><p>\u57282014\u5E74\uFF0C\u6B27\u6D32\u513F\u79D1\u80C3\u80A0\u75C5\u5B66\u3001\u809D\u75C5\u5B66\u548C\u8425\u517B\u534F\u4F1A\u8054\u5408\u6B27\u6D32\u513F\u79D1\u611F\u67D3\u75C5\u534F\u4F1A\uFF0C\u53D1\u5E03\u4E86\u4E00\u4EFD\u5173\u4E8E\u513F\u7AE5\u6025\u6027\u80C3\u80A0\u708E\u7684\u6307\u5357\uFF0C\u7EFC\u5408\u4E86\u5F53\u65F6\u7684\u7814\u7A76\u8BC1\u636E\uFF0C\u8FD9\u4EFD\u6307\u5357\u5BF9\u8499\u8131\u77F3\u7684\u610F\u89C1\u4E3A<strong>\u53EF\u7528\u4E8E\u513F\u7AE5\u6025\u6027\u80C3\u80A0\u708E\u7684\u6CBB\u7597(\u2161\uFF0CB)(\u5F31\u63A8\u8350\uFF0C\u4E2D\u7B49\u8D28\u91CF\u8BC1\u636E)</strong></p><p>\u6B64\u540E\u57282015\u5E74\uFF0C \u5BF9\u4E8E\u8499\u8131\u77F3\u6563\uFF0C\u6709\u4E86\u4E00\u4EFD\u65B0\u7684\u835F\u8403\u5206\u6790\uFF0C\u7EFC\u5408\u4E86\u4E00\u4E9B\u65B0\u7684\u7814\u7A76\uFF0C\u8FD9\u4EFD\u835F\u8403\u5206\u6790\u7684\u610F\u89C1\u4E3A\uFF1A\u8499\u8131\u77F3\u5BF9\u4E8E\u513F\u7AE5\u6025\u6027\u8179\u6CFB\u53EF\u80FD\u6709\u7528\uFF0C\u4F46\u76EE\u524D\u7684\u8BC1\u636E\u4F9D\u7136\u662F\u4F4E\u8D28\u91CF\u7684\uFF0C\u5728\u7ED9\u51FA\u66F4\u5F3A\u7684\u63A8\u8350\u4E4B\u524D\u9700\u8981\u6709\u66F4\u9AD8\u8D28\u91CF\u7684\u7814\u7A76\u3002</p><p>\u5728\u4E0A\u4E2A\u6708\uFF0C\u4E5F\u5C31\u662F2016\u5E747\u6708\uFF0C\u6211\u56FD\u7684\u300A\u4E2D\u534E\u513F\u79D1\u6742\u5FD7\u300B\u53D1\u8868\u4E86\u4E00\u4EFD\u300A\u4E2D\u56FD\u513F\u7AE5\u6025\u6027\u611F\u67D3\u6027\u8179\u6CFB\u75C5\u4E34\u5E8A\u5B9E\u8DF5\u6307\u5357\u300B\uFF0C\u8FD9\u662F\u56FD\u5185\u5173\u4E8E\u513F\u7AE5\u8179\u6CFB\u7684\u6700\u65B0\u6700\u6743\u5A01\u7684\u6307\u5357\u3002</p><p>\u6211\u56FD\u53D1\u5E03\u7684\u8FD9\u4EFD\u6700\u65B0\u6307\u5357\uFF0C\u5173\u4E8E\u8499\u8131\u77F3\u5F15\u7528\u7684\u53C2\u8003\u6587\u732E\u6709\u4E09\u7BC7\uFF0C\u4E00\u7BC7\u662F1993\u5E74\u5728\u57C3\u53CA\u7684\u4E00\u9879\u7814\u7A76\uFF0C\u4E00\u7BC7\u662F2009\u5E74\u6CD5\u56FD\u4EBA\u5728\u79D8\u9C81\u548C\u9A6C\u6765\u897F\u4E9A\u505A\u7684\u7814\u7A76\u3002\u518D\u8FC7\u6765\u7684\u4E00\u7BC7\u6709\u70B9\u5947\u602A\uFF0C\u4E0D\u662F2015\u5E74\u7684\u65B0\u7684\u835F\u8403\u7814\u7A76\uFF0C\u800C\u662F2006\u5E74\u7684\u65E7\u7684\u835F\u8403\u5206\u6790\u3002\u6700\u540E\u7684\u63A8\u8350\u610F\u89C1\u662F\uFF1A<strong>\u8499\u8131\u77F3\u6CBB\u7597\u513F\u7AE5\u6025\u6027\u6C34\u6837\u8179\u6CFB\uFF0C\u53EF\u4EE5\u7F29\u77ED\u8179\u6CFB\u75C5\u7A0B,\u51CF\u5C11\u8179\u6CFB\u6392\u4FBF\u6B21\u6570\u548C\u91CF,\u63D0\u9AD8\u6CBB\u6108\u7387\u3002\u6700\u540E\u7ED9\u7684\u63A8\u8350\u7EA7\u522B\u662F\u6700\u9AD8\u7EA7\u522B\u7684A\u7EA7\u3002</strong></p><p>\u53EF\u4EE5\u60F3\u8C61\uFF0C\u8FD9\u4EFD\u65B0\u6307\u5357\u51FA\u53F0\u540E\uFF0C\u8499\u8131\u77F3\u5BF9\u4E8E\u8179\u6CFB\u7684\u5B69\u5B50\u5E94\u8BE5\u4F1A\u7528\u7684\u66F4\u5E7F\u6CDB\u3002\u5199\u6307\u5357\u7684\u4EBA\u5BF9\u4E8E\u8BC1\u636E\u7684\u5224\u65AD\u53EF\u80FD\u6709\u81EA\u5DF1\u7684\u6807\u51C6\uFF0C\u4F46\u5728\u65B0\u7684\u8BC1\u636E\u9762\u524D\u53C2\u800310\u5E74\u524D\u65E7\u7684\u8BC1\u636E\uFF0C\u57FA\u4E8E\u540C\u6837\u7684\u7814\u7A76\u8BC1\u636E\uFF0C\u7ED9\u51FA\u548C\u522B\u7684\u6743\u5A01\u673A\u6784\u4E0D\u540C\u7684\u63A8\u8350\u7EA7\u522B\uFF0C\u8FD8\u662F\u633A\u8BA9\u4EBA\u56F0\u60D1\u3002</p><p>\u603B\u7684\u6765\u8BF4\uFF0C\u5BF9\u4E8E\u8499\u8131\u77F3\uFF0C\u6211\u4EEC\u8981\u77E5\u9053\u7684\u662F\uFF0C\u6709\u4E00\u4E9B\u7814\u7A76\u8BA4\u4E3A\u5B83\u80FD\u7F29\u77ED\u6025\u6027\u8179\u6CFB\u6301\u7EED\u65F6\u95F4\uFF0C\u76EE\u524D\u4E5F\u6CA1\u53D1\u73B0\u4EC0\u4E48\u4E0D\u826F\u53CD\u5E94\uFF0C\u57FA\u4E8E\u5F53\u524D\u7684\u7814\u7A76\uFF0C\u5982\u679C\u4E0D\u8003\u8651\u8D39\u7528\u95EE\u9898\u53EF\u4EE5\u7528\uFF0C\u4F46\u5F53\u524D\u7684\u7814\u7A76\u8D28\u91CF\u4E0D\u592A\u9AD8\u3002(\u9898\u56FE\u6765\u81EAPixabay,\u5728\u5BF9\u8BDD\u6846\u91CC\u56DE\u590D<strong>\u8179\u6CFB</strong>\uFF0C\u53EF\u4E86\u89E3\u5982\u4F55\u5E94\u5BF9\u5B69\u5B50\u8179\u6CFB\u3002)</p><hr><p>\u76F8\u5173\u533B\u751F\u63A8\uFF1A\u89E3\u653E\u519B\u7B2C\u4E5D\u56DB\u533B\u9662\u513F\u79D1\u4E3B\u4EFB\uFF0C\u4ECE\u4E8B\u513F\u79D1\u4E34\u5E8A\u5DE5\u4F5C15\u5E74\u3002</p><p>\u957F\u6309\u4E0B\u9762\u4E8C\u7EF4\u7801\u8BC6\u522B\u5173\u6CE8\uFF0C\u53EF\u5411\u9AD8\u533B\u751F\u54A8\u8BE2\u513F\u7AE5\u5185\u79D1\u5E38\u89C1\u75C5\u3001\u65B0\u751F\u513F\u75BE\u75C5\u53CA\u513F\u7AE5\u4FDD\u5065\u95EE\u9898\u3002</p>',18),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626320&idx=1&sn=a64f4a7fc9c7a725da5ec555dc2b365c&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function m(f,x){const p=a("ExternalLinkIcon");return r(),t(o,null,[_,e("p",null,[e("a",l,[h,n(p)])])],64)}var b=d(i,[["render",m]]);export{b as default};
