import{r as a,o as c,c as r,a as e,b as t,F as n,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=o('<h3 id="\u8981\u4E0D\u8981\u8BA9\u8001\u4EBA\u5E2E\u5FD9\u5E26\u5B69\u5B50" tabindex="-1"><a class="header-anchor" href="#\u8981\u4E0D\u8981\u8BA9\u8001\u4EBA\u5E2E\u5FD9\u5E26\u5B69\u5B50" aria-hidden="true">#</a> \u8981\u4E0D\u8981\u8BA9\u8001\u4EBA\u5E2E\u5FD9\u5E26\u5B69\u5B50\uFF1F</h3><p>\u56E0\u4E3A\u62C5\u5FC3\u4EBA\u5C71\u4EBA\u6D77\uFF0C\u4EE5\u524D\u5728\u533B\u9662\u4E0A\u73ED\u65F6\uFF0C\u6211\u90FD\u4F1A\u9009\u62E9\u5728\u56FD\u5E86\u524D\u4F11\u5047\u56FD\u5E86\u4E0A\u73ED\u3002\u51FA\u6765\u8FD9\u51E0\u5E74\uFF0C\u4E5F\u6CA1\u60F3\u8FC7\u5728\u56FD\u5E86\u671F\u95F4\u51FA\u95E8\uFF0C\u4F46\u4ECA\u5E74\u611F\u89C9\u7279\u522B\u7684\u7D2F\uFF0C\u5F88\u60F3\u5F7B\u5E95\u653E\u7A7A\u4E00\u4E0B\u5927\u8111\u3002</p><p>\u5047\u671F\u5728\u5BB6\u5728\u7535\u8111\u524D\u770B\u4E862\u5929\u7535\u5F71\u540E\uFF0C\u4E34\u65F6\u51B3\u5B9A\u51FA\u95E8\u8D70\u8D70\uFF0C\u5750\u4E86\u4E2A\u77ED\u9014\u52A8\u8F66\u79BB\u5F00\u6DF1\u5733\uFF0C\u627E\u5230\u4E00\u7247\u5B89\u9759\u7684\u6D77\uFF0C\u5728\u9759\u8C27\u4E2D\u5446\u4E86\u4E24\u5929\u3002</p><p>\u90A3\u5929\u52A8\u8F66\u4E0A\uFF0C\u4E00\u4E2A\u521A\u5B66\u4F1A\u8D70\u8DEF\u7684\u5B69\u5B50\u7531\u4E00\u4E2A\u8001\u4EBA\u5E26\u7740\uFF0C\u5B69\u5B50\u4ECE\u8F66\u53A2\u8FD9\u5934\u6447\u5230\u90A3\u5934\uFF0C\u4E00\u4F1A\u54ED\uFF0C\u4E00\u4F1A\u7B11\uFF0C\u4E00\u4F1A\u8DD1\uFF0C\u4E00\u4F1A\u8981\u62B1\uFF0C\u8001\u4EBA\u8EAB\u4E0A\u80CC\u7740\u5305\uFF0C\u4E00\u624B\u63A8\u7740\u5A74\u513F\u8F66\uFF0C\u6709\u65F6\u624B\u91CC\u8FD8\u62FF\u7740\u52FA\u5B50\u3001\u82F9\u679C\uFF0C\u8DDF\u7740\u4ECE\u8F66\u53A2\u8FD9\u5934\u8D70\u5230\u90A3\u5934\uFF0C\u6765\u6765\u56DE\u56DE\u51E0\u4E2A\u5C0F\u65F6\u5C31\u6CA1\u505C\u6B47\u8FC7\u3002</p><p>\u6628\u665A\u6211\u5728\u5FAE\u535A\u4E0A\u5206\u4EAB\u4E86\u8FD9\u70B9\u611F\u60F3\uFF0C\u7ED3\u679C\u8BC4\u8BBA\u91CC\u4E00\u7FA4\u4EBA\u4E3A\u8981\u4E0D\u8981\u8BA9\u8001\u4EBA\u5E26\u5B69\u5B50\u7684\u95EE\u9898\u5435\u5F97\u4E0D\u53EF\u5F00\u4EA4\uFF0C\u6070\u597D\u6211\u4EE5\u524D\u5199\u8FC7\u4E00\u7BC7\u6587\u7AE0\uFF0C\u5206\u4EAB\u7ED9\u5927\u5BB6\u3002</p><hr><p>\u4F5C\u4E3A\u4E00\u4E2A\u5927\u90E8\u5206\u8BFB\u8005\u662F\u5B69\u5B50\u5988\u5988\u7684\u516C\u4F17\u53F7\uFF0C\u540E\u53F0\u770B\u8FC7\u592A\u591A\u5BF9\u8001\u4EBA\u5410\u69FD\u7684\u7559\u8A00\uFF0C\u5A46\u5AB3\u77DB\u76FE\u4F3C\u4E4E\u4E5F\u662F\u5988\u5988\u7FA4\u91CC\u6C38\u6052\u7684\u4E3B\u9898\u4E4B\u4E00\uFF0C\u8FD9\u4E9B\u77DB\u76FE\u5F88\u591A\u662F\u6765\u81EA\u5BF9\u5B69\u5B50\u517B\u80B2\u95EE\u9898\u4E0A\u7684\u5206\u6B67\u3002</p><p>\u90A3\u8981\u4E0D\u8981\u8BA9\u8001\u4EBA\u53C2\u4E0E\u5E26\u5B69\u5B50\uFF1F</p><p>\u8FD9\u4E2A\u95EE\u9898\u8BA9\u6211\u60F3\u8D77\u51E0\u5E74\u524D\u6CBB\u8FC7\u7684\u4E00\u4E2A\u5B69\u5B50\u3002 \u5B69\u5B50\u521A\u51FA\u751F\u65F6\u809A\u5B50\u80C0\uFF0C\u4E0D\u6392\u5927\u4FBF\uFF0C\u6000\u7591\u5DE8\u7ED3\u80A0\uFF0C\u5EFA\u8BAE\u8FDB\u4E00\u6B65\u505A\u68C0\u67E5\uFF0C\u5B69\u5B50\u7236\u6BCD\u540C\u610F\u505A\uFF0C\u4F46\u5B69\u5B50\u5976\u5976\u89C9\u5F97\uFF0C\u5F53\u5E74\u5B69\u5B50\u7236\u4EB2\u5F53\u5E74\u4E5F\u662F\u8FD9\u6837\uFF0C\u540E\u6765\u5403\u4E2D\u836F\u5C31\u597D\u4E86\u3002</p><p>\u5976\u5976\u5728\u5BB6\u6BD4\u8F83\u5F3A\u52BF\uFF0C\u6240\u4EE5\u5C31\u6CA1\u8BA9\u505A\u68C0\u67E5\uFF0C\u8BA9\u628A\u5B69\u5B50\u5E26\u56DE\u5BB6\u53BB\u5403\u4E2D\u836F\uFF0C\u4E2D\u836F\u4E00\u5403\u5C31\u662F\u4E24\u5E74\uFF0C\u4F46\u5927\u4FBF\u8FD8\u662F\u4E00\u4E2A\u661F\u671F\u624D\u62C9\u4E00\u6B21\uFF0C\u5B69\u5B50\u809A\u5B50\u4E5F\u8D8A\u80C0\u8D8A\u5927\uFF0C\u4E0D\u5F97\u5DF2\u518D\u56DE\u5230\u533B\u9662\u3002</p><p>\u7EC8\u4E8E\u505A\u4E86\u94A1\u704C\u80A0\u68C0\u67E5\uFF0C\u7ED3\u679C\u53D1\u73B0\u7ED3\u80A0\u7C97\u5F97\u50CF\u5145\u6C14\u7684\u8F6E\u80CE\uFF0C\u662F\u4E00\u4E2A\u5178\u578B\u7684\u5DE8\u7ED3\u80A0\uFF0C\u53EA\u597D\u505A\u624B\u672F\uFF0C\u624B\u672F\u4E2D\u53D1\u73B0\u6240\u6709\u7684\u7ED3\u80A0\u90FD\u5DF2\u7ECF\u80A5\u539A\u6269\u5F20\uFF0C\u90FD\u6CA1\u80FD\u4FDD\u7559\u4E0B\u6765\u3002</p><p>\u5982\u679C\u65E9\u4E00\u70B9\u8BCA\u65AD\uFF0C\u5B69\u5B50\u53EF\u4EE5\u5C11\u53D7\u5F88\u957F\u65F6\u95F4\u7684\u7F6A\uFF0C\u5982\u679C\u65E9\u4E00\u70B9\u624B\u672F\uFF0C\u4E5F\u8BB8\u5C31\u80FD\u4FDD\u7559\u4E00\u4E9B\u7ED3\u80A0\uFF0C\u6062\u590D\u4E5F\u80FD\u66F4\u597D\u4E00\u4E9B\uFF0C\u4F46\u6CA1\u6709\u5982\u679C\u3002 \u7ECF\u8FC7\u8FD9\u4EF6\u4E8B\uFF0C\u5B69\u5B50\u5976\u5976\u5C31\u4E0D\u600E\u4E48\u53C2\u4E0E\u5B69\u5B50\u7684\u517B\u80B2\u95EE\u9898\u4E86\u3002</p><p>\u8FD9\u662F\u4E00\u4E2A\u8001\u4EBA\u53C2\u4E0E\u5B69\u5B50\u517B\u80B2\u6BD4\u8F83\u6781\u7AEF\u7684\u4F8B\u5B50\uFF0C\u4E0D\u5177\u6709\u4EE3\u8868\u6027\uFF0C\u4F46\u4E0D\u53EF\u5426\u8BA4\u7684\u662F\uFF0C\u7C7B\u4F3C\u7684\u89C2\u5FF5\u51B2\u7A81\u9020\u6210\u7684\u77DB\u76FE\uFF0C\u56F0\u6270\u7740\u5F88\u591A\u5BB6\u5EAD\u3002</p><p>\u4EBA\u7684\u89C2\u5FF5\u4F1A\u53D7\u793E\u4F1A\u73AF\u5883\u548C\u6559\u80B2\u80CC\u666F\u7684\u5F71\u54CD\uFF0C\u4E2D\u56FD\u793E\u4F1A\u5728\u8FC7\u53BB\u7684\u51E0\u5341\u5E74\u91CC\u98DE\u901F\u53D1\u5C55\uFF0C\u65E5\u65B0\u6708\u5F02\u7684\u4E0D\u4EC5\u4EC5\u662F\u7269\u8D28\u6761\u4EF6\uFF0C\u8FD8\u6709\u77E5\u8BC6\u548C\u89C2\u5FF5\uFF0C\u8FD9\u5FC5\u7136\u9020\u6210\u4E24\u4EE3\u4EBA\u5728\u517B\u80B2\u5B69\u5B50\u95EE\u9898\u4EA7\u751F\u4E0D\u540C\u770B\u6CD5\u3002</p><p>\u8001\u4EBA\u4EEC\u5F53\u5E74\u4E5F\u6CA1\u6709\u673A\u4F1A\u5F97\u5230\u66F4\u597D\u7684\u79D1\u5B66\u548C\u6587\u5316\u6559\u80B2\uFF0C\u6240\u4EE5\u66F4\u559C\u6B22\u7528\u4EE5\u524D\u5728\u7F3A\u533B\u5C11\u836F\u5E74\u4EE3\u91CC\u79EF\u7D2F\u7684\u7ECF\u9A8C\u53BB\u5E26\u5B69\u5B50\u3002 \u4ED6\u4EEC\u6210\u957F\u5728\u7ECF\u6D4E\u843D\u540E\u7684\u73AF\u5883\u91CC\uFF0C\u4E00\u8F88\u5B50\u5F88\u957F\u65F6\u95F4\u90FD\u7F3A\u8863\u5C11\u5403\uFF0C\u6240\u4EE5\u66F4\u5BB9\u6613\u62C5\u5FC3\u5B69\u5B50\u5403\u4E0D\u9971\u7A7F\u4E0D\u6696\uFF0C\u4F1A\u66F4\u559C\u6B22\u7ED9\u5B69\u5B50\u591A\u5582\u996D\u3001\u8BA9\u5B69\u5B50\u591A\u7A7F\u8863\u3002 \u5F53\u5E74\u6CA1\u6709\u5C3F\u4E0D\u6E7F\u3001\u7A7A\u8C03\u8FD9\u4E9B\u4E1C\u897F\uFF0C\u8001\u4EBA\u4EEC\u4F1A\u89C9\u5F97\u6CA1\u6709\u8FD9\u4E9B\u4E1C\u897F\u6211\u4E5F\u628A\u4F60\u4EEC\u517B\u5927\u4E86\uFF0C\u4E3A\u4EC0\u4E48\u73B0\u5728\u5C31\u975E\u8981\u7528\u5462\uFF1F</p><p>\u89C2\u5FF5\u5F62\u6210\u5F97\u8D8A\u4E45\uFF0C\u4E5F\u5C31\u8D8A\u7262\u56FA\uFF0C\u4E5F\u5C31\u8D8A\u96BE\u4EE5\u6539\u53D8\u3002 \u867D\u7136\u4E0D\u6392\u9664\u6709\u4E0E\u65F6\u4FF1\u8FDB\u3001\u5F00\u653E\u597D\u5B66\u7684\u5976\u5976\u5916\u5A46\u7237\u7237\u5916\u516C\uFF0C\u4E5F\u4F1A\u6709\u62B1\u6B8B\u5B88\u7F3A\u7684\u5E74\u8F7B\u7236\u6BCD\uFF0C\u4F46\u80AF\u5B9A\u90FD\u662F\u5C11\u6570\u3002 \u603B\u4F53\u800C\u8A00\uFF0C\u76F8\u5BF9\u4E8E\u66F4\u5E74\u8F7B\u7236\u6BCD\u4EEC\u800C\u8A00\uFF0C\u8001\u4EBA\u4EEC\u7684\u89C2\u5FF5\u548C\u77E5\u8BC6\u4E0D\u6B63\u786E\u7684\u6982\u7387\u4F1A\u66F4\u5927\u3002</p><p>\u5B69\u5B50\u4ECE\u51FA\u751F\u65F6\u548C\u5988\u5988\u4EEC\u808C\u80A4\u63A5\u89E6\uFF0C\u5438\u542E\u7B2C\u4E00\u53E3\u6BCD\u4E73\uFF0C\u7136\u540E\u5988\u5988\u4EEC\u54C4\u7761\u3001\u6D17\u6FA1\uFF0C\u7238\u7238\u4EEC\u7B2C\u4E00\u6B21\u966A\u7740\u8FC7\u591C\uFF0C\u66F4\u6362\u5C3F\u7247\u7B49\u7B49\uFF0C\u5B69\u5B50\u5F88\u5FEB\u5C31\u80FD\u719F\u6089\u5988\u5988\u548C\u7238\u7238\u7684\u6C14\u606F\uFF0C\u5728\u4ED6\u4EEC\u8EAB\u8FB9\u4E5F\u5F88\u5BB9\u6613\u4EA7\u751F\u5B89\u5168\u611F\u3002</p><p>\u65B0\u624B\u7236\u6BCD\u521A\u5F00\u59CB\u53EF\u80FD\u4F1A\u56E0\u4E3A\u7F3A\u4E4F\u7ECF\u9A8C\u800C\u7D27\u5F20\u7126\u8651\uFF0C\u4F46\u8FD9\u79CD\u7126\u8651\u4E5F\u4F1A\u662F\u4ED6\u4EEC\u5B66\u4E60\u7684\u52A8\u529B\uFF0C\u5F97\u76CA\u4E8E\u4E92\u8054\u7F51\u7684\u53D1\u8FBE\uFF0C\u4E5F\u53EF\u4EE5\u5F88\u4FBF\u6377\u5730\u5F97\u5230\u66F4\u53EF\u9760\u7684\u89C2\u5FF5\u548C\u77E5\u8BC6\u3002 \u76F8\u5BF9\u4E8E\u8001\u4EBA\uFF0C\u5E74\u8F7B\u7684\u7236\u6BCD\u4EEC\u7CBE\u529B\u548C\u4F53\u529B\u4E5F\u66F4\u65FA\u76DB\u3002 \u6240\u4EE5\uFF0C\u5982\u679C\u6761\u4EF6\u5141\u8BB8\uFF0C\u7236\u6BCD\u81EA\u5DF1\u80AF\u5B9A\u662F\u6BD4\u8001\u4EBA\u4EEC\u66F4\u5408\u9002\u7684\u770B\u62A4\u4EBA\uFF0C\u5982\u679C\u6709\u6761\u4EF6\uFF0C\u81EA\u7136\u53EF\u4EE5\u4E0D\u8BA9\u8001\u4EBA\u5E2E\u5FD9\u5E26\u5B69\u5B50\u3002</p><p>\u4F46\u5B69\u5B50\u4E0D\u662F\u751F\u6D3B\u7684\u5168\u90E8\uFF0C\u751F\u4E86\u5B69\u5B50\u5C31\u5168\u804C\u5728\u5BB6\u5E26\u5B69\u5B50\u662F\u4E0D\u592A\u73B0\u5B9E\uFF0C\u800C\u56FD\u5185\u7684\u5E7C\u6258\u673A\u6784\u6BD4\u8F83\u532E\u4E4F\uFF0C\u4E3A\u4E86\u5B69\u5B50\u5C31\u5B8C\u5168\u653E\u5F03\u81EA\u5DF1\u7684\u751F\u6D3B\u548C\u4E8B\u4E1A\uFF0C\u5BF9\u5B69\u5B50\u4E5F\u4E0D\u662F\u4EC0\u4E48\u597D\u7684\u699C\u6837\u3002</p><p>\u5B69\u5B50\u4E5F\u4E0D\u662F\u5BB6\u5EAD\u7684\u5168\u90E8\uFF0C\u4E3A\u4E86\u5B69\u5B50\u5C31\u5B8C\u5168\u5265\u593A\u8001\u4EBA\u4EB2\u8FD1\u5B69\u5B50\u7684\u65F6\u95F4\u4E5F\u662F\u4E0D\u5408\u60C5\u7406\u7684\u3002 \u76F8\u5BF9\u4E8E\u4FDD\u59C6\u6216\u5176\u5B83\u5916\u4EBA\uFF0C\u957F\u8F88\u4EEC\u4E5F\u4F1A\u66F4\u8010\u5FC3\u66F4\u7EC6\u5FC3\uFF0C\u80FD\u7ED9\u5B69\u5B50\u66F4\u591A\u7231\u7684\u5475\u62A4\uFF0C\u4E5F\u6709\u66F4\u591A\u7684\u65F6\u95F4\u966A\u4F34\u5B69\u5B50\uFF0C\u9664\u4E86\u7236\u6BCD\uFF0C\u7956\u7236\u6BCD\u4EEC\u4E5F\u662F\u7167\u770B\u5B69\u5B50\u6700\u597D\u7684\u4EBA\u9009\u3002</p><p>\u6240\u4EE5\uFF0C\u8981\u4E0D\u8981\u8BA9\u8001\u4EBA\u5E26\u5B69\u5B50\u4E0D\u662F\u4E00\u4E2A\u6709\u6807\u51C6\u7B54\u6848\u7684\u95EE\u9898\uFF0C\u6709\u6761\u4EF6\u5C3D\u91CF\u81EA\u5DF1\u591A\u5E26\uFF0C\u5982\u679C\u6CA1\u6709\u6761\u4EF6\u8981\u8001\u4EBA\u6765\u5E2E\u5FD9\uFF0C\u5728\u89C2\u5FF5\u51B2\u7A81\u65F6\u591A\u6C9F\u901A\uFF0C\u5728\u6C9F\u901A\u56F0\u96BE\u65F6\u53EF\u4EE5\u501F\u7528\u53EF\u4FE1\u5EA6\u6BD4\u8F83\u9AD8\u7684\u7B2C\u4E09\u65B9\u7684\u8BF4\u6CD5\uFF0C\u4F1A\u66F4\u5BB9\u6613\u8BF4\u6CD5\u5BF9\u65B9\u3002</p><p>\u5982\u679C\u65E0\u6CD5\u8BF4\u670D\u5F7C\u6B64\uFF0C\u5728\u5F71\u54CD\u5B69\u5B50\u5B89\u5168\u7684\u539F\u5219\u6027\u95EE\u9898\u4E0A\uFF0C\u5EFA\u8BAE\u8FD8\u662F\u81EA\u5DF1\u505A\u4E3B\uFF0C\u6BD5\u7ADF\u5B69\u5B50\u662F\u81EA\u5DF1\u7684\u3002 \u5728\u4E0D\u592A\u8981\u7D27\u7684\u95EE\u9898\u4E0A\uFF0C\u5EFA\u8BAE\u591A\u4E00\u4E9B\u59A5\u534F\u548C\u5BBD\u5BB9\uFF0C\u6BD5\u7ADF\u8001\u4EBA\u4EEC\u5E76\u6CA1\u6709\u5E26\u5B69\u5B50\u7684\u4E49\u52A1\uFF0C\u5E26\u5B69\u5B50\u8FD9\u4E48\u8F9B\u82E6\uFF0C\u613F\u610F\u5E2E\u5FD9\u5E26\u5C31\u5F88\u4E0D\u9519\u4E86\u3002</p><p>\u4EE5\u4E0A\u662F\u6211\u5BF9\u8FD9\u4E2A\u95EE\u9898\u7684\u770B\u6CD5\uFF0C\u4F9B\u5927\u5BB6\u53C2\u8003\u3002</p>',23),f={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651628882&idx=1&sn=5a7e9c29652037ed7b1127fa53c7fd1b&chksm=8b5c8ca8bc2b05be145b36fdfb65a3584adc1edc9bca3914885f0cef23cfad1d5be586722ae9&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function l(b,m){const p=a("ExternalLinkIcon");return c(),r(n,null,[i,e("p",null,[e("a",f,[h,t(p)])])],64)}var k=d(_,[["render",l]]);export{k as default};
