import{r as a,o as c,c as r,a as e,b as t,F as n,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=o('<h3 id="\u7236\u6BCD\u75C5\u4E86-\u5B69\u5B50\u5403\u836F" tabindex="-1"><a class="header-anchor" href="#\u7236\u6BCD\u75C5\u4E86-\u5B69\u5B50\u5403\u836F" aria-hidden="true">#</a> \u7236\u6BCD\u75C5\u4E86\uFF0C\u5B69\u5B50\u5403\u836F</h3><p>\u5173\u4E8E\u7528\u836F\u7684\u79D1\u666E\u6211\u5199\u8FC7\u5F88\u591A\uFF0C\u63D0\u9192\u5927\u5BB6\u907F\u514D\u4E00\u4E9B\u5751\uFF0C\u5F88\u591A\u4EBA\u89C9\u5F97\u6709\u7528\uFF0C\u4F46\u4E5F\u6709\u5E38\u5E38\u770B\u5230\u8FD9\u6837\u7684\u7559\u8A00\uFF1A\u8FD9\u4E2A\u4E0D\u8981\u7528\uFF0C\u90A3\u4E2A\u4E0D\u8981\u7528\uFF0C\u90A3\u5230\u5E95\u8BE5\u7528\u4EC0\u4E48\uFF1F</p><p>\u6BCF\u4E2A\u4EBA\u7684\u75C5\u60C5\u4E0D\u4E00\u6837\uFF0C\u9700\u8981\u7528\u7684\u836F\u81EA\u7136\u4E5F\u53EF\u80FD\u4E0D\u4E00\u6837\uFF0C\u79D1\u666E\u53EF\u4EE5\u8BB2\u4E00\u4E9B\u666E\u904D\u6027\u7684\u533B\u5B66\u539F\u5219\uFF0C\u8BA9\u5927\u5BB6\u907F\u514D\u4E00\u4E9B\u9519\u8BEF\u7684\u9009\u62E9\uFF0C\u4F46\u6307\u671B\u901A\u8FC7\u79D1\u666E\u6765\u6307\u5BFC\u6240\u6709\u7528\u836F\u662F\u4E0D\u73B0\u5B9E\u7684\u3002</p><p>\u90A3\u7528\u836F\u6709\u6CA1\u6709\u4EC0\u4E48\u666E\u904D\u539F\u5219\u53EF\u4EE5\u9075\u5FAA\uFF1F\u5728\u51C6\u5907\u7528\u4E00\u79CD\u836F\u4E4B\u524D\uFF0C\u53EF\u4EE5\u60F3\u4E00\u60F3\u4E0B\u9762\u4E09\u4E2A\u95EE\u9898\uFF1A</p><p>\u6709\u6CA1\u6709\u597D\u5904\uFF1F</p><p>\u662F\u4E0D\u662F\u597D\u5904\u5927\u4E8E\u574F\u5904\uFF1F</p><p>\u662F\u4E0D\u662F\u53EF\u9009\u836F\u91CC\u6536\u76CA/\u98CE\u9669\u6BD4\u6700\u9AD8\uFF1F</p><p>\u5982\u679C\u4E09\u4E2A\u95EE\u9898\u90FD\u662F\u80AF\u5B9A\u7684\uFF0C\u90A3\u5C31\u53EF\u4EE5\u7528\uFF0C\u5982\u679C\u4EFB\u4F55\u4E00\u4E2A\u56DE\u7B54\u662F\u5426\u5B9A\u7684\uFF0C\u5C31\u4E0D\u8981\u7528\u3002</p><p>\u76F8\u4FE1\u6709\u601D\u7EF4\u80FD\u529B\u7684\u4EBA\u90FD\u80FD\u660E\u767D\u4E0A\u9762\u7684\u903B\u8F91\uFF0C\u4F46\u73B0\u5B9E\u5E38\u5E38\u662F\uFF0C\u54EA\u6015\u5B69\u5B50\u5C31\u662F\u4E00\u4E2A\u666E\u901A\u611F\u5192\u5F15\u8D77\u7684\u53D1\u70E7\uFF0C\u5BB6\u957F\u4E5F\u53EF\u80FD\u4F1A\u8D34\u9000\u70ED\u8D34\u3001\u6D17\u6E29\u6C34\u6FA1\u3001\u5403\u9000\u70E7\u836F\u3001\u5403\u4E2D\u836F\u3001\u5403\u6297\u75C5\u6BD2\u836F\u3001\u5403\u6297\u751F\u7D20\uFF0C\u751A\u81F3\u4E0A\u533B\u9662\u6253\u540A\u74F6\u3002</p><p>\u54EA\u6015\u5B69\u5B50\u672C\u6765\u597D\u597D\u7684\uFF0C\u4F46\u6709\u7684\u5BB6\u957F\u603B\u62C5\u5FC3\u5B69\u5B50\u5403\u4E0D\u591F\uFF0C\u5582\u996D\uFF0C\u903C\u4ED6\u4EEC\u591A\u5403\uFF0C\u5B69\u5B50\u5403\u4E0D\u4E0B\u5C31\u8BA4\u4E3A\u662F\u79EF\u98DF\u4E86\uFF0C\u7136\u540E\u770B\u4E2D\u533B\u5403\u4E2D\u836F\uFF0C\u751A\u81F3\u53BB\u6311\u75B3\u79EF\uFF0C\u6311\u5B8C\u4E0D\u5403\u518D\u53BB\u6311\uFF0C\u76F4\u5230\u5B69\u5B50\u4E0D\u6562\u4E0D\u5403\u3002</p><p>\u8FD9\u79CD\u5C31\u53EB\u7236\u6BCD\u75C5\u4E86\uFF0C\u5B69\u5B50\u5403\u836F\u3002</p><p>\u5176\u5B9E\u4F5C\u4E3A\u7236\u6BCD\uFF0C\u90FD\u5E0C\u671B\u5B69\u5B50\u597D\u7684\uFF0C\u4E4B\u6240\u4EE5\u4F1A\u505A\u90A3\u4E9B\u660E\u663E\u9519\u8BEF\u7684\u9009\u62E9\uFF0C\u4E00\u65B9\u9762\u662F\u7F3A\u4E4F\u76F8\u5173\u77E5\u8BC6\uFF0C\u4E0D\u77E5\u9053\u4EC0\u4E48\u662F\u6B63\u5E38\u4EC0\u4E48\u662F\u4E0D\u6B63\u5E38\uFF0C\u4E0D\u77E5\u9053\u4E00\u4E2A\u836F\u7684\u597D\u5904\u5728\u54EA\u91CC\u574F\u5904\u5728\u54EA\u91CC\uFF0C\u8D8A\u662F\u4E0D\u4E86\u89E3\uFF0C\u5C31\u8D8A\u5BB9\u6613\u7126\u8651\uFF0C\u8D8A\u662F\u7126\u8651\uFF0C\u5C31\u8D8A\u60F3\u505A\u70B9\u4EC0\u4E48\u6765\u5B89\u6170\u81EA\u5DF1\u3002</p><p>\u53E6\u4E00\u65B9\u9762\u662F\u56E0\u4E3A\u5173\u5FC3\u5219\u4E71\uFF0C\u54EA\u6015\u5929\u5929\u7ED9\u522B\u4EBA\u5BB6\u5B69\u5B50\u770B\u53D1\u70E7\u54B3\u55FD\u7684\u513F\u79D1\u533B\u751F\uFF0C\u8F6E\u5230\u81EA\u5DF1\u5B69\u5B50\u751F\u75C5\uFF0C\u4E00\u770B\u5230\u5B69\u5B50\u70E6\u8E81\u54ED\u95F9\uFF0C\u4E5F\u5C31\u53EF\u80FD\u516D\u795E\u65E0\u4E3B\uFF0C\u7136\u540E\u53BB\u5C1D\u8BD5\u4E00\u4E9B\u6CA1\u4F9D\u636E\u7684\u836F\u7269\u548C\u6CBB\u7597\u3002</p><p>\u6211\u4EEC\u6216\u591A\u6216\u5C11\u591A\u505A\u8FC7\u8FD9\u6837\u7684\u4E8B\uFF0C\u8981\u60F3\u8FD9\u6837\u7684\u4E8B\u5C11\u4E00\u70B9\uFF0C\u4F5C\u4E3A\u7236\u6BCD\uFF0C\u5728\u533B\u7597\u4F53\u7CFB\u4E0D\u592A\u597D\u7684\u73AF\u5883\u4E0B\uFF0C\u9700\u8981\u591A\u5B66\u4E00\u4E9B\u4E00\u70B9\u513F\u7AE5\u5065\u5EB7\u77E5\u8BC6\uFF0C\u5982\u679C\u628A\u8FD9\u4E2A\u516C\u4F17\u53F7\u7684\u5386\u53F2\u6D88\u606F\u8BFB\u5B8C\u4E86\uFF0C\u76F8\u4FE1\u4F60\u5728\u9762\u5BF9\u5B69\u5B50\u751F\u75C5\u65F6\u90FD\u4F1A\u6DE1\u5B9A\u5F88\u591A\u3002</p><p>\u867D\u7136\u7528\u836F\u5927\u90E8\u5206\u662F\u533B\u751F\u7684\u4E8B\uFF0C\u4F46\u5BB6\u957F\u7684\u610F\u613F\u4E5F\u5BB9\u6613\u5F71\u54CD\u533B\u751F\u7684\u51B3\u7B56\uFF0C\u800C\u4E14\u81EA\u5DF1\u4E70\u836F\u7ED9\u5B69\u5B50\u7528\u7684\u5BB6\u957F\u4E5F\u4E0D\u5C11\u3002\u6240\u4EE5\uFF0C\u5728\u7528\u836F\u4E4B\u524D\uFF0C\u6700\u597D\u4ED4\u7EC6\u9605\u8BFB\u4E00\u4E0B\u836F\u54C1\u8BF4\u660E\u4E66\uFF0C\u56E0\u4E3A\u6240\u6709\u7684\u836F\u54C1\u8BF4\u660E\u4E66\u91CC\u90FD\u4F1A\u6CE8\u660E\u9002\u5E94\u8BC1\u548C\u4E0D\u826F\u53CD\u5E94\u7684\uFF0C\u5176\u5B9E\u4E5F\u5C31\u662F\u544A\u8BC9\u5927\u5BB6\u54EA\u79CD\u60C5\u51B5\u53EF\u4EE5\u7528\uFF0C\u7528\u4E86\u6709\u4EC0\u4E48\u597D\u5904\uFF0C\u6709\u4EC0\u4E48\u98CE\u9669\u3002\u867D\u7136\u6211\u4EEC\u7684\u836F\u76D1\u5C40\u6279\u51C6\u4E86\u5F88\u591A\u4E71\u4E03\u516B\u7CDF\u7684\u836F\uFF0C\u4E5F\u6279\u51C6\u4E86\u5F88\u591A\u4E0D\u51C6\u786E\u7684\u9002\u5E94\u75C7\uFF0C\u4F46\u770B\u603B\u6BD4\u4E0D\u770B\u66F4\u5B89\u5168\u3002</p><p>\u6211\u4E5F\u5199\u8FC7\u4E00\u7BC7\u300A\u6CA1\u7528\u8FD8\u53EF\u80FD\u6709\u5BB3\u7684\u513F\u79D1\u836F\u7269\u300B\u7684\u6587\u7AE0\uFF0C\u5C06\u513F\u79D1\u7528\u836F\u91CC\u90A3\u4E9B\u5E38\u89C1\u7684\u5751\u6807\u6CE8\u51FA\u6765\uFF0C\u8BFB\u5B8C\u53EF\u4EE5\u907F\u514D\u4E00\u4E9B\u4E0D\u5FC5\u8981\u7684\u98CE\u9669\uFF0C\u5728\u8FD9\u4E2A\u516C\u4F17\u53F7\u91CC\u56DE\u590D\u300C\u836F\u7269\u300D\u5C31\u53EF\u4EE5\u67E5\u770B\u3002</p><p>\u53E6\u5916\uFF0C\u5728\u81EA\u5DF1\u62C5\u5FC3\u7126\u8651\uFF0C\u5FC3\u91CC\u6CA1\u5E95\u7684\u65F6\u5019\uFF0C\u5BFB\u6C42\u4E13\u4E1A\u4EBA\u58EB\u7684\u5E2E\u52A9\u4F1A\u6BD4\u81EA\u5DF1\u76F2\u76EE\u51B3\u7B56\u66F4\u5B89\u5168\uFF0C\u8BE5\u95EE\u533B\u751F\u7684\u65F6\u5019\uFF0C\u8BE5\u53BB\u533B\u9662\u7684\u65F6\u5019\u53BB\u533B\u9662\uFF0C\u542C\u4E00\u542C\u53EF\u4FE1\u4EFB\u7684\u7B2C\u4E09\u65B9\u7684\u610F\u89C1\uFF0C\u53EF\u4EE5\u8BA9\u81EA\u5DF1\u5C11\u505A\u4E00\u4E9B\u6298\u817E\u5B69\u5B50\u5B89\u6170\u81EA\u5DF1\u7684\u4E8B\u3002</p><p>\u70B9\u51FB<strong>\u9605\u8BFB\u539F\u6587</strong>\uFF0C\u53EF\u5411\u6211\u548C\u5176\u5B83\u533B\u751F\u54A8\u8BE2</p>',18),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626718&idx=1&sn=7bc1c64e230fcd5edde7224e33b075b9&chksm=8b5cb524bc2b3c3244cd991aa2fb0b5e71fda7bcb96011650af9c9ea9c1882562eb716ef3062&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},b=s("\u67E5\u770B\u539F\u6587");function h(f,m){const p=a("ExternalLinkIcon");return c(),r(n,null,[i,e("p",null,[e("a",l,[b,t(p)])])],64)}var k=d(_,[["render",h]]);export{k as default};
