import{r as o,o as t,c as n,a as p,b as e,F as s,d as a,e as c}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const d={},g=a('<h3 id="\u4F53\u68C0\u53D1\u73B0\u7532\u72B6\u817A\u7ED3\u8282-\u8981\u6CBB\u7597\u5417-\u4F1A\u4E0D\u4F1A\u6076\u5316" tabindex="-1"><a class="header-anchor" href="#\u4F53\u68C0\u53D1\u73B0\u7532\u72B6\u817A\u7ED3\u8282-\u8981\u6CBB\u7597\u5417-\u4F1A\u4E0D\u4F1A\u6076\u5316" aria-hidden="true">#</a> \u4F53\u68C0\u53D1\u73B0\u7532\u72B6\u817A\u7ED3\u8282\uFF0C\u8981\u6CBB\u7597\u5417\uFF1F\u4F1A\u4E0D\u4F1A\u6076\u5316\uFF1F</h3><p>\u5E74\u5EA6\u4F53\u68C0\u662F\u5F88\u591A\u516C\u53F8\u7684\u798F\u5229\uFF0C\u505A\u5B8C\u8D85\u58F0\uFF0C\u5F88\u591A\u4EBA\u5F00\u59CB\u5FE7\u5FC3\u5FE1\u5FE1\uFF0C\u56E0\u4E3A\u8D85\u58F0\u53D1\u73B0\u6709\u7532\u72B6\u817A\u7ED3\u8282\u3002\u8FD9\u662F\u4E0D\u662F\u764C\u75C7\uFF1F\u5982\u679C\u4E0D\u662F\u4EE5\u540E\u4F1A\u4E0D\u4F1A\u6076\u53D8\uFF1F\u73B0\u5728\u8981\u4E0D\u8981\u8FDB\u4E00\u6B65\u68C0\u67E5\u6216\u6CBB\u7597\uFF1F\u4ECA\u5929\u5C31\u6765\u7ED9\u5927\u5BB6\u6253\u5F00\u7532\u72B6\u817A\u7ED3\u8282\u8FD9\u4E2A\u201C\u5FC3\u7ED3\u201D\u3002</p><p><strong>\u4EC0\u4E48\u662F\u7532\u72B6\u817A\u7ED3\u8282\uFF1F</strong></p><p>\u5728\u8BA4\u8BC6\u7532\u72B6\u817A\u7ED3\u8282\u4E4B\u524D\uFF0C\u6211\u4EEC\u5148\u6765\u4E86\u89E3\u4E00\u4E0B\u7532\u72B6\u817A\u3002</p><p>\u7532\u72B6\u817A\u50CF\u4E00\u53EA\u8774\u8776\uFF0C\u8DB4\u5728\u9888\u90E8\u5589\u7ED3\u4E0B\u7F18\u7684\u6B63\u524D\u65B9\uFF0C\u4E24\u7FC5\u4F38\u5C55\uFF0C\u8986\u76D6\u6C14\u7BA1\u7684\u524D\u5916\u4FA7\u9762\u3002\u8FD9\u53EA\u201C\u8774\u8776\u201D\u7684\u4E3B\u8981\u751F\u7406\u529F\u80FD\u662F\u5408\u6210\u3001\u5206\u6CCC\u548C\u50A8\u5B58\u7532\u72B6\u817A\u7D20\uFF0C\u7532\u72B6\u817A\u7D20\u662F\u4E00\u79CD\u975E\u5E38\u91CD\u8981\u7684\u6FC0\u7D20\uFF0C\u5728\u4EBA\u4F53\u7684\u751F\u957F\u53D1\u80B2\u3001\u65B0\u9648\u4EE3\u8C22\u7B49\u5404\u65B9\u9762\u90FD\u53D1\u6325\u91CD\u8981\u4F5C\u7528\u3002</p><p>\u800C\u7532\u72B6\u817A\u7ED3\u8282\uFF0C\u7B80\u5355\u8BF4\u5C31\u662F\u51FA\u73B0\u5728\u7532\u72B6\u817A\u5185\u90E8\u5F02\u5E38\u589E\u751F\u7684\u7EC6\u80DE\u56E2\u5757\u3002</p><p><strong>\u4E3A\u4EC0\u4E48\u4F1A\u6709\u7532\u72B6\u817A\u7ED3\u8282\uFF1F</strong></p><p>\u6839\u636E2010\u5E74\u7684\u4E00\u9879\u6D41\u884C\u75C5\u5B66\u8C03\u67E5\u663E\u793A\uFF0C\u76EE\u524D\u6211\u56FD\u7532\u72B6\u817A\u7ED3\u8282\u7684\u60A3\u75C5\u7387\u9AD8\u8FBE18.6%\uFF0C\u51E0\u4E4E\u6BCF5\u4E2A\u4EBA\u91CC\u5C31\u67091\u4EBA\u5B58\u5728\u7532\u72B6\u817A\u7ED3\u8282\u3002</p><p>\u6709\u4EBA\u731C\u60F3\u8FD9\u662F\u73AF\u5883\u6C61\u67D3\u7684\u6076\u679C\uFF0C\u8FD8\u6709\u4EBA\u8BA4\u4E3A\u8FD9\u662F\u957F\u671F\u98DF\u7528\u7898\u76D0\u6240\u81F4\uFF0C\u4F46\u662F\uFF0C\u8FD9\u4E9B\u731C\u6D4B\u4ECE\u6765\u90FD\u6CA1\u6709\u5F97\u5230\u786E\u5207\u7684\u79D1\u5B66\u8BC1\u5B9E\u3002</p><p>\u76EE\u524D\uFF0C\u533B\u5B66\u754C\u666E\u904D\u8BA4\u4E3A\uFF0C\u4FC3\u4F7F\u7532\u72B6\u817A\u7ED3\u8282\u60A3\u75C5\u7387\u7684\u201C\u5347\u9AD8\u201D\u7684\u4E3B\u8981\u539F\u56E0\u662F\u8D85\u58F0\u53CA\u4F53\u68C0\u7684\u666E\u53CA\uFF0C\u8BA9\u7532\u72B6\u817A\u7ED3\u8282\u6709\u66F4\u591A\u673A\u4F1A\u88AB\u53D1\u73B0\u3002</p><p><strong>\u53D1\u73B0\u7532\u72B6\u817A\u7ED3\u8282\uFF0C\u53EF\u4EE5\u505A\u54EA\u4E9B\u68C0\u67E5\uFF1F</strong></p><p>\u5F53\u7136\u4E5F\u4E0D\u662F\u6240\u6709\u7684\u7532\u72B6\u817A\u7ED3\u8282\u90FD\u662F\u8D85\u58F0\u53D1\u73B0\u7684\uFF0C\u5982\u679C\u67E5\u4F53\u53D1\u73B0\u7532\u72B6\u817A\u7ED3\u8282\uFF0C\u53EF\u4EE5\u5148\u505A\u8D85\u58F0\u68C0\u67E5\u3002</p><p>\u7532\u72B6\u817A\u8D85\u58F0\u68C0\u67E5\u6709\u8F6F\u7EC4\u7EC7\u9AD8\u5206\u8FA8\u529B\u3001\u65E0\u521B\u4F24\u3001\u4EF7\u683C\u4F4E\u5EC9\u3001\u64CD\u4F5C\u65B9\u4FBF\u7B49\u6BD4\u8F83\u7A81\u51FA\u7684\u4F18\u70B9\uFF0C\u662F\u4E34\u5E8A\u4E0A\u9488\u5BF9\u7532\u72B6\u817A\u7ED3\u8282\u9996\u9009\u7684\u68C0\u67E5\u65B9\u6CD5\u3002</p><p>\u901A\u8FC7\u7532\u72B6\u817A\u8D85\u58F0\u68C0\u67E5\uFF0C\u4E0D\u4EC5\u53EF\u4EE5\u660E\u786E\u7ED3\u8282\u662F\u5426\u5B58\u5728\uFF0C\u8FD8\u80FD\u591F\u786E\u5B9A\u7ED3\u8282\u7684\u5927\u5C0F\u3001\u6570\u91CF\u3001\u4F4D\u7F6E\u3001\u8D28\u5730\u3001\u5F62\u72B6\u3001\u8FB9\u754C\u3001\u9499\u5316\u3001 \u8840\u4F9B\u3001\u4EE5\u53CA\u548C\u5468\u56F4\u7EC4\u7EC7\u7684\u5173\u7CFB\uFF0C\u8BC4\u4F30\u9888\u90E8\u533A\u57DF\u6709\u65E0\u6DCB\u5DF4\u7ED3\u80BF\u5927\u3002</p><p>\u56E0\u6B64\uFF0C\u5BF9\u4E8E\u6240\u6709\u5DF2\u77E5\u6216\u6000\u7591\u5B58\u5728\u7ED3\u8282\u7684\u60A3\u8005\uFF0C\u90FD\u63A8\u8350\u8FDB\u884C\u7532\u72B6\u817A\u8D85\u58F0\u68C0\u67E5\u3002\u8D85\u58F0\u5BF9\u7532\u72B6\u817A\u7ED3\u8282\u7684\u9274\u522B\u80FD\u529B\uFF0C\u751A\u81F3\u4F18\u4E8ECT\u4E0E\u78C1\u5171\u632F\u3002</p><p>\u4F46\u662F\uFF0C\u8D85\u58F0\u4F5C\u4E3A\u4E00\u79CD\u975E\u5E38\u91CD\u8981\u7684\u7ED3\u8282\u7B5B\u67E5\u65B9\u6CD5\uFF0C\u53EA\u80FD\u505A\u51FA\u201C\u826F\u6027\u53EF\u80FD\u201D\u6216\u201C\u6076\u6027\u53EF\u80FD\u201D\u7684\u521D\u6B65\u5224\u65AD\uFF0C\u65E0\u6CD5\u5BF9\u7ED3\u8282\u8FDB\u884C\u786E\u8BCA\u3002</p><p>\u5982\u679C\u8D85\u58F0\u68C0\u67E5\u7ED3\u679C\u7591\u4F3C\u6076\u6027\u3001\u76F4\u5F84\u8F83\u5927\uFF0C\u8FD8\u53EF\u4EE5\u901A\u8FC7\u7EC6\u9488\u7A7F\u523A\u6D3B\u68C0\u6765\u660E\u786E\u7ED3\u8282\u6027\u8D28\uFF0C\u8FD9\u662F\u786E\u8BCA\u7532\u72B6\u817A\u7ED3\u8282\u6027\u8D28\u7684\u91D1\u6807\u51C6\u3002\u901A\u8FC7\u7A7F\u523A\u75C5\u7406\u6D3B\u68C0\uFF0C\u4E0D\u4EC5\u80FD\u591F\u786E\u8BCA\u7532\u72B6\u817A\u764C\uFF0C\u8FD8\u53EF\u4EE5\u5C06\u7532\u72B6\u817A\u764C\u7684\u5177\u4F53\u75C5\u7406\u7C7B\u578B\u533A\u5206\u4E3A\u4E73\u5934\u72B6\u764C\u3001\u6EE4\u6CE1\u72B6\u764C\u3001\u9AD3\u6837\u764C\u3001\u672A\u5206\u5316\u764C\u7B49 \u3002</p><p>\u53E6\u5916\uFF0C\u57FA\u56E0\u68C0\u6D4B\u4E5F\u6709\u52A9\u4E8E\u8BC4\u4F30\u7ED3\u8282\u6027\u8D28\u3001\u534F\u52A9\u5224\u65AD\u624B\u672F\u6307\u5F81\u3002</p><p><strong>\u5982\u4F55\u786E\u5B9A\u7532\u72B6\u817A\u7ED3\u8282\u7684\u826F\u6076\u6027\uFF1F</strong></p><p>\u7532\u72B6\u817A\u7ED3\u8282\u591A\u6570\u60C5\u51B5\u4E0B\u6CA1\u6709\u660E\u663E\u75C7\u72B6\uFF0C\u4F53\u68C0\u53D1\u73B0\u7532\u72B6\u817A\u7ED3\u8282\u65F6\uFF0C\u6700\u9700\u8981\u505A\u7684\u662F\u9274\u522B\u5176\u826F\u6076\u6027\u3002</p><p>2017\u5E74\uFF0C\u7F8E\u56FD\u653E\u5C04\u5B66\u4F1A\uFF08ACR\uFF09\u516C\u5E03\u4E86\u7532\u72B6\u817A\u8D85\u58F0\u68C0\u67E5\u7684TI-RADS\u5206\u7EA7\u6807\u51C6\uFF0C\u5EFA\u8BAE\u5206\u522B\u5BF9\u7ED3\u8282\u7684\u6210\u5206\u3001\u56DE\u58F0\u3001\u5F62\u72B6\u3001\u8FB9\u754C\u3001\u9499\u5316\u8FDB\u884C\u6253\u5206\uFF0C\u6839\u636E\u603B\u5206\u9AD8\u4F4E\u5C06\u7ED3\u8282\u5206\u4E3A1~5\u7EA7\u3002\u7EA7\u522B\u8D8A\u4F4E\uFF0C\u826F\u6027\u53EF\u80FD\u6027\u8D8A\u5927\uFF1B\u53CD\u4E4B\uFF0C\u7EA7\u522B\u8D8A\u9AD8\uFF0C\u6076\u6027\u53EF\u80FD\u6027\u8D8A\u5927\u3002\u5BF9\u4E8E\u4E0D\u540C\u5206\u7EA7\uFF0C\u4E5F\u63D0\u51FA\u4E86\u9488\u5BF9\u6027\u7684\u590D\u67E5\u548C\u968F\u8BBF\u5EFA\u8BAE\u3002</p><p><strong>\u8D85\u58F0\u7279\u70B9</strong></p><p><strong>\u8BC4\u5206</strong></p><p><strong>\u6210\u5206</strong></p><p>\u56CA\u6027\u6210\u5206\u53CA\u6D77\u7EF5\u72B6\u6210\u5206\u4E3A +0\u5206</p><p>\u6DF7\u5408\u6027\u6210\u5206\u8BC4\u5206 +1\u5206</p><p>\u5B9E\u6027\u6210\u5206\u8BC4\u5206 +2\u5206</p><p><strong>\u56DE\u58F0</strong></p><p>\u65E0\u56DE\u58F0 +0\u5206</p><p>\u9AD8\u56DE\u58F0\u6216\u8005\u7B49\u56DE\u58F0\u8BC4\u5206 +1\u5206</p><p>\u4F4E\u56DE\u58F0 +2\u5206</p><p>\u6781\u4F4E\u56DE\u58F0 +3\u5206</p><p><strong>\u5F62\u72B6</strong></p><p>\u7EB5\u6A2A\u6BD4\uFF1C1\uFF0C +0\u5206</p><p>\u7EB5\u6A2A\u6BD4\uFF1E1\uFF0C +3\u5206</p><p><strong>\u8FB9\u754C</strong></p><p>\u8FB9\u754C\u5149\u6ED1 +0\u5206</p><p>\u8FB9\u754C\u4E0D\u6E05 +0\u5206</p><p>\u5C0F\u53F6\u72B6\u6216\u8005\u4E0D\u89C4\u5219 +2\u5206</p><p>\u7532\u72B6\u817A\u5916\u4FB5\u72AF +3\u5206</p><p><strong>\u70B9\u72B6\u5F3A\u56DE\u58F0</strong></p><p>\u65E0\u6216\u8005\u4F34\u6709\u5927\u5F57\u661F\u5C3E\u5DF4\u5F81\u4E3A +0\u5206</p><p>\u7C97\u5927\u9499\u5316 +1\u5206</p><p>\u5468\u56F4\u578B\u9499\u5316 +2\u5206</p><p>\u7EC6\u70B9\u72B6\u5F3A\u56DE\u58F0 +3\u5206</p><p><strong>\u8BC4\u7EA7</strong></p><p><strong>\u5206\u503C</strong></p><p><strong>\u6076\u6027\u98CE\u9669\u53CA\u5904\u7406</strong></p><p><strong>TR1</strong></p><p>0\u5206</p><p>\u826F\u6027\uFF0C\u4E0D\u9700\u8981\u5904\u7406</p><p><strong>TR2</strong></p><p>2\u5206</p><p>\u6076\u6027\u98CE\u9669\uFF1C2%\uFF1A\u8003\u8651\u826F\u6027</p><p>\u968F\u8BBF</p><p><strong>TR3</strong></p><p>3\u5206</p><p>\u6076\u6027\u98CE\u9669\uFF1C5%\uFF1A\u4F4E\u5EA6\u53EF\u7591\u6076\u6027</p><p>\u22652.5cm\u884C\u7EC6\u9488\u7A7F\u523A</p><p>\uFF1C2.5cm\u968F\u8BBF\uFF081\u30013\u30015\u5E74\u968F\u8BBF\uFF09</p><p><strong>TR4</strong></p><p>4-6\u5206</p><p>\u6076\u6027\u98CE\u96695%-20%\uFF1A\u4E2D\u5EA6\u53EF\u7591\u6076\u6027</p><p>\u22651.5cm\u884C\u7EC6\u9488\u7A7F\u523A</p><p>\uFF1C1.5cm\u968F\u8BBF\uFF081\u30012\u30013\u30015\u5E74\u968F\u8BBF\uFF09</p><p><strong>TR5</strong></p><p>\u22657\u5206</p><p>\u6076\u6027\u98CE\u9669\u5927\u4E8E20%\uFF1A\u9AD8\u5EA6\u53EF\u7591\u6076\u6027</p><p>\u22651cm\u884C\u7EC6\u9488\u7A7F\u523A</p><p>\uFF1C1cm\u968F\u8BBF\uFF08\u6BCF\u5E74\u968F\u8BBF\u6301\u7EED5\u5E74\uFF09</p><p><strong>\u7532\u72B6\u817A\u7ED3\u8282\u9700\u8981\u6CBB\u7597\u5417\uFF1F</strong></p><p>\u5927\u591A\u6570\u7532\u72B6\u817A\u7ED3\u8282\u90FD\u5C5E\u4E8E\u826F\u6027\u75C5\u53D8\uFF0C\u4E0D\u4F1A\u5F71\u54CD\u7532\u72B6\u817A\u7D20\u7684\u5408\u6210\u548C\u5206\u6CCC\uFF0C\u4E0D\u4F1A\u5BFC\u81F4\u7532\u72B6\u817A\u529F\u80FD\u7D0A\u4E71\u3002\u800C\u4E14\uFF0C\u5BF9\u4E8E\u786E\u8BCA\u4E3A\u826F\u6027\u7684\u7ED3\u8282\uFF0C\u4E5F\u4E0D\u4F1A\u5411\u6076\u6027\u65B9\u5411\u8F6C\u5316\u3001\u8FDB\u800C\u8BF1\u53D1\u7532\u72B6\u817A\u764C\u3002</p><p>\u9664\u975E\u7532\u72B6\u817A\u7ED3\u8282\u76F4\u5F84\u8FC7\u5927\u3001\u5BF9\u5468\u56F4\u7EC4\u7EC7\u4EA7\u751F\u538B\u8FEB\u75C7\u72B6\uFF0C\u6216\u8005\u5F71\u54CD\u7F8E\u89C2\uFF0C\u53EF\u4EE5\u901A\u8FC7\u624B\u672F\u6216\u5176\u4ED6\u5FAE\u521B\u6CBB\u7597\u65B9\u6CD5\u8FDB\u884C\u5904\u7406\u3002\u9664\u6B64\u800C\u5916\uFF0C\u7EDD\u5927\u591A\u6570\u826F\u6027\u7532\u72B6\u817A\u7ED3\u8282\u4E0D\u9700\u8981\u4EFB\u4F55\u836F\u7269\u6216\u624B\u672F\u6CBB\u7597\u3002</p><p>\u5373\u4F7F\u6700\u7EC8\u8BCA\u65AD\u4E3A\u7532\u72B6\u817A\u764C\uFF0C\u65B0\u7684\u7532\u72B6\u817A\u764C\u6CBB\u7597\u7406\u5FF5\u4E5F\u4E3B\u5F20\u7EFC\u5408\u8BC4\u4F30\u3002\u5BF9\u4E8E\u7532\u72B6\u817A\u6EE4\u6CE1\u72B6\u764C\u3001\u9AD3\u6837\u764C\u548C\u672A\u5206\u5316\u764C\uFF0C\u5EFA\u8BAE\u79EF\u6781\u624B\u672F\u6CBB\u7597\u3002\u800C\u5BF9\u4E8E\u5C40\u7076\u5FAE\u5C0F\u7684\u7532\u72B6\u817A\u4E73\u5934\u72B6\u764C\uFF0C\u751A\u81F3\u53EF\u4EE5\u6682\u4E0D\u6CBB\u7597\uFF0C\u4FDD\u6301\u968F\u8BBF\u89C2\u5BDF\uFF0C\u800C\u4E0D\u662F\u4E00\u5473\u624B\u672F\u5207\u9664\u3002</p><p>\u5B66\u672F\u754C\u751A\u81F3\u6709\u5EFA\u8BAE\u8981\u628A\u76EE\u524D\u5360\u6BD4\u6700\u5927\u3001\u60F0\u6027\u751F\u957F\u3001\u9884\u540E\u826F\u597D\u7684\u7532\u72B6\u817A\u4E73\u5934\u72B6\u764C\uFF08cancer\uFF09 \u66F4\u540D\u4E3A\u7532\u72B6\u817A\u4E73\u5934\u72B6\u7624\uFF08tumor\uFF09\u3002\u8FD9\u6837\u6709\u52A9\u4E8E\u964D\u4F4E\u516C\u4F17\u5BF9\u4E8E\u75BE\u75C5\u7684\u6050\u60E7\u3001\u51CF\u5C11\u4E0D\u5FC5\u8981\u7684\u624B\u672F\u3002</p><p>\u7532\u72B6\u817A\u7ED3\u8282\u5176\u5B9E\u4E00\u70B9\u4E5F\u4E0D\u53EF\u6015\uFF0C\u5C31\u50CF\u76B1\u7EB9\u4E00\u6837\uFF0C\u4E5F\u662F\u5C81\u6708\u7559\u5728\u8EAB\u4F53\u4E0A\u7684\u75D5\u8FF9\uFF0C\u4F60\u8981\u505A\u7684\uFF0C\u53EA\u662F\u4E0D\u8981\u5931\u53BB\u5B83\u7684\u6D88\u606F\uFF0C\u77E5\u9053\u5B83\u6709\u591A\u5927\u3001\u5728\u54EA\u91CC\u3002</p><p><strong>\u53C2\u8003\u6587\u732E</strong></p><p>1.2015 American Thyroid Association Management Guidelines for Adult Patients with Thyroid Nodules and Differentiated Thyroid Cancer: The American Thyroid Association Guidelines Task Force on Thyroid Nodules and Differentiated Thyroid Cancer[J]. Thyroid, 2016\uFF0C26(1)\uFF1A1-133.</p><p>2.ACR Thyroid Imaging, Reporting and Data System (TI-RADS): White Paper of the ACR TI-RADS Committee. J Am Coll Radiol. 2017;14(5):587-595.</p><p>\uFF08\u53EF\u4E0A\u4E0B\u6ED1\u52A8\u67E5\u770B\uFF09</p><hr><p>\u5173\u6CE8\u6021\u79BE\uFF0C\u53EF\u54A8\u8BE2\u533B\u751F</p>',82),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651630196&idx=1&sn=338df14c64ddf10a5fc36f8221baa6bf&chksm=8b5c838ebc2b0a98ae18e005681ea74a3854967bd28d91a83e65abd71f1d23989ad9541da140&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},m=c("\u67E5\u770B\u539F\u6587");function l(_,f){const r=o("ExternalLinkIcon");return t(),n(s,null,[g,p("p",null,[p("a",h,[m,e(r)])])],64)}var A=i(d,[["render",l]]);export{A as default};
