import{r as t,o as c,c as a,a as p,b as n,w as d,F as _,d as o,e}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const l={},h=o('<h3 id="\u5B69\u5B50\u665A\u4E0A\u4E0D\u7761-\u65E9\u4E0A\u4E0D\u8D77-\u600E\u4E48\u529E" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u665A\u4E0A\u4E0D\u7761-\u65E9\u4E0A\u4E0D\u8D77-\u600E\u4E48\u529E" aria-hidden="true">#</a> \u5B69\u5B50\u665A\u4E0A\u4E0D\u7761\uFF0C\u65E9\u4E0A\u4E0D\u8D77\uFF0C\u600E\u4E48\u529E\uFF1F</h3><p>\u5F88\u665A\u4E86\u8FD8\u5728\u6298\u817E\uFF0C\u54EA\u6015\u4F5C\u4E1A\u505A\u5B8C\u4E86\u4E5F\u8FDF\u8FDF\u4E0D\u80AF\u53BB\u7761\uFF0C\u7B2C\u4E8C\u5929\u65E9\u4E0A\u5374\u53C8\u53D8\u8EAB\u201C\u8D77\u5E8A\u56F0\u96BE\u6237\u201D\uFF0C\u95F9\u949F\u54CD\u4E86\u4E0D\u8D77\u6765\uFF0C\u5DE6\u558A\u53F3\u558A\u4E5F\u8D77\u4E0D\u6765\u3002</p><p>\u4F60\u662F\u5426\u88AB\u5B69\u5B50\u8FD9\u4E2A\u95EE\u9898\u56F0\u6270\uFF1F\u4E3A\u4EC0\u4E48\u4F1A\u8FD9\u6837\uFF1F\u6709\u4EC0\u4E48\u597D\u7684\u529E\u6CD5\u5417\uFF1F</p><p><em><strong>01</strong></em></p><p><strong>\u96BE\u4EE5\u5165\u7761\u7684\u539F\u56E0</strong></p><p>\u5176\u5B9E\u4EC0\u4E48\u65F6\u5019\u5165\u7761\u8DDF\u5B69\u5B50\u7684\u751F\u7269\u949F\u6709\u5173\u3002</p><p>\u7761\u7720\u5BF9\u4E8E\u6211\u4EEC\u6765\u8BF4\u662F\u6709\u4E00\u7CFB\u5217\u7684\u751F\u7406\u673A\u5236\u7684\uFF0C\u5B69\u5B50\u60F3\u8981\u5165\u7761\u662F\u9700\u8981\u79EF\u7D2F\u4E00\u5B9A\u7684\u201C\u7761\u7720\u538B\u529B\u201D\u624D\u80FD\u591F\u542F\u52A8\u7761\u7720\u7684\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u53D7\u5230\u5927\u8111\u7684\u8C03\u63A7\uFF0C\u5728\u4F53\u5185\u6FC0\u7D20\u7684\u8FD0\u4F5C\u4E0B\u5F62\u6210\u663C\u591C\u8282\u5F8B\uFF0C\u4E5F\u5C31\u662F\u6211\u4EEC\u901A\u4FD7\u6240\u4E86\u89E3\u7684\u751F\u7269\u949F\uFF0C\u63A7\u5236\u7740\u6211\u4EEC\u9192\u6765\u548C\u7761\u7740\u3002</p><p>\u6211\u4EEC\u7684\u751F\u6D3B\u8D8A\u89C4\u5F8B\uFF0C\u4F53\u5185\u7684\u6FC0\u7D20\u53CD\u5E94\u4E5F\u4F1A\u76F8\u5E94\u7684\u5EFA\u7ACB\u8D77\u89C4\u5F8B\uFF0C\u8FDB\u800C\u4FDD\u969C\u6211\u4EEC\u5728\u8BE5\u56F0\u7684\u65F6\u5019\u56F0\uFF0C\u8BE5\u9192\u7684\u65F6\u5019\u9192\u3002</p><p>\u53CD\u4E4B\u4EA6\u7136\uFF0C\u6211\u4EEC\u7684\u751F\u6D3B\u8D8A\u4E0D\u89C4\u5F8B\uFF0C\u4F53\u5185\u7684\u6FC0\u7D20\u53CD\u5E94\u4E5F\u5EFA\u7ACB\u4E0D\u8D77\u6765\u89C4\u5F8B\u3002</p><p>\u201C\u7761\u7720\u538B\u529B\u201D\u662F\u9700\u8981\u4E0D\u65AD\u50A8\u5907\u548C\u79EF\u7D2F\u7684\uFF0C\u79EF\u7D2F\u5230\u538B\u529B\u70B9\u4E86\u5C31\u5BB9\u6613\u5165\u7761\uFF0C\u800C\u5F53\u6211\u4EEC\u7761\u7740\u4E86\u538B\u529B\u5C31\u4F1A\u5F97\u5230\u7F13\u89E3\uFF0C\u9192\u6765\u540E\u8981\u518D\u6B21\u5165\u7761\u5C31\u53C8\u518D\u6B21\u9700\u8981\u79EF\u7D2F\u538B\u529B\u3002\u6240\u4EE5\u5982\u679C\u5B69\u5B50\u4E2D\u5348\u5348\u7761\u7761\u665A\u4E86\u6216\u8005\u7761\u5F97\u65F6\u95F4\u8FC7\u957F\uFF0C\u90A3\u5C31\u81EA\u7136\u4F1A\u5F71\u54CD\u665A\u4E0A\u7684\u5165\u7761\u65F6\u95F4\u3002</p><p>\u6B64\u5916\uFF0C\u201C\u7761\u7720\u538B\u529B\u201D\u8FD8\u548C\u5B69\u5B50\u7684\u75B2\u52B3\u7A0B\u5EA6\u6709\u5173\uFF0C\u5982\u679C\u5B69\u5B50\u767D\u5929\u7684\u6D3B\u52A8\u91CF\u6BD4\u8F83\u5C11\uFF0C\u5B69\u5B50\u5E76\u6CA1\u6709\u7279\u522B\u7684\u75B2\u52B3\u611F\uFF0C\u4E5F\u4F1A\u5EF6\u957F\u5165\u7761\u7684\u65F6\u95F4\u3002</p><p>\u8FD8\u6709\u4E00\u4E9B\u60C5\u51B5\u4E0B\uFF0C\u4E0A\u5B66\u548C\u4E0A\u5E7C\u513F\u56ED\u7684\u5B69\u5B50\u56E0\u4E3A\u767D\u5929\u548C\u7238\u7238\u5988\u5988\u5206\u5F00\uFF0C\u665A\u4E0A\u89C1\u5230\u7238\u7238\u5988\u5988\u540E\u662F\u6709\u548C\u4ED6\u4EEC\u91CD\u805A\u7684\u9700\u6C42\u7684\uFF0C\u6240\u4EE5\u4ED6\u4EEC\u9700\u8981\u7238\u7238\u5988\u5988\u966A\u4F34\u3002</p><p>\u8FD9\u79CD\u966A\u4F34\u5E76\u4E0D\u662F\u5728\u5B69\u5B50\u8EAB\u8FB9\uFF0C\u7167\u987E\u751F\u6D3B\u8D77\u5C45\u5C31\u53EF\u4EE5\uFF0C\u800C\u662F\u9700\u8981\u548C\u7238\u7238\u5988\u5988\u6709\u5B9E\u8D28\u6027\u7684\u4E92\u52A8\u548C\u60C5\u611F\u4EA4\u6D41\u7684\uFF0C\u8FD9\u79CD\u966A\u4F34\u80FD\u591F\u7F13\u89E3\u5B69\u5B50\u767D\u5929\u79EF\u7D2F\u7684\u60C5\u7EEA\uFF0C\u4E5F\u80FD\u6EE1\u8DB3\u5B69\u5B50\u5185\u5FC3\u7684\u9700\u6C42\u3002</p><p>\u5982\u82E5\u6211\u4EEC\u6CA1\u80FD\u56DE\u5E94\u5B69\u5B50\u7684\u8FD9\u79CD\u9700\u6C42\uFF0C\u800C\u662F\u4E0D\u65AD\u50AC\u4FC3\u5B69\u5B50\u4F5C\u4E1A\u3001\u6D17\u6FA1\u3001\u7761\u89C9\uFF0C\u6216\u8005\u662F\u6577\u884D\u7684\u5E94\u4ED8\u4E00\u4E0B\u5B69\u5B50\uFF0C\u4ED6\u4EEC\u7684\u5185\u5FC3\u9700\u6C42\u6CA1\u6709\u5F97\u5230\u6EE1\u8DB3\uFF0C\u5B69\u5B50\u4E5F\u4F1A\u56E0\u6B64\u800C\u201C\u820D\u4E0D\u5F97\u201D\u5165\u7761\u7684\u3002</p><p>\u800C\u5982\u679C\u662F\u4E0A\u5B66\u7684\u5B69\u5B50\u8FD8\u8981\u8003\u8651\uFF0C\u6709\u65F6\u5B69\u5B50\u4F5C\u4E1A\u505A\u5230\u6BD4\u8F83\u665A\u4E86\uFF0C\u53EF\u80FDta\u7684\u8EAB\u4F53\u5DF2\u7ECF\u5F88\u75B2\u52B3\u4E86\uFF0C\u4F46\u662F\u5927\u8111\u76AE\u5C42\u56E0\u4E3A\u4E4B\u524D\u5728\u4E0D\u65AD\u201C\u5DE5\u4F5C\u201D\uFF0C\u4F1A\u4ECD\u7136\u5904\u4E8E\u6BD4\u8F83\u5174\u594B\u7684\u72B6\u6001\uFF0C\u8FD9\u65F6\u4E5F\u4E0D\u5BB9\u6613\u5165\u7761\u3002</p><p><em><strong>02</strong></em></p><p><strong>\u56DB\u6761\u7B56\u7565\u52A9\u5B69\u5B50\u5165\u7720</strong></p><p>\u90A3\u6211\u4EEC\u53EF\u4EE5\u7528\u54EA\u4E9B\u65B9\u6CD5\u5E2E\u52A9\u5B69\u5B50\u65E9\u70B9\u7761\u89C9\u5462\uFF1F</p><p><strong>1. \u589E\u52A0\u6D3B\u52A8\u91CF\uFF0C\u5145\u5206\u653E\u7535</strong></p><p>\u6237\u5916\u6D3B\u52A8\u80FD\u591F\u6D88\u8017\u5B69\u5B50\u7684\u80FD\u91CF\uFF0C\u8BA9ta\u5145\u5206\u653E\u7535\uFF0C\u80FD\u591F\u589E\u52A0\u5B69\u5B50\u7684\u75B2\u52B3\u7A0B\u5EA6\u8FDB\u800C\u589E\u52A0\u7761\u7720\u538B\u529B\u4FC3\u8FDB\u5B69\u5B50\u65E9\u70B9\u7761\u89C9\u3002</p><p>\u4F46\u662F\u6D3B\u52A8\u7684\u65F6\u95F4\u4E5F\u662F\u9700\u8981\u8003\u8651\uFF0C\u901A\u5E38\u5EFA\u8BAE\u5728\u7761\u524D\u76841-2\u5C0F\u65F6\u5C3D\u91CF\u907F\u514D\u6BD4\u8F83\u6FC0\u70C8\u7684\u6D3B\u52A8\uFF0C\u6240\u4EE5\u53EF\u4EE5\u628A\u6D3B\u52A8\u65F6\u95F4\u5B89\u6392\u5728\u653E\u5B66\u540E\u3001\u5403\u996D\u524D\u7ED9\u5B69\u5B50\u4E00\u4E9B\u65F6\u95F4\u5145\u5206\u7684\u8FDB\u884C\u6237\u5916\u6D3B\u52A8\uFF0C\u65E2\u80FD\u589E\u8FDB\u98DF\u6B32\uFF0C\u53C8\u80FD\u5145\u5206\u653E\u7535\u3002</p><p><strong>2. \u521B\u9020\u9AD8\u8D28\u91CF\u966A\u4F34\u7684\u673A\u4F1A\uFF0C\u6EE1\u8DB3\u5B69\u5B50\u7684\u91CD\u805A\u9700\u6C42</strong></p><p>\u9AD8\u8D28\u91CF\u7684\u966A\u4F34\u8BB2\u7A76\u7684\u662F\u6211\u4EEC\u548C\u5B69\u5B50\u540C\u65F6\u5728\u5F53\u4E0B\uFF0C\u6709\u4E92\u52A8\u7684\u8FC7\u7A0B\uFF0C\u5F62\u5F0F\u53EF\u4EE5\u591A\u6837\u3002</p><p>\u6BD4\u5982\u548C\u5B69\u5B50\u73A9\u6E38\u620F\u3001\u966A\u5B69\u5B50\u8BFB\u7ED8\u672C\u3001\u7761\u524D\u591C\u8BDD\u7B49\u7B49\uFF0C\u8FD9\u6837\u7684\u65F6\u5149\u5BF9\u5B69\u5B50\u6765\u8BF4\u5F25\u8DB3\u73CD\u8D35\uFF0C\u6709\u65F6\u5E76\u4E0D\u9700\u8981\u592A\u957F\uFF0C\u4E5F\u80FD\u6EE1\u8DB3\u5B69\u5B50\u7684\u5185\u5FC3\u9700\u6C42\u3002\u4F46\u5173\u952E\u662F\u6211\u4EEC\u8981\u5207\u5B9E\u7684\u5728\u6B64\u65F6\u6B64\u523B\u548C\u5B69\u5B50\u5728\u4E00\u8D77\uFF0C\u5E76\u8FDB\u884C\u4E92\u52A8\u6027\u7684\u6D3B\u52A8\u3002</p><p><strong>3. \u6CE8\u91CD\u5B89\u6392\u4F5C\u606F\u89C4\u5F8B</strong></p><p>\u663C\u591C\u8282\u5F8B\u548C\u6211\u4EEC\u7684\u4F5C\u606F\u65F6\u95F4\u5B89\u6392\u662F\u76F8\u4E92\u4F5C\u7528\u7684\uFF0C\u4E3A\u4E86\u8BA9\u5B69\u5B50\u7684\u751F\u7269\u949F\u80FD\u591F\u66F4\u51C6\u65F6\uFF0C\u6211\u4EEC\u4E5F\u8981\u5C3D\u91CF\u5B89\u6392\u76F8\u5BF9\u56FA\u5B9A\u7684\u4F5C\u606F\uFF0C\u6309\u987A\u5E8F\u628A\u65E9\u4E0A\u8D77\u5E8A\u3001\u5348\u7761\u3001\u4E2D\u5348\u8D77\u5E8A\u3001\u665A\u4E0A\u7761\u89C9\u8FD9\u51E0\u4E2A\u5173\u952E\u7684\u65F6\u95F4\u70B9\u76F8\u5BF9\u56FA\u5B9A\u3002</p><p>\u5982\u679C\u65E2\u5F80\u7684\u4F5C\u606F\u4E0D\u592A\u89C4\u5F8B\u9700\u8981\u8C03\u6574\uFF0C\u4E00\u822C\u9700\u8981\u4ECE\u65E9\u4E0A\u8D77\u5E8A\u7684\u65F6\u95F4\u7740\u624B\uFF0C\u9010\u4E00\u5B89\u6392\u3002</p><p>\u6BD4\u5982\u5B69\u5B50\u65E9\u4E0A7\u70B9\u8D77\u5E8A\uFF0C\u4E2D\u5348\u4E00\u822C\u89811\u70B9\u5165\u7761\uFF0C\u90A3\u4E2D\u95F4\u95F4\u9694\u5927\u6982\u662F6\u4E2A\u5C0F\u65F6\uFF1B\u4E0B\u53483\u70B9\u8D77\u5E8A\uFF0C\u90A3\u540C\u6837\u4E2D\u95F4\u95F4\u96946\u5C0F\u65F6\u5DE6\u53F3\uFF0C\u5B69\u5B50\u5927\u6982\u4E5F\u89819\u70B9\u5DE6\u53F3\u624D\u80FD\u5165\u7761\u3002\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\u9700\u8981\u6CE8\u610F\u4E24\u70B9\uFF1A</p>',28),m=e("\u6BCF\u4E2A\u5B69\u5B50\u7684\u7761\u7720\u9700\u8981\u91CF\u662F\u4E0D\u4E00\u6837\u7684\uFF0C\u8981\u6839\u636E\u5B69\u5B50\u5B9E\u9645\u7684\u9700\u8981\u91CF\u8FDB\u884C\u6BCF\u5929\u603B\u7684\u7761\u7720\u65F6\u95F4\u7684\u8C03\u6574\uFF0C\u4E0D\u8981\u6267\u7740\u7684\u6309\u7167\u7761\u7720\u63A8\u8350\u91CF\u6765\u5F3A\u8FEB\u5B69\u5B50\u5165\u7761\uFF0C\u800C\u662F\u53EF\u4EE5\u8BA1\u7B97\u5B69\u5B50\u7684\u5E73\u5747\u7761\u7720\u65F6\u957F\u6765\u4F5C\u4E3A\u81EA\u5BB6\u5A03\u7761\u7720\u9700\u8981\u91CF\u7684\u53C2\u8003\uFF1B\uFF08\u76F8\u5173\u9605\u8BFB\uFF1A"),f=e("\u5B69\u5B50\u4E00\u5929\u7761\u591A\u4E45\u6700\u5408\u9002\uFF1F\u770B\u770B\u81EA\u5DF1\u5B69\u5B50\u7761\u591F\u4E86\u6CA1\u6709"),g=e("\uFF09"),b=p("li",null,[p("p",null,"\u5348\u7761\u65F6\u95F4\u5C3D\u91CF\u4E0D\u8981\u592A\u957F\uFF0C\u4E2D\u5348\u8D77\u5E8A\u7684\u65F6\u95F4\u4F1A\u76F4\u63A5\u5F71\u54CD\u5230\u665A\u4E0A\u5165\u7761\u7684\u65F6\u95F4\uFF0C\u5982\u679C\u4E2D\u5348\u8D77\u5F97\u592A\u665A\u4E86\uFF0C\u90A3\u665A\u4E0A\u81EA\u7136\u5C31\u4F1A\u987A\u5EF6\u5165\u7761\u65F6\u95F4\u3002\u6709\u4E9B\u5B69\u5B50\u4E2D\u5348\u5B9E\u5728\u96BE\u4EE5\u5348\u7761\u4E5F\u4E0D\u8981\u7279\u522B\u52C9\u5F3A\uFF0C\u6216\u8005\u53EF\u4EE5\u8003\u8651\u201C\u534A\u4E2A\u5348\u7761\u201D\uFF0C\u4E5F\u5C31\u662F\u7A0D\u5EF6\u540E\u5348\u7761\u65F6\u95F4\uFF0C\u4F46\u662F\u4E2D\u5348\u8D77\u5E8A\u65F6\u95F4\u662F\u76F8\u5BF9\u56FA\u5B9A\u7684\uFF0C\u8FD9\u6837\u5B69\u5B50\u80FD\u591F\u9002\u5F53\u7684\u4F11\u606F\uFF0C\u4E5F\u4E0D\u81F3\u4E8E\u4E2D\u5348\u8D77\u5E8A\u65F6\u95F4\u592A\u8FDF\u800C\u5F71\u54CD\u665A\u4E0A\u7761\u89C9\u7684\u65F6\u95F4\u3002")],-1),u=o("<p><strong>4. \u8425\u9020\u7761\u7720\u73AF\u5883\u548C\u5EFA\u7ACB\u7761\u524D\u7A0B\u5E8F</strong></p><p>\u8212\u9002\u7684\u7761\u7720\u73AF\u5883\u80FD\u4FC3\u8FDB\u7761\u7720\u542F\u52A8\uFF0C\u6E29\u5EA6\u9002\u4E2D\u3001\u5E72\u51C0\u6574\u6D01\uFF0C\u5305\u542B\u5B69\u5B50\u559C\u6B22\u7684\u5B89\u629A\u7269\u7B49\u7684\u73AF\u5883\u80FD\u8BA9\u5B69\u5B50\u66F4\u5BB9\u6613\u653E\u677E\u4E0B\u6765\u3002</p><p>\u53E6\u5916\u8FD8\u5C24\u4E3A\u9700\u8981\u6CE8\u610F\u7684\u662F\u5149\u7EBF\u7684\u8C03\u6574\uFF0C\u6211\u4EEC\u5927\u8111\u91CC\u5206\u6CCC\u7684\u892A\u9ED1\u7D20\u662F\u542F\u52A8\u5165\u7761\u7684\u91CD\u8981\u6FC0\u7D20\uFF0C\u662F\u9700\u8981\u5728\u9ED1\u6697\u73AF\u5883\u91CC\u624D\u80FD\u5145\u5206\u5206\u6CCC\u7684\uFF0C\u56E0\u6B64\u6211\u4EEC\u8981\u4FDD\u8BC1\u7761\u7720\u73AF\u5883\u4E2D\u5173\u706F\u3001\u62C9\u7A97\u5E18\uFF0C\u907F\u514D\u4F7F\u7528\u591C\u706F\u6216\u5176\u4ED6\u7684\u706F\u5149\u8BBE\u5907\u3002</p><p>\u7761\u524D\u7A0B\u5E8F\u662F\u6307\u5728\u5165\u7761\u524D\u8FDB\u884C\u7684\u4E00\u7CFB\u5217\u6D3B\u52A8\uFF0C\u53EF\u4EE5\u5305\u62EC\u559D\u5976\u3001\u5237\u7259\u3001\u6D17\u6FA1\u3001\u6362\u7761\u8863\u3001\u8BB2\u6545\u4E8B\u3001\u5173\u706F\u7B49\uFF0C\u5C3D\u53EF\u80FD\u5728\u6BCF\u5929\u5165\u7761\u524D\u4FDD\u8BC1\u4E00\u81F4\u6027\u7684\u6D3B\u52A8\uFF0C\u4EE5\u8BA9\u5B69\u5B50\u5BF9\u5165\u7761\u5EFA\u7ACB\u8D77\u9884\u671F\uFF0C\u4E5F\u80FD\u4FC3\u8FDB\u5165\u7761\u3002</p><p>\u5982\u679C\u5B69\u5B50\u5F88\u665A\u624D\u505A\u5B8C\u4F5C\u4E1A\uFF0C\u4E0D\u4E00\u5B9A\u8981\u5373\u523B\u5C31\u8BA9\u5B69\u5B50\u53BB\u7761\u89C9\uFF0C\u800C\u662F\u53EF\u4EE5\u901A\u8FC7\u542C\u8212\u7F13\u7684\u97F3\u4E50\u3001\u548C\u7238\u7238\u5988\u5988\u5367\u8C08\u3001\u8BB2\u51E0\u4E2A\u6545\u4E8B\u7B49\u65B9\u5F0F\u6765\u8BA9\u5B69\u5B50\u5927\u8111\u76AE\u5C42\u4ECE\u5174\u594B\u72B6\u6001\u9010\u6E10\u7684\u201C\u5E73\u9759\u201D\u4E0B\u6765\uFF0C\u53CD\u800C\u80FD\u591F\u5E2E\u52A9\u5165\u7761\uFF0C\u540C\u65F6\u4E5F\u53EF\u4EE5\u907F\u514D\u5B69\u5B50\u56E0\u4E3A\u8EBA\u4E0B\u4E86\u4F46\u7761\u4E0D\u7740\u800C\u5F15\u53D1\u7126\u8651\u3002</p><p>\u6700\u540E\u60F3\u8865\u5145\u7684\u662F\uFF0C\u5F88\u591A\u7236\u6BCD\u5728\u54C4\u5B9D\u5B9D\u7761\u89C9\u65F6\uFF0C\u9664\u4E86\u7531\u4E8E\u5B9D\u5B9D\u4E0D\u80AF\u5165\u7761\u800C\u5BFC\u81F4\u7684\u7126\u8651\u5916\uFF0C\u6709\u65F6\u4E5F\u4F1A\u56E0\u4E3A\u81EA\u5DF1\u624B\u5934\u8FD8\u6709\u5DE5\u4F5C\u6216\u5BB6\u52A1\u6CA1\u6709\u5B8C\u6210\uFF0C\u5185\u5FC3\u5BF9\u4E8E\u5B9D\u5B9D\u5728\u4E00\u5B9A\u65F6\u95F4\u5185\u7761\u7740\uFF0C\u62B1\u6709\u7279\u522B\u6025\u5207\u7684\u671F\u671B\u3002\u4E5F\u8BB8\u6709\u7684\u65F6\u5019\uFF0C\u7236\u6BCD\u4E5F\u5E94\u8BE5\u5148\u770B\u770B\u81EA\u5DF1\u662F\u5426\u591C\u95F4\u6709\u6025\u5F85\u5B8C\u6210\u7684\u4E8B\uFF0C\u4ECE\u800C\u63D0\u524D\u5206\u914D\u597D\u5404\u81EA\u7684\u80B2\u513F\u4EFB\u52A1\u3002</p><p>\u5B69\u5B50\u7684\u7761\u7720\u662F\u5BB9\u6613\u53D7\u5230\u5F71\u54CD\u800C\u53D8\u5316\u7684\uFF0C\u665A\u4E0A\u4E0D\u7761\u56FA\u7136\u8BA9\u4EBA\u7126\u8651\u548C\u70E6\u8E81\uFF0C\u4F46\u66F4\u91CD\u8981\u7684\u8FD8\u662F\u9700\u8981\u4E86\u89E3\u539F\u56E0\u4EE5\u8C03\u6574\u65B9\u6CD5\u6539\u53D8\uFF0C\u5207\u5FCC\u7531\u4E8E\u8FD9\u4E2A\u95EE\u9898\u548C\u5B69\u5B50\u201C\u5F00\u6218\u201D\uFF0C\u66F4\u4E0D\u8981\u8BA9\u5B69\u5B50\u5E26\u7740\u60C5\u7EEA\u5165\u7761\uFF0C\u53CD\u800C\u5F62\u6210\u6B7B\u5FAA\u73AF\uFF0C\u66F4\u6DFB\u70E6\u607C\u3002</p><hr><p>\u9664\u4E86\u5B69\u5B50\u7684\u7761\u89C9\u95EE\u9898\uFF0C\u56F0\u6270\u5BB6\u957F\u7684\u8FD8\u6709\u5F88\u591A\u7684\u517B\u80B2\u95EE\u9898\uFF0C\u6BD4\u5982\u5B69\u5B50\u5C31\u662F\u62D6\u62C9\u78E8\u8E6D\uFF0C\u672C\u6587\u4F5C\u8005\u738B\u99A8\u533B\u751F\u6B64\u524D\u4E5F\u5728\u6021\u79BE\u5F00\u8BBE\u8FC7\u76F8\u5173\u8BFE\u7A0B\uFF0C\u5927\u7EB2\u5982\u4E0B\u3002</p><p>\u6709\u9700\u8981\u7684\u53EF\u901A\u8FC7\u4E0B\u9762\u4E8C\u7EF4\u7801\u4E86\u89E3\uFF0C\u6021\u79BE\u4F1A\u5458\u53EF\u514D\u8D39\u542C\uFF0C\u9605\u8BFB\u539F\u6587\u53EF\u4E86\u89E3\u6021\u79BE\u4F1A\u5458\u3002</p>",10),x={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651633648&idx=1&sn=7cf10fb5ecbc2784e029540e5f79399e&chksm=8b5c9e0abc2b171c92232e6666e865db9bc3e6d0aafa9ecbdcf1105999dcd7bfde13d732e7b1&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},k=e("\u67E5\u770B\u539F\u6587");function z(N,E){const s=t("RouterLink"),r=t("ExternalLinkIcon");return c(),a(_,null,[h,p("ul",null,[p("li",null,[p("p",null,[m,n(s,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651632732&idx=1&sn=ceb2445d59535d9027676bb2641ff575/"},{default:d(()=>[f]),_:1}),g])]),b]),u,p("p",null,[p("a",x,[k,n(r)])])],64)}var L=i(l,[["render",z]]);export{L as default};
