import{r as c,o as a,c as r,a as e,b as t,F as n,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=o('<h3 id="\u533B\u751F\u4EB2\u5386-\u5B69\u5B50\u5F97\u4E86\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u600E\u4E48\u529E" tabindex="-1"><a class="header-anchor" href="#\u533B\u751F\u4EB2\u5386-\u5B69\u5B50\u5F97\u4E86\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u600E\u4E48\u529E" aria-hidden="true">#</a> \u533B\u751F\u4EB2\u5386\uFF1A\u5B69\u5B50\u5F97\u4E86\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u600E\u4E48\u529E\uFF1F</h3><p>\u590F\u5929\u5230\u4E86\uFF0C\u6700\u8FD1\u513F\u7AE5\u533B\u9662\u770B\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u7684\u5B69\u5B50\u591A\u4E86\u8D77\u6765\uFF0C\u5B69\u5B50\u5F97\u4E86\u8FD9\u4E2A\u75C5\u5F88\u591A\u5BB6\u957F\u5FE7\u5FC3\u5FE1\u5FE1\uFF0C\u4E0D\u77E5\u9053\u600E\u4E48\u529E\u3002\u6211\u5973\u513F\u4E5F\u5F97\u8FC7\u4E00\u6B21\u8FD9\u4E2A\u75C5\uFF0C\u5206\u4EAB\u4E00\u4E0B\u8FD9\u4E2A\u7ECF\u5386\uFF0C\u4F9B\u540C\u75C5\u76F8\u601C\u7684\u5BB6\u957F\u4EEC\u53C2\u8003\u3002</p><p>\u524D\u5E746\u6708\uFF0C\u5973\u513F\u7ECF\u5386\u4E86\u5168\u5E74\u552F\u4E00\u7684\u4E00\u6B21\u751F\u75C5\uFF0C\u90A3\u5929\u5403\u5B8C\u665A\u996D\u6D17\u5B8C\u6FA1\uFF0C\u5979\u8BF4\u5589\u5499\u75DB\uFF0C\u5934\u6655\u72AF\u56F0\uFF0C\u7136\u540E\u65E9\u65E9\u4E0A\u5E8A\u7761\u89C9\u53BB\u4E86\uFF0C\u8981\u5728\u5E73\u65F6\uFF0C\u5979\u603B\u662F\u73A9\u5230\u6211\u4EEC\u4E00\u518D\u50AC\u4FC3\u4E0B\u624D\u80AF\u53BB\u7761\u89C9\u3002</p><p>\u90A3\u5929\u665A\u4E0A\u5E7C\u513F\u56ED\u7684\u5BB6\u957F\u7FA4\u91CC\u4E5F\u5F88\u70ED\u95F9\uFF0C\u56E0\u4E3A\u5F88\u591A\u5B69\u5B50\u90FD\u53D1\u70E7\u4E86\uFF0C\u6709\u7684\u8BF4\u5934\u75DB\uFF0C\u5589\u5499\u75DB\uFF0C\u6709\u7684\u56E0\u4E3A\u9AD8\u70E7\u53BB\u4E86\u533B\u9662\uFF0C\u6709\u7684\u8BF4\u533B\u751F\u68C0\u67E5\u4E86\u5589\u5499\uFF0C\u8BF4\u662F\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u3002\u6211\u4EEC\u6478\u4E86\u6478\u5973\u513F\u7684\u989D\u5934\uFF0C\u679C\u4E0D\u5176\u7136\u5979\u4E5F\u70E7\u8D77\u6765\u4E86\u3002</p><p>\u5E73\u65F6\u5973\u513F\u53D1\u70E7\uFF0C\u6211\u591A\u5C11\u4E5F\u4F1A\u6709\u70B9\u62C5\u5FC3\u7D27\u5F20\uFF0C\u5012\u4E0D\u662F\u62C5\u5FC3\u70E7\u51FA\u4EC0\u4E48\u95EE\u9898\uFF0C\u800C\u662F\u4E0D\u77E5\u9053\u53D1\u70E7\u662F\u4EC0\u4E48\u539F\u56E0\uFF0C\u90A3\u6B21\u6211\u5374\u6CA1\u600E\u4E48\u62C5\u5FC3\uFF0C\u56E0\u4E3A\u4E00\u4E2A\u73ED\u91CC\u7A81\u7136\u8FD9\u4E48\u591A\u5B69\u5B50\u96C6\u4F53\u51FA\u73B0\u4E86\u53D1\u70E7\uFF0C\u800C\u4E14\u6709\u7684\u5B69\u5B50\u5DF2\u7ECF\u51FA\u73B0\u54BD\u5589\u90E8\u7684\u75B1\u75B9\uFF0C\u5C31\u5F88\u6E05\u695A\u5979\u53D1\u70E7\u7684\u539F\u56E0\u4E86\u3002</p><p>\u4E0D\u62C5\u5FC3\u7684\u53E6\u4E00\u4E2A\u539F\u56E0\u662F\u6211\u77E5\u9053\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u662F\u4E00\u4E2A\u9884\u540E\u5F88\u597D\uFF0C\u4E5F\u662F\u4E00\u4E2A\u6CA1\u6709\u7279\u6548\u6CBB\u7597\u7684\u81EA\u6108\u6027\u75BE\u75C5\uFF0C\u9664\u4E86\u7B49\u65F6\u95F4\uFF0C\u4E5F\u5C31\u662F\u53EA\u80FD\u5BF9\u75C7\u6CBB\u7597\u3002</p><p>\u63A5\u4E0B\u6765\uFF0C\u5979\u95F4\u95F4\u65AD\u65AD\u70E7\u4E86\u4E24\u5929\uFF0C\u5076\u5C14\u8FD8\u662F\u8BF4\u5589\u5499\u75DB\u5934\u75DB\uFF0C\u6211\u68C0\u67E5\u4E86\u5589\u5499\u6CA1\u6709\u770B\u5230\u75B1\u75B9\uFF0C\u7ED9\u5979\u51C6\u5907\u7A00\u996D\uFF0C\u5589\u5499\u75DB\u5C31\u559D\u51C9\u6C34\uFF0C\u770B\u5979\u7CBE\u795E\u4E5F\u90FD\u4E0D\u9519\uFF0C\u80FD\u5403\u80FD\u73A9\uFF0C\u4F53\u6E29\u4E5F\u6CA1\u600E\u4E48\u91CF\uFF0C\u9000\u70E7\u836F\u4E5F\u6CA1\u5403\uFF0C\u4E24\u5929\u540E\u6240\u6709\u75C7\u72B6\u90FD\u6D88\u5931\u4E86\u3002</p><p>\u5973\u513F\u73ED\u4E0A\u56DB\u5341\u4E2A\u5B69\u5B50\uFF0C\u56E0\u4E3A\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\uFF0C\u63A5\u4E0B\u6765\u7684\u5468\u4E00\u53EA\u6709\u4E03\u516B\u4E2A\u53BB\u4E0A\u5B66\u4E86\uFF0C\u6709\u7684\u56E0\u4E3A\u751F\u75C5\u6CA1\u53BB\uFF0C\u6709\u7684\u662F\u56E0\u4E3A\u62C5\u5FC3\u88AB\u4F20\u67D3\u4E0A\u6CA1\u53BB\u3002\u4E00\u4E24\u5929\u5185\u5BFC\u81F4\u4E00\u4E2A\u73ED\u51E0\u4E4E\u505C\u8BFE\uFF0C\u4E5F\u8BA9\u6211\u89C1\u8BC6\u4E86\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u7684\u4F20\u67D3\u6027\u3002</p><p>\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u548C\u624B\u8DB3\u53E3\u75C5\u4E00\u6837\uFF0C\u90FD\u662F\u80A0\u9053\u75C5\u6BD2\u611F\u67D3\u5F15\u8D77\u7684\uFF0C\u624B\u8DB3\u53E3\u75C5\u4E3B\u8981\u7531\u67EF\u8428\u5947A16\u548C\u80A0\u9053\u75C5\u6BD2EV71\u5F15\u8D77\uFF0C\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u4E3B\u8981\u7531\u67EF\u8428\u5947A\u578B\u5F15\u8D77\uFF0C\u5305\u62ECA1-6,8\u7B49\uFF0CEV71\u4E5F\u53EF\u4EE5\u5F15\u8D77\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\uFF0C\u4F46\u6BD4\u8F83\u5C11\u3002</p><p>\u56E0\u4E3A\u5BFC\u81F4\u53D1\u75C5\u7684\u75C5\u6BD2\u5DEE\u4E0D\u591A\uFF0C\u6240\u4EE5\u4E24\u4E2A\u75C5\u7684\u75C7\u72B6\u4E5F\u5F88\u76F8\u4F3C\uFF0C\u90FD\u53EF\u80FD\u4F1A\u6709\u53D1\u70E7\uFF0C\u5589\u5499\u75DB\uFF0C\u5934\u75DB\uFF0C\u5455\u5410\u8FD9\u4E9B\u75C7\u72B6\uFF0C\u4E5F\u90FD\u53EF\u80FD\u4F1A\u6709\u75B1\u75B9\u3002\u53EA\u4E0D\u8FC7\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u7684\u75B1\u75B9\u5C40\u9650\u4E8E\u54BD\u5589\u90E8\uFF0C\u4EE5\u54BD\u540E\u58C1\uFF0C\u6241\u6843\u4F53\uFF0C\u8F6F\u816D\u4E3A\u4E3B\uFF0C\u800C\u624B\u8DB3\u53E3\u75C5\u7684\u75B1\u75B9\u9664\u4E86\u53EF\u4EE5\u51FA\u73B0\u5728\u53E3\u54BD\u90E8\uFF0C\u8FD8\u53EF\u4EE5\u51FA\u73B0\u5728\u624B\u3001\u8DB3\u3001\u5C41\u80A1\u3001\u8179\u80A1\u6C9F\u533A\uFF0C\u9664\u975E\u662F\u51FA\u73B0\u4E86\u8FD9\u4E9B\u90E8\u4F4D\u7684\u75B1\u75B9\uFF0C\u624B\u8DB3\u53E3\u75C5\u548C\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u5F88\u96BE\u533A\u522B\u3002</p><p>\u540C\u6837\u662F\u80A0\u9053\u75C5\u6BD2\u611F\u67D3\uFF0C\u5728\u4E0D\u540C\u7684\u4EBA\u8EAB\u4E0A\u8868\u73B0\u51FA\u6765\u7684\u75C7\u72B6\u53EF\u80FD\u5B8C\u5168\u4E0D\u540C\uFF0C\u6709\u7684\u5B69\u5B50\u611F\u67D3\u540E\u53EF\u4EE5\u6CA1\u6709\u4EFB\u4F55\u75C7\u72B6\uFF0C\u6709\u7684\u53EA\u8868\u73B0\u4E3A\u53D1\u70E7\uFF0C\u6709\u7684\u53D1\u70E7\u3001\u5934\u75DB\u80CC\u75DB\u5455\u5410\uFF0C\u6709\u7684\u8FDE\u540C\u75B1\u75B9\u3001\u5589\u5499\u75DB\u8FD9\u4E9B\u75C7\u72B6\u5168\u90FD\u51FA\u73B0\uFF0C\u8FD9\u4E9B\u75C7\u72B6\u6301\u7EED\u7684\u65F6\u95F4\u4E5F\u56E0\u4EBA\u800C\u5F02\uFF0C\u6709\u7684\u6301\u7EED\u4E24\u4E09\u5929\uFF0C\u6709\u7684\u6301\u7EED\u4E00\u5468\uFF0C\u4E5F\u6709\u6781\u5C11\u4E00\u90E8\u5206\u51FA\u73B0\u8111\u708E\u8FD9\u6837\u7684\u4E25\u91CD\u5E76\u53D1\u75C7\u3002\u5BF9\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u6765\u8BF4\uFF0C\u5C0F\u4E00\u70B9\u7684\u5B69\u5B50\u53D1\u70E7\u66F4\u5389\u5BB3\uFF0C\u5927\u5B69\u5B50\u5934\u75DB\u80CC\u75DB\u7684\u75C7\u72B6\u66F4\u660E\u663E\u3002</p><p>\u56E0\u4E3A\u548C\u624B\u8DB3\u53E3\u75C5\u4E00\u6837\u90FD\u662F\u80A0\u9053\u75C5\u6BD2\u611F\u67D3\u5F15\u8D77\u7684\uFF0C\u76EE\u524D\u6CA1\u6709\u9488\u5BF9\u80A0\u9053\u75C5\u6BD2\u7684\u6297\u75C5\u6BD2\u836F\u7269\uFF0C\u6240\u4EE5\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u7684\u6CBB\u7597\u4E5F\u548C\u624B\u8DB3\u53E3\u75C5\u4E00\u6837\uFF0C\u90FD\u662F\u4EE5\u5BF9\u75C7\u6CBB\u7597\u4E3A\u4E3B\u3002\u4F53\u6E29\u8D85\u8FC739\u5EA6\u5B69\u5B50\u660E\u663E\u4E0D\u8212\u670D\u7684\u65F6\u5019\u53EF\u4EE5\u5403\u5E03\u6D1B\u82AC\u548C\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u9000\u70E7\uFF0C\u8FD9\u4E9B\u836F\u7269\u540C\u65F6\u4E5F\u6709\u6B62\u75DB\u7684\u6548\u679C\uFF0C\u540C\u65F6\u53EF\u4EE5\u7F13\u89E3\u5589\u5499\u75DB\u3001\u5934\u75DB\u80CC\u75DB\u3002</p><p>\u4E3A\u51CF\u8F7B\u54BD\u5589\u90E8\u7684\u523A\u6FC0\uFF0C\u5C3D\u91CF\u7ED9\u5B69\u5B50\u5403\u6E05\u6DE1\u7684\uFF0C\u4E0D\u9700\u8981\u600E\u4E48\u5480\u56BC\u7684\u98DF\u7269\uFF0C\u5589\u5499\u75DB\u5F97\u6BD4\u8F83\u5389\u5BB3\u7684\u65F6\u5019\uFF0C\u7ED9\u5B69\u5B50\u559D\u51C9\u6C34\u3001\u51B0\u6C34\u3001\u5403\u51B0\u68D2\uFF0C\u4E00\u65B9\u9762\u53EF\u4EE5\u6682\u65F6\u7F13\u89E3\u4E00\u70B9\u75BC\u75DB\uFF0C\u53E6\u4E00\u65B9\u9762\u53EF\u4EE5\u8865\u5145\u6C34\u5206\u9884\u9632\u8131\u6C34\uFF0C\u9664\u6B64\u4E4B\u5916\u5C31\u662F\u7B49\u81EA\u6108\u3002</p><p>\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u51FA\u73B0\u8111\u708E\u5F88\u5C11\u89C1\uFF0C\u4F46\u8FD8\u662F\u8981\u4FDD\u6301\u8B66\u60D5\uFF0C\u5982\u679C\u5B69\u5B50\u51FA\u73B0\u5934\u75DB\u3001\u5455\u5410\u3001\u8116\u5B50\u50F5\u786C\u6216\u75BC\u75DB\uFF0C\u6216\u8005\u884C\u4E3A\u5F02\u5E38\u3001\u547C\u5438\u6025\u4FC3\u3001\u7CBE\u795E\u4E0D\u597D\u4E5F\u8981\u4E0A\u533B\u9662\u770B\u770B\u3002\u5982\u679C\u75BC\u5F97\u8FDE\u6C34\u90FD\u559D\u4E0D\u4E86\uFF0C\u6709\u8131\u6C34\u8868\u73B0\u4E5F\u8981\u4E0A\u533B\u9662\u3002</p><p>\u80A0\u9053\u75C5\u6BD2\u4E3B\u8981\u901A\u8FC7\u7CAA-\u53E3\u4F20\u64AD\uFF0C\u4E5F\u53EF\u4EE5\u901A\u8FC7\u54B3\u55FD\u3001\u55B7\u568F\u7684\u98DE\u6CAB\u4F20\u64AD\uFF0C\u5728\u53D1\u75C5\u524D\u7684\u6F5C\u4F0F\u671F\u5230\u60A3\u75C5\u540E\u7684\u51E0\u5468\u4E4B\u5185\uFF0C\u90FD\u4F1A\u6709\u4F20\u67D3\u6027\uFF0C\u4EE5\u611F\u67D3\u540E\u7684\u7B2C\u4E00\u5468\u4F20\u67D3\u6027\u6700\u5F3A\uFF0C\u6240\u4EE5\u5728\u7FA4\u5C45\u7684\u5E7C\u513F\u56ED\u3001\u5B66\u6821\u5F88\u5BB9\u6613\u5F15\u8D77\u7206\u53D1\u6D41\u884C\u3002</p><p>\u65B0\u4E0A\u5E02\u7684EV71\u75AB\u82D7\uFF0C\u53EF\u4EE5\u9884\u9632EV71\u75C5\u6BD2\u5F15\u8D77\u7684\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\uFF0C\u4F46\u9884\u9632\u4E0D\u4E86\u5176\u5B83\u80A0\u9053\u75C5\u6BD2\u5F15\u8D77\u7684\u8FD9\u4E2A\u75C5\uFF0C\u4F60\u4E0D\u77E5\u9053\u522B\u7684\u5B69\u5B50\u662F\u4E0D\u662F\u5904\u4E8E\u6F5C\u4F0F\u671F\uFF0C\u6240\u4EE5\u53EA\u8981\u53BB\u4E0A\u5B66\u8FC7\u96C6\u4F53\u751F\u6D3B\u5C31\u6709\u53EF\u80FD\u88AB\u4F20\u67D3\uFF0C\u597D\u5728\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\u9884\u540E\u4E5F\u90FD\u5F88\u597D\uFF0C\u611F\u67D3\u4E86\u4E5F\u4E0D\u5FC5\u592A\u62C5\u5FC3\uFF0C\u56E0\u4E3A\u5BB3\u6015\u88AB\u4F20\u67D3\u8BA9\u5B69\u5B50\u51E0\u4E2A\u661F\u671F\u90FD\u4E0D\u4E0A\u8BFE\u662F\u4E0D\u73B0\u5B9E\u4E5F\u4E0D\u7406\u667A\u3002</p><p>\u9664\u4E86\u63A5\u79CDEV71\u75AB\u82D7\u5916\uFF0C\u6211\u4EEC\u6240\u80FD\u505A\u7684\u662F\u6CE8\u610F\u536B\u751F\uFF0C\u5B69\u5B50\u4F7F\u7528\u7684\u7269\u54C1\u3001\u73A9\u5177\u4E4B\u7C7B\u53CA\u65F6\u6E05\u6D17\u6D88\u6BD2\uFF0C\u540C\u65F6\u8BA9\u5B69\u5B50\u4EEC\u517B\u6210\u52E4\u6D17\u624B\u7684\u597D\u4E60\u60EF\u6765\u51CF\u5C11\u611F\u67D3\u7684\u98CE\u9669\u3002</p><p>\u70B9\u51FB<strong>\u9605\u8BFB\u539F\u6587</strong>\uFF0C\u53EF\u5411\u533B\u751F\u54A8\u8BE2</p>',18),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626747&idx=2&sn=e69bc88dec72851b275efce1d596d1d0&chksm=8b5cb501bc2b3c178117d82c730de08cea12594ff0c56dfd49b8a704805ec1ebc062f9d933bb&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function f(b,m){const p=c("ExternalLinkIcon");return a(),r(n,null,[i,e("p",null,[e("a",l,[h,t(p)])])],64)}var V=d(_,[["render",f]]);export{V as default};
