import{r as n,o as i,c,a as p,b as o,w as s,F as d,e,d as r}from"./app.9aaa2256.js";import{_ as l}from"./plugin-vue_export-helper.21dcd24c.js";const _={},h=p("h3",{id:"\u5FEB\u5F00\u5B66\u4E86-\u5B69\u5B50\u8FD1\u89C6-\u5982\u4F55\u7ED9\u4ED6\u914D\u4E00\u526F\u5408\u9002\u7684\u773C\u955C",tabindex:"-1"},[p("a",{class:"header-anchor",href:"#\u5FEB\u5F00\u5B66\u4E86-\u5B69\u5B50\u8FD1\u89C6-\u5982\u4F55\u7ED9\u4ED6\u914D\u4E00\u526F\u5408\u9002\u7684\u773C\u955C","aria-hidden":"true"},"#"),e(" \u5FEB\u5F00\u5B66\u4E86\uFF0C\u5B69\u5B50\u8FD1\u89C6\uFF0C\u5982\u4F55\u7ED9\u4ED6\u914D\u4E00\u526F\u5408\u9002\u7684\u773C\u955C\uFF1F")],-1),g=e("\u6628\u5929\u7684\u63A8\u9001"),m=e("\uFF0C\u6765\u81EA\u95FD\u5357\u5730\u533A\u8BFB\u8005\u7684\u7559\u8A00\u4E0D\u5C11\uFF0C \u5F88\u591A\u90FD\u662F\u5410\u69FD\u7ED9\u65B0\u751F\u513F\u5582\u9AD8\u4E3D\u53C2\u6C34\u8FD9\u4EF6\u4E8B\uFF0C\u4E5F\u6709\u5410\u69FD\u5582\u86C7\u80C6\u3001\u8461\u8404\u7CD6\u6C34\u7684\u3002"),f=r("<p>\u5176\u5B9E\u7C7B\u4F3C\u7684\u504F\u65B9\u80AF\u5B9A\u4E0D\u6B62\u95FD\u5357\u6709\uFF0C\u5168\u56FD\u5404\u5730\u90FD\u6709\uFF0C\u53EA\u4E0D\u8FC7\u5404\u5730\u6709\u5404\u5730\u7684\u7279\u8272\u7F62\u4E86\u3002</p><hr><p>\u8A00\u5F52\u6B63\u4F20\u3002\u9A6C\u4E0A\u5F00\u5B66\u4E86\uFF0C\u5B69\u5B50\u4EEC\u9A6C\u4E0A\u8981\u56DE\u5F52\u6821\u56ED\u3002</p><p>\u672C\u6765\u8FD1\u89C6\u5C31\u5F88\u9AD8\u53D1\uFF0C\u518D\u52A0\u4E0A\u4ECA\u5E74\u75AB\u60C5\u5B69\u5B50\u4EEC\u6237\u5916\u65F6\u95F4\u5C11\u4E86\uFF0C\u8FD1\u8DDD\u79BB\u7528\u773C\u66F4\u591A\uFF0C\u8FD1\u89C6\u7684\u95EE\u9898\u5C31\u66F4\u7A81\u51FA\u4E86\uFF0C\u5F88\u591A\u5BB6\u957F\u4E5F\u5728\u4E3A\u4E4B\u53D1\u6101\u4E86\u3002</p><p>\u8FD1\u89C6\u4E86\uFF0C\u5E94\u5BF9\u7684\u6700\u597D\u65B9\u6CD5\u5C31\u662F\u5230\u533B\u9662\u68C0\u67E5\uFF0C\u914D\u5907\u5408\u9002\u7684\u773C\u955C\u3002\u90A3\u773C\u955C\u5E94\u8BE5\u600E\u4E48\u914D\u5462\uFF1F\u4E01\u5A1F\u533B\u751F\u66FE\u5199\u8FC7\u6587\u7AE0\uFF0C\u5206\u4EAB\u7ED9\u5927\u5BB6\u3002</p><p><strong>\u513F\u7AE5\u773C\u955C\u600E\u4E48\u9009\uFF1F</strong></p><p><strong>\u773C\u955C\u6846\u7684\u9009\u62E9</strong></p><p>\u8981\u9009\u62E9\u4E00\u4E2A\u5408\u9002\u7684\u513F\u7AE5\u955C\u6846\uFF0C\u6700\u597D\u662F\u5E26\u5B69\u5B50\u53BB\u773C\u955C\u5E97\uFF0C\u8BA9\u5B69\u5B50\u4EB2\u81EA\u5C1D\u8BD5\u5404\u79CD\u4E0D\u540C\u7684\u955C\u6846\u3002</p><p>\u955C\u6846\u9700\u8981\u6234\u5728\u8138\u4E0A\u8D34\u5408\u9F3B\u5B50\u8033\u6735\uFF0C\u4E0D\u5BB9\u6613\u6ED1\u843D\uFF0C\u540C\u65F6\u4E5F\u8212\u670D\u7F8E\u89C2\u3002\u9F3B\u6258\u5E94\u8BE5\u6B63\u597D\u67B6\u5728\u9F3B\u6881\u4E0A\uFF0C\u4E0D\u662F\u7279\u522B\u7D27\u5730\u538B\u4E0B\u53BB\uFF0C\u4F46\u4E5F\u4E0D\u80FD\u662F\u60AC\u7A7A\u7684\uFF0C\u4E0D\u7136\u5F88\u5BB9\u6613\u5C31\u6ED1\u843D\u4E0B\u6765\u3002</p><p>\u5851\u6599\u955C\u6846\u56E0\u4E3A\u53EF\u4EE5\u6709\u5404\u79CD\u4E0D\u540C\u7684\u989C\u8272\uFF0C\u53EF\u80FD\u4F1A\u6BD4\u8F83\u6F02\u4EAE\u3001\u597D\u770B\uFF0C\u4F46\u662F\u7F3A\u4E4F\u5F39\u6027\uFF0C\u6709\u65F6\u5019\u4E0D\u4E00\u5B9A\u80FD\u591F\u505A\u5230\u5F88\u597D\u5730\u8D34\u5408\u9F3B\u5B50\u548C\u8138\u578B\u3002</p><p>\u949B\u5408\u91D1\u6750\u6599\u7684\u955C\u6846\u6BD4\u8F83\u597D\u8C03\u6574\uFF0C\u901A\u5E38\u80FD\u591F\u7B26\u5408\u5B69\u5B50\u7684\u8138\u578B\u3002</p><p>\u5982\u679C\u62C5\u5FC3\u513F\u7AE5\u6D3B\u52A8\u91CF\u6BD4\u8F83\u5927\uFF0C\u8DD1\u8DF3\u8FC7\u7A0B\u4E2D\u5C06\u773C\u955C\u4E22\u5931\uFF0C\u53EF\u4EE5\u8003\u8651\u9009\u62E9\u8FD0\u52A8\u578B\u7684\u513F\u7AE5\u955C\u6846\u3002\u8FD9\u79CD\u955C\u6846\u6BD4\u8F83\u9002\u5408\u5954\u8DD1\u3001\u8E66\u8DF3\uFF0C\u800C\u4E14\u6750\u6599\u66F4\u575A\u56FA\uFF0C\u4FDD\u62A4\u6027\u6BD4\u666E\u901A\u955C\u6846\u66F4\u597D\u3002</p><p><strong>\u955C\u7247\u6750\u8D28\u7684\u9009\u62E9</strong></p><p>\u955C\u7247\u5E94\u8BE5\u9009\u62E9\u4F7F\u7528\u805A\u78B3\u9178\u916F\u7684\u6750\u6599\uFF0C\u8FD9\u662F\u4E00\u79CD\u4E0D\u5BB9\u6613\u788E\u7684\u6750\u6599\uFF0C\u53EF\u4EE5\u8D77\u5230\u4FDD\u62A4\u513F\u7AE5\u773C\u775B\u7684\u4F5C\u7528\u3002</p><p><strong>\u5176\u4ED6\u5C0F\u914D\u4EF6</strong></p><p>\u5BF9\u4E8E\u4E09\u5C81\u4EE5\u4E0B\u7684\u513F\u7AE5\uFF0C\u6700\u597D\u80FD\u591F\u5728\u773C\u955C\u817F\u67B6\u5728\u8033\u6735\u5904\u52A0\u4E0A\u4E00\u4E2A\u7845\u80F6\u7684\u5C0F\u57AB\u5B50\uFF0C\u8FD9\u6837\u773C\u955C\u4E0D\u5BB9\u6613\u4ECE\u8033\u6735\u4E0A\u6ED1\u843D\u3002</p><p>\u5BF9\u4E8E\u5E74\u9F84\u66F4\u5C0F\u7684\u5A74\u513F\uFF0C\u5E94\u8BE5\u5728\u773C\u955C\u540E\u9762\u7528\u4E00\u6839\u7EF3\u5B50\u7CFB\u4F4F\uFF0C\u5426\u5219\u773C\u955C\u5F88\u5BB9\u6613\u4ECE\u8138\u4E0A\u6ED1\u6389\uFF0C\u6216\u8005\u88AB\u5B9D\u5B9D\u7684\u624B\u626F\u6389\u3002</p><p>\u5728\u773C\u955C\u5E97\u4E00\u822C\u53EF\u4EE5\u8D2D\u4E70\u5F97\u5230\u8FD9\u4E9B\u5C0F\u914D\u4EF6\u3002</p><p><strong>\u914D\u955C\u9009\u62E9\u533B\u9662\u8FD8\u662F\u5916\u9762\u773C\u955C\u5E97\uFF1F</strong></p><p>\u6BCF\u4E00\u526F\u826F\u597D\u7684\u9002\u5408\u5B69\u5B50\u7684\u773C\u955C\u90FD\u5E94\u8BE5\u6EE1\u8DB3\u4EE5\u4E0A\u6761\u4EF6\uFF0C\u53EA\u8981\u7B26\u5408\u4EE5\u4E0A\u513F\u7AE5\u914D\u773C\u955C\u7684\u4E00\u4E9B\u6CE8\u610F\u4E8B\u9879\uFF0C\u662F\u5728\u533B\u9662\u9644\u5C5E\u7684\u773C\u955C\u5E97\u3001\u8FD8\u662F\u5728\u5916\u8FB9\u7684\u72EC\u7ACB\u773C\u955C\u5E97\u914D\u955C\uFF0C\u8FD9\u4E2A\u5012\u5E76\u4E0D\u91CD\u8981\u3002</p><p>\u6700\u4E3B\u8981\u7684\u662F\u5BB6\u957F\u62FF\u7740\u533B\u751F\u7ED9\u7684\u773C\u955C\u5904\u65B9\u5355\u6765\u4EB2\u81EA\u6311\u9009\u5408\u9002\u5B69\u5B50\u7684\u773C\u955C\uFF0C\u54EA\u4E2A\u773C\u955C\u5E97\u7684\u955C\u6846\u5B69\u5B50\u6234\u8D77\u6765\u5408\u9002\u5C31\u9009\u54EA\u4E2A\u3002</p><p><strong>\u4EF7\u683C\u8D8A\u8D35\u5C31\u66F4\u597D\u5417\uFF1F</strong></p><p>\u4EF7\u94B1\u4E5F\u4E0D\u662F\u9009\u62E9\u955C\u6846\u6216\u8005\u955C\u7247\u7684\u6807\u51C6\uFF0C\u5982\u679C\u8D35\u4F46\u5E76\u4E0D\u7B26\u5408\u4EE5\u4E0A\u7684\u6761\u4EF6\uFF0C\u90A3\u4E5F\u6CA1\u5FC5\u8981\u8D2D\u4E70\uFF0C\u56E0\u4E3A\u8D35\u4E0D\u4E00\u5B9A\u66F4\u597D\u3002</p><p>\u53EA\u8981\u7B26\u5408\u4EE5\u4E0A\u6761\u4EF6\uFF0C\u4FBF\u5B9C\u6216\u8D35\u7684\u955C\u6846\u90FD\u53EF\u4EE5\u9009\u7528\u3002</p><p><strong>\u90A3\u4E9B\u5BA3\u79F0\u80FD\u5EF6\u7F13\u8FD1\u89C6\u7684\u8FD1\u89C6\u955C\u7247\u53EF\u4EE5\u9009\u62E9\u5417\uFF1F</strong></p><p>\u5982\u679C\u662F\u5355\u5149\u955C\u7247\uFF0C\u4E5F\u5C31\u662F\u666E\u901A\u7684\u77EB\u6B63\u8FD1\u89C6\u7684\u955C\u7247\uFF0C\u662F\u4E0D\u5177\u6709\u63A7\u5236\u8FD1\u89C6\u53D1\u5C55\u7684\u6548\u679C\uFF0C\u53EA\u662F\u5355\u7EAF\u77EB\u6B63\u89C6\u529B\u4F5C\u7528\u800C\u5DF2\u3002</p>",26),b=e("\u76F8\u6BD4\u76EE\u524D\u5DF2\u516C\u8BA4\u6BD4\u8F83\u597D\u7684\u63A7\u5236\u8FD1\u89C6\u65B9\u6CD5\uFF0C\u5982"),u=p("strong",null,"\u89D2\u819C\u5851\u5F62\u955C",-1),x=e("\u548C\u4F4E\u6D53\u5EA6\u963F\u6258\u54C1\u773C\u836F\u6C34\uFF0C\u4F20\u7EDF\u7684\u591A\u7126\u8DDD\u955C\u7247\u7684\u773C\u955C\u5BF9\u4E8E\u7F13\u89E3\u8FD1\u89C6\u8FDB\u4E00\u6B65\u53D1\u5C55\u4F5C\u7528\u4E5F\u975E\u5E38\u6709\u9650\u3002"),D=r("<p>\u6700\u8FD1\u6709\u4E00\u79CD\u591A\u7126\u8DDD\u955C\u7247\uFF08DIMS\uFF09\u7684\u65B0\u4EA7\u54C1\uFF0C\u5728160\u4F4D\u4E2D\u56FD\u513F\u7AE5\u53C2\u4E0E\u7684\u4E3A\u671F2\u5E74\u7684\u521D\u6B65\u7814\u7A76\u4E2D\u53D1\u73B0\uFF0C\u513F\u7AE5\u7684\u8FD1\u89C6\u8FDB\u5C55\u964D\u4F4E\u4E8659%[1]\u3002</p><p>\u7531\u4E8E\u8FD9\u4E2A\u4EA7\u54C1\u6BD4\u8F83\u65B0\uFF0C\u76EE\u524D\u7684\u7814\u7A76\u5E76\u4E0D\u591A\uFF0C\u548C\u89D2\u819C\u5851\u5F62\u955C\u6216\u8005\u4F4E\u6D53\u5EA6\u963F\u6258\u54C1\u773C\u836F\u6C34\u7684\u7814\u7A76\u76F8\u6BD4\uFF0C\u6587\u732E\u6BD4\u8F83\u5C11\uFF0C\u5E76\u4E14\u8FD8\u7F3A\u5C11\u72EC\u7ACB\u5B9E\u9A8C\u5BA4\u6216\u8005\u7B2C\u4E09\u65B9\u7814\u7A76\u7684\u9A8C\u8BC1\u3002</p><p>\u4F46\u662F\u5BF9\u4E8E\u4E0D\u613F\u610F\u4F7F\u7528\u89D2\u819C\u5851\u5F62\u955C\u6216\u8005\u4F4E\u6D53\u5EA6\u963F\u6258\u54C1\u773C\u836F\u6C34\u7684\u5B69\u5B50\u6765\u8BF4\uFF0C\u8FD9\u7C7B\u773C\u955C\u4E5F\u8BB8\u53EF\u4EE5\u8003\u8651\u3002</p><p>\u4F46\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u5E02\u9762\u4E0A\u5404\u79CD\u591A\u7126\u8DDD\u773C\u955C\u53EF\u80FD\u4F1A\u5F88\u591A\uFF0C\u9664\u4E86\u4E0A\u8FF0\u7814\u7A76\u4E2D\u91C7\u7528\u7684\u955C\u7247\u5916\uFF0C\u522B\u7684\u5E76\u6CA1\u6709\u7814\u7A76\u8BF4\u660E\u5176\u80FD\u63A7\u5236\u8FD1\u89C6\u3002</p><p>\u7531\u4E8E\u8FD9\u7C7B\u773C\u955C\u4EF7\u683C\u4E0D\u4FBF\u5B9C\uFF0C\u6240\u4EE5\u5BB6\u957F\u8D2D\u4E70\u524D\u8BF7\u6CE8\u610F\u8FD9\u4E2A\u6240\u8C13\u7684\u63A7\u5236\u8FD1\u89C6\u7684\u955C\u7247\u6280\u672F\u662F\u5426\u6709\u7814\u7A76\u652F\u6301\u3002</p><p>\u6211\u4EEC\u66FE\u8BF4\u8FC7\u6237\u5916\u6D3B\u52A8\uFF0C\u591A\u63A5\u89E6\u9633\u5149\u6709\u52A9\u4E8E\u9884\u9632\u8FD1\u89C6\u53D1\u751F\uFF0C\u65B0\u7684\u7814\u7A76\u4E5F\u53D1\u73B0\uFF0C\u5373\u4F7F\u8FD1\u89C6\u5DF2\u7ECF\u53D1\u751F\uFF0C\u591A\u505A\u6237\u5916\u6D3B\u52A8\u4ECD\u7136\u6BD4\u7F3A\u4E4F\u6237\u5916\u6D3B\u52A8\u66F4\u80FD\u7F13\u89E3\u8FD1\u89C6\u53D1\u5C55[2]\u3002</p><p>\u6240\u4EE5\u5982\u679C\u8FD1\u89C6\u53D1\u751F\u4E86\uFF0C\u7F13\u89E3\u8FD1\u89C6\u52A0\u6DF1\u6700\u5177\u6709\u5145\u8DB3\u8BC1\u636E\u652F\u6301\u7684\u65B9\u6CD5\u662F\u89D2\u819C\u5851\u5F62\u955C\u3001\u4F4E\u6D53\u5EA6\u963F\u6258\u54C1\u773C\u836F\u6C34\u3001\u7EE7\u7EED\u52A0\u5F3A\u6237\u5916\u6D3B\u52A8\u3001\u4EE5\u53CA\u63A7\u5236\u8FD1\u8DDD\u79BB\u7528\u773C\u7684\u65F6\u95F4\u3002</p><p><strong>\u4EC0\u4E48\u68C0\u67E5\u53EF\u4EE5\u51C6\u786E\u5F97\u77E5\u5B69\u5B50\u773C\u775B\u5EA6\u6570\uFF1F</strong></p><p>\u5982\u679C\u8981\u914D\u4E00\u526F\u5408\u9002\u7684\u773C\u955C\uFF0C\u89C6\u529B\u68C0\u67E5\u7ED3\u679C\u4F1A\u5F88\u91CD\u8981\u3002\u4E00\u822C\u53BB\u533B\u9662\u8FDB\u884C\u89C6\u529B\u68C0\u67E5\uFF0C\u533B\u751F\u4F1A\u7ED9\u5B69\u5B50\u505A\u6563\u77B3\u9A8C\u5149\uFF0C\u5373\u4F7F\u7528\u6563\u77B3\u836F\u6C34\u9EBB\u75F9\u776B\u72B6\u808C\u4EE5\u540E\u518D\u8FDB\u884C\u9A8C\u5149\u3002</p><p>\u5BF9\u4E8E\u6563\u77B3\u9A8C\u5149\uFF0C\u5FEB\u6563\u548C\u6162\u6563\u90FD\u662F\u53EF\u4EE5\u7684\u3002\u6162\u6563\u7684\u836F\u6548\u6BD4\u8F83\u957F\uFF0C\u526F\u4F5C\u7528\u65F6\u95F4\u4F1A\u6301\u7EED2-3\u5468\u3002\u56E0\u6B64\u5BF9\u4E8E\u5DF2\u7ECF\u4E0A\u5B66\u7684\u513F\u7AE5\uFF0C\u5FEB\u6563\u66F4\u5408\u9002\u3002\u5F53\u5929\u68C0\u67E5\u5B8C\u4EE5\u540E\uFF0C\u526F\u4F5C\u7528\u6700\u591A\u6301\u7EED\u4E00\u5929[3]\u3002</p><p>\u5982\u679C\u60F3\u8981\u8DDF\u8E2A\u770B\u8FD1\u89C6\u7684\u8FDB\u5C55\uFF0C\u6700\u597D\u662F\u6D4B\u773C\u8F74\u957F\u5EA6\u30026-9\u5C81\u4E4B\u95F4\u7684\u5B69\u5B50\u773C\u8F74\u957F\u5EA6\u5927\u7EA6\u5E73\u5747\u6BCF\u5E74\u589E\u957F0.21mm[4]\uFF0C\u5982\u679C\u5927\u5927\u8D85\u8FC7\u8FD9\u4E2A\u6570\u5B57\uFF0C\u5F80\u5F80\u610F\u5473\u7740\u8FD1\u89C6\u5728\u53D1\u5C55\u3002\uFF08\u672C\u6587\u9996\u53D1\u4E8E\u6021\u79BE\u5065\u5EB7\uFF0C\u533B\u5B66\u6821\u5BF9\uFF1A\u674E\u5FD7\u4F1F\u533B\u751F\uFF09</p><p><strong>\u53C2\u8003\u8D44\u6599</strong></p><p>1.Lam C S Y , Tang W C , Tse Y Y , et al. Defocus Incorporated Multiple Segments (DIMS) spectacle lenses slow myopia progression: a 2-year randomised clinical trial[J]. British Journal of Ophthalmology, 2019, 104(3): bjophthalmol-2018-313739.</p><p>2.Deng, Li PhD1*; Pang, Yi MD, OD, PhD2 Effect of Outdoor Activities in Myopia Control, Optometry and Vision Science: April 2019 - Volume 96 - Issue 4 - p 276-282</p><p>3. \u4E2D\u56FD\u513F\u7AE5\u776B\u72B6\u808C\u9EBB\u75F9\u9A8C\u5149\u53CA\u5B89\u5168\u7528\u836F\u4E13\u5BB6\u5171\u8BC6(2019\u5E74) \u4F5C\u8005:\u4E2D\u534E\u533B\u5B66\u4F1A\u773C\u79D1\u5B66\u5206\u4F1A\u659C\u89C6\u4E0E\u5C0F\u513F\u773C\u79D1\u5B66\u7EC4 \u6765\u6E90:\u4E2D\u534E\u773C\u79D1\u6742\u5FD7, 2019,55(1):7-12.</p><p>4. Tideman JWL, Polling JR, Vingerling JR, et al. Axial length growth and the risk of developing myopia in European children. Acta Ophthalmol. 2018;96(3):301\u2010309. doi:10.1111/aos.13603</p><p>\uFF08\u53EF\u4E0A\u4E0B\u6ED1\u52A8\u67E5\u770B\uFF09</p><hr><p>\u5173\u4E8E\u8FD1\u89C6\u95EE\u9898\uFF0C\u672C\u6587\u4F5C\u8005\u4E01\u5A1F\u533B\u751F\u4E5F\u66FE\u5728\u5728\u6021\u79BE\u5F00\u8BBE\u8FC7\u4E00\u4E2A\u7CFB\u7EDF\u7684\u4E13\u680F\u8BFE\u7A0B\uFF0C\u6709\u9700\u8981\u7684\u53EF\u901A\u8FC7\u4E0B\u9762\u4E8C\u7EF4\u7801\u4E86\u89E3\uFF0C\u6021\u79BE\u4F1A\u5458\u514D\u8D39\u542C\u3002</p>",19),I={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651631060&idx=1&sn=6b399b9307a4bde83f4d007942bd9db7&chksm=8b5c842ebc2b0d38f2a9c6027223bd83daa56caf32b19cf83db6ffbe619e35f209944efc45de&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},M=e("\u67E5\u770B\u539F\u6587");function O(k,z){const t=n("RouterLink"),a=n("ExternalLinkIcon");return i(),c(d,null,[h,p("p",null,[o(t,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651631047&idx=1&sn=be6d5c68b59422c756f663f089709817/"},{default:s(()=>[g]),_:1}),m]),f,p("p",null,[b,o(t,{to:"/s/__biz=MzIwMzM3NjI5OQ==&mid=2247485854&idx=1&sn=a78840cec754bc88c69e8ef81a3d5cdd/"},{default:s(()=>[u]),_:1}),x]),D,p("p",null,[p("a",I,[M,o(a)])])],64)}var E=l(_,[["render",O]]);export{E as default};
