import{r as a,o as c,c as t,a as e,b as n,F as o,d as r,e as s}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const d={},_=r('<h3 id="\u4ECE\u80A4\u4E50\u971C\u770B\u513F\u7814\u6240\u81EA\u5236\u836F" tabindex="-1"><a class="header-anchor" href="#\u4ECE\u80A4\u4E50\u971C\u770B\u513F\u7814\u6240\u81EA\u5236\u836F" aria-hidden="true">#</a> \u4ECE\u80A4\u4E50\u971C\u770B\u513F\u7814\u6240\u81EA\u5236\u836F</h3><p>\u4E0A\u4E00\u7BC7\u6587\u7AE0\u5728\u5FAE\u4FE1\u4E0A\u9605\u8BFB\u91CF\u5F88\u9AD8\uFF0C\u5728\u5FAE\u535A\u4E5F\u6709\u51E0\u5341\u4E07\u7684\u9605\u8BFB\u91CF\u3002\u80A4\u4E50\u971C\u867D\u7136\u77E5\u540D\u5EA6\u5F88\u9AD8\uFF0C\u4F46\u6BD5\u7ADF\u662F\u4E00\u4E2A\u9662\u5185\u81EA\u5236\u836F\uFF0C\u4E3B\u8981\u7528\u6237\u8FD8\u5E94\u8BE5\u5728\u5317\u4EAC\u53CA\u5468\u8FB9\uFF0C\u5173\u6CE8\u8FD9\u4E2A\u836F\u7684\u4EBA\u5E94\u8BE5\u8FD8\u662F\u6709\u9650\uFF0C\u8FD9\u7BC7\u6587\u7AE0\u5F15\u8D77\u8FD9\u4E48\u5927\u7684\u53CD\u54CD\u4E5F\u51FA\u4E4E\u6211\u7684\u610F\u6599\u3002</p><p>\u8FD9\u4E24\u5929\u4E5F\u770B\u5230\u5F88\u591A\u8BC4\u8BBA\uFF0C\u4E0D\u8BBA\u5728\u5FAE\u535A\u8FD8\u662F\u5FAE\u4FE1\uFF0C\u5927\u90E8\u5206\u662F\u8D5E\u540C\u6216\u9707\u60CA\u7684\uFF0C\u4E5F\u6709\u5F88\u591A\u4EBA\u6765\u6C42\u8BC1\u81EA\u5DF1\u7528\u8FC7\u7684\u836F\u818F\u3002\u5F53\u7136\u4E5F\u6709\u4E00\u90E8\u5206\u53CD\u5F39\u548C\u8D28\u7591\uFF0C\u4E3B\u8981\u6765\u81EA\u4E2D\u533B\u7C89\u3001\u513F\u7814\u6240\u5DE5\u4F5C\u4EBA\u5458\u4EE5\u53CA\u4E00\u4E9B\u513F\u7814\u6240\u7684\u4EE3\u8D2D\u3002</p><p>\u4ECE\u8FD9\u4E9B\u8BC4\u8BBA\u91CC\uFF0C\u6211\u4E86\u89E3\u5230\u4E86\u4E00\u4E9B\u65B0\u7684\u4FE1\u606F\uFF0C\u540C\u65F6\u53D1\u73B0\u80A4\u4E50\u971C\u7684\u80CC\u540E\u5176\u5B9E\u8FD8\u6709\u5F88\u591A\u4E1C\u897F\u53EF\u4EE5\u8BF4\uFF0C\u6240\u4EE5\u518D\u8865\u5145\u8FD9\u7BC7\u6587\u7AE0\uFF0C\u4E00\u662F\u56DE\u5E94\u4E00\u4E9B\u8D28\u7591\uFF0C\u4E8C\u662F\u8BF4\u8BF4\u80A4\u4E50\u971C\u80CC\u540E\u7684\u9662\u5185\u81EA\u5236\u836F\u4EA7\u4E1A\u3002</p><p>\u5FAE\u535A\u4E0A\u6709\u4E2A@\u7231\u836F\u4E0D\u836F \u8BA4\u8BC1\u4FE1\u606F\u4E3A\u513F\u7814\u6240\u9644\u5C5E\u513F\u7AE5\u533B\u9662\u4E2D\u7EA7\u836F\u5E08\uFF0C\u4ED6\u4E3A\u80A4\u4E50\u971C\u63D0\u51FA\u4E86\u4E00\u4E9B\u8FA9\u62A4\u610F\u89C1\uFF0C\u8BF4\u6211\u65AD\u7AE0\u53D6\u4E49\uFF0C\u7406\u7531\u662F\u90A3\u7BC7\u8BBA\u6587\u91CCA\u7EC4\u7528\u5730\u585E\u7C73\u677E\u662F\u7528\u4E24\u6B21\uFF0CC\u7EC4\u662F\u80A4\u4E50\u971C\u548C\u5730\u585E\u7C73\u677E\u5404\u4E00\u6B21\uFF0C\u800C\u4E14\u6587\u7AE0\u7684\u8BA8\u8BBA\u90E8\u5206\u4E5F\u6709\u8BF4\uFF1A\u80A4\u4E50\u971C\u8054\u5408\u5730\u585E\u7C73\u677E\u4F7F\u7528\u53EF\u4EE5\u51CF\u5C11\u7CD6\u76AE\u8D28\u6FC0\u7D20\u7528\u91CF\uFF0C\u964D\u4F4E\u957F\u671F\u5916\u7528\u7CD6\u76AE\u8D28\u6FC0\u7D20\u5F15\u53D1\u7684\u4E0D\u826F\u53CD\u5E94\u3002</p><p>\u8FD9\u4E2A\u6709\u9053\u7406\u5417\uFF1F\u8054\u5408\u7528\u836F\u7684\u90A3\u4E00\u7EC4\u786E\u5B9E\u662F\u5404\u7528\u4E00\u6B21\uFF0C\u8C8C\u4F3C\u7528\u80A4\u4E50\u971C\u4EE3\u66FF\u4E00\u6B21\u6FC0\u7D20\u7684\u4F7F\u7528\u91CF\u4E5F\u8FBE\u5230\u4E86\u4F7F\u7528\u4E24\u6B21\u6FC0\u7D20\u7684\u6548\u679C\uFF0C\u5982\u679C\u4F5C\u8005\u4F5C\u51FA\u201C\u80A4\u4E50\u971C\u8054\u5408\u5730\u585E\u7C73\u677E\u4F7F\u7528\u53EF\u4EE5\u51CF\u5C11\u6FC0\u7D20\u7528\u91CF\u201D\u7684\u7ED3\u8BBA\uFF0C\u6211\u89C9\u5F97\u53EF\u4EE5\u7406\u89E3\uFF0C\u4F46\u4F5C\u8005\u5728\u6458\u8981\u91CC\u548C\u6587\u7AE0\u6700\u540E\u90FD\u662F\u7ED9\u51FA\u4E86\u201C\u5730\u585E\u7C73\u677E\u4E73\u818F\u8054\u5408\u80A4\u4E50\u971C\u6CBB\u7597\u5A74\u5E7C\u513F\u6E7F\u75B9\u548C\u7279\u5E94\u6027\u76AE\u708E\u7597\u6548\u53EF\u9760\u5B89\u5168\u6027\u9AD8\u201D\u7684\u7ED3\u8BBA\uFF0C\u8FD9\u662F\u4F5C\u8005\u5728\u6545\u610F\u8BEF\u5BFC\u8BFB\u8005\u8FD8\u662F\u522B\u4EBA\u65AD\u7AE0\u53D6\u4E49\u5462\uFF1F</p><p>\u90A3\u201C\u80A4\u4E50\u971C\u8054\u5408\u5730\u585E\u7C73\u677E\u4F7F\u7528\u53EF\u4EE5\u51CF\u5C11\u6FC0\u7D20\u7528\u91CF\u201D\uFF0C\u80FD\u8BF4\u660E\u80A4\u4E50\u971C\u8FD8\u662F\u6709\u4EF7\u503C\u5417\uFF1F</p><p>\u6211\u5728\u6587\u7AE0\u4E5F\u8BF4\u4E86\uFF0C\u80A4\u4E50\u971C\u7684\u8F85\u6599\u4E3B\u8981\u662F\u7518\u6CB9\u3001\u4E8C\u7532\u7845\u6CB9\u4E4B\u7C7B\u6709\u4FDD\u6E7F\u4F5C\u7528\u7684\u57FA\u8D28\uFF0C\u5355\u7528\u8FD9\u4E9B\u57FA\u8D28\u4E0D\u52A0\u4EFB\u4F55\u4E2D\u836F\u6210\u4EFD\u4E5F\u4F1A\u5BF9\u6E7F\u75B9\u4EA7\u751F\u4E00\u5B9A\u6CBB\u7597\u6548\u679C\uFF0C\u4FDD\u6E7F\u672C\u6765\u5C31\u662F\u6E7F\u75B9\u7684\u57FA\u7840\u6CBB\u7597\uFF0C\u5728\u7528\u6FC0\u7D20\u7684\u540C\u65F6\u7528\u4E86\u4FDD\u6E7F\u5242\uFF0C\u51CF\u5C11\u6FC0\u7D20\u7684\u7528\u91CF\u5E76\u6B65\u5947\u602A\u3002\u53EA\u4E0D\u8FC7\uFF0C\u51E0\u5757\u94B1\u53EF\u4EE5\u4E70\u51E0\u767E\u514B\u7684\u51E1\u58EB\u6797\uFF0C\u4E3A\u4EC0\u4E48\u8981\u82B1\u56DB\u4E94\u5341\u53BB\u4E7020\u514B\u4E00\u652F\u7684\u80A4\u4E50\u971C\u6765\u4FDD\u6E7F\uFF1F</p><p>\u80A4\u4E50\u971C\u8981\u8BC1\u660E\u5B83\u91CC\u9762\u7684\u4E2D\u836F\u6210\u4EFD\u6709\u72EC\u7279\u7684\u4F5C\u7528\uFF0C\u81F3\u5C11\u5E94\u8BE5\u5148\u548C\u4FDD\u6E7F\u5242\u5BF9\u7167\u4E00\u4E0B\u6548\u679C\uFF0C\u800C\u4E0D\u662F\u8EB2\u5728\u6FC0\u7D20\u540E\u9762\u72D0\u5047\u864E\u5A01\u3002\u6253\u7740\u7EAF\u4E2D\u836F\u7684\u540D\u4E49\uFF0C\u501F\u7528\u6FC0\u7D20\u548C\u57FA\u8D28\u6548\u679C\u6765\u5356\u9AD8\u4EF7\u94B1\uFF0C\u548C\u5751\u8499\u62D0\u9A97\u6709\u4EC0\u4E48\u533A\u522B\uFF1F\u66F4\u4F55\u51B5\u80A4\u4E50\u971C\u91CC\u516B\u79CD\u4E2D\u836F\u6709\u4E09\u79CD\u56E0\u4E3A\u6709\u6BD2\u6027\u6216\u523A\u6FC0\u6027\u800C\u7981\u7528\u4E8E\u5316\u5986\u54C1\uFF0C\u800C\u6E7F\u75B9\u672C\u6765\u9700\u8981\u907F\u514D\u76AE\u80A4\u523A\u6FC0\u3002</p><p>\u90A3\u7BC7\u6587\u7AE0\u7684\u901A\u8BAF\u4F5C\u8005\u662F\u513F\u7814\u6240\u9644\u5C5E\u533B\u9662\u76AE\u80A4\u79D1\u4E3B\u4EFB\u533B\u751F\u5218\u6653\u96C1\uFF0C\u4F5C\u4E3A\u4E00\u4E2A\u4E13\u4E1A\u76AE\u80A4\u79D1\u533B\u751F\uFF0C\u540C\u65F6\u5979\u5728\u8BBA\u6587\u91CC\u4E5F\u5F15\u7528\u4E86\u4E2D\u56FD2008\u7248\u7684\u7279\u5E94\u6027\u76AE\u708E\u7684\u8BCA\u65AD\u548C\u6CBB\u7597\u6307\u5357\uFF0C\u4E0D\u53EF\u80FD\u4E0D\u77E5\u9053\u6307\u5357\u91CC\u5199\u7740\u7279\u5E94\u6027\u76AE\u708E\u7684\u4E00\u7EBF\u6CBB\u7597\u65B9\u6848\u5C31\u662F\u5916\u7528\u6FC0\u7D20+\u6DA6\u80A4\u4FDD\u6E7F\u3002</p><p>\u57282012\u7248\u7684\u6307\u5357\u91CC\uFF0C\u8FD8\u7279\u522B\u5F3A\u8C03\u4E86\u201C\u7531\u4E8E\u90E8\u5206\u60A3\u8005\u5BF9\u5916\u7528\u7CD6\u76AE\u8D28\u6FC0\u7D20\u5FC3\u5B58\u7591\u8651\uFF0C\u751A\u81F3\u62D2\u7EDD\u4F7F\u7528\uFF0C\u533B\u751F\u8981\u8010\u5FC3\u89E3\u91CA\u6B63\u89C4\u4F7F\u7528\u836F\u7269\u7684\u5B89\u5168\u6027\u3001\u7528\u836F\u91CF\u2026\u63D0\u9AD8\u6CBB\u7597\u7684\u4F9D\u4ECE\u6027\u201D\u800C\u5218\u6653\u96C1\u533B\u751F\u5728\u81EA\u5DF1\u7684\u8BBA\u6587\u91CC\uFF0C\u5374\u5C06\u4E00\u4E9B\u4E0D\u5F53\u4F7F\u7528\u6FC0\u7D20\u4EA7\u751F\u7684\u540E\u679C\u4F5C\u4E3A\u7406\u7531\uFF0C\u4F5C\u4E3A\u81EA\u5DF1\u533B\u9662\u7528\u4E2D\u836F\u5236\u5242\u80A4\u4E50\u971C\u7684\u501F\u53E3\u3002</p><p>\u90A3\u7BC7\u8BBA\u6587\u91CC\u8FD8\u8BF4\u80A4\u4E50\u971C\u8054\u5408\u5730\u585E\u7C73\u677E\u53EF\u4EE5\u201C\u964D\u4F4E\u957F\u671F\u5916\u7528\u7CD6\u76AE\u8D28\u6FC0\u7D20\u5F15\u53D1\u7684\u4E0D\u826F\u53CD\u5E94\u201D\u4F46\u90A3\u4E2A\u8BD5\u9A8C\u8BF4\u603B\u5171\u67098\u4F8B\u53D1\u751F\u836F\u7269\u4E0D\u826F\u4E8B\u4EF6\uFF0C\u90FD\u88AB\u4F5C\u8005\u5224\u5B9A\u4E0E\u836F\u7269\u65E0\u5173\u3002\u65E2\u7136\u90FD\u548C\u836F\u7269\u65E0\u5173\uFF0C\u4E0D\u77E5\u9053\u53C8\u662F\u5982\u4F55\u5F97\u51FA\u964D\u4F4E\u4E0D\u826F\u53CD\u5E94\u7684\u7ED3\u8BBA\uFF1F</p><p>\u66F4\u6709\u610F\u601D\u7684\u662F\uFF0C\u8FD9\u7BC7\u201C\u8BC1\u5B9E\u201D\u4E86\u6BCF\u5929\u7528\u80A4\u4E50\u971C+\u5730\u585E\u7C73\u677E\u5404\u4E00\u6B21\u5C31\u53EF\u4EE5\u8FBE\u5230\u6BCF\u5929\u4E24\u6B21\u7528\u4E24\u6B21\u5730\u585E\u7C73\u677E\u7597\u6548\u7684\u8BBA\u6587\u57282014\u5E743\u6708\u5C31\u5BC4\u7ED9\u6742\u5FD7\u793E\u4E86\uFF0C\u4F46\u57282015\u5E742\u6708\uFF0C\u8BBA\u6587\u7684\u901A\u8BAF\u4F5C\u8005\u5218\u6653\u96C1\uFF0C\u4F9D\u7136\u5728\u5FAE\u535A\u4E0A\u786E\u8FD8\u662F\u63A8\u8350\u75C5\u4EBA\u80A4\u4E50\u971C+\u827E\u6D1B\u677E1:1\u6DF7\u5408\uFF0C\u6BCF\u5929\u8FD8\u662F2\u6B21\uFF0C\u4E5F\u4E0D\u77E5\u9053\u662F\u4E0D\u76F8\u4FE1\u81EA\u5DF1\u7814\u7A76\u7ED3\u8BBA\uFF0C\u8FD8\u662F\u8BA4\u4E3A\u591A\u7528\u4E00\u6B21\u6FC0\u7D20\u5E76\u6CA1\u6709\u95EE\u9898\u3002</p><p>\u513F\u7814\u6240\u7684\u836F\u5E08@\u7231\u836F\u4E0D\u836F \u4EE5\u53CA\u4E00\u4E9B\u522B\u7684\u7F51\u53CB\u8BC4\u8BBA\u8FD8\u8BF4\uFF0C\u7528\u65E5\u7528\u5316\u5986\u54C1\u7684\u6807\u51C6\u6765\u8981\u6C42\u975E\u65E5\u7528\u7684\u5904\u65B9\u836F\u54C1\uFF0C\u662F\u4E0D\u79D1\u5B66\u7684\u3002\u65E5\u7528\u5316\u5986\u54C1\u7981\u7528\u6FC0\u7D20\uFF0C\u90A3\u4E48\u6709\u6FC0\u7D20\u7684\u5904\u65B9\u836F\u4E5F\u4E0D\u503C\u5F97\u7528\uFF1F\u5316\u5986\u54C1\u662F\u5728\u5546\u573A\u4E70\uFF0C\u80A4\u4E50\u971C\u662F\u5728\u533B\u9662\u51ED\u533B\u751F\u5904\u65B9\u5230\u836F\u623F\u9886\u3002</p><p>\u836F\u54C1\u662F\u6CBB\u75C5\uFF0C\u5316\u5986\u54C1\u662F\u4E3A\u4E86\u7F8E\u89C2\uFF0C\u76EE\u7684\u4E0D\u4E00\u6837\u6709\u4E0D\u540C\u6807\u51C6\u662F\u53EF\u4EE5\u7406\u89E3\u7684\uFF0C\u5927\u90E8\u5206\u836F\u90FD\u6709\u526F\u4F5C\u7528\uFF0C\u4F46\u53EA\u8981\u6536\u76CA\u5927\u4E8E\u98CE\u9669\uFF0C\u800C\u4E14\u6536\u76CA\uFF0F\u98CE\u9669\u6BD4\u662F\u6240\u6709\u9009\u62E9\u91CC\u6700\u5927\u7684\uFF0C\u5C31\u53EF\u4EE5\u7528\u3002\u6FC0\u7D20\u662F\u6709\u53EF\u80FD\u4EA7\u751F\u4E00\u4E9B\u4E0D\u826F\u53CD\u5E94\uFF0C\u6B63\u5E38\u4EBA\u80AF\u5B9A\u4E0D\u9700\u8981\u7528\uFF0C\u4F46\u5B83\u5BF9\u4E8E\u7279\u5E94\u6027\u76AE\u708E\u7684\u6CBB\u7597\u6548\u679C\u662F\u786E\u5207\u7684\uFF0C\u6E7F\u75B9\u5B69\u5B50\u9009\u7528\u5408\u9002\u5F3A\u5EA6\uFF0C\u6309\u7167\u6B63\u786E\u7684\u65B9\u6CD5\u4F7F\u7528\uFF0C\u662F\u6709\u6548\u800C\u4E14\u5B89\u5168\u7684\uFF0C\u81EA\u7136\u53EF\u4EE5\u7528\u3002</p><p>\u800C\u80A4\u4E50\u971C\u91CC\u7684\u8FD9\u4E9B\u4E2D\u836F\uFF1F\u6709\u4E09\u79CD\u6BD2\u6027\u548C\u523A\u6FC0\u6027\u662F\u660E\u786E\u7684\uFF0C\u6240\u4EE5\u5316\u5986\u54C1\u91CC\u88AB\u7981\u7528\uFF0C\u800C\u201C\u6E05\u70ED\u71E5\u6E7F\u3001\u795B\u98CE\u89E3\u6BD2\u98CE\u201D\u7684\u529F\u6548\u53C8\u65E0\u6CD5\u8BC1\u5B9E\uFF0C\u7528\u6765\u6CBB\u7597\u6E7F\u75B9\u548C\u7279\u5E94\u6027\u76AE\u708E\uFF0C\u4E5F\u6CA1\u6709\u8BC1\u5B9E\u8FC7\u6BD4\u4E00\u74F6\u4FDD\u6E7F\u5242\u6548\u679C\u66F4\u597D\uFF0C\u800C\u7B49\u91CF\u7684\u836F\u5356\u7684\u4EF7\u683C\u5374\u662F\u4FDD\u6E7F\u5242\u8D35\u51E0\u5341\u500D\uFF0C\u8FD8\u80FD\u6BD4\u8FD9\u66F4\u5751\u4E00\u70B9\u5417\uFF1F</p><p>\u6309\u7167\u89C4\u5B9A\uFF0C\u9662\u5185\u5236\u5242\u53EA\u6709\u533B\u9662\u8FD9\u6837\u7684\u533B\u7597\u673A\u6784\u624D\u53EF\u4EE5\u7533\u8BF7\u81EA\u5DF1\u914D\u5236\uFF0C\u4E5F\u53EA\u80FD\u7531\u5728\u81EA\u5DF1\u533B\u9662\u5185\u51ED\u533B\u751F\u7684\u5904\u65B9\u624D\u80FD\u4F7F\u7528\uFF0C\u4F46\u5B9E\u9645\u4E0A\uFF0C\u65E0\u8BBA\u662F\u6211\u5728\u5FAE\u535A\u4E0A\u7684\u8C03\u67E5\uFF0C\u8FD8\u662F\u4EE5\u524D\u7684\u8BB0\u8005\u8C03\u67E5[1]\uFF0C\u513F\u7814\u6240\u5176\u5B9E\u4E3A\u80A4\u4E50\u971C\u8FD9\u4E9B\u836F\u5F00\u8BBE\u4E86\u4E13\u95E8\u7684\u5356\u836F\u901A\u9053\uFF0C\u5BB6\u957F\u4E0D\u7528\u5E26\u5B69\u5B50\u53BB\u533B\u9662\uFF0C\u6302\u53F7\u540E\u5C31\u53EF\u4EE5\u5728\u4E09\u697C\u4E13\u95E8\u7684\u7A97\u53E3\u4E70\u836F\u3002\u4E0D\u770B\u75C5\u4EBA\u4E0D\u95EE\u75C5\u60C5\u5C31\u5F00\u836F\uFF0C\u548C\u5316\u5986\u54C1\u7684\u9500\u552E\u6709\u4EC0\u4E48\u533A\u522B\uFF1F</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u9996\u90FD\u513F\u79D1\u7814\u7A76\u6240\u7684\u9662\u5185\u5236\u5242\u8FDC\u4E0D\u6B62\u80A4\u4E50\u971C\u4E00\u4E2A\uFF0C\u53CC\u9EC4\u795B\u6E7F\u6D17\u5242\uFF0C\u8F9B\u82CD\u9F3B\u8212\u53E3\u670D\u6DB2\u3001\u674F\u8D1D\u6B62\u54B3\u6D17\u75F0\u53E3\u670D\u6DB2\u3001\u6E05\u80BA\u5316\u75F0\u9897\u7C92....\u4E5F\u6B63\u662F\u56E0\u4E3A\u7BA1\u7406\u677E\u6563\uFF0C\u6240\u4EE5\u9996\u90FD\u513F\u79D1\u7814\u7A76\u6240\u7684\u9662\u5185\u81EA\u5236\u836F\u505A\u7684\u98CE\u751F\u6C34\u8D77\uFF0C\u5DF2\u7ECF\u5F62\u6210\u4E86\u4E00\u4E2A\u81A8\u5927\u7684\u4EA7\u4E1A\u94FE\uFF0C\u56E0\u4E3A\u5728\u7F51\u7EDC\u4E0A\u5E7F\u6CDB\u9500\u552E\uFF0C\u57282011\u5E74[2]\uFF0C2014\u5E74[1]\u4E24\u5EA6\u88AB\u88AB\u5A92\u4F53\u66DD\u5149\uFF0C\u6DD8\u5B9D\u8FD8\u4E13\u95E8\u8FDB\u884C\u4E0B\u67B6\u5904\u7406\u8FC7\u3002</p><p>\u9644\u5C5E\u533B\u9662\u8D1F\u8D23\u7533\u8BF7\u6279\u53F7\uFF0C\u9996\u513F\u836F\u5382\u8D1F\u8D23\u751F\u4EA7\u3001\u9644\u5C5E\u533B\u9662\u518D\u5F00\u8BBE\u5356\u836F\u7684\u901A\u9053\uFF0C\u8FDB\u800C\u4EA7\u751F\u4EE3\u8D2D\u3001\u7F51\u7EDC\u9500\u552E\uFF0C\u8FD9\u5DF2\u7ECF\u662F\u4EA7\u9500\u4E00\u6761\u9F99\u7684\u4EA7\u4E1A\u94FE\u3002\u6211\u7684\u4E0A\u4E00\u7BC7\u6587\u7AE0\u63A8\u9001\u540E\uFF0C\u5728\u5FAE\u535A\u5FAE\u4FE1\u4E0A\u90FD\u6709\u6302\u7740\u201C\u513F\u7814\u6240\u4EE3\u8D2D\u201D\u540D\u5B57\u7684ID\u6765\u9A82\u8857\uFF0C\u5728QQ\u4E0A\u641C\u7D22\u4E5F\u53EF\u4EE5\u770B\u5230\u5927\u91CF\u7684\u76F8\u5173\u4ECE\u4E1A\u4EBA\u5458\u3002</p><p>\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u9996\u513F\u836F\u5382\u4F5C\u4E3A\u4E00\u5BB6\u72EC\u7ACB\u4E8E\u9644\u5C5E\u533B\u9662\u4E4B\u5916\u7684\u836F\u5382\uFF0C\u6309\u7167\u300A\u533B\u7597\u673A\u6784\u5236\u5242\u914D\u5236\u76D1\u7763\u7BA1\u7406\u529E\u6CD5\u300B\uFF0C\u5B83\u5E76\u6CA1\u6709\u751F\u4EA7\u9662\u5185\u5236\u5242\u7684\u8D44\u8D28\u3002\u5B83\u4E3A\u4EC0\u4E48\u80FD\u751F\u4EA7\uFF1F\u8981\u4E48\u662F\u56E0\u4E3A\u6211\u4EEC\u7684\u653F\u7B56\u4E3A\u4E2D\u836F\u5236\u5242\u5F00\u4E86\u4E00\u4E2A\u59D4\u6258\u914D\u5236\u7684\u901A\u9053\uFF0C\u8981\u4E48\u662F\u76D1\u7BA1\u6709\u95EE\u9898\uFF0C\u8FD9\u4E0D\u662F\u6211\u6240\u80FD\u6DF1\u6316\u7684\u3002</p><p>\u4F5C\u4E3A\u5BB6\u957F\uFF0C\u9762\u5BF9\u8FD9\u6837\u7684\u653F\u7B56\u548C\u533B\u7597\u73AF\u5883\uFF0C\u53EA\u80FD\u81EA\u5DF1\u591A\u770B\u591A\u5B66\u63D0\u9AD8\u8FA8\u8BC6\u80FD\u529B\u53BB\u4FDD\u62A4\u81EA\u5DF1\u3002\u4E0B\u4E00\u7BC7\u6211\u518D\u8BF4\u8BF4\u7433\u7405\u6EE1\u76EE\u7684\u6E7F\u75B9\u818F\u5427\u3002</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p>[1]http://finance.sina.com.cn/consume/puguangtai/20140521/165119181780.shtml</p><p>[2]http://it.sohu.com/20110324/n279967568.shtml</p>',24),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626548&idx=1&sn=e5f8671e40324b68dae399bbc6c864cc&chksm=8b5cb5cebc2b3cd89c8876781f87d75644fbeb6ae124e22a7824680890a1cf920e0ae9347bf4&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=s("\u67E5\u770B\u539F\u6587");function m(f,b){const p=a("ExternalLinkIcon");return c(),t(o,null,[_,e("p",null,[e("a",h,[l,n(p)])])],64)}var k=i(d,[["render",m]]);export{k as default};
