import{r as n,o as s,c as i,a as p,b as e,w as a,F as l,d as c,e as t}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const g={},_=c('<h3 id="\u5982\u4F55\u8BA9\u5B69\u5B50\u4E0D\u53D7-\u94C5-\u7ECA" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u8BA9\u5B69\u5B50\u4E0D\u53D7-\u94C5-\u7ECA" aria-hidden="true">#</a> \u5982\u4F55\u8BA9\u5B69\u5B50\u4E0D\u53D7\u201C\u94C5\u201D\u7ECA\uFF1F</h3><p>\u8BF4\u5230\u201C\u94C5\u201D\uFF0C\u5F88\u591A\u5BB6\u957F\u53EF\u80FD\u6709\u6240\u4E86\u89E3\uFF0C\u4E5F\u53EF\u80FD\u77E5\u9053\uFF0C\u94C5\u5BF9\u5B69\u5B50\u5065\u5EB7\u6709\u5371\u5BB3\uFF0C\u6BD4\u5982\u5F71\u54CD\u667A\u529B\u53D1\u80B2\u7B49\uFF0C\u90A3\u5B69\u5B50\u662F\u5426\u90FD\u8981\u7B5B\u67E5\u8840\u94C5\uFF1F\u5982\u4F55\u907F\u514D\u5B69\u5B50\u7684\u53D1\u80B2\u56E0\u4E3A\u94C5\u4E2D\u6BD2\u800C\u88AB\u7275\u7ECA\uFF1F\u4ECA\u5929\u5C31\u6765\u8C08\u8C08\u8FD9\u4E9B\u95EE\u9898\u3002</p><p><strong>\u94C5\u4E2D\u6BD2\u5BF9\u5B69\u5B50\u6709\u54EA\u4E9B\u5371\u5BB3\uFF1F</strong></p><p>\u94C5\u4E3B\u8981\u4F1A\u5BF9\u5B69\u5B50\u7684\u795E\u7ECF\u7CFB\u7EDF\u3001\u8840\u6DB2\u7CFB\u7EDF\u53CA\u80BE\u810F\u9020\u6210\u4F24\u5BB3[1]\uFF0C\u800C\u635F\u5BB3\u7684\u7A0B\u5EA6\uFF0C\u5219\u53D7\u8840\u94C5\u6C34\u5E73\uFF0C\u4EE5\u53CA\u94C5\u5728\u4F53\u5185\u7684\u4EE3\u8C22\u8FC7\u7A0B\u7B49\u56E0\u7D20\u7684\u5F71\u54CD\u3002\u5927\u90E8\u5206\u94C5\u66B4\u9732\u7684\u5B69\u5B50\u6CA1\u6709\u4EC0\u4E48\u7279\u5F02\u6027\u7684\u8868\u73B0\uFF0C\u66F4\u591A\u662F\u8868\u73B0\u4E3A\u538C\u98DF\u3001\u95F4\u65AD\u809A\u5B50\u75DB\u3001\u4FBF\u79D8\u3001\u7CBE\u795E\u72B6\u6001\u6539\u53D8\u7B49\u3002\u800C\u4E25\u91CD\u7684\u94C5\u4E2D\u6BD2\uFF0C\u5219\u53EF\u4EE5\u81F4\u6B8B\u751A\u81F3\u81F4\u547D\u3002</p><p><strong>\u54EA\u4E2A\u5E74\u9F84\u6BB5\u5B69\u5B50\u6700\u6613\u53D7\u94C5\u4FB5\u5BB3\uFF1F</strong></p><p>\u5E74\u9F84\u5C0F\u7684\u5B69\u5B50\uFF0C\u547C\u5438\u66F4\u5FEB\uFF0C\u4E5F\u559C\u6B22\u5230\u5904\u722C\u3001\u7231\u5403\u624B\uFF0C\u8FD9\u4E9B\u90FD\u4F1A\u589E\u52A0\u94C5\u66B4\u9732\u7684\u98CE\u9669[1]\uFF0C\u540C\u65F6\u8FD9\u4E2A\u9636\u6BB5\u7684\u5B69\u5B50\u5BB9\u6613\u7F3A\u94C1\uFF0C\u5BFC\u81F4\u80C3\u80A0\u9053\u5BF9\u94C5\u7684\u5438\u6536\u66F4\u5F3A[2]\uFF0C\u518D\u52A0\u4E0A\u8FD9\u4E2A\u9636\u6BB5\u5B69\u5B50\u7684\u8840\u8111\u5C4F\u969C\u529F\u80FD\u4E0D\u5B8C\u5584\uFF0C\u8FDB\u5165\u8840\u91CC\u7684\u94C5\u4E5F\u66F4\u5BB9\u6613\u635F\u4F24\u4E2D\u67A2\u795E\u7ECF\uFF0C\u6240\u4EE5<strong>6\u5C81\u4EE5\u4E0B</strong>\uFF0C<strong>\u7279\u522B\u662F3\u5C81\u4EE5\u4E0B</strong>\u7684\u5B69\u5B50\u6700\u5BB9\u6613\u53D7\u5230\u94C5\u7684\u4FB5\u5BB3\u3002</p><p><strong>\u6240\u6709\u7684\u5B69\u5B50\u90FD\u8981\u53BB\u67E5\u8840\u94C5\u5417\uFF1F</strong></p><p>\u94C5\u5BF9\u795E\u7ECF\u7CFB\u7EDF\u53D1\u80B2\u635F\u4F24\u7684\u5F71\u54CD\u6CA1\u6709\u7279\u5B9A\u7684\u9608\u503C\uFF0C\u6240\u4EE5\u513F\u7AE5\u8840\u94C5\u6C34\u5E73\u8D8A\u4F4E\u8D8A\u5B89\u5168\uFF0C\u7F8E\u56FD\u75BE\u75C5\u63A7\u5236\u9884\u9632\u4E2D\u5FC3\u57282012\u5E74\u5BF9\u4E8E\u7F8E\u56FD\u513F\u7AE5\u8840\u94C5\u6C34\u5E73\u5347\u9AD8\u7684\u5B9A\u4E49\uFF0C\u4ECE\u4E4B\u524D\u7684100ug/L\u4E0B\u8C03\u4E3A50ug/L\u3002</p><p>\u56E0\u4E3A\u94C5\u66B4\u9732\u6BD4\u8F83\u9690\u853D\uFF0C\u5F88\u591A\u5BB6\u957F\u641E\u4E0D\u6E05\u5B69\u5B50\u662F\u5426\u8840\u94C5\u8FC7\u9AD8\uFF0C\u6240\u4EE5\u4F1A\u62C5\u5FC3\uFF0C\u7136\u540E\u60F3\u7740\u8981\u4E0D\u8981\u5E26\u5B69\u5B50\u53BB\u67E5\u8840\u94C5\u3002\u8FD9\u5C31\u8981\u770B\u5B69\u5B50\u51FA\u73B0\u94C5\u4E2D\u6BD2\u7684\u6982\u7387\u6709\u591A\u5927\u4E86\u3002</p><p>2004\u5E74\u4F30\u8BA1\u5168\u4E16\u754C\u513F\u7AE5\u4E2D\u5927\u7EA6\u670916%\u8840\u94C5\u6C34\u5E73\u5927\u4E8E100ug/L\uFF0C\u800C\u5176\u4E2D90%\u751F\u6D3B\u5728\u4F4E\u6536\u5165\u5730\u533A[1]\u3002</p><p>2018\u5E74\uFF0C\u9996\u90FD\u513F\u7814\u6240\u5BF9\u6211\u56FD18\u4E2A\u57CE\u5E02\u603B\u8BA121463\u540D\u513F\u7AE5\u7684\u8840\u94C5\u6C34\u5E73\u8FDB\u884C\u4E86\u8C03\u67E5\uFF0C\u53D1\u73B0\u8840\u94C550-99ug/L\u536027.1%\uFF0C100-199ug/L\u53600.98%\uFF0C\u2265200ug/L\u53600.09%[3]\u3002</p><p>\u800C\u4E14\u6211\u8FD1\u5E74\u6765\u6211\u56FD\u513F\u7AE5\u8840\u94C5\u6C34\u5E73\u603B\u4F53\u4E0A\u5448\u4E0B\u964D\u8D8B\u52BF\uFF0C\u800C\u4E14\u5927\u591A\u6570\u57CE\u4E61\u513F\u7AE5\u4E2D\uFF0C\u8840\u94C5\u6C34\u5E73\u2265 200\u03BCg/L \u7684\u6BD4\u4F8B\u5F88\u4F4E\u3002\u9274\u4E8E\u6B64\uFF0C2006\u536B\u751F\u90E8\u7684\u300A\u513F\u7AE5\u9AD8\u94C5\u8840\u75C7\u548C\u94C5\u4E2D\u6BD2\u9884\u9632\u6307\u5357\u300B[4]\u8BA4\u4E3A\uFF1A<strong>\u65E0\u9700\u8FDB\u884C\u513F\u7AE5\u94C5\u4E2D\u6BD2\u666E\u904D\u7B5B\u67E5\uFF0C\u5BF9\u4E8E\u5B58\u5728\u6216\u6000\u7591\u6709\u5DE5\u4E1A\u6027\u94C5\u6C61\u67D3\u7684\u5730\u533A\uFF0C\u53EF\u8003\u8651\u8FDB\u884C\u513F\u7AE5\u94C5\u4E2D\u6BD2\u7684\u7B5B\u67E5\u3002</strong></p><p><strong>\u90A3\u56FD\u5916\u662F\u600E\u4E48\u770B\u8FD9\u4E2A\u95EE\u9898\u5462\uFF1F</strong></p><p>\u7F8E\u56FD\u5BB6\u5EAD\u533B\u5E08\u534F\u4F1A\uFF08AAFP\uFF09\u548C\u7F8E\u56FD\u9884\u9632\u533B\u5B66\u5DE5\u4F5C\u7EC4\uFF08USPSTF\uFF09\u8BA4\u4E3A\u6CA1\u6709\u5145\u8DB3\u7684\u8BC1\u636E\u63A8\u8350\u6216\u53CD\u5BF9\u5E38\u89C4\u8840\u94C5\u7B5B\u67E5\uFF0C\u800C\u7F8E\u56FD\u94C5\u4E2D\u6BD2\u60A3\u75C5\u7387\u4E0B\u964D\uFF0C\u8840\u94C5\u226550ug/L\u6BD4\u4F8B\u4E3A4%[5]\uFF0C<strong>\u6240\u4EE5\u94C5\u4E2D\u6BD2\u7684\u7B5B\u67E5\u4ECE\u666E\u904D\u7B5B\u67E5\u53D8\u66F4\u4E3A\u4E86\u76EE\u6807\u4EBA\u7FA4\u7B5B\u67E5\u3002</strong></p><p>\u7F8E\u56FD\u513F\u79D1\u534F\u4F1A\uFF08AAP\uFF09\u5149\u660E\u672A\u6765\u7684\u9884\u9632\u4FDD\u5065\u5EFA\u8BAE\u662F\uFF1A<strong>\u5E94\u5728\u5B69\u5B506\u30019\u300112\u300118\u548C24\u4E2A\u6708\u65F6\u8BC4\u4F30\u94C5\u4E2D\u6BD2\u98CE\u9669</strong>\uFF0C\u5E76\u5728\u6B64\u540E\u6BCF\u5E74\u8BC4\u4F30\uFF0C\u76F4\u81F36\u5C81\uFF0C\u5982\u679C\u53D1\u73B0\u6709\u9AD8\u98CE\u9669\u56E0\u7D20\uFF0C\u5219\u5E94\u68C0\u67E5\u8840\u94C5\u6C34\u5E73[6]\u3002**\u5982\u679C\u6240\u5728\u793E\u533A\u7B26\u5408\u4EE5\u4E0B1\u9879\u6216\u591A\u9879\uFF0C\u5219\u5EFA\u8BAE\u8FDB\u884C\u94C5\u4E2D\u6BD2\u666E\u904D\u7B5B\u67E5\uFF0C**<strong>\u572812\u6708\u9F84\u548C24\u6708\u9F84\u65F6\u8FDB\u884C</strong>[6]\u3002</p><ul><li><p>\u8840\u94C5\u6C34\u5E73\u5347\u9AD8\u7684\u60A3\u75C5\u7387\u6570\u636E\u4E0D\u8DB3</p></li><li><p>\u226527%\u7684\u4F4F\u623F\u57281950\u5E74\u4EE5\u524D\u5EFA\u9020</p></li><li><p>\u226512%\u768412-36\u6708\u9F84\u513F\u7AE5\u7684\u8840\u94C5\u6C34\u5E73\u5927\u4E8E\u6216\u7B49\u4E8E100ug/L</p></li></ul><p><strong>\u600E\u6837\u7B5B\u67E5\u8840\u94C5\u6C34\u5E73\uFF1F</strong>[7]</p><p>\u4E3B\u8981\u901A\u8FC7\u68C0\u6D4B\u8840\u6DB2\u91CC\u7684\u94C5\u6C34\u5E73\u6765\u5224\u65AD\uFF0C\u76EE\u524D\u4E3B\u8981\u6709\u4E24\u79CD\u65B9\u5F0F\uFF0C\u4E00\u79CD\u662F\u672B\u68A2\u8840\uFF08\u624E\u624B\u6307\uFF09\uFF0C\u53E6\u5916\u4E00\u79CD\u662F\u9759\u8109\u8840\uFF08\u62BD\u8840\uFF09\u3002</p><p>\u4ECE\u51C6\u786E\u5EA6\u800C\u8A00\uFF0C\u5219\u63A8\u8350\u9996\u9009\u9759\u8109\u8840\u3002\u672B\u68A2\u8840\u64CD\u4F5C\u8D77\u6765\u65B9\u4FBF\u5FEB\u6377\uFF0C\u6240\u4EE5\u4E5F\u53EF\u4EE5\u4F5C\u4E3A\u4E00\u4E2A\u9009\u62E9\uFF0C\u4F46\u5982\u679C\u76AE\u80A4\u53D7\u5230\u94C5\u6C61\u67D3\u65F6\uFF0C\u4F1A\u5BFC\u81F4\u8840\u94C5\u5047\u6027\u5347\u9AD8\uFF0C\u6240\u4EE5\u5982\u679C\u672B\u68A2\u8840\u63D0\u793A\u8840\u94C5\u6C34\u5E73\u5347\u9AD8\u65F6\uFF0C\u5219\u9700\u8981\u518D\u6B21\u67E5\u9759\u8109\u8840\u8840\u94C5\u6765\u8FDB\u884C\u786E\u8BA4\u3002</p><p>\u53E6\u5916\uFF0C\u5373\u4F7F\u590D\u67E5\u9759\u8109\u8840\u672A\u8BC1\u5B9E\u8840\u94C5\u5347\u9AD8\uFF0C\u672B\u68A2\u8840\u94C5\u5347\u9AD8\u4E5F\u5E94\u8BE5\u8B66\u60D5\uFF0C\u56E0\u4E3A\u53EF\u80FD\u8BF4\u660E\u5B69\u5B50\u7684\u5468\u56F4\u73AF\u5883\u6709\u94C5\u6C61\u67D3\u98CE\u9669\u3002</p><p><strong>\u9884\u9632\u94C5\u4E2D\u6BD2\uFF0C\u5BB6\u957F\u53EF\u4EE5\u505A\u54EA\u4E9B\uFF1F</strong>[4]</p><p><strong>\u517B\u6210\u826F\u597D\u7684\u536B\u751F\u4E60\u60EF</strong></p><p>\u73AF\u5883\u4E2D\u7684\u94C5\u5C18\u53EF\u901A\u8FC7\u5B69\u5B50\u73A9\u800D\u65F6\u6CBE\u5728\u624B\u4E0A\uFF0C\u5403\u624B\u6216\u8005\u7528\u624B\u6293\u98DF\u7269\u65F6\uFF0C\u4F1A\u5BFC\u81F4\u94C5\u6444\u5165\uFF0C\u56E0\u6B64\uFF0C\u5E94\u8BE5</p><ul><li><p>\u5B69\u5B50\u52E4\u6D17\u624B\uFF0C\u5C24\u5176\u662F\u996D\u524D\u6D17\u624B\u3002</p></li><li><p>\u6307\u7532\u7F1D\u662F\u7279\u522B\u5BB9\u6613\u85CF\u533F\u94C5\u5C18\u7684\u5730\u65B9\uFF0C\u5BB6\u957F\u5E94\u7ED9\u5B69\u5B50\u52E4\u526A\u6307\u7532\u3002</p></li><li><p>\u7ECF\u5E38\u6E05\u6D17\u513F\u7AE5\u7684\u73A9\u5177\u548C\u7528\u54C1\u3002</p></li><li><p>\u5BF9\u4E8E\u5B69\u5B50\u80FD\u89E6\u53CA\u5230\u7684\u7070\u5C18\u5E94\u7528\u5E72\u51C0\u7684\u6E7F\u62B9\u5E03\u53CA\u65F6\u6E05\u6D01\u3002</p></li></ul><p><strong>\u8FDC\u79BB\u5404\u79CD\u94C5\u66B4\u9732\u6E90</strong></p><ul><li><p>\u4E0D\u8981\u5E26\u5B69\u5B50\u5230\u94C5\u4F5C\u4E1A\u5DE5\u5382\u9644\u8FD1\u6563\u6B65\u3001\u73A9\u800D\u3002</p></li><li><p>\u4ECE\u4E8B\u94C5\u4F5C\u4E1A\u7684\u5BB6\u4EBA\uFF0C\u4E0B\u73ED\u524D\u5FC5\u987B\u66F4\u6362\u5DE5\u4F5C\u670D\u548C\u6D17\u6FA1\uFF1B\u4E0D\u8981\u5C06\u5DE5\u4F5C\u670D\u548C\u5B69\u5B50\u8863\u670D\u4E00\u8D77\u6D17\uFF1B\u4E0D\u5728\u94C5\u4F5C\u4E1A\u573A\u6240\uFF08\u6216\u5DE5\u95F4\uFF09\u7ED9\u5B69\u5B50\u54FA\u4E73\u3002</p></li><li><p>\u70E7\u7164\u7684\u5BB6\u5EAD\u5E94\u591A\u5F00\u7A97\u901A\u98CE\u3002</p></li><li><p>\u5B55\u5987\u548C\u5B69\u5B50\u5C3D\u91CF\u907F\u514D\u88AB\u52A8\u5438\u70DF\u3002</p></li><li><p>\u9009\u8D2D\u513F\u7AE5\u9910\u5177\u5E94\u907F\u514D\u5F69\u8272\u56FE\u6848\u548C\u4F2A\u52A3\u4EA7\u54C1\u3002</p></li></ul><p><strong>\u57F9\u517B\u5065\u5EB7\u7684\u996E\u98DF\u4E60\u60EF</strong></p><ul><li><p>\u76AE\u86CB\u548C\u8001\u5F0F\u7206\u7C73\u82B1\u673A\u6240\u7206\u98DF\u54C1\uFF0C\u542B\u94C5\u8F83\u9AD8\uFF0C\u5E94\u907F\u514D\u7ED9\u5B69\u5B50\u5403\u3002</p></li><li><p>\u9F13\u52B1\u5B69\u5B50\u591A\u5403\u9499\u542B\u91CF\u9AD8\u7684\u4E73\u5236\u54C1\u548C\u8C46\u5236\u54C1\uFF0C\u94C1\u3001\u950C\u542B\u91CF\u9AD8\u7684\u8089\u7C7B\u3001\u86CB\u7C7B\u3001\u6D77\u4EA7\u54C1\uFF1B\u7EF4\u751F\u7D20C\u542B\u91CF\u9AD8\u7684\u65B0\u9C9C\u852C\u83DC\u3001\u6C34\u679C\u7B49\u3002</p></li></ul><p><strong>\u53C2\u8003\u6587\u732E</strong></p><p>1.Uptodate \u513F\u7AE5\u94C5\u4E2D\u6BD2\u4E34\u5E8A\u8868\u73B0\u548C\u8BCA\u65AD</p><p>2.Wright RO, Tsaih SW, Schwartz J, et al. Association between iron deficiency and blood lead level in a longitudinal analysis of children followed in an urban primary care clinic. J Pediatr 2003; 142:9.</p><p>3.\u5218\u7231\u534E,\u674E\u6D9B,\u5F20\u5E05\u660E,\u8C08\u85CF\u6587,\u6234\u8000\u534E.\u4E2D\u56FD18\u57CE\u5E02\u513F\u7AE5\u8840\u94C5\u6C34\u5E73\u53CA\u5F71\u54CD\u56E0\u7D20\u73B0\u51B5\u8C03\u67E5[J].\u4E2D\u56FD\u5987\u5E7C\u5065\u5EB7\u7814\u7A76,2018,29(05):539-542.</p><p>4.2006\u536B\u751F\u90E8 \u300A\u513F\u7AE5\u9AD8\u94C5\u8840\u75C7\u548C\u94C5\u4E2D\u6BD2\u9884\u9632\u6307\u5357\u300B\u3001\u300A\u513F\u7AE5\u9AD8\u94C5\u8840\u75C7\u548C\u94C5\u4E2D\u6BD2\u5206\u7EA7\u548C\u5904\u7406\u539F\u5219\uFF08\u8BD5\u884C\uFF09\u300B</p><p>5.https://www.cdc.gov/nceh/lead/data/CBLS-National-Table-508.pdf</p><p>6.Uptodate \u300A\u513F\u7AE5\u548C\u9752\u5C11\u5E74\u7B5B\u67E5\u5B9E\u9A8C\u300B</p><p>7.Uptodate \u300A\u513F\u7AE5\u94C5\u4E2D\u6BD2\u7684\u5904\u7406\u300B</p><p>\uFF08\u53EF\u4E0A\u4E0B\u6ED1\u52A8\u67E5\u770B\uFF09</p><hr><p>\u672C\u6587\u4F5C\u8005<strong>\u9648\u5EFA\u519B</strong>\u533B\u751F\u4E3A\u6021\u79BE\u95E8\u8BCA\u7684\u513F\u79D1&amp;\u513F\u4FDD\u533B\u751F\u3002</p>',39),u=t("\u6021\u79BE\u95E8\u8BCA\u8FD0\u84252\u5E74\uFF0C\u7D2F\u8BA1\u5DF2\u5411\u6570\u5343\u4F4D\u5B69\u5B50\u63D0\u4F9B\u4E86\u513F\u4FDD\u670D\u52A1\uFF0C8\u6708\u662F\u6021\u79BE\u95E8\u8BCA\u7684\u5468\u5E74\u5E86\uFF0C\u672C\u6708\u5230\u95E8\u8BCA\u505A\u513F\u4FDD\u7684\u5B69\u5B50\u53EF\u83B7\u8D60\u89C6\u529B\u7B5B\u67E5\u4E00\u6B21\uFF0C\u8BE6\u60C5\u89C1\uFF1A"),h=t("2\u5468\u5E74\u5E86\u4F18\u60E0"),f={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651631025&idx=1&sn=ccda52bc59798526fc4f8149dde81cfa&chksm=8b5c844bbc2b0d5d40c9a2b0e141cc3f2c7e1452b2cc6b89cace444797cf23f131de50967159&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},m=t("\u67E5\u770B\u539F\u6587");function b(L,x){const o=n("RouterLink"),r=n("ExternalLinkIcon");return s(),i(l,null,[_,p("p",null,[u,e(o,{to:"/s/__biz=MzU3MTU0NTE0NA==&mid=2247485560&idx=1&sn=6ed2d2070f9af99e60af7cd82ca9f1dc/"},{default:a(()=>[h]),_:1})]),p("p",null,[p("a",f,[m,e(r)])])],64)}var N=d(g,[["render",b]]);export{N as default};
