import{r as o,o as s,c as i,a as e,b as p,w as t,F as c,d as l,e as n}from"./app.9aaa2256.js";import{_ as H}from"./plugin-vue_export-helper.21dcd24c.js";const P={},V=l('<h3 id="\u5A5A\u90FD\u7ED3\u4E86-\u8FD8\u8981\u6253hpv\u75AB\u82D7\u5417" tabindex="-1"><a class="header-anchor" href="#\u5A5A\u90FD\u7ED3\u4E86-\u8FD8\u8981\u6253hpv\u75AB\u82D7\u5417" aria-hidden="true">#</a> \u5A5A\u90FD\u7ED3\u4E86\uFF0C\u8FD8\u8981\u6253HPV\u75AB\u82D7\u5417\uFF1F</h3><p>HPV\u75AB\u82D7\u4E00\u76F4\u662F\u5F88\u591A\u8BFB\u8005\u7279\u522B\u5173\u6CE8\u7684\u95EE\u9898\uFF0C\u4E5F\u6709\u5F88\u591A\u7684\u56F0\u60D1\uFF0C\u6BD4\u5982\u7ED3\u5A5A\u4E86\u8FD8\u6709\u6CA1\u6709\u5FC5\u8981\u6253\u8FD9\u4E2A\u75AB\u82D7\uFF0C\u6021\u79BE\u4E0A\u66FE\u63A8\u9001\u8FC7\u75BE\u63A7\u533B\u751F\u5F20\u91CF\u667A\u533B\u751F\u5199\u8FC7\u7684\u76F8\u5173\u79D1\u666E\uFF0C\u5206\u4EAB\u7ED9\u5927\u5BB6\uFF0C\u4EE5\u4E0B\u4E3A\u539F\u6587\u3002</p><hr><p>\u5973\u6027\u670B\u53CB\u4EEC\u5BF9HPV\u75AB\u82D7\u6216\u591A\u6216\u5C11\u6709\u5173\u6CE8\u3002\u4ECA\u5E74\u4E94\u6708\uFF0C\u968F\u7740\u56FD\u4EA7HPV\u75AB\u82D7\u7684\u4E0A\u5E02\uFF0CHPV\u75AB\u82D7\u53C8\u5237\u4E86\u4E00\u6CE2\u70ED\u5EA6\uFF0C**#\u5EFA\u8BAE\u5C06HPV\u75AB\u82D7\u7EB3\u5165\u56FD\u5BB6\u514D\u75AB\u89C4\u5212****#**\u7684\u8BDD\u9898\u751A\u81F3\u4E00\u5EA6\u4E0A\u4E86\u70ED\u641C\u3002</p><p>\u4F5C\u4E3A\u533B\u751F\uFF0C\u4E5F\u5E38\u5E38\u9762\u5BF9\u4E0B\u9762\u8FD9\u4E9B\u7075\u9B42\u62F7\u95EE\uFF1A</p><ul><li><p>\u6253HPV\u75AB\u82D7\u771F\u80FD\u9884\u9632\u5BAB\u9888\u764C\u5417\uFF1F</p></li><li><p>\u7ED3\u5A5A\u4E86\u8FD8\u6709\u5FC5\u8981\u6253HPV\u75AB\u82D7\u5417\uFF1F</p></li><li><p>\u6211\u6253\u4E86\u75AB\u82D7\uFF0C\u8001\u516C\u8FD8\u9700\u8981\u6253\u5417\uFF1F</p></li><li><p>\u6253\u4E86HPV\u75AB\u82D7\uFF0C\u5C31\u4E0D\u7528\u62C5\u5FC3\u5BAB\u9888\u764C\u4E86\u5417\uFF1F</p></li></ul><p>\u8BA8\u8BBA\u8FD9\u4E9B\u95EE\u9898\u4E4B\u524D\uFF0C\u6211\u4EEC\u6709\u5FC5\u8981\u5148\u4E86\u89E3\u4E00\u4E0BHPV\u3001HPV\u5BFC\u81F4\u7684\u75BE\u75C5\u3001HPV\u75AB\u82D7\u4E4B\u95F4\u7684\u5173\u7CFB\u3002</p><p><em><strong>01</strong></em></p><p><strong>HPV\u662F\u4EC0\u4E48\uFF1F</strong></p><p>HPV\u662F\u4EBA\u4E73\u5934\u7624\u75C5\u6BD2\uFF08Human Papillomavirus\uFF09\u7684\u7B80\u79F0\u3002HPV\u662F\u4E00\u79CD\u53E4\u8001\u7684\u75C5\u6BD2\uFF0C\u76EE\u524D\u5728\u75C5\u6BD2\u5B66\u754C\u5DF2\u7ECF\u9274\u5B9A\u51FA228\u79CD\u4E0D\u540C\u7684\u578B\u522B[1]\u3002</p><p>\u6709\u7684\u578B\u522B\u53EF\u4EE5\u5BFC\u81F4\u6076\u6027\u80BF\u7624\uFF0C\u88AB\u79F0\u4E3A\u9AD8\u5371\u578BHPV\uFF0C\u89C6\u611F\u67D3\u90E8\u4F4D\u7684\u4E0D\u540C\u53EF\u5F15\u8D77\u5927\u5BB6\u719F\u77E5\u7684\u5BAB\u9888\u764C\uFF0C\u4E5F\u53EF\u4EE5\u5F15\u8D77\u809B\u95E8\u764C\u3001\u9634\u9053\u764C\u3001\u9634\u830E\u764C\u751A\u81F3\u53E3\u54BD\u764C\u3001\u5589\u764C\u7B49\u3002</p><p>\u6709\u7684\u5219\u4EE5\u5F15\u8D77\u76AE\u80A4\u7C98\u819C\u7684\u826F\u6027\u75C5\u53D8\u4E3A\u4E3B\uFF0C\u4F4E\u5371\u578BHPV\u4E3B\u8981\u5F15\u8D77\u7535\u7EBF\u6746\u5C0F\u5E7F\u544A\u4E0A\u957F\u671F\u9738\u5C4F\u7684\u5C16\u9510\u6E7F\u75A3\u7B49\u751F\u6B96\u5668\u75A3\u3001\u76AE\u80A4\u75A3\u7B49\u75BE\u75C5\uFF0C\u5305\u62ECHPV6/11\u7B49\u578B\u522B\u3002</p><p><strong>\u9AD8\u5371\u578BHPV\u578B\u522B</strong></p><p><strong>\u4F4E\u5371\u578BHPV\u578B\u522B</strong></p><p>16\u300118\u300131\u300133\u300135\u300139\u300145\u300151\u300152\u300156\u300158\u300159\u548C68\u578B</p><p>6\u300111\u300140\u300142\u300143\u300144\u300153\u300154\u300161\u300172\u300173\u548C81\u578B</p><p>HPV\u5728\u4EBA\u7FA4\u4E2D\u4E3B\u8981\u901A\u8FC7\u6027\u884C\u4E3A\u4F20\u64AD\uFF0C\u6027\u884C\u4E3A\u6A21\u5F0F\u4E0EHPV\u81F4\u75C5\u8868\u73B0\u5BC6\u5207\u76F8\u5173\uFF0C\u6BD4\u5982\u7537\u6027\u540C\u6027\u604B\u8005\u662F\u809B\u95E8\u764C\u7684\u9AD8\u5371\u4EBA\u7FA4\uFF1B\u591A\u6027\u4F34\u7684\u5973\u6027\u662F\u5BAB\u9888\u764C\u7684\u9AD8\u5371\u4EBA\u7FA4\u3002</p><p>\u5BF9\u4E8E\u53E3\u54BD\u764C\uFF0C\u6700\u5927\u7684\u9AD8\u5371\u56E0\u7D20\u662F\u5438\u70DF\u548C\u9157\u9152\uFF0C\u5176\u6B21\u5C31\u662F\u7ECF\u53E3\u6027\u884C\u4E3A\u76F8\u5173\u7684HPV\u611F\u67D3\u3002</p><p><em><strong>02</strong></em></p><p><strong>\u5973\u6027\u611F\u67D3HPV</strong></p><p><strong>\u5C31\u7B49\u4E8E\u60A3\u4E0A\u5BAB\u9888\u764C\u5417\uFF1F</strong></p><p>\u8FD9\u91CC\u9700\u8981\u5F3A\u8C03\u4E00\u4E0B\uFF0C\u611F\u67D3HPV\u5E76\u4E0D\u80FD\u76F4\u63A5\u548C\u5BAB\u9888\u764C\u5212\u4E0A\u7B49\u53F7\uFF0C\u53EA\u6709\u9AD8\u5371HPV\u7684\u957F\u671F\u6301\u7EED\u6027\u611F\u67D3\u624D\u662F\u5BFC\u81F4\u6076\u6027\u80BF\u7624\u6BD4\u5982\u5BAB\u9888\u764C\u53D1\u751F\u7684\u5FC5\u8981\u6761\u4EF6\u3002</p><p>\u4E8B\u5B9E\u4E0A\u611F\u67D3HPV\u7684\u4EBA\u7FA4\u4E2D\u53EA\u6709\u4E00\u5C0F\u90E8\u5206\u4F1A\u5904\u4E8E\u6301\u7EED\u611F\u67D3\u72B6\u6001\uFF0C\u800C\u8D85\u8FC790%\u7684\u611F\u67D3\u8005\u5176\u81EA\u8EAB\u7684\u514D\u75AB\u7CFB\u7EDF\u4F1A\u57286\u4E2A\u6708\u5230\u6570\u5E74\u5185\u6E05\u9664\u6389\u75C5\u6BD2\u3002\u5728\u6301\u7EED\u611F\u67D3\u8005\u4E2D\u53C8\u53EA\u6709\u5C11\u90E8\u5206\u4EBA\u53D1\u5C55\u4E3A\u764C\u524D\u75C5\u53D8\uFF0C\u66F4\u5C11\u7684\u4EBA\u624D\u4F1A\u6700\u7EC8\u8FDB\u5C55\u4E3A\u764C\u75C7\uFF3B2\uFF3D\u3002</p><p>\u6700\u540E\u60A3\u764C\u7684\u4EBA\u6709\u591A\u5C11\u5462\uFF1F\u6839\u636E\u4E2D\u56FD\u4E00\u4E2A\u7814\u7A76\uFF0C\u6700\u7EC8\u53D1\u5C55\u4E3A\u764C\u75C7\u7684\u4EBA\u4E0D\u5230HPV\u611F\u67D3\u8005\u76841\u2030[3]\u3002\u6211\u56FD2018\u5E74\u5BAB\u9888\u764C\u7684\u65B0\u53D1\u75C5\u4F8B\u8FD111\u4E07\uFF0C\u653E\u5230\u6574\u4E2A\u4EBA\u7FA4\u4E2D\u53D1\u75C5\u7387\u7EA6\u4E3A16.21/10\u4E07[4]\uFF0C\u5373\u6211\u56FD2018\u5E74\u6BCF10\u4E07\u540D\u5973\u6027\u4E2D\uFF0C\u5927\u7EA6\u670916\u4E2A\u60A3\u4E0A\u4E86\u5BAB\u9888\u764C\u3002</p><p>\u8FD9\u91CC\u6709\u4E00\u4E2A\u5927\u5751\u9700\u8981\u7ED5\u884C\u907F\u5F00\u3002\u6B63\u5982\u4E0A\u9762\u6240\u8BF4\uFF0C\u7EDD\u5927\u90E8\u5206HPV\u611F\u67D3\u8005\u4F1A\u81EA\u884C\u75CA\u6108\uFF0C\u52A0\u4E0A\u76EE\u524D\u5E76\u6CA1\u6709\u6CBB\u7597HPV\u7684\u6709\u6548\u836F\u7269\uFF0C\u65E0\u8BBA\u662F\u6297\u75C5\u6BD2\u836F\u7269\u3001\u4E2D\u836F\u3001\u718F\u84B8\u7B49\u5185\u670D\u5916\u7528\u7684\u5904\u7406\u63AA\u65BD\u90FD\u662F\u5B8C\u5168\u4E0D\u5FC5\u8981\u7684\u3002\u6240\u4EE5\u5982\u679C\u68C0\u6D4B\u51FA\u975E\u9AD8\u5371\u578BHPV\u75C5\u6BD2\u611F\u67D3\u800C\u6CA1\u6709\u5BAB\u9888\u75C5\u53D8\u7684\u8BDD\uFF0C\u4E0D\u9700\u8981\u505A\u4EFB\u4F55\u6CBB\u7597\u3002\u5982\u679C\u68C0\u67E5\u7ED3\u679C\u662F\u9AD8\u5371\u578BHPV\u9633\u6027\uFF0C\u5219\u89C6\u60C5\u51B5\u8003\u8651\u6D3B\u68C0\u6216\u4E00\u5E74\u540E\u518D\u590D\u67E5\u3002</p><p><em><strong>03</strong></em></p><p><strong>\u5BAB\u9888\u764C\u4E0E****\u54EA\u7C7B</strong></p><p><strong>\u9AD8\u5371\u578BHPV\u611F\u67D3\u76F8\u5173\uFF1F</strong></p><p>\u5173\u4E8E\u6301\u7EED\u611F\u67D3\u9AD8\u5371\u578BHPV\u4E0E\u5BAB\u9888\u764C\u7684\u5173\u7CFB\u7684\u7814\u7A76\u5DF2\u7ECF\u8F83\u4E3A\u6DF1\u5165\uFF0C\u4E8C\u8005\u7684\u76F8\u5173\u6027\u4E5F\u5F97\u5230\u4E86\u660E\u786E\u8BC1\u5B9E[5,6]\u3002</p><p>\u5168\u7403\u5BAB\u9888\u764C\u60A3\u8005\u4E2D\uFF0C\u6709\u9AD8\u8FBE60.6%\u7684\u75C5\u4F8B\u4E0EHPV16\u611F\u67D3\u76F8\u5173\uFF0C\u5176\u4F59\u4F9D\u6B21\u662FHPV18\uFF0810.2%\uFF09\u3001HPV45\uFF085.9%\uFF09\u3001HPV33\uFF083.8%\uFF09\u3001HPV31\uFF083.7%\uFF09\u3001HPV52\uFF082.8%\uFF09\u3001HPV58\uFF082.3%\uFF09[7]\u3002</p><p>\u56FE \u6E90 | \u5C0F\u6021\u7ED8\u5236</p><p>\u7531\u6B64\u53EF\u89C1\uFF0CHPV16\u7684\u81F4\u764C\u6027\u6700\u5F3A\uFF0C\u8FDC\u8FDC\u9AD8\u4E8E\u5176\u5B83\u578B\u522B\u3002HPV16\u611F\u67D3\u5BFC\u81F4\u66F4\u574F\u7ED3\u679C\u7684\u6982\u7387\u66F4\u9AD8\uFF0C\u611F\u67D3HPV16\u7684\u75BE\u75C5\u8FDB\u5C55\u4E5F\u66F4\u5FEB\uFF0C\u65E0\u8BBA\u4E2D\u5916\u90FD\u662F\u5982\u6B64[8]\u3002</p><p><em><strong>04</strong></em></p><p><strong>\u63A5\u79CDHPV\u75AB\u82D7=\u9884\u9632\u5BAB\u9888\u764C\uFF1F</strong></p><p>\u4F5C\u4E3A\u7B2C\u4E00\u79CD\u4EE5\u9884\u9632\u764C\u75C7\u4E3A\u76EE\u6807\u7684\u75AB\u82D7\uFF0CHPV\u75AB\u82D7\u7684\u51FA\u73B0\u5149\u73AF\u5341\u8DB3\u3002\u4E0D\u8FC7\u4EBA\u7EA2\u662F\u975E\u591A\uFF0C\u5149\u73AF\u4E4B\u4E0B\u8BEF\u89E3\u4E5F\u5F88\u591A\u3002</p><p><strong>\u8BEF\u89E3\u4E00\uFF1AHPV\u75AB\u82D7\u53EA\u9884\u9632\u5BAB\u9888\u764C</strong></p><p>\u5176\u5B9EHPV\u75AB\u82D7\u5E76\u4E0D\u76F4\u63A5\u9884\u9632\u5BAB\u9888\u764C\uFF0CHPV\u75AB\u82D7\u662F\u4F7F\u4EBA\u4F53\u4EA7\u751F\u9488\u5BF9HPV\u7684\u6297\u4F53\uFF0C\u62B5\u5FA1HPV\u611F\u67D3\uFF0C\u4ECE\u800C\u9884\u9632\u75C5\u6BD2\u6301\u7EED\u611F\u67D3\u53EF\u5F15\u8D77\u7684\u4E00\u7CFB\u5217\u75BE\u75C5\u3002</p><p><strong>\u8BEF\u89E3\u4E8C\uFF1A\u63A5\u79CD\u4E86HPV\u75AB\u82D7\u5C31\u80FD\u9884\u9632\u6240\u6709\u5BAB\u9888\u764C</strong></p><p>HPV\u75AB\u82D7\u4E0D\u80FD\u9884\u9632\u6240\u6709\u7684\u5BAB\u9888\u764C\uFF0C\u76EE\u524D\u4EBA\u4EEC\u8FD8\u65E0\u6CD5\u751F\u4EA7\u51FA\u9884\u9632\u6240\u6709\u578B\u522B\u7684HPV\u611F\u67D3\u7684\u75AB\u82D7\uFF0C\u73B0\u6709\u7684\u75AB\u82D7\u53EA\u6709\u4E09\u79CD\uFF0C\u5206\u522B\u662F\uFF1A\u4E8C\u4EF7HPV\u75AB\u82D7\u3001\u56DB\u4EF7HPV\u75AB\u82D7\u3001\u4E5D\u4EF7HPV\u75AB\u82D7\uFF0C\u8FD9\u91CC\u7684\u201C\u4EF7\u201D\u5373\u662F\u75AB\u82D7\u6240\u5305\u542B\u7684HPV\u7684\u578B\u522B\u3002</p><p>\u5404\u79CD\u75AB\u82D7\u7684\u533A\u522B\u8BE6\u89C1\u4E0B\u8868\uFF1A</p><p>\u5176\u5B83\u6761\u4EF6\u76F8\u540C\u7684\u60C5\u51B5\u4E0B\uFF0C\u201C\u4EF7\u201D\u6570\u8D8A\u9AD8\u4FDD\u62A4\u6548\u679C\u8D8A\u5168\u9762\uFF0C\u4F46\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0CHPV\u75AB\u82D7\u5BF9\u5DF2\u7ECF\u53D1\u751F\u7684HPV\u611F\u67D3\u5E76\u6CA1\u6709\u6CBB\u7597\u4F5C\u7528\uFF0C\u56E0\u6B64\u5C3D\u65E9\u63A5\u79CD\u5BF9\u9884\u9632HPV\u611F\u67D3\u4E5F\u5177\u6709\u91CD\u8981\u7684\u610F\u4E49\u3002\u4E8C\u4EF7HPV\u75AB\u82D7\u7531\u4E8E\u63A5\u79CD\u7684\u8D77\u59CB\u5E74\u9F84\u8F83\u5C0F\uFF0C\u76F8\u5BF9\u4E8E\u56DB\u4EF7\u548C\u4E5D\u4EF7HPV\u75AB\u82D7\u4E5F\u6709\u5176\u72EC\u7279\u7684\u4F18\u52BF\u3002</p><p><em><strong>05</strong></em></p><p><strong>\u7ED3\u5A5A\u540E\u8FD8\u6709</strong></p><p><strong>\u5FC5\u8981\u63A5\u79CDHPV\u75AB\u82D7\u5417\uFF1F</strong></p><p>\u4ECE\u533B\u5B66\u7684\u89D2\u5EA6\u8BB2\uFF0C\u867D\u7136\u5A5A\u540E\u5DF2\u7ECF\u611F\u67D3HPV\u7684\u6982\u7387\u8F83\u9AD8\uFF0C\u4F46\u7531\u4E8EHPV\u578B\u522B\u8F83\u591A\uFF0C\u5982\u679C\u672A\u611F\u67D3\u75AB\u82D7\u53EF\u8986\u76D6\u5230\u7684\u5176\u4ED6\u7C7B\u578B\u75C5\u6BD2\uFF0C\u5EFA\u8BAE\u63A5\u79CD\u3002</p><p>\u4E00\u65B9\u9762\uFF0C\u8003\u8651\u5230\u4E8C\u4EF7\u548C\u56DB\u4EF7HPV\u75AB\u82D7\u90FD\u88AB\u89C2\u5BDF\u5230\u6709\u4EA4\u53C9\u4FDD\u62A4\u4F5C\u7528\uFF08\u56FD\u4EA7\u4E8C\u4EF7HPV\u75AB\u82D7\u662F\u5426\u5177\u6709\u4EA4\u53C9\u4FDD\u62A4\u4F5C\u7528\u5C1A\u672A\u8BC1\u5B9E\uFF09\uFF0C\u4E5F\u5C31\u662F\u5BF9\u75AB\u82D7\u672A\u5305\u542B\u7684\u578B\u522B\uFF08\u5982HPV31\u300133\u548C45\u578B\u7B49\uFF09\u4ECD\u6709\u4FDD\u62A4\u4F5C\u7528[9]\uFF08\u75AB\u82D7\u754C\u7684\u4E70\u4E00\u8D60\u591A\u5927\u4FC3\u9500\uFF09\uFF1B\u53E6\u4E00\u65B9\u9762\uFF0C\u666E\u901A\u4EBA\u5F88\u96BE\u5168\u90E8\u611F\u67D3\u8FD9\u4E48\u591A\u79CD\u578B\u522B\u7684HPV\uFF0C\u56E0\u6B64\u63A5\u79CDHPV\u75AB\u82D7\u4ECD\u7136\u662F\u6709\u7528\u7684\u3002</p><p>HPV\u611F\u67D3\u53CA\u81F4\u75C5\u5E76\u4E0D\u533A\u5206\u7537\u5973\uFF0C\u56E0\u6B64\u7537\u5B69\u53CA\u6210\u5E74\u7537\u6027\u4E5F\u6709\u5FC5\u8981\u63A5\u79CDHPV\u75AB\u82D7\u3002\u7136\u800C\u56FD\u5185\u6240\u6709HPV\u75AB\u82D7\u8BF4\u660E\u4E66\u4E0A\u6CE8\u660E\u7684\u63A5\u79CD\u5BF9\u8C61\u90FD\u662F\u201C\xD7\xD7\u5C81\u81F3\xD7\xD7\u5C81\u5973\u6027\u201D\u3002\u6240\u4EE5\u5F88\u9057\u61BE\uFF0C\u56FD\u5185\u7684\u7537\u6027\u540C\u80DE\u6682\u65F6\u8FD8\u65E0\u6CD5\u63A5\u79CD\u3002</p><p><em><strong>06</strong></em></p><p><strong>\u9884\u9632\u5BAB\u9888\u764C\u63AA\u65BD\u8FD8\u6709\u54EA\u4E9B\uFF1F</strong></p><p>\u5373\u4F7F\u662F\u201C\u6BB5\u4F4D\u201D\u6700\u9AD8\u7684\u4E5D\u4EF7HPV\u75AB\u82D7\uFF0C\u4E5F\u5E76\u672A\u5305\u542B\u6240\u6709\u53EF\u80FD\u5BFC\u81F4\u5BAB\u9888\u764C\u7684\u578B\u522B\uFF0C\u56E0\u6B64\u65E0\u8BBA\u662F\u5426\u63A5\u79CD\u75AB\u82D7\uFF0C\u5B9A\u671F\u8FDB\u884C\u5BAB\u9888\u764C\u7B5B\u67E5\u4E5F\u5FC5\u4E0D\u53EF\u5C11\u3002</p><p>\u56FD\u5916\u5EFA\u8BAE\u5973\u6027\u4ECE21\u5C81\u5F00\u59CB\u5BAB\u9888\u764C\u7B5B\u67E5[10]\uFF0C\u4E2D\u534E\u9884\u9632\u533B\u5B66\u4F1A\u5987\u5973\u4FDD\u5065\u5206\u4F1A\u6839\u636E\u6211\u56FD\u5BAB\u9888\u764C\u53D1\u75C5\u5E74\u9F84\u7279\u70B9\uFF0C\u63A8\u8350\u4ECE25-30\u5C81\u5F00\u59CB\u7B5B\u67E5[11]\uFF0C\u7B5B\u67E5\u7684\u8981\u70B9\u57FA\u672C\u4E00\u81F4\uFF0C\u5EFA\u8BAE\u5982\u4E0B\u8868\uFF1A</p><p><strong>\u5973\u6027\u5BAB\u9888\u764C\u7B5B\u67E5\u5B89\u6392</strong></p><p>\u5728\u4EFB\u4F55\u4E00\u6B21\u7B5B\u67E5\u4E2D\uFF0C\u5982\u679C\u7ED3\u679C\u9634\u6027\uFF0C\u90A3\u4E48\u7EE7\u7EED\u6309\u7167\u539F\u6709\u9891\u7387\u7B5B\u67E5\uFF0C\u5982\u679C\u7B5B\u67E5\u7ED3\u679C\u5F02\u5E38\uFF0C\u90A3\u4E48\u5C31\u9700\u8981\u6839\u636E\u7ED3\u679C\u6765\u505A\u8FDB\u4E00\u6B65\u68C0\u67E5\u4EE5\u53CA\u8C03\u6574\u7B5B\u67E5\u7684\u5468\u671F\u3002</p><p>\u5F53\u7136\uFF0C\u4EE5\u4E0A\u5EFA\u8BAE\u9488\u5BF9\u7684\u662F\u666E\u901A\u5973\u6027\u4EBA\u7FA4\uFF0C\u4E00\u4E9B\u7279\u6B8A\u4EBA\u7FA4\uFF08\u5982HIV\u611F\u67D3\u8005\u3001\u764C\u75C7\u6CBB\u7597\u540E\u6216\u5DF2\u6709\u5BAB\u9888\u75C5\u53D8\u7B49\uFF09\u9700\u7531\u4E13\u79D1\u533B\u751F\u63D0\u4F9B\u4E2A\u4F53\u5316\u7684\u7B5B\u67E5\u5EFA\u8BAE\u3002</p><p>\u4E0D\u65AD\u63D0\u9AD8HPV\u75AB\u82D7\u63A5\u79CD\u7387\u3001\u589E\u5F3A\u5BAB\u9888\u764C\u7B5B\u67E5\u610F\u8BC6\u3001\u6D01\u8EAB\u81EA\u597D\u8DF5\u884C\u5065\u5EB7\u7684\u751F\u6D3B\u65B9\u5F0F\uFF0C\u8FD9\u4E9B\u90FD\u662F\u53EF\u4EE5\u963B\u6B62\u5BAB\u9888\u764C\u8086\u8650\u7684\u63AA\u65BD\u30022018\u5E749\u6708\uFF0C\u4E16\u754C\u536B\u751F\u7EC4\u7EC7\u66F4\u662F\u63D0\u51FA\u5168\u7403\u5BAB\u9888\u764C\u7684\u9632\u63A7\u7684\u7F8E\u597D\u613F\u666F\uFF1A\u4E00\u4E2A\u6CA1\u6709\u5BAB\u9888\u764C\u7684\u4E16\u754C\u3002</p><p>\u8BA9\u6211\u4EEC\u671F\u5F85\u8FD9\u4E00\u5929\u7684\u5230\u6765\u3002</p><p>\uFF08\u533B\u5B66\u5BA1\u6838\uFF1A\u7FC1\u82E5\u9E4F\u3001\u738B\u5E86\u533B\u751F\uFF09</p><p><strong>\u53C2\u8003\u6587\u732E</strong></p><p>[1]International Human Papillomavirus Reference Center. Human papillomavirus reference clones.</p><p>https://www.hpvcenter.se/human_reference_clones/, accessed June 2020.</p><p>[2]Moscicki AB, Schiffman M, Burchell A, et al. Updating the natural history of human papillomavirus and anogenital cancers[J]. Vaccine, 2012,30 Suppl 5: F24\u201033. DOI:10.1016/j.vaccine.2012.05.089.</p><p>[3]Zhao FH, Adam KL, Hu SY, et al. Prevalence of human papillomavirus and cervical intraepithelial neoplasia in China:a pooled analysis of 17 population\u2010based studies[J]. Int J Cancer, 2012,131(12): 2929\u20102938. DOI:10.1002/ijc.27571.</p><p>[4] Bruni L, Albero G, Serrano B, et al. Human papillomavirus and related diseases in the world[R]. Barcelona: ICO/IARC Information Centre on HPV and Cancer, 2019.</p><p>[5]Walboomers JL et al. Human papillomavirus is a necessary cause of invasive cervical cancer worldwide. J. Pathol, 1999;189:12 19.</p><p>[6]Bosch FX et al. The causal relation between human papillomavirus and cervical cancer. J Clin Pathol, 2002;55(4):244 265.</p><p>[7] Serrano B et al. Human papillomavirus genotype attribution for HPVs 6,11,16,18,31,33,45,52 and 58 in female anogenital lesions. Eur J Cancer. 2015;51(13):1732 1741.</p><p>[8] Stanley A. Plotkin WAO, Paul A \u7B49. \u75AB\u82D7\u5B66\uFF08\u7B2C6 \u7248\uFF09[M]. \u5317\u4EAC: \u4EBA\u6C11\u536B\u751F\u51FA\u7248\u793E;2017</p><p>[9]HPV vaccine background document. Available at http://www.who.int/immunization/sage/meetings/2016/october/1_HPV_vaccine_background_document_27Sept2016.pdf?ua=1, accessed June 2020.</p><p>[10]US Preventive Services Task Force, Curry SJ, Krist AH, Owens DK, et al. Screening for cervical cancer: US preventive services task force recommendation statement. JAMA 201.</p><p>[11]\u4E2D\u534E\u9884\u9632\u533B\u5B66\u4F1A\u5987\u5973\u4FDD\u5065\u5206\u4F1A. \u5B50\u5BAB\u9888\u764C\u7EFC\u5408\u9632\u63A7\u6307\u5357[M]. \u5317\u4EAC:\u4EBA\u6C11\u536B\u751F\u51FA\u7248\u793E, 2017.</p><p>\uFF08\u53EF\u4E0A\u4E0B\u6ED1\u52A8\u67E5\u770B\uFF09</p><hr><p>\u76F8\u5173\u9605\u8BFB\uFF1A</p>',73),d=n("\u80BF\u7624\u6807\u5FD7\u7269\u9633\u6027\u662F\u764C\u75C7\uFF1F\u764C\u75C7\u7B5B\u67E5\u600E\u4E48\u67E5\uFF1F"),m=n("\u56FD\u4EA713\u4EF7\u80BA\u708E\u75AB\u82D7\u83B7\u6279\uFF0C\u80BA\u708E\u75AB\u82D7\u5982\u4F55\u9009\u62E9\uFF1F"),u=n("\u540C\u4E00\u79CD\u75AB\u82D7\uFF0C\u4E3A\u4EC0\u4E48\u6709\u7684\u5730\u65B9\u201C\u8D85\u9F84\u201D\u4E86\u4ECD\u80FD\u63A5\u79CD\uFF1F"),g=e("p",null,"\u5173\u6CE8\u6021\u79BE\uFF0C\u53EF\u54A8\u8BE2\u533B\u751F",-1),_={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651631197&idx=1&sn=c8ce7ff5c294c4ae8f4c5a8c039a7a48&chksm=8b5c87a7bc2b0eb196cbe8c1b09d783187596c59977db09bdb160641c28922a1f044522b6c90&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=n("\u67E5\u770B\u539F\u6587");function f(v,b){const a=o("RouterLink"),r=o("ExternalLinkIcon");return s(),i(c,null,[V,e("p",null,[p(a,{to:"/s/__biz=MzIwMzM3NjI5OQ==&mid=2247488707&idx=1&sn=713b023195aa8c02f4faad982fd763d9/"},{default:t(()=>[d]),_:1})]),e("p",null,[p(a,{to:"/s/__biz=MzIwMzM3NjI5OQ==&mid=2247488707&idx=1&sn=713b023195aa8c02f4faad982fd763d9/"},{default:t(()=>[m]),_:1})]),e("p",null,[p(a,{to:"/s/__biz=MzIwMzM3NjI5OQ==&mid=2247488963&idx=3&sn=d91486fa1847e418d19d492d4bd7611f/"},{default:t(()=>[u]),_:1})]),g,e("p",null,[e("a",_,[h,p(r)])])],64)}var x=H(P,[["render",f]]);export{x as default};
