import{r as p,o as n,c as o,a as e,b as r,F as s,d as a,e as c}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const d={},h=a('<h3 id="\u300A\u8B66\u60D5\u6EE5\u7528\u7684\u5229\u5DF4\u97E6\u6797\u300B\u7EED\u4E00" tabindex="-1"><a class="header-anchor" href="#\u300A\u8B66\u60D5\u6EE5\u7528\u7684\u5229\u5DF4\u97E6\u6797\u300B\u7EED\u4E00" aria-hidden="true">#</a> \u300A\u8B66\u60D5\u6EE5\u7528\u7684\u5229\u5DF4\u97E6\u6797\u300B\u7EED\u4E00</h3><p>\u300A\u8B66\u60D5\u6EE5\u7528\u7684\u5229\u5DF4\u97E6\u6797\u300B\u8FD9\u7BC7\u6587\u7AE0\u53D1\u5E03\u540E\uFF0C\u5728\u5FAE\u535A\u4E0A\u8F6C\u53D1\u4E864\u5343\u591A\u6B21\uFF0C\u53CD\u54CD\u4E4B\u5927\u51FA\u4E4E\u6211\u7684\u610F\u6599\u3002\u56E0\u4E3A\u66FE\u5199\u8FC7\u4E00\u7BC7\u5173\u4E8E\u8FD9\u4E2A\u836F\u7684\u6587\u7AE0\uFF0C\u5176\u6EE5\u7528\u95EE\u9898\u5FAE\u535A\u4E0A\u5F88\u591A\u5176\u5B83\u533B\u751F\u4E5F\u8BF4\u8FC7\u5F88\u591A\u6B21\uFF0C\u6211\u4EE5\u4E3A\u5F88\u591A\u4EBA\u90FD\u6E05\u695A\u5176\u6EE5\u7528\u95EE\u9898\uFF0C\u6240\u4EE5\u672C\u6765\u4E5F\u6CA1\u5174\u8DA3\u518D\u5199\u8FD9\u4E2A\u9898\u76EE\u3002</p><p>\u524D\u4E0D\u4E45\u770B\u5230@\u9A6C\u4F0A\u740D \u5728\u4E00\u6761\u8F6C\u53D1\u5FAE\u535A\u91CC\u8BF4\u201C\u670B\u53CB\u7684\u5B69\u5B50\u6700\u8FD1\u5F97\u4E86\u624B\u8DB3\u53E3\u75C5\uFF0C\u73ED\u4E0A\u5B69\u5B50\u4E5F\u88AB\u4F20\u67D3\uFF0C\u5BB6\u957F\u4EA4\u6D41\u65F6\u53D1\u73B0\u51E0\u4E4E\u6240\u6709\u5B69\u5B50\u90FD\u88AB\u5F00\u4E86\u5229\u5DF4\u97E6\u6797\u201D \u3002\u5C31\u8FD9\u6837\u4E00\u6761\u5F88\u5E73\u6DE1\u7684\u8BB2\u8FF0\uFF0C\u7ED3\u679C\u7ADF\u5F15\u6765\u4E00\u5806\u8C29\u9A82\uFF0C\u201C\u5229\u5DF4\u97E6\u6797\u662F\u6548\u679C\u6700\u597D\u4E14\u6700\u4FBF\u5B9C\u7684\u6297\u75C5\u6BD2\u836F\u7269\u201D\u3001\u201C\u75C5\u6BD2\u611F\u67D3\u5FC5\u987B\u5229\u5DF4\u97E6\u6797\u201D \u8FD9\u6837\u7684\u8BC4\u8BBA\u7ADF\u7136\u88AB\u5F88\u591A\u70B9\u8D5E\u800C\u6210\u4E3A\u70ED\u95E8\u8BC4\u8BBA\u3002</p><p>\u4E00\u9762\u662F\u5229\u5DF4\u97E6\u6797\u88AB\u5E7F\u6CDB\u6EE5\u7528\uFF0C\u4E00\u9762\u662F\u5927\u4F17\u8FD8\u5728\u4E3A\u8FD9\u79CD\u6EE5\u7528\u884C\u4E3A\u53EB\u597D\uFF0C\u751A\u81F3\u5BF9\u63D0\u51FA\u8B66\u9192\u7684\u4EBA\u8FDB\u884C\u56F4\u653B\u8C29\u9A82\uFF0C\u6DF1\u611F\u5BF9\u8FD9\u4E2A\u836F\u7684\u6EE5\u7528\u95EE\u9898\u8BF4\u5F97\u4E0D\u662F\u591F\u591A\u800C\u662F\u8FDC\u8FDC\u4E0D\u591F\uFF0C\u6240\u4EE5\u8FD8\u662F\u5199\u4E86\u90A3\u7BC7\u6587\u7AE0\u3002\u5728\u5FAE\u4FE1\u4E0A\u7684\u516B\u767E\u591A\u4EBA\u53C2\u4E0E\u7684\u8C03\u67E5\u8FD8\u53D1\u73B0\uFF0C\u5E26\u5B69\u5B50\u4E0A\u533B\u9662\uFF0C\u8D85\u8FC780%\u7684\u5BB6\u957F\u9047\u5230\u8FC7\u533B\u751F\u7ED9\u81EA\u5DF1\u5B69\u5B50\u5F00\u5229\u5DF4\u97E6\u6797\uFF0C\u8FD9\u4E2A\u7ED3\u679C\u4E5F\u8BA9\u662F\u8D85\u4E4E\u60F3\u8C61\u3002</p><p>\u6211\u4E5F\u9884\u6599\u5230\u8FD9\u7BC7\u6587\u7AE0\u4F1A\u6709\u4E9B\u53CD\u5F39\uFF0C\u6BD4\u5982\u6211\u4EEC\u7684\u513F\u79D1\u533B\u751F\u540C\u884C\u4EEC\uFF0C\u6240\u4EE5\u518D\u9009\u62E9\u4E00\u4E9B\u6BD4\u8F83\u96C6\u4E2D\u7684\u95EE\u9898\u505A\u4E2A\u56DE\u7B54\uFF0C\u56E0\u4E3A\u5185\u5BB9\u592A\u591A\uFF0C\u5206\u4E24\u6B21\u63A8\u9001\u5427\u3002</p><p>**@<strong><strong>\u513F\u79D1\u533B\u751F\u90D1\u542F\u57CE</strong></strong>\uFF1A****\u624B\u8DB3\u53E3\u75C5\u4E00\u5E74\u6B7B\u591A\u5C11\u4E2A\u4F60\u77E5\u9053\u4E48\uFF1F[\u9119\u89C6][\u9119\u89C6]**<strong>\u5BF9\u4E8E\u624B\u8DB3\u53E3\u75C5\u60A3\u8005\uFF0C\u4F7F\u7528\u5229\u5DF4\u97E6\u6797\u662F\u5426\u5229\u5927\u4E8E\u5F0A\uFF0C\u6211\u5E76\u6CA1\u6709\u7814\u7A76\u3002\u53EF\u662F\u4F60\u5199\u8FD9\u7BC7\u79D1\u666E\u662F\u5426\u5145\u5206\u67E5\u9605\u8FC7\u76F8\u5173\u7684\u7814\u7A76\u62A5\u9053\u5462\uFF1F\u4F60\u662F\u5426\u53EF\u4EE5\u8BF7\u6559\u4E00\u4E0B\u5317\u4EAC\u513F\u7AE5\u533B\u9662\u7684\u611F\u67D3\u79D1\u4E13\u5BB6\u6216\u662F\u56FD\u5185\u7814\u7A76\u624B\u8DB3\u53E3\u75C5\u7684\u4E13\u5BB6\u5462\uFF1F\u4E00\u4E2A\u60A3\u513F\u5982\u679C\u60A3\u4E86\u624B\u8DB3\u53E3\u75C5\u662F\u5426\u5E94\u8BE5\u5C1D\u8BD5\u6297\u75C5\u6BD2\u6CBB\u7597\u5462\uFF1F</strong></p><p>\u624B\u8DB3\u53E3\u75C5\u4E00\u5E74\u6B7B\u591A\u5C11\u4E2A\u6211\u771F\u4E0D\u77E5\u9053\uFF0C\u4F46\u5199\u8FD9\u7BC7\u6587\u7AE0\u4E4B\u524D\uFF0C\u624B\u8DB3\u53E3\u75C5\u662F\u5426\u5E94\u8BE5\u6297\u75C5\u6BD2\u6CBB\u7597\u6211\u8FD8\u662F\u77E5\u9053\u800C\u4E14\u67E5\u8BC1\u8FC7\u7684\uFF0C\u6211\u6CA1\u6709\u67E5\u5230\u54EA\u4E2A\u53EF\u9760\u7684\u7814\u7A76\u8BF4\u7528\u4E86\u5229\u5DF4\u97E6\u6797\u53EF\u4EE5\u964D\u4F4E\u624B\u8DB3\u53E3\u75C5\u7684\u6B7B\u4EA1\u7387\uFF0C\u5982\u679C\u4F60\u67E5\u5230\u4E86\u53EF\u4EE5\u544A\u8BC9\u6211\uFF0C\u4E5F\u53EF\u4EE5\u544A\u8BC9\uFF1A</p><p>\u4E16\u754C\u536B\u751F\u7EC4\u7EC7 [1]\u3002\u4ED6\u4EEC\u7684\u610F\u89C1\u662F\uFF1A\u6CA1\u6709\u7279\u6548\u7684\u6297\u75C5\u6BD2\u836F\uFF08no specific antivirals)\uFF1B</p><p>\u8FD8\u53EF\u4EE5\u544A\u8BC9\u7F8E\u56FD\u75BE\u75C5\u63A7\u5236\u4E2D\u5FC3 [2]\uFF0C\u4ED6\u4EEC\u7684\u610F\u89C1\u662F\uFF1A\u624B\u8DB3\u53E3\u75C5\u6CA1\u6709\u7279\u6548\u6CBB\u7597\uFF0C\u4F46\u4F60\u53EF\u4EE5\u505A\u70B9\u4EC0\u4E48\u6765\u7F13\u89E3\u75C7\u72B6\uFF0C\u6BD4\u5982\u5403\u70B9\u975E\u5904\u65B9\u836F\u6765\u9000\u70E7\u548C\u6B62\u75DB\uFF0C\u7528\u6F31\u53E3\u6C34\u6216\u55B7\u96FE\u5242\u6765\u7ED9\u5634\u91CC\u6B62\u75DB\uFF1B</p><p>\u4E5F\u53EF\u4EE5\u544A\u8BC9\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A[3]\uFF0C\u56E0\u4E3A\u76EE\u524D\u4ED6\u4EEC\u7ED9\u7684\u610F\u89C1\u662F\uFF1A\u5BF9\u80A0\u9053\u75C5\u6BD2\u5F15\u8D77\u7684\u611F\u67D3\u76EE\u524D\u6CA1\u6709\u6279\u51C6\u7684\u7279\u6548\u836F\uFF0C\u8BD5\u9A8C\u8FC7\u4E00\u4E2A\u53EB\u666E\u53EF\u90A3\u5229\u7684\u6297\u75C5\u6BD2\u836F\uFF0C\u4F46\u8FD8\u6CA1\u6279\u51C6\u5E38\u89C4\u4F7F\u7528\u3002</p><p>\u867D\u7136\u6211\u4E00\u76F4\u8BA4\u4E3A\u56FD\u5185\u7684\u6307\u5357\u4E0D\u592A\u9760\u8C31\uFF0C\u5317\u4EAC\u513F\u7AE5\u533B\u9662\u81EA\u5DF1\u7684\u95E8\u8BCA\u5904\u65B9\u8D85\u8FC710%\u542B\u6709\u5229\u5DF4\u97E6\u6797\uFF0C\u4F46\u65E2\u7136\u4F60\u8FD9\u4E48\u76F8\u4FE1\u56FD\u5185\u7684\u4E13\u5BB6\uFF0C\u90A3\u770B\u770B\u6211\u4EEC\u56FD\u5185\u4E13\u5BB6\u5236\u5B9A\u7684\u4E34\u5E8A\u6307\u5357[4]\u91CC\uFF0C\u6709\u6CA1\u6709\u63D0\u5230\u5229\u5DF4\u97E6\u6797\u6297\u75C5\u6BD2\u6CBB\u7597\uFF0C\u5982\u679C\u6CA1\u6709\u4F60\u4E5F\u53EF\u4EE5\u63D0\u9192\u4ED6\u4EEC\u6BCF\u5E74\u5F88\u591A\u5B69\u5B50\u56E0\u4E3A\u624B\u8DB3\u53E3\u75C5\u6B7B\u4EA1\u3002</p><p>\u624B\u8DB3\u53E3\u75C5\u76EE\u524D\u6CA1\u6709\u6709\u6548\u7684\u6297\u75C5\u6BD2\u836F\uFF0C\u4E5F\u6CA1\u6709\u75AB\u82D7\uFF0C\u8981\u964D\u4F4E\u56E0\u624B\u8DB3\u53E3\u75C5\u800C\u6B7B\u4EA1\u7684\u5B69\u5B50\u6570\u91CF\uFF0C\u8981\u9760\u9884\u9632\uFF0C\u8981\u6CE8\u610F\u536B\u751F\uFF0C\u52E4\u6D17\u624B\uFF0C\u9760\u5BB6\u957F\u63D0\u9AD8\u5BF9\u75BE\u75C5\u7684\u8BA4\u8BC6\uFF0C\u53D1\u73B0\u6709\u91CD\u75C7\u8868\u73B0\u53CA\u65F6\u9001\u533B\uFF0C\u9760\u533B\u7597\u673A\u6784\u6CBB\u7597\u6C34\u5E73\u7684\u63D0\u9AD8\uFF0C\u800C\u4E0D\u662F\u9760\u7528\u4E00\u4E2A\u6CA1\u8BC1\u5B9E\u6548\u679C\u800C\u660E\u786E\u6709\u4E0D\u5C11\u526F\u4F5C\u7528\u7684\u5229\u5DF4\u97E6\u6797\u3002\u5728\u6CA1\u6709\u8BC1\u5B9E\u6709\u6548\u6027\u4E4B\u524D\uFF0C\u7ED9\u7EDD\u5927\u591A\u6570\u53EF\u4EE5\u81EA\u6108\u7684\u624B\u8DB3\u53E3\u75C5\u4F7F\u7528\u526F\u4F5C\u7528\u4E0D\u5C11\u7684\u5229\u5DF4\u97E6\u6797\uFF0C\u589E\u52A0\u7684\u8FD9\u4E9B\u526F\u4F5C\u7528\u662F\u4E0D\u662F\u6709\u53EF\u80FD\u589E\u5927\u6B7B\u4EA1\u7387\u7684\u98CE\u9669\u5462\uFF1F\u5982\u679C\u624B\u8DB3\u53E3\u75C5\u4E00\u5E74\u6B7B\u4EA1\u7684\u6570\u5B57\u5F88\u60CA\u4EBA\uFF0C\u90A3\u662F\u4E0D\u662F\u66F4\u4E0D\u5E94\u8BE5\u6EE5\u7528\u5462\uFF1F</p><p>**@<strong><strong>Da\u80D6\u4E01</strong></strong>\uFF1A**<strong>\u88F4\u533B\u751F\uFF0C\u80FD\u79D1\u666E\u4E0B\uFF0C\u7528\u4EC0\u4E48\u836F\u4EE3\u66FF\u6BD4\u8F83\u597D\u5462\uFF1F</strong></p><p>\u5F88\u591A\u4EBA\u90FD\u8DDF\u6211\u8BF4\u8FC7\uFF0C\u4F60\u603B\u662F\u544A\u8BC9\u6211\u8FD9\u4E0D\u80FD\u7528\uFF0C\u90A3\u4E0D\u80FD\u7528\uFF0C\u5229\u5DF4\u97E6\u6797\u4E0D\u80FD\u7528\uFF0C\u90A3\u4F60\u80FD\u4E0D\u80FD\u544A\u8BC9\u8BE5\u7528\u4EC0\u4E48\uFF1F</p><p>\u5728\u5F88\u591A\u4EBA\u773C\u91CC\uFF0C\u75C5\u5C31\u662F\u9760\u6CBB\u597D\u7684\uFF0C\u751F\u75C5\u4E86\u5C31\u5E94\u8BE5\u7528\u836F\uFF0C\u54EA\u6015\u4F60\u544A\u8BC9\u4ED6\u8FD9\u662F\u81EA\u6108\u6027\u75BE\u75C5\u3002\u666E\u901A\u611F\u5192\u3001\u624B\u8DB3\u53E3\u75C5\u3001\u8F6E\u72B6\u75C5\u6BD2\u80A0\u708E\u662F\u75C5\u6BD2\u611F\u67D3\u5F15\u8D77\u7684\uFF0C\u8FD9\u4E9B\u75C5\u6BD2\u76EE\u524D\u6CA1\u6709\u6709\u6548\u7684\u6297\u75C5\u6BD2\u836F\uFF0C\u5229\u5DF4\u97E6\u6797\u662F\u6297\u75C5\u6BD2\u836F\uFF0C\u4F46\u76EE\u524D\u6CA1\u6709\u53EF\u9760\u7684\u7814\u7A76\u8BC1\u5B9E\u5B83\u53EF\u4EE5\u6CBB\u7597\u8FD9\u4E9B\u75C5\uFF0C\u65E2\u4E0D\u80FD\u6539\u5584\u75C7\u72B6\uFF0C\u4E5F\u4E0D\u80FD\u7F29\u77ED\u751F\u75C5\u65F6\u95F4\uFF0C\u4E5F\u4E0D\u80FD\u964D\u4F4E\u6B7B\u4EA1\u7387\u3002\u800C\u5229\u5DF4\u97E6\u6797\u526F\u4F5C\u7528\u8FD8\u4E0D\u5C11\uFF0C\u7528\u4E86\u8FD9\u6709\u574F\u5904\u6CA1\u6709\u597D\u5904\uFF0C\u81EA\u7136\u4E0D\u80FD\u7528\u3002</p><p>\u90A3\u8BE5\u7528\u4EC0\u4E48\uFF1F\u6709\u4E9B\u75C5\u6BD2\u611F\u67D3\uFF0C\u5B58\u5728\u6709\u6548\u7684\u6297\u75C5\u6BD2\u836F\u7269\uFF0C\u90A3\u81EA\u7136\u53EF\u4EE5\u7528\uFF0C\u8BE5\u7528\u4EC0\u4E48\u836F\u8981\u6839\u636E\u75C5\u79CD\u3001\u75C5\u60C5\u3001\u75C5\u6BD2\u79CD\u7C7B\uFF0C\u8010\u836F\u6027\u7B49\u5177\u4F53\u5206\u6790\uFF0C\u800C\u4E0D\u662F\u4E00\u6982\u800C\u8BBA\u7528\u4EC0\u4E48\u836F\u53D6\u4EE3\u4EC0\u4E48\u836F\u3002\u666E\u901A\u611F\u5192\u624B\u8DB3\u53E3\u75C5\u75C5\u76EE\u524D\u6CA1\u6709\u6709\u6548\u7684\u6297\u75C5\u6BD2\u836F\uFF0C\u90A3\u5C31\u662F\u4E0D\u8BE5\u7528\u4EC0\u4E48\u836F\u53BB\u6297\u75C5\u6BD2\uFF0C\u9664\u975E\u5C06\u6765\u54EA\u4E00\u5929\u7814\u53D1\u51FA\u4E86\u6709\u7528\u800C\u4E14\u5B89\u5168\u7684\u6297\u75C5\u6BD2\u836F\u3002</p><p>\u6709\u75C5\u5C31\u8981\u6253\u9488\u5403\u836F\uFF0C\u4E0D\u7BA1\u5BF9\u4E0D\u5BF9\u8DEF\uFF0C\u6709\u6CA1\u6709\u6548\uFF0C\u7528\u4E0A\u4E86\u5C31\u5FC3\u6EE1\u610F\u8DB3\uFF0C\u4E0D\u7528\u5C31\u5FC3\u91CC\u53D1\u614C\uFF0C\u751A\u81F3\u6253\u9A82\u533B\u751F\uFF0C\u8FD9\u662F\u5F88\u591A\u5BB6\u957F\u7684\u5FC3\u6001\uFF0C\u8FD9\u4E5F\u662F\u4E00\u4E9B\u533B\u751F\u5F00\u836F\u7684\u539F\u56E0\uFF0C\u6240\u4EE5\u8BF4\u4E00\u4E9B\u836F\u7269\u7684\u6EE5\u7528\uFF0C\u5BB6\u957F\u4E0D\u662F\u5B8C\u5168\u65E0\u8F9C\u7684\u3002</p><p>**@<strong><strong>12\u65F634\u5206</strong></strong>\uFF1A**<strong>\u5373\u4F7F\u91CD\u611F\u5192\u624B\u8DB3\u53E3\u4E5F\u4E0D\u8981\u6EE5\u7528\u6297\u751F\u7D20\u6216\u8005\u5229\u5DF4\u97E6\u6797\u7684\u610F\u601D\u662F\u5417\uFF1F\u591A\u559D\u6C34\uFF0C\u633A\u8FC7\u53BB\u4E86\u5C31\u662F\u6761\u6C49\u5B50\uFF0C\u633A\u4E0D\u8FC7\u53BB18\u5E74\u540E\u53C8\u662F\u6761\u597D\u6C49\uFF01</strong></p><p>\u8FD9\u53E5\u8BDD\u597D\u524D\u534A\u53E5\u662F\u5BF9\u7684\uFF0C\u662F\u5426\u4F7F\u7528\u4E00\u4E2A\u836F\u4E0D\u662F\u5B8C\u5168\u770B\u75C5\u60C5\u7684\u8F7B\u91CD\uFF0C\u800C\u662F\u770B\u5BF9\u75C5\u60C5\u6709\u6CA1\u6709\u5E2E\u52A9\u3002\u65E2\u7136\u666E\u901A\u611F\u5192\u624B\u8DB3\u53E3\u75C5\u662F\u6CA1\u6709\u6709\u6548\u7684\u6297\u75C5\u6BD2\u836F\u7269\uFF0C\u90A3\u5C31\u65E0\u8BBA\u8F7B\u91CD\u4E5F\u4E0D\u8981\u6EE5\u7528\u5229\u5DF4\u97E6\u6797\u3002\u4F46\u4E0D\u7528\u836F\u4E0D\u7B49\u4E8E\u4E0D\u9700\u8981\u89C2\u5BDF\uFF0C\u4E0D\u9700\u8981\u6CBB\u7597\uFF0C\u6BD4\u5982\u624B\u8DB3\u53E3\u75C5\u867D\u7136\u7EDD\u5927\u591A\u6570\u662F\u666E\u901A\u578B\uFF0C\u5230\u65F6\u4F1A\u81EA\u5DF1\u597D\uFF0C\u4F46\u4F60\u8FD8\u662F\u5FC5\u987B\u6CE8\u610F\u5B69\u5B50\u7684\u7CBE\u795E\u53CD\u5E94\uFF0C\u5982\u679C\u6301\u7EED\u9AD8\u70ED\uFF0C\u7CBE\u795E\u4E0D\u597D\uFF0C\u5455\u5410\u7B49\u8981\u53CA\u65F6\u53BB\u533B\u9662\u3002</p><p>\u65E2\u7136\u6CA1\u6709\u6548\u7684\u6297\u75C5\u6BD2\u836F\u4E0A\u533B\u9662\u5E72\u561B\uFF1F\u53BB\u533B\u9662\u8BA9\u533B\u751F\u5E2E\u5B69\u5B50\u633A\u8FC7\u53BB\uFF0C\u6BD4\u5982\u4ED6\u6709\u8111\u6C34\u80BF\u4E86\u5E2E\u4ED6\u8131\u6C34\u6D88\u80BF\uFF0C\u547C\u5438\u4E0D\u884C\u4E86\u7528\u547C\u5438\u673A\u5E2E\u4ED6\u547C\u5438\uFF0C\u5728\u533B\u7597\u63AA\u65BD\u5E2E\u52A9\u4E0B\u633A\u8FC7\u53BB\u6BD4\u5B8C\u5168\u9760\u4ED6\u81EA\u5DF1\u633A\u8FC7\u53BB\u7684\u6210\u529F\u7684\u673A\u4F1A\u5927\uFF0C\u6240\u4EE5\u8BE5\u4E0A\u533B\u9662\u8FD8\u662F\u8981\u4E0A\u533B\u9662\u3002\u4E0A\u533B\u9662\u8FD8\u633A\u4E0D\u8FC7\u53BB\u600E\u4E48\u529E\uFF1F\u90A3\u5C31\u662F\u53EA\u6709\u518D\u7B4918\u5E74\u540E\u518D\u505A\u597D\u6C49\u4E86\u3002</p><p>\u5F53\u7136\uFF0C\u5982\u679C\u611F\u89C9\u5C31\u8981\u633A\u4E0D\u8FC7\u53BB\u4E86\uFF0C\u8FD9\u65F6\u5019\u60F3\u8BD5\u8BD5\u5229\u5DF4\u97E6\u6797\u4E5F\u4E0D\u662F\u672A\u5C1D\u4E0D\u53EF\uFF0C\u6211\u56FD\u7684\u300A\u80A0\u9053\u75C5\u6BD271\u578B\uFF08EV71\uFF09\u611F\u67D3\u91CD\u75C7\u75C5\u4F8B\u4E34\u5E8A\u6551\u6CBB\u4E13\u5BB6\u5171\u8BC6\uFF082011\u5E74\u7248\uFF09\u300B\u91CC\u8BF4\uFF1A\u76EE\u524D\u5C1A\u65E0\u786E\u5207\u6709\u6548\u7684\u6297EV71\u75C5\u6BD2\uFF08\u4F5C\u8005\u6CE8\uFF1A\u5F15\u8D77\u624B\u8DB3\u53E3\u75C5\u7684\u4E00\u79CD\u75C5\u6BD2\uFF09\u836F\u7269\uFF0C\u5229\u5DF4\u97E6\u6797\u4F53\u5916\u8BD5\u9A8C\u8BC1\u5B9E\u6709\u6291\u5236EV71\u590D\u5236\u548C\u90E8\u5206\u706D\u6D3B\u75C5\u6BD2\u4F5C\u7528\uFF0C\u53EF\u8003\u8651\u4F7F\u7528\u3002</p><p>\u8FD9\u79CD\u8BF4\u6CD5\u8BF4\u96BE\u542C\u4E00\u70B9\u5C31\u662F\u6B7B\u9A6C\u5F53\u6D3B\u9A6C\u533B\uFF0C\u53CD\u6B63\u5DF2\u7ECF\u6CA1\u5E0C\u671B\u4E86\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u540C\u610F\u8BA9\u533B\u751F\u81EA\u5DF1\u51B3\u5B9A\u662F\u4E0D\u662F\u7ED9\u91CD\u75C7\u547C\u5438\u9053\u5408\u80DE\u75C5\u6BD2\u80BA\u708E\u7684\u5B69\u5B50\u4F7F\u7528\u5229\u5DF4\u97E6\u6797\uFF0C\u5927\u6982\u4E5F\u662F\u8FD9\u4E2A\u5FC3\u6001\u3002\u8BD5\u8BD5\u8FD9\u4E2A\u5728\u6CA1\u6709\u4EBA\u4F53\u9A8C\u8BC1\u8FC7\u6709\u6548\u6027\uFF0C\u4F46\u5728\u4F53\u5916\u8BC1\u660E\u6709\u6297\u75C5\u6BD2\u6548\u679C\u7684\u836F\u7269\uFF0C\u6709\u7528\u6700\u597D\uFF0C\u6CA1\u7528\u5C31\u5F53\u4E3A\u533B\u5B66\u7814\u7A76\u505A\u4E86\u4E9B\u8D21\u732E\uFF0C\u603B\u7ED3\u4E00\u4E0B\u7136\u540E\u63D0\u9192\u4EE5\u540E\u7684\u533B\u751F\u7528\u6216\u8005\u4E0D\u7528\uFF0C\u8FD9\u6211\u89C9\u5F97\u53EF\u4EE5\u7406\u89E3\uFF0C\u4F46\u8FD9\u79CD\u72B6\u6001\u7684\u75C5\u4EBA\u57FA\u672C\u662F\u8EBA\u5728ICU\u91CC\u4E86\uFF0C\u5728\u95E8\u8BCA\u7ED9\u672C\u6765\u5C31\u7EDD\u5927\u90E8\u5206\u53EF\u4EE5\u81EA\u6108\u7684\u666E\u901A\u578B\u624B\u8DB3\u53E3\u75C5\u5728\u95E8\u8BCA\u7528\u8FD9\u4E48\u591A\u526F\u4F5C\u7528\u7684\u5229\u5DF4\u97E6\u6797\u5C31\u662F\u6EE5\u7528\u3002\uFF08\u672A\u5B8C\u5F85\u7EED\uFF09</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p>[1]http://www.wpro.who.int/publications/docs/GuidancefortheclinicalmanagementofHFMD.pdf</p><p>[2]http://www.cdc.gov/hand-foot-mouth/about/prevention-treatment.html</p><p>[3]http://www.healthychildren.org/English/health-issues/conditions/infections/Pages/Hand-Foot-and-Mouth-Disease.aspx</p><p>[4]http://www.nhfpc.gov.cn/yzygj/s3593g/201306/6d935c0f43cd4a1fb46f8f71acf8e245.shtml</p>',27),g={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=204450106&idx=1&sn=778f5bc7f1eb6ec998e58baead1de24d&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=c("\u67E5\u770B\u539F\u6587");function f(_,m){const t=p("ExternalLinkIcon");return n(),o(s,null,[h,e("p",null,[e("a",g,[l,r(t)])])],64)}var x=i(d,[["render",f]]);export{x as default};
