import{r,o as t,c as n,a as e,b as c,F as p,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=o('<h3 id="\u5B69\u5B50\u53D1\u70E7-\u4E0D\u7528\u9000\u70E7\u836F\u4F1A\u4E0D\u4F1A\u8D8A\u70E7\u8D8A\u9AD8" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u53D1\u70E7-\u4E0D\u7528\u9000\u70E7\u836F\u4F1A\u4E0D\u4F1A\u8D8A\u70E7\u8D8A\u9AD8" aria-hidden="true">#</a> \u5B69\u5B50\u53D1\u70E7\uFF0C\u4E0D\u7528\u9000\u70E7\u836F\u4F1A\u4E0D\u4F1A\u8D8A\u70E7\u8D8A\u9AD8\uFF1F</h3><p>\u773C\u4E0B\u6D41\u611F\u8086\u8650\uFF0C\u5F88\u591A\u5B69\u5B50\u53D1\u70E7\uFF0C\u770B\u5230\u5B69\u5B50\u70E7\u5F97\u660F\u660F\u6B32\u7761\uFF0C\u6D51\u8EAB\u53D1\u70EB\uFF0C\u5F88\u591A\u5BB6\u957F\u4F1A\u7ACB\u523B\u7ED9\u5B69\u5B50\u5582\u9000\u70E7\u836F\uFF0C\u5982\u679C\u4F53\u6E29\u63A7\u5236\u4E0D\u597D\uFF0C\u751A\u81F3\u4F1A\u4E24\u79CD\u9000\u70E7\u836F\u4E00\u8D77\u7528\u3002\u54EA\u6015\u5FCD\u7740\u6CA1\u6709\u5582\u836F\u7684\u5BB6\u957F\uFF0C\u4E5F\u4F1A\u62C5\u5FC3\u4F1A\u4E0D\u4F1A\u8D8A\u70E7\u8D8A\u9AD8\u70E7\u574F\u5B69\u5B50\u3002</p><p>\u5728\u4E4B\u524D\u7684\u6587\u7AE0\u91CC\uFF0C\u6211\u8BF4\u8FC7\u53D1\u70E7\u4E0D\u4F1A\u70E7\u574F\u8111\u5B50\uFF0C\u4E5F\u4E0D\u4F1A\u5BF9\u8EAB\u4F53\u4EA7\u751F\u5371\u5BB3\uFF0C\u9664\u975E\u662F41\u2103\u4EE5\u4E0A\u7684\u8D85\u9AD8\u70ED\u6216\u70ED\u6027\u60CA\u53A5\u6301\u7EED\u72B6\u6001\u3002\u90A3\u5982\u679C\u4E0D\u7528\u9000\u70E7\u836F\uFF0C\u5B69\u5B50\u4F53\u6E29\u4F1A\u8D8A\u6765\u8D8A\u9AD8\u5417\uFF0C\u4F1A\u70E7\u523042\u2103\u751A\u81F3\u66F4\u9AD8\u5BFC\u81F4\u5371\u9669\u5417\uFF1F</p><p>\u4E0D\u4F1A\u7684\u3002</p><p>\u4EBA\u4F53\u7684\u4F53\u6E29\u662F\u53D7\u4E0B\u4E18\u8111\u7684\u4F53\u6E29\u4E2D\u67A2\u8C03\u63A7\u7684\uFF0C\u5E73\u65F6\u4F53\u6E29\u4E2D\u67A2\u4F1A\u628A\u4F53\u6E29\u63A7\u5236\u572837\u2103\u5DE6\u53F3\uFF0C\u6240\u4EE5\u4F53\u6E29\u662F\u76F8\u5BF9\u6052\u5B9A\u7684\uFF0C\u5F53\u53D1\u751F\u611F\u67D3\u3001\u708E\u75C7\u65F6\uFF0C\u4F53\u6E29\u4E2D\u67A2\u53EF\u80FD\u4F1A\u628A\u8BBE\u5B9A\u6E29\u5EA6\u8C03\u9AD8\uFF0C\u5BFC\u81F4\u53D1\u70E7\u3002</p><p>\u4F46\u4F53\u6E29\u4E2D\u67A2\u5E76\u4E0D\u4F1A\u65E0\u8282\u5236\u7684\u628A\u4F53\u6E29\u8C03\u9AD8\uFF0C\u800C\u662F\u5F88\u5C11\u8D85\u8FC741\u2103\uFF0C\u4E0D\u4F1A\u8D85\u8FC742\u2103\uFF0C\u8FD9\u662F\u56E0\u4E3A<strong>\u53D1\u70E7\u5B58\u5728\u4E00\u4E2A\u201C\u73BB\u7483\u5929\u82B1\u677F\u201D\u6548\u5E94</strong>\u3002\u65E9\u57281949\u5E74\uFF0C\u5C31\u6709\u4EBA\u6CE8\u610F\u5230\u4E86\u8FD9\u4E2A\u6548\u5E94\uFF0C\u4F5C\u8005\u7EDF\u8BA1\u4E86357\u4E2A\u9AD8\u70ED\u75C5\u4EBA\u7684\u4F53\u6E29\uFF0C\u53D1\u73B0\u53EA\u67094.3%\u7684\u75C5\u4EBA\u8D85\u8FC7\u4E8641.1\u2103\uFF0C\u4F46\u6CA1\u6709\u4E00\u4E2A\u8D85\u8FC7\u4E8642\u2103\u3002[1]</p><p>\u8FD9\u5E94\u8BE5\u662F\u4EBA\u4F53\u7684\u4E00\u4E2A\u81EA\u6211\u4FDD\u62A4\u673A\u5236\u7684\uFF0C\u8FD9\u4E2A\u4FDD\u62A4\u673A\u5236\u662F\u600E\u4E48\u4EA7\u751F\u7684\u76EE\u524D\u4E0D\u662F\u5F88\u6E05\u695A\uFF0C\u53EF\u80FD\u548C\u8C03\u8282\u4F53\u6E29\u7684\u4E0B\u4E18\u8111\u795E\u7ECF\u5143\u6709\u5173\uFF0C\u4E5F\u53EF\u80FD\u548C\u4F53\u5185\u7684\u6297\u81F4\u70ED\u539F\u7269\u8D28\u6709\u5173\uFF0C\u4F46\u53D1\u70E7\u5B58\u5728\u4E0A\u9650\u662F\u88AB\u4E34\u5E8A\u548C\u5B9E\u9A8C\u8BC1\u5B9E\u7684\u3002[2]\u6240\u4EE5\u5B69\u5B50\u53D1\u70E7\u6CA1\u5403\u9000\u70E7\u836F\uFF0C<strong>\u5E76\u4E0D\u662F\u4F1A\u4E00\u76F4\u8D8A\u70E7\u8D8A\u9AD8\u70E7\u574F\u5B69\u5B50</strong>\u3002</p><p>\u867D\u7136\u5C11\u89C1\uFF0C\u4F46\u4E5F\u786E\u5B9E\u5B58\u5728\u4F53\u6E29\u8D85\u8FC741\u2103\u7684\u8D85\u9AD8\u70ED\uFF0C\u8FD9\u79CD\u60C5\u51B5\u53EF\u80FD\u662F\u4F53\u6E29\u4E2D\u67A2\u51FA\u4E86\u95EE\u9898\uFF0C\u6BD4\u5982\u4E00\u4E9B\u836F\u7269\u5F71\u54CD\u4E86\u4F53\u6E29\u4E2D\u67A2\u5BFC\u81F4\u7684\u6076\u6027\u9AD8\u70ED\uFF0C\u6216\u8005\u9885\u5185\u51FA\u8840\u5F71\u54CD\u4E86\u4F53\u6E29\u4E2D\u67A2\u3002</p><p>\u518D\u8FC7\u6765\u5C31\u662F\u5916\u90E8\u73AF\u5883\u6E29\u5EA6\u8FC7\u9AD8\uFF0C\u6BD4\u5982\u590F\u5929\u5728\u592A\u9633\u5E95\u4E0B\u66B4\u6652\uFF0C\u6216\u8005\u7528\u539A\u539A\u7684\u88AB\u5B50\u7ED9\u5B69\u5B50\u6342\u70ED\uFF0C\u4EBA\u4F53\u63A5\u53D7\u6216\u4EA7\u751F\u7684\u70ED\u91CF\u8D85\u51FA\u4E86\u8EAB\u4F53\u7684\u6563\u70ED\u80FD\u529B\uFF0C\u5BFC\u81F4\u6B63\u5E38\u4F53\u6E29\u8C03\u8282\u529F\u80FD\u5931\u6548\u4E86\uFF0C\u4E5F\u53EF\u80FD\u5BFC\u81F4\u4F53\u6E29\u8FC7\u9AD8\u3002</p><p>\u56E0\u4E3A\u9000\u70E7\u836F\u662F\u901A\u8FC7\u8C03\u8282\u4F53\u6E29\u4E2D\u67A2\u7684\u6E29\u5EA6\u8BBE\u5B9A\u70B9\u6765\u53D1\u6325\u4F5C\u7528\u7684\uFF0C\u5982\u679C\u662F\u4F53\u6E29\u4E2D\u67A2\u51FA\u4E86\u95EE\u9898\uFF0C\u6216\u8005\u73AF\u5883\u6E29\u5EA6\u8FC7\u9AD8\u5BFC\u81F4\u7684\u4F53\u6E29\u4E0A\u5347\uFF0C\u7528\u9000\u70E7\u836F\u4E5F\u4E0D\u4F1A\u6709\u6548\u679C\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5982\u679C\u771F\u7684\u662F\u8D85\u9AD8\u70ED\u4E86\uFF0C\u7528\u9000\u70E7\u836F\u4E5F\u662F\u6CA1\u7528\u7684\u3002</p><p>\u6240\u4EE5\uFF0C\u5982\u679C\u5B69\u5B50\u53D1\u70E7\uFF0C\u5982\u679C\u4E0D\u7528\u9000\u70E7\u836F\uFF0C\u4F53\u6E29\u53EF\u80FD\u4F1A\u5347\u9AD8\uFF0C\u4F1A\u8BA9\u5B69\u5B50\u96BE\u53D7\uFF0C\u4F46\u4F53\u6E29\u4E0D\u4F1A\u65E0\u9650\u5236\u7684\u5347\u9AD8\uFF0C\u4E0D\u9700\u8981\u62C5\u5FC3\u70E7\u574F\u5B69\u5B50\uFF0C\u5982\u679C\u771F\u7684\u662F\u4F1A\u5230\u8FBE\u80FD\u70E7\u574F\u5B69\u5B50\u7684\u6E29\u5EA6\uFF0C\u90A3\u4E5F\u662F\u4F53\u6E29\u8C03\u8282\u4E2D\u67A2\u51FA\u4E86\u95EE\u9898\uFF0C\u9760\u5403\u9000\u70E7\u836F\u4E5F\u6CA1\u6709\u7528\u3002</p><p>\u9000\u70E7\u836F\u7684\u4F5C\u7528<strong>\u53EA\u662F\u9000\u70ED\u6B62\u75DB</strong>\uFF0C\u5582\u4E86\u53EF\u4EE5\u8BA9\u5B69\u5B50\u4E0D\u90A3\u4E48\u96BE\u53D7\uFF0C\u5982\u679C\u5B69\u5B50\u6709\u660E\u663E\u7684\u4E0D\u9002\u53EF\u4EE5\u5582\u3002\u4F46\u5BF9\u6CA1\u6709\u57FA\u7840\u75BE\u75C5\u7684\u5B69\u5B50\u6765\u8BF4\uFF0C\u4EFB\u4F55\u65F6\u5019\u4E0D\u5582\u9000\u70E7\u836F\uFF0C\u90FD\u4E0D\u4F1A\u803D\u8BEF\u75C5\u60C5\uFF0C\u56E0\u4E3A\u5B83\u6CA1\u6709\u6CBB\u75C5\u7684\u4F5C\u7528\u3002\u89C2\u5BDF\u5B69\u5B50\u7CBE\u795E\u72B6\u6001\uFF0C\u8B66\u60D5\u53D1\u70E7\u80CC\u540E\u7684\u90A3\u4E9B\u4E25\u91CD\u75BE\u75C5\uFF0C\u6BD4\u5173\u6CE8\u4F53\u6E29\u66F4\u91CD\u8981\uFF0C\u62C5\u5FC3\u8D85\u9AD8\u70ED\u7684\u8BDD\uFF0C\u5982\u679C\u5B69\u5B50\u4F53\u6E29\u53CD\u590D\u8D85\u8FC740\u2103\uFF0C\u5C31\u53CA\u65F6\u53BB\u770B\u533B\u751F\u3002</p><p>\u60F3\u660E\u767D\u8FD9\u4E00\u70B9\uFF0C\u5C31\u4E0D\u9700\u8981\u4E3A\u8981\u4E0D\u8981\u7528\u9000\u70E7\u836F\uFF0C\u8981\u4E0D\u8981\u4EA4\u66FF\u7528\u836F\u800C\u7EA0\u7ED3\u4E86\u3002</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p>[1]Dubois EF. Why are fever temperatures over 106\xB0F rare? Am J Med Sci 1949;217:361\u20138.</p><p>[2]Mackowiak PA1, Boulant JA. Fever&#39;s glass ceiling Clin Infect Dis. 1996 Mar;22(3):525-36.</p>',16),f={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627264&idx=1&sn=08486851d0ad401e1a9607947254c6b3&chksm=8b5cb6fabc2b3fecf724c708dc4bfc272dacbabf35b8870ef412d3a6cfae9fe1d024489fc4d8&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=s("\u67E5\u770B\u539F\u6587");function h(m,b){const a=r("ExternalLinkIcon");return t(),n(p,null,[_,e("p",null,[e("a",f,[l,c(a)])])],64)}var x=d(i,[["render",h]]);export{x as default};
